SET NAMES utf8mb4;
SET FOREIGN_KEY_CHECKS = 0;

-- ----------------------------
-- Table structure for article
-- ----------------------------
DROP TABLE IF EXISTS `article`;
CREATE TABLE `article`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '文章id',
  `title` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '文章标题\r\n',
  `description` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '简要描述',
  `tags` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '文章标签',
  `author` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '作者\r\n',
  `author_id` int(11) NULL DEFAULT NULL COMMENT '作者id\r\n',
  `author_img` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '作者头像',
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '文章内容_(不带类名css)',
  `htmlcontent` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '文章内容_(带类名css)',
  `img` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '文章图片',
  `pv` int(11) NULL DEFAULT 0 COMMENT '阅读量',
  `comment` int(11) NULL DEFAULT 0 COMMENT '评论量',
  `like` int(11) NULL DEFAULT 0 COMMENT '点赞量',
  `valid` tinyint(1) NULL DEFAULT 0 COMMENT '是否已审核',
  `created` datetime(0) NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '创建时间(时间戳)',
  `updated` datetime(0) NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '更新时间(时间戳)',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 16220 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of article
-- ----------------------------
INSERT INTO `article` VALUES (15620, '2020 Q3 智能手机销量排名：三星重回第一，realme 增速惊人', '根据Canalys、IDC以及Counterpoint最新公布的2020年第三季度全球智能手机市场数据报告显示，虽然因为统计渠道以及口径等方面的不同，使得厂商的...', '[object Object]', '冯伟文', 19, 'http://pic.zaeke.com/user_1610094930png', '<p>根据 Canalys、IDC 以及 Counterpoint 最新公布的 2020 年第三季度全球智能手机市场数据报告显示，虽然因为统计渠道以及口径等方面的不同，使得厂商的发货量有所不同，可是大致相当。那就是全球在疫情后的恢复要比预料中的要好，5G 智能手机的销量进一步增长，在第三季度的出货量超过 2020 年上半年的整体出货量，这也是得益于 5G 智能手机的价格进一步下探至 300 美元的原因。随着苹果也就是 iPhone 12 系列的推出，在后续将进一步推动 5G 智能手机的总体销量。</p><p><img alt=\"kV50xrO3MVsgO07B7Cykh_DkjSND6Xyu.png\" src=\"http://pic.zaeke.com/img160402903776709.png\" width=\"900\" height=\"506\"><br></p><p>-三星重新夺回市场第一的位置，其出货量环比 47%，这也是其三年以来最高的出货量。这是得益于 Note20 系列以及 A 系列机型的帮助，使得其在印度、西欧以及拉美等市场的复苏。并且得益于 M 系列的帮助，三星也在印度市场获得不错的销量，也夺得当地市场第一的位置。</p><p>-小米相较上一季度增长 75%，这也是其有史以来最高的出货量。并且小米在中国、拉美、欧洲以及东南亚等市场都有着相当不错的表现，这也是因为华为被限制的原因。</p><p>-realme 在 2020 年第三季度的累计出货量超过 5000 万台，这也是全球达成该出货量最高的智能手机品牌。得益于推出相当不错的 5G 智能手机组合，其在中国、东南亚等不同国家地区都有着相当不错的销量表现。</p><p>-苹果在 2020 年第三季度的出货量同比下降 7%，这是因为 iPhone 12 系列发布时间从第三季度推迟至第四季度，预计随着该系列的发布，苹果的业绩将会有不错的表现。</p><p>-得益于 Y 系列，vivo 在海外市场特别是东南亚市场的表现不错；OPPO 则是通过与运营商合作伙伴的关系来拓展欧洲市场，也与领先的体育赛事建立合作伙伴关系以扩大其在欧洲市场的影响力。</p><p><img alt=\"微信图片_20201030102149.jpg\" src=\"http://pic.zaeke.com/img160402904767233.jpeg\" width=\"1780\" height=\"890\"><br></p><p>具体的数字以 Counterpoint 为例，其中华为包含荣耀，小米包含 Redmi：</p><p>-三星，7980 万台，同比 2%；</p><p>-华为，5090 万台，同比 -24%；</p><p>-小米，4620 万台，同比 46%；</p><p>-苹果，4170 万台，同比 -7%；</p><p>-OPPO，3100 万台，同比 -4；</p><p>-vivo，3100 万台，同比 -1%</p><p>-realme，1480 万台，同比 45%；</p><p>-联想，1020 万台，同比 2%；</p><p>-LG，650 万台，同比 -11%；</p><p>-传音，560 万台，同比 2%；</p><p><br></p><p>Counterpoint：<a href=\"https://www.counterpointresearch.com/global-smartphone-market-shows-signs-recovery-q3-realme-grows-fastest-132-qoq/\" target=\"_blank\">戳我</a><br></p><p>IDC：<a href=\"https://www.idc.com/getdoc.jsp?containerId=prUS46974920\" target=\"_blank\">戳我</a></p><p>canalys：<a href=\"https://www.canalys.com/newsroom/canalys-worldwide-smartphone-market-q3-2020\" target=\"_blank\">戳我</a></p>', '<p class=\"p_style\">根据 Canalys、IDC 以及 Counterpoint 最新公布的 2020 年第三季度全球智能手机市场数据报告显示，虽然因为统计渠道以及口径等方面的不同，使得厂商的发货量有所不同，可是大致相当。那就是全球在疫情后的恢复要比预料中的要好，5G 智能手机的销量进一步增长，在第三季度的出货量超过 2020 年上半年的整体出货量，这也是得益于 5G 智能手机的价格进一步下探至 300 美元的原因。随着苹果也就是 iPhone 12 系列的推出，在后续将进一步推动 5G 智能手机的总体销量。</p><p class=\"jsmargin\"><img alt=\"kV50xrO3MVsgO07B7Cykh_DkjSND6Xyu.png\" src=\"http://pic.zaeke.com/img160402903776709.png\" width=\"900\" height=\"506\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">-三星重新夺回市场第一的位置，其出货量环比 47%，这也是其三年以来最高的出货量。这是得益于 Note20 系列以及 A 系列机型的帮助，使得其在印度、西欧以及拉美等市场的复苏。并且得益于 M 系列的帮助，三星也在印度市场获得不错的销量，也夺得当地市场第一的位置。</p><p class=\"p_style\">-小米相较上一季度增长 75%，这也是其有史以来最高的出货量。并且小米在中国、拉美、欧洲以及东南亚等市场都有着相当不错的表现，这也是因为华为被限制的原因。</p><p class=\"p_style\">-realme 在 2020 年第三季度的累计出货量超过 5000 万台，这也是全球达成该出货量最高的智能手机品牌。得益于推出相当不错的 5G 智能手机组合，其在中国、东南亚等不同国家地区都有着相当不错的销量表现。</p><p class=\"p_style\">-苹果在 2020 年第三季度的出货量同比下降 7%，这是因为 iPhone 12 系列发布时间从第三季度推迟至第四季度，预计随着该系列的发布，苹果的业绩将会有不错的表现。</p><p class=\"p_style\">-得益于 Y 系列，vivo 在海外市场特别是东南亚市场的表现不错；OPPO 则是通过与运营商合作伙伴的关系来拓展欧洲市场，也与领先的体育赛事建立合作伙伴关系以扩大其在欧洲市场的影响力。</p><p class=\"jsmargin\"><img alt=\"微信图片_20201030102149.jpg\" src=\"http://pic.zaeke.com/img160402904767233.jpeg\" width=\"1780\" height=\"890\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">具体的数字以 Counterpoint 为例，其中华为包含荣耀，小米包含 Redmi：</p><p class=\"p_style\">-三星，7980 万台，同比 2%；</p><p class=\"p_style\">-华为，5090 万台，同比 -24%；</p><p class=\"p_style\">-小米，4620 万台，同比 46%；</p><p class=\"p_style\">-苹果，4170 万台，同比 -7%；</p><p class=\"p_style\">-OPPO，3100 万台，同比 -4；</p><p class=\"p_style\">-vivo，3100 万台，同比 -1%</p><p class=\"p_style\">-realme，1480 万台，同比 45%；</p><p class=\"p_style\">-联想，1020 万台，同比 2%；</p><p class=\"p_style\">-LG，650 万台，同比 -11%；</p><p class=\"p_style\">-传音，560 万台，同比 2%；</p><p class=\"p_style\"><br></p><p class=\"p_style\">Counterpoint：<a href=\"https://www.counterpointresearch.com/global-smartphone-market-shows-signs-recovery-q3-realme-grows-fastest-132-qoq/\" target=\"_blank\">戳我</a><br></p><p class=\"p_style\">IDC：<a href=\"https://www.idc.com/getdoc.jsp?containerId=prUS46974920\" target=\"_blank\">戳我</a></p><p class=\"p_style\">canalys：<a href=\"https://www.canalys.com/newsroom/canalys-worldwide-smartphone-market-q3-2020\" target=\"_blank\">戳我</a></p>', 'http://pic.zaeke.com/img160402903776709.png', 8626, 0, 0, NULL, '2020-10-29 19:44:25', '2021-05-04 22:31:12');
INSERT INTO `article` VALUES (15631, '蓝牙收发一体，飞傲 BTA30 让你的台机“无线”起来', '飞傲BTA30是个很难一次性搞明白的产品，即便是身经百战的我也得同时通过说明书以及官方操作视频的双重教育，才能搞明白这款它的全部用途。从功能上来说，飞傲BTA3...', '[object Object]', '烫头孙师傅', 38, 'http://pic.zaeke.com/user_1533281477_5.png', '<p>飞傲BTA30是个很难一次性搞明白的产品，即便是身经百战的我也得同时通过说明书以及官方操作视频的双重教育，才能搞明白这款它的全部用途。</p><p><img alt=\"591A5586.jpg\" src=\"http://pic.zaeke.com/img160448314463849.jpeg\" width=\"1400\" height=\"788\"><br></p><p>从功能上来说，飞傲BTA30的主要有三个：<strike>接、化、发</strike>接收、发射与DAC。通俗来说，它既可以作为Hi-Fi台式系统的蓝牙接收器，也可以将让你使用蓝牙耳机欣赏台式系统的音乐，亦或是直接作为电脑的DAC使用。</p><p>如此丰富的功能与集成度，让飞傲BTA30成为了台式系统用户蓝牙大跃进的最佳选择，以数百元成本换来整个系统的蓝牙化，确实是个很有吸引力的方案。</p><p style=\"text-align: center;\"><b style=\"font-size: medium;\">看似复杂，实则有用</b></p><p>在看完开头这段之后，相信你肯定对飞傲BTA30有一定的理解了。以我个人的理解来看，你可以将飞傲BTA30近似看做台式版的飞傲BTR系列。</p><p><img alt=\"591A5597.jpg\" src=\"http://pic.zaeke.com/img160448319048333.jpeg\" width=\"1400\" height=\"788\"><br></p><p>这款产品本质上依然是使用外置设备的方式将有线设备实现蓝牙化，因此它有着与蓝牙解码耳放接近的定位，只不过除了接收之外也有蓝牙发射与DAC功能，功能上相对来说更加完备。并且相较于飞傲BTR系列希望找回耳机孔的定位，飞傲BTA30做的是相对不同的为有线设备增加无线功能的思路。</p><p>为了保证高音质蓝牙需求，飞傲BTA30采用高通CSR8675蓝牙芯片，配备高性能音频DSP，并且拥有独立的AK 4490高性能DAC。并且这款产品还支持较新的蓝牙5.0，并且在外置增益天线的加持下能够实现最高30米内的稳定连接范围，远超常规蓝牙产品10米的距离。</p><p>并且飞傲BTA30还准备了音量调节快速且无偏音的旋钮，也有兼顾且观感优秀的铝合金机身，也支持蓝牙一拖二功能。</p><p><img alt=\"591A5599-编辑.jpg\" src=\"http://pic.zaeke.com/img160448392990331.jpeg\" width=\"1400\" height=\"788\"><br></p><p><img alt=\"591A5591.jpg\" src=\"http://pic.zaeke.com/img160448315330241.jpeg\" width=\"1400\" height=\"787\"><br></p><p><img alt=\"591A5596.jpg\" src=\"http://pic.zaeke.com/img160448317929118.jpeg\" width=\"1400\" height=\"788\"><br></p><p>面对各类台式系统的丰富接口，飞傲BTA30也同样配备了丰富的机身接口以满足需求。该机拥有RCA模拟输出接口、光纤/同轴输出接口、光纤/同轴输入接口与Type-C接口。并且其还能够通过RGB灯光来显示目前的蓝牙格式编码，无需通过APP查看，更加直观便捷。</p><p>针对而飞傲BTA30没有屏显的问题，其也可以通过连接APP的方式进行控制，例如声带、滤波器与蓝牙格式优先级等都可以调节，并且针对相关功能也有操作说明可供参考。</p><p><img alt=\"591A5551.jpg\" src=\"http://pic.zaeke.com/img160448305244485.jpeg\" width=\"1400\" height=\"788\"><br></p><p>但说到操作说明，还真就有槽可吐了。看完前面的内容你肯定会知道飞傲BTA30功能多且复杂。而飞傲提供的说明书内容则相对单薄，很多内容必须反复揣摩理解才能明白，并且部分功能我也是在结合	B站官方视频的辅助下才能够完全理解。</p><p>功能多显然是优点，但飞傲显然还得在说明书上再下点功夫。例如常见的蓝牙耳机都是通过在蓝牙列表选择的形式进行配对连接，而面对无操作界面的飞傲BTA30在初次上手时确实容易一筹莫展，若果能将具体情况下的连接配对方式以详细直观的形式体现在说明书中，显然可以尽可能降低上手成本。</p><p style=\"text-align: center;\"><b style=\"font-size: medium;\">不是万能，但很全能</b></p><p>作为一款蓝牙收发器，飞傲BTA30“收”的功能显然非常重要。毕竟通过这款收发器，我们可以让支持响应接口的台式系统获得便捷的蓝牙音源，举个简单的例子，大早上你不需要从床上爬起来便可以通过飞傲BTA30以蓝牙的方式让音响系统播放音乐。</p><p><img alt=\"591A5568.jpg\" src=\"http://pic.zaeke.com/img160448309392304.jpeg\" width=\"1400\" height=\"788\"><br></p><p><img alt=\"591A5557.jpg\" src=\"http://pic.zaeke.com/img160448306464189.jpeg\" width=\"1400\" height=\"788\"><br></p><p>换句话说，飞傲BTA30完全能够以数百元的成本，让传统形态的有线台式系统时代成为一套不错的蓝牙系统，无论是播放蓝牙音源还是输出。尽管这套系统的顶点始终限制于最高LDAC的瓶颈，可本来就以便利性为卖点的蓝牙，本来也就是音质与便利性之间的妥协嘛。</p><p>当然，考虑到我基本都是在办公室内使用飞傲BTR30，因此我也就直接将通过USB线材连接电脑，同时满足供电与音源的需求。此时电脑不仅为飞傲BTA30供电，也能够将其作为DAC使用。而拨动模式拨杆即可在DAC、蓝牙发射与蓝牙接收三个模式下切换。</p><p>在作为蓝牙接收器时，飞傲BTA30的使用方式与常规的蓝牙设备差不多，只需开启配对并在智能手机的蓝牙列表找到设备并成功配对即可。而在使用RCA线连接传统Hi-Fi音箱之后，便可以通过智能手机播放音乐了。</p><p>而从音质来说，如果你有相对高端的前级，那么音质有限的蓝牙传输或许会让听感下降相对明显。不过如果你像我一样以连接PC的话，那么蓝牙传输本身造成的音质损失就相对可以接受了，并且即便对于专业系统而言，蓝牙播放仅仅是拓展了全新的功能与适用场景，并不影响音响系统有线下的音质表现。</p><p><img alt=\"591A5560.jpg\" src=\"http://pic.zaeke.com/img160448307234971.jpeg\" width=\"1400\" height=\"788\"><br></p><p><img alt=\"591A5554.jpg\" src=\"http://pic.zaeke.com/img160448306167138.jpeg\" width=\"1400\" height=\"788\"><br></p><p>而更出乎我意料的是……飞傲BTA30它……它有低时延……</p><p>或许延迟表现或许没办法和顶级的真无线耳机相比，但飞傲BTA30的延迟表现甚至足以满足《穿越火线：枪战王者》的需求，声音反馈基本没有可明显感知的延迟，这也就意味你完全能够通过飞傲BTA30来享受低延迟的音箱级游戏音频体验。</p><p>当然对于飞傲BTA30的用户来说，这似乎是个餐后甜点式的功能，可对于一款针对台式系统的蓝牙首发器来说，在功能相对滞后于时代的台式系统上带来时髦的低时延蓝牙，本来就是一件好事。无论你是否真的会用音箱玩手游，低时延蓝牙都同样是可明确感知的体验提升。</p><p><img alt=\"591A5565.jpg\" src=\"http://pic.zaeke.com/img160448307785640.jpeg\" width=\"1400\" height=\"788\"><br></p><p><img alt=\"591A5577.jpg\" src=\"http://pic.zaeke.com/img160448312555302.jpeg\" width=\"1400\" height=\"788\"><br></p><p>而在作为蓝牙发射器的同时，飞傲BTA30可以便捷地连接诸如蓝牙音箱或蓝牙耳机一类的产品。只不过与智能手机那种需要通过弹窗或手动匹配的方式不同，其配对的方式是在调至蓝牙发射模式并打开收发器本身蓝牙配对之后，再将蓝牙耳机调节至配对模式，等待一段时间之后即可配对成功。</p><p>这个功能本身或许没啥稀奇的，毕竟电脑只要自带蓝牙就可以实现同样的功能，可除了电脑之外，飞傲BTA30能够输入的音源非常丰富多样，我们还能通过同轴和光纤接口输入音源，这也意味着台式系统也能直接直接接蓝牙耳机。</p><p>尽管这听着有些多此一举，但例如也不难设想出诸如走线不方便或是单纯希望听歌的时候能走动，毕竟飞傲BTA30那30米的连接距离，甚至足以让你戴着真无线联系折返跑。不过正经来说，让台式系统能够实现蓝牙发射本身并不影响有线下的表现，只是拓宽了使用场景。</p><p><img alt=\"591A5574.jpg\" src=\"http://pic.zaeke.com/img160448310857135.jpeg\" width=\"1400\" height=\"787\"><br></p><p><img alt=\"591A5576.jpg\" src=\"http://pic.zaeke.com/img160448311398310.jpeg\" width=\"1400\" height=\"788\"><br></p><p>况且蓝牙发射功能既可以让不支持蓝牙功能的台式系统或电脑能够便捷地连接蓝牙耳机，即便是真无线也有不错的体验，甚至还能够让拥有对应输出的设备都直接蓝牙化。例如支持想用接口的电视也能够便捷的使用真无线耳机，在不打扰家人的前提下独享电影又或是畅玩游戏。</p><p>总而言之，只要你想得到，飞傲BTA30在台式设备的蓝牙化上也基本能做得到。</p><p style=\"text-align: center;\"><b style=\"font-size: medium;\">被戳中痛点？那就对了</b></p><p>尽管要划分到Hi-Fi品类之中，但飞傲BTA30显然并不是款音质导向的产品，毕竟对于台式系统来说这或许是款音质降级型产品。可蓝牙也从来不是在音质上与有线系统相媲美的品类， 而是追求在音质与便利性上更完美的平衡。</p><p><img alt=\"591A5581.jpg\" src=\"http://pic.zaeke.com/img160448312822916.jpeg\" width=\"1400\" height=\"787\"><br></p><p>所以一款能够让台式耳机、音响系统用上手机流媒体音源、能够让台式Hi-Fi系统、电脑与电视连接蓝牙耳机，也能够作为有线DAC使用的蓝牙收发器，在功能的完备性与使用场景的多样性上就已经足以戳中很多人的痛点了。</p><p>或许有的人会觉得单纯的蓝牙收发器似乎没什么意义？可要知道的是，飞傲BTA30并不是款大众必需品型的产品，而是一款针对少数人痛点所打造的产品——如果你需要，那么这款售价678元（甚至双十一特价598元）的产品或许就是最优解。</p>', '<p class=\"p_style\">飞傲BTA30是个很难一次性搞明白的产品，即便是身经百战的我也得同时通过说明书以及官方操作视频的双重教育，才能搞明白这款它的全部用途。</p><p class=\"jsmargin\"><img alt=\"591A5586.jpg\" src=\"http://pic.zaeke.com/img160448314463849.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">从功能上来说，飞傲BTA30的主要有三个：<strike>接、化、发</strike>接收、发射与DAC。通俗来说，它既可以作为Hi-Fi台式系统的蓝牙接收器，也可以将让你使用蓝牙耳机欣赏台式系统的音乐，亦或是直接作为电脑的DAC使用。</p><p class=\"p_style\">如此丰富的功能与集成度，让飞傲BTA30成为了台式系统用户蓝牙大跃进的最佳选择，以数百元成本换来整个系统的蓝牙化，确实是个很有吸引力的方案。</p><p style=\"text-align: center;\" class=\"p_style\"><b style=\"font-size: medium;\">看似复杂，实则有用</b></p><p class=\"p_style\">在看完开头这段之后，相信你肯定对飞傲BTA30有一定的理解了。以我个人的理解来看，你可以将飞傲BTA30近似看做台式版的飞傲BTR系列。</p><p class=\"jsmargin\"><img alt=\"591A5597.jpg\" src=\"http://pic.zaeke.com/img160448319048333.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">这款产品本质上依然是使用外置设备的方式将有线设备实现蓝牙化，因此它有着与蓝牙解码耳放接近的定位，只不过除了接收之外也有蓝牙发射与DAC功能，功能上相对来说更加完备。并且相较于飞傲BTR系列希望找回耳机孔的定位，飞傲BTA30做的是相对不同的为有线设备增加无线功能的思路。</p><p class=\"p_style\">为了保证高音质蓝牙需求，飞傲BTA30采用高通CSR8675蓝牙芯片，配备高性能音频DSP，并且拥有独立的AK 4490高性能DAC。并且这款产品还支持较新的蓝牙5.0，并且在外置增益天线的加持下能够实现最高30米内的稳定连接范围，远超常规蓝牙产品10米的距离。</p><p class=\"p_style\">并且飞傲BTA30还准备了音量调节快速且无偏音的旋钮，也有兼顾且观感优秀的铝合金机身，也支持蓝牙一拖二功能。</p><p class=\"jsmargin\"><img alt=\"591A5599-编辑.jpg\" src=\"http://pic.zaeke.com/img160448392990331.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"591A5591.jpg\" src=\"http://pic.zaeke.com/img160448315330241.jpeg\" width=\"1400\" height=\"787\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"591A5596.jpg\" src=\"http://pic.zaeke.com/img160448317929118.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">面对各类台式系统的丰富接口，飞傲BTA30也同样配备了丰富的机身接口以满足需求。该机拥有RCA模拟输出接口、光纤/同轴输出接口、光纤/同轴输入接口与Type-C接口。并且其还能够通过RGB灯光来显示目前的蓝牙格式编码，无需通过APP查看，更加直观便捷。</p><p class=\"p_style\">针对而飞傲BTA30没有屏显的问题，其也可以通过连接APP的方式进行控制，例如声带、滤波器与蓝牙格式优先级等都可以调节，并且针对相关功能也有操作说明可供参考。</p><p class=\"jsmargin\"><img alt=\"591A5551.jpg\" src=\"http://pic.zaeke.com/img160448305244485.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">但说到操作说明，还真就有槽可吐了。看完前面的内容你肯定会知道飞傲BTA30功能多且复杂。而飞傲提供的说明书内容则相对单薄，很多内容必须反复揣摩理解才能明白，并且部分功能我也是在结合	B站官方视频的辅助下才能够完全理解。</p><p class=\"p_style\">功能多显然是优点，但飞傲显然还得在说明书上再下点功夫。例如常见的蓝牙耳机都是通过在蓝牙列表选择的形式进行配对连接，而面对无操作界面的飞傲BTA30在初次上手时确实容易一筹莫展，若果能将具体情况下的连接配对方式以详细直观的形式体现在说明书中，显然可以尽可能降低上手成本。</p><p style=\"text-align: center;\" class=\"p_style\"><b style=\"font-size: medium;\">不是万能，但很全能</b></p><p class=\"p_style\">作为一款蓝牙收发器，飞傲BTA30“收”的功能显然非常重要。毕竟通过这款收发器，我们可以让支持响应接口的台式系统获得便捷的蓝牙音源，举个简单的例子，大早上你不需要从床上爬起来便可以通过飞傲BTA30以蓝牙的方式让音响系统播放音乐。</p><p class=\"jsmargin\"><img alt=\"591A5568.jpg\" src=\"http://pic.zaeke.com/img160448309392304.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"591A5557.jpg\" src=\"http://pic.zaeke.com/img160448306464189.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">换句话说，飞傲BTA30完全能够以数百元的成本，让传统形态的有线台式系统时代成为一套不错的蓝牙系统，无论是播放蓝牙音源还是输出。尽管这套系统的顶点始终限制于最高LDAC的瓶颈，可本来就以便利性为卖点的蓝牙，本来也就是音质与便利性之间的妥协嘛。</p><p class=\"p_style\">当然，考虑到我基本都是在办公室内使用飞傲BTR30，因此我也就直接将通过USB线材连接电脑，同时满足供电与音源的需求。此时电脑不仅为飞傲BTA30供电，也能够将其作为DAC使用。而拨动模式拨杆即可在DAC、蓝牙发射与蓝牙接收三个模式下切换。</p><p class=\"p_style\">在作为蓝牙接收器时，飞傲BTA30的使用方式与常规的蓝牙设备差不多，只需开启配对并在智能手机的蓝牙列表找到设备并成功配对即可。而在使用RCA线连接传统Hi-Fi音箱之后，便可以通过智能手机播放音乐了。</p><p class=\"p_style\">而从音质来说，如果你有相对高端的前级，那么音质有限的蓝牙传输或许会让听感下降相对明显。不过如果你像我一样以连接PC的话，那么蓝牙传输本身造成的音质损失就相对可以接受了，并且即便对于专业系统而言，蓝牙播放仅仅是拓展了全新的功能与适用场景，并不影响音响系统有线下的音质表现。</p><p class=\"jsmargin\"><img alt=\"591A5560.jpg\" src=\"http://pic.zaeke.com/img160448307234971.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"591A5554.jpg\" src=\"http://pic.zaeke.com/img160448306167138.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">而更出乎我意料的是……飞傲BTA30它……它有低时延……</p><p class=\"p_style\">或许延迟表现或许没办法和顶级的真无线耳机相比，但飞傲BTA30的延迟表现甚至足以满足《穿越火线：枪战王者》的需求，声音反馈基本没有可明显感知的延迟，这也就意味你完全能够通过飞傲BTA30来享受低延迟的音箱级游戏音频体验。</p><p class=\"p_style\">当然对于飞傲BTA30的用户来说，这似乎是个餐后甜点式的功能，可对于一款针对台式系统的蓝牙首发器来说，在功能相对滞后于时代的台式系统上带来时髦的低时延蓝牙，本来就是一件好事。无论你是否真的会用音箱玩手游，低时延蓝牙都同样是可明确感知的体验提升。</p><p class=\"jsmargin\"><img alt=\"591A5565.jpg\" src=\"http://pic.zaeke.com/img160448307785640.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"591A5577.jpg\" src=\"http://pic.zaeke.com/img160448312555302.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">而在作为蓝牙发射器的同时，飞傲BTA30可以便捷地连接诸如蓝牙音箱或蓝牙耳机一类的产品。只不过与智能手机那种需要通过弹窗或手动匹配的方式不同，其配对的方式是在调至蓝牙发射模式并打开收发器本身蓝牙配对之后，再将蓝牙耳机调节至配对模式，等待一段时间之后即可配对成功。</p><p class=\"p_style\">这个功能本身或许没啥稀奇的，毕竟电脑只要自带蓝牙就可以实现同样的功能，可除了电脑之外，飞傲BTA30能够输入的音源非常丰富多样，我们还能通过同轴和光纤接口输入音源，这也意味着台式系统也能直接直接接蓝牙耳机。</p><p class=\"p_style\">尽管这听着有些多此一举，但例如也不难设想出诸如走线不方便或是单纯希望听歌的时候能走动，毕竟飞傲BTA30那30米的连接距离，甚至足以让你戴着真无线联系折返跑。不过正经来说，让台式系统能够实现蓝牙发射本身并不影响有线下的表现，只是拓宽了使用场景。</p><p class=\"jsmargin\"><img alt=\"591A5574.jpg\" src=\"http://pic.zaeke.com/img160448310857135.jpeg\" width=\"1400\" height=\"787\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"591A5576.jpg\" src=\"http://pic.zaeke.com/img160448311398310.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">况且蓝牙发射功能既可以让不支持蓝牙功能的台式系统或电脑能够便捷地连接蓝牙耳机，即便是真无线也有不错的体验，甚至还能够让拥有对应输出的设备都直接蓝牙化。例如支持想用接口的电视也能够便捷的使用真无线耳机，在不打扰家人的前提下独享电影又或是畅玩游戏。</p><p class=\"p_style\">总而言之，只要你想得到，飞傲BTA30在台式设备的蓝牙化上也基本能做得到。</p><p style=\"text-align: center;\" class=\"p_style\"><b style=\"font-size: medium;\">被戳中痛点？那就对了</b></p><p class=\"p_style\">尽管要划分到Hi-Fi品类之中，但飞傲BTA30显然并不是款音质导向的产品，毕竟对于台式系统来说这或许是款音质降级型产品。可蓝牙也从来不是在音质上与有线系统相媲美的品类， 而是追求在音质与便利性上更完美的平衡。</p><p class=\"jsmargin\"><img alt=\"591A5581.jpg\" src=\"http://pic.zaeke.com/img160448312822916.jpeg\" width=\"1400\" height=\"787\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">所以一款能够让台式耳机、音响系统用上手机流媒体音源、能够让台式Hi-Fi系统、电脑与电视连接蓝牙耳机，也能够作为有线DAC使用的蓝牙收发器，在功能的完备性与使用场景的多样性上就已经足以戳中很多人的痛点了。</p><p class=\"p_style\">或许有的人会觉得单纯的蓝牙收发器似乎没什么意义？可要知道的是，飞傲BTA30并不是款大众必需品型的产品，而是一款针对少数人痛点所打造的产品——如果你需要，那么这款售价678元（甚至双十一特价598元）的产品或许就是最优解。</p>', 'http://pic.zaeke.com/img160552175869413.jpeg', 15551, 0, 0, NULL, '2020-11-04 02:16:58', '2021-03-27 07:01:47');
INSERT INTO `article` VALUES (15639, '一加8T赛博朋克2077限定版图赏：欢迎来到夜之城', '虽然《赛博朋克2077》跳票了，可一加之前预告过的一加8T赛博朋克2077限定款没有。一加在这款限定机型上倾注了大量的设计和心血，和CDPROJEKTRED全面...', '[object Object]', 'victory3332', 31, 'http://pic.zaeke.com/user_1533281475_1.png', '<p>虽然《赛博朋克2077》跳票了，可一加之前预告过的一加8T赛博朋克2077限定款没有。一加在这款限定机型上倾注了大量的设计和心血，和CD PROJEKT RED全面且深度的合作让这台限定机型拥有所有限定款机型中数一数二的定制深度和诚意。</p><p><img src=\"http://pic.zaeke.com/img160457142881965.jpeg\" alt=\"_1082099.jpg\">  </p><p>“高科技，低生活”是“赛博朋克”理念的特点，这个理念是对未来世界观的一种展望。</p><p>未来社会科技高度发达，但同时社会阶级高度分化。生活在高度发达的科技社会中，可是生活水平却不高，这种冲突也被不少赛博朋克世界观电影反映。一加在设计这款一加8T赛博朋克2077限定版的机身设计时，这个理念被放在了设计语言最明显的位置。</p><p><img src=\"http://pic.zaeke.com/img160457123424725.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img160457123450365.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img160457123469509.jpeg\" alt=\"\">  </p><p>机身上“高科技”部分的代表，是机身后背顶部巨大的摄像头玻璃盖板。一加将此盖板命名为“全景视窗”，视窗中黄色的2077字样以及条形码努力展示出科技感。机身底部带有锻造碳纹理的抛光玻璃，则是反映游戏中夜之城复杂无序低生活的元素。夹在二者之间是一加善用的AG玻璃，代表“命中注定之外的未知”，和底部接合部分则是游戏中标志性的刀锋折线。</p><p>除了机身设计，一加8T赛博朋克2077限定版运行的氢OS 11也经过全面且深度的定制。系统中一方面是以霓虹灯管为设计灵感的图标和UI元素，另一方面则是来源于《赛博朋克2077》E3首秀的开机动画，以及富有科技感的黑客骇入充电动画。大量游戏元素的加入，让整机的未来感和科技感大幅度提高。</p><p><img src=\"http://pic.zaeke.com/img160457118044046.jpeg\" alt=\"\">  </p><p><img src=\"http://pic.zaeke.com/img160457147592536.jpeg\" alt=\"\">  </p><p><img src=\"http://pic.zaeke.com/img160457150767952.jpeg\" alt=\"\">  </p><p>最后，一加还专门为这款限定机型打造了专属的“夜之城”与“加州北部”相机滤镜。可用于拍摄静态照片或录制视频，录制视频时除了调色还会为视频增加画面抖动失真效果，看起来更加“赛博朋克”。随机附赠有专属定制荧光保护壳，日间吸收紫外线后会在夜间隐隐发亮，和游戏视觉元素相对应。</p><p>过足了眼瘾，想知道一加8T用起来怎么样？那么请点击阅读<a href=\"http://www.zaeke.com/article-15560.html\">《一加8T的方向对了，所以什么时候开始加速？</a>》吧。</p><p><strong>更多图赏：</strong></p><p><img src=\"http://pic.zaeke.com/img160457179632112.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img160457180585861.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img160457159477969.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img160457162672264.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img160457165446359.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img160457185279336.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img160457165831927.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img160457170278679.jpeg\" alt=\"\">  </p>', '<p class=\"p_style\">虽然《赛博朋克2077》跳票了，可一加之前预告过的一加8T赛博朋克2077限定款没有。一加在这款限定机型上倾注了大量的设计和心血，和CD PROJEKT RED全面且深度的合作让这台限定机型拥有所有限定款机型中数一数二的定制深度和诚意。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160457142881965.jpeg\" alt=\"_1082099.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">“高科技，低生活”是“赛博朋克”理念的特点，这个理念是对未来世界观的一种展望。</p><p class=\"p_style\">未来社会科技高度发达，但同时社会阶级高度分化。生活在高度发达的科技社会中，可是生活水平却不高，这种冲突也被不少赛博朋克世界观电影反映。一加在设计这款一加8T赛博朋克2077限定版的机身设计时，这个理念被放在了设计语言最明显的位置。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160457123424725.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160457123450365.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160457123469509.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">机身上“高科技”部分的代表，是机身后背顶部巨大的摄像头玻璃盖板。一加将此盖板命名为“全景视窗”，视窗中黄色的2077字样以及条形码努力展示出科技感。机身底部带有锻造碳纹理的抛光玻璃，则是反映游戏中夜之城复杂无序低生活的元素。夹在二者之间是一加善用的AG玻璃，代表“命中注定之外的未知”，和底部接合部分则是游戏中标志性的刀锋折线。</p><p class=\"p_style\">除了机身设计，一加8T赛博朋克2077限定版运行的氢OS 11也经过全面且深度的定制。系统中一方面是以霓虹灯管为设计灵感的图标和UI元素，另一方面则是来源于《赛博朋克2077》E3首秀的开机动画，以及富有科技感的黑客骇入充电动画。大量游戏元素的加入，让整机的未来感和科技感大幅度提高。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160457118044046.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160457147592536.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160457150767952.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">最后，一加还专门为这款限定机型打造了专属的“夜之城”与“加州北部”相机滤镜。可用于拍摄静态照片或录制视频，录制视频时除了调色还会为视频增加画面抖动失真效果，看起来更加“赛博朋克”。随机附赠有专属定制荧光保护壳，日间吸收紫外线后会在夜间隐隐发亮，和游戏视觉元素相对应。</p><p class=\"p_style\">过足了眼瘾，想知道一加8T用起来怎么样？那么请点击阅读<a href=\"http://www.zaeke.com/article-15560.html\">《一加8T的方向对了，所以什么时候开始加速？</a>》吧。</p><p class=\"p_style\"><strong>更多图赏：</strong></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160457179632112.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160457180585861.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160457159477969.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160457162672264.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160457165446359.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160457185279336.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160457165831927.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160457170278679.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p>', 'http://pic.zaeke.com/img160462614217368.jpeg', 13139, 0, 0, NULL, '2020-11-05 17:29:43', '2021-03-26 22:59:46');
INSERT INTO `article` VALUES (15640, '双十一骁龙 865 平台机型如何选?不妨先参考一下购机指南', '今年的“双十一”购物狂欢节比往年来得更早一些，当大家沉溺于抢购心头好物的时候，似乎不少人已经遗忘了“双十一光棍节”的初衷是年轻人展现单身为傲的一种娱乐性“节日”...', '[object Object]', '小啤', 170, 'http://pic.zaeke.com/user_1533281478_8.png', '<p>今年的“双十一”购物狂欢节比往年来得更早一些，当大家沉溺于抢购心头好物的时候，似乎不少人已经遗忘了“双十一光棍节”的初衷是年轻人展现单身为傲的一种娱乐性“节日”。当然，无论是形影不离的情侣，还是暂时形单影只的单身贵族，我们均需要一款足够优秀的手机提升生活品质。</p><p>考虑到高通骁龙 865 平台是目前 Android 阵营中表现最均衡的旗舰级 SoC，因此本文的推荐也主要环绕着该平台为主。</p><p style=\"text-align: center;\"><strong>以轻快铸就极致舒适度，一加 8T 凭高效体验取胜</strong></p><p>尽管集多项顶尖硬件于一身的旗舰机型，早已不必担心硬件瓶颈导致的系统卡顿情况，可是一加 8T 凭借极致简洁高效的氢 OS 系统和高通骁龙 865 平台加持，使其仍能营造出拔群出萃的顺滑流畅体验。对于追求极致轻快体验的用户而言，一加 8T 几乎是目前首选的产品之一，因为该机型在一加 8 的 90Hz 刷新率基础上提升为 120Hz 瞳孔屏，因此屏幕显示效果更加顺滑流畅。</p><p><img src=\"http://pic.zaeke.com/img160463056514288.jpeg\" alt=\"img160274765773841.jpg\">  </p><p>单凭 120Hz 瞳孔屏是不足以实现如此全面的流畅体验，其实一加 8T “轻快”体验的核心离不开高通骁龙 865 平台作为性能基础，使该机实现了最高 144Hz 的画面刷新率支持，今年 Android 阵营厂商如此积极地拥抱高刷屏，少不了新一代高通骁龙平台提供的技术支撑。除此，由于高通骁龙 865 平台集成的 Adreno 650 GPU 带来了 25% 的整体性能提升，支持最新一代 Snapdragon Elite Gaming 特性，使其不仅满足基本的系统流畅性之余，配合 True 10-bit HDR、超现实增强画质等技术，能够带来更加极致的游戏体验。这也不难理解，为何一加把高通骁龙 865 平台比喻为一颗“好胜的芯”，因为它无论是应对日常的系统流畅性，还是面对需要强劲算力的游戏时，它都能游刃有余。</p><p><img src=\"http://pic.zaeke.com/img160463056599997.jpeg\" alt=\"img160274711836244.jpg\">  </p><p>一加 8T 的快体验不局限于系统层面，其充电效率也得到了大幅度的升级，因为一加 8T 从一加 8 的 30W 提升至 65W 超级闪充，这就意味着 4500mAh&nbsp;的机身电池仅需要 15 分钟即可充电 58%；同样，升级的多重立体散热特性，也使该机型的散热效率得到了有效提升；仅有 188 克的机身重量，为便携性和握持体验提供了良好基础；一加打造氢 OS 的核心理念是“轻快流畅”，难免在功能丰富程度上有所担待，这是鱼与熊掌不可兼得的。</p><p><img src=\"http://pic.zaeke.com/img160463065431706.jpeg\" alt=\"img160457118044046.jpg\">  </p><p>“高效”是一加 8T 的主旋律，对于速度有着极致追求对于用户而言，一加 8T 是现阶段旗舰级别产品中对性价比也有所兼具的最优选。与此同时，一加与《赛博朋克2077》联合打造了一加 8T 赛博朋克 2077 限定版，该机型在外观以及 UI 等方面均作出了重新的调整，迎合了游戏爱好者对于手机个性化设计的需求。</p><p>一加 8T 官方指导价：8GB + 128GB 售价 3399 元；12GB + 256GB 售价  3699 元；12GB + 256GB 赛博朋克 2077 限定版售价 3999 元。  </p><p style=\"text-align: center;\"><strong>非凡的影音娱乐表现，造就小米 10 不俗的产品力</strong></p><p>也许很多人会好奇，为何我这次不直接推荐小米 10 至尊纪念版，反而推荐了这款已经发布了大半年的“老”旗舰。其实推荐小米 10 理由并不复杂，不仅只是考虑到该机型有着数百元的官方降价幅度，而且我相对看重的还有它不俗的外放表现。</p><p><img src=\"http://pic.zaeke.com/img160463070025186.jpeg\" alt=\"img158400408491556.jpg\">  </p><p>可以毫不夸张地说，小米 10 配备了小米迄今为止最强的外放系统，其采用的双 1216 超线性扬声器组合，不仅给予了整机对称式立体声效果，等效 1.0cc 大音腔也使其响度更大、音域更广的外放表现，配合音乐、电影等不同场景调校专属立体声场的优化，为该机的外放表现提供了扎实基础。对于喜欢使用手机欣赏音乐、观看流媒体视频、玩游戏的娱乐向用户而言，小米 10 的外放表现大概率不会让消费者败兴而归。</p><p><img src=\"http://pic.zaeke.com/img160463069981151.jpeg\" alt=\"img158400388059407.jpg\">  </p><p>在小米 10 出色的外放表现之下，其实高通骁龙 865 平台也起到了举足轻重的作用，该平台配合高通 WCD9380 音频编解码器，在低功耗的特性基础下实现了 Qualcomm Aqstic 声音技术的实现，为整机音质提供了强而有力的技术支撑。不过，考虑到双 1216 超线性扬声器对于手机内部的占用面积太大的缘故，因此下一款小米旗舰是否能保留该功能特性，目前仍是一个未知数。</p><p><img src=\"http://pic.zaeke.com/img160463070078664.jpeg\" alt=\"img158400435468751.jpg\">  </p><p>相比于以往的小米年度旗舰产品，小米 10 在影像方面也有了显著的提升，无论是硬件的选择和软件功能的研发都比以往有所加码。骁龙865集成的Spectre 480 ISP 支持高达每秒20亿像素的处理速度，可支持最高 2 亿像素的照片拍摄，再搭配作为移动传感器的第一阶梯产品的三星 ISOCELL Bright HMX 传感器，使小米 10 成为首批获得 1.08 亿像素拍摄能力“美誉”的旗舰机型。同时，凭借高通骁龙 865 平台第五代 AI Engine 的加持，实现与上代产品相比两倍以上的 AI 运算性能，带来了更丰富的 AI 拍摄应用。 </p><p>小米 10 官方指导价：8GB+128GB 售价 3499 元；8GB+256GB 售价 3699 元；12GB + 256GB 售价 4099 元。</p><p style=\"text-align: center;\"><strong>旗舰典范，Find X2 Pro 是 OPPO 对高端的理解</strong></p><p>距离 2020 年的结束已不足两个月，今年给我留下深刻印象的旗舰机型并不算太多，而 OPPO Find X2 Pro 是少数可以给我留下深刻印象的旗舰机型。</p><p><img src=\"http://pic.zaeke.com/img160463078266940.jpeg\" alt=\"img158787192551602.jpg\"></p><p>在大部分旗舰机型仍在坚守 1080p 分辨率规格的时候，OPPO Find X2 Pro 采用了一块 120Hz QHD+ 超感屏，凭借 3168×1440 的高分辨率优势，使它屏幕像素密度可实现 513 PPI，其画面细腻度自然就不言而喻。除此，再配合高通骁龙 865 平台 Snapdragon Elite Gaming 特性支持，使 OPPO Find X2 Pro 超感屏可以实现从传统的 1670 万色 8 bit 跃升至 10.7 亿色 8+2 bit，从而带来 64 倍的丰富色彩呈现。简而言之，就是屏幕的显示效果更佳了，再配合立体声双扬声器、杜比全景声、Motion Clear 视频动态插帧、HDR 视频画质增强等技术加持，其有足够实力摇身变成一款可欣赏高品质 HDR 视频内容的“口袋私人影院”。</p><p><img src=\"http://pic.zaeke.com/img160463078252562.jpeg\" alt=\"img158797825946893.jpg\">  </p><p>5G 时代也是速度的时代，凭借高通骁龙 865 平台搭配骁龙&nbsp;X55&nbsp;调制解调器，OPPO Find X2 Pro 可实现对 SA / NSA 双模 5G 的支持，实现了真正意义的全球畅行。理论上，该机型可以实现最高 5G + Wi-Fi&nbsp;最高  5.9 Gbps 的下行速度，而 5G&nbsp;最高下行速度可达到 4.1 Gbps。该机型对于速度的执念很深，从其突破性的 65W 超级闪充、主打轻快的 Color OS、120Hz 超感屏，还有基于骁龙 865 平台支持的 LPDDR5 + UFS 3.0 存储规格，使它轻松成为旗舰机型中的极致流畅代表机型之一。</p><p><img src=\"http://pic.zaeke.com/img160463078220205.jpeg\" alt=\"img158797917491771.jpg\">  </p><p>对于追求一定轻薄体验的潜在消费者而言，我推荐大家选购雅灰和茶橘两款素皮配色，因为该两个配色版本的重量约 200 克，而缎黑陶瓷版的机身重量约 217 克。</p><p>OPPO Find X2 Pro 官方指导价：12GB + 256GB 售价 5999 元。</p><p style=\"text-align: center;\"><strong>堆料十足的 iQOO 5 Pro，还是治愈续航焦虑小能手</strong></p><p>也许，很多人认为面面俱到是旗舰机型具备的要素。实际上，每个人对于手机的需求并不完全相同，旗舰机型上一些细微特性，都有可能成为吸引细分市场消费者的关键性因素。这不， iQOO 5 Pro 就专门针对手机续航焦虑的人群，提供了一个极具吸引力的解决方案。</p><p><img src=\"http://pic.zaeke.com/img160463082547976.jpeg\" alt=\"img159971105619501.jpg\">  </p><p>iQOO 5 Pro 并没有配备了远高于其它旗舰机型的大容量电池，其仅配备了相对常规的 4000mAh 电池，可是该机型采用了双 6C 电芯 + 双 IC 配置组合，使机身电池可通入更大的电流，从而实现了惊人的 120W 超快闪充。如此高功率的快充，随之而来的就是控温挑战，为了解决电池发热问题，该机型采用了创新性的推出了“MTW 阵列式极耳结构”，实现了电芯内部做了多次并联，大幅降低了正负极电阻，真正做到了从源头控制电池的发热。除此，通过独立的温控感应芯片，可随时智能调节电压电流的输入与输出。依附 120W 超快闪充，使得该机型最快可以实现 15% 分钟完全从无电量状态充至满电状态。</p><p><img src=\"http://pic.zaeke.com/img160463082624458.jpeg\" alt=\"img159971183983901.jpg\">  </p><p>同时，因为拥有高通骁龙 865 平台的加持，iQOO 5 Pro 配合 120Hz 高刷屏 + 240Hz 触控采样率，可以实现极致顺滑的系统流畅体验，配合超导 VC 液冷散热系统，给予长时间游戏需求的玩家提供了持续稳定的运行基础。为了提升手游玩家的上手体验，该机型还配备了鹰眼显示增强 2.0、4D 游戏震感、高帧率游戏专区、智能游戏伴侣，再配合立体声双扬声器，一定程度上实现了游戏中的听音辨位效果。</p><p><img src=\"http://pic.zaeke.com/img160463082624089.jpeg\" alt=\"img159971179785025.jpg\">  </p><p>在影音表现方面，其配备了 5000 万像素主摄 + 1300 万像素超广角 + 800 万像素潜望式长焦镜头后置三摄组合，基于 1/1.3\'\' 大底主摄，从根本提升了弱光环境下的拍摄表现。不仅如此，鉴于高通骁龙 865 平台内置的 Spectre 480 ISP 最高可以实现 8K 视频录制的特性，iQOO 5 Pro 顺势推出了 8K 电影拍摄功能，便于我们以更清晰的动态形式记录生活中的美好点滴。在弱光环境的拍摄能力上，充分利用高通骁龙 865 平台第五代 AI Engine 的技术支持，大幅度提升了夜景照片的生成效率，在光线复杂的环境下，需要有一套精准的算法尽可能还原我们肉眼所见的画面，这一切都是基于 AI 算法提供了足够充沛的算力支撑。</p><p>iQOO 5 Pro 5G 版官方指导价：8GB+256GB 赛道版&amp;传奇版售价 4998 元；12GB+256GB 赛道版&amp;传奇版 5498 元；特别版礼盒 12GB+256GB 赛道版 5498 元。</p><p style=\"text-align: center;\"><strong>年度均衡机皇，非三星 Galaxy S20 5G 莫属</strong></p><p>在不少人心目中，三星的年度旗舰机型代表着 Android 阵营旗舰标杆之作，这种立于标杆地位的固有印象，其实是三星多年以来凭借绝对领先的软硬结合实力换取回来的成果。</p><p><img src=\"http://pic.zaeke.com/img160463290747798.jpeg\" alt=\"img158145219192662.jpg\">  </p><p>Galaxy S20 5G 作为三星今年主打的高端机型产品，这款机型的硬件堆料依旧十足，首先其搭载了分辨率 3200 x 1440&nbsp;的 6.2 英寸 AMOLED 直角屏幕（或 6.1 英寸圆角屏幕），该屏幕获得了 HDR 10+&nbsp;的权威认证，在色彩呈现上提供了一定的基础。作为一款机身重量仅为 163 克的机型，三星 Galaxy S20 5G 实现了 15W 无线充电和 9W 反向无线充电，只不过该机型只能提供 25W 有线快充，对比国内大部分旗舰机型而言，其快充效率确实略显乏力。当然，对于追求极致轻巧机身体验的用户群体而言，三星 Galaxy S20 5G 无疑是首推机型，毕竟它机身重量仅有 163 克。</p><p><img src=\"http://pic.zaeke.com/img160463290795593.jpeg\" alt=\"img158145323514060.jpg\">  </p><p>影像表现同样是近年三星旗舰的“杀手锏”，三星 Galaxy S20 5G 矩阵三摄提供了 1200 万主摄 + 6400 万长焦摄像头 + 1200 万超广角摄像头组合，满足了用户对于不同拍摄场景的拍照所需，其中凭借高通骁龙 865 平台内置的 Spectre 480 ISP 与高通第五代人工智能引擎 AIEngine 配合，该机型可实现 960&nbsp;fps 超级慢动作视频录制，丰富了手机拍摄的创作乐趣。动态视频方面，三星 Galaxy S20 5G 也同样可以提供 8K 视频录制，实现了移动视频分辨率的蜕变，为以后 8K 分辨率设备做好充分准备。另外，通过 8K 视频快照功能，三星 Galaxy S20 5G 实现了从 8K 视频中获得 3300 万像素高清静态照片，做到了真正的动静皆宜。</p><p><img src=\"http://pic.zaeke.com/img160463290718552.jpeg\" alt=\"S20-X.jpg\">  </p><p>尽管配备了 12GB LPDDR5 内存规格，可是国行版本仅提供了 128GB 的机身存储空间可供选择，对于存储空间要求较高的用户，也可以选择官方自带的 micro SD 存储扩容进行扩大机身容量，一定程度上弥补了机身存储空间不足情况。</p><p>三星 Galaxy S20 5G 官方优惠价：12GB + 128GB 售价 5999 元。<br></p><p>本次双十一购机优选清单推荐机型分别为一加 8T 、小米 10、OPPO Find X2 Pro 、 iQOO 5 Pro、三星 Galaxy S20 5G 五款机型，它们均为搭载了高通骁龙 865 平台的旗舰级机型，也恰是采用了该 SoC 的缘故，所以这些机型均给消费者带来高刷特性屏幕、5G 网络技术支持、第五代 AI Engine 、Snapdragon Elite Gaming等前沿技术带来的旗舰级的用机体验。</p><p>通过高通骁龙 865 平台内置的 Spectre 480 ISP 与高通第五代人工智能引擎 AIEngine 配合，上述五款旗舰机型在拍照能力上也迎来了新高度，无论是超高像素传感器静态照片的处理能力，个别机型甚至可以实现 8K 视频拍摄和 960 fps 动态视频处理。基本的连接性方面，高通骁龙 865 搭配骁龙 X55 5G 调制解调器组合，除了支持5G 以外，还引入了 Wi-Fi 6 以及蓝牙超宽带语音的技术支持，真正用行动积极拥抱先进的无线接入技术。</p><p>上述五款机型均是现阶段相对值得选购的机型，考虑到双十一期间不少渠道也会有对应的降价促销活动，再配合一线城市的地方购机补贴政策，因此 2020 年双十一不失为一次换机好机遇。</p>', '<p class=\"p_style\">今年的“双十一”购物狂欢节比往年来得更早一些，当大家沉溺于抢购心头好物的时候，似乎不少人已经遗忘了“双十一光棍节”的初衷是年轻人展现单身为傲的一种娱乐性“节日”。当然，无论是形影不离的情侣，还是暂时形单影只的单身贵族，我们均需要一款足够优秀的手机提升生活品质。</p><p class=\"p_style\">考虑到高通骁龙 865 平台是目前 Android 阵营中表现最均衡的旗舰级 SoC，因此本文的推荐也主要环绕着该平台为主。</p><p style=\"text-align: center;\" class=\"p_style\"><strong>以轻快铸就极致舒适度，一加 8T 凭高效体验取胜</strong></p><p class=\"p_style\">尽管集多项顶尖硬件于一身的旗舰机型，早已不必担心硬件瓶颈导致的系统卡顿情况，可是一加 8T 凭借极致简洁高效的氢 OS 系统和高通骁龙 865 平台加持，使其仍能营造出拔群出萃的顺滑流畅体验。对于追求极致轻快体验的用户而言，一加 8T 几乎是目前首选的产品之一，因为该机型在一加 8 的 90Hz 刷新率基础上提升为 120Hz 瞳孔屏，因此屏幕显示效果更加顺滑流畅。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160463056514288.jpeg\" alt=\"img160274765773841.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">单凭 120Hz 瞳孔屏是不足以实现如此全面的流畅体验，其实一加 8T “轻快”体验的核心离不开高通骁龙 865 平台作为性能基础，使该机实现了最高 144Hz 的画面刷新率支持，今年 Android 阵营厂商如此积极地拥抱高刷屏，少不了新一代高通骁龙平台提供的技术支撑。除此，由于高通骁龙 865 平台集成的 Adreno 650 GPU 带来了 25% 的整体性能提升，支持最新一代 Snapdragon Elite Gaming 特性，使其不仅满足基本的系统流畅性之余，配合 True 10-bit HDR、超现实增强画质等技术，能够带来更加极致的游戏体验。这也不难理解，为何一加把高通骁龙 865 平台比喻为一颗“好胜的芯”，因为它无论是应对日常的系统流畅性，还是面对需要强劲算力的游戏时，它都能游刃有余。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160463056599997.jpeg\" alt=\"img160274711836244.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">一加 8T 的快体验不局限于系统层面，其充电效率也得到了大幅度的升级，因为一加 8T 从一加 8 的 30W 提升至 65W 超级闪充，这就意味着 4500mAh&nbsp;的机身电池仅需要 15 分钟即可充电 58%；同样，升级的多重立体散热特性，也使该机型的散热效率得到了有效提升；仅有 188 克的机身重量，为便携性和握持体验提供了良好基础；一加打造氢 OS 的核心理念是“轻快流畅”，难免在功能丰富程度上有所担待，这是鱼与熊掌不可兼得的。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160463065431706.jpeg\" alt=\"img160457118044046.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">“高效”是一加 8T 的主旋律，对于速度有着极致追求对于用户而言，一加 8T 是现阶段旗舰级别产品中对性价比也有所兼具的最优选。与此同时，一加与《赛博朋克2077》联合打造了一加 8T 赛博朋克 2077 限定版，该机型在外观以及 UI 等方面均作出了重新的调整，迎合了游戏爱好者对于手机个性化设计的需求。</p><p class=\"p_style\">一加 8T 官方指导价：8GB + 128GB 售价 3399 元；12GB + 256GB 售价  3699 元；12GB + 256GB 赛博朋克 2077 限定版售价 3999 元。  </p><p style=\"text-align: center;\" class=\"p_style\"><strong>非凡的影音娱乐表现，造就小米 10 不俗的产品力</strong></p><p class=\"p_style\">也许很多人会好奇，为何我这次不直接推荐小米 10 至尊纪念版，反而推荐了这款已经发布了大半年的“老”旗舰。其实推荐小米 10 理由并不复杂，不仅只是考虑到该机型有着数百元的官方降价幅度，而且我相对看重的还有它不俗的外放表现。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160463070025186.jpeg\" alt=\"img158400408491556.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">可以毫不夸张地说，小米 10 配备了小米迄今为止最强的外放系统，其采用的双 1216 超线性扬声器组合，不仅给予了整机对称式立体声效果，等效 1.0cc 大音腔也使其响度更大、音域更广的外放表现，配合音乐、电影等不同场景调校专属立体声场的优化，为该机的外放表现提供了扎实基础。对于喜欢使用手机欣赏音乐、观看流媒体视频、玩游戏的娱乐向用户而言，小米 10 的外放表现大概率不会让消费者败兴而归。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160463069981151.jpeg\" alt=\"img158400388059407.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">在小米 10 出色的外放表现之下，其实高通骁龙 865 平台也起到了举足轻重的作用，该平台配合高通 WCD9380 音频编解码器，在低功耗的特性基础下实现了 Qualcomm Aqstic 声音技术的实现，为整机音质提供了强而有力的技术支撑。不过，考虑到双 1216 超线性扬声器对于手机内部的占用面积太大的缘故，因此下一款小米旗舰是否能保留该功能特性，目前仍是一个未知数。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160463070078664.jpeg\" alt=\"img158400435468751.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">相比于以往的小米年度旗舰产品，小米 10 在影像方面也有了显著的提升，无论是硬件的选择和软件功能的研发都比以往有所加码。骁龙865集成的Spectre 480 ISP 支持高达每秒20亿像素的处理速度，可支持最高 2 亿像素的照片拍摄，再搭配作为移动传感器的第一阶梯产品的三星 ISOCELL Bright HMX 传感器，使小米 10 成为首批获得 1.08 亿像素拍摄能力“美誉”的旗舰机型。同时，凭借高通骁龙 865 平台第五代 AI Engine 的加持，实现与上代产品相比两倍以上的 AI 运算性能，带来了更丰富的 AI 拍摄应用。 </p><p class=\"p_style\">小米 10 官方指导价：8GB+128GB 售价 3499 元；8GB+256GB 售价 3699 元；12GB + 256GB 售价 4099 元。</p><p style=\"text-align: center;\" class=\"p_style\"><strong>旗舰典范，Find X2 Pro 是 OPPO 对高端的理解</strong></p><p class=\"p_style\">距离 2020 年的结束已不足两个月，今年给我留下深刻印象的旗舰机型并不算太多，而 OPPO Find X2 Pro 是少数可以给我留下深刻印象的旗舰机型。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160463078266940.jpeg\" alt=\"img158787192551602.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">在大部分旗舰机型仍在坚守 1080p 分辨率规格的时候，OPPO Find X2 Pro 采用了一块 120Hz QHD+ 超感屏，凭借 3168×1440 的高分辨率优势，使它屏幕像素密度可实现 513 PPI，其画面细腻度自然就不言而喻。除此，再配合高通骁龙 865 平台 Snapdragon Elite Gaming 特性支持，使 OPPO Find X2 Pro 超感屏可以实现从传统的 1670 万色 8 bit 跃升至 10.7 亿色 8+2 bit，从而带来 64 倍的丰富色彩呈现。简而言之，就是屏幕的显示效果更佳了，再配合立体声双扬声器、杜比全景声、Motion Clear 视频动态插帧、HDR 视频画质增强等技术加持，其有足够实力摇身变成一款可欣赏高品质 HDR 视频内容的“口袋私人影院”。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160463078252562.jpeg\" alt=\"img158797825946893.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">5G 时代也是速度的时代，凭借高通骁龙 865 平台搭配骁龙&nbsp;X55&nbsp;调制解调器，OPPO Find X2 Pro 可实现对 SA / NSA 双模 5G 的支持，实现了真正意义的全球畅行。理论上，该机型可以实现最高 5G + Wi-Fi&nbsp;最高  5.9 Gbps 的下行速度，而 5G&nbsp;最高下行速度可达到 4.1 Gbps。该机型对于速度的执念很深，从其突破性的 65W 超级闪充、主打轻快的 Color OS、120Hz 超感屏，还有基于骁龙 865 平台支持的 LPDDR5 + UFS 3.0 存储规格，使它轻松成为旗舰机型中的极致流畅代表机型之一。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160463078220205.jpeg\" alt=\"img158797917491771.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">对于追求一定轻薄体验的潜在消费者而言，我推荐大家选购雅灰和茶橘两款素皮配色，因为该两个配色版本的重量约 200 克，而缎黑陶瓷版的机身重量约 217 克。</p><p class=\"p_style\">OPPO Find X2 Pro 官方指导价：12GB + 256GB 售价 5999 元。</p><p style=\"text-align: center;\" class=\"p_style\"><strong>堆料十足的 iQOO 5 Pro，还是治愈续航焦虑小能手</strong></p><p class=\"p_style\">也许，很多人认为面面俱到是旗舰机型具备的要素。实际上，每个人对于手机的需求并不完全相同，旗舰机型上一些细微特性，都有可能成为吸引细分市场消费者的关键性因素。这不， iQOO 5 Pro 就专门针对手机续航焦虑的人群，提供了一个极具吸引力的解决方案。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160463082547976.jpeg\" alt=\"img159971105619501.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">iQOO 5 Pro 并没有配备了远高于其它旗舰机型的大容量电池，其仅配备了相对常规的 4000mAh 电池，可是该机型采用了双 6C 电芯 + 双 IC 配置组合，使机身电池可通入更大的电流，从而实现了惊人的 120W 超快闪充。如此高功率的快充，随之而来的就是控温挑战，为了解决电池发热问题，该机型采用了创新性的推出了“MTW 阵列式极耳结构”，实现了电芯内部做了多次并联，大幅降低了正负极电阻，真正做到了从源头控制电池的发热。除此，通过独立的温控感应芯片，可随时智能调节电压电流的输入与输出。依附 120W 超快闪充，使得该机型最快可以实现 15% 分钟完全从无电量状态充至满电状态。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160463082624458.jpeg\" alt=\"img159971183983901.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">同时，因为拥有高通骁龙 865 平台的加持，iQOO 5 Pro 配合 120Hz 高刷屏 + 240Hz 触控采样率，可以实现极致顺滑的系统流畅体验，配合超导 VC 液冷散热系统，给予长时间游戏需求的玩家提供了持续稳定的运行基础。为了提升手游玩家的上手体验，该机型还配备了鹰眼显示增强 2.0、4D 游戏震感、高帧率游戏专区、智能游戏伴侣，再配合立体声双扬声器，一定程度上实现了游戏中的听音辨位效果。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160463082624089.jpeg\" alt=\"img159971179785025.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">在影音表现方面，其配备了 5000 万像素主摄 + 1300 万像素超广角 + 800 万像素潜望式长焦镜头后置三摄组合，基于 1/1.3\'\' 大底主摄，从根本提升了弱光环境下的拍摄表现。不仅如此，鉴于高通骁龙 865 平台内置的 Spectre 480 ISP 最高可以实现 8K 视频录制的特性，iQOO 5 Pro 顺势推出了 8K 电影拍摄功能，便于我们以更清晰的动态形式记录生活中的美好点滴。在弱光环境的拍摄能力上，充分利用高通骁龙 865 平台第五代 AI Engine 的技术支持，大幅度提升了夜景照片的生成效率，在光线复杂的环境下，需要有一套精准的算法尽可能还原我们肉眼所见的画面，这一切都是基于 AI 算法提供了足够充沛的算力支撑。</p><p class=\"p_style\">iQOO 5 Pro 5G 版官方指导价：8GB+256GB 赛道版&amp;传奇版售价 4998 元；12GB+256GB 赛道版&amp;传奇版 5498 元；特别版礼盒 12GB+256GB 赛道版 5498 元。</p><p style=\"text-align: center;\" class=\"p_style\"><strong>年度均衡机皇，非三星 Galaxy S20 5G 莫属</strong></p><p class=\"p_style\">在不少人心目中，三星的年度旗舰机型代表着 Android 阵营旗舰标杆之作，这种立于标杆地位的固有印象，其实是三星多年以来凭借绝对领先的软硬结合实力换取回来的成果。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160463290747798.jpeg\" alt=\"img158145219192662.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">Galaxy S20 5G 作为三星今年主打的高端机型产品，这款机型的硬件堆料依旧十足，首先其搭载了分辨率 3200 x 1440&nbsp;的 6.2 英寸 AMOLED 直角屏幕（或 6.1 英寸圆角屏幕），该屏幕获得了 HDR 10+&nbsp;的权威认证，在色彩呈现上提供了一定的基础。作为一款机身重量仅为 163 克的机型，三星 Galaxy S20 5G 实现了 15W 无线充电和 9W 反向无线充电，只不过该机型只能提供 25W 有线快充，对比国内大部分旗舰机型而言，其快充效率确实略显乏力。当然，对于追求极致轻巧机身体验的用户群体而言，三星 Galaxy S20 5G 无疑是首推机型，毕竟它机身重量仅有 163 克。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160463290795593.jpeg\" alt=\"img158145323514060.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">影像表现同样是近年三星旗舰的“杀手锏”，三星 Galaxy S20 5G 矩阵三摄提供了 1200 万主摄 + 6400 万长焦摄像头 + 1200 万超广角摄像头组合，满足了用户对于不同拍摄场景的拍照所需，其中凭借高通骁龙 865 平台内置的 Spectre 480 ISP 与高通第五代人工智能引擎 AIEngine 配合，该机型可实现 960&nbsp;fps 超级慢动作视频录制，丰富了手机拍摄的创作乐趣。动态视频方面，三星 Galaxy S20 5G 也同样可以提供 8K 视频录制，实现了移动视频分辨率的蜕变，为以后 8K 分辨率设备做好充分准备。另外，通过 8K 视频快照功能，三星 Galaxy S20 5G 实现了从 8K 视频中获得 3300 万像素高清静态照片，做到了真正的动静皆宜。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160463290718552.jpeg\" alt=\"S20-X.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">尽管配备了 12GB LPDDR5 内存规格，可是国行版本仅提供了 128GB 的机身存储空间可供选择，对于存储空间要求较高的用户，也可以选择官方自带的 micro SD 存储扩容进行扩大机身容量，一定程度上弥补了机身存储空间不足情况。</p><p class=\"p_style\">三星 Galaxy S20 5G 官方优惠价：12GB + 128GB 售价 5999 元。<br></p><p class=\"p_style\">本次双十一购机优选清单推荐机型分别为一加 8T 、小米 10、OPPO Find X2 Pro 、 iQOO 5 Pro、三星 Galaxy S20 5G 五款机型，它们均为搭载了高通骁龙 865 平台的旗舰级机型，也恰是采用了该 SoC 的缘故，所以这些机型均给消费者带来高刷特性屏幕、5G 网络技术支持、第五代 AI Engine 、Snapdragon Elite Gaming等前沿技术带来的旗舰级的用机体验。</p><p class=\"p_style\">通过高通骁龙 865 平台内置的 Spectre 480 ISP 与高通第五代人工智能引擎 AIEngine 配合，上述五款旗舰机型在拍照能力上也迎来了新高度，无论是超高像素传感器静态照片的处理能力，个别机型甚至可以实现 8K 视频拍摄和 960 fps 动态视频处理。基本的连接性方面，高通骁龙 865 搭配骁龙 X55 5G 调制解调器组合，除了支持5G 以外，还引入了 Wi-Fi 6 以及蓝牙超宽带语音的技术支持，真正用行动积极拥抱先进的无线接入技术。</p><p class=\"p_style\">上述五款机型均是现阶段相对值得选购的机型，考虑到双十一期间不少渠道也会有对应的降价促销活动，再配合一线城市的地方购机补贴政策，因此 2020 年双十一不失为一次换机好机遇。</p>', 'http://pic.zaeke.com/img160463281945857.jpeg', 20974, 0, 0, NULL, '2020-11-05 19:22:26', '2021-03-27 04:39:14');
INSERT INTO `article` VALUES (15668, 'Redmi K30S 至尊纪念版评测：性能、价格与质感，便宜给你', 'RedmiK30S至尊纪念版是一款定位非常直接的机型：它就是为了在双十一期间好好赚大家一笔的。且慢，上面这段话可不是贬义。毕竟RedmiK30S至尊纪念版始终是...', '[object Object]', '烫头孙师傅', 38, 'http://pic.zaeke.com/user_1533281477_5.png', '<p>Redmi K30S 至尊纪念版是一款定位非常直接的机型：它就是为了在双十一期间好好赚大家一笔的。</p><p><img src=\"http://pic.zaeke.com/img160549267480740.jpeg\" alt=\"591A5472.jpg\">  </p><p>且慢，上面这段话可不是贬义。毕竟Redmi K30S 至尊纪念版始终是个实力派，为了让更多人愿意掏钱购买，该机提供了非常不错的性能配置与同样不错的用料做工，它吸引大家的方式是实实在在的实力与性价比。</p><p>对于追求顶级做工和顶级体验的用户来说，Redmi K30S 至尊纪念版似乎还差了那么一些，可对于这个价位的机型来说，无论在做工用料还是硬件功能上，这款机型都是毫无疑问的王者。</p><p style=\"text-align: center;\"><strong style=\"font-size: medium;\">同价位的盛世美颜</strong></p><p>无论是看起来还是摸起来亦或是设计美感上，Redmi K30S 至尊纪念版都精致得不像是这个价位的手机，或者说是这个价位上的Redmi手机所应该有的表现。</p><p><img src=\"http://pic.zaeke.com/img160549269419952.jpeg\" alt=\"591A5480.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160549267190615.jpeg\" alt=\"591A5468.jpg\">  </p><p>仅从颜值来看，这似乎是一个完全能够与小米10系列抗衡、甚至在某些方面美感略有超越的机型。不同于K30家族某些机型圆形张扬的相机模组，以及其上亮骚扎眼的同心圆花纹，这个采用麻将牌设计的后置模组在观感上非常和谐简洁，并且AG玻璃配合金属中框的设计，也让这款机型有着非常优秀的手感甚至是质感。</p><p>要知道的是。同价位的机型甚至还有正在使用塑料中框和塑料后盖的机型，仅仅是在手感上，Redmi K30S 至尊纪念版就能给我们惊喜。</p><p><img src=\"http://pic.zaeke.com/img160549270684372.jpeg\" alt=\"591A5484.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160549268253545.jpeg\" alt=\"591A5475.jpg\">  </p><p>但质感与做工上的惊喜仅仅是开始。在保证性价比的同时，Redmi K30S 至尊纪念版也采用了144Hz刷新率的LCD全面屏。这块屏幕虽然受限于成本并不能说观感于素质非常优秀，但在这个价位上他依然是合格且妥帖的。尽管在屏幕边缘以及打孔处依然能够看到背光不均的情况，但这也是这个价位的LCD屏幕的固有特性了。</p><p>可别忘了，之前的Redmi K30系列的部分机型是没有高刷新率屏幕的。如今的Redmi K30S 至尊纪念版在这点上进行了非常好的补充，并且是在保证一致性能的情况下带来这样的体验性提升，显然提升了产品力。毕竟还是那句老话，对于现在的智能手机来说高刷新率屏幕本身就是产品力的体现。</p><p>另外，可别忘了高刷新率屏幕对于续航也提出了更高的要求，而为了让Redmi K30S 至尊纪念版在高刷新率下也能够实现更好的续航表现，其这次还支持智能自适应刷新率，能够在不同的场景下手动调节屏幕刷新率。</p><p>从而在需要流畅视觉观感的时候给你流畅的视觉体验，而在那些无关紧要的时候通过就自动降低刷新率的方式来保证续航，这种高刷新率招之即来挥之即去的方式，确实很好地平衡了视觉流畅度与续航之间的矛盾。</p><p><img src=\"http://pic.zaeke.com/img160549265683848.jpeg\" alt=\"591A5466.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160549265286307.jpeg\" alt=\"591A5459.jpg\">  </p><p>而在性能上，Redmi K30S 至尊纪念版搭载高通骁龙865移动平台。光是这款旗舰移动平台或许没什么稀奇的，毕竟搭载这款移动平台的机型多如牛毛，并且它也几乎走到了自己产品周期的后期。可通过在这个情况下的后发优势，Redmi K30S 至尊纪念版拥有了极为强悍的性价比表现。</p><p>要知道同价位的机型可远来的没有手机这么全面，要么是拥有同等级的性能表现，但整体机身做工设计相对敷衍；要么则是拥有不错的精神做工用料，但整体性能表现却只能说差强人意。</p><p>只要看Redmi K30S 至尊纪念版上市之后，部分价位稍高的手机在双十一期间通过降价的方式来试图个获取同等竞争力的做法，就不难看出手机的性价比较表现究竟有多么强。毕竟如今这个价格可距离当年小米1999元起的初心，甚至都已经不远了。</p><p>从实际体验的角度来说，这款2K档位的机型在体验上也并不会和定位更高的机型在可感知的范围产生过于明显的差距。</p><p><img alt=\"591A5495.jpg\" src=\"http://pic.zaeke.com/img160558368330151.jpeg\" width=\"1400\" height=\"788\"><br></p><p><img src=\"http://pic.zaeke.com/img160549266294988.jpeg\" alt=\"591A5467.jpg\">  </p><p>例如尽管Redmi K30S 至尊纪念版的LCD屏幕不可避免会有拖影感，可144Hz高刷新率下的屏幕体验其实非常不错，尤其在游戏中的高帧率体验也非常流畅。并且难能可贵的是，得益于自适应刷新率的加成以及5000mAh的大电池，我在实际使用中并没有明显地在高帧率模式开启时感受到续航焦虑，这点对于同时在乎高刷新率和续航的用户来说感知也很明显。</p><p>并且大电池配合较好的续航能力，也让Redmi K30S 至尊纪念版足以成为一款不错的游戏手机。虽然不是顶级散热配置，不过只要你不追求顶级画质和顶级帧率不那么发热也绝对能够处在可接受的范围之内，无论是长时间游戏还是挂机都可以满足。考虑到性价比旗舰的消费者与手游爱好者或许有一定的重合，这样的表现也是非常妥帖的。</p><p>或者说光是LCD屏幕一个特性，就能成为不少人选择Redmi K30S 至尊纪念版的理由了，对吧？</p><p style=\"text-align: center;\"><strong style=\"font-size: medium;\">符合定位的拍照表现</strong></p><p>或许在两千元出头的性价比机型中，没人会太在乎拍照表现，不过Redmi K30S 至尊纪念版这次还是配备了了一个非常符合其定位、但表现还不错的相机模组。</p><p><img src=\"http://pic.zaeke.com/img160549261026706.jpeg\" alt=\"591A5436.jpg\">  </p><p>这个相机模组包括采用6400万像素索尼IMX682感光元件的主摄，以及一颗采用ov13b10感光元件的1300万像素超广角，以及一颗规格有些凑数的500万像素微距相机。</p><p>值得强调的是，与之前K30系列部分机型上的微距相机不同，这颗微距相机并不是所谓的50mm微距。</p><p><img src=\"http://pic.zaeke.com/img160558006890073.jpeg\" alt=\"IMG_20201110_142254.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160558005355879.jpeg\" alt=\"IMG_20201110_141406.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160558007623292.jpeg\" alt=\"IMG_20201110_144827.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160557997171050.jpeg\" alt=\"IMG_20201102_094334.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160558004234658.jpeg\" alt=\"IMG_20201110_135240.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160558002572232.jpeg\" alt=\"IMG_20201110_135231.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160558008987796.jpeg\" alt=\"IMG_20201110_145251.jpg\">  </p><p>先来说说主摄，与之前那几款同系列机型差不多，Redmi K30S 至尊纪念版拥有非常不错的画面细节与扎实度表现，锐度方面也在同规格机型中非常突出，并且对于画面噪点伪色色的控制也非常优秀。从这点上能够很明确地看出，Redmi的影像算法显然是在进步中。</p><p>而在色彩上，Redmi K30S 至尊纪念版在AI场景智能介入之后，在部分场景下也拥有相对鲜艳的表现。不过另一部分场景：例如建筑之的长江侠画面整体还是相对低饱和度与相对平实的。此外，Redmi K30S 至尊纪念版拍摄出的一部分色彩可能会在本机屏幕上出现非常明显的溢出，例如红色的花朵或红色的相机手绳溢出的概率相当大，绿色也有概率饱和度过高。</p><p><img src=\"http://pic.zaeke.com/img160558010922169.jpeg\" alt=\"IMG_20201116_192552.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160558012170991.jpeg\" alt=\"IMG_20201116_193031.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160558015248033.jpeg\" alt=\"IMG_20201116_193231.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160558011299652.jpeg\" alt=\"IMG_20201116_192806.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160557997122282.jpeg\" alt=\"IMG_20201110_114820.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160558009146393.jpeg\" alt=\"IMG_20201116_191958.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160558001262239.jpeg\" alt=\"IMG_20201110_114933.jpg\">  </p><p>而在夜景上，Redmi K30S 至尊纪念版依然保持着不错的画面细节与扎实度，并且整体的画面纯净度也可圈可点，可以说其夜景画质依然算是亮点。并且其也拥有非常不错的动态范围，无论是对暗部的提亮还是得高光的压制都相对靠谱吗，并且也没有出现什么明显的问题。</p><p>不过Redmi K30S 至尊纪念版在审美这件事上依然算不得高手。例如很多场景其实并不需要那么高的动态范围，并不是什么场景都需要提亮暗部或压制高光，有的时候就应该让高光过曝或让暗部丢失细节。我丝毫不怀疑小米目前在HDR上的实力与技术水平，但如何在更多的场景下提升出片的美感才是未来需要考虑的。</p><p>不过考虑到Redmi K30S 至尊纪念版仅仅是一个两千元出头的骁龙865机型，极致性价比之下，这些在影像算法的上的不足在很多人看来或许都是可以接受的。</p><p><img src=\"http://pic.zaeke.com/img160558027011439.jpeg\" alt=\"IMG_20201110_135947.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160558027119963.jpeg\" alt=\"IMG_20201024_130310.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160558029431507.jpeg\" alt=\"IMG_20201110_141411.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160558029430433.jpeg\" alt=\"IMG_20201110_144356.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160558027074627.jpeg\" alt=\"IMG_20201110_124736.jpg\">  </p><p>而在超广角相机上，这次Redmi K30S 至尊纪念版拥有与小米10至尊纪念版相同的优点，例如说整体画质虽然依然受限于感光元件本身的性能，不过其边角的伪色控制以及噪点控制都非常优秀，不再会出现那种边角暗部大范围发紫以及造点大颗粒成堆的情况。</p><p>而在色彩倾向上，其实这颗超广角相机也与主摄相对类似，因此不再赘述。不过值得强调的是，这颗超广角相机白平衡往往与主摄不怎么一致，不过考虑到这个相机模组上的焦段仅覆盖了超广角和主摄两个焦段，并且二者之间也没什么必要通过数码变焦进行衔接。所以这个问题其实总体照片不严重或者说感知不强。</p><p><img src=\"http://pic.zaeke.com/img160558031292115.jpeg\" alt=\"IMG_20201116_192351.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160558034576226.jpeg\" alt=\"IMG_20201116_192906.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160558032619941.jpeg\" alt=\"IMG_20201116_192637.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160558032563833.jpeg\" alt=\"IMG_20201116_192453.jpg\">  </p><p>另外这个超广角相机能够在光线较强的场景下保证非常不错的画面细节，不过在室内场景和夜景等各种低照度场景下其画面丢失相对严重，油画感也会比较明显。</p><p>但考虑到同价位或许还有那么一些机型是连超广角边角是的画质和伪色控制都保证不了，这颗硬件有限单算法上没什么毛病的超广角相机其实也非常合格了。</p><p><img src=\"http://pic.zaeke.com/img160549274574250.jpeg\" alt=\"591A5498.jpg\">  </p><p>至于剩下的那颗微距相机嘛，它既没有高规格也没有50毫米等效焦距以及超高放大倍率，那么这颗有点凑数的相机还是过了吧，知道它的存在就够了。</p><p style=\"text-align: center;\"><strong style=\"font-size: medium;\">打铁还需自身硬</strong></p><p>一款产品只要便宜到一个地步，那么它所有的缺点也就跟着消失不见了。或许Redmi K30S 至尊纪念版还做不到这样的水平，但是在这个价位上它已经提供给你非常优秀的性能与表现、体验及手感，那么这款机型的很多缺点也就逐渐能够被包容与接受。</p><p>毕竟天底下哪有这么多完美的手机，即便是小米的顶级旗舰也不能够做到十全十美。而在2000元出头的价位上，能够买到Redmi K30S 至尊纪念版这样的机型，已经是一件值得表扬的事情，也是花小钱办大事的典范。要知道，在硬件价格全面上涨的5G时代，能够以这个价格买到一款旗舰级性能的性价比机型的事实，就已经足够有 吸引力了，就更别说什么骁龙865、6400万像素主摄、Ag玻璃机身与金属中框这些喜闻乐见的东西了。</p><p><img src=\"http://pic.zaeke.com/img160549275179825.jpeg\" alt=\"591A5500.jpg\"></p><p>确实，小米只有在摆脱性价比桎梏之后，才能真正意义上在中高端市场放开手脚大干一场。可这并不意味着原来那些追求极致性价比的米粉就不不复存在，市场依然需要性能与性价比兼顾的机型，而Redmi K30S 至尊纪念版正是对这些老粉们最好的回应：“看看，我还是那个性价比王者。”</p><p>Redmi K30S 至尊纪念版看似是面对双十一市场的“定制机”，可骨子里却比谁都要更懂米粉，它便宜却不低质，性能、质感与体验全都在线，而这正是性价比机型爱好者们在5G时代无比期待的事情。</p>', '<p class=\"p_style\">Redmi K30S 至尊纪念版是一款定位非常直接的机型：它就是为了在双十一期间好好赚大家一笔的。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160549267480740.jpeg\" alt=\"591A5472.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">且慢，上面这段话可不是贬义。毕竟Redmi K30S 至尊纪念版始终是个实力派，为了让更多人愿意掏钱购买，该机提供了非常不错的性能配置与同样不错的用料做工，它吸引大家的方式是实实在在的实力与性价比。</p><p class=\"p_style\">对于追求顶级做工和顶级体验的用户来说，Redmi K30S 至尊纪念版似乎还差了那么一些，可对于这个价位的机型来说，无论在做工用料还是硬件功能上，这款机型都是毫无疑问的王者。</p><p style=\"text-align: center;\" class=\"p_style\"><strong style=\"font-size: medium;\">同价位的盛世美颜</strong></p><p class=\"p_style\">无论是看起来还是摸起来亦或是设计美感上，Redmi K30S 至尊纪念版都精致得不像是这个价位的手机，或者说是这个价位上的Redmi手机所应该有的表现。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160549269419952.jpeg\" alt=\"591A5480.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160549267190615.jpeg\" alt=\"591A5468.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">仅从颜值来看，这似乎是一个完全能够与小米10系列抗衡、甚至在某些方面美感略有超越的机型。不同于K30家族某些机型圆形张扬的相机模组，以及其上亮骚扎眼的同心圆花纹，这个采用麻将牌设计的后置模组在观感上非常和谐简洁，并且AG玻璃配合金属中框的设计，也让这款机型有着非常优秀的手感甚至是质感。</p><p class=\"p_style\">要知道的是。同价位的机型甚至还有正在使用塑料中框和塑料后盖的机型，仅仅是在手感上，Redmi K30S 至尊纪念版就能给我们惊喜。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160549270684372.jpeg\" alt=\"591A5484.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160549268253545.jpeg\" alt=\"591A5475.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">但质感与做工上的惊喜仅仅是开始。在保证性价比的同时，Redmi K30S 至尊纪念版也采用了144Hz刷新率的LCD全面屏。这块屏幕虽然受限于成本并不能说观感于素质非常优秀，但在这个价位上他依然是合格且妥帖的。尽管在屏幕边缘以及打孔处依然能够看到背光不均的情况，但这也是这个价位的LCD屏幕的固有特性了。</p><p class=\"p_style\">可别忘了，之前的Redmi K30系列的部分机型是没有高刷新率屏幕的。如今的Redmi K30S 至尊纪念版在这点上进行了非常好的补充，并且是在保证一致性能的情况下带来这样的体验性提升，显然提升了产品力。毕竟还是那句老话，对于现在的智能手机来说高刷新率屏幕本身就是产品力的体现。</p><p class=\"p_style\">另外，可别忘了高刷新率屏幕对于续航也提出了更高的要求，而为了让Redmi K30S 至尊纪念版在高刷新率下也能够实现更好的续航表现，其这次还支持智能自适应刷新率，能够在不同的场景下手动调节屏幕刷新率。</p><p class=\"p_style\">从而在需要流畅视觉观感的时候给你流畅的视觉体验，而在那些无关紧要的时候通过就自动降低刷新率的方式来保证续航，这种高刷新率招之即来挥之即去的方式，确实很好地平衡了视觉流畅度与续航之间的矛盾。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160549265683848.jpeg\" alt=\"591A5466.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160549265286307.jpeg\" alt=\"591A5459.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">而在性能上，Redmi K30S 至尊纪念版搭载高通骁龙865移动平台。光是这款旗舰移动平台或许没什么稀奇的，毕竟搭载这款移动平台的机型多如牛毛，并且它也几乎走到了自己产品周期的后期。可通过在这个情况下的后发优势，Redmi K30S 至尊纪念版拥有了极为强悍的性价比表现。</p><p class=\"p_style\">要知道同价位的机型可远来的没有手机这么全面，要么是拥有同等级的性能表现，但整体机身做工设计相对敷衍；要么则是拥有不错的精神做工用料，但整体性能表现却只能说差强人意。</p><p class=\"p_style\">只要看Redmi K30S 至尊纪念版上市之后，部分价位稍高的手机在双十一期间通过降价的方式来试图个获取同等竞争力的做法，就不难看出手机的性价比较表现究竟有多么强。毕竟如今这个价格可距离当年小米1999元起的初心，甚至都已经不远了。</p><p class=\"p_style\">从实际体验的角度来说，这款2K档位的机型在体验上也并不会和定位更高的机型在可感知的范围产生过于明显的差距。</p><p class=\"jsmargin\"><img alt=\"591A5495.jpg\" src=\"http://pic.zaeke.com/img160558368330151.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160549266294988.jpeg\" alt=\"591A5467.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">例如尽管Redmi K30S 至尊纪念版的LCD屏幕不可避免会有拖影感，可144Hz高刷新率下的屏幕体验其实非常不错，尤其在游戏中的高帧率体验也非常流畅。并且难能可贵的是，得益于自适应刷新率的加成以及5000mAh的大电池，我在实际使用中并没有明显地在高帧率模式开启时感受到续航焦虑，这点对于同时在乎高刷新率和续航的用户来说感知也很明显。</p><p class=\"p_style\">并且大电池配合较好的续航能力，也让Redmi K30S 至尊纪念版足以成为一款不错的游戏手机。虽然不是顶级散热配置，不过只要你不追求顶级画质和顶级帧率不那么发热也绝对能够处在可接受的范围之内，无论是长时间游戏还是挂机都可以满足。考虑到性价比旗舰的消费者与手游爱好者或许有一定的重合，这样的表现也是非常妥帖的。</p><p class=\"p_style\">或者说光是LCD屏幕一个特性，就能成为不少人选择Redmi K30S 至尊纪念版的理由了，对吧？</p><p style=\"text-align: center;\" class=\"p_style\"><strong style=\"font-size: medium;\">符合定位的拍照表现</strong></p><p class=\"p_style\">或许在两千元出头的性价比机型中，没人会太在乎拍照表现，不过Redmi K30S 至尊纪念版这次还是配备了了一个非常符合其定位、但表现还不错的相机模组。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160549261026706.jpeg\" alt=\"591A5436.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">这个相机模组包括采用6400万像素索尼IMX682感光元件的主摄，以及一颗采用ov13b10感光元件的1300万像素超广角，以及一颗规格有些凑数的500万像素微距相机。</p><p class=\"p_style\">值得强调的是，与之前K30系列部分机型上的微距相机不同，这颗微距相机并不是所谓的50mm微距。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558006890073.jpeg\" alt=\"IMG_20201110_142254.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558005355879.jpeg\" alt=\"IMG_20201110_141406.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558007623292.jpeg\" alt=\"IMG_20201110_144827.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160557997171050.jpeg\" alt=\"IMG_20201102_094334.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558004234658.jpeg\" alt=\"IMG_20201110_135240.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558002572232.jpeg\" alt=\"IMG_20201110_135231.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558008987796.jpeg\" alt=\"IMG_20201110_145251.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">先来说说主摄，与之前那几款同系列机型差不多，Redmi K30S 至尊纪念版拥有非常不错的画面细节与扎实度表现，锐度方面也在同规格机型中非常突出，并且对于画面噪点伪色色的控制也非常优秀。从这点上能够很明确地看出，Redmi的影像算法显然是在进步中。</p><p class=\"p_style\">而在色彩上，Redmi K30S 至尊纪念版在AI场景智能介入之后，在部分场景下也拥有相对鲜艳的表现。不过另一部分场景：例如建筑之的长江侠画面整体还是相对低饱和度与相对平实的。此外，Redmi K30S 至尊纪念版拍摄出的一部分色彩可能会在本机屏幕上出现非常明显的溢出，例如红色的花朵或红色的相机手绳溢出的概率相当大，绿色也有概率饱和度过高。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558010922169.jpeg\" alt=\"IMG_20201116_192552.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558012170991.jpeg\" alt=\"IMG_20201116_193031.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558015248033.jpeg\" alt=\"IMG_20201116_193231.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558011299652.jpeg\" alt=\"IMG_20201116_192806.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160557997122282.jpeg\" alt=\"IMG_20201110_114820.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558009146393.jpeg\" alt=\"IMG_20201116_191958.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558001262239.jpeg\" alt=\"IMG_20201110_114933.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">而在夜景上，Redmi K30S 至尊纪念版依然保持着不错的画面细节与扎实度，并且整体的画面纯净度也可圈可点，可以说其夜景画质依然算是亮点。并且其也拥有非常不错的动态范围，无论是对暗部的提亮还是得高光的压制都相对靠谱吗，并且也没有出现什么明显的问题。</p><p class=\"p_style\">不过Redmi K30S 至尊纪念版在审美这件事上依然算不得高手。例如很多场景其实并不需要那么高的动态范围，并不是什么场景都需要提亮暗部或压制高光，有的时候就应该让高光过曝或让暗部丢失细节。我丝毫不怀疑小米目前在HDR上的实力与技术水平，但如何在更多的场景下提升出片的美感才是未来需要考虑的。</p><p class=\"p_style\">不过考虑到Redmi K30S 至尊纪念版仅仅是一个两千元出头的骁龙865机型，极致性价比之下，这些在影像算法的上的不足在很多人看来或许都是可以接受的。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558027011439.jpeg\" alt=\"IMG_20201110_135947.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558027119963.jpeg\" alt=\"IMG_20201024_130310.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558029431507.jpeg\" alt=\"IMG_20201110_141411.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558029430433.jpeg\" alt=\"IMG_20201110_144356.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558027074627.jpeg\" alt=\"IMG_20201110_124736.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">而在超广角相机上，这次Redmi K30S 至尊纪念版拥有与小米10至尊纪念版相同的优点，例如说整体画质虽然依然受限于感光元件本身的性能，不过其边角的伪色控制以及噪点控制都非常优秀，不再会出现那种边角暗部大范围发紫以及造点大颗粒成堆的情况。</p><p class=\"p_style\">而在色彩倾向上，其实这颗超广角相机也与主摄相对类似，因此不再赘述。不过值得强调的是，这颗超广角相机白平衡往往与主摄不怎么一致，不过考虑到这个相机模组上的焦段仅覆盖了超广角和主摄两个焦段，并且二者之间也没什么必要通过数码变焦进行衔接。所以这个问题其实总体照片不严重或者说感知不强。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558031292115.jpeg\" alt=\"IMG_20201116_192351.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558034576226.jpeg\" alt=\"IMG_20201116_192906.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558032619941.jpeg\" alt=\"IMG_20201116_192637.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160558032563833.jpeg\" alt=\"IMG_20201116_192453.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">另外这个超广角相机能够在光线较强的场景下保证非常不错的画面细节，不过在室内场景和夜景等各种低照度场景下其画面丢失相对严重，油画感也会比较明显。</p><p class=\"p_style\">但考虑到同价位或许还有那么一些机型是连超广角边角是的画质和伪色控制都保证不了，这颗硬件有限单算法上没什么毛病的超广角相机其实也非常合格了。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160549274574250.jpeg\" alt=\"591A5498.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">至于剩下的那颗微距相机嘛，它既没有高规格也没有50毫米等效焦距以及超高放大倍率，那么这颗有点凑数的相机还是过了吧，知道它的存在就够了。</p><p style=\"text-align: center;\" class=\"p_style\"><strong style=\"font-size: medium;\">打铁还需自身硬</strong></p><p class=\"p_style\">一款产品只要便宜到一个地步，那么它所有的缺点也就跟着消失不见了。或许Redmi K30S 至尊纪念版还做不到这样的水平，但是在这个价位上它已经提供给你非常优秀的性能与表现、体验及手感，那么这款机型的很多缺点也就逐渐能够被包容与接受。</p><p class=\"p_style\">毕竟天底下哪有这么多完美的手机，即便是小米的顶级旗舰也不能够做到十全十美。而在2000元出头的价位上，能够买到Redmi K30S 至尊纪念版这样的机型，已经是一件值得表扬的事情，也是花小钱办大事的典范。要知道，在硬件价格全面上涨的5G时代，能够以这个价格买到一款旗舰级性能的性价比机型的事实，就已经足够有 吸引力了，就更别说什么骁龙865、6400万像素主摄、Ag玻璃机身与金属中框这些喜闻乐见的东西了。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160549275179825.jpeg\" alt=\"591A5500.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">确实，小米只有在摆脱性价比桎梏之后，才能真正意义上在中高端市场放开手脚大干一场。可这并不意味着原来那些追求极致性价比的米粉就不不复存在，市场依然需要性能与性价比兼顾的机型，而Redmi K30S 至尊纪念版正是对这些老粉们最好的回应：“看看，我还是那个性价比王者。”</p><p class=\"p_style\">Redmi K30S 至尊纪念版看似是面对双十一市场的“定制机”，可骨子里却比谁都要更懂米粉，它便宜却不低质，性能、质感与体验全都在线，而这正是性价比机型爱好者们在5G时代无比期待的事情。</p>', 'http://pic.zaeke.com/img160627170013341.jpeg', 14666, 0, 0, NULL, '2020-11-15 19:43:05', '2021-03-27 06:24:51');
INSERT INTO `article` VALUES (15709, '全能只是表象，自我突破才是小米Air2 Pro的核心', '小米的真无线耳机是从相对入门的市场起步的，不过伴随着真无线这类产品逐渐普及，定位相对较高的产品也就随之而来了。无论从定价还是定位上来说，小米Air2Pro在小米...', '[object Object]', '烫头孙师傅', 38, 'http://pic.zaeke.com/user_1533281477_5.png', '<p>小米的真无线耳机是从相对入门的市场起步的，不过伴随着真无线这类产品逐渐普及，定位相对较高的产品也就随之而来了。</p><p><img alt=\"591A5824.jpg\" src=\"http://pic.zaeke.com/img160647246647097.jpeg\" width=\"1400\" height=\"788\"><br></p><p>无论从定价还是定位上来说，小米Air2 Pro在小米的真无线耳机品类中都处在最顶点。尽管这个价格并非真正的高端，但小米Air2 Pro依然是一款功能足够全面，音质足以满足需求的产品，并且相对较高的性价比也有助于高品质真无线耳机的普及。</p><p>所以让我们放宽些心，或许小米Air2 Pro并不是最好的产品，不过却可能是最适合你的那款，对吧？</p><p style=\"text-align: center;\"><b>又一款全能型真无线</b></p><p>其实小米Air2 Pro并不是一款小米生态链产品，但长得确实很生态链。小米Air2 Pro依然是那种颜色相对偏向黑白简约，外观上以纯粹几何形状为主的设计。</p><p><img alt=\"591A5803.jpg\" src=\"http://pic.zaeke.com/img160647209099117.jpeg\" width=\"1400\" height=\"788\"><br></p><p><img alt=\"591A5811.jpg\" src=\"http://pic.zaeke.com/img160647211188761.jpeg\" width=\"1400\" height=\"788\"><br></p><p>这样的设计确实很小米，毕竟小米曾经以没有设计就是最好的设计来当做设计理念，这款以纯粹几何形体外形耳机也确实很对的上这个概念。只不过要严格以美感来说，这款耳机的设计就显得过于普通，毕竟简单的几何外形很难说有足够讨人喜欢的元素，设计上只能说简约却并不能说优秀。</p><p>并且对于对产品质感有要求的用户来说，小米Air2 Pro的充电盒与耳机腔体塑料感都太重了。市面上常见真无线其实大多数都是以塑料材质或塑料搭配金属的材质为主，可小米Air2 Pro所采用的塑料材质会给人一种单薄、粗糙且廉价的感受，而手感相对较好的真无线则能给人一种扎实感与品质感。</p><p>考虑到便宜的价格以及极为全面的功能，我们显然并不能对小米Air2 Pro做太多的要求。只不过对于一款产品来说，做工与质感肯定也是产品体验的重要组成部分，如果有下一代或是定位更高的产品，那么小米在做工与质感上显然也要做得更好才行，毕竟相近价位的产品质感往往都更好。</p><p><img alt=\"591A5808.jpg\" src=\"http://pic.zaeke.com/img160647209064855.jpeg\" width=\"1400\" height=\"788\"><br></p><p><img alt=\"591A5822.jpg\" src=\"http://pic.zaeke.com/img160647215761953.jpeg\" width=\"1400\" height=\"788\"><br></p><p>在腔体内部，小米Air2 Pro配备了12mm液晶动圈单元以保证音质，并且通过反馈、前馈与通话三个麦克风同时保证通话与降噪。而充电盒部分除了数据接口外，还支持Qi标准无线快充，在关闭降噪时单次续航7小时，而配合充电盒的整体续航则为28小时。</p><p>另外小米Air2 Pro的耳机套在出厂前或许并没有经过比较全面的人体工程学测试，毕竟其耳机套在广泛的网络反馈中很多人都认为无法稳固佩戴。我也不例外，无论是哪个尺寸的耳机套，我在戴上之后头部的移动动作照耀稍大便立即会脱落，而且不是松动而是整个腔体直接跌落。所幸小米后期也提供了可供选择的新耳机套，可以更换以增强佩戴稳固性。</p><p><img alt=\"591A5813.jpg\" src=\"http://pic.zaeke.com/img160647211927719.jpeg\" width=\"1400\" height=\"788\"><br></p><p><img alt=\"591A5810.jpg\" src=\"http://pic.zaeke.com/img160647210973114.jpeg\" width=\"1400\" height=\"788\"><br></p><p>而在操控上，小米Air2 Pro的腔体拥有陶瓷背盖，能够通过敲击与长按等方式来控制，此外也支持取下自动暂停功能。而通过内置的小爱同学，小米Air2 Pro也能够与智能手机的功能联动，从而实现更加丰富的功能体验，并且其也支持MIUI弹窗连接功能。</p><p>总体来说，小米Air2 Pro同样是一款功能非常齐全的真无线耳机，它或许不能够在音质、体验或降噪等单一项目上做到同价位一流水准，但该有的功能都有。而从这点来看，小米Air2 Pro显然也是一款骨子里很小米的产品。</p><p style=\"text-align: center;\"><b>音质好，降噪更好</b></p><p>作为一款耳机，我们不如先来聊聊音质。</p><p>小米Air2 Pro的声音属于真无线耳机中少数没有向市场妥协的风格。要知道绝大多数真无线耳机为了适应消费级市场的听音需求，往往在低频上都提供了较大的量感，整体风格表现并不均衡。而小米Air2 Pro则是在真无线中少见的均衡主义者，整体听感也还不错。</p><p><img alt=\"591A6072.jpg\" src=\"http://pic.zaeke.com/img160647229542136.jpeg\" width=\"1400\" height=\"788\"><br></p><p><img alt=\"591A6038.jpg\" src=\"http://pic.zaeke.com/img160647221794760.jpeg\" width=\"1400\" height=\"788\"><br></p><p>从三频来说，小米Air2 Pro的低频量感并不重但有一定的存在感，甚至可以认为是低频相对较少的那种类型。但相对较少的低频并不会影响它整体的听感，而是说更适合那些喜欢清丽风格的用户，况且其中频尤其是人声有着不错的厚度与顺滑度，尽管人声并不贴耳，但合格的分离度与解析力也显得并不拉胯。</p><p>尽管高频部分或许会稍显暗淡些，但这依然不影响小米Air2 Pro整体的风格，它依然是一款非常清新的耳机。或者换一种说法，如果说那些低频较多且高频刺激的耳机在久听之后很容易感到疲倦的话，那么小米Air2 Pro显然是那种久听不累的类型。</p><p><img alt=\"591A6037.jpg\" src=\"http://pic.zaeke.com/img160647220541333.jpeg\" width=\"1400\" height=\"788\"><br></p><p><img alt=\"591A6041.jpg\" src=\"http://pic.zaeke.com/img160647223072349.jpeg\" width=\"1400\" height=\"788\"><br></p><p>要知道，以往小米音频产品的调音在音频爱好者耳中往往都不太受待见。而这回的小米Air2 Pro则在调音以及音质表现上，都做到了同价位真无线耳机中相对优秀的水准，这也就意味着如今小米的真无线耳机可以不只聊体验与性价比，而是有了和用户聊聊音质的实力。</p><p>对于小米来说这肯定是一件好事。虽然说真无线降噪耳机最重要的功能，依然是真无线下的体验以及降噪表现，但谁都不希望与此同时在音质上付出过大的牺牲。因此在保证作为真无线的体验以及降噪表现的优异性后，音质表现的提升显然增加了小米Air2 Pro的竞争力。</p><p>况且相较于友商一些低频量感过重、中高频刺激性过强的耳机，小米Air2 Pro清丽温吞的风格则更有利于长时间听音，听久了也不会觉得太累。虽然说这样的调音风格某种程度上或许会缺乏一些特色，但对于真无线耳机这种日常使用频率较高、使用时间较长的产品来说，久听不累调音风格其实也是一个加分点。</p><p><img alt=\"591A6044.jpg\" src=\"http://pic.zaeke.com/img160647223061623.jpeg\" width=\"1400\" height=\"788\"><br></p><p>而在降噪方面小米Air2 Pro的表现可以说令人惊喜。对于交通工具的噪声，例如飞机发动机轰鸣或是地铁运行中的轰鸣声，小米Air2 Pro都能够有效降低低频噪声，而对于空调或风扇这样的室内轻度规律噪声基本可以完全消除。只不过对于人声与键盘声这样包含一定中高频噪声的尖锐声音来说，主动降噪依然还是有些爱莫能助，且抗风噪能力一般。</p><p>从降噪水平来说，我认为小米Air2 Pro处在同价位真无线的一流水准，甚至即便是越级比较也不会显得太吃亏，毕竟这块真无线在降噪上的表现确实足够好，或者说很超值。并且小米Air2 Pro也同样支持通透模式，因此在使用中也能够听到环境中的声音，从而尽可能避免因降噪误事或遭遇危险。</p><p style=\"text-align: center;\"><b>高端之路，未来可期</b></p><p>作为一款降噪真无线耳机，小米Air2 Pro的功能全面性的确值得表扬。这是一款涵盖了目前真无线耳机绝大多数功能点的产品，无论是降噪、无线充电还是弹窗连接之类的功能都没有缺席，满足了更多人的更多需求。</p><p><img alt=\"591A6074.jpg\" src=\"http://pic.zaeke.com/img160647230559029.jpeg\" width=\"1400\" height=\"788\"><br></p><p>更重要的是，小米Air2 Pro作为一款降噪耳机，同时也保持着不错的音质表现。对于在音频上表现并不突出的小米而言，打造出这样音质可靠的真无线耳机，不仅仅是满足耳机最基础功能的需求，同时也是小米对于自身的突破。</p><p>所以无论是对于用户还是小米本身而言，小米Air2 Pro都是值得肯定的。小米在音频上实现进步，而用户成为进步本身的受益者，完全可以说是双赢。只不过要是未来小米还希望在定位上更进一步，与真正意义上的高端产品一较高下，那么设计与质感可以说就是未来需要探讨的话题了。</p>', '<p class=\"p_style\">小米的真无线耳机是从相对入门的市场起步的，不过伴随着真无线这类产品逐渐普及，定位相对较高的产品也就随之而来了。</p><p class=\"jsmargin\"><img alt=\"591A5824.jpg\" src=\"http://pic.zaeke.com/img160647246647097.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">无论从定价还是定位上来说，小米Air2 Pro在小米的真无线耳机品类中都处在最顶点。尽管这个价格并非真正的高端，但小米Air2 Pro依然是一款功能足够全面，音质足以满足需求的产品，并且相对较高的性价比也有助于高品质真无线耳机的普及。</p><p class=\"p_style\">所以让我们放宽些心，或许小米Air2 Pro并不是最好的产品，不过却可能是最适合你的那款，对吧？</p><p style=\"text-align: center;\" class=\"p_style\"><b>又一款全能型真无线</b></p><p class=\"p_style\">其实小米Air2 Pro并不是一款小米生态链产品，但长得确实很生态链。小米Air2 Pro依然是那种颜色相对偏向黑白简约，外观上以纯粹几何形状为主的设计。</p><p class=\"jsmargin\"><img alt=\"591A5803.jpg\" src=\"http://pic.zaeke.com/img160647209099117.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"591A5811.jpg\" src=\"http://pic.zaeke.com/img160647211188761.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">这样的设计确实很小米，毕竟小米曾经以没有设计就是最好的设计来当做设计理念，这款以纯粹几何形体外形耳机也确实很对的上这个概念。只不过要严格以美感来说，这款耳机的设计就显得过于普通，毕竟简单的几何外形很难说有足够讨人喜欢的元素，设计上只能说简约却并不能说优秀。</p><p class=\"p_style\">并且对于对产品质感有要求的用户来说，小米Air2 Pro的充电盒与耳机腔体塑料感都太重了。市面上常见真无线其实大多数都是以塑料材质或塑料搭配金属的材质为主，可小米Air2 Pro所采用的塑料材质会给人一种单薄、粗糙且廉价的感受，而手感相对较好的真无线则能给人一种扎实感与品质感。</p><p class=\"p_style\">考虑到便宜的价格以及极为全面的功能，我们显然并不能对小米Air2 Pro做太多的要求。只不过对于一款产品来说，做工与质感肯定也是产品体验的重要组成部分，如果有下一代或是定位更高的产品，那么小米在做工与质感上显然也要做得更好才行，毕竟相近价位的产品质感往往都更好。</p><p class=\"jsmargin\"><img alt=\"591A5808.jpg\" src=\"http://pic.zaeke.com/img160647209064855.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"591A5822.jpg\" src=\"http://pic.zaeke.com/img160647215761953.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">在腔体内部，小米Air2 Pro配备了12mm液晶动圈单元以保证音质，并且通过反馈、前馈与通话三个麦克风同时保证通话与降噪。而充电盒部分除了数据接口外，还支持Qi标准无线快充，在关闭降噪时单次续航7小时，而配合充电盒的整体续航则为28小时。</p><p class=\"p_style\">另外小米Air2 Pro的耳机套在出厂前或许并没有经过比较全面的人体工程学测试，毕竟其耳机套在广泛的网络反馈中很多人都认为无法稳固佩戴。我也不例外，无论是哪个尺寸的耳机套，我在戴上之后头部的移动动作照耀稍大便立即会脱落，而且不是松动而是整个腔体直接跌落。所幸小米后期也提供了可供选择的新耳机套，可以更换以增强佩戴稳固性。</p><p class=\"jsmargin\"><img alt=\"591A5813.jpg\" src=\"http://pic.zaeke.com/img160647211927719.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"591A5810.jpg\" src=\"http://pic.zaeke.com/img160647210973114.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">而在操控上，小米Air2 Pro的腔体拥有陶瓷背盖，能够通过敲击与长按等方式来控制，此外也支持取下自动暂停功能。而通过内置的小爱同学，小米Air2 Pro也能够与智能手机的功能联动，从而实现更加丰富的功能体验，并且其也支持MIUI弹窗连接功能。</p><p class=\"p_style\">总体来说，小米Air2 Pro同样是一款功能非常齐全的真无线耳机，它或许不能够在音质、体验或降噪等单一项目上做到同价位一流水准，但该有的功能都有。而从这点来看，小米Air2 Pro显然也是一款骨子里很小米的产品。</p><p style=\"text-align: center;\" class=\"p_style\"><b>音质好，降噪更好</b></p><p class=\"p_style\">作为一款耳机，我们不如先来聊聊音质。</p><p class=\"p_style\">小米Air2 Pro的声音属于真无线耳机中少数没有向市场妥协的风格。要知道绝大多数真无线耳机为了适应消费级市场的听音需求，往往在低频上都提供了较大的量感，整体风格表现并不均衡。而小米Air2 Pro则是在真无线中少见的均衡主义者，整体听感也还不错。</p><p class=\"jsmargin\"><img alt=\"591A6072.jpg\" src=\"http://pic.zaeke.com/img160647229542136.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"591A6038.jpg\" src=\"http://pic.zaeke.com/img160647221794760.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">从三频来说，小米Air2 Pro的低频量感并不重但有一定的存在感，甚至可以认为是低频相对较少的那种类型。但相对较少的低频并不会影响它整体的听感，而是说更适合那些喜欢清丽风格的用户，况且其中频尤其是人声有着不错的厚度与顺滑度，尽管人声并不贴耳，但合格的分离度与解析力也显得并不拉胯。</p><p class=\"p_style\">尽管高频部分或许会稍显暗淡些，但这依然不影响小米Air2 Pro整体的风格，它依然是一款非常清新的耳机。或者换一种说法，如果说那些低频较多且高频刺激的耳机在久听之后很容易感到疲倦的话，那么小米Air2 Pro显然是那种久听不累的类型。</p><p class=\"jsmargin\"><img alt=\"591A6037.jpg\" src=\"http://pic.zaeke.com/img160647220541333.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"591A6041.jpg\" src=\"http://pic.zaeke.com/img160647223072349.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">要知道，以往小米音频产品的调音在音频爱好者耳中往往都不太受待见。而这回的小米Air2 Pro则在调音以及音质表现上，都做到了同价位真无线耳机中相对优秀的水准，这也就意味着如今小米的真无线耳机可以不只聊体验与性价比，而是有了和用户聊聊音质的实力。</p><p class=\"p_style\">对于小米来说这肯定是一件好事。虽然说真无线降噪耳机最重要的功能，依然是真无线下的体验以及降噪表现，但谁都不希望与此同时在音质上付出过大的牺牲。因此在保证作为真无线的体验以及降噪表现的优异性后，音质表现的提升显然增加了小米Air2 Pro的竞争力。</p><p class=\"p_style\">况且相较于友商一些低频量感过重、中高频刺激性过强的耳机，小米Air2 Pro清丽温吞的风格则更有利于长时间听音，听久了也不会觉得太累。虽然说这样的调音风格某种程度上或许会缺乏一些特色，但对于真无线耳机这种日常使用频率较高、使用时间较长的产品来说，久听不累调音风格其实也是一个加分点。</p><p class=\"jsmargin\"><img alt=\"591A6044.jpg\" src=\"http://pic.zaeke.com/img160647223061623.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">而在降噪方面小米Air2 Pro的表现可以说令人惊喜。对于交通工具的噪声，例如飞机发动机轰鸣或是地铁运行中的轰鸣声，小米Air2 Pro都能够有效降低低频噪声，而对于空调或风扇这样的室内轻度规律噪声基本可以完全消除。只不过对于人声与键盘声这样包含一定中高频噪声的尖锐声音来说，主动降噪依然还是有些爱莫能助，且抗风噪能力一般。</p><p class=\"p_style\">从降噪水平来说，我认为小米Air2 Pro处在同价位真无线的一流水准，甚至即便是越级比较也不会显得太吃亏，毕竟这块真无线在降噪上的表现确实足够好，或者说很超值。并且小米Air2 Pro也同样支持通透模式，因此在使用中也能够听到环境中的声音，从而尽可能避免因降噪误事或遭遇危险。</p><p style=\"text-align: center;\" class=\"p_style\"><b>高端之路，未来可期</b></p><p class=\"p_style\">作为一款降噪真无线耳机，小米Air2 Pro的功能全面性的确值得表扬。这是一款涵盖了目前真无线耳机绝大多数功能点的产品，无论是降噪、无线充电还是弹窗连接之类的功能都没有缺席，满足了更多人的更多需求。</p><p class=\"jsmargin\"><img alt=\"591A6074.jpg\" src=\"http://pic.zaeke.com/img160647230559029.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">更重要的是，小米Air2 Pro作为一款降噪耳机，同时也保持着不错的音质表现。对于在音频上表现并不突出的小米而言，打造出这样音质可靠的真无线耳机，不仅仅是满足耳机最基础功能的需求，同时也是小米对于自身的突破。</p><p class=\"p_style\">所以无论是对于用户还是小米本身而言，小米Air2 Pro都是值得肯定的。小米在音频上实现进步，而用户成为进步本身的受益者，完全可以说是双赢。只不过要是未来小米还希望在定位上更进一步，与真正意义上的高端产品一较高下，那么设计与质感可以说就是未来需要探讨的话题了。</p>', 'http://pic.zaeke.com/img160647402489404.jpeg', 6442, 0, 0, NULL, '2020-11-27 02:48:49', '2021-03-26 15:52:57');
INSERT INTO `article` VALUES (15719, '高通发布骁龙888：前所未有的迅捷、强大，以及……命名', '你以为高通准备在今年发布骁龙“875”？错啦，今天没有骁龙875，不过今年有骁龙888。虽然听起来很像是洋葱新闻或某个网友恶搞出来的东西，可高通的新一代旗舰移动...', '[object Object]', 'victory3332', 31, 'http://pic.zaeke.com/user_1533281475_1.png', '<p>你以为高通准备在今年发布骁龙“875”？错啦，今天没有骁龙875，不过今年有骁龙888。</p><p>虽然听起来很像是洋葱新闻或某个网友恶搞出来的东西，可高通的新一代旗舰移动平台真的就叫骁龙888。高通对于全新的命名解释是“8”这个数字对于他们来说代表着幸运和高端，并且这个命名确实也和高通的中国团队有关系。毕竟在中国，8这个数字被赋予了特殊的含义。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160692084595927.jpeg\" width=\"1920\" height=\"1080\"><br></p><p>没错，高通真的是因为中国市场的关系，才将取了骁龙888这个名字的哟。</p><p>当然了，全新的强大5G移动平台光有个响亮名字可不够。在各家纷纷拿出最好5G移动平台产品的当下，高通自然有必要将骁龙888武装到牙齿。只有这颗888够强大，明年顶级的Android旗舰手机们才不会受窝囊气。</p><p><b>全新的骁龙888主要规格如下：</b></p><ul><li>5nm制造工艺；</li><li>Kryo 680 CPU，1+3+4核心设计；</li><li>Adreno 660 GPU；</li><li>Hexagon 780处理器；</li><li>Spectra 580 ISP；</li><li>集成第二代高通Sensing Hub；</li><li>集成高通FastConnect 6900子系统；</li><li>集成高通X60 5G调制解调器-射频系统；</li><li>搭载第六代高通AI引擎。</li></ul><p><img alt=\"\" src=\"http://pic.zaeke.com/img160692041539438.png\" width=\"1465\" height=\"821\"><br></p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160692043412367.png\" width=\"1468\" height=\"820\"><br></p><p>新一代的骁龙888是全球首个使用了ARM Cortex-X1架构的移动平台，其搭载的Kryo 680 CPU保持着和上代产品相同的超级内核+性能核+效率核的三簇群架构。</p><p>其中超级内核是一颗运行于2.84GHz的Cortex-X1，拥有独立1MB L2缓存；3颗性能核心是运行于2.4GHz的Cortex-A78，各自拥有512KB L2缓存；4颗效率核心则是运行于1.8GHz的Cortex-A55，各自拥有128KB L2缓存。在大规模使用高性能架构且更换为5nm工艺之后，Kryo 680的性能和能效均比上一代提高25%。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160692045278843.png\" width=\"1461\" height=\"824\"><br></p><p>至于骁龙移动平台里面一直神秘得很的Adreno GPU，今年则是获得了比上一代产品快35%的性能，以及高20%的能效。</p><p>另外让我们感到十分有趣的是——可能因为当下OLED屏幕被广泛应用于高端智能手机的关系，骁龙888搭载的Adreno 660 GPU甚至还专门加入了针对OLED屏幕，改善其一致性的特性。这颗GPU甚至还支持针对OLED的Demura（也就是俗称的防“抹布屏”）和次像素渲染功能，可以说是专门为OLED屏幕<strike>以及非三星OLED屏幕</strike>的智能手机着想啊。<br></p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160692055265731.png\" width=\"1470\" height=\"828\"><br></p><p>在经历过骁龙855和骁龙865两代产品外挂modem的设计之后，高通骁龙888也成了一个自身集成modem的移动平台。它集成的5G modem不是别人，正是高通在今年曾经正式发布过的骁龙X60。</p><p>骁龙X60这颗modem我们倒是不陌生，不过在骁龙888这边高通将其称为“modem-RF系统”。它是首个同时支持第3代毫米波和Sub-6GHz射频的同类产品，另外它也是首个支持跨FDD/TDD 5G载波聚合的同类产品。其峰值5G下行速度可达7.5Gbps，峰值5G上行速度则有3Gbps。</p><p>FastConnect 6900系统我们更加不陌生，这是业内首个支持Wi-Fi 6和Wi-Fi 6E（6GHz）的射频系统。另外它还支持4K QAM、160MHz频段和最多4数据流的双频同步（DBS）功能。这个系统也为骁龙888提供了目前移动平台中最顶级的无线连接能力，比如蓝牙5.2、双天线和高音质蓝牙音频等。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160692059524846.png\" width=\"1466\" height=\"823\"><br></p><p>至于其他移动平台制造商们喜欢提的“NPU”概念，今年在高通骁龙888上依旧不会见到。</p><p>骁龙888延续了高通对于移动设备AI计算的“异构”理念，也就是使用CPU、GPU、DSP进行协同计算。不过和以往不同的是，高通这次的AI引擎里也包含了第二代Sensing Hub，此模块专门用于处理5G、Wi-Fi、蓝牙和定位信息等，这个Sensing Hub里面本身则拥有专门的AI处理器。<br></p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160692060914574.png\" width=\"1468\" height=\"820\"><br></p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160692067213927.png\" width=\"1477\" height=\"812\"><br></p><p>至于高通向来喜欢强调的DSP模组，今年则是获得了专门的AI加速单元。在新设计和新工艺的帮助下，Hexagon 780 DSP拥有最多3倍于前代的每瓦性能，以及2倍于前代的张量加速计算能力。</p><p>Hexagon 780 DSP的性能提升来源于内部的3个方面，其中标量加速器的性能比上一代提高50%，张量加速器的计算能力达到前代2倍。但是真正的重点在于Hexagon 780能够访问共享内存，因此对单个任务的内存容量提高了16倍。在部分应用场景里因为共享内存的关系，甚至可以将交接时间改善1000倍之多。另外共享内存的设计还使得DSP能够更迅速无缝地在标量、向量和张量计算之间灵活切换，大幅度提高AI计算的效率。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160692064623803.png\" width=\"1468\" height=\"825\"><br></p><p>将CPU、GPU、DSP和Sensing Hub组合到一起后，高通骁龙888搭载的第六代AI引擎拥有高达26 TOPS的AI算力，是目前高通最强的移动AI计算平台，也是有史以来最大的性能提升。另外骁龙888还支持高通AI引擎加速器，能够在AI软件计算中进一步提升性能。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160692069958849.png\" width=\"1467\" height=\"813\"></p><p><img alt=\"高通Spectra 580 ISP-处理速度.png\" src=\"http://pic.zaeke.com/img160692069737879.png\" width=\"1474\" height=\"826\"><br></p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160692070665224.png\" width=\"1472\" height=\"825\"><br></p><p>最后则是Spectra 580 ISP。高通是最早在移动平台用ISP里面引入计算机视觉技术的厂商，Spectra 580 ISP自然也不能丢掉这个传统。其在骁龙移动平台产品线中首次采用3核心设计，并首次支持支持堆叠HDR传感器。其处理性能最高可达每秒27亿像素，速度比上一代产品提高35%。</p><p>在强大的ISP帮助下，骁龙888可以支持4K 120FPS视频录制、带计算HDR的4K HDR录制以及10bit-HDR HEIF照片拍摄。另外如果你想同时录制3个4K HDR视频，或是同时拍摄3张2800万像素照片，或是在不同摄像头之间完美地模拟变焦，骁龙888都做得到。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160692073019417.png\" width=\"1467\" height=\"824\"><br></p><p>骁龙888带给我们的新东西其实不止硬件，软件生态也是骁龙888很重要的一环，比如新的Snapdragon Elite Gaming体系。骁龙888给这一体系带来的新东西很多，具体我们列表如下：</p><ul><li>高通游戏快速触摸响应功能——可以在所有游戏里将<b>降低20%触摸延迟</b>；</li><li>可变速率着色功能——首次出现于移动设备上，可将<b>游戏性能提高30%</b>；</li><li>超平滑游戏——Qualcomm Game Smoother如今支持最高144fps画面；</li><li>AAA作品针对优化——合（sai）作（qian）就完事了？</li></ul><p>另外Snapdragon Elite Gaming体系中还包含下面这些：</p><ul><li>最佳HDR品质——真10bit HDR画面渲染、高通游戏色彩增强、HDR快速合成；</li><li>桌面级游戏特性——桌面级正向渲染、可自主更新GPU驱动；</li><li>云端游戏——基于5G或Wi-Fi 6，支持Xbox云游戏、Google Stadia、亚马逊Luna；</li><li>游戏音频——高通aptX自适应音频、高通Aqstic音频技术、杜比全景声支持。</li></ul><p><img alt=\"\" src=\"http://pic.zaeke.com/img160692079927434.jpeg\" width=\"3178\" height=\"2120\"><br></p><p>在骁龙888官宣之后，一大票厂商已经宣布会首发/首批使用其打造全新的智能手机产品。当然具体是谁咱们目前也不知道，不过可以肯定的是，最快明年1月就会有搭载骁龙888的智能手机产品正式发布。</p>', '<p class=\"p_style\">你以为高通准备在今年发布骁龙“875”？错啦，今天没有骁龙875，不过今年有骁龙888。</p><p class=\"p_style\">虽然听起来很像是洋葱新闻或某个网友恶搞出来的东西，可高通的新一代旗舰移动平台真的就叫骁龙888。高通对于全新的命名解释是“8”这个数字对于他们来说代表着幸运和高端，并且这个命名确实也和高通的中国团队有关系。毕竟在中国，8这个数字被赋予了特殊的含义。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160692084595927.jpeg\" width=\"1920\" height=\"1080\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">没错，高通真的是因为中国市场的关系，才将取了骁龙888这个名字的哟。</p><p class=\"p_style\">当然了，全新的强大5G移动平台光有个响亮名字可不够。在各家纷纷拿出最好5G移动平台产品的当下，高通自然有必要将骁龙888武装到牙齿。只有这颗888够强大，明年顶级的Android旗舰手机们才不会受窝囊气。</p><p class=\"p_style\"><b>全新的骁龙888主要规格如下：</b></p><ul><li>5nm制造工艺；</li><li>Kryo 680 CPU，1+3+4核心设计；</li><li>Adreno 660 GPU；</li><li>Hexagon 780处理器；</li><li>Spectra 580 ISP；</li><li>集成第二代高通Sensing Hub；</li><li>集成高通FastConnect 6900子系统；</li><li>集成高通X60 5G调制解调器-射频系统；</li><li>搭载第六代高通AI引擎。</li></ul><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160692041539438.png\" width=\"1465\" height=\"821\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160692043412367.png\" width=\"1468\" height=\"820\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">新一代的骁龙888是全球首个使用了ARM Cortex-X1架构的移动平台，其搭载的Kryo 680 CPU保持着和上代产品相同的超级内核+性能核+效率核的三簇群架构。</p><p class=\"p_style\">其中超级内核是一颗运行于2.84GHz的Cortex-X1，拥有独立1MB L2缓存；3颗性能核心是运行于2.4GHz的Cortex-A78，各自拥有512KB L2缓存；4颗效率核心则是运行于1.8GHz的Cortex-A55，各自拥有128KB L2缓存。在大规模使用高性能架构且更换为5nm工艺之后，Kryo 680的性能和能效均比上一代提高25%。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160692045278843.png\" width=\"1461\" height=\"824\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">至于骁龙移动平台里面一直神秘得很的Adreno GPU，今年则是获得了比上一代产品快35%的性能，以及高20%的能效。</p><p class=\"p_style\">另外让我们感到十分有趣的是——可能因为当下OLED屏幕被广泛应用于高端智能手机的关系，骁龙888搭载的Adreno 660 GPU甚至还专门加入了针对OLED屏幕，改善其一致性的特性。这颗GPU甚至还支持针对OLED的Demura（也就是俗称的防“抹布屏”）和次像素渲染功能，可以说是专门为OLED屏幕<strike>以及非三星OLED屏幕</strike>的智能手机着想啊。<br></p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160692055265731.png\" width=\"1470\" height=\"828\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">在经历过骁龙855和骁龙865两代产品外挂modem的设计之后，高通骁龙888也成了一个自身集成modem的移动平台。它集成的5G modem不是别人，正是高通在今年曾经正式发布过的骁龙X60。</p><p class=\"p_style\">骁龙X60这颗modem我们倒是不陌生，不过在骁龙888这边高通将其称为“modem-RF系统”。它是首个同时支持第3代毫米波和Sub-6GHz射频的同类产品，另外它也是首个支持跨FDD/TDD 5G载波聚合的同类产品。其峰值5G下行速度可达7.5Gbps，峰值5G上行速度则有3Gbps。</p><p class=\"p_style\">FastConnect 6900系统我们更加不陌生，这是业内首个支持Wi-Fi 6和Wi-Fi 6E（6GHz）的射频系统。另外它还支持4K QAM、160MHz频段和最多4数据流的双频同步（DBS）功能。这个系统也为骁龙888提供了目前移动平台中最顶级的无线连接能力，比如蓝牙5.2、双天线和高音质蓝牙音频等。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160692059524846.png\" width=\"1466\" height=\"823\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">至于其他移动平台制造商们喜欢提的“NPU”概念，今年在高通骁龙888上依旧不会见到。</p><p class=\"p_style\">骁龙888延续了高通对于移动设备AI计算的“异构”理念，也就是使用CPU、GPU、DSP进行协同计算。不过和以往不同的是，高通这次的AI引擎里也包含了第二代Sensing Hub，此模块专门用于处理5G、Wi-Fi、蓝牙和定位信息等，这个Sensing Hub里面本身则拥有专门的AI处理器。<br></p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160692060914574.png\" width=\"1468\" height=\"820\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160692067213927.png\" width=\"1477\" height=\"812\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">至于高通向来喜欢强调的DSP模组，今年则是获得了专门的AI加速单元。在新设计和新工艺的帮助下，Hexagon 780 DSP拥有最多3倍于前代的每瓦性能，以及2倍于前代的张量加速计算能力。</p><p class=\"p_style\">Hexagon 780 DSP的性能提升来源于内部的3个方面，其中标量加速器的性能比上一代提高50%，张量加速器的计算能力达到前代2倍。但是真正的重点在于Hexagon 780能够访问共享内存，因此对单个任务的内存容量提高了16倍。在部分应用场景里因为共享内存的关系，甚至可以将交接时间改善1000倍之多。另外共享内存的设计还使得DSP能够更迅速无缝地在标量、向量和张量计算之间灵活切换，大幅度提高AI计算的效率。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160692064623803.png\" width=\"1468\" height=\"825\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">将CPU、GPU、DSP和Sensing Hub组合到一起后，高通骁龙888搭载的第六代AI引擎拥有高达26 TOPS的AI算力，是目前高通最强的移动AI计算平台，也是有史以来最大的性能提升。另外骁龙888还支持高通AI引擎加速器，能够在AI软件计算中进一步提升性能。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160692069958849.png\" width=\"1467\" height=\"813\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"高通Spectra 580 ISP-处理速度.png\" src=\"http://pic.zaeke.com/img160692069737879.png\" width=\"1474\" height=\"826\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160692070665224.png\" width=\"1472\" height=\"825\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">最后则是Spectra 580 ISP。高通是最早在移动平台用ISP里面引入计算机视觉技术的厂商，Spectra 580 ISP自然也不能丢掉这个传统。其在骁龙移动平台产品线中首次采用3核心设计，并首次支持支持堆叠HDR传感器。其处理性能最高可达每秒27亿像素，速度比上一代产品提高35%。</p><p class=\"p_style\">在强大的ISP帮助下，骁龙888可以支持4K 120FPS视频录制、带计算HDR的4K HDR录制以及10bit-HDR HEIF照片拍摄。另外如果你想同时录制3个4K HDR视频，或是同时拍摄3张2800万像素照片，或是在不同摄像头之间完美地模拟变焦，骁龙888都做得到。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160692073019417.png\" width=\"1467\" height=\"824\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">骁龙888带给我们的新东西其实不止硬件，软件生态也是骁龙888很重要的一环，比如新的Snapdragon Elite Gaming体系。骁龙888给这一体系带来的新东西很多，具体我们列表如下：</p><ul><li>高通游戏快速触摸响应功能——可以在所有游戏里将<b>降低20%触摸延迟</b>；</li><li>可变速率着色功能——首次出现于移动设备上，可将<b>游戏性能提高30%</b>；</li><li>超平滑游戏——Qualcomm Game Smoother如今支持最高144fps画面；</li><li>AAA作品针对优化——合（sai）作（qian）就完事了？</li></ul><p class=\"p_style\">另外Snapdragon Elite Gaming体系中还包含下面这些：</p><ul><li>最佳HDR品质——真10bit HDR画面渲染、高通游戏色彩增强、HDR快速合成；</li><li>桌面级游戏特性——桌面级正向渲染、可自主更新GPU驱动；</li><li>云端游戏——基于5G或Wi-Fi 6，支持Xbox云游戏、Google Stadia、亚马逊Luna；</li><li>游戏音频——高通aptX自适应音频、高通Aqstic音频技术、杜比全景声支持。</li></ul><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160692079927434.jpeg\" width=\"3178\" height=\"2120\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">在骁龙888官宣之后，一大票厂商已经宣布会首发/首批使用其打造全新的智能手机产品。当然具体是谁咱们目前也不知道，不过可以肯定的是，最快明年1月就会有搭载骁龙888的智能手机产品正式发布。</p>', 'http://pic.zaeke.com/img160692078422975.png', 7441, 0, 0, NULL, '2020-12-02 06:54:18', '2021-03-26 07:33:32');
INSERT INTO `article` VALUES (15830, '华为 nova 8 系列登场：点满技能的爆款之作', '随着2020年临近尾声，国内的智能手机厂商们纷纷开始推出新一波定位线下的主力销售机型。前有OPPO的Reno5系列，后有vivoX60系列，而夹在这两中间推出的...', '[object Object]', 'victory3332', 31, 'http://pic.zaeke.com/user_1533281475_1.png', '<p>随着2020年临近尾声，国内的智能手机厂商们纷纷开始推出新一波定位线下的主力销售机型。前有OPPO的Reno5系列，后有vivo X60系列，而夹在这两中间推出的则是线下主力机型中另一支重要力量——华为nova8系列。这次为了让nova8系列机型在竞争激烈的线下市场脱颖而出，华为可以说是将它们武装到了牙齿。</p><p><img src=\"http://pic.zaeke.com/img160871173037737.jpeg\" alt=\"\">  </p><p><strong>华为nova8 Pro主要规格配置：</strong></p><ul><li>6.72英寸2676×1236分辨率双曲面OLED显示屏，10bit色深，120Hz刷新率，300Hz触控采样率；</li><li>麒麟985芯片，支持双模5G；</li><li>后置6400万像素主相机，f/1.8光圈；</li><li>后置800万像素超广角相机，120°视角；</li><li>后置微距相机，最近对焦距离4cm；</li><li>后置人像虚化相机；</li><li>前置3200万像素广角相机；</li><li>前置1600万像素人像相机；</li><li>内置4000毫安时电池，支持66W快速充电；</li><li>支持Wi-Fi 6+、NFC、线性马达。</li></ul><p><strong>华为nova8主要规格配置：</strong></p><ul><li>6.57英寸2340×1080分辨率双曲面OLED显示屏，10bit色深、90Hz刷新率，240Hz从触控次阳绿；</li><li>麒麟985芯片，支持双模5G；</li><li><p>后置6400万像素主相机，f/1.8光圈；</p></li><li><p>后置800万像素超广角相机，120°视角；</p></li><li>后置微距相机，最近对焦距离4cm；</li><li>后置人像虚化相机；</li><li>前置3200万像素广角相机；</li><li>内置3800毫安时电池，支持66W快速充电；</li><li>支持NFC、线性马达。</li></ul><p><img src=\"http://pic.zaeke.com/img160871170971042.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img160871170958252.jpeg\" alt=\"\">  </p><p>华为nova8系列机型的屏幕是这次发布会上的一个亮点，nova8系列两台机型如今皆有高刷新率OLED显示屏加持，并且两台机器都支持10bit色深。同时每一台nova8系列机型的屏幕都经过色准调校，以保证△E＜1。</p><p>其中nova8 Pro的屏幕刷新率达到目前较高的120Hz级别，触控采样率也有相当高的300Hz。甚至为了更好地凸出这款手机高刷新率屏幕和高触控采样率对游戏操作的帮助，nova8系列甚至还是首批支持《王者荣耀》手游90Hz刷新率模式的手机。</p><p><img src=\"http://pic.zaeke.com/img160871175615758.jpeg\" alt=\"\">  </p><p>nova8系列机型另一个特色是其搭载的快速充电特性。和其他高功率快充不太一样，nova8系列使用的66W快充是单电芯双路三电极的方案。发布会上华为号称这样的设计可以让电池容量灯效增加10%，电池续航灯效增加5%。另外和双电芯比起来，单电芯设计的抗老化性能和循环寿命也有优势。nova8 Pro依靠66W快速充电可以在15分钟内充电至60%，35分钟内充电至100%。</p><p>华为在nova8 Pro这款产品的影像系统上花费了大量的心思，nova8 Pro拥有双前置相机方案，其中包含一颗3200万像素的超广角相机。手机在检测到前置相机拍摄多人的时候甚至可以选择自动切换至超广角镜头拍摄，另外专门的行走模式vlog自拍则可以让nova8 Pro在运动状态下拍摄稳定的前置视频。</p><p><img src=\"http://pic.zaeke.com/img160871179736581.jpeg\" alt=\"\">  </p><p>除了前置相机，nova8 Pro的视频还包括立体声收音、音频变焦和前后同时录制视频。搭配华为的Freebuds Pro真无线蓝牙耳机时，也可以使用耳机上的麦克风进行收音，改善录制视频的收音质量。</p><p>华为nova8系列两台机型各自有2个配置，具体如下。</p><p>nova8：</p><ul><li>8GB RAM+128GB ROM：￥3299；</li><li>8GB RAM+256GB ROM：￥3699。</li></ul><p>nova8 Pro</p><ul><li>8GB RAM+128GB ROM：￥3999；</li><li>8GB RAM+256GB ROM：￥4399。</li></ul><p>一同发布的，<span style=\"font-size: 16px;\">还有华为首款采用方形屏幕设计的华为 Watch Fit 智能手表、华为 Watch GT2 Pro 新年款、华为 FreeLace Pro&nbsp;颈带式耳机新色樱语粉等多款智能可穿戴式设备，诸如亲民的入门级机型以及笔记本电脑等同样登场。</span>恰逢新年期间，华为更是给喜庆多添点色彩，具体售价如下：</p><ul><li>华为 Watch Fit 智能手表 活力款，售价为 799 元；</li><li>华为 Watch Fit 智能手表 新年款，售价为 899 元；</li><li>华为 Watch GT 2 Pro 智能手表 新年款，售价为 2388 元；</li><li>华为 Watch GT 2 Pro 智能手表 ECG 款，售价为 2688 元；</li><li>华为 MateBook D 14 笔记本电脑，售价为 4999 元起；</li><li>华为 MateBook D 15 笔记本电脑，售价为 4999 元起；</li><li>华为畅享 20 SE，4+128 售价为 1299 元；</li></ul><p><img src=\"http://pic.zaeke.com/img160871184296009.jpeg\" alt=\"0020sXlVly1glxtrvt5dlj61sa0u0npe02.jpg\">  </p><p>其中华为 Watch Fit 智能手表的最大特点，应该是这块方形屏幕，1.64 英寸、456*286 分辨率以及 2.5D 圆润倒角设计，再搭配多种表盘以及运动模式支持，还有内置独立 GPS 和仅 21g 的本体重量，造型设计与使用体验都有所保证。</p><p>华为还给华为 Watch Fit 智能手表塞进去了 10 天续航时间的内置电池，内置独立 GPS 以及 13 门科学跑步课程，更是支持第四代 HUAWEI TruSeen 4.0 心率检测技术，这对于喜欢运动的消费者来说是个不错的选择。对了，华为 Watch Fit 智能手表提供薄荷绿、樱语粉、蜜瓜橙、曜石黑等四种配色选择，恰逢新年期间的新年款还有红色，当然也支持表带更换啦。</p>', '<p class=\"p_style\">随着2020年临近尾声，国内的智能手机厂商们纷纷开始推出新一波定位线下的主力销售机型。前有OPPO的Reno5系列，后有vivo X60系列，而夹在这两中间推出的则是线下主力机型中另一支重要力量——华为nova8系列。这次为了让nova8系列机型在竞争激烈的线下市场脱颖而出，华为可以说是将它们武装到了牙齿。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160871173037737.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\"><strong>华为nova8 Pro主要规格配置：</strong></p><ul><li>6.72英寸2676×1236分辨率双曲面OLED显示屏，10bit色深，120Hz刷新率，300Hz触控采样率；</li><li>麒麟985芯片，支持双模5G；</li><li>后置6400万像素主相机，f/1.8光圈；</li><li>后置800万像素超广角相机，120°视角；</li><li>后置微距相机，最近对焦距离4cm；</li><li>后置人像虚化相机；</li><li>前置3200万像素广角相机；</li><li>前置1600万像素人像相机；</li><li>内置4000毫安时电池，支持66W快速充电；</li><li>支持Wi-Fi 6+、NFC、线性马达。</li></ul><p class=\"p_style\"><strong>华为nova8主要规格配置：</strong></p><ul><li>6.57英寸2340×1080分辨率双曲面OLED显示屏，10bit色深、90Hz刷新率，240Hz从触控次阳绿；</li><li>麒麟985芯片，支持双模5G；</li><li><p>后置6400万像素主相机，f/1.8光圈；</p></li><li><p>后置800万像素超广角相机，120°视角；</p></li><li>后置微距相机，最近对焦距离4cm；</li><li>后置人像虚化相机；</li><li>前置3200万像素广角相机；</li><li>内置3800毫安时电池，支持66W快速充电；</li><li>支持NFC、线性马达。</li></ul><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160871170971042.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160871170958252.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">华为nova8系列机型的屏幕是这次发布会上的一个亮点，nova8系列两台机型如今皆有高刷新率OLED显示屏加持，并且两台机器都支持10bit色深。同时每一台nova8系列机型的屏幕都经过色准调校，以保证△E＜1。</p><p class=\"p_style\">其中nova8 Pro的屏幕刷新率达到目前较高的120Hz级别，触控采样率也有相当高的300Hz。甚至为了更好地凸出这款手机高刷新率屏幕和高触控采样率对游戏操作的帮助，nova8系列甚至还是首批支持《王者荣耀》手游90Hz刷新率模式的手机。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160871175615758.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">nova8系列机型另一个特色是其搭载的快速充电特性。和其他高功率快充不太一样，nova8系列使用的66W快充是单电芯双路三电极的方案。发布会上华为号称这样的设计可以让电池容量灯效增加10%，电池续航灯效增加5%。另外和双电芯比起来，单电芯设计的抗老化性能和循环寿命也有优势。nova8 Pro依靠66W快速充电可以在15分钟内充电至60%，35分钟内充电至100%。</p><p class=\"p_style\">华为在nova8 Pro这款产品的影像系统上花费了大量的心思，nova8 Pro拥有双前置相机方案，其中包含一颗3200万像素的超广角相机。手机在检测到前置相机拍摄多人的时候甚至可以选择自动切换至超广角镜头拍摄，另外专门的行走模式vlog自拍则可以让nova8 Pro在运动状态下拍摄稳定的前置视频。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160871179736581.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">除了前置相机，nova8 Pro的视频还包括立体声收音、音频变焦和前后同时录制视频。搭配华为的Freebuds Pro真无线蓝牙耳机时，也可以使用耳机上的麦克风进行收音，改善录制视频的收音质量。</p><p class=\"p_style\">华为nova8系列两台机型各自有2个配置，具体如下。</p><p class=\"p_style\">nova8：</p><ul><li>8GB RAM+128GB ROM：￥3299；</li><li>8GB RAM+256GB ROM：￥3699。</li></ul><p class=\"p_style\">nova8 Pro</p><ul><li>8GB RAM+128GB ROM：￥3999；</li><li>8GB RAM+256GB ROM：￥4399。</li></ul><p class=\"p_style\">一同发布的，<span style=\"font-size: 16px;\">还有华为首款采用方形屏幕设计的华为 Watch Fit 智能手表、华为 Watch GT2 Pro 新年款、华为 FreeLace Pro&nbsp;颈带式耳机新色樱语粉等多款智能可穿戴式设备，诸如亲民的入门级机型以及笔记本电脑等同样登场。</span>恰逢新年期间，华为更是给喜庆多添点色彩，具体售价如下：</p><ul><li>华为 Watch Fit 智能手表 活力款，售价为 799 元；</li><li>华为 Watch Fit 智能手表 新年款，售价为 899 元；</li><li>华为 Watch GT 2 Pro 智能手表 新年款，售价为 2388 元；</li><li>华为 Watch GT 2 Pro 智能手表 ECG 款，售价为 2688 元；</li><li>华为 MateBook D 14 笔记本电脑，售价为 4999 元起；</li><li>华为 MateBook D 15 笔记本电脑，售价为 4999 元起；</li><li>华为畅享 20 SE，4+128 售价为 1299 元；</li></ul><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160871184296009.jpeg\" alt=\"0020sXlVly1glxtrvt5dlj61sa0u0npe02.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">其中华为 Watch Fit 智能手表的最大特点，应该是这块方形屏幕，1.64 英寸、456*286 分辨率以及 2.5D 圆润倒角设计，再搭配多种表盘以及运动模式支持，还有内置独立 GPS 和仅 21g 的本体重量，造型设计与使用体验都有所保证。</p><p class=\"p_style\">华为还给华为 Watch Fit 智能手表塞进去了 10 天续航时间的内置电池，内置独立 GPS 以及 13 门科学跑步课程，更是支持第四代 HUAWEI TruSeen 4.0 心率检测技术，这对于喜欢运动的消费者来说是个不错的选择。对了，华为 Watch Fit 智能手表提供薄荷绿、樱语粉、蜜瓜橙、曜石黑等四种配色选择，恰逢新年期间的新年款还有红色，当然也支持表带更换啦。</p>', 'http://pic.zaeke.com/img160871204098497.jpeg', 6770, 0, 0, NULL, '2020-12-23 00:27:33', '2021-03-27 06:44:47');
INSERT INTO `article` VALUES (15832, '性能与轻薄能共存么？OPPO Reno5 Pro+说可以', 'Reno系列在短短时间内就出到了第五代，不过这其中性能中端的多，采用旗舰移动平台的却并不多。令人高兴的是，OPPOReno5Pro+是其中少有的旗舰机型。先来说...', '[object Object]', '烫头孙师傅', 38, 'http://pic.zaeke.com/user_1533281477_5.png', '<p>Reno系列在短短时间内就出到了第五代，不过这其中性能中端的多，采用旗舰移动平台的却并不多。令人高兴的是，OPPO Reno5 Pro+是其中少有的旗舰机型。</p><p><img alt=\"_1093481.jpg\" src=\"http://pic.zaeke.com/img160877909657036.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>先来说说外观，OPPO Reno5 Pro+的整体设计与Reno4系列相近，不过工艺上则有更好的优化。该机采用炫彩层、纹理层与反光层的工艺层叠加结构来实现变幻的色彩，而玻璃蚀刻工艺也进一步提升了颜值，玻璃表面的金字塔结晶结构也能够实现疏水疏油的效果。</p><p><img alt=\"DSC03050.jpg\" src=\"http://pic.zaeke.com/img160877917870229.jpeg\" width=\"1400\" height=\"933\"><br></p><p><img alt=\"DSC03084.jpg\" src=\"http://pic.zaeke.com/img160877919060325.jpeg\" width=\"1400\" height=\"933\"><br></p><p>除了常规的款式之外，这回OPPO还带来了自己的黑科技，也就是采用电致变色技术的“变色幻境”版本。该版本是与美国洛杉矶视觉艺术家Joshua Vides联手推出，漫画笔迹加波点的风格能够带来独特的视觉观感。</p><p>为了实现电致变色的特性。OPPO经过三年的努力解决了电致变色的轻量化难题，并且以贴近LCD的封装工艺来保证寿命，并且能够让用户看到色彩的渐变过程。此外该技术的有机电解材质安全无毒，并且两台手机在绑定后可实现咚咚传情功能，能够收到怦然心动特效并通过双击后壳回应。</p><p><img alt=\"_1093509.jpg\" src=\"http://pic.zaeke.com/img160877912130505.jpeg\" width=\"2000\" height=\"1127\"><br></p><p><img alt=\"_1093471.jpg\" src=\"http://pic.zaeke.com/img160877907999561.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>而在硬件上，OPPO Reno5 Pro+采用6.55英寸90Hz高感曲面屏，触控采样率为180Hz，还支持第六代屏下指纹技术。这块屏幕的素质也相对不错，对得起OPPO Reno5 Pro+的定位。</p><p>至于性能方面，OPPO Reno5 Pro+搭载高通骁龙865移动平台，提供8GB RAM+128GB ROM与12GB RAM+256GB ROM版本可选。或许有人会觉得骁龙888发布之后的骁龙865机型似乎来得有些迟，不过对于这个定位并不完全线上的机型来说，性能是完全足够的。</p><p><img alt=\"_1093521.jpg\" src=\"http://pic.zaeke.com/img160877913491242.jpeg\" width=\"2000\" height=\"1127\"><br></p><p><img alt=\"_1093502.jpg\" src=\"http://pic.zaeke.com/img160877922394293.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>为了让高通骁龙865移动平台的性能得以充分发挥，这回OPPO Reno5 Pro+准备了500平方毫米的VC立体液冷散热系统。并且为了提升游戏的画面感、沉浸感、操纵感、氛围感与趣味感，该机也支持游戏闪电启动与Hyper Boost 4.0引擎。</p><p>&nbsp;另外，可别忘了OPPO如今正在试图打造属于自己的生态链，因此多设备的协同对于OPPO Reno5 Pro+来说也至关重要。OPPO Reno5 Pro+支持屏幕共享、闪达窗口、Iot互联与车机操作等功能，能够与更多OPPO设备进行更好的互动。<br></p><p>OPPO Reno5 Pro+内置4500毫安时电池，在保证5G网络下续航的同时也拥有非常纤薄的机身，很难想象这样一款旗舰级5G手机居然只拥有7.9毫米的厚度与184克的机身重量，对于负担日趋加重的小拇指来说实在是非常友好。</p><p><img alt=\"_1093514.jpg\" src=\"http://pic.zaeke.com/img160877936390733.jpeg\" width=\"2000\" height=\"1125\"><br></p><p><img alt=\"_1093487.jpg\" src=\"http://pic.zaeke.com/img160877909683071.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>在相机上，OPPO Reno5 Pro+采用5000万像素主摄，而且这颗主摄还采用了定制的索尼IMX766感光元件，也配备了1600万像素超广角相机与1300万像素5X长焦相机与一颗200万像素相机。 其中主摄的定制索尼IMX766感光元件，拥有1/1.56英寸的大底，同时支持全像素全向对焦与像素四合一功能，也拥有DOL-HDR功能。</p><p> 而在视频方面，OPPO Reno5 Pro+支持视频超级防抖3.0，拥有场景判断与运动趋势判断算法，并且也可以实现移动延时摄影功能。而AI视频增强则将视频超级夜景与liveHDR功能相结合，能能够实现更好的暗光视频表现。而全新的AI焕彩视频美颜则拥有296个特征点，以及8重100级美颜，美颜算法相对智能个性。</p><p><img alt=\"DSC03251.jpg\" src=\"http://pic.zaeke.com/img160877916788911.jpeg\" width=\"2000\" height=\"1125\"><br></p><p>无论你是一个性能主义者，还是喜欢漂亮轻薄的智能智能手机，手机显然都能满足你的需求。就我个人的观点来说，手机是一个能够满足更多人需求的机型，它或许并没有在硬件上做到极致，但却以一定的代价换来了更好的手感与轻薄感。</p><p>这样的思路或许有些见仁见智的味道，不过要知道的是，在不少5G智能手机重量突破了200g大关，甚至重量逼近半斤的时候，这样一款轻薄旗舰机型确实能够吸引不少人的注意力。毕竟无论是性能还是外观，它甚至足以满足两个群体的需求。</p><p>所以说手机最大的意义还是在于它告诉了我们：性能与轻薄其实在5G时代可以共存。</p><p><b>更多美图：</b></p><p><img alt=\"DSC03256.jpg\" src=\"http://pic.zaeke.com/img160877917116048.jpeg\" width=\"2000\" height=\"1125\"><br></p><p><img alt=\"_1093473.jpg\" src=\"http://pic.zaeke.com/img160877907931900.jpeg\" width=\"2000\" height=\"1127\"></p><p><img alt=\"_1093475.jpg\" src=\"http://pic.zaeke.com/img160877907958715.jpeg\" width=\"2000\" height=\"1127\"></p><p><img alt=\"_1093559.jpg\" src=\"http://pic.zaeke.com/img160877914640092.jpeg\" width=\"2000\" height=\"1127\"></p><p><img alt=\"_1093562.jpg\" src=\"http://pic.zaeke.com/img160877914857977.jpeg\" width=\"2000\" height=\"1127\"></p><p><img alt=\"_1093563.jpg\" src=\"http://pic.zaeke.com/img160877915855506.jpeg\" width=\"2000\" height=\"1127\"></p><p><img alt=\"_1093570.jpg\" src=\"http://pic.zaeke.com/img160877916241335.jpeg\" width=\"2000\" height=\"1127\"><br></p>', '<p class=\"p_style\">Reno系列在短短时间内就出到了第五代，不过这其中性能中端的多，采用旗舰移动平台的却并不多。令人高兴的是，OPPO Reno5 Pro+是其中少有的旗舰机型。</p><p class=\"jsmargin\"><img alt=\"_1093481.jpg\" src=\"http://pic.zaeke.com/img160877909657036.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">先来说说外观，OPPO Reno5 Pro+的整体设计与Reno4系列相近，不过工艺上则有更好的优化。该机采用炫彩层、纹理层与反光层的工艺层叠加结构来实现变幻的色彩，而玻璃蚀刻工艺也进一步提升了颜值，玻璃表面的金字塔结晶结构也能够实现疏水疏油的效果。</p><p class=\"jsmargin\"><img alt=\"DSC03050.jpg\" src=\"http://pic.zaeke.com/img160877917870229.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"DSC03084.jpg\" src=\"http://pic.zaeke.com/img160877919060325.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">除了常规的款式之外，这回OPPO还带来了自己的黑科技，也就是采用电致变色技术的“变色幻境”版本。该版本是与美国洛杉矶视觉艺术家Joshua Vides联手推出，漫画笔迹加波点的风格能够带来独特的视觉观感。</p><p class=\"p_style\">为了实现电致变色的特性。OPPO经过三年的努力解决了电致变色的轻量化难题，并且以贴近LCD的封装工艺来保证寿命，并且能够让用户看到色彩的渐变过程。此外该技术的有机电解材质安全无毒，并且两台手机在绑定后可实现咚咚传情功能，能够收到怦然心动特效并通过双击后壳回应。</p><p class=\"jsmargin\"><img alt=\"_1093509.jpg\" src=\"http://pic.zaeke.com/img160877912130505.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"_1093471.jpg\" src=\"http://pic.zaeke.com/img160877907999561.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">而在硬件上，OPPO Reno5 Pro+采用6.55英寸90Hz高感曲面屏，触控采样率为180Hz，还支持第六代屏下指纹技术。这块屏幕的素质也相对不错，对得起OPPO Reno5 Pro+的定位。</p><p class=\"p_style\">至于性能方面，OPPO Reno5 Pro+搭载高通骁龙865移动平台，提供8GB RAM+128GB ROM与12GB RAM+256GB ROM版本可选。或许有人会觉得骁龙888发布之后的骁龙865机型似乎来得有些迟，不过对于这个定位并不完全线上的机型来说，性能是完全足够的。</p><p class=\"jsmargin\"><img alt=\"_1093521.jpg\" src=\"http://pic.zaeke.com/img160877913491242.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"_1093502.jpg\" src=\"http://pic.zaeke.com/img160877922394293.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">为了让高通骁龙865移动平台的性能得以充分发挥，这回OPPO Reno5 Pro+准备了500平方毫米的VC立体液冷散热系统。并且为了提升游戏的画面感、沉浸感、操纵感、氛围感与趣味感，该机也支持游戏闪电启动与Hyper Boost 4.0引擎。</p><p class=\"p_style\">&nbsp;另外，可别忘了OPPO如今正在试图打造属于自己的生态链，因此多设备的协同对于OPPO Reno5 Pro+来说也至关重要。OPPO Reno5 Pro+支持屏幕共享、闪达窗口、Iot互联与车机操作等功能，能够与更多OPPO设备进行更好的互动。<br></p><p class=\"p_style\">OPPO Reno5 Pro+内置4500毫安时电池，在保证5G网络下续航的同时也拥有非常纤薄的机身，很难想象这样一款旗舰级5G手机居然只拥有7.9毫米的厚度与184克的机身重量，对于负担日趋加重的小拇指来说实在是非常友好。</p><p class=\"jsmargin\"><img alt=\"_1093514.jpg\" src=\"http://pic.zaeke.com/img160877936390733.jpeg\" width=\"2000\" height=\"1125\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"_1093487.jpg\" src=\"http://pic.zaeke.com/img160877909683071.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">在相机上，OPPO Reno5 Pro+采用5000万像素主摄，而且这颗主摄还采用了定制的索尼IMX766感光元件，也配备了1600万像素超广角相机与1300万像素5X长焦相机与一颗200万像素相机。 其中主摄的定制索尼IMX766感光元件，拥有1/1.56英寸的大底，同时支持全像素全向对焦与像素四合一功能，也拥有DOL-HDR功能。</p><p class=\"p_style\"> 而在视频方面，OPPO Reno5 Pro+支持视频超级防抖3.0，拥有场景判断与运动趋势判断算法，并且也可以实现移动延时摄影功能。而AI视频增强则将视频超级夜景与liveHDR功能相结合，能能够实现更好的暗光视频表现。而全新的AI焕彩视频美颜则拥有296个特征点，以及8重100级美颜，美颜算法相对智能个性。</p><p class=\"jsmargin\"><img alt=\"DSC03251.jpg\" src=\"http://pic.zaeke.com/img160877916788911.jpeg\" width=\"2000\" height=\"1125\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">无论你是一个性能主义者，还是喜欢漂亮轻薄的智能智能手机，手机显然都能满足你的需求。就我个人的观点来说，手机是一个能够满足更多人需求的机型，它或许并没有在硬件上做到极致，但却以一定的代价换来了更好的手感与轻薄感。</p><p class=\"p_style\">这样的思路或许有些见仁见智的味道，不过要知道的是，在不少5G智能手机重量突破了200g大关，甚至重量逼近半斤的时候，这样一款轻薄旗舰机型确实能够吸引不少人的注意力。毕竟无论是性能还是外观，它甚至足以满足两个群体的需求。</p><p class=\"p_style\">所以说手机最大的意义还是在于它告诉了我们：性能与轻薄其实在5G时代可以共存。</p><p class=\"p_style\"><b>更多美图：</b></p><p class=\"jsmargin\"><img alt=\"DSC03256.jpg\" src=\"http://pic.zaeke.com/img160877917116048.jpeg\" width=\"2000\" height=\"1125\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"_1093473.jpg\" src=\"http://pic.zaeke.com/img160877907931900.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"_1093475.jpg\" src=\"http://pic.zaeke.com/img160877907958715.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"_1093559.jpg\" src=\"http://pic.zaeke.com/img160877914640092.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"_1093562.jpg\" src=\"http://pic.zaeke.com/img160877914857977.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"_1093563.jpg\" src=\"http://pic.zaeke.com/img160877915855506.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"_1093570.jpg\" src=\"http://pic.zaeke.com/img160877916241335.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p>', 'http://pic.zaeke.com/img160991555411554.jpeg', 11997, 0, 0, NULL, '2020-12-23 19:17:04', '2021-03-26 16:32:24');
INSERT INTO `article` VALUES (15855, '小米 11 初体验：变轻了，也变强了', '这是全球首款正式发布的骁龙888手机，也可能是大家能买到的第一款骁龙888手机。因为赶在2021年来临之际，小米率先在2020年12月28日发布了旗下最新年度旗...', '[object Object]', '小啤', 170, 'http://pic.zaeke.com/user_1533281478_8.png', '<p>这是全球首款正式发布的骁龙 888 手机，也可能是大家能买到的第一款骁龙 888 手机。因为赶在 2021 年来临之际，小米率先在 2020 年 12 月 28 日发布了旗下最新年度旗舰机型 —— 小米 11。毫无疑问，作为一款为明年市场而准备的小米数字系列旗舰机型，小米 11 的发布时间似乎比往年的前辈机型都要来得更早一些。</p><p><img alt=\"IMG_0104.jpg\" src=\"http://pic.zaeke.com/img160916627350103.jpeg\" width=\"1400\" height=\"933\"><br></p><p>本月初，高通骁龙 8 系新一代旗舰移动平台骁龙 888 正式发布，而小米 11 “理所当然”地成为了该平台的首发机型。骁龙 888 移动平台有着亮眼的账面数据，例如 CPU 部分采用了 1 超 3 大 4 小的核心组合；GPU 方面，其配备了全新的 Adreno660 图形处理器，据说该 GPU 相比较于上代骁龙 865 所采用的 Adreno650 GPU ，据说前者有着 35% 的性能提升，可是功耗却降低 20%。</p><p>在存储组合搭配方面，这次小米 11 也依旧有着亮眼表现，被小米 10 率先搭载的 LPDDR5，这次在小米 11 身上得到了再一次升级，其 LPDDR5 内存主频由小米 10 的 2750MHz，升级为 3200MHz，数据传输速率也从 5500Mbps 提升至 6400Mbps。配合 UFS 3.1 高速闪存，意味着该机型的机身存储性能表现也有了足够充沛的性能保障。</p><p><img alt=\"IMG_0128.jpg\" src=\"http://pic.zaeke.com/img160916687486367.jpeg\" width=\"1400\" height=\"933\"><br></p><p><img alt=\"IMG_0064.jpg\" src=\"http://pic.zaeke.com/img160916629151854.jpeg\" width=\"1400\" height=\"933\"><br></p><p>尽管骁龙 888 平台采用了 5nm 工艺制程，可是小米为了能让该 SoC 可以持续地带来稳定的高负载运算能力，小米 11 采用了 VC 液冷立体散热系统，通过大面积 VC 均热板、全新的超薄气凝胶热阻断材料、包夹三明治结构，使得机身局部热源散热更加均匀，从根本提高整机散热效率。</p><p>得益于高通骁龙 888 移动平台在 Wi-Fi 6、Wi-Fi 6E（6GHz 频段）、蓝牙 5.2 等连接能力支持，小米 11 也实现了第六代 Wi-Fi 802.11ax 标准的支持。配合早前预热的 Wi-Fi 6 增强版路由器，小米 11 可以带来比上一代机型翻一倍的频宽（从 80MHz 升级至 160MHz），同时编码也由 1024QAM 升级至 4096QAM，真正从根本实现了更快、密度更高的 Wi-Fi 连接能力。</p><p><img alt=\"IMG_0123.jpg\" src=\"http://pic.zaeke.com/img160916682885306.jpeg\" width=\"1400\" height=\"933\"><br></p><p>这次小米 11 的发布会使用了较大的篇幅去描述屏幕部分，因为该机型配备了 6.81 英寸 20:9 比例四曲面全面屏，其分辨率达到了 3200x1440，像素密度高达 515PPI，配合最新 E4 发光材料，峰值亮度高达 1500 尼特。相较于上一代产品的 90Hz 刷新率 + 180Hz 采样率组合，小米 11 这次升级为 120Hz 超高刷新率和 480Hz 超高触控采样率。</p><p>按照小米的描述，小米 11 的这块屏幕在 DisplayMate 屏幕评测中获得 A+ 评级。与此同时，小米 11 屏幕还覆盖了康宁最新的大猩猩玻璃 Victus，它与以往的康宁大猩猩玻璃产品在跌落高度、抗刮花性能方面均有了一定提升。</p><p><img alt=\"IMG_0132.jpg\" src=\"http://pic.zaeke.com/img160916684750999.jpeg\" width=\"1400\" height=\"933\"><br></p><p>考虑到“四曲面”屏幕在使用上可能带来的误触情况，小米 11 在机身正面上下方分别加入了两颗全新的握姿传感器，另外配合机身的陀螺仪等元器件，使得该机可实时监测用户的握持状态，从而智能判断机身的防误触区域。除此，小米 11 的超薄指纹识别还率先加入了全新心率检测功能，据说精准度媲美市面上智能手表产品，而该功能的入口位于“小米健康” APP。</p><p>相比较于小米 10 的 8.96mm 机身厚度和 208 克机身重量，小米 11 的机身重量有所下降，其中小米 11 的 AG 磨砂玻璃版本 196g，厚度 8.06mm，素皮版本 194g，厚度 8.56mm。在配色方面，小米 11 共计 5 个配色版本，除采用 AG 磨砂玻璃的黑、白、蓝三种颜色外，还有采用素皮背盖的烟紫和卡其色。</p><p><img alt=\"IMG_0069.jpg\" src=\"http://pic.zaeke.com/img160916645123752.jpeg\" width=\"1400\" height=\"933\"><br></p><p>小米 11 采用了后置三摄组合，其中延续了 1 亿像素主摄，采用的三星 HMX 图像传感器尺寸为 1/1.33 英寸，搭配 7P 镜头设计和 OIS 光学防抖。配合广角镜头，其能够提供 123° 的超大视野范围，满足日常拍摄建筑、风光场景下的广角拍摄能力。第三颗长焦微距镜头，等效 50mm 焦段，支持 3-7cm 自动对焦。通过长焦镜头足够远的拍摄距离特性，尽可能减少对动物干扰，从而达到提高拍摄成品率的作用。另外，小米 11 还配备了 2000 万像素超微型前置相机。</p><p><img alt=\"IMG_0151.jpg\" src=\"http://pic.zaeke.com/img160916681420851.jpeg\" width=\"1400\" height=\"933\"><br></p><p>配合最新的 MIUI 12 系统，小米 11 相机方面也新增了不少新功能，其中包括超级夜景视频、大师电影相机滤镜、电影镜头、魔法换天（新增更多天气效果滤镜），从根本丰富了相机功能的可玩性。</p><p>鉴于小米 11 采用了更轻薄的机身设计，因此在电池容量方面相比上一代产品有所下降，其搭载了 4600mAh 机身电池，搭配 55W 有线快充和 50W 无线快充。与此同时，小米 11 保留了 10W 无线反向充电功能特性。</p><p><img alt=\"IMG_0135.jpg\" src=\"http://pic.zaeke.com/img160916641535706.jpeg\" width=\"1400\" height=\"933\"><br></p><p><img alt=\"IMG_0087.jpg\" src=\"http://pic.zaeke.com/img160916637137465.jpeg\" width=\"1400\" height=\"933\"><br></p><p>小米 10 的外放效果得到了广泛认可，这次小米 11 在继续沿用 1216 超线性扬声单元的硬件基础之下，还邀请了哈曼卡顿进行了专业调音加持，进一步提高了外放效果。</p><p>专注于用户使用体验的小米 11，加入了蓝牙分享、蓝牙传音特性，通过这两项功能，蓝牙分享可以做到同时连接两个蓝牙音箱或两副蓝牙耳机，蓝牙传音则支持“无线麦克风”蓝牙传音功能，使连接了音箱的小米 11 摇身变成扩音器。其它方面，通过全新的小米自研录音降噪算法，可以有效过滤通话过程中的环境噪音，对人声进行智能增强。</p><p><img alt=\"IMG_0121.jpg\" src=\"http://pic.zaeke.com/img160916691383072.jpeg\" width=\"1400\" height=\"933\"><br></p><p>雷军表示，小米 11 的成本比以往都要高了不少，可是小米 11 的价格基本与小米 10 保持了一致。小米 11 8+128GB 售价为 3999 元；小米 11 8GB+256GB 售价 4299 元；小米 11 12GB+256GB 售价 4699 元。Mi Care （小米 11 全系保险）售价 679 元。</p><p><img alt=\"IMG_0122.jpg\" src=\"http://pic.zaeke.com/img160916691970032.jpeg\" width=\"1400\" height=\"933\"><br></p><p>小米 11 一共提供了“标准”和“套装”两个版本，其中标准版是不含充电器和数据线，套装版则配备了 55W GaN 充电器和数据线，而 55W GaN 充电器单独销售价格为 99 元。有趣的是，小米 11 “标准”和“套装”的售价是同价的，这就意味着用户可根据自己需求选择对应版本。</p><p>除此，小米 11 雷军签名版，通过小米首创的立体纹理玻璃，可以带来与众不同的后盖视觉效果。这款手机还采用了全新的外包装设计，通过这项名为视高迪水晶工艺，能带来凹凸立体的质感。小米 11 雷军签名版 12GB+256GB 售价 4699 元，其与同配置的标准版同价。 ​​​​</p><p><b>更多图赏：</b></p><p><img alt=\"IMG_0055.jpg\" src=\"http://pic.zaeke.com/img160916696385140.jpeg\" width=\"1400\" height=\"933\"></p><p><img alt=\"IMG_0118.jpg\" src=\"http://pic.zaeke.com/img160916698653270.jpeg\" width=\"1400\" height=\"933\"></p><p><img alt=\"IMG_0060.jpg\" src=\"http://pic.zaeke.com/img160916696330956.jpeg\" width=\"1400\" height=\"933\"></p><p><img alt=\"IMG_0101.jpg\" src=\"http://pic.zaeke.com/img160916697656923.jpeg\" width=\"1400\" height=\"933\"></p>', '<p class=\"p_style\">这是全球首款正式发布的骁龙 888 手机，也可能是大家能买到的第一款骁龙 888 手机。因为赶在 2021 年来临之际，小米率先在 2020 年 12 月 28 日发布了旗下最新年度旗舰机型 —— 小米 11。毫无疑问，作为一款为明年市场而准备的小米数字系列旗舰机型，小米 11 的发布时间似乎比往年的前辈机型都要来得更早一些。</p><p class=\"jsmargin\"><img alt=\"IMG_0104.jpg\" src=\"http://pic.zaeke.com/img160916627350103.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">本月初，高通骁龙 8 系新一代旗舰移动平台骁龙 888 正式发布，而小米 11 “理所当然”地成为了该平台的首发机型。骁龙 888 移动平台有着亮眼的账面数据，例如 CPU 部分采用了 1 超 3 大 4 小的核心组合；GPU 方面，其配备了全新的 Adreno660 图形处理器，据说该 GPU 相比较于上代骁龙 865 所采用的 Adreno650 GPU ，据说前者有着 35% 的性能提升，可是功耗却降低 20%。</p><p class=\"p_style\">在存储组合搭配方面，这次小米 11 也依旧有着亮眼表现，被小米 10 率先搭载的 LPDDR5，这次在小米 11 身上得到了再一次升级，其 LPDDR5 内存主频由小米 10 的 2750MHz，升级为 3200MHz，数据传输速率也从 5500Mbps 提升至 6400Mbps。配合 UFS 3.1 高速闪存，意味着该机型的机身存储性能表现也有了足够充沛的性能保障。</p><p class=\"jsmargin\"><img alt=\"IMG_0128.jpg\" src=\"http://pic.zaeke.com/img160916687486367.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_0064.jpg\" src=\"http://pic.zaeke.com/img160916629151854.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">尽管骁龙 888 平台采用了 5nm 工艺制程，可是小米为了能让该 SoC 可以持续地带来稳定的高负载运算能力，小米 11 采用了 VC 液冷立体散热系统，通过大面积 VC 均热板、全新的超薄气凝胶热阻断材料、包夹三明治结构，使得机身局部热源散热更加均匀，从根本提高整机散热效率。</p><p class=\"p_style\">得益于高通骁龙 888 移动平台在 Wi-Fi 6、Wi-Fi 6E（6GHz 频段）、蓝牙 5.2 等连接能力支持，小米 11 也实现了第六代 Wi-Fi 802.11ax 标准的支持。配合早前预热的 Wi-Fi 6 增强版路由器，小米 11 可以带来比上一代机型翻一倍的频宽（从 80MHz 升级至 160MHz），同时编码也由 1024QAM 升级至 4096QAM，真正从根本实现了更快、密度更高的 Wi-Fi 连接能力。</p><p class=\"jsmargin\"><img alt=\"IMG_0123.jpg\" src=\"http://pic.zaeke.com/img160916682885306.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">这次小米 11 的发布会使用了较大的篇幅去描述屏幕部分，因为该机型配备了 6.81 英寸 20:9 比例四曲面全面屏，其分辨率达到了 3200x1440，像素密度高达 515PPI，配合最新 E4 发光材料，峰值亮度高达 1500 尼特。相较于上一代产品的 90Hz 刷新率 + 180Hz 采样率组合，小米 11 这次升级为 120Hz 超高刷新率和 480Hz 超高触控采样率。</p><p class=\"p_style\">按照小米的描述，小米 11 的这块屏幕在 DisplayMate 屏幕评测中获得 A+ 评级。与此同时，小米 11 屏幕还覆盖了康宁最新的大猩猩玻璃 Victus，它与以往的康宁大猩猩玻璃产品在跌落高度、抗刮花性能方面均有了一定提升。</p><p class=\"jsmargin\"><img alt=\"IMG_0132.jpg\" src=\"http://pic.zaeke.com/img160916684750999.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">考虑到“四曲面”屏幕在使用上可能带来的误触情况，小米 11 在机身正面上下方分别加入了两颗全新的握姿传感器，另外配合机身的陀螺仪等元器件，使得该机可实时监测用户的握持状态，从而智能判断机身的防误触区域。除此，小米 11 的超薄指纹识别还率先加入了全新心率检测功能，据说精准度媲美市面上智能手表产品，而该功能的入口位于“小米健康” APP。</p><p class=\"p_style\">相比较于小米 10 的 8.96mm 机身厚度和 208 克机身重量，小米 11 的机身重量有所下降，其中小米 11 的 AG 磨砂玻璃版本 196g，厚度 8.06mm，素皮版本 194g，厚度 8.56mm。在配色方面，小米 11 共计 5 个配色版本，除采用 AG 磨砂玻璃的黑、白、蓝三种颜色外，还有采用素皮背盖的烟紫和卡其色。</p><p class=\"jsmargin\"><img alt=\"IMG_0069.jpg\" src=\"http://pic.zaeke.com/img160916645123752.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">小米 11 采用了后置三摄组合，其中延续了 1 亿像素主摄，采用的三星 HMX 图像传感器尺寸为 1/1.33 英寸，搭配 7P 镜头设计和 OIS 光学防抖。配合广角镜头，其能够提供 123° 的超大视野范围，满足日常拍摄建筑、风光场景下的广角拍摄能力。第三颗长焦微距镜头，等效 50mm 焦段，支持 3-7cm 自动对焦。通过长焦镜头足够远的拍摄距离特性，尽可能减少对动物干扰，从而达到提高拍摄成品率的作用。另外，小米 11 还配备了 2000 万像素超微型前置相机。</p><p class=\"jsmargin\"><img alt=\"IMG_0151.jpg\" src=\"http://pic.zaeke.com/img160916681420851.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">配合最新的 MIUI 12 系统，小米 11 相机方面也新增了不少新功能，其中包括超级夜景视频、大师电影相机滤镜、电影镜头、魔法换天（新增更多天气效果滤镜），从根本丰富了相机功能的可玩性。</p><p class=\"p_style\">鉴于小米 11 采用了更轻薄的机身设计，因此在电池容量方面相比上一代产品有所下降，其搭载了 4600mAh 机身电池，搭配 55W 有线快充和 50W 无线快充。与此同时，小米 11 保留了 10W 无线反向充电功能特性。</p><p class=\"jsmargin\"><img alt=\"IMG_0135.jpg\" src=\"http://pic.zaeke.com/img160916641535706.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_0087.jpg\" src=\"http://pic.zaeke.com/img160916637137465.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">小米 10 的外放效果得到了广泛认可，这次小米 11 在继续沿用 1216 超线性扬声单元的硬件基础之下，还邀请了哈曼卡顿进行了专业调音加持，进一步提高了外放效果。</p><p class=\"p_style\">专注于用户使用体验的小米 11，加入了蓝牙分享、蓝牙传音特性，通过这两项功能，蓝牙分享可以做到同时连接两个蓝牙音箱或两副蓝牙耳机，蓝牙传音则支持“无线麦克风”蓝牙传音功能，使连接了音箱的小米 11 摇身变成扩音器。其它方面，通过全新的小米自研录音降噪算法，可以有效过滤通话过程中的环境噪音，对人声进行智能增强。</p><p class=\"jsmargin\"><img alt=\"IMG_0121.jpg\" src=\"http://pic.zaeke.com/img160916691383072.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">雷军表示，小米 11 的成本比以往都要高了不少，可是小米 11 的价格基本与小米 10 保持了一致。小米 11 8+128GB 售价为 3999 元；小米 11 8GB+256GB 售价 4299 元；小米 11 12GB+256GB 售价 4699 元。Mi Care （小米 11 全系保险）售价 679 元。</p><p class=\"jsmargin\"><img alt=\"IMG_0122.jpg\" src=\"http://pic.zaeke.com/img160916691970032.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">小米 11 一共提供了“标准”和“套装”两个版本，其中标准版是不含充电器和数据线，套装版则配备了 55W GaN 充电器和数据线，而 55W GaN 充电器单独销售价格为 99 元。有趣的是，小米 11 “标准”和“套装”的售价是同价的，这就意味着用户可根据自己需求选择对应版本。</p><p class=\"p_style\">除此，小米 11 雷军签名版，通过小米首创的立体纹理玻璃，可以带来与众不同的后盖视觉效果。这款手机还采用了全新的外包装设计，通过这项名为视高迪水晶工艺，能带来凹凸立体的质感。小米 11 雷军签名版 12GB+256GB 售价 4699 元，其与同配置的标准版同价。 ​​​​</p><p class=\"p_style\"><b>更多图赏：</b></p><p class=\"jsmargin\"><img alt=\"IMG_0055.jpg\" src=\"http://pic.zaeke.com/img160916696385140.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_0118.jpg\" src=\"http://pic.zaeke.com/img160916698653270.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_0060.jpg\" src=\"http://pic.zaeke.com/img160916696330956.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_0101.jpg\" src=\"http://pic.zaeke.com/img160916697656923.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"></p>', 'http://pic.zaeke.com/img160920713598562.jpeg', 4177, 0, 0, NULL, '2020-12-28 17:59:00', '2021-03-27 02:54:34');
INSERT INTO `article` VALUES (15864, '轻薄优雅与不错的性能，OPPO Reno5 Pro尽数收入囊中', '从R系列到如今的Reno，OPPO的线下主力机型定位也在逐步改变，例如R系列从来和真旗舰没啥关系，但OPPOReno5系列却有旗舰机的超大杯可选。因此我们手里身...', '[object Object]', '烫头孙师傅', 38, 'http://pic.zaeke.com/user_1533281477_5.png', '<p>从R系列到如今的Reno，OPPO的线下主力机型定位也在逐步改变，例如R系列从来和真旗舰没啥关系，但OPPO Reno5系列却有旗舰机的超大杯可选。因此我们手里身为大杯的OPPO Reno5 Pro，则更像是曾经R系列的延续。</p><p><img src=\"http://pic.zaeke.com/img160915371746803.jpeg\" alt=\"F31A7597.jpg\">  </p><p>不过相较于曾经那些搭载骁龙6/7系移动平台R系列来说，如今的OPPO Reno5 Pro在性能上更加友好不说，外观上也逐渐开始形成属于自己的独特风格。这或许是款有些向线上市场延伸的机型，但与此同时依然有浓浓的线下基因。</p><p>这份基因或许意味着品质，或许意味着颜值，或许意味着  OPPO的销量中坚，可无论如何这熟悉的Reno始终是我们最常见到的、属于OPPO的形象代表。Reno就是如此重要，而OPPO Reno5 Pro正是如此。</p><p style=\"text-align: center;\"><strong>轻薄是最好的第一印象</strong></p><p>能看出来OPPO的确在那个被他们称作星钻工艺的粗磨砂玻璃处理工艺上尝到了甜头，这个通过精细蚀刻工艺让金字塔形状晶体结构遍布背板的工艺，在我看来确实兼顾了美观度与手感，防滑体验也很棒。</p><p><img src=\"http://pic.zaeke.com/img160915029437329.jpeg\" alt=\"_1093547.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160915371054801.jpeg\" alt=\"F31A7609.jpg\">  </p><p>或者换句话说，无论是镜面玻璃还是常规的AG工艺，我这汗手还是不太敢不戴壳直接拿，而星钻工艺的防滑能力确实让我敢不怎么用手机壳了。要知道外出拍摄样张的时候玩意手机掉进海里，是不会有海神钻出来问我丢到是金Reno还是银Reno的。</p><p>所以如果你是个“裸奔”主义者，我觉得还是有必要尝试下星钻工艺的体验。</p><p><img src=\"http://pic.zaeke.com/img160915033771633.jpeg\" alt=\"_1093579.jpg\">  </p><p>所以OPPO Reno5 Pro节约成本了吗？当然，比如塑料边框就是节约成本的其中一环，不过只能说在节约成本这件事情上OPPO显得很聪明。例如OPPO Reno5 Pro是将是塑料材质用在了中框上，换句话说这份成本节约在实际体验上的确感知不强。</p><p>诚然这款机型器的售价并不算便宜，不过考虑到Reno系列一贯主打轻薄，在感知不强的位置使用塑料材质以降低机身总体重量，也有除节约成本之外的第二层意义。况且这个塑料中框的手感居然出人意料地还不错，很难通过纯粹的触摸来分辨材质，这点已经做得非常优秀了，</p><p>啊，不过如果说你不管轻薄不轻薄，只想要金属中框的话，那这个话题就该到此为止了。</p><p><img src=\"http://pic.zaeke.com/img160915366012811.jpeg\" alt=\"F31A7606.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160915366042265.jpeg\" alt=\"F31A7595.jpg\"></p><p>从性能上来说，搭载联发科天玑1000+处理器的OPPO Reno5 Pro，确实比曾经的那些骁龙6/7系机型更好。虽然说联发科天玑1000+的确打不过同期的高通旗舰移动平台，但对于这款以往定位相对线下的系列来说却是非常大的进步。</p><p>在日常使用当中，我基本没有感受到OPPO Reno5 Pro会出现明显的卡顿，即便在游戏中开启较高画质时也是如此。我觉得只要你不是那种个非得破解和平精英最高画质+极限帧率的用户，那么联发科天玑1000+处理器在使用中或许并不会有那么强大的性能不足，尤其是能够被你感知到的那种。</p><p>并且OPPO Reno5 Pro还配备了6.55英寸的90Hz高感曲面屏，触控采样率达到180Hz。这块屏幕尽管不是120Hz屏，不过流畅度依然比较优秀，并且较高的触控采样率也使其触控响应也比较灵敏。并且OPPO中高端机型采用的屏幕一直都有不错的素质表现，因此我对这款屏幕的现实效果没啥意见。</p><p><img src=\"http://pic.zaeke.com/img160915033648896.jpeg\" alt=\"_1093574.jpg\">  </p><p>只不过不知道是因为曲面屏的问题，还是得归咎于手势操作防误触的调校不到位，总之我是不推荐诸位使用OPPO Reno5 Pro玩快节奏的枪战类游戏。</p><p>原因很简单，在使用手势操作的时候，一旦战斗节奏激烈并需要快速滑动屏幕操作时（例如CF手游），有非常高的概率会触发OPPO Reno5 Pro的全面屏手势。这是因为横屏时该机从底部向上滑动两次可返回桌面，而很多操作会在意一定程度上导致触发全面屏手势。</p><p><img src=\"http://pic.zaeke.com/img160915377887381.jpeg\" alt=\"Screenshot_2020-12-28-15-43-39-89_6d56ab8c2b0bbb2edf6a1db1b5de8150.jpg\">  </p><p>游戏操作容易误触全面屏手势看似问题不大，实则影响明显。因为触发全面屏手势也就同时意味着你在游戏中的操作没有被触发。这个问题很难真正意义上进行可重复的复现，有时或整个屏幕的内容全部无法操作，有时只是前进的操作无法执行，但对于游戏操作的影响必然存在。</p><p>诚然这个问题并不是每个人都会遇到，也并不是每个游戏都会出现，不过只要遇到的话对自己来说肯定是百分百，所以这个问题还需要OPPO的软件工程师设法修复。</p><p><img src=\"http://pic.zaeke.com/img160915379142605.jpeg\" alt=\"F31A7681.jpg\">  </p><p>从个人观点来说，OPPO Reno5 Pro确实还是有着非常明显的线下特色。只不过在一定程度上的成本节约之外，同时在那些容易感知的部分保持了一定的精致性。或许有蛮多人更希望用上金属中框，不过加钱确实能够买到金属中框的超大杯版本。</p><p>而更加便宜、轻薄，同时也不失性能的OPPO Reno5 Pro，显然就成为了更面向线下，也不过分丢失线上竞争力的型号了。</p><p style=\"text-align: center;\"><strong>动态与观感，或许不是冤家</strong></p><p>从硬件上来说，OPPO Reno5 Pro的相机还是蛮符合线下机型定位的。</p><p><img src=\"http://pic.zaeke.com/img160915036358634.jpeg\" alt=\"DSC03269.jpg\">  </p><p>OPPO Reno5 Pro的后置相机为6400万像素主摄+800万像素超广角相机+200万像素微距相机+200万像素人像相机的后置四摄。其中主摄和超广角作用更大些，剩下两颗200万像素的相机则基本上是为了实现拥有后置四摄的任务而生。而该机的前置相机则是3200万像素。</p><p>聊OPPO Reno5 Pro的拍照之前我想先聊聊审美。</p><p>对于手机拍照的审美，一部分纯粹的参数党追求绝对的“高光有细节，暗部无死黑”的参数式正义，另一部分人则不太喜欢相对平实的画面。当然，在我个人看，这两个追求相对都有些极端，毕竟对于审美的追求只有一件事情就够了：好看就行。</p><p>换句话说在这个AI与计算摄影深度介入前期的时代，智能手机的前期算法本身就承载着很多传统上在后期才能实现的。无论是立体感强还是相对平实都可以是纯粹的过程，重要的是结果究竟好不好，过于纠结标准答案就显得过于教条。</p><p><img src=\"http://pic.zaeke.com/img160915331912119.jpeg\" alt=\"IMG20201221154554.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160915331819098.jpeg\" alt=\"IMG20201221135300.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160915331858413.jpeg\" alt=\"IMG20201221151125.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160915334480132.jpeg\" alt=\"IMG20201221170513.jpg\">  </p><p>先说说白天，OPPO Reno5 Pro的主摄面其实未必总是在追求动态范围本身，有些场景之下是有一定的过曝或死黑发生的，显然不是无脑高动态范围。而在光线充足的环境之下，该机的主摄色彩也相对朴素些，并非浓妆艳抹的形态。</p><p>只不过在白天时，该机的画面细节并不如一些搭载高通骁龙旗舰移动平台的同底机型丰富，或者说并非顶级水准，但整体观感的舒适度倒是还不错，不过这个影调显然算不上顶级水准。</p><p>OPPO Reno5 Pro显然属于整体影调相对常平实的一种，的的确确是高光有细节暗部无死黑的类型。可尽管我喜欢更有立体感的影调，但相较于有些手机在画面平实同时并没有合理的色彩与明暗关系，从而导致画面观感极差的情况不同，OPPO Reno5 Pro在画面相对平的同时拥有不错的影调以及非常舒服的色彩。</p><p>换句话说，无论是追求合理的反差还是追求动态范围，都一样有办法把画面做的好看。所以对于那些动态范围高而又直出观感恶劣的手机来说，高动态范围或许只是调教审美不足的遮羞布。而OPPO Reno5 Pro在这方面的表现则显得蛮妥帖，尽管后面因为高动态范围显得较平，但观感却并不差。</p><p><img src=\"http://pic.zaeke.com/img160915337583161.jpeg\" alt=\"IMG20201221184516.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160915341883068.jpeg\" alt=\"IMG20201221184922.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160915334938665.jpeg\" alt=\"IMG20201221183606.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160915335259653.jpeg\" alt=\"IMG20201221184214.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160915344081381.jpeg\" alt=\"IMG20201221185149.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160915344652922.jpeg\" alt=\"IMG20201221185524.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160915344183086.jpeg\" alt=\"IMG20201221185303.jpg\">  </p><p>除了色彩让画面观感极为优秀之外，OPPO Reno5 Pro也拥有非常不错的夜景降噪表现，尽管与那些追求极致画质的类似配置机型不同，及画面细节表现与锐度并不是最好的，可其画面伪色控制以及降噪表现都非常出彩，画面平滑度极高，并且整体都没有某处画质崩溃（尤其是边角）的情况。</p><p>总而言之，如果你蛮在乎动态范围与降噪表现，那么OPPO Reno5 Pro的表现其实蛮不错。或者换句话说，在那些追求动态范围的机型中，OPPO Reno5 Pro是其中观感相对出彩的。可如果你更希望购买算法能够知道什么时候应该不追求动态范围，在画面立体感上更明晰些的机型，我觉得就还是再看看吧。</p><p><img src=\"http://pic.zaeke.com/img160915331169765.jpeg\" alt=\"IMG20201221135504.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160915331159614.jpeg\" alt=\"IMG20201221154606.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img160915331184473.jpeg\" alt=\"IMG20201221184409.jpg\">  </p><p>而在超广角方面，OPPO Reno5 Pro的画质就和类似参数的超广角差不多，画面细节很难说丰富。不过OPPO对于这颗超广角相机的调校显然是相对成熟的，尽管画面细节无法期待，对于边角噪点与伪色的控制非常到位。</p><p>而在夜间，这颗超广角相机同样受限于硬件性能没法拍出细节丰富的画面，不过与此同时其画面纯净度与降噪表现都显得教我出彩。至于整体发色上，这颗超广角相机在观感上与主摄也相对接近，观感也类似。</p><p>啊，那么又到了熟悉的环节，那两颗200万像素的副摄没必要提了吧？对吧？</p><p style=\"text-align: center;\"><strong>给你的小拇指放个假吧</strong></p><p>很熟悉，对吧，OPPO Reno5 Pro依然还是蛮线下的。</p><p>但在蛮线下的同时，我们的确能够感受到Reno系列的进化。在保持轻薄机身的同时，OPPO Reno5 Pro的性能也基本可以说是旗舰级，或者你接受不了天玑的话说是次旗舰性能也没啥问题。并且尽管主打的相机或许有妥协市场的凑数行为，但那两颗不凑数的相机在实际表现上也是合格的。</p><p><img src=\"http://pic.zaeke.com/img160915363424830.jpeg\" alt=\"F31A7682.jpg\">  </p><p>以往的中高端线下机型是重体验而轻性能，OPPO Reno5 Pro的天平向性能上倾斜了很多之后，这个问题显然得到了不小的改善。即便是线上用户，或许也愿意试图接受这款轻薄漂亮的机型——尤其是在这个物理上的“重量级机型”层出不穷的年代。</p><p>给你的小拇指放个假，或许就能成为选择OPPO Reno5 Pro的理由？</p>', '<p class=\"p_style\">从R系列到如今的Reno，OPPO的线下主力机型定位也在逐步改变，例如R系列从来和真旗舰没啥关系，但OPPO Reno5系列却有旗舰机的超大杯可选。因此我们手里身为大杯的OPPO Reno5 Pro，则更像是曾经R系列的延续。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915371746803.jpeg\" alt=\"F31A7597.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">不过相较于曾经那些搭载骁龙6/7系移动平台R系列来说，如今的OPPO Reno5 Pro在性能上更加友好不说，外观上也逐渐开始形成属于自己的独特风格。这或许是款有些向线上市场延伸的机型，但与此同时依然有浓浓的线下基因。</p><p class=\"p_style\">这份基因或许意味着品质，或许意味着颜值，或许意味着  OPPO的销量中坚，可无论如何这熟悉的Reno始终是我们最常见到的、属于OPPO的形象代表。Reno就是如此重要，而OPPO Reno5 Pro正是如此。</p><p style=\"text-align: center;\" class=\"p_style\"><strong>轻薄是最好的第一印象</strong></p><p class=\"p_style\">能看出来OPPO的确在那个被他们称作星钻工艺的粗磨砂玻璃处理工艺上尝到了甜头，这个通过精细蚀刻工艺让金字塔形状晶体结构遍布背板的工艺，在我看来确实兼顾了美观度与手感，防滑体验也很棒。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915029437329.jpeg\" alt=\"_1093547.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915371054801.jpeg\" alt=\"F31A7609.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">或者换句话说，无论是镜面玻璃还是常规的AG工艺，我这汗手还是不太敢不戴壳直接拿，而星钻工艺的防滑能力确实让我敢不怎么用手机壳了。要知道外出拍摄样张的时候玩意手机掉进海里，是不会有海神钻出来问我丢到是金Reno还是银Reno的。</p><p class=\"p_style\">所以如果你是个“裸奔”主义者，我觉得还是有必要尝试下星钻工艺的体验。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915033771633.jpeg\" alt=\"_1093579.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">所以OPPO Reno5 Pro节约成本了吗？当然，比如塑料边框就是节约成本的其中一环，不过只能说在节约成本这件事情上OPPO显得很聪明。例如OPPO Reno5 Pro是将是塑料材质用在了中框上，换句话说这份成本节约在实际体验上的确感知不强。</p><p class=\"p_style\">诚然这款机型器的售价并不算便宜，不过考虑到Reno系列一贯主打轻薄，在感知不强的位置使用塑料材质以降低机身总体重量，也有除节约成本之外的第二层意义。况且这个塑料中框的手感居然出人意料地还不错，很难通过纯粹的触摸来分辨材质，这点已经做得非常优秀了，</p><p class=\"p_style\">啊，不过如果说你不管轻薄不轻薄，只想要金属中框的话，那这个话题就该到此为止了。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915366012811.jpeg\" alt=\"F31A7606.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915366042265.jpeg\" alt=\"F31A7595.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">从性能上来说，搭载联发科天玑1000+处理器的OPPO Reno5 Pro，确实比曾经的那些骁龙6/7系机型更好。虽然说联发科天玑1000+的确打不过同期的高通旗舰移动平台，但对于这款以往定位相对线下的系列来说却是非常大的进步。</p><p class=\"p_style\">在日常使用当中，我基本没有感受到OPPO Reno5 Pro会出现明显的卡顿，即便在游戏中开启较高画质时也是如此。我觉得只要你不是那种个非得破解和平精英最高画质+极限帧率的用户，那么联发科天玑1000+处理器在使用中或许并不会有那么强大的性能不足，尤其是能够被你感知到的那种。</p><p class=\"p_style\">并且OPPO Reno5 Pro还配备了6.55英寸的90Hz高感曲面屏，触控采样率达到180Hz。这块屏幕尽管不是120Hz屏，不过流畅度依然比较优秀，并且较高的触控采样率也使其触控响应也比较灵敏。并且OPPO中高端机型采用的屏幕一直都有不错的素质表现，因此我对这款屏幕的现实效果没啥意见。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915033648896.jpeg\" alt=\"_1093574.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">只不过不知道是因为曲面屏的问题，还是得归咎于手势操作防误触的调校不到位，总之我是不推荐诸位使用OPPO Reno5 Pro玩快节奏的枪战类游戏。</p><p class=\"p_style\">原因很简单，在使用手势操作的时候，一旦战斗节奏激烈并需要快速滑动屏幕操作时（例如CF手游），有非常高的概率会触发OPPO Reno5 Pro的全面屏手势。这是因为横屏时该机从底部向上滑动两次可返回桌面，而很多操作会在意一定程度上导致触发全面屏手势。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915377887381.jpeg\" alt=\"Screenshot_2020-12-28-15-43-39-89_6d56ab8c2b0bbb2edf6a1db1b5de8150.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">游戏操作容易误触全面屏手势看似问题不大，实则影响明显。因为触发全面屏手势也就同时意味着你在游戏中的操作没有被触发。这个问题很难真正意义上进行可重复的复现，有时或整个屏幕的内容全部无法操作，有时只是前进的操作无法执行，但对于游戏操作的影响必然存在。</p><p class=\"p_style\">诚然这个问题并不是每个人都会遇到，也并不是每个游戏都会出现，不过只要遇到的话对自己来说肯定是百分百，所以这个问题还需要OPPO的软件工程师设法修复。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915379142605.jpeg\" alt=\"F31A7681.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">从个人观点来说，OPPO Reno5 Pro确实还是有着非常明显的线下特色。只不过在一定程度上的成本节约之外，同时在那些容易感知的部分保持了一定的精致性。或许有蛮多人更希望用上金属中框，不过加钱确实能够买到金属中框的超大杯版本。</p><p class=\"p_style\">而更加便宜、轻薄，同时也不失性能的OPPO Reno5 Pro，显然就成为了更面向线下，也不过分丢失线上竞争力的型号了。</p><p style=\"text-align: center;\" class=\"p_style\"><strong>动态与观感，或许不是冤家</strong></p><p class=\"p_style\">从硬件上来说，OPPO Reno5 Pro的相机还是蛮符合线下机型定位的。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915036358634.jpeg\" alt=\"DSC03269.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">OPPO Reno5 Pro的后置相机为6400万像素主摄+800万像素超广角相机+200万像素微距相机+200万像素人像相机的后置四摄。其中主摄和超广角作用更大些，剩下两颗200万像素的相机则基本上是为了实现拥有后置四摄的任务而生。而该机的前置相机则是3200万像素。</p><p class=\"p_style\">聊OPPO Reno5 Pro的拍照之前我想先聊聊审美。</p><p class=\"p_style\">对于手机拍照的审美，一部分纯粹的参数党追求绝对的“高光有细节，暗部无死黑”的参数式正义，另一部分人则不太喜欢相对平实的画面。当然，在我个人看，这两个追求相对都有些极端，毕竟对于审美的追求只有一件事情就够了：好看就行。</p><p class=\"p_style\">换句话说在这个AI与计算摄影深度介入前期的时代，智能手机的前期算法本身就承载着很多传统上在后期才能实现的。无论是立体感强还是相对平实都可以是纯粹的过程，重要的是结果究竟好不好，过于纠结标准答案就显得过于教条。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915331912119.jpeg\" alt=\"IMG20201221154554.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915331819098.jpeg\" alt=\"IMG20201221135300.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915331858413.jpeg\" alt=\"IMG20201221151125.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915334480132.jpeg\" alt=\"IMG20201221170513.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">先说说白天，OPPO Reno5 Pro的主摄面其实未必总是在追求动态范围本身，有些场景之下是有一定的过曝或死黑发生的，显然不是无脑高动态范围。而在光线充足的环境之下，该机的主摄色彩也相对朴素些，并非浓妆艳抹的形态。</p><p class=\"p_style\">只不过在白天时，该机的画面细节并不如一些搭载高通骁龙旗舰移动平台的同底机型丰富，或者说并非顶级水准，但整体观感的舒适度倒是还不错，不过这个影调显然算不上顶级水准。</p><p class=\"p_style\">OPPO Reno5 Pro显然属于整体影调相对常平实的一种，的的确确是高光有细节暗部无死黑的类型。可尽管我喜欢更有立体感的影调，但相较于有些手机在画面平实同时并没有合理的色彩与明暗关系，从而导致画面观感极差的情况不同，OPPO Reno5 Pro在画面相对平的同时拥有不错的影调以及非常舒服的色彩。</p><p class=\"p_style\">换句话说，无论是追求合理的反差还是追求动态范围，都一样有办法把画面做的好看。所以对于那些动态范围高而又直出观感恶劣的手机来说，高动态范围或许只是调教审美不足的遮羞布。而OPPO Reno5 Pro在这方面的表现则显得蛮妥帖，尽管后面因为高动态范围显得较平，但观感却并不差。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915337583161.jpeg\" alt=\"IMG20201221184516.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915341883068.jpeg\" alt=\"IMG20201221184922.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915334938665.jpeg\" alt=\"IMG20201221183606.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915335259653.jpeg\" alt=\"IMG20201221184214.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915344081381.jpeg\" alt=\"IMG20201221185149.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915344652922.jpeg\" alt=\"IMG20201221185524.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915344183086.jpeg\" alt=\"IMG20201221185303.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">除了色彩让画面观感极为优秀之外，OPPO Reno5 Pro也拥有非常不错的夜景降噪表现，尽管与那些追求极致画质的类似配置机型不同，及画面细节表现与锐度并不是最好的，可其画面伪色控制以及降噪表现都非常出彩，画面平滑度极高，并且整体都没有某处画质崩溃（尤其是边角）的情况。</p><p class=\"p_style\">总而言之，如果你蛮在乎动态范围与降噪表现，那么OPPO Reno5 Pro的表现其实蛮不错。或者换句话说，在那些追求动态范围的机型中，OPPO Reno5 Pro是其中观感相对出彩的。可如果你更希望购买算法能够知道什么时候应该不追求动态范围，在画面立体感上更明晰些的机型，我觉得就还是再看看吧。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915331169765.jpeg\" alt=\"IMG20201221135504.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915331159614.jpeg\" alt=\"IMG20201221154606.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915331184473.jpeg\" alt=\"IMG20201221184409.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">而在超广角方面，OPPO Reno5 Pro的画质就和类似参数的超广角差不多，画面细节很难说丰富。不过OPPO对于这颗超广角相机的调校显然是相对成熟的，尽管画面细节无法期待，对于边角噪点与伪色的控制非常到位。</p><p class=\"p_style\">而在夜间，这颗超广角相机同样受限于硬件性能没法拍出细节丰富的画面，不过与此同时其画面纯净度与降噪表现都显得教我出彩。至于整体发色上，这颗超广角相机在观感上与主摄也相对接近，观感也类似。</p><p class=\"p_style\">啊，那么又到了熟悉的环节，那两颗200万像素的副摄没必要提了吧？对吧？</p><p style=\"text-align: center;\" class=\"p_style\"><strong>给你的小拇指放个假吧</strong></p><p class=\"p_style\">很熟悉，对吧，OPPO Reno5 Pro依然还是蛮线下的。</p><p class=\"p_style\">但在蛮线下的同时，我们的确能够感受到Reno系列的进化。在保持轻薄机身的同时，OPPO Reno5 Pro的性能也基本可以说是旗舰级，或者你接受不了天玑的话说是次旗舰性能也没啥问题。并且尽管主打的相机或许有妥协市场的凑数行为，但那两颗不凑数的相机在实际表现上也是合格的。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160915363424830.jpeg\" alt=\"F31A7682.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">以往的中高端线下机型是重体验而轻性能，OPPO Reno5 Pro的天平向性能上倾斜了很多之后，这个问题显然得到了不小的改善。即便是线上用户，或许也愿意试图接受这款轻薄漂亮的机型——尤其是在这个物理上的“重量级机型”层出不穷的年代。</p><p class=\"p_style\">给你的小拇指放个假，或许就能成为选择OPPO Reno5 Pro的理由？</p>', 'http://pic.zaeke.com/img161033208543815.jpeg', 8544, 0, 0, NULL, '2020-12-30 22:42:34', '2021-03-26 09:32:41');
INSERT INTO `article` VALUES (15871, '高通骁龙 480 移动平台登场：敢越级', '高通正式发布骁龙4系列最新成员，也是该系列首次具备5G网络连接能力的骁龙4805G移动平台。等等，别看到4开头就说告辞了，确实4系列的定位以及定义决定了这是普惠...', '[object Object]', '冯伟文', 19, 'http://pic.zaeke.com/user_1610094930png', '<p>高通正式发布骁龙 4 系列最新成员，也是该系列首次具备 5G 网络连接能力的骁龙 480 5G 移动平台。等等，别看到 4 开头就说告辞了，确实 4 系列的定位以及定义决定了这是普惠型产品，高通也明确表明这将加速全球 5G 商用化进程，并面向海量智能手机市场。</p><p>可是不同于该系列的前辈们，骁龙 480 移动平台可以说 是 4 系的定位，有着 6 系的性能和用着 7 系的制程，甚至还有点 8 系列的特性呢。难怪 HMD Global、一加、OPPO 以及 vivo 均表示将会在近期发布搭载该移动平台的相关机型，预计定价会相当美丽。</p><p><img src=\"http://pic.zaeke.com/img160974332133538.jpeg\" alt=\"0023.jpg\">  </p><p>首先要肯定的，绝对是性能。毕竟骁龙 480 移动平台采用的是 8nm 制程工艺，搭载 Kyro 460 CPU、Adreno 619 GPU 以及 Hexagon 686。这就使得其相较上一代有着 100% CPU 性能提升、100% GPU 性能提升以及 70% AI 性能提升。这已经可以说是 7 系制程和 6 系性能了，高通也表示骁龙 480 移动平台的性能要比骁龙 675 移动平台的更强。</p><p><img src=\"http://pic.zaeke.com/img160974911471126.jpeg\" alt=\"微信图片_20210104163115.jpg\">  </p><p>值得注意的，是骁龙 480 移动平台还采用了 Spectra 345 ISP，其中单个摄像头最高可以支持到 6400 万像素拍照。然而重点是这可是三 ISP，这就意味着搭载该处理器的机型可以同时使用三个摄像头进行拍照或者是视频录制，并且获得更顺畅的变焦效果。此外骁龙 480 移动平台还支持 120Hz、FHS+ 显示，以及支持图形驱动更新特性，还有 aptX、Quick Charge 4+ 等高通传统艺能加持。</p><p><img src=\"http://pic.zaeke.com/img160974702519694.jpeg\" alt=\"0015.jpg\"></p><p><img src=\"http://pic.zaeke.com/img160974702481202.jpeg\" alt=\"0018.jpg\">  </p><p>最关键的，当然是网络连接能力了啦。骁龙 480 移动平台是该系列首款具备 5G 网络连接能力的处理器。其采用的骁龙 X51 5G 调制解调器以及射频系统可以支持毫米波和 Sub-6GHz 5G 连接，也支持独立（SA）与非独立（NSA）组网模式，还支持 TDD、FDD 以及 DSS（动态频谱共享） 等特性。</p><p>得益于 FastConnect 6200 移动连接子系统，骁龙 480 移动平台还具备蓝牙 5.1、2*2 W-Fi 以及部分 Wi-Fi 6 关键特性，这都让搭载该处理器的普惠型机型能够给予使用者足够圆满的使用体验。</p>', '<p class=\"p_style\">高通正式发布骁龙 4 系列最新成员，也是该系列首次具备 5G 网络连接能力的骁龙 480 5G 移动平台。等等，别看到 4 开头就说告辞了，确实 4 系列的定位以及定义决定了这是普惠型产品，高通也明确表明这将加速全球 5G 商用化进程，并面向海量智能手机市场。</p><p class=\"p_style\">可是不同于该系列的前辈们，骁龙 480 移动平台可以说 是 4 系的定位，有着 6 系的性能和用着 7 系的制程，甚至还有点 8 系列的特性呢。难怪 HMD Global、一加、OPPO 以及 vivo 均表示将会在近期发布搭载该移动平台的相关机型，预计定价会相当美丽。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160974332133538.jpeg\" alt=\"0023.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">首先要肯定的，绝对是性能。毕竟骁龙 480 移动平台采用的是 8nm 制程工艺，搭载 Kyro 460 CPU、Adreno 619 GPU 以及 Hexagon 686。这就使得其相较上一代有着 100% CPU 性能提升、100% GPU 性能提升以及 70% AI 性能提升。这已经可以说是 7 系制程和 6 系性能了，高通也表示骁龙 480 移动平台的性能要比骁龙 675 移动平台的更强。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160974911471126.jpeg\" alt=\"微信图片_20210104163115.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">值得注意的，是骁龙 480 移动平台还采用了 Spectra 345 ISP，其中单个摄像头最高可以支持到 6400 万像素拍照。然而重点是这可是三 ISP，这就意味着搭载该处理器的机型可以同时使用三个摄像头进行拍照或者是视频录制，并且获得更顺畅的变焦效果。此外骁龙 480 移动平台还支持 120Hz、FHS+ 显示，以及支持图形驱动更新特性，还有 aptX、Quick Charge 4+ 等高通传统艺能加持。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160974702519694.jpeg\" alt=\"0015.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img160974702481202.jpeg\" alt=\"0018.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">最关键的，当然是网络连接能力了啦。骁龙 480 移动平台是该系列首款具备 5G 网络连接能力的处理器。其采用的骁龙 X51 5G 调制解调器以及射频系统可以支持毫米波和 Sub-6GHz 5G 连接，也支持独立（SA）与非独立（NSA）组网模式，还支持 TDD、FDD 以及 DSS（动态频谱共享） 等特性。</p><p class=\"p_style\">得益于 FastConnect 6200 移动连接子系统，骁龙 480 移动平台还具备蓝牙 5.1、2*2 W-Fi 以及部分 Wi-Fi 6 关键特性，这都让搭载该处理器的普惠型机型能够给予使用者足够圆满的使用体验。</p>', 'http://pic.zaeke.com/img160974972675344.png', 6285, 0, 0, NULL, '2021-01-03 23:38:00', '2021-03-26 11:44:30');
INSERT INTO `article` VALUES (15875, 'OPPO Reno5 Pro+评测：在星河之梦里迎来2021', 'OPPOReno5Pro+这款产品本身对于Reno这个名字其实有点OOC，可也正是因为这种“OOC”让它成了台实打实的好手机。它出乎意料地将一台线下机的特质和线...', '[object Object]', 'victory3332', 31, 'http://pic.zaeke.com/user_1533281475_1.png', '<p>OPPO Reno5 Pro+这款产品本身对于Reno这个名字其实有点OOC，可也正是因为这种“OOC”让它成了台实打实的好手机。它出乎意料地将一台线下机的特质和线上机的配置融合到一起，于是在2020年年末就有了这台“最后的865”。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160992758320676.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>说实话，如果Reno5 Pro+这款手机换到今年上半年发布，那么它将成为绝杀，可惜换不得。于是在2020年年末这个有趣的时间点，在新一代的骁龙888移动平台机型们已经开始预热甚至上市的环境下，OPPO Reno5 Pro+甚至有种“在错误的时间做了正确的事情”之感觉。</p><p>或者……这会买OPPO Reno5 Pro+这样一台美丽、轻薄、拍照出色的手机，反而是个很理性的做法？</p><p><b>精致、闪耀、轻盈、纤薄……</b></p><p>这手机真漂亮。</p><p>这话可不是我说的，事实上每一个见到我掏出OPPO Reno5 Pro+的人都这么说。这台又轻又薄的手机似乎有某种魔力和吸引力，它的后背能够将每个人的注意力都牢牢抓住。哪怕是我这种用手机必戴保护壳的中年男人，也不太舍得给这个闪闪亮的后背套上一层雾霾，当然部分原因也是因为评测机不用花钱。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160992762613642.jpeg\" width=\"2000\" height=\"1127\"></p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160992762647190.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>OPPO是绝对懂得Reno系列的目标人群喜欢什么外观的手机品牌，可想当难得的是OPPO在Reno5 Pro+这款机器上将两拨看起来不太能调和到一起的审美成功调和起来。它闪闪亮的后背会让线下的少男少女们一见倾心，如刀切一般平整的机身顶部和底部让人想起当年大家都说好看的Lumia 800。机身顶上的夜光点缀也颇有意思，只要你让它在白天充分吸收能量，那么晚上被窝里它就会用幽雅的绿光作为你的报答。</p><p>Reno5 Pro+的用户界面也是让我有动力换机的原因之一，ColorOS 11在几个版本更新之后解决了不少之前存在的不稳定和高刷新率兼容性问题。因此现在这套可自定义空间很高，看着美观优雅且阅读体验很好的用户界面就变得更让人喜欢。我也可以在这上面获得美观易读应用抽屉，光是这一点比起其他一些国产UI就不知道高到哪里去了。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160992771042182.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>可Reno5 Pro+的漂亮与精致其实也并不是完全无代价，这台苗条的手机身上有一个不大不小的遗憾，那就是续航。</p><p>Reno5 Pro+的续航能力说实话只是个中游水平，以其配置而言并未达到我预期。它内置电池容量并不小，有4500毫安时，可在亮屏的一般使用过程中它的电量还是会以肉眼可见的速度减少。从100%电量开始关闭5G，连接Wi-Fi且室内自动屏幕亮度的情况下，搭配Enco W51看B站在线视频加上刷微博，一个半小时后电量就只剩86%。</p><p>是，我知道从严格的百分比意义上来看，这个耗电量乍看上去对于一台骁龙865的手机来说并不显得过分。可OPPO Reno5 Pro+的屏幕规格只有FHD/90Hz，而它在类似应用场景下的耗电量却和2K/120Hz屏幕的一加8 Pro类似。好在这个锅大概率可以甩到当时搭载的A.26固件上，后续升级A.27固件之后耗电量有明显改善，可运行A.27固件的Reno5 Pro+续航能力相比类似配置的机型依旧有些差距。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160998574665302.jpeg\" width=\"2000\" height=\"1125\"><br></p><p>因此在使用Reno5 Pro+的日子里，我变得比使用其他手机时更加关注电量的变化。在经过一圈不太严谨的对比之后，我发现和同配置（屏幕、移动芯片、电池容量）手机们比起来，Reno5 Pro+连接Wi-Fi网络时的耗电量相对更高，而切换至4G网络时Reno5 Pro+的耗电量反而相对更低一点。但值得表扬的是Reno5 Pro+的待机功耗控制倒是很好，后台常驻QQ和微信，一晚上待机耗电量也只在个位数。</p><p>所以Reno5 Pro+的续航整体来说还算OK，中等强度用可以坚持超过一天。可考虑到其屏幕规格比2020下半年的平均水平低半档，这个续航能力显然没有达到这个配置最优水平。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160998577881310.jpeg\" width=\"2000\" height=\"1125\"><br></p><p>假设你是个大部分时间在室内连接Wi-Fi网络，那么Reno5 Pro+的耗电量可能会高于你的预期。可如果你大部分时间在外或日常使用4G比例较高，那Reno5 Pro+的续航能力反而会显得挺不错。当然在绝对意义上Reno5 Pro+在Wi-Fi网络下的耗电量还是比4G网络更低，以上的表述的基础是和同配置机型横向对比。</p><p>啥？我为啥不试试5G？这怪不得我。毕竟在深圳这个神奇的城市你总能碰到5G网络信号满格但网速巨慢或死活获取不到数据的地方，一进到室内更是会有5G、4G反复横跳的情况……总之深圳的5G真的是个感知不强又徒增耗电的东西。</p><p>在性能表现这边Reno5 Pro+更没啥好担心的。被国产手机厂商们宣传了一整年的骁龙865性能表现绝不会让人失望，真正玩起游戏来当然是稳压天玑1000一头。确实接下来的2021年是骁龙888大刷存在感的时候，可骁龙865的性能还是可以稳坐在手机界T1阶级。3999元这个价位称不上顶级性价比，可也绝不是“高价低配”。</p><p><b>追光与追色</b></p><p>IMX766，是Reno5 Pro+身上的一个标签以及作为系列产品“超大杯”的区分手段。766这颗传感器的尺寸以及全像素全向对焦特性让它看起来像是Find X2 Pro上那颗689的“小底版”，可766本身又相比689多了个DOL-HDR的特性，号称可以大幅度减少HDR合成过程中每张照片的曝光间隔。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160998580577370.jpeg\" width=\"2000\" height=\"1127\"></p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160998581668332.jpeg\" width=\"2000\" height=\"1125\"><br></p><p>可对于Reno5 Pro+这个级别以及价位段的产品来说，IMX766这颗传感器的核心价值其实就两个——大底和全像素全向对焦。这两个高端特性的“下放”表明了OPPO提高这款手机拍照品质的决心。和这颗766主摄组队的还有一颗很常规的1600万像素超广角，以及参数看起来不亮眼但实际拍照过程中无比使用的1300万像素等效2x长焦。</p><p>没错我知道Reno5 Pro+还有颗200万像素的微距，可在ZAEKE知客的拍照价值观里这种规格的相机只能被划入“凑数”行列。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160998604455667.jpeg\" width=\"2000\" height=\"1500\"></p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160998603473970.jpeg\" width=\"2000\" height=\"1500\"></p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160998605831485.jpeg\" width=\"2000\" height=\"1500\"></p><p style=\"text-align: center;\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998606978165.jpeg\" width=\"522.1406628940987\" height=\"696\"></p><p style=\"text-align: center;\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998608122965.jpeg\" width=\"2000\" height=\"1500\"></p><p style=\"text-align: center;\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998609064336.jpeg\" width=\"2000\" height=\"1500\"></p><p style=\"text-align: center;\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998610197882.jpeg\" width=\"2000\" height=\"1500\"><br></p><p>Reno5 Pro+对拍照色彩的理解和Find X2 Pro是一个水平，因此其在行业里应当是没啥争议的T0，以通俗易懂的语言解释便是“好看”。OPPO有意地通过色彩的调整去让整个画面看起来很鲜活动人，可他们又很聪明地不会让色彩艳丽到溢出或不自然。哪怕是个追求还原肉眼观感的原教旨主义者，Reno5 Pro+也只需要关闭AI增强就能满足。</p><p>另外，Reno5 Pro+也是目前不多见的能够在各个焦段相机之间保持相对统一和稳定白平衡的手机之一。在超广角、主摄和长焦三颗相机均处于理想稳定情况下时，这三颗相机的白平衡风格、发色倾向均高度相似，三颗相机之间的数字变焦衔接也比较顺畅。只有在夜景和光线不佳的时候，超广角相机才会出现明显的色彩暗淡。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160998626783613.jpeg\" width=\"2000\" height=\"1500\"></p><p style=\"text-align: center;\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998625187508.jpeg\" width=\"522.1406628940987\" height=\"696\"></p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160998623487182.jpeg\" width=\"2000\" height=\"1500\"><br></p><p>所以用Reno5 Pro+拍照是件很惬意的事情。除了主摄表现稳定感光能力出色，那颗独立的等效2x长焦相机更是扩展了拍摄街景和静物的可能性。这颗2x相机虽然规格上并不显得出彩，可OPPO良好的调校让它拥有和主摄同样出色的色彩表现。甚至这颗规格参数不咋地的2x长焦在夜晚的表现还挺不错，大大超出我预期。</p><p>可色彩归色彩，我个人的审美并不是那么认同OPPO Reno5 Pro+的夜景风格。因为拍摄夜景的时候Reno5 Pro+的相机突然就从“为了拍好照片而努力”变成某种“秀肌肉”式的跑分风格。丢到DxOMark上去肯定能拿高分，可在我这儿就逃不了批评。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160998637811928.jpeg\" width=\"2000\" height=\"1500\"></p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160998634613241.jpeg\" width=\"2000\" height=\"1500\"></p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160998636216641.jpeg\" width=\"2000\" height=\"1500\"></p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160998639277611.jpeg\" width=\"2000\" height=\"1500\"><br></p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160998640739448.jpeg\" width=\"2000\" height=\"1500\"><br></p><p>OPPO明显懂得照片里什么样的色彩好看，可到了夜景照片这一方面他们的审美却突然滑坡。OPPO明显追求着所谓的“压高光”和“提暗部”的DxOMark式政治正确，因此在夜景场景中Reno5 Pro+拍出来的照片会因为过分地压低高光和过分提亮暗部，让整个照片失去应有的层次感和真实感。甚至在开启夜景模式之后，这种调校风格还会让整个画面有种笼罩在雾里的感觉。确实和Reno5 Pro比起来Pro+的这个倾向相对收敛了点，可总的来说还是不完全符合我的审美。</p><p>你喜欢这种风格？没事我不拦着。</p><p>可很神奇的是，即便是在这种风格下面，Reno5 Pro+的调色审美依旧在线。即便是在整个画面已经有点缺乏层次感之后，Reno5 Pro+还是可以靠很出色的调色将画面的观感维持在整体良好之水平。这个时候Reno5 Pro+拍出来的照片看起来还是算“比较好看”，至少和其他恪行此种风格的手机品牌比起来，Reno5 Pro+的照片更好看。</p><p>嗨，真是白瞎了这么好的夜景调色。</p><p style=\"text-align: center;\"><img alt=\"Record_2021-01-05-14-54-19_1.gif\" src=\"http://pic.zaeke.com/img160998645150051.gif\" width=\"313.88235294117646\" height=\"696\"><br></p><p>Reno5 Pro+相机另外一个不足则在于近距离对焦。Reno5 Pro+的主摄在较近距离拍摄，且不手动点选对焦点的时候会有一定概率无法正确识别应当对焦的物体，或是对焦拉风箱的情况。</p><p>虽然只要手法得当并且了解手机特点就在很多机器上“骗”出来类似的现象，可Reno5 Pro+显然比我接触的其他一些机器更容易“上钩”。所以希望OPPO除了前面提到的续航优化，也在这方面花点心思整整。</p><p><b>另一种方式的“水桶”机</b></p><p>其实OPPO Reno5 Pro+发布的时间点很奇妙。它是2020年最后一款865手机，在它正式发布和上市之后，骁龙888平台的手机们也已经开始登台。要价3999元起的Reno5 Pro+本身的产品力没有啥问题，可架不住友商的骁龙888手机也卖这个价钱，更别提友商还有块贼牛逼的屏幕。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160998652693834.jpeg\" width=\"2000\" height=\"2000\"><br></p><p>我喜欢Reno5 Pro+——我喜欢这台手机轻薄美丽的机身；我喜欢这台手机优雅的用户界面；我喜欢这台手机的拍照色彩。甚至这台定位于线下市场的手机还拥有一对效果出色的立体声喇叭，有一颗调校得不错的线性马达，那个不起眼的USB Type-C接口甚至还是3.1规格，支持视频输出。这样一台机器放到2020年上半年那就是妥妥的旗舰，能逼死一堆打着旗舰口号然后后背连颗独立长焦都没有的手机们。</p><p>它其实挺“水桶”，只不过不是2021年你期望的那种将各种配置都拉满的“水桶”。</p><p>Reno5 Pro+来晚了？也许吧。反正Reno这个名字不会被安到将来OPPO品牌的真旗舰身上，Reno5 Pro+只是线下热销Reno5系列一个进一步细分的延伸。它填上了OPPO品牌在4000~4500价位段的空缺，它本身的产品力也确实高于Reno5 Pro，这样对OPPO来说已经足够，更不要提Reno机型都是半年一更新。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img160998655742157.jpeg\" width=\"2000\" height=\"1125\"><br></p><p>“但是我既想要漂亮外观，又舍不得好拍照，但我这会也不想买个骁龙865，并且FHD屏幕属实不够2021，咋办？”</p><p>害能咋办，这不还会有OPPO Find X3 Pro嘛……</p>', '<p class=\"p_style\">OPPO Reno5 Pro+这款产品本身对于Reno这个名字其实有点OOC，可也正是因为这种“OOC”让它成了台实打实的好手机。它出乎意料地将一台线下机的特质和线上机的配置融合到一起，于是在2020年年末就有了这台“最后的865”。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160992758320676.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">说实话，如果Reno5 Pro+这款手机换到今年上半年发布，那么它将成为绝杀，可惜换不得。于是在2020年年末这个有趣的时间点，在新一代的骁龙888移动平台机型们已经开始预热甚至上市的环境下，OPPO Reno5 Pro+甚至有种“在错误的时间做了正确的事情”之感觉。</p><p class=\"p_style\">或者……这会买OPPO Reno5 Pro+这样一台美丽、轻薄、拍照出色的手机，反而是个很理性的做法？</p><p class=\"p_style\"><b>精致、闪耀、轻盈、纤薄……</b></p><p class=\"p_style\">这手机真漂亮。</p><p class=\"p_style\">这话可不是我说的，事实上每一个见到我掏出OPPO Reno5 Pro+的人都这么说。这台又轻又薄的手机似乎有某种魔力和吸引力，它的后背能够将每个人的注意力都牢牢抓住。哪怕是我这种用手机必戴保护壳的中年男人，也不太舍得给这个闪闪亮的后背套上一层雾霾，当然部分原因也是因为评测机不用花钱。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160992762613642.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160992762647190.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">OPPO是绝对懂得Reno系列的目标人群喜欢什么外观的手机品牌，可想当难得的是OPPO在Reno5 Pro+这款机器上将两拨看起来不太能调和到一起的审美成功调和起来。它闪闪亮的后背会让线下的少男少女们一见倾心，如刀切一般平整的机身顶部和底部让人想起当年大家都说好看的Lumia 800。机身顶上的夜光点缀也颇有意思，只要你让它在白天充分吸收能量，那么晚上被窝里它就会用幽雅的绿光作为你的报答。</p><p class=\"p_style\">Reno5 Pro+的用户界面也是让我有动力换机的原因之一，ColorOS 11在几个版本更新之后解决了不少之前存在的不稳定和高刷新率兼容性问题。因此现在这套可自定义空间很高，看着美观优雅且阅读体验很好的用户界面就变得更让人喜欢。我也可以在这上面获得美观易读应用抽屉，光是这一点比起其他一些国产UI就不知道高到哪里去了。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160992771042182.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">可Reno5 Pro+的漂亮与精致其实也并不是完全无代价，这台苗条的手机身上有一个不大不小的遗憾，那就是续航。</p><p class=\"p_style\">Reno5 Pro+的续航能力说实话只是个中游水平，以其配置而言并未达到我预期。它内置电池容量并不小，有4500毫安时，可在亮屏的一般使用过程中它的电量还是会以肉眼可见的速度减少。从100%电量开始关闭5G，连接Wi-Fi且室内自动屏幕亮度的情况下，搭配Enco W51看B站在线视频加上刷微博，一个半小时后电量就只剩86%。</p><p class=\"p_style\">是，我知道从严格的百分比意义上来看，这个耗电量乍看上去对于一台骁龙865的手机来说并不显得过分。可OPPO Reno5 Pro+的屏幕规格只有FHD/90Hz，而它在类似应用场景下的耗电量却和2K/120Hz屏幕的一加8 Pro类似。好在这个锅大概率可以甩到当时搭载的A.26固件上，后续升级A.27固件之后耗电量有明显改善，可运行A.27固件的Reno5 Pro+续航能力相比类似配置的机型依旧有些差距。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998574665302.jpeg\" width=\"2000\" height=\"1125\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">因此在使用Reno5 Pro+的日子里，我变得比使用其他手机时更加关注电量的变化。在经过一圈不太严谨的对比之后，我发现和同配置（屏幕、移动芯片、电池容量）手机们比起来，Reno5 Pro+连接Wi-Fi网络时的耗电量相对更高，而切换至4G网络时Reno5 Pro+的耗电量反而相对更低一点。但值得表扬的是Reno5 Pro+的待机功耗控制倒是很好，后台常驻QQ和微信，一晚上待机耗电量也只在个位数。</p><p class=\"p_style\">所以Reno5 Pro+的续航整体来说还算OK，中等强度用可以坚持超过一天。可考虑到其屏幕规格比2020下半年的平均水平低半档，这个续航能力显然没有达到这个配置最优水平。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998577881310.jpeg\" width=\"2000\" height=\"1125\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">假设你是个大部分时间在室内连接Wi-Fi网络，那么Reno5 Pro+的耗电量可能会高于你的预期。可如果你大部分时间在外或日常使用4G比例较高，那Reno5 Pro+的续航能力反而会显得挺不错。当然在绝对意义上Reno5 Pro+在Wi-Fi网络下的耗电量还是比4G网络更低，以上的表述的基础是和同配置机型横向对比。</p><p class=\"p_style\">啥？我为啥不试试5G？这怪不得我。毕竟在深圳这个神奇的城市你总能碰到5G网络信号满格但网速巨慢或死活获取不到数据的地方，一进到室内更是会有5G、4G反复横跳的情况……总之深圳的5G真的是个感知不强又徒增耗电的东西。</p><p class=\"p_style\">在性能表现这边Reno5 Pro+更没啥好担心的。被国产手机厂商们宣传了一整年的骁龙865性能表现绝不会让人失望，真正玩起游戏来当然是稳压天玑1000一头。确实接下来的2021年是骁龙888大刷存在感的时候，可骁龙865的性能还是可以稳坐在手机界T1阶级。3999元这个价位称不上顶级性价比，可也绝不是“高价低配”。</p><p class=\"p_style\"><b>追光与追色</b></p><p class=\"p_style\">IMX766，是Reno5 Pro+身上的一个标签以及作为系列产品“超大杯”的区分手段。766这颗传感器的尺寸以及全像素全向对焦特性让它看起来像是Find X2 Pro上那颗689的“小底版”，可766本身又相比689多了个DOL-HDR的特性，号称可以大幅度减少HDR合成过程中每张照片的曝光间隔。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998580577370.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998581668332.jpeg\" width=\"2000\" height=\"1125\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">可对于Reno5 Pro+这个级别以及价位段的产品来说，IMX766这颗传感器的核心价值其实就两个——大底和全像素全向对焦。这两个高端特性的“下放”表明了OPPO提高这款手机拍照品质的决心。和这颗766主摄组队的还有一颗很常规的1600万像素超广角，以及参数看起来不亮眼但实际拍照过程中无比使用的1300万像素等效2x长焦。</p><p class=\"p_style\">没错我知道Reno5 Pro+还有颗200万像素的微距，可在ZAEKE知客的拍照价值观里这种规格的相机只能被划入“凑数”行列。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998604455667.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998603473970.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998605831485.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998606978165.jpeg\" width=\"522.1406628940987\" height=\"696\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998608122965.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998609064336.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998610197882.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">Reno5 Pro+对拍照色彩的理解和Find X2 Pro是一个水平，因此其在行业里应当是没啥争议的T0，以通俗易懂的语言解释便是“好看”。OPPO有意地通过色彩的调整去让整个画面看起来很鲜活动人，可他们又很聪明地不会让色彩艳丽到溢出或不自然。哪怕是个追求还原肉眼观感的原教旨主义者，Reno5 Pro+也只需要关闭AI增强就能满足。</p><p class=\"p_style\">另外，Reno5 Pro+也是目前不多见的能够在各个焦段相机之间保持相对统一和稳定白平衡的手机之一。在超广角、主摄和长焦三颗相机均处于理想稳定情况下时，这三颗相机的白平衡风格、发色倾向均高度相似，三颗相机之间的数字变焦衔接也比较顺畅。只有在夜景和光线不佳的时候，超广角相机才会出现明显的色彩暗淡。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998626783613.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998625187508.jpeg\" width=\"522.1406628940987\" height=\"696\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998623487182.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">所以用Reno5 Pro+拍照是件很惬意的事情。除了主摄表现稳定感光能力出色，那颗独立的等效2x长焦相机更是扩展了拍摄街景和静物的可能性。这颗2x相机虽然规格上并不显得出彩，可OPPO良好的调校让它拥有和主摄同样出色的色彩表现。甚至这颗规格参数不咋地的2x长焦在夜晚的表现还挺不错，大大超出我预期。</p><p class=\"p_style\">可色彩归色彩，我个人的审美并不是那么认同OPPO Reno5 Pro+的夜景风格。因为拍摄夜景的时候Reno5 Pro+的相机突然就从“为了拍好照片而努力”变成某种“秀肌肉”式的跑分风格。丢到DxOMark上去肯定能拿高分，可在我这儿就逃不了批评。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998637811928.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998634613241.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998636216641.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998639277611.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998640739448.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">OPPO明显懂得照片里什么样的色彩好看，可到了夜景照片这一方面他们的审美却突然滑坡。OPPO明显追求着所谓的“压高光”和“提暗部”的DxOMark式政治正确，因此在夜景场景中Reno5 Pro+拍出来的照片会因为过分地压低高光和过分提亮暗部，让整个照片失去应有的层次感和真实感。甚至在开启夜景模式之后，这种调校风格还会让整个画面有种笼罩在雾里的感觉。确实和Reno5 Pro比起来Pro+的这个倾向相对收敛了点，可总的来说还是不完全符合我的审美。</p><p class=\"p_style\">你喜欢这种风格？没事我不拦着。</p><p class=\"p_style\">可很神奇的是，即便是在这种风格下面，Reno5 Pro+的调色审美依旧在线。即便是在整个画面已经有点缺乏层次感之后，Reno5 Pro+还是可以靠很出色的调色将画面的观感维持在整体良好之水平。这个时候Reno5 Pro+拍出来的照片看起来还是算“比较好看”，至少和其他恪行此种风格的手机品牌比起来，Reno5 Pro+的照片更好看。</p><p class=\"p_style\">嗨，真是白瞎了这么好的夜景调色。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"Record_2021-01-05-14-54-19_1.gif\" src=\"http://pic.zaeke.com/img160998645150051.gif\" width=\"313.88235294117646\" height=\"696\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">Reno5 Pro+相机另外一个不足则在于近距离对焦。Reno5 Pro+的主摄在较近距离拍摄，且不手动点选对焦点的时候会有一定概率无法正确识别应当对焦的物体，或是对焦拉风箱的情况。</p><p class=\"p_style\">虽然只要手法得当并且了解手机特点就在很多机器上“骗”出来类似的现象，可Reno5 Pro+显然比我接触的其他一些机器更容易“上钩”。所以希望OPPO除了前面提到的续航优化，也在这方面花点心思整整。</p><p class=\"p_style\"><b>另一种方式的“水桶”机</b></p><p class=\"p_style\">其实OPPO Reno5 Pro+发布的时间点很奇妙。它是2020年最后一款865手机，在它正式发布和上市之后，骁龙888平台的手机们也已经开始登台。要价3999元起的Reno5 Pro+本身的产品力没有啥问题，可架不住友商的骁龙888手机也卖这个价钱，更别提友商还有块贼牛逼的屏幕。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998652693834.jpeg\" width=\"2000\" height=\"2000\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">我喜欢Reno5 Pro+——我喜欢这台手机轻薄美丽的机身；我喜欢这台手机优雅的用户界面；我喜欢这台手机的拍照色彩。甚至这台定位于线下市场的手机还拥有一对效果出色的立体声喇叭，有一颗调校得不错的线性马达，那个不起眼的USB Type-C接口甚至还是3.1规格，支持视频输出。这样一台机器放到2020年上半年那就是妥妥的旗舰，能逼死一堆打着旗舰口号然后后背连颗独立长焦都没有的手机们。</p><p class=\"p_style\">它其实挺“水桶”，只不过不是2021年你期望的那种将各种配置都拉满的“水桶”。</p><p class=\"p_style\">Reno5 Pro+来晚了？也许吧。反正Reno这个名字不会被安到将来OPPO品牌的真旗舰身上，Reno5 Pro+只是线下热销Reno5系列一个进一步细分的延伸。它填上了OPPO品牌在4000~4500价位段的空缺，它本身的产品力也确实高于Reno5 Pro，这样对OPPO来说已经足够，更不要提Reno机型都是半年一更新。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img160998655742157.jpeg\" width=\"2000\" height=\"1125\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">“但是我既想要漂亮外观，又舍不得好拍照，但我这会也不想买个骁龙865，并且FHD屏幕属实不够2021，咋办？”</p><p class=\"p_style\">害能咋办，这不还会有OPPO Find X3 Pro嘛……</p>', 'http://pic.zaeke.com/img160992919212338.jpeg', 13043, 0, 0, NULL, '2021-01-06 02:33:54', '2021-03-27 05:21:29');
INSERT INTO `article` VALUES (15898, 'iQOO7图赏：回归性能与速度的主旋律', '尽管是个蛮新的品牌，不过iQOO给我们留下的印象却非常清晰：这是一个追求极致性能与极致速度的品牌，同时也以超高的性价比征服着每个人的心。而如今全新的iQOO旗舰...', '[object Object]', '烫头孙师傅', 38, 'http://pic.zaeke.com/user_1533281477_5.png', '<p>尽管是个蛮新的品牌，不过iQOO给我们留下的印象却非常清晰：这是一个追求极致性能与极致速度的品牌，同时也以超高的性价比征服着每个人的心。</p><p>而如今全新的iQOO旗舰手机又来了，这也就是令人期待的iQOO 7，这款机型依然将以性能和速度为主打，只不过在定位上又稍稍往“游戏”方向偏重一些。</p><p><img alt=\"Capture09919.jpg\" src=\"http://pic.zaeke.com/img161035228748426.jpeg\" width=\"1400\" height=\"933\"><br></p><p><img alt=\"Capture10009.jpg\" src=\"http://pic.zaeke.com/img161035228764198.jpeg\" width=\"1400\" height=\"933\"><br></p><p>iQOO 7采用6.62英寸、2400×1080分辨率的AMOLED极点全面屏，这块屏幕拥有91.4%的屏占比与6000000:1高屏幕对比度，此外还500nit的屏幕典型亮度、800nit的峰值亮度与1300nit的局部最高亮度。</p><p>作为旗舰机，iQOO 7当然搭载了高通骁龙888移动平台，这也是第二款正式发布的骁龙888手机。此外，关乎性能的LPDDR5 RAM与UFS3.1 ROM也没有缺席，并提供8GB RAM+128GB ROM、12GB RAM+128GB ROM与12GB RAM+256GB ROM三个版本可选。</p><p><img alt=\"Capture09651.jpg\" src=\"http://pic.zaeke.com/img161035127818217.jpeg\" width=\"1400\" height=\"933\"><br></p><p><img alt=\"Capture10038.jpg\" src=\"http://pic.zaeke.com/img161035060395966.jpeg\" width=\"1400\" height=\"933\"><br></p><p>而为了尽可能保证骁龙888的性能输出，iQOO 7还配备了所谓的4K级动力泵液冷散热，其中拥有号称业界最大的4096mm²超大均热板，并且采用100%纯铜材质和99.999%纯水。内部则拥有毛细结构和Fiber动力泵，以增加液体循环动力。</p><p>根据官方的测试，iQOO 7的均热板相比常规均热板产品能够将机身表面温度降低1.5℃，CPU温度降低2℃。而如果包含VC均热板、散热膜、碳纤维与导热凝胶在内，该机的散热总面积为17733mm²，是有史以来散热面积最大的iQOO手机。</p><p><img alt=\"Capture09548.jpg\" src=\"http://pic.zaeke.com/img161035051164632.jpeg\" width=\"1400\" height=\"933\"><br></p><p>还记得iQOO 5 Pro上惊艳的120W功率FlashCharge闪充技术吗？iQOO 7依然拥有这个产品力极强的功能。该机通过定制的高电转化率电荷泵芯片、6C高倍率电芯与创新的MTW阵列式极耳结构的搭配，实现了安全且高功率的快充表现。</p><p>并且iQOO 7的120输出功率也支持宽电压输入，其原装充充电器也能够兼容65W PD协议，可为笔记本电脑充电。<br></p><p><img alt=\"Capture09651.jpg\" src=\"http://pic.zaeke.com/img161035051163745.jpeg\" width=\"1400\" height=\"933\"><br></p><p><img alt=\"Capture09786.jpg\" src=\"http://pic.zaeke.com/img161035410025154.jpeg\" width=\"1400\" height=\"933\"><br></p><p>还记得前面说到iQOO 7这次更加偏向游戏嘛，原因嘛是全新的Monster touch屏下双控压感功能。iQOO 7的屏幕支持左右分区分别按压对应两个压力操作，将肩键的功能直接集成到了屏幕上。并且最重要的是可以一次按压对应两个操作，或是按住以保持长时间开镜等，对于操作相对复杂的游戏来说可以说是个福音。</p><p>此外，iQOO 7这次也拥有Monster Engine双路线性马达能够带来更好的反馈震感，并且能够为相应的游戏场景音频增加4D震感效果。而Monster Beat立体双扬声器则能够带来更高品质的音质，双扬声器相较于单扬声器在音质表现上的优势非常明显，对于有更高外放需求的用户来说也是一件非常重要的事情。</p><p><img alt=\"Capture09582.jpg\" src=\"http://pic.zaeke.com/img161035051156232.jpeg\" width=\"1400\" height=\"933\"><br></p><p><img alt=\"Capture09742.jpg\" src=\"http://pic.zaeke.com/img161035052829181.jpeg\" width=\"1400\" height=\"933\"><br></p><p>而在影像上，iQOO 7则支持4800万像素主摄+1300万像素超广角相机+1300万像素2X人像相机的后置三摄。其中主摄采用定制的索尼IMX598感光元件，支持OIS光学防抖。并且其还支持超清像素位移功能与RAW HDR，此外还拥有超级夜景5.0。</p><p>在视频方面，iQOO 7还支持则支持视频专业模式，内置Vlog短视频模板等功能。</p><p><img alt=\"Capture09802.jpg\" src=\"http://pic.zaeke.com/img161035054668009.jpeg\" width=\"1400\" height=\"933\"><br></p><p>作为iQOO系列2021年的首款旗舰级机型，iQOO 7在看起来似乎在影像上稍退一步，不过却在体验上保持了前代的优势，与此同时则在定位上更偏向游戏了些。考虑到性价比机型用户往往也很在乎游戏体验，这样的取舍也能够理解。</p><p>而更重要的一点是：iQOO 7是骁龙888机型上相对较早发布上市的机型，也就是说如果你希望获得最新的高通骁龙旗舰机移动平台，目前市面上的选择其实并不多，这也是iQOO 7竞争力的来源之一啦。</p>', '<p class=\"p_style\">尽管是个蛮新的品牌，不过iQOO给我们留下的印象却非常清晰：这是一个追求极致性能与极致速度的品牌，同时也以超高的性价比征服着每个人的心。</p><p class=\"p_style\">而如今全新的iQOO旗舰手机又来了，这也就是令人期待的iQOO 7，这款机型依然将以性能和速度为主打，只不过在定位上又稍稍往“游戏”方向偏重一些。</p><p class=\"jsmargin\"><img alt=\"Capture09919.jpg\" src=\"http://pic.zaeke.com/img161035228748426.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"Capture10009.jpg\" src=\"http://pic.zaeke.com/img161035228764198.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">iQOO 7采用6.62英寸、2400×1080分辨率的AMOLED极点全面屏，这块屏幕拥有91.4%的屏占比与6000000:1高屏幕对比度，此外还500nit的屏幕典型亮度、800nit的峰值亮度与1300nit的局部最高亮度。</p><p class=\"p_style\">作为旗舰机，iQOO 7当然搭载了高通骁龙888移动平台，这也是第二款正式发布的骁龙888手机。此外，关乎性能的LPDDR5 RAM与UFS3.1 ROM也没有缺席，并提供8GB RAM+128GB ROM、12GB RAM+128GB ROM与12GB RAM+256GB ROM三个版本可选。</p><p class=\"jsmargin\"><img alt=\"Capture09651.jpg\" src=\"http://pic.zaeke.com/img161035127818217.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"Capture10038.jpg\" src=\"http://pic.zaeke.com/img161035060395966.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">而为了尽可能保证骁龙888的性能输出，iQOO 7还配备了所谓的4K级动力泵液冷散热，其中拥有号称业界最大的4096mm²超大均热板，并且采用100%纯铜材质和99.999%纯水。内部则拥有毛细结构和Fiber动力泵，以增加液体循环动力。</p><p class=\"p_style\">根据官方的测试，iQOO 7的均热板相比常规均热板产品能够将机身表面温度降低1.5℃，CPU温度降低2℃。而如果包含VC均热板、散热膜、碳纤维与导热凝胶在内，该机的散热总面积为17733mm²，是有史以来散热面积最大的iQOO手机。</p><p class=\"jsmargin\"><img alt=\"Capture09548.jpg\" src=\"http://pic.zaeke.com/img161035051164632.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">还记得iQOO 5 Pro上惊艳的120W功率FlashCharge闪充技术吗？iQOO 7依然拥有这个产品力极强的功能。该机通过定制的高电转化率电荷泵芯片、6C高倍率电芯与创新的MTW阵列式极耳结构的搭配，实现了安全且高功率的快充表现。</p><p class=\"p_style\">并且iQOO 7的120输出功率也支持宽电压输入，其原装充充电器也能够兼容65W PD协议，可为笔记本电脑充电。<br></p><p class=\"jsmargin\"><img alt=\"Capture09651.jpg\" src=\"http://pic.zaeke.com/img161035051163745.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"Capture09786.jpg\" src=\"http://pic.zaeke.com/img161035410025154.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">还记得前面说到iQOO 7这次更加偏向游戏嘛，原因嘛是全新的Monster touch屏下双控压感功能。iQOO 7的屏幕支持左右分区分别按压对应两个压力操作，将肩键的功能直接集成到了屏幕上。并且最重要的是可以一次按压对应两个操作，或是按住以保持长时间开镜等，对于操作相对复杂的游戏来说可以说是个福音。</p><p class=\"p_style\">此外，iQOO 7这次也拥有Monster Engine双路线性马达能够带来更好的反馈震感，并且能够为相应的游戏场景音频增加4D震感效果。而Monster Beat立体双扬声器则能够带来更高品质的音质，双扬声器相较于单扬声器在音质表现上的优势非常明显，对于有更高外放需求的用户来说也是一件非常重要的事情。</p><p class=\"jsmargin\"><img alt=\"Capture09582.jpg\" src=\"http://pic.zaeke.com/img161035051156232.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"Capture09742.jpg\" src=\"http://pic.zaeke.com/img161035052829181.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">而在影像上，iQOO 7则支持4800万像素主摄+1300万像素超广角相机+1300万像素2X人像相机的后置三摄。其中主摄采用定制的索尼IMX598感光元件，支持OIS光学防抖。并且其还支持超清像素位移功能与RAW HDR，此外还拥有超级夜景5.0。</p><p class=\"p_style\">在视频方面，iQOO 7还支持则支持视频专业模式，内置Vlog短视频模板等功能。</p><p class=\"jsmargin\"><img alt=\"Capture09802.jpg\" src=\"http://pic.zaeke.com/img161035054668009.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">作为iQOO系列2021年的首款旗舰级机型，iQOO 7在看起来似乎在影像上稍退一步，不过却在体验上保持了前代的优势，与此同时则在定位上更偏向游戏了些。考虑到性价比机型用户往往也很在乎游戏体验，这样的取舍也能够理解。</p><p class=\"p_style\">而更重要的一点是：iQOO 7是骁龙888机型上相对较早发布上市的机型，也就是说如果你希望获得最新的高通骁龙旗舰机移动平台，目前市面上的选择其实并不多，这也是iQOO 7竞争力的来源之一啦。</p>', 'http://pic.zaeke.com/img161035433913519.jpeg', 13032, 0, 0, NULL, '2021-01-11 00:47:18', '2021-03-27 07:03:55');
INSERT INTO `article` VALUES (15913, '小米 11 是旗舰的一次新突破，更是骁龙 888 高端阵营中的“守门员”', '数字系列一直都是小米的年度重头戏，而再次成功获得了高通骁龙8系列首发权的小米，更是赶在2021年来临之际“抢先”发布了小米11。作为首款面世的骁龙888平台机型...', '[object Object]', '小啤', 170, 'http://pic.zaeke.com/user_1533281478_8.png', '<p>数字系列一直都是小米的年度重头戏，而再次成功获得了高通骁龙 8 系列首发权的小米，更是赶在 2021 年来临之际“抢先”发布了小米 11。作为首款面世的骁龙 888 平台机型，小米 11 的首销实现了 5 分钟销售额破 15 亿的骄人成绩，显然小米在这场提前吹响战斗号角的“战斗”上，已经取得了一次不俗的开门红。</p><p style=\"text-align: center;\"><b>高通骁龙 888 移动平台，重新定义顶级移动体验</b></p><p>尽管小米 11 为首销已经做了一定的备货，可显然还无法满足庞大的市场需求。当然，相比较于渴望抢先吃上第一波吃“螃蟹”的米粉，其实仍然有不少消费者对于这款未经过“市场检验”的新机型持观望态度，毕竟它是首款搭载高通骁龙 888 移动平台的机型呢，在使用体验上仍存在很多未知因素。</p><p><img alt=\"IMG_0309.jpg\" src=\"http://pic.zaeke.com/img161045733889939.jpeg\" width=\"1400\" height=\"933\"><br></p><p>实际上，高通骁龙 888 移动平台的账面数据是非常诱人的，首先 CPU 部分采用了 1 超 3 大 4 小的核心组合，1 颗基于 Cortex-X1 深度定制的 Kryo680 超级大核，主频高达 2.84GHz，3 颗基于 Cortex-A78 深度定制的 Kryo680 大核，主频 2.42GHz，以及 4 颗基于 Cortex-A55 的 Kryo680 小核；GPU 方面，其配备了全新的 Adreno660 图形处理器，据说该 GPU 相比较于上代骁龙 865 所采用的 Adreno650 GPU ，前者有着 35% 的性能提升，可是功耗却降低 20%。</p><p>在如此强劲的性能支撑之下，小米 11 即便搭载了一块 2K 分辨率 120Hz 高刷屏幕，其应付基本的系统流畅性显然已经不在话下。为了尽可能发挥出高通骁龙 888 移动平台的极限能力，只能通过目前一些相对主流的顶尖大型手游对其进行测试。</p><p><img src=\"http://pic.zaeke.com/img161045606784700.jpeg\" alt=\"使命召唤手游.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161045606784621.jpeg\" alt=\"和平精英.jpg\"></p><p>在使用小米 11 上手《和平精英》、《使命召唤手游》、《原神》三款大型游戏时，通过使用 PerfDog 的监控数据可以发现，在室内温度 18℃ 的环境下，小米 11 在《和平精英》的 HDR高清画质、极限帧数设置之下，其帧数可以达到几乎满帧的 59.6 帧，基本感受不到游戏过程中的画面卡顿情况；《使命召唤手游》在精致画质、极限帧数设置之下，小米 11 可实现全程 60 帧的满帧运行，整个游戏过程非常顺畅。</p><p><img src=\"http://pic.zaeke.com/img161045605832658.jpeg\" alt=\"原神.jpg\"></p><p>除此，面对《原神》这款对性能要求更高的开放世界冒险游戏，我也尝试把它的画面调至极限画质、帧数设置为 60 帧，经过 40 分钟以上的上手体验测试，小米 11 获得了 54.8 帧的平均帧数表现，游戏过程中能感受到帧数有所波动，可是整体不会有明显卡顿感。</p><p><img alt=\"IMG_0312.jpg\" src=\"http://pic.zaeke.com/img161045731912455.jpeg\" width=\"1400\" height=\"933\"></p><p><img alt=\"IMG_0277.jpg\" src=\"http://pic.zaeke.com/img161045799297060.jpeg\" width=\"1400\" height=\"933\"><br></p><p>纵使骁龙 888 平台采用了先进的 5nm 工艺制程，可是小米为了能让该 SoC 可以持续地带来稳定的高负载运算能力，小米 11 采用了 VC 液冷立体散热系统、多点温度监控等控温措施，确保了机器能带来足够强大的散热效率，我从小米 11 在多款大型游戏中的持续稳定发挥，已经可以得到一份满意的答卷。</p><p>显而易见，骁龙 888 移动平台延续了骁龙移动平台在游戏方面的优异表现。凭借性能强悍的 Adreno660 GPU 和第三代 Snapdragon Elite Gaming 加持，使骁龙 888 移动平台拥有足够强劲的算法和优化表现，另外除了延续了GPU驱动更新、端游级正向渲染、高达 144 帧率游戏画面以外，骁龙 888 移动平台还新增了可变分辨率渲染技术、Game Quick Touch 技术，其中可变分辨率渲染技术在提高游戏运行的性能基础下实现更低的功耗表现，而 Game Quick Touch 可针对游戏中的帧同步问题进行了毫秒级优化，有效降低玩家的触控延迟，通俗理解就是玩家在游戏过程中的触控体验更“跟手”了。</p><p><img alt=\"IMG_0123.jpg\" src=\"http://pic.zaeke.com/img161045736673852.jpeg\" width=\"1400\" height=\"933\"><br></p><p>在我看来，拥有高通骁龙 888 移动平台加持的小米 11，可以充分地发挥出 6.81 英寸 2K 屏幕的优势，特别在部分游戏中，2K 分辨率屏幕可带来更加细腻的游戏画面，这是全高清屏幕机型所难以企及的地方，而高通骁龙 888 移动平台的高性能特性可支撑小米 11 轻松应对市面上绝大部分中大型手机游戏。</p><p><img alt=\"IMG_0324.jpg\" src=\"http://pic.zaeke.com/img161045739096183.jpeg\" width=\"1400\" height=\"933\"></p><p><img alt=\"IMG_0329.jpg\" src=\"http://pic.zaeke.com/img161045739198886.jpeg\" width=\"1400\" height=\"933\"><br></p><p>除了屏幕输出所带来的视觉体验，听感体验也是手机娱乐中非常关键的一环。相比较于小米 10 的双 1216 超线性对称式扬声器组合，采用非对称听筒式双扬声器的小米 11 在外放表现方面还是稍逊一筹，当中差距主要在于小米 11 的细节、响度方面都稍有退步，而且该机在中大音量下，存在机身共振情况。其实市面上不少轻薄型手机都有扬声器外放共振情况，通常是因为机身后盖材料过于纤薄所导致。即便如此，小米 11 的外放效果仍然比往年的不少旗舰机型都要优秀，也许不是小米 11 的外放逊色，而是小米 10 的外放表现太好了。</p><p><img alt=\"1610453732(1).jpg\" src=\"http://pic.zaeke.com/img161045754595353.jpeg\" width=\"1124\" height=\"829\"><br></p><p>在存储组合搭配方面，这次小米 11 也依旧有着亮眼表现，去年被小米 10 率先搭载的 LPDDR5，这次在小米 11 身上得到了再一次升级，其 LPDDR5 内存主频由小米 10 的 2750MHz，升级为 3200MHz，数据传输速率也从 5500Mbps 提升至 6400Mbps。配合 UFS 3.1 高速闪存，意味着该机型的机身存储性能表现也有了足够充沛的性能保障。小米 11 在 AndroBench 存储跑分软件中，取得了顺序读取速度 1844.18MB/s、顺序写入速度 766.29MB / s、随机读取速度 259.88MB / s、随机写入速度 233.95MB / s 的成绩，综合跑分表现不俗。</p><p><img alt=\"IMG_0239.jpg\" src=\"http://pic.zaeke.com/img161045755632281.jpeg\" width=\"1300\" height=\"867\"><br></p><p>鉴于小米 11 采用了更轻薄的机身设计，因此在电池容量方面相比上一代产品有所下降，其搭载了 4600mAh 机身电池，搭配 55W 有线快充，可以实现 45 分钟从零电量充满至 100%，并且该机型还支持 50W 无线快充，零电量状态下充满至 100% 需要耗时 53 分钟。</p><p>与此同时，小米 11 保留了 10W 无线反向充电功能特性。为了保证基本的充电性能，小米 11 采用了全新低功耗充电管理芯片，既大幅度降低热损耗，也降低了充电发热，而且配合 Mi-FC 快充技术进一步提升充电速度。注：图片中使用的并非无线快充充电板，仅是展示作用。</p><p style=\"text-align: center;\"><b>缓解全球运营商 5G 规模难度，同时积极拥抱未来</b></p><p><img src=\"http://pic.zaeke.com/img161045595534164.jpeg\" alt=\"qualcomm-sdx60.jpg\"></p><p>上述体验都需要高度依赖网络连接能力，骁龙 888 移动平台为了确保网络稳定性和先进性，其集成了全球首款 5nm 制程的 5G 调制解调器 —— 高通骁龙 X60，考虑到高通骁龙 X60 调制解调器采用了最新一代 AP + Modem 的集成式解决方案，因此它可以在更低的功耗表现下，带来更小的体积和能效表现。</p><p><img src=\"http://pic.zaeke.com/img161045602296376.jpeg\" alt=\"csm_001215_16f0aea033.jpg\"></p><p>在如此先进的硬件基础之下，高通骁龙 X60 调制解调器实现了 5G&nbsp;Sub-6GHz 载波聚合和毫米波的支持，可实现最高 7.5Gbps 的下载速率和 3Gbps 的上传速率，这就意味着小米 11 的 5G SA 峰值速率达到翻倍提升。为了提高网络的使用效率，高通骁龙 X60 调制解调器还新增了对动态频谱共享技术（DSS）的支持，运营商可以针对拆分频谱的方式经济高效地部署 5G 服务，实现 4G 与 5G 频谱的共存，从而达到提高信号覆盖率的作用，缓解全球各大运营商对于 5G 网络部署难度过高的过渡性问题。</p><p style=\"text-align: center;\"><img src=\"http://pic.zaeke.com/img161045603163706.gif\" alt=\"VbuUFr.gif\"></p><p>不仅如此，高通骁龙 X60 调制解调器凭借基于 5G SA 独立组网的 VoNR 语音及视频业务，从 VoLTE 的基础下有效提升语音通话的音质和降低延迟低，而且有效解决通话时信号回落的问题，给予用户更高质量的沟通体验。也许距离 VoNR 通话的正式大面积商用仍然有一段漫长的时间，可是随着以高通为首的芯片厂商的对于该技术的积极拥抱，可以对该技术起到推进作用。</p><p>得益于高通骁龙 888 移动平台在 Wi-Fi 6、Wi-Fi 6E（6GHz 频段）、蓝牙 5.2 等连接能力支持，小米 11 也实现了第六代 Wi-Fi 802.11ax 标准的支持。按照小米官方的描述，小米 11 在配合 Wi-Fi 6 增强版路由器的情况下，可以带来比上一代机型翻一倍的频宽（从 80MHz 升级至 160MHz），同时编码也由 1024QAM 升级至 4096QAM，真正实现了更快、密度更高的 Wi-Fi 连接能力。当然，想要发挥出小米 11 极致的 Wi-Fi 连接体验，就意味着还需要重新为家里购置一款支持 Wi-Fi 6E 标准的路由器。</p><p><img alt=\"IMG_0328.jpg\" src=\"http://pic.zaeke.com/img161045765388984.jpeg\" width=\"1400\" height=\"933\"><br></p><p>有着旗舰定位的小米 11，其搭载的骁龙 888 移动平台通过上述众多先进通讯功能特性，不仅确保了现阶段的基本网络使用体验，它还具备衔接未来发展趋势的连接能力，为未来几年运营商开放相应新技术做好充分准备。</p><p style=\"text-align: center;\"><b>更高效的拍摄体验，助用户捕捉美好瞬间</b></p><p>以摄像头数量论好坏大趋势之下，小米 11 仍然选择在小米 10 的后置四摄基础之下采用了后置三摄方案，分别为 1 亿超清主摄 + 1300 万超广角 + 500 万长焦微距，其中小米 11 延续了上一代产品的 1 亿像素主摄，采用的三星 HMX 图像传感器尺寸为 1/1.33 英寸，搭配 7P 镜头设计和 OIS 光学防抖，弱光环境下可实现四合一 1.6μm 大像素夜拍能力。</p><p><img src=\"http://pic.zaeke.com/img161045609092428.png\" alt=\"img160747692548750.png\"></p><p>尽管主摄的配置与上代产品保持一致，可是小米 11 通过算法的升级优化，使它在弱光环境下带来更显纯净的画面表现。除此，骁龙 888 移动平台对于小米 11 的使用体验提升并不止步于娱乐和通讯，因为高通骁龙 888 移动平台凭借其最新的 Spectra 580 ISP，实现了比上一代产品 35% 的性能提升，每秒可以实现 27 亿次像素的运算性能。</p><p><img src=\"http://pic.zaeke.com/img161045644759806.jpeg\" alt=\"IMG_20201231_172407.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161045644729039.jpeg\" alt=\"IMG_20201231_172820.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161045644729259.jpeg\" alt=\"IMG_20201231_175110.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161045649884933.jpeg\" alt=\"IMG_20201231_201549.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161045651122274.jpeg\" alt=\"IMG_20210102_203616.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161045649887733.jpeg\" alt=\"IMG_20210102_203208.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161045649860605.jpeg\" alt=\"IMG_20210102_203504.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161045656154536.jpeg\" alt=\"IMG_20210102_203903.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161045656117728.jpeg\" alt=\"IMG_20210102_210142.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161045656822604.jpeg\" alt=\"IMG_20210102_211030.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161045657345919.jpeg\" alt=\"IMG_20210102_211455.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161045659482871.jpeg\" alt=\"IMG_20210102_213210.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161045660244438.jpeg\" alt=\"IMG_20210105_161220.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161045661123750.jpeg\" alt=\"IMG_20210109_170936.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161045661713287.jpeg\" alt=\"IMG_20210109_180437.jpg\"></p><p><img alt=\"IMG_20210102_211617.jpg\" src=\"http://pic.zaeke.com/img161050983840963.jpeg\" width=\"1300\" height=\"975\"><br></p><p><img alt=\"IMG_20210102_203254.jpg\" src=\"http://pic.zaeke.com/img161050984256157.jpeg\" width=\"1300\" height=\"975\"><br></p><p><img alt=\"IMG_20210102_203834.jpg\" src=\"http://pic.zaeke.com/img161050986639356.jpeg\" width=\"1300\" height=\"975\"><br></p><p>基于上述硬件性能提升，小米 11 的成像速度相比较于上一代产品有着更显著的成像速度提升，特别在 1.08 亿像素拍照模式下，小米 10 每次拍摄完毕之后大概需要大约 2 秒左右的时间才能在相册中查阅最终样张效果，可是小米 11 却能实现秒拍输出，并不需要额外的等待时长。</p><p><img src=\"http://pic.zaeke.com/img161045621045802.jpeg\" alt=\"1610448313(1).jpg\"></p><p>另外，得益于成像速度的显著提升，一旦小米 11 相机系统判断环境光线过于昏暗时，自动通过降低快门速度换取更充足的光线，整个成像过程一气呵成，不必繁琐手动切换至夜景模式。在 Spectra 580 ISP 足够强劲的算法基础之下，小米 11 支持超级夜景视频、大师电影相机滤镜、电影镜头、魔法换天（新增更多天气效果滤镜），从根本丰富了相机功能的可玩性。</p><p style=\"text-align: center;\"><img src=\"http://pic.zaeke.com/img161045621459846.gif\" alt=\"GIF 2021-1-12 18-42-05.gif\"></p><p>上述提及的创意拍摄特性，其实需要强大的 AI 性能配合完成的，骁龙 888 移动平台搭载了第六代高通 AI 引擎，其中内含的 Hexagon 780 处理器使整个平台的 AI 算力可以达到惊人的 26 万亿次运算能力，而骁龙&nbsp;865&nbsp;的最高算力则为 15 万亿次运算能力，前者的算力性能提高了 73%。</p><p><img src=\"http://pic.zaeke.com/img161045613895211.jpeg\" alt=\"IMG_0304.jpg\"></p><p>高通骁龙 888 移动平台通过强大的运算能力，使其可以带来足够流畅的实时动态影像新玩法，以新增的“电影镜头”功能为例，即便在“平行梦境”这种水平颠倒的复杂画面时，其相机界面不会出现任何卡顿感，拍摄过程画面非常顺滑。不仅如此，小米 11 配合 1300 万像素广角镜头，其能够提供 123° 的超大视野范围。</p><p><img src=\"http://pic.zaeke.com/img161045670048013.jpeg\" alt=\"IMG_20201231_220511.jpg\"></p><p><img alt=\"IMG_20210102_211119.jpg\" src=\"http://pic.zaeke.com/img161045685017353.jpeg\" width=\"1300\" height=\"964\"></p><p><img alt=\"IMG_20210102_212119.jpg\" src=\"http://pic.zaeke.com/img161045685058285.jpeg\" width=\"1300\" height=\"964\"><br></p><p><img alt=\"IMG_20210102_212228.jpg\" src=\"http://pic.zaeke.com/img161045685737316.jpeg\" width=\"1300\" height=\"964\"></p><p><img alt=\"IMG_20210111_204724.jpg\" src=\"http://pic.zaeke.com/img161045687421580.jpeg\" width=\"1300\" height=\"964\"><br></p><p>从样张不难发现，小米 11 的 1300 万像素超广角镜头满足日常拍摄建筑、风光场景下的广角拍摄能力，通过 AI 超广角畸变校正特性，可一定程度改善超广角镜头的边缘畸变情况。虽说这颗摄像头的综合成像质量远不如主摄，可是 123° 的超大视野很大程度上丰富了用户在不同场景下的拍照需求，而且该摄像头同样支持夜景模式，可弥补弱光环境下超广角镜头进光量不足的问题，再结合 Spectra 580 ISP 最新的 AI 引擎，能够大幅提升低光环境下的照片拍摄能力。</p><p><img alt=\"IMG_20201231_164506_1.jpg\" src=\"http://pic.zaeke.com/img161045696268759.jpeg\" width=\"1300\" height=\"975\"></p><p><img alt=\"IMG_20201231_201951.jpg\" src=\"http://pic.zaeke.com/img161045696226170.jpeg\" width=\"1300\" height=\"975\"></p><p><img alt=\"IMG_20210102_213101.jpg\" src=\"http://pic.zaeke.com/img161045696294506.jpeg\" width=\"1300\" height=\"975\"><br></p><p><img alt=\"IMG_20210111_205553.jpg\" src=\"http://pic.zaeke.com/img161045706035811.jpeg\" width=\"1300\" height=\"975\"></p><p><img alt=\"IMG_20210111_210320.jpg\" src=\"http://pic.zaeke.com/img161045706070079.jpeg\" width=\"1300\" height=\"975\"></p><p><img alt=\"IMG_20210111_210350.jpg\" src=\"http://pic.zaeke.com/img161045706057663.jpeg\" width=\"1300\" height=\"975\"><br></p><p><img alt=\"IMG_20210111_210751.jpg\" src=\"http://pic.zaeke.com/img161045713378559.jpeg\" width=\"1300\" height=\"975\"></p><p><img alt=\"IMG_20210111_212233.jpg\" src=\"http://pic.zaeke.com/img161045713322005.jpeg\" width=\"1300\" height=\"975\"><br></p><p>继主摄与超广角镜头之后，小米 11 的第三颗后置摄像头为长焦微距镜头，该镜头等效 50mm 焦段，支持 3-7cm 自动对焦，有效像素为 500 万像素。尽管这款镜头的有效像素仅有 500 万像素，可是通过长焦镜头足够远的拍摄距离特性尽可能减少对动物干扰，达到提高拍摄成品率的效果。配合前置的 2000 万像素超微型前置相机，基于 Spectra 580 ISP 的强大算力，可以实现连续三张照片的快速拍摄，通过相机程序的后期处理之后，即可获得一张足够广的前置拍摄样张。</p><p><img alt=\"IMG_0069.jpg\" src=\"http://pic.zaeke.com/img161045722942165.jpeg\" width=\"1400\" height=\"933\"><br></p><p>小米 11 这次在摄像头配置方面采用的“只在精，不求多”的策略还是相当务实的，另一方面，小米 11 摄像头排列方式对于营造多摄无缝切换体验也是起着举足轻重的作用。小米 11 这次在多摄无缝切换体验中也带来显著提升，一方面要得益于矩阵式排列令每一颗摄像头的间距足够一致和尽可能靠拢，而且通过高通骁龙 888 移动平台支持三个摄像头的并发、并行拍摄特性，大幅度降低摄像头之间的启动时间，从一定程度上提高了多摄的无缝切换体验。当然，小米 11 的 500 万像素长焦微距镜头是不参与无缝切换特性的。</p><p style=\"text-align: center;\"><b>拔得头筹的小米 11，是一次具有诚意的换代</b></p><p>小米 11 作为首款搭载了高通迄今为止最先进的移动平台骁龙 888 机型，该机型在 Android 阵营中的核心性能表现方面无疑占据了绝对优势，通过内置性能强悍的 Adreno660 GPU 和第三代高通 Snapdragon Elite Gaming 的双加持之下，让手游玩家能够享受最具沉浸感的游戏体验。在通讯表现方面，集成的高通骁龙 X60 调制解调器能更好地让用户感受 5G 的魅力，而且还率先支持了更先进的 Wi-Fi6 增强版特性。</p><p><img alt=\"IMG_0366.jpg\" src=\"http://pic.zaeke.com/img161045768892057.jpeg\" width=\"1400\" height=\"933\"><br></p><p>骁龙 888 移动平台的性能优势，在小米 11 的影像系统方面也起到正面作用，通过 Spectra 580 ISP 和第六代高通 AI 引擎加持，让小米 11 的拍照体验迎来了一次跨越式“优化”，不仅拍照成片速度迎来了显著提升，在强大的算法基础之下，MIUI 12 可以提供更多创意的拍照新玩法，丰富了用户的影像拍摄乐趣。对于注重手机性能的消费者而言，小米 11 自然而然成为了这部分消费者的最优选之一。</p><p><img alt=\"IMG_0319.jpg\" src=\"http://pic.zaeke.com/img161045769537692.jpeg\" width=\"1400\" height=\"933\"><br></p><p>除了性能的提升以外，小米 11 相比较于上代产品，在使用体验方面也作出了不少的努力，例如机身变得更加轻薄，从根本减少了握持疲劳感；小米 11 屏幕误触情况大有改善，基本在佩戴手机壳的情况下不容易触发误触情况；6.81 英寸 2K 分辨率屏幕观感更细腻，120Hz 刷新率观感更显顺滑；屏幕指纹位置进行了上移处理，解锁体验更舒适；55W 有线快充加持，充电速度更快；并且配备了更抗摔、耐磨的大猩猩玻璃 Victus。</p><p>当然，小米 11 相比较于上一代产品其实也存在一些“退步”的地方，例如电池容量从 4780mAh 降至 4600mAh；外放表现有所下降，中大音量情况下机身存在共振情况；机身能感知到的表温更高，即便是冬季的室内，机身正反表温超过 40℃。</p><p><img alt=\"IMG_0121.jpg\" src=\"http://pic.zaeke.com/img161045790486863.jpeg\" width=\"1400\" height=\"933\"></p><p><img alt=\"IMG_0122.jpg\" src=\"http://pic.zaeke.com/img161045790456317.jpeg\" width=\"1400\" height=\"933\"><br></p><p>毋庸置疑的是，拔得头筹的小米 11 仅 10 天就取得了销售 50 万台的骄人成绩，该成绩还是建立在市场供不应求的情况下实现的，可以称得上是小米取得 2021 年迎来的开门红。相比较于小米 10 ，小米 11 的硬件得到了较大幅度的提升，无形中拉高了该价位段高端机型的竞争格局。现阶段而言，小米 11 还能成为持续利用宝贵的新一代旗舰手机空窗期优势，目前产能问题才是小米 11 的困扰根源。</p>', '<p class=\"p_style\">数字系列一直都是小米的年度重头戏，而再次成功获得了高通骁龙 8 系列首发权的小米，更是赶在 2021 年来临之际“抢先”发布了小米 11。作为首款面世的骁龙 888 平台机型，小米 11 的首销实现了 5 分钟销售额破 15 亿的骄人成绩，显然小米在这场提前吹响战斗号角的“战斗”上，已经取得了一次不俗的开门红。</p><p style=\"text-align: center;\" class=\"p_style\"><b>高通骁龙 888 移动平台，重新定义顶级移动体验</b></p><p class=\"p_style\">尽管小米 11 为首销已经做了一定的备货，可显然还无法满足庞大的市场需求。当然，相比较于渴望抢先吃上第一波吃“螃蟹”的米粉，其实仍然有不少消费者对于这款未经过“市场检验”的新机型持观望态度，毕竟它是首款搭载高通骁龙 888 移动平台的机型呢，在使用体验上仍存在很多未知因素。</p><p class=\"jsmargin\"><img alt=\"IMG_0309.jpg\" src=\"http://pic.zaeke.com/img161045733889939.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">实际上，高通骁龙 888 移动平台的账面数据是非常诱人的，首先 CPU 部分采用了 1 超 3 大 4 小的核心组合，1 颗基于 Cortex-X1 深度定制的 Kryo680 超级大核，主频高达 2.84GHz，3 颗基于 Cortex-A78 深度定制的 Kryo680 大核，主频 2.42GHz，以及 4 颗基于 Cortex-A55 的 Kryo680 小核；GPU 方面，其配备了全新的 Adreno660 图形处理器，据说该 GPU 相比较于上代骁龙 865 所采用的 Adreno650 GPU ，前者有着 35% 的性能提升，可是功耗却降低 20%。</p><p class=\"p_style\">在如此强劲的性能支撑之下，小米 11 即便搭载了一块 2K 分辨率 120Hz 高刷屏幕，其应付基本的系统流畅性显然已经不在话下。为了尽可能发挥出高通骁龙 888 移动平台的极限能力，只能通过目前一些相对主流的顶尖大型手游对其进行测试。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045606784700.jpeg\" alt=\"使命召唤手游.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045606784621.jpeg\" alt=\"和平精英.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">在使用小米 11 上手《和平精英》、《使命召唤手游》、《原神》三款大型游戏时，通过使用 PerfDog 的监控数据可以发现，在室内温度 18℃ 的环境下，小米 11 在《和平精英》的 HDR高清画质、极限帧数设置之下，其帧数可以达到几乎满帧的 59.6 帧，基本感受不到游戏过程中的画面卡顿情况；《使命召唤手游》在精致画质、极限帧数设置之下，小米 11 可实现全程 60 帧的满帧运行，整个游戏过程非常顺畅。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045605832658.jpeg\" alt=\"原神.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">除此，面对《原神》这款对性能要求更高的开放世界冒险游戏，我也尝试把它的画面调至极限画质、帧数设置为 60 帧，经过 40 分钟以上的上手体验测试，小米 11 获得了 54.8 帧的平均帧数表现，游戏过程中能感受到帧数有所波动，可是整体不会有明显卡顿感。</p><p class=\"jsmargin\"><img alt=\"IMG_0312.jpg\" src=\"http://pic.zaeke.com/img161045731912455.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_0277.jpg\" src=\"http://pic.zaeke.com/img161045799297060.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">纵使骁龙 888 平台采用了先进的 5nm 工艺制程，可是小米为了能让该 SoC 可以持续地带来稳定的高负载运算能力，小米 11 采用了 VC 液冷立体散热系统、多点温度监控等控温措施，确保了机器能带来足够强大的散热效率，我从小米 11 在多款大型游戏中的持续稳定发挥，已经可以得到一份满意的答卷。</p><p class=\"p_style\">显而易见，骁龙 888 移动平台延续了骁龙移动平台在游戏方面的优异表现。凭借性能强悍的 Adreno660 GPU 和第三代 Snapdragon Elite Gaming 加持，使骁龙 888 移动平台拥有足够强劲的算法和优化表现，另外除了延续了GPU驱动更新、端游级正向渲染、高达 144 帧率游戏画面以外，骁龙 888 移动平台还新增了可变分辨率渲染技术、Game Quick Touch 技术，其中可变分辨率渲染技术在提高游戏运行的性能基础下实现更低的功耗表现，而 Game Quick Touch 可针对游戏中的帧同步问题进行了毫秒级优化，有效降低玩家的触控延迟，通俗理解就是玩家在游戏过程中的触控体验更“跟手”了。</p><p class=\"jsmargin\"><img alt=\"IMG_0123.jpg\" src=\"http://pic.zaeke.com/img161045736673852.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">在我看来，拥有高通骁龙 888 移动平台加持的小米 11，可以充分地发挥出 6.81 英寸 2K 屏幕的优势，特别在部分游戏中，2K 分辨率屏幕可带来更加细腻的游戏画面，这是全高清屏幕机型所难以企及的地方，而高通骁龙 888 移动平台的高性能特性可支撑小米 11 轻松应对市面上绝大部分中大型手机游戏。</p><p class=\"jsmargin\"><img alt=\"IMG_0324.jpg\" src=\"http://pic.zaeke.com/img161045739096183.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_0329.jpg\" src=\"http://pic.zaeke.com/img161045739198886.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">除了屏幕输出所带来的视觉体验，听感体验也是手机娱乐中非常关键的一环。相比较于小米 10 的双 1216 超线性对称式扬声器组合，采用非对称听筒式双扬声器的小米 11 在外放表现方面还是稍逊一筹，当中差距主要在于小米 11 的细节、响度方面都稍有退步，而且该机在中大音量下，存在机身共振情况。其实市面上不少轻薄型手机都有扬声器外放共振情况，通常是因为机身后盖材料过于纤薄所导致。即便如此，小米 11 的外放效果仍然比往年的不少旗舰机型都要优秀，也许不是小米 11 的外放逊色，而是小米 10 的外放表现太好了。</p><p class=\"jsmargin\"><img alt=\"1610453732(1).jpg\" src=\"http://pic.zaeke.com/img161045754595353.jpeg\" width=\"1124\" height=\"829\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">在存储组合搭配方面，这次小米 11 也依旧有着亮眼表现，去年被小米 10 率先搭载的 LPDDR5，这次在小米 11 身上得到了再一次升级，其 LPDDR5 内存主频由小米 10 的 2750MHz，升级为 3200MHz，数据传输速率也从 5500Mbps 提升至 6400Mbps。配合 UFS 3.1 高速闪存，意味着该机型的机身存储性能表现也有了足够充沛的性能保障。小米 11 在 AndroBench 存储跑分软件中，取得了顺序读取速度 1844.18MB/s、顺序写入速度 766.29MB / s、随机读取速度 259.88MB / s、随机写入速度 233.95MB / s 的成绩，综合跑分表现不俗。</p><p class=\"jsmargin\"><img alt=\"IMG_0239.jpg\" src=\"http://pic.zaeke.com/img161045755632281.jpeg\" width=\"1300\" height=\"867\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">鉴于小米 11 采用了更轻薄的机身设计，因此在电池容量方面相比上一代产品有所下降，其搭载了 4600mAh 机身电池，搭配 55W 有线快充，可以实现 45 分钟从零电量充满至 100%，并且该机型还支持 50W 无线快充，零电量状态下充满至 100% 需要耗时 53 分钟。</p><p class=\"p_style\">与此同时，小米 11 保留了 10W 无线反向充电功能特性。为了保证基本的充电性能，小米 11 采用了全新低功耗充电管理芯片，既大幅度降低热损耗，也降低了充电发热，而且配合 Mi-FC 快充技术进一步提升充电速度。注：图片中使用的并非无线快充充电板，仅是展示作用。</p><p style=\"text-align: center;\" class=\"p_style\"><b>缓解全球运营商 5G 规模难度，同时积极拥抱未来</b></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045595534164.jpeg\" alt=\"qualcomm-sdx60.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">上述体验都需要高度依赖网络连接能力，骁龙 888 移动平台为了确保网络稳定性和先进性，其集成了全球首款 5nm 制程的 5G 调制解调器 —— 高通骁龙 X60，考虑到高通骁龙 X60 调制解调器采用了最新一代 AP + Modem 的集成式解决方案，因此它可以在更低的功耗表现下，带来更小的体积和能效表现。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045602296376.jpeg\" alt=\"csm_001215_16f0aea033.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">在如此先进的硬件基础之下，高通骁龙 X60 调制解调器实现了 5G&nbsp;Sub-6GHz 载波聚合和毫米波的支持，可实现最高 7.5Gbps 的下载速率和 3Gbps 的上传速率，这就意味着小米 11 的 5G SA 峰值速率达到翻倍提升。为了提高网络的使用效率，高通骁龙 X60 调制解调器还新增了对动态频谱共享技术（DSS）的支持，运营商可以针对拆分频谱的方式经济高效地部署 5G 服务，实现 4G 与 5G 频谱的共存，从而达到提高信号覆盖率的作用，缓解全球各大运营商对于 5G 网络部署难度过高的过渡性问题。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045603163706.gif\" alt=\"VbuUFr.gif\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">不仅如此，高通骁龙 X60 调制解调器凭借基于 5G SA 独立组网的 VoNR 语音及视频业务，从 VoLTE 的基础下有效提升语音通话的音质和降低延迟低，而且有效解决通话时信号回落的问题，给予用户更高质量的沟通体验。也许距离 VoNR 通话的正式大面积商用仍然有一段漫长的时间，可是随着以高通为首的芯片厂商的对于该技术的积极拥抱，可以对该技术起到推进作用。</p><p class=\"p_style\">得益于高通骁龙 888 移动平台在 Wi-Fi 6、Wi-Fi 6E（6GHz 频段）、蓝牙 5.2 等连接能力支持，小米 11 也实现了第六代 Wi-Fi 802.11ax 标准的支持。按照小米官方的描述，小米 11 在配合 Wi-Fi 6 增强版路由器的情况下，可以带来比上一代机型翻一倍的频宽（从 80MHz 升级至 160MHz），同时编码也由 1024QAM 升级至 4096QAM，真正实现了更快、密度更高的 Wi-Fi 连接能力。当然，想要发挥出小米 11 极致的 Wi-Fi 连接体验，就意味着还需要重新为家里购置一款支持 Wi-Fi 6E 标准的路由器。</p><p class=\"jsmargin\"><img alt=\"IMG_0328.jpg\" src=\"http://pic.zaeke.com/img161045765388984.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">有着旗舰定位的小米 11，其搭载的骁龙 888 移动平台通过上述众多先进通讯功能特性，不仅确保了现阶段的基本网络使用体验，它还具备衔接未来发展趋势的连接能力，为未来几年运营商开放相应新技术做好充分准备。</p><p style=\"text-align: center;\" class=\"p_style\"><b>更高效的拍摄体验，助用户捕捉美好瞬间</b></p><p class=\"p_style\">以摄像头数量论好坏大趋势之下，小米 11 仍然选择在小米 10 的后置四摄基础之下采用了后置三摄方案，分别为 1 亿超清主摄 + 1300 万超广角 + 500 万长焦微距，其中小米 11 延续了上一代产品的 1 亿像素主摄，采用的三星 HMX 图像传感器尺寸为 1/1.33 英寸，搭配 7P 镜头设计和 OIS 光学防抖，弱光环境下可实现四合一 1.6μm 大像素夜拍能力。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045609092428.png\" alt=\"img160747692548750.png\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">尽管主摄的配置与上代产品保持一致，可是小米 11 通过算法的升级优化，使它在弱光环境下带来更显纯净的画面表现。除此，骁龙 888 移动平台对于小米 11 的使用体验提升并不止步于娱乐和通讯，因为高通骁龙 888 移动平台凭借其最新的 Spectra 580 ISP，实现了比上一代产品 35% 的性能提升，每秒可以实现 27 亿次像素的运算性能。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045644759806.jpeg\" alt=\"IMG_20201231_172407.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045644729039.jpeg\" alt=\"IMG_20201231_172820.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045644729259.jpeg\" alt=\"IMG_20201231_175110.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045649884933.jpeg\" alt=\"IMG_20201231_201549.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045651122274.jpeg\" alt=\"IMG_20210102_203616.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045649887733.jpeg\" alt=\"IMG_20210102_203208.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045649860605.jpeg\" alt=\"IMG_20210102_203504.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045656154536.jpeg\" alt=\"IMG_20210102_203903.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045656117728.jpeg\" alt=\"IMG_20210102_210142.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045656822604.jpeg\" alt=\"IMG_20210102_211030.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045657345919.jpeg\" alt=\"IMG_20210102_211455.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045659482871.jpeg\" alt=\"IMG_20210102_213210.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045660244438.jpeg\" alt=\"IMG_20210105_161220.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045661123750.jpeg\" alt=\"IMG_20210109_170936.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045661713287.jpeg\" alt=\"IMG_20210109_180437.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210102_211617.jpg\" src=\"http://pic.zaeke.com/img161050983840963.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_20210102_203254.jpg\" src=\"http://pic.zaeke.com/img161050984256157.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_20210102_203834.jpg\" src=\"http://pic.zaeke.com/img161050986639356.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">基于上述硬件性能提升，小米 11 的成像速度相比较于上一代产品有着更显著的成像速度提升，特别在 1.08 亿像素拍照模式下，小米 10 每次拍摄完毕之后大概需要大约 2 秒左右的时间才能在相册中查阅最终样张效果，可是小米 11 却能实现秒拍输出，并不需要额外的等待时长。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045621045802.jpeg\" alt=\"1610448313(1).jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">另外，得益于成像速度的显著提升，一旦小米 11 相机系统判断环境光线过于昏暗时，自动通过降低快门速度换取更充足的光线，整个成像过程一气呵成，不必繁琐手动切换至夜景模式。在 Spectra 580 ISP 足够强劲的算法基础之下，小米 11 支持超级夜景视频、大师电影相机滤镜、电影镜头、魔法换天（新增更多天气效果滤镜），从根本丰富了相机功能的可玩性。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045621459846.gif\" alt=\"GIF 2021-1-12 18-42-05.gif\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">上述提及的创意拍摄特性，其实需要强大的 AI 性能配合完成的，骁龙 888 移动平台搭载了第六代高通 AI 引擎，其中内含的 Hexagon 780 处理器使整个平台的 AI 算力可以达到惊人的 26 万亿次运算能力，而骁龙&nbsp;865&nbsp;的最高算力则为 15 万亿次运算能力，前者的算力性能提高了 73%。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045613895211.jpeg\" alt=\"IMG_0304.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">高通骁龙 888 移动平台通过强大的运算能力，使其可以带来足够流畅的实时动态影像新玩法，以新增的“电影镜头”功能为例，即便在“平行梦境”这种水平颠倒的复杂画面时，其相机界面不会出现任何卡顿感，拍摄过程画面非常顺滑。不仅如此，小米 11 配合 1300 万像素广角镜头，其能够提供 123° 的超大视野范围。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161045670048013.jpeg\" alt=\"IMG_20201231_220511.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210102_211119.jpg\" src=\"http://pic.zaeke.com/img161045685017353.jpeg\" width=\"1300\" height=\"964\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210102_212119.jpg\" src=\"http://pic.zaeke.com/img161045685058285.jpeg\" width=\"1300\" height=\"964\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_20210102_212228.jpg\" src=\"http://pic.zaeke.com/img161045685737316.jpeg\" width=\"1300\" height=\"964\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210111_204724.jpg\" src=\"http://pic.zaeke.com/img161045687421580.jpeg\" width=\"1300\" height=\"964\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">从样张不难发现，小米 11 的 1300 万像素超广角镜头满足日常拍摄建筑、风光场景下的广角拍摄能力，通过 AI 超广角畸变校正特性，可一定程度改善超广角镜头的边缘畸变情况。虽说这颗摄像头的综合成像质量远不如主摄，可是 123° 的超大视野很大程度上丰富了用户在不同场景下的拍照需求，而且该摄像头同样支持夜景模式，可弥补弱光环境下超广角镜头进光量不足的问题，再结合 Spectra 580 ISP 最新的 AI 引擎，能够大幅提升低光环境下的照片拍摄能力。</p><p class=\"jsmargin\"><img alt=\"IMG_20201231_164506_1.jpg\" src=\"http://pic.zaeke.com/img161045696268759.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20201231_201951.jpg\" src=\"http://pic.zaeke.com/img161045696226170.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210102_213101.jpg\" src=\"http://pic.zaeke.com/img161045696294506.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_20210111_205553.jpg\" src=\"http://pic.zaeke.com/img161045706035811.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210111_210320.jpg\" src=\"http://pic.zaeke.com/img161045706070079.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210111_210350.jpg\" src=\"http://pic.zaeke.com/img161045706057663.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_20210111_210751.jpg\" src=\"http://pic.zaeke.com/img161045713378559.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210111_212233.jpg\" src=\"http://pic.zaeke.com/img161045713322005.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">继主摄与超广角镜头之后，小米 11 的第三颗后置摄像头为长焦微距镜头，该镜头等效 50mm 焦段，支持 3-7cm 自动对焦，有效像素为 500 万像素。尽管这款镜头的有效像素仅有 500 万像素，可是通过长焦镜头足够远的拍摄距离特性尽可能减少对动物干扰，达到提高拍摄成品率的效果。配合前置的 2000 万像素超微型前置相机，基于 Spectra 580 ISP 的强大算力，可以实现连续三张照片的快速拍摄，通过相机程序的后期处理之后，即可获得一张足够广的前置拍摄样张。</p><p class=\"jsmargin\"><img alt=\"IMG_0069.jpg\" src=\"http://pic.zaeke.com/img161045722942165.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">小米 11 这次在摄像头配置方面采用的“只在精，不求多”的策略还是相当务实的，另一方面，小米 11 摄像头排列方式对于营造多摄无缝切换体验也是起着举足轻重的作用。小米 11 这次在多摄无缝切换体验中也带来显著提升，一方面要得益于矩阵式排列令每一颗摄像头的间距足够一致和尽可能靠拢，而且通过高通骁龙 888 移动平台支持三个摄像头的并发、并行拍摄特性，大幅度降低摄像头之间的启动时间，从一定程度上提高了多摄的无缝切换体验。当然，小米 11 的 500 万像素长焦微距镜头是不参与无缝切换特性的。</p><p style=\"text-align: center;\" class=\"p_style\"><b>拔得头筹的小米 11，是一次具有诚意的换代</b></p><p class=\"p_style\">小米 11 作为首款搭载了高通迄今为止最先进的移动平台骁龙 888 机型，该机型在 Android 阵营中的核心性能表现方面无疑占据了绝对优势，通过内置性能强悍的 Adreno660 GPU 和第三代高通 Snapdragon Elite Gaming 的双加持之下，让手游玩家能够享受最具沉浸感的游戏体验。在通讯表现方面，集成的高通骁龙 X60 调制解调器能更好地让用户感受 5G 的魅力，而且还率先支持了更先进的 Wi-Fi6 增强版特性。</p><p class=\"jsmargin\"><img alt=\"IMG_0366.jpg\" src=\"http://pic.zaeke.com/img161045768892057.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">骁龙 888 移动平台的性能优势，在小米 11 的影像系统方面也起到正面作用，通过 Spectra 580 ISP 和第六代高通 AI 引擎加持，让小米 11 的拍照体验迎来了一次跨越式“优化”，不仅拍照成片速度迎来了显著提升，在强大的算法基础之下，MIUI 12 可以提供更多创意的拍照新玩法，丰富了用户的影像拍摄乐趣。对于注重手机性能的消费者而言，小米 11 自然而然成为了这部分消费者的最优选之一。</p><p class=\"jsmargin\"><img alt=\"IMG_0319.jpg\" src=\"http://pic.zaeke.com/img161045769537692.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">除了性能的提升以外，小米 11 相比较于上代产品，在使用体验方面也作出了不少的努力，例如机身变得更加轻薄，从根本减少了握持疲劳感；小米 11 屏幕误触情况大有改善，基本在佩戴手机壳的情况下不容易触发误触情况；6.81 英寸 2K 分辨率屏幕观感更细腻，120Hz 刷新率观感更显顺滑；屏幕指纹位置进行了上移处理，解锁体验更舒适；55W 有线快充加持，充电速度更快；并且配备了更抗摔、耐磨的大猩猩玻璃 Victus。</p><p class=\"p_style\">当然，小米 11 相比较于上一代产品其实也存在一些“退步”的地方，例如电池容量从 4780mAh 降至 4600mAh；外放表现有所下降，中大音量情况下机身存在共振情况；机身能感知到的表温更高，即便是冬季的室内，机身正反表温超过 40℃。</p><p class=\"jsmargin\"><img alt=\"IMG_0121.jpg\" src=\"http://pic.zaeke.com/img161045790486863.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_0122.jpg\" src=\"http://pic.zaeke.com/img161045790456317.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">毋庸置疑的是，拔得头筹的小米 11 仅 10 天就取得了销售 50 万台的骄人成绩，该成绩还是建立在市场供不应求的情况下实现的，可以称得上是小米取得 2021 年迎来的开门红。相比较于小米 10 ，小米 11 的硬件得到了较大幅度的提升，无形中拉高了该价位段高端机型的竞争格局。现阶段而言，小米 11 还能成为持续利用宝贵的新一代旗舰手机空窗期优势，目前产能问题才是小米 11 的困扰根源。</p>', 'http://pic.zaeke.com/img161095059867225.jpeg', 20619, 0, 0, NULL, '2021-01-12 22:46:58', '2021-03-27 08:11:53');
INSERT INTO `article` VALUES (15939, 'realme V15给自己定了个小目标：成为一部线上线下到处卖的线上机', '经过几天赶工，善于拖稿的我们最终赶在大家还有印象时完成了realmeV15的评测。这是部拥有锦鲤色后盖、50W有线快充、6400万后置三摄、6.4英寸AMOLE...', '[object Object]', 'Kururu', 33, 'http://pic.zaeke.com/user_1533281476_2.png', '<p>经过几天赶工，善于拖稿的我们最终赶在大家还有印象时完成了realme V15的评测。这是部拥有锦鲤色后盖、50W有线快充、6400万后置三摄、6.4英寸AMOLED显示屏、最低176克重量和首发价1399元起的“国潮锦鲤手机”。</p><p>该机提供6或8GB内存版本，全系标配128GB存储空间，拥有新月银、镜湖蓝和锦鲤色这三种配色可选。我们收到并用于评测的是8GB内存+128GB存储的锦鲤色，系统为截止发稿前最新的realme UI 1.0 A.23固件。</p><p><img alt=\"_1093631.jpg\" src=\"http://pic.zaeke.com/img161069170138757.jpeg\" width=\"2000\" height=\"1127\"><br></p><p><img alt=\"_1093624.jpg\" src=\"http://pic.zaeke.com/img161069162427669.jpeg\" width=\"2000\" height=\"1127\"><br></p><p><img alt=\"_1093966.jpg\" src=\"http://pic.zaeke.com/img161069163981141.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>realme V15的诸多特征之中，“锦鲤色”后盖是重中之重。它由realme与央视《国家宝藏》栏目组合作打造，灵感取自传世名画《洛神赋图》中象征着好运和现代锦鲤原型的“文鱼”，并通过非光谱渐变色彩设计和双纹双镀工艺将其实现。从真机上看，“锦鲤色”可以用“红中带金、金中带蓝”来形容，并且在众机群中也有较高的辨识度。</p><p>可较高的辨识度在实际生活中对于realme V15而言并不太友好，因为指纹和掌印在这部以设计见长的玻璃后盖手机上将无处藏身。所以你如果有意购入realme V15，那我们建议戴好标配的透明保护壳。这不仅可以让你端庄又不失优雅的展示这部“锦鲤”，还可以确保你朋友或同事在仔细端详“锦鲤”时，不会都以一句令人尴尬的“哇，这手机这么沾指纹呀”来当做开场白。</p><p><img alt=\"591A7711.jpg\" src=\"http://pic.zaeke.com/img161069173571994.jpeg\" width=\"2000\" height=\"1125\"><br></p><p><img alt=\"_1093979.jpg\" src=\"http://pic.zaeke.com/img161069174980790.jpeg\" width=\"2000\" height=\"1127\"><br></p><p><img alt=\"_1093986.jpg\" src=\"http://pic.zaeke.com/img161069198749163.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>在大家比较关心也是这个价格段相对关键的配置上，realme V15基于联发科技天玑800U处理器打造，具有SA/NSA双组网方式、5G双卡双待和双通道网络加速等特征。并配备支持180Hz触控采样率的6.4英寸2400×1080分辨率AMOLED单孔屏，LPDDR4X双通道内存、UFS 2.1存储空间和4310毫安时的电池。</p><p>经ZAEKE知客的测试结果显示，realme V15在单卡5G+单卡4G模式并关闭无线网络的9小时待机测试中耗电量为3%。而屏幕亮度约50%，使用无线网络并连接有线耳机，在哔哩哔哩开启弹幕看30分钟1080P视频耗电5%，抖音刷30分钟短视频耗电4%。</p><p>同样环境下测试游戏续航表现，《王者荣耀》王者峡谷排位赛2局，首局9分37秒，第二局18分53秒，算上排队、BP英雄和结算接近30分钟，耗电量为20%；《和平精英》海岛地图连续2局约40分钟，耗电量为11%；《天龙八部手游》在燕王古墓5层挂机且屏幕亮度调到最低的情况下，一小时耗电量为7%。</p><p><img alt=\"591A7893.jpg\" src=\"http://pic.zaeke.com/img161069180173084.jpeg\" width=\"1400\" height=\"787\"><br></p><p><img alt=\"591A7887.jpg\" src=\"http://pic.zaeke.com/img161069181233320.jpeg\" width=\"1400\" height=\"788\"><br></p><p>至于充电，realme V15全系标配65W充电器和官方宣称可以在“47分钟充满4310毫安时电池”的50W有线快充。我们的测试结果与官方描述基本一致，realme V15的电量可以在5分钟左右从0%被快速提升至20%，然后在10分钟充到36%，在20分钟充到60%，在30分钟充到80%，在40分钟充到92%，在47分时充满。</p><p>将续航和充电结合在一起形象点概述，就是临出门上班前发现手机没电了，等一首歌的时间就可以获得满足路途中刷微博或是看视频需求的电量。如果发现的再早一点，例如起床后就发现没电并开始充的话，那么还有较大可能在出门前获得路途中可以全程打游戏甚至是用大半天的电量。</p><p><img alt=\"591A7892-编辑.jpg\" src=\"http://pic.zaeke.com/img161069178055115.jpeg\" width=\"1400\" height=\"788\"><br></p><p>影像方面，后置由6400万像素(主摄)+800万像素(119°广角)+200万像素(4厘米微距)三摄组成的realme V15基本符合ZAEKE知客对于这个价位手机“鱼和熊掌不可兼得”的预期。相机的启动速度和对焦速度尚可，提供了自动HDR和AI场景增强功能，光线充足的前提下拍风景、拍花草树木、晒个美食或是跟朋友合个影都不会有难以接受的问题出现。</p><p><img alt=\"IMG20210115155100.jpg\" src=\"http://pic.zaeke.com/img161069992377971.jpeg\" width=\"1400\" height=\"1880\"><br></p><p><img alt=\"IMG20210115155129.jpg\" src=\"http://pic.zaeke.com/img161069997810157.jpeg\" width=\"1400\" height=\"1880\"><br></p><p><img alt=\"IMG20210115160312.jpg\" src=\"http://pic.zaeke.com/img161070001977497.jpeg\" width=\"1400\" height=\"1043\"><br></p><p><img alt=\"IMG20210115154038.jpg\" src=\"http://pic.zaeke.com/img161070005439873.jpeg\" width=\"1400\" height=\"1043\"><br></p><p><img alt=\"IMG20210115154940.jpg\" src=\"http://pic.zaeke.com/img161070006758000.jpeg\" width=\"1400\" height=\"1880\"><br></p><p><img alt=\"IMG20210115153046.jpg\" src=\"http://pic.zaeke.com/img161069989271926.jpeg\" width=\"1400\" height=\"1043\"><br></p><p><img alt=\"IMG20210115154556.jpg\" src=\"http://pic.zaeke.com/img161070011396326.jpeg\" width=\"1400\" height=\"1043\"><br></p><p><img alt=\"IMG20210115154724.jpg\" src=\"http://pic.zaeke.com/img161070013553096.jpeg\" width=\"1400\" height=\"1880\"><br></p><p><img alt=\"IMG20210115161352.jpg\" src=\"http://pic.zaeke.com/img161070015959678.jpeg\" width=\"1400\" height=\"1880\"><br></p><p>可一旦室内光线较差或是夜幕降临，realme V15的成像质量用高情商说法的话就是“不合眼缘”，低情商说法的话就是“糊成一片”。无论通过常规模式还是夜景模式进行拍摄，大多时候得到的照片都会有“你是在块略脏的玻璃后拍出来的”那种感觉。<br></p><p><img alt=\"IMG20210116192640.jpg\" src=\"http://pic.zaeke.com/img161079923893876.jpeg\" width=\"1400\" height=\"1880\"><br></p><p><img alt=\"IMG20210116185331.jpg\" src=\"http://pic.zaeke.com/img161079924739457.jpeg\" width=\"1400\" height=\"1880\"><br></p><p><img alt=\"IMG20210116191243.jpg\" src=\"http://pic.zaeke.com/img161079926599848.jpeg\" width=\"1400\" height=\"1880\"><br></p><p><img alt=\"IMG20210116191321.jpg\" src=\"http://pic.zaeke.com/img161079929510685.jpeg\" width=\"1400\" height=\"1880\"><br></p><p><img alt=\"IMG20210115233458.jpg\" src=\"http://pic.zaeke.com/img161079932979460.jpeg\" width=\"1400\" height=\"1043\"><br></p><p><img alt=\"IMG20210115232943.jpg\" src=\"http://pic.zaeke.com/img161079936133570.jpeg\" width=\"1400\" height=\"1880\"><br></p><p><img alt=\"IMG20210116192857.jpg\" src=\"http://pic.zaeke.com/img161079934917401.jpeg\" width=\"1400\" height=\"1050\"><br></p><p>最后是系统，realme V15搭载了“基于Android 10深度定制的ColorOS 7进行二次定制的realme UI 1.0”，日常需要用到的各种功能可以用“齐全也正常”来形容。例如DC调光、暗色模式、屏幕录制、视频通话美颜和游戏空间等功能都有，全面屏手势也在屏幕支持180Hz触控采样率的前提下非常顺手。<br></p><p><img alt=\"未标题-7.jpg\" src=\"http://pic.zaeke.com/img161069417682369.jpeg\" width=\"1400\" height=\"788\"><br></p><p>并且realme已经承诺会在晚些时候为V15推送全新的realme UI 2.0，让这部手机获得全局主题色、图标自定义、深海隐私保护和电影字幕拼接分享等功能。但那都是后话，目前我们只先介绍两个即刻就能用上且非常推荐开启功能：“闪回键”和“图标下沉”。</p><p>闪回键举游戏场景的例子说，就是让你可以在《王者荣耀》里等复活或是《和平精英》游戏开始前放心的切到QQ、微信或其它应用，因为复活倒计时或跳伞倒计时会以小按钮的形式出现在手机左下方(支持自行拖动调整位置)，回完消息后点击闪回键即可切回游戏。</p><p><img alt=\"未标题-6.jpg\" src=\"http://pic.zaeke.com/img161069392369037.jpeg\" width=\"1400\" height=\"788\"><br></p><p>图标下沉则是非常适合“在地铁和公交车上没有座位”以及“只能单手控制手机”的情况下使用。因为它只要沿着屏幕其中一侧向上滑就可以很唤醒，且唤醒后手指不用抬起，只要一直贴着屏幕并移到应用图标上就可以打开应用，可以说是解决了单手模式依旧无法点到桌面第一排图标的问题。</p><p><img alt=\"591A7889.jpg\" src=\"http://pic.zaeke.com/img161069189662728.jpeg\" width=\"1400\" height=\"787\"><br></p><p>虽然realme UI总体不错，但写文章的这几天我们也遇到了两个可以轻松复现的问题。首先是横屏游戏时无法通过通知栏切换无线网络，你只能看到它在转圈和空白之间反复循环。但我们打开哔哩哔哩和芒果TV看横屏视频时，通知栏却可以正常显示和切换无线网络。</p><p>难道是手机本身游戏相关功能或设置的问题？为此我们尝试了“将游戏移出游戏空间”和“关闭游戏助手”这两种方式，然而结果都是realme V15在《王者荣耀》、《和平精英》、《天龙八部》和《使命召唤手游》这四款手游中无法通过通知栏快捷的切换无线网络。</p><p><img alt=\"未标题-5.jpg\" src=\"http://pic.zaeke.com/img161069296722242.jpeg\" width=\"1400\" height=\"788\"><br></p><p>其次，我们还遇到了暗色模式下悬浮窗应用与主应用难以区分的问题。如下图所示，realme UI在开启暗色模式后诸如计算器、日历和文件管理之类的系统应用都可以顺利的拥抱黑暗，但悬浮窗的那些系统应用也会一起遁入黑暗之中且难以区分，因为悬浮窗应用与主应用之间缺少了一道明确的分界线。</p><p><img alt=\"未标题-1.jpg\" src=\"http://pic.zaeke.com/img161070106627603.jpeg\" width=\"1400\" height=\"788\"><br></p><p>尽管这两个不是会严重影响使用的问题，但我们还是抱着试试的心态，在一番折腾后给找到了“治标不治本”的解决方案。例如在游戏过程中觉得目前网络延迟太高想从2.4G Wi-Fi切换到5G Wi-Fi时，先切换到桌面或其它应用，然后再下拉通知栏即可。再比如保留暗色模式在通知栏的快捷开关，当悬浮窗应用与主应用难以分辨时，下拉通知栏关闭暗色模式即可。</p><p>什么？你想知道这俩问题何时才能被“根治”？很遗憾，我们能告诉你的只有“期待realme后续的系统更新吧”。<br></p><p><img alt=\"_1093945.jpg\" src=\"http://pic.zaeke.com/img161069220097095.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>显而易见，realme V15在产品定义、配置选择和整个产品方向都有明确目标，那就是另一款同价位、同处理器的竞品。虽然双方都要“过同一个弯”，但realme V15显然跟竞品选择了不同的“入弯线路”。例如在外观设计方面倾注了更多资源，打造出了更有可能吸引少男少女们目光的外观设计。</p><p>当然这并不意味着realme V15是部只有外观的手机。在同价位中它的电池容量不落下风，有“独家”的50W快充。并且在与同价位选手们的LCD显示屏比较时，realme V15的OLED显示屏能让整机的显示效果更好，外加随OLED屏而来的屏幕指纹功能也让这部价格实惠的5G手机多了几分档次。</p><p><img alt=\"_1093968.jpg\" src=\"http://pic.zaeke.com/img161069212915179.jpeg\" width=\"2000\" height=\"1127\"><br></p><p><img alt=\"_1093939.jpg\" src=\"http://pic.zaeke.com/img161069217436562.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>此外，这部手机里其实还有个大家没太在意的线索，那就是系统里预装了些中国移动的应用。这意味着realme V15或许会与这家国内最大的运营商开展较为深度的合作，并依托运营商数量众多的门店发展线下渠道。所以不出意外的话，相信我们很快就能在全国各地的移动营业厅里见到这部手机。</p><p>做部高性价比的线上机，然后线上线下到处卖，不仅是realme V15给自己定的一个小目标，甚至还有可能是realme V系列在2021年的其中一个重要战略。</p>', '<p class=\"p_style\">经过几天赶工，善于拖稿的我们最终赶在大家还有印象时完成了realme V15的评测。这是部拥有锦鲤色后盖、50W有线快充、6400万后置三摄、6.4英寸AMOLED显示屏、最低176克重量和首发价1399元起的“国潮锦鲤手机”。</p><p class=\"p_style\">该机提供6或8GB内存版本，全系标配128GB存储空间，拥有新月银、镜湖蓝和锦鲤色这三种配色可选。我们收到并用于评测的是8GB内存+128GB存储的锦鲤色，系统为截止发稿前最新的realme UI 1.0 A.23固件。</p><p class=\"jsmargin\"><img alt=\"_1093631.jpg\" src=\"http://pic.zaeke.com/img161069170138757.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"_1093624.jpg\" src=\"http://pic.zaeke.com/img161069162427669.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"_1093966.jpg\" src=\"http://pic.zaeke.com/img161069163981141.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">realme V15的诸多特征之中，“锦鲤色”后盖是重中之重。它由realme与央视《国家宝藏》栏目组合作打造，灵感取自传世名画《洛神赋图》中象征着好运和现代锦鲤原型的“文鱼”，并通过非光谱渐变色彩设计和双纹双镀工艺将其实现。从真机上看，“锦鲤色”可以用“红中带金、金中带蓝”来形容，并且在众机群中也有较高的辨识度。</p><p class=\"p_style\">可较高的辨识度在实际生活中对于realme V15而言并不太友好，因为指纹和掌印在这部以设计见长的玻璃后盖手机上将无处藏身。所以你如果有意购入realme V15，那我们建议戴好标配的透明保护壳。这不仅可以让你端庄又不失优雅的展示这部“锦鲤”，还可以确保你朋友或同事在仔细端详“锦鲤”时，不会都以一句令人尴尬的“哇，这手机这么沾指纹呀”来当做开场白。</p><p class=\"jsmargin\"><img alt=\"591A7711.jpg\" src=\"http://pic.zaeke.com/img161069173571994.jpeg\" width=\"2000\" height=\"1125\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"_1093979.jpg\" src=\"http://pic.zaeke.com/img161069174980790.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"_1093986.jpg\" src=\"http://pic.zaeke.com/img161069198749163.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">在大家比较关心也是这个价格段相对关键的配置上，realme V15基于联发科技天玑800U处理器打造，具有SA/NSA双组网方式、5G双卡双待和双通道网络加速等特征。并配备支持180Hz触控采样率的6.4英寸2400×1080分辨率AMOLED单孔屏，LPDDR4X双通道内存、UFS 2.1存储空间和4310毫安时的电池。</p><p class=\"p_style\">经ZAEKE知客的测试结果显示，realme V15在单卡5G+单卡4G模式并关闭无线网络的9小时待机测试中耗电量为3%。而屏幕亮度约50%，使用无线网络并连接有线耳机，在哔哩哔哩开启弹幕看30分钟1080P视频耗电5%，抖音刷30分钟短视频耗电4%。</p><p class=\"p_style\">同样环境下测试游戏续航表现，《王者荣耀》王者峡谷排位赛2局，首局9分37秒，第二局18分53秒，算上排队、BP英雄和结算接近30分钟，耗电量为20%；《和平精英》海岛地图连续2局约40分钟，耗电量为11%；《天龙八部手游》在燕王古墓5层挂机且屏幕亮度调到最低的情况下，一小时耗电量为7%。</p><p class=\"jsmargin\"><img alt=\"591A7893.jpg\" src=\"http://pic.zaeke.com/img161069180173084.jpeg\" width=\"1400\" height=\"787\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"591A7887.jpg\" src=\"http://pic.zaeke.com/img161069181233320.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">至于充电，realme V15全系标配65W充电器和官方宣称可以在“47分钟充满4310毫安时电池”的50W有线快充。我们的测试结果与官方描述基本一致，realme V15的电量可以在5分钟左右从0%被快速提升至20%，然后在10分钟充到36%，在20分钟充到60%，在30分钟充到80%，在40分钟充到92%，在47分时充满。</p><p class=\"p_style\">将续航和充电结合在一起形象点概述，就是临出门上班前发现手机没电了，等一首歌的时间就可以获得满足路途中刷微博或是看视频需求的电量。如果发现的再早一点，例如起床后就发现没电并开始充的话，那么还有较大可能在出门前获得路途中可以全程打游戏甚至是用大半天的电量。</p><p class=\"jsmargin\"><img alt=\"591A7892-编辑.jpg\" src=\"http://pic.zaeke.com/img161069178055115.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">影像方面，后置由6400万像素(主摄)+800万像素(119°广角)+200万像素(4厘米微距)三摄组成的realme V15基本符合ZAEKE知客对于这个价位手机“鱼和熊掌不可兼得”的预期。相机的启动速度和对焦速度尚可，提供了自动HDR和AI场景增强功能，光线充足的前提下拍风景、拍花草树木、晒个美食或是跟朋友合个影都不会有难以接受的问题出现。</p><p class=\"jsmargin\"><img alt=\"IMG20210115155100.jpg\" src=\"http://pic.zaeke.com/img161069992377971.jpeg\" width=\"1400\" height=\"1880\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG20210115155129.jpg\" src=\"http://pic.zaeke.com/img161069997810157.jpeg\" width=\"1400\" height=\"1880\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG20210115160312.jpg\" src=\"http://pic.zaeke.com/img161070001977497.jpeg\" width=\"1400\" height=\"1043\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG20210115154038.jpg\" src=\"http://pic.zaeke.com/img161070005439873.jpeg\" width=\"1400\" height=\"1043\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG20210115154940.jpg\" src=\"http://pic.zaeke.com/img161070006758000.jpeg\" width=\"1400\" height=\"1880\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG20210115153046.jpg\" src=\"http://pic.zaeke.com/img161069989271926.jpeg\" width=\"1400\" height=\"1043\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG20210115154556.jpg\" src=\"http://pic.zaeke.com/img161070011396326.jpeg\" width=\"1400\" height=\"1043\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG20210115154724.jpg\" src=\"http://pic.zaeke.com/img161070013553096.jpeg\" width=\"1400\" height=\"1880\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG20210115161352.jpg\" src=\"http://pic.zaeke.com/img161070015959678.jpeg\" width=\"1400\" height=\"1880\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">可一旦室内光线较差或是夜幕降临，realme V15的成像质量用高情商说法的话就是“不合眼缘”，低情商说法的话就是“糊成一片”。无论通过常规模式还是夜景模式进行拍摄，大多时候得到的照片都会有“你是在块略脏的玻璃后拍出来的”那种感觉。<br></p><p class=\"jsmargin\"><img alt=\"IMG20210116192640.jpg\" src=\"http://pic.zaeke.com/img161079923893876.jpeg\" width=\"1400\" height=\"1880\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG20210116185331.jpg\" src=\"http://pic.zaeke.com/img161079924739457.jpeg\" width=\"1400\" height=\"1880\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG20210116191243.jpg\" src=\"http://pic.zaeke.com/img161079926599848.jpeg\" width=\"1400\" height=\"1880\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG20210116191321.jpg\" src=\"http://pic.zaeke.com/img161079929510685.jpeg\" width=\"1400\" height=\"1880\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG20210115233458.jpg\" src=\"http://pic.zaeke.com/img161079932979460.jpeg\" width=\"1400\" height=\"1043\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG20210115232943.jpg\" src=\"http://pic.zaeke.com/img161079936133570.jpeg\" width=\"1400\" height=\"1880\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG20210116192857.jpg\" src=\"http://pic.zaeke.com/img161079934917401.jpeg\" width=\"1400\" height=\"1050\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">最后是系统，realme V15搭载了“基于Android 10深度定制的ColorOS 7进行二次定制的realme UI 1.0”，日常需要用到的各种功能可以用“齐全也正常”来形容。例如DC调光、暗色模式、屏幕录制、视频通话美颜和游戏空间等功能都有，全面屏手势也在屏幕支持180Hz触控采样率的前提下非常顺手。<br></p><p class=\"jsmargin\"><img alt=\"未标题-7.jpg\" src=\"http://pic.zaeke.com/img161069417682369.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">并且realme已经承诺会在晚些时候为V15推送全新的realme UI 2.0，让这部手机获得全局主题色、图标自定义、深海隐私保护和电影字幕拼接分享等功能。但那都是后话，目前我们只先介绍两个即刻就能用上且非常推荐开启功能：“闪回键”和“图标下沉”。</p><p class=\"p_style\">闪回键举游戏场景的例子说，就是让你可以在《王者荣耀》里等复活或是《和平精英》游戏开始前放心的切到QQ、微信或其它应用，因为复活倒计时或跳伞倒计时会以小按钮的形式出现在手机左下方(支持自行拖动调整位置)，回完消息后点击闪回键即可切回游戏。</p><p class=\"jsmargin\"><img alt=\"未标题-6.jpg\" src=\"http://pic.zaeke.com/img161069392369037.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">图标下沉则是非常适合“在地铁和公交车上没有座位”以及“只能单手控制手机”的情况下使用。因为它只要沿着屏幕其中一侧向上滑就可以很唤醒，且唤醒后手指不用抬起，只要一直贴着屏幕并移到应用图标上就可以打开应用，可以说是解决了单手模式依旧无法点到桌面第一排图标的问题。</p><p class=\"jsmargin\"><img alt=\"591A7889.jpg\" src=\"http://pic.zaeke.com/img161069189662728.jpeg\" width=\"1400\" height=\"787\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">虽然realme UI总体不错，但写文章的这几天我们也遇到了两个可以轻松复现的问题。首先是横屏游戏时无法通过通知栏切换无线网络，你只能看到它在转圈和空白之间反复循环。但我们打开哔哩哔哩和芒果TV看横屏视频时，通知栏却可以正常显示和切换无线网络。</p><p class=\"p_style\">难道是手机本身游戏相关功能或设置的问题？为此我们尝试了“将游戏移出游戏空间”和“关闭游戏助手”这两种方式，然而结果都是realme V15在《王者荣耀》、《和平精英》、《天龙八部》和《使命召唤手游》这四款手游中无法通过通知栏快捷的切换无线网络。</p><p class=\"jsmargin\"><img alt=\"未标题-5.jpg\" src=\"http://pic.zaeke.com/img161069296722242.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">其次，我们还遇到了暗色模式下悬浮窗应用与主应用难以区分的问题。如下图所示，realme UI在开启暗色模式后诸如计算器、日历和文件管理之类的系统应用都可以顺利的拥抱黑暗，但悬浮窗的那些系统应用也会一起遁入黑暗之中且难以区分，因为悬浮窗应用与主应用之间缺少了一道明确的分界线。</p><p class=\"jsmargin\"><img alt=\"未标题-1.jpg\" src=\"http://pic.zaeke.com/img161070106627603.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">尽管这两个不是会严重影响使用的问题，但我们还是抱着试试的心态，在一番折腾后给找到了“治标不治本”的解决方案。例如在游戏过程中觉得目前网络延迟太高想从2.4G Wi-Fi切换到5G Wi-Fi时，先切换到桌面或其它应用，然后再下拉通知栏即可。再比如保留暗色模式在通知栏的快捷开关，当悬浮窗应用与主应用难以分辨时，下拉通知栏关闭暗色模式即可。</p><p class=\"p_style\">什么？你想知道这俩问题何时才能被“根治”？很遗憾，我们能告诉你的只有“期待realme后续的系统更新吧”。<br></p><p class=\"jsmargin\"><img alt=\"_1093945.jpg\" src=\"http://pic.zaeke.com/img161069220097095.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">显而易见，realme V15在产品定义、配置选择和整个产品方向都有明确目标，那就是另一款同价位、同处理器的竞品。虽然双方都要“过同一个弯”，但realme V15显然跟竞品选择了不同的“入弯线路”。例如在外观设计方面倾注了更多资源，打造出了更有可能吸引少男少女们目光的外观设计。</p><p class=\"p_style\">当然这并不意味着realme V15是部只有外观的手机。在同价位中它的电池容量不落下风，有“独家”的50W快充。并且在与同价位选手们的LCD显示屏比较时，realme V15的OLED显示屏能让整机的显示效果更好，外加随OLED屏而来的屏幕指纹功能也让这部价格实惠的5G手机多了几分档次。</p><p class=\"jsmargin\"><img alt=\"_1093968.jpg\" src=\"http://pic.zaeke.com/img161069212915179.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"_1093939.jpg\" src=\"http://pic.zaeke.com/img161069217436562.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">此外，这部手机里其实还有个大家没太在意的线索，那就是系统里预装了些中国移动的应用。这意味着realme V15或许会与这家国内最大的运营商开展较为深度的合作，并依托运营商数量众多的门店发展线下渠道。所以不出意外的话，相信我们很快就能在全国各地的移动营业厅里见到这部手机。</p><p class=\"p_style\">做部高性价比的线上机，然后线上线下到处卖，不仅是realme V15给自己定的一个小目标，甚至还有可能是realme V系列在2021年的其中一个重要战略。</p>', 'http://pic.zaeke.com/img161119855380119.jpeg', 7895, 0, 0, NULL, '2021-01-17 18:59:39', '2021-03-27 02:30:41');
INSERT INTO `article` VALUES (15949, '率先冲刺的 iQOO 7，时刻保持着强烈的求“胜”欲', '在大部分手机品牌都在朝着高端破局之路前行时，总是希望能减少品牌身上的标签束缚。可是，作为新兴品牌的iQOO，却主动地把“生而强悍”这样的标签贴在身上，给人感觉看...', '[object Object]', '小啤', 170, 'http://pic.zaeke.com/user_1533281478_8.png', '<p>在大部分手机品牌都在朝着高端破局之路前行时，总是希望能减少品牌身上的标签束缚。可是，作为新兴品牌的 iQOO，却主动地把“生而强悍”这样的标签贴在身上，给人感觉看似走上了一条追求极致性能的不归路，可在我眼里，它却是一张让 iQOO 快速入局手机“血海”市场的一张无形入场券。</p><p style=\"text-align: center;\"><b>保持强悍，是 iQOO 7 的基本功</b></p><p>既然 iQOO 把性能表现视为品牌机型的立足之本，这次 iQOO 7 自然也开启了一贯的堆料操作，而处理器被视为手机性能的核心关键，iQOO 7 自然不甘落后。身为市面上第二款正式发布的骁龙 888 移动平台机型，iQOO 7 已经比绝大部分新一代年度旗舰都要来得更早一些。</p><p><img alt=\"591A7689.jpg\" src=\"http://pic.zaeke.com/img161105050652374.jpeg\" width=\"1400\" height=\"788\"><br></p><p>骁龙 888 移动平台作为高通旗下最新的旗舰级产品，据称其 CPU 部分与上一代产品相比有着 25% 的提升，GPU 的性能提升幅度达到 35%，再加上它是高通首款 5nm 制程工艺 5G 芯片，因此不少人对这款 SoC 的性能抱有较高的期待。上述账面数据看起来提升还是很显著，可是能在实际的体验中带来多少提升，则需要通过一些对硬件要求较高的大型游戏去充当性能“试金石”。</p><p><img alt=\"和平精英.jpg\" src=\"http://pic.zaeke.com/img161105018237728.jpeg\" width=\"1077\" height=\"764\"><br></p><p>在室温为 18℃ 的室内，通过使用开启“Monster”模式之后的 iQOO 7 上手《和平精英》、《使命召唤手游》、《原神》三款大型游戏，可以发现 iQOO 7 在面对《和平精英》的 HDR 高清画质、极限帧数、开启抗锯齿设置之下，其平均帧数可以达到几乎满帧的 59.5 帧，游戏过程中基本感受不到画面卡顿感。如果在超高清画质、超高帧数、开启抗锯齿设置之下，其帧数也能带来 39.8 帧的表现，如果在游戏不进行 40 帧数的强行锁帧干预情况之下，相信 iQOO 7 在该画质下的帧数还能进一步提高。</p><p><img alt=\"使命召唤手游.jpg\" src=\"http://pic.zaeke.com/img161105019270466.jpeg\" width=\"1073\" height=\"772\"><br></p><p>同样，上手最近人气较高的《使命召唤手游》时，在精致画质、极限帧数设置之下，iQOO 7 可以带来平均 59.6 的帧数表现，由于这款游戏本身对于硬件的要求比《和平精英》略低，所以帧数表现更高也是意料之内的事。当然，该游戏还可以设置到更高的画质，可是游戏本身还是强行进行一定的锁帧操作，无法完全发挥出骁龙 888 的极限性能。</p><p><img alt=\"591A7663.jpg\" src=\"http://pic.zaeke.com/img161105015382263.jpeg\" width=\"1400\" height=\"788\"><br></p><p>面对闻风丧胆的性能克星《原神》时，即便在画面调至极限画质、帧数设置为 60 帧，经过 40 分钟以上的上手之后，取得了平均帧数 46.1 帧。实际上，iQOO 虽然并没有自诩“游戏手机”这样的定位，可是作为 KPL 官方比赛用机的 iQOO 7，却具备了很多针对游戏而提升的软硬亮点，在硬件上其具备 120Hz 高刷+ 300Hz 触控报点率（1000Hz 瞬时触控采样率）、双路线性马达、立体双扬声器、侧边天线全模覆盖等，丝毫不逊色于部分游戏向手机产品；在软件层面，它可实现 4D 游戏震感、GVoice 游戏语音优化、屏下双控压感等功能，上述功能亮点无疑都是朝着打造更佳的游戏体验而生，从根本提升该机在游戏领域的上手体验。</p><p><img alt=\"1244.jpg\" src=\"http://pic.zaeke.com/img161105047863567.jpeg\" width=\"1330\" height=\"260\"><br></p><p>在 iQOO 7 的发布会上，他们用了一定的篇幅去描述散热系统，通过内部配备的 4096 平⽅毫⽶的均热板、螺旋编织丝⽹结构等方式，尽可能压制骁龙 888 移动平台的发热问题，至于散热表现的最终成效，已经在上述游戏测试环节中可以得出答案。与此同时，通过测温仪的数据可以发现，在室温 19.8℃ 的环境下，测得 iQOO 7 在 40 分钟以上的《原神》游戏测试中，机身正面取得的最高表温为 43.3℃，位置处于机身左侧，而机身背面取得的最高温度为 42.3 ℃，区域位于后置三摄下方。</p><p>上述提及的“Monster”模式，其实是 iQOO 7 针对用户不同使用需求而划分的一种性能模式，其中“省电”模式顾名思义就是降低各元器件的耗电量，实现续航的尽可能延长；“均衡模式”则是在极致性能与功耗之间尽可能取得平衡，尽管在游戏过程中它几乎也不针对处理器进行锁频；至于“Monster 模式”，则是在完全不考虑续航焦虑情况下，把性能尽可能发挥到极致。在大部分情况下，其实建议首选“均衡模式”即可。</p><p><img alt=\"跑分成绩.jpg\" src=\"http://pic.zaeke.com/img161105052923334.jpeg\" width=\"1527\" height=\"892\"><br></p><p>想要实现足够稳定、流畅的上手体验，仅依靠旗舰级 SoC “单枪匹马”是远远不够的，旗舰级机型在存储性能方面自然也不能拖了后腿。在存储性能方面，iQOO 7 采用了 LPDDR5 + UFS 3.1 规格存储组合，iQOO 7 在 AndroBench 存储跑分软件中，取得了顺序读取速度 1934.35MB/s、顺序写入速度 781.8MB / s、随机读取速度 298.05MB / s、随机写入速度 276.88MB / s 的成绩，综合跑分表现不俗。<br></p><p>不仅拥有最新旗舰 SoC 、高性能存储组合加持，iQOO 7 还有一项极具吸引力的硬核特长 —— 120W&nbsp;FlashCharge 超快闪充。</p><p><img alt=\"iQOO 7 充电速度.jpg\" src=\"http://pic.zaeke.com/img161105056845594.jpeg\" width=\"1462\" height=\"728\"><br></p><p>在室温大约 20 ℃ 的环境下，iQOO 7 的机身电量从零充满至 100%，一共耗时 15 分钟 16 秒，通过实测发现，该机在完全无电状态下，仅需要充电 2 分钟就能把电量补充至 13%，4 分钟之后电量为33%，6 分钟之后电量为 52%，8 分钟电量为 65%，10 分钟之后电量为 79%，12 分钟之后电量为 89%，14 分钟之后电量为 96%，15 分钟 16 秒之后电量为 100%，通过每相隔 2 分钟的充电数据不难看出，iQOO 7 不仅充电速度迅速，而且补电节奏相对匀速。<br></p><p>在充电测试过程中，iQOO 7 和 120W&nbsp;FlashCharge 超快闪充充电器的表温仅为温热状态，这是非常了不起的充电新体验。这并不是 vivo 施展了魔法，其实是得益于 vivo 采用了创新性的“MTW 阵列式极耳结构”，通过双电芯的多次并联，实现了大幅度降低了电芯阻抗，使充电转化高达 98.5%，也从根本有效降低电池发热情况。当然，120W&nbsp;FlashCharge 超快闪充充电器也存在充电头体积偏大，且重量偏重的情况，一定程度上降低了便携性。</p><p><img alt=\"IMG_0377.jpg\" src=\"http://pic.zaeke.com/img161105059372224.jpeg\" width=\"1400\" height=\"933\"><br></p><p>除此，iQOO 7 由于本身电池容量仅有 4000 毫安时，因此在续航能力方面，相较于其它采用更大电池容量的手机会略逊色一些。即便只搭载了 4000 毫安时机身电池，iQOO 7 也并没有带来相对轻薄的机身，其机身厚度为 8.7mm，而机身净重量为 209.5 克。至于握感，得益于这款机型采用了先对贴合掌心的弧面后盖、合理的机身配重、AG磨砂玻璃后盖（传奇版），使其带来了不俗的机身握感。</p><p style=\"text-align: center;\"><b>除了性能，新鲜感也是 iQOO 7 的一大竞争力</b></p><p>旗舰级机型的硬件其实很容易就触碰到天花板，毕竟堆料整合对于一线手机厂商而言早已驾轻就熟，可是如何增加消费者新鲜感，反而是一件更加需要绞尽脑汁的事情。</p><p><img alt=\"img161036853027501.jpg\" src=\"http://pic.zaeke.com/img161105065230424.jpeg\" width=\"1919\" height=\"1079\"><br></p><p>从外观设计，iQOO 7 就做到了三种机身配每款均赋予了独特的后盖设计，较大程度地满足不同消费群体的审美喜好。只是，由于前置摄像头特意加了银边点缀，并且采用了中置挖孔布局，因此在息屏状态下该前置摄像头略显突兀。可是胜在 iQOO 7 的前置摄像头开孔的孔径偏小，而且摄像头摆放位置精准，没有丝毫偏移情况。</p><p><img alt=\"591A7678.jpg\" src=\"http://pic.zaeke.com/img161105074663724.jpeg\" width=\"1400\" height=\"788\"><br></p><p>其实相比较于硬件，iQOO 7 软体验部分才是给我带来新鲜感的源泉。其中，由 vivo 全新打造的 OriginOS 系统本身就充满了浓厚的“新”气息，除了通过独特的“原子组件”实现了新颖的桌面布局方式以外，大量采用的拟物化图标，使得它在扁平化时代显得格外耀眼。不过，“原子组件”仅针对部分原生软件进行个性化设计，第三方软件无法自由拉伸布局方式，而且我试过误删部分“原子组件”，却找不到恢复渠道。</p><p><img alt=\"FLIR2948_副本.jpg\" src=\"http://pic.zaeke.com/img161105070612853.jpeg\" width=\"4370\" height=\"2420\"><br></p><p><img alt=\"IMG_0375.jpg\" src=\"http://pic.zaeke.com/img161105075929138.jpeg\" width=\"1400\" height=\"933\"><br></p><p>为满足不同用户的视觉审美，OriginOS 系统也提供了单色、模糊、天空、时光共四种选项，丰富了用户的视觉呈现，上述四种选项也能通过组合方式进行效果叠加。当然，为满足喜欢守旧的用户群体，OriginOS 系统也提供了一键切换新/旧界面的“变形器”。<br></p><p><img alt=\"FLIR2948_副本4.jpg\" src=\"http://pic.zaeke.com/img161105087765257.jpeg\" width=\"4370\" height=\"2420\"><br></p><p>除了系统的外观设计以外，OriginOS 在交互方面也下了不少功夫，例如无论在什么软件下，直接从屏幕左下角可以唤出应用抽屉，长按任意软件拖拽至屏幕之后，即可实现同屏多任务操作。不仅如此，为了提高用户的移动支付效率，在锁屏、主页、程序界面下，均可以通过右下侧手势实现移动支付中心、细分相机摄像头卡片进行快速唤出。另外，通过“交互池”特性，可以实现超二十种不同手势操作组合，给予用户更自由、灵活的手势操作定制权。</p><p><img alt=\"IMG_0371.jpg\" src=\"http://pic.zaeke.com/img161105723255441.jpeg\" width=\"1400\" height=\"933\"><br></p><p>让我耳目一新的地方，还有 OriginOS 在 iQOO 7 所呈现的顺滑动画过渡效果，这种慢条斯理的动画质感可以说是恰到好处，并不是那种生硬的“傻快”。包括进行一些软件的滑动操作时，iQOO 7 的系统交互也能带来比较平顺的触控体验，这种触控质感就像在上手了一台具备高刷新特性的 iOS 设备。</p><p><img alt=\"1610941320(1).jpg\" src=\"http://pic.zaeke.com/img161105115530788.jpeg\" width=\"1300\" height=\"731\"></p><p><img alt=\"1610960818(1).jpg\" src=\"http://pic.zaeke.com/img161105251839762.jpeg\" width=\"1300\" height=\"731\"><br></p><p>手机的交互体验固然重要，可是打通生态横沟也是未来手机的发展新趋势，电脑与手机是我们生活中最常见的电子设备，通过丰富两款设备之间联动性，给予用户更高效的操作体验。iQOO 7 通过“互传”APP 的多屏互动特性，使手机与电脑之间通过 Wi-Fi 局域网实现投屏显示，虽然其它加入了“互传”联盟的厂商机型也能实现基本的屏幕投影，可是它们并无法像 OriginOS 那样做到相对细致的电脑端远程操作体验，例如 OriginOS 在游戏中提供了键盘触控映射功能，直接采用鼠标和键盘操作 iQOO 7 的各种远程操作，如果电脑端屏幕本身支持触控，那么用户可直接通过电脑端实现类似的触控操作。<br></p><p><img alt=\"1610962077(1).jpg\" src=\"http://pic.zaeke.com/img161105118227240.jpeg\" width=\"1300\" height=\"731\"><br></p><p>不过，在 ZAEKE知客工作室的网络环境下，iQOO 7 与电脑端如果采用 1080P 分辨率画质进行传输操作，偶尔会出现画面传输卡顿情况，如果把画面设置为 720P 或者 480P 之后，整体画面流畅性会有所提高。多屏互动也许不适合上手一些对操作反应要求较高的游戏，日常刷一刷微博、看一看短视频，何况手机与电脑之间能自由复制和剪切文字内容，对于办公需求的人群是非常友好的，如果可以针对键盘组合键进行兼容整合，那么对于其上手效果的提升和入手门槛的降低都有所帮助。</p><p style=\"text-align: center;\"><b>iQOO 7 继承了 vivo 优秀的影像基因</b></p><p>在千元机都开始追求影像表现的时代，旗舰级机型的拍照表现自然不能忽视。主打全局影像创作的 iQOO 7，分别配备了 4800 万像素广角主摄 + 1300 万像素超广角镜头 + 1300 万像素人像镜头，面对部分以像素论成败的消费者，也许会觉得 iQOO 7 的 4800 万像素广角主摄跟不上时代步伐，实际上这是一颗由索尼定制的 IMX598 传感器，其在 vivo X50 系列产品上取得了不俗的口碑。</p><p><img alt=\"IMG_20210115_143432.jpg\" src=\"http://pic.zaeke.com/img161105547538602.jpeg\" width=\"1300\" height=\"975\"><br></p><p><img alt=\"IMG_20210115_144828.jpg\" src=\"http://pic.zaeke.com/img161105550458547.jpeg\" width=\"1300\" height=\"975\"></p><p><img alt=\"IMG_20210115_144907.jpg\" src=\"http://pic.zaeke.com/img161105550477622.jpeg\" width=\"1300\" height=\"975\"><br></p><p><img alt=\"IMG_20210115_150153.jpg\" src=\"http://pic.zaeke.com/img161105557734811.jpeg\" width=\"1300\" height=\"975\"><br></p><p><img alt=\"IMG_20210115_172324.jpg\" src=\"http://pic.zaeke.com/img161105560129639.jpeg\" width=\"1300\" height=\"975\"></p><p><img alt=\"IMG_20210115_191523.jpg\" src=\"http://pic.zaeke.com/img161105560273022.jpeg\" width=\"1300\" height=\"975\"></p><p><img alt=\"IMG_20210116_191803.jpg\" src=\"http://pic.zaeke.com/img161105560225869.jpeg\" width=\"1300\" height=\"975\"></p><p><img alt=\"IMG_20210116_224643.jpg\" src=\"http://pic.zaeke.com/img161105567391009.jpeg\" width=\"1300\" height=\"975\"><br></p><p><img alt=\"IMG_20210118_184549.jpg\" src=\"http://pic.zaeke.com/img161105563384984.jpeg\" width=\"1300\" height=\"975\"></p><p><img alt=\"IMG_20210118_213957.jpg\" src=\"http://pic.zaeke.com/img161105563383824.jpeg\" width=\"1300\" height=\"975\"><br></p><p><img alt=\"IMG_20210114_195949.jpg\" src=\"http://pic.zaeke.com/img161105539596878.jpeg\" width=\"1300\" height=\"975\"></p><p><img alt=\"IMG_20210115_140140.jpg\" src=\"http://pic.zaeke.com/img161105539558948.jpeg\" width=\"1300\" height=\"975\"></p><p><img alt=\"IMG_20210115_143004.jpg\" src=\"http://pic.zaeke.com/img161105540987677.jpeg\" width=\"1300\" height=\"975\"><br></p><p><img alt=\"IMG_20210115_143113.jpg\" src=\"http://pic.zaeke.com/img161105541267442.jpeg\" width=\"1300\" height=\"975\"><br></p><p><img alt=\"IMG_20210118_212337_副本.jpg\" src=\"http://pic.zaeke.com/img161105534355916.jpeg\" width=\"4030\" height=\"1520\"></p><p><img alt=\"IMG_20210118_212337_副4本.jpg\" src=\"http://pic.zaeke.com/img161105534446606.jpeg\" width=\"2630\" height=\"995\"></p><p><img alt=\"IMG_20210118_2123537_副本.jpg\" src=\"http://pic.zaeke.com/img161105534365975.jpeg\" width=\"2630\" height=\"995\"><br></p><p>实际上，iQOO 7 这颗 4800 万像素广角主摄在弱光环境下的表现非常出众，这要得益于 f/1.6 大光圈、O.I.S 防抖、四合一成 1.6μm 像素尺寸、定制滤光片等特性，使这颗摄像头具备更大进光量和更高信噪比，因此该摄像头在弱光环境下表现尤为出众，再加上高通骁龙 888 移动平台凭借其最新的 Spectra 580 ISP，实现了比上一代产品 35% 的性能提升，进一步提升了 IMX598 传感器的弱光成像效率。可由于该主摄默认输出像素为 1200 万，因此不利于后期裁剪的应用，而且考虑到成像速度，因此常规夜景模式仅适合拍摄静态景色，除非用户想要通过降低快门获得一些特殊的光轨效果。</p><p><img alt=\"IMG_20210115_143102.jpg\" src=\"http://pic.zaeke.com/img161105570837925.jpeg\" width=\"1300\" height=\"975\"></p><p><img alt=\"IMG_20210115_143343.jpg\" src=\"http://pic.zaeke.com/img161105570854100.jpeg\" width=\"1300\" height=\"975\"></p><p><img alt=\"IMG_20210115_143448.jpg\" src=\"http://pic.zaeke.com/img161105574255038.jpeg\" width=\"1300\" height=\"975\"></p><p><img alt=\"IMG_20210115_144546.jpg\" src=\"http://pic.zaeke.com/img161105574415077.jpeg\" width=\"1300\" height=\"975\"></p><p><img alt=\"IMG_20210115_144811.jpg\" src=\"http://pic.zaeke.com/img161105574733453.jpeg\" width=\"1300\" height=\"975\"></p><p><img alt=\"IMG_20210115_145830.jpg\" src=\"http://pic.zaeke.com/img161105576686267.jpeg\" width=\"1300\" height=\"975\"></p><p><img alt=\"IMG_20210115_145929.jpg\" src=\"http://pic.zaeke.com/img161105576825478.jpeg\" width=\"1300\" height=\"975\"></p><p><img alt=\"IMG_20210115_154937.jpg\" src=\"http://pic.zaeke.com/img161105577213433.jpeg\" width=\"1300\" height=\"975\"></p><p><img alt=\"IMG_20210115_191527.jpg\" src=\"http://pic.zaeke.com/img161105577950503.jpeg\" width=\"1300\" height=\"975\"><br></p><p><img alt=\"IMG_20210115_143319.jpg\" src=\"http://pic.zaeke.com/img161105570852392.jpeg\" width=\"975\" height=\"1300\"><br></p><p>相比较于主摄，iQOO 7 的 1300 万像素超广角镜头的综合画质肯定要略逊一筹，不过这款摄像头胜在足够广，120° 的超广角特性，使其尽可能把画面尽收眼底，再加上这颗摄像头本身可以实现 2.5cm 最近拍摄距离，因此可起到微距拍摄效果，真正实现了远近皆从容的效果。</p><p><img alt=\"IMG_20210115_122236.jpg\" src=\"http://pic.zaeke.com/img161105666780167.jpeg\" width=\"1300\" height=\"975\"><br></p><p><img alt=\"IMG_20210118_215523.jpg\" src=\"http://pic.zaeke.com/img161105697066390.jpeg\" width=\"1300\" height=\"975\"><br></p><p><img alt=\"IMG_20210118_213952.jpg\" src=\"http://pic.zaeke.com/img161105704943964.jpeg\" width=\"1300\" height=\"975\"><br></p><p><img alt=\"IMG_20210118_214435.jpg\" src=\"http://pic.zaeke.com/img161105678865724.jpeg\" width=\"1300\" height=\"975\"><br></p><p><img alt=\"IMG_20210118_214539.jpg\" src=\"http://pic.zaeke.com/img161105679467366.jpeg\" width=\"1300\" height=\"975\"><br></p><p><img alt=\"IMG_20210118_214219.jpg\" src=\"http://pic.zaeke.com/img161105676876603.jpeg\" width=\"1300\" height=\"975\"><br></p><p><img alt=\"IMG_20210118_213909.jpg\" src=\"http://pic.zaeke.com/img161105611126544.jpeg\" width=\"1300\" height=\"975\"><br></p><p>为了进一步突出被摄主体，iQOO 7 还配备了一颗为 50mm 焦段标准镜头，个人主观感觉这颗摄像头的整体解析力还不错，特别在光线足够充沛的环境下，其与主摄之间并没有拉开太大的成像差距，但是在弱光环境下的表现就显得比较悬殊了。总体而言，iQOO7 的后置三颗可谓是各司其职，每一颗都有它存在的必要性、</p><p>只是相比较于普通手机，想要完全摸透 iQOO 7 的相机功能需花费更多的上手时间，例如“超清像素位移”并非所有焦段下都能触发；常规“相机”界面之下的人像镜头并不一定介入工作，系统会“智能”地根据场景光线自动切换镜头，想要强制采用人像镜头拍摄时，只能手动进入“人像”模式进行拍摄操作；“微距”模式下的开关为二级菜单，每次开关闭至少需要两个步骤，不过用户可开启自动模式，无需每次频繁开/关微距模式。</p><p style=\"text-align: center;\"><b>是性能旗舰，也是新一代“价格屠夫”</b></p><p>iQOO 7 的强悍性能是从“芯”展开的，拥有高通骁龙 888 移动平台加持的它，配合 6.62 英寸 120Hz 屏幕、 LPDDR5 + UFS 3.1 存储组合，为机型的极致流畅交互体验提供了扎实的硬件基础。搭载“新鲜出炉”的 OriginOS 系统，该机不仅带来了足够自然流畅的丰富交互方式，还赋予了整机新颖的更多上手体验，例如交互池、超级卡包、变形器、原子组件等独特功能。所以，相比较于一成不变，也许搭载了 OriginOS 系统的 iQOO 7 反而是渴望打破平庸刻板的用户群体新选择。</p><p><img alt=\"IMG_0386.jpg\" src=\"http://pic.zaeke.com/img161105713922264.jpeg\" width=\"1400\" height=\"933\"><br></p><p>在续航能力方面，iQOO 7 也是一台相当激进的机型，因为虽然它搭载了硬核十足的 120W&nbsp;FlashCharge 超快闪充，可作为一款 5G 旗舰机型，它的电池容量却仅有 4000mAh。实际上，按照我的实测可以发现，iQOO 7 配合 120W&nbsp;FlashCharge 超快闪充，可以在 15 分钟左右实现从无电量充至满电状态，这样的的充电速度确实快得惊人，可是不能否认 4000mAh 的电池容量会对这款机型的单次续航能力带来难以掩盖的短板。</p><p><img alt=\"IMG_0385.jpg\" src=\"http://pic.zaeke.com/img161105717123299.jpeg\" width=\"1400\" height=\"933\"><br></p><p>在定价方面，这次 iQOO 7 一如既往地带来了 iQOO 式的“惊喜价”，8GB + 128GB 存储组合售价 3798元，而 12GB + 256GB 售价 4198 元，尽管两个配置的差价仅有 400 元，可是标准版凭借 3798 元定价，却较大程度地降低了潜在消费者入手首批骁龙 888 移动平台机型的门槛。</p><p>对于一个成立不足两周年的新锐手机品牌，iQOO 7 已经称得上是 iQOO 目前最耀眼的一张“成绩单”，背后除了拥有 vivo 的鼎力相助，更多的是他们非常清晰自身在市场上的定位，并且朝着通一个方向砥砺前行，使其在竞争激烈的手机市场上有了立足之本。</p>', '<p class=\"p_style\">在大部分手机品牌都在朝着高端破局之路前行时，总是希望能减少品牌身上的标签束缚。可是，作为新兴品牌的 iQOO，却主动地把“生而强悍”这样的标签贴在身上，给人感觉看似走上了一条追求极致性能的不归路，可在我眼里，它却是一张让 iQOO 快速入局手机“血海”市场的一张无形入场券。</p><p style=\"text-align: center;\" class=\"p_style\"><b>保持强悍，是 iQOO 7 的基本功</b></p><p class=\"p_style\">既然 iQOO 把性能表现视为品牌机型的立足之本，这次 iQOO 7 自然也开启了一贯的堆料操作，而处理器被视为手机性能的核心关键，iQOO 7 自然不甘落后。身为市面上第二款正式发布的骁龙 888 移动平台机型，iQOO 7 已经比绝大部分新一代年度旗舰都要来得更早一些。</p><p class=\"jsmargin\"><img alt=\"591A7689.jpg\" src=\"http://pic.zaeke.com/img161105050652374.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">骁龙 888 移动平台作为高通旗下最新的旗舰级产品，据称其 CPU 部分与上一代产品相比有着 25% 的提升，GPU 的性能提升幅度达到 35%，再加上它是高通首款 5nm 制程工艺 5G 芯片，因此不少人对这款 SoC 的性能抱有较高的期待。上述账面数据看起来提升还是很显著，可是能在实际的体验中带来多少提升，则需要通过一些对硬件要求较高的大型游戏去充当性能“试金石”。</p><p class=\"jsmargin\"><img alt=\"和平精英.jpg\" src=\"http://pic.zaeke.com/img161105018237728.jpeg\" width=\"1077\" height=\"764\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">在室温为 18℃ 的室内，通过使用开启“Monster”模式之后的 iQOO 7 上手《和平精英》、《使命召唤手游》、《原神》三款大型游戏，可以发现 iQOO 7 在面对《和平精英》的 HDR 高清画质、极限帧数、开启抗锯齿设置之下，其平均帧数可以达到几乎满帧的 59.5 帧，游戏过程中基本感受不到画面卡顿感。如果在超高清画质、超高帧数、开启抗锯齿设置之下，其帧数也能带来 39.8 帧的表现，如果在游戏不进行 40 帧数的强行锁帧干预情况之下，相信 iQOO 7 在该画质下的帧数还能进一步提高。</p><p class=\"jsmargin\"><img alt=\"使命召唤手游.jpg\" src=\"http://pic.zaeke.com/img161105019270466.jpeg\" width=\"1073\" height=\"772\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">同样，上手最近人气较高的《使命召唤手游》时，在精致画质、极限帧数设置之下，iQOO 7 可以带来平均 59.6 的帧数表现，由于这款游戏本身对于硬件的要求比《和平精英》略低，所以帧数表现更高也是意料之内的事。当然，该游戏还可以设置到更高的画质，可是游戏本身还是强行进行一定的锁帧操作，无法完全发挥出骁龙 888 的极限性能。</p><p class=\"jsmargin\"><img alt=\"591A7663.jpg\" src=\"http://pic.zaeke.com/img161105015382263.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">面对闻风丧胆的性能克星《原神》时，即便在画面调至极限画质、帧数设置为 60 帧，经过 40 分钟以上的上手之后，取得了平均帧数 46.1 帧。实际上，iQOO 虽然并没有自诩“游戏手机”这样的定位，可是作为 KPL 官方比赛用机的 iQOO 7，却具备了很多针对游戏而提升的软硬亮点，在硬件上其具备 120Hz 高刷+ 300Hz 触控报点率（1000Hz 瞬时触控采样率）、双路线性马达、立体双扬声器、侧边天线全模覆盖等，丝毫不逊色于部分游戏向手机产品；在软件层面，它可实现 4D 游戏震感、GVoice 游戏语音优化、屏下双控压感等功能，上述功能亮点无疑都是朝着打造更佳的游戏体验而生，从根本提升该机在游戏领域的上手体验。</p><p class=\"jsmargin\"><img alt=\"1244.jpg\" src=\"http://pic.zaeke.com/img161105047863567.jpeg\" width=\"1330\" height=\"260\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">在 iQOO 7 的发布会上，他们用了一定的篇幅去描述散热系统，通过内部配备的 4096 平⽅毫⽶的均热板、螺旋编织丝⽹结构等方式，尽可能压制骁龙 888 移动平台的发热问题，至于散热表现的最终成效，已经在上述游戏测试环节中可以得出答案。与此同时，通过测温仪的数据可以发现，在室温 19.8℃ 的环境下，测得 iQOO 7 在 40 分钟以上的《原神》游戏测试中，机身正面取得的最高表温为 43.3℃，位置处于机身左侧，而机身背面取得的最高温度为 42.3 ℃，区域位于后置三摄下方。</p><p class=\"p_style\">上述提及的“Monster”模式，其实是 iQOO 7 针对用户不同使用需求而划分的一种性能模式，其中“省电”模式顾名思义就是降低各元器件的耗电量，实现续航的尽可能延长；“均衡模式”则是在极致性能与功耗之间尽可能取得平衡，尽管在游戏过程中它几乎也不针对处理器进行锁频；至于“Monster 模式”，则是在完全不考虑续航焦虑情况下，把性能尽可能发挥到极致。在大部分情况下，其实建议首选“均衡模式”即可。</p><p class=\"jsmargin\"><img alt=\"跑分成绩.jpg\" src=\"http://pic.zaeke.com/img161105052923334.jpeg\" width=\"1527\" height=\"892\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">想要实现足够稳定、流畅的上手体验，仅依靠旗舰级 SoC “单枪匹马”是远远不够的，旗舰级机型在存储性能方面自然也不能拖了后腿。在存储性能方面，iQOO 7 采用了 LPDDR5 + UFS 3.1 规格存储组合，iQOO 7 在 AndroBench 存储跑分软件中，取得了顺序读取速度 1934.35MB/s、顺序写入速度 781.8MB / s、随机读取速度 298.05MB / s、随机写入速度 276.88MB / s 的成绩，综合跑分表现不俗。<br></p><p class=\"p_style\">不仅拥有最新旗舰 SoC 、高性能存储组合加持，iQOO 7 还有一项极具吸引力的硬核特长 —— 120W&nbsp;FlashCharge 超快闪充。</p><p class=\"jsmargin\"><img alt=\"iQOO 7 充电速度.jpg\" src=\"http://pic.zaeke.com/img161105056845594.jpeg\" width=\"1462\" height=\"728\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">在室温大约 20 ℃ 的环境下，iQOO 7 的机身电量从零充满至 100%，一共耗时 15 分钟 16 秒，通过实测发现，该机在完全无电状态下，仅需要充电 2 分钟就能把电量补充至 13%，4 分钟之后电量为33%，6 分钟之后电量为 52%，8 分钟电量为 65%，10 分钟之后电量为 79%，12 分钟之后电量为 89%，14 分钟之后电量为 96%，15 分钟 16 秒之后电量为 100%，通过每相隔 2 分钟的充电数据不难看出，iQOO 7 不仅充电速度迅速，而且补电节奏相对匀速。<br></p><p class=\"p_style\">在充电测试过程中，iQOO 7 和 120W&nbsp;FlashCharge 超快闪充充电器的表温仅为温热状态，这是非常了不起的充电新体验。这并不是 vivo 施展了魔法，其实是得益于 vivo 采用了创新性的“MTW 阵列式极耳结构”，通过双电芯的多次并联，实现了大幅度降低了电芯阻抗，使充电转化高达 98.5%，也从根本有效降低电池发热情况。当然，120W&nbsp;FlashCharge 超快闪充充电器也存在充电头体积偏大，且重量偏重的情况，一定程度上降低了便携性。</p><p class=\"jsmargin\"><img alt=\"IMG_0377.jpg\" src=\"http://pic.zaeke.com/img161105059372224.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">除此，iQOO 7 由于本身电池容量仅有 4000 毫安时，因此在续航能力方面，相较于其它采用更大电池容量的手机会略逊色一些。即便只搭载了 4000 毫安时机身电池，iQOO 7 也并没有带来相对轻薄的机身，其机身厚度为 8.7mm，而机身净重量为 209.5 克。至于握感，得益于这款机型采用了先对贴合掌心的弧面后盖、合理的机身配重、AG磨砂玻璃后盖（传奇版），使其带来了不俗的机身握感。</p><p style=\"text-align: center;\" class=\"p_style\"><b>除了性能，新鲜感也是 iQOO 7 的一大竞争力</b></p><p class=\"p_style\">旗舰级机型的硬件其实很容易就触碰到天花板，毕竟堆料整合对于一线手机厂商而言早已驾轻就熟，可是如何增加消费者新鲜感，反而是一件更加需要绞尽脑汁的事情。</p><p class=\"jsmargin\"><img alt=\"img161036853027501.jpg\" src=\"http://pic.zaeke.com/img161105065230424.jpeg\" width=\"1919\" height=\"1079\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">从外观设计，iQOO 7 就做到了三种机身配每款均赋予了独特的后盖设计，较大程度地满足不同消费群体的审美喜好。只是，由于前置摄像头特意加了银边点缀，并且采用了中置挖孔布局，因此在息屏状态下该前置摄像头略显突兀。可是胜在 iQOO 7 的前置摄像头开孔的孔径偏小，而且摄像头摆放位置精准，没有丝毫偏移情况。</p><p class=\"jsmargin\"><img alt=\"591A7678.jpg\" src=\"http://pic.zaeke.com/img161105074663724.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">其实相比较于硬件，iQOO 7 软体验部分才是给我带来新鲜感的源泉。其中，由 vivo 全新打造的 OriginOS 系统本身就充满了浓厚的“新”气息，除了通过独特的“原子组件”实现了新颖的桌面布局方式以外，大量采用的拟物化图标，使得它在扁平化时代显得格外耀眼。不过，“原子组件”仅针对部分原生软件进行个性化设计，第三方软件无法自由拉伸布局方式，而且我试过误删部分“原子组件”，却找不到恢复渠道。</p><p class=\"jsmargin\"><img alt=\"FLIR2948_副本.jpg\" src=\"http://pic.zaeke.com/img161105070612853.jpeg\" width=\"4370\" height=\"2420\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_0375.jpg\" src=\"http://pic.zaeke.com/img161105075929138.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">为满足不同用户的视觉审美，OriginOS 系统也提供了单色、模糊、天空、时光共四种选项，丰富了用户的视觉呈现，上述四种选项也能通过组合方式进行效果叠加。当然，为满足喜欢守旧的用户群体，OriginOS 系统也提供了一键切换新/旧界面的“变形器”。<br></p><p class=\"jsmargin\"><img alt=\"FLIR2948_副本4.jpg\" src=\"http://pic.zaeke.com/img161105087765257.jpeg\" width=\"4370\" height=\"2420\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">除了系统的外观设计以外，OriginOS 在交互方面也下了不少功夫，例如无论在什么软件下，直接从屏幕左下角可以唤出应用抽屉，长按任意软件拖拽至屏幕之后，即可实现同屏多任务操作。不仅如此，为了提高用户的移动支付效率，在锁屏、主页、程序界面下，均可以通过右下侧手势实现移动支付中心、细分相机摄像头卡片进行快速唤出。另外，通过“交互池”特性，可以实现超二十种不同手势操作组合，给予用户更自由、灵活的手势操作定制权。</p><p class=\"jsmargin\"><img alt=\"IMG_0371.jpg\" src=\"http://pic.zaeke.com/img161105723255441.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">让我耳目一新的地方，还有 OriginOS 在 iQOO 7 所呈现的顺滑动画过渡效果，这种慢条斯理的动画质感可以说是恰到好处，并不是那种生硬的“傻快”。包括进行一些软件的滑动操作时，iQOO 7 的系统交互也能带来比较平顺的触控体验，这种触控质感就像在上手了一台具备高刷新特性的 iOS 设备。</p><p class=\"jsmargin\"><img alt=\"1610941320(1).jpg\" src=\"http://pic.zaeke.com/img161105115530788.jpeg\" width=\"1300\" height=\"731\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"1610960818(1).jpg\" src=\"http://pic.zaeke.com/img161105251839762.jpeg\" width=\"1300\" height=\"731\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">手机的交互体验固然重要，可是打通生态横沟也是未来手机的发展新趋势，电脑与手机是我们生活中最常见的电子设备，通过丰富两款设备之间联动性，给予用户更高效的操作体验。iQOO 7 通过“互传”APP 的多屏互动特性，使手机与电脑之间通过 Wi-Fi 局域网实现投屏显示，虽然其它加入了“互传”联盟的厂商机型也能实现基本的屏幕投影，可是它们并无法像 OriginOS 那样做到相对细致的电脑端远程操作体验，例如 OriginOS 在游戏中提供了键盘触控映射功能，直接采用鼠标和键盘操作 iQOO 7 的各种远程操作，如果电脑端屏幕本身支持触控，那么用户可直接通过电脑端实现类似的触控操作。<br></p><p class=\"jsmargin\"><img alt=\"1610962077(1).jpg\" src=\"http://pic.zaeke.com/img161105118227240.jpeg\" width=\"1300\" height=\"731\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">不过，在 ZAEKE知客工作室的网络环境下，iQOO 7 与电脑端如果采用 1080P 分辨率画质进行传输操作，偶尔会出现画面传输卡顿情况，如果把画面设置为 720P 或者 480P 之后，整体画面流畅性会有所提高。多屏互动也许不适合上手一些对操作反应要求较高的游戏，日常刷一刷微博、看一看短视频，何况手机与电脑之间能自由复制和剪切文字内容，对于办公需求的人群是非常友好的，如果可以针对键盘组合键进行兼容整合，那么对于其上手效果的提升和入手门槛的降低都有所帮助。</p><p style=\"text-align: center;\" class=\"p_style\"><b>iQOO 7 继承了 vivo 优秀的影像基因</b></p><p class=\"p_style\">在千元机都开始追求影像表现的时代，旗舰级机型的拍照表现自然不能忽视。主打全局影像创作的 iQOO 7，分别配备了 4800 万像素广角主摄 + 1300 万像素超广角镜头 + 1300 万像素人像镜头，面对部分以像素论成败的消费者，也许会觉得 iQOO 7 的 4800 万像素广角主摄跟不上时代步伐，实际上这是一颗由索尼定制的 IMX598 传感器，其在 vivo X50 系列产品上取得了不俗的口碑。</p><p class=\"jsmargin\"><img alt=\"IMG_20210115_143432.jpg\" src=\"http://pic.zaeke.com/img161105547538602.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_20210115_144828.jpg\" src=\"http://pic.zaeke.com/img161105550458547.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210115_144907.jpg\" src=\"http://pic.zaeke.com/img161105550477622.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_20210115_150153.jpg\" src=\"http://pic.zaeke.com/img161105557734811.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_20210115_172324.jpg\" src=\"http://pic.zaeke.com/img161105560129639.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210115_191523.jpg\" src=\"http://pic.zaeke.com/img161105560273022.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210116_191803.jpg\" src=\"http://pic.zaeke.com/img161105560225869.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210116_224643.jpg\" src=\"http://pic.zaeke.com/img161105567391009.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_20210118_184549.jpg\" src=\"http://pic.zaeke.com/img161105563384984.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210118_213957.jpg\" src=\"http://pic.zaeke.com/img161105563383824.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_20210114_195949.jpg\" src=\"http://pic.zaeke.com/img161105539596878.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210115_140140.jpg\" src=\"http://pic.zaeke.com/img161105539558948.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210115_143004.jpg\" src=\"http://pic.zaeke.com/img161105540987677.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_20210115_143113.jpg\" src=\"http://pic.zaeke.com/img161105541267442.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_20210118_212337_副本.jpg\" src=\"http://pic.zaeke.com/img161105534355916.jpeg\" width=\"4030\" height=\"1520\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210118_212337_副4本.jpg\" src=\"http://pic.zaeke.com/img161105534446606.jpeg\" width=\"2630\" height=\"995\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210118_2123537_副本.jpg\" src=\"http://pic.zaeke.com/img161105534365975.jpeg\" width=\"2630\" height=\"995\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">实际上，iQOO 7 这颗 4800 万像素广角主摄在弱光环境下的表现非常出众，这要得益于 f/1.6 大光圈、O.I.S 防抖、四合一成 1.6μm 像素尺寸、定制滤光片等特性，使这颗摄像头具备更大进光量和更高信噪比，因此该摄像头在弱光环境下表现尤为出众，再加上高通骁龙 888 移动平台凭借其最新的 Spectra 580 ISP，实现了比上一代产品 35% 的性能提升，进一步提升了 IMX598 传感器的弱光成像效率。可由于该主摄默认输出像素为 1200 万，因此不利于后期裁剪的应用，而且考虑到成像速度，因此常规夜景模式仅适合拍摄静态景色，除非用户想要通过降低快门获得一些特殊的光轨效果。</p><p class=\"jsmargin\"><img alt=\"IMG_20210115_143102.jpg\" src=\"http://pic.zaeke.com/img161105570837925.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210115_143343.jpg\" src=\"http://pic.zaeke.com/img161105570854100.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210115_143448.jpg\" src=\"http://pic.zaeke.com/img161105574255038.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210115_144546.jpg\" src=\"http://pic.zaeke.com/img161105574415077.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210115_144811.jpg\" src=\"http://pic.zaeke.com/img161105574733453.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210115_145830.jpg\" src=\"http://pic.zaeke.com/img161105576686267.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210115_145929.jpg\" src=\"http://pic.zaeke.com/img161105576825478.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210115_154937.jpg\" src=\"http://pic.zaeke.com/img161105577213433.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210115_191527.jpg\" src=\"http://pic.zaeke.com/img161105577950503.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_20210115_143319.jpg\" src=\"http://pic.zaeke.com/img161105570852392.jpeg\" width=\"975\" height=\"1300\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">相比较于主摄，iQOO 7 的 1300 万像素超广角镜头的综合画质肯定要略逊一筹，不过这款摄像头胜在足够广，120° 的超广角特性，使其尽可能把画面尽收眼底，再加上这颗摄像头本身可以实现 2.5cm 最近拍摄距离，因此可起到微距拍摄效果，真正实现了远近皆从容的效果。</p><p class=\"jsmargin\"><img alt=\"IMG_20210115_122236.jpg\" src=\"http://pic.zaeke.com/img161105666780167.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_20210118_215523.jpg\" src=\"http://pic.zaeke.com/img161105697066390.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_20210118_213952.jpg\" src=\"http://pic.zaeke.com/img161105704943964.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_20210118_214435.jpg\" src=\"http://pic.zaeke.com/img161105678865724.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_20210118_214539.jpg\" src=\"http://pic.zaeke.com/img161105679467366.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_20210118_214219.jpg\" src=\"http://pic.zaeke.com/img161105676876603.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_20210118_213909.jpg\" src=\"http://pic.zaeke.com/img161105611126544.jpeg\" width=\"1300\" height=\"975\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">为了进一步突出被摄主体，iQOO 7 还配备了一颗为 50mm 焦段标准镜头，个人主观感觉这颗摄像头的整体解析力还不错，特别在光线足够充沛的环境下，其与主摄之间并没有拉开太大的成像差距，但是在弱光环境下的表现就显得比较悬殊了。总体而言，iQOO7 的后置三颗可谓是各司其职，每一颗都有它存在的必要性、</p><p class=\"p_style\">只是相比较于普通手机，想要完全摸透 iQOO 7 的相机功能需花费更多的上手时间，例如“超清像素位移”并非所有焦段下都能触发；常规“相机”界面之下的人像镜头并不一定介入工作，系统会“智能”地根据场景光线自动切换镜头，想要强制采用人像镜头拍摄时，只能手动进入“人像”模式进行拍摄操作；“微距”模式下的开关为二级菜单，每次开关闭至少需要两个步骤，不过用户可开启自动模式，无需每次频繁开/关微距模式。</p><p style=\"text-align: center;\" class=\"p_style\"><b>是性能旗舰，也是新一代“价格屠夫”</b></p><p class=\"p_style\">iQOO 7 的强悍性能是从“芯”展开的，拥有高通骁龙 888 移动平台加持的它，配合 6.62 英寸 120Hz 屏幕、 LPDDR5 + UFS 3.1 存储组合，为机型的极致流畅交互体验提供了扎实的硬件基础。搭载“新鲜出炉”的 OriginOS 系统，该机不仅带来了足够自然流畅的丰富交互方式，还赋予了整机新颖的更多上手体验，例如交互池、超级卡包、变形器、原子组件等独特功能。所以，相比较于一成不变，也许搭载了 OriginOS 系统的 iQOO 7 反而是渴望打破平庸刻板的用户群体新选择。</p><p class=\"jsmargin\"><img alt=\"IMG_0386.jpg\" src=\"http://pic.zaeke.com/img161105713922264.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">在续航能力方面，iQOO 7 也是一台相当激进的机型，因为虽然它搭载了硬核十足的 120W&nbsp;FlashCharge 超快闪充，可作为一款 5G 旗舰机型，它的电池容量却仅有 4000mAh。实际上，按照我的实测可以发现，iQOO 7 配合 120W&nbsp;FlashCharge 超快闪充，可以在 15 分钟左右实现从无电量充至满电状态，这样的的充电速度确实快得惊人，可是不能否认 4000mAh 的电池容量会对这款机型的单次续航能力带来难以掩盖的短板。</p><p class=\"jsmargin\"><img alt=\"IMG_0385.jpg\" src=\"http://pic.zaeke.com/img161105717123299.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">在定价方面，这次 iQOO 7 一如既往地带来了 iQOO 式的“惊喜价”，8GB + 128GB 存储组合售价 3798元，而 12GB + 256GB 售价 4198 元，尽管两个配置的差价仅有 400 元，可是标准版凭借 3798 元定价，却较大程度地降低了潜在消费者入手首批骁龙 888 移动平台机型的门槛。</p><p class=\"p_style\">对于一个成立不足两周年的新锐手机品牌，iQOO 7 已经称得上是 iQOO 目前最耀眼的一张“成绩单”，背后除了拥有 vivo 的鼎力相助，更多的是他们非常清晰自身在市场上的定位，并且朝着通一个方向砥砺前行，使其在竞争激烈的手机市场上有了立足之本。</p>', 'http://pic.zaeke.com/img161156160854969.jpeg', 11187, 0, 0, NULL, '2021-01-19 23:44:24', '2021-03-26 20:22:01');
INSERT INTO `article` VALUES (15991, '紧贴轻量化趋势的雷柏 V360 鼠标，给予玩家充足的定制化空间', '鼠标作为最早诞生的电脑外设之一，在历经五十余年的发展之后，如今的鼠标产品已经衍生出多样化形态。相比较于前几年的“配重潮”，轻量化的鼠标减重设计反而成为了一种新趋...', '[object Object]', '小啤', 170, 'http://pic.zaeke.com/user_1533281478_8.png', '<p>鼠标作为最早诞生的电脑外设之一，在历经五十余年的发展之后，如今的鼠标产品已经衍生出多样化形态。相比较于前几年的“配重潮”，轻量化的鼠标减重设计反而成为了一种新趋势，这何尝不是一种“返璞归真”。</p><p style=\"text-align: center;\"><b>轻量化是“主食”，而模块化宛如餐后“甜点”</b></p><p>蜂窝式设计可谓是近年大热的鼠标设计范畴之一，因为相比较于传统鼠标产品，蜂窝式打孔机身鼠标具备轻量化、利于散热、提高阻力等优势，而雷柏 V360 鼠标显然也紧贴了蜂窝式设计理念。</p><p><img alt=\"IMG_0438.jpg\" src=\"http://pic.zaeke.com/img161171766824466.jpeg\" width=\"1400\" height=\"933\"><br></p><p>得益于采用了蜂窝镂空设计和有线连接的缘故，雷柏 V360 鼠标在蜂窝形态模式下的机身重量仅有 82 克，身为一位 MX Master&nbsp;用户，初次上手雷柏 V360 鼠标时，其轻巧机身确实给我带来了一丝欣喜，甚至需要短暂的时间重新适应这份久违的轻巧手感。</p><p><img alt=\"IMG_0468.jpg\" src=\"http://pic.zaeke.com/img161171768137691.jpeg\" width=\"1400\" height=\"933\"><br></p><p><img alt=\"IMG_0466.jpg\" src=\"http://pic.zaeke.com/img161171772993757.jpeg\" width=\"1400\" height=\"933\"><br></p><p>不仅如此，为了迎合不同用户对于鼠标手感的需求，雷柏 V360 鼠标还提供了左侧群、右侧群、尾部顶盖 3 个区域的模块化拆卸组合功能，通过 6 个磁吸模组的 8 种模块化自由组合特性。</p><p><img alt=\"IMG_0499.jpg\" src=\"http://pic.zaeke.com/img161171860574971.jpeg\" width=\"1400\" height=\"933\"><br></p><p>雷柏 V360 鼠标可实现 82 克、86 克、95 克的机身重量变化，其中实心顶盖采用了可拆卸式双配重块设计，用户可根据自己喜好调节配重块数量，该设计颇具心思。</p><p><img alt=\"IMG_0456.jpg\" src=\"http://pic.zaeke.com/img161171852058353.jpeg\" width=\"1400\" height=\"933\"><br></p><p><img alt=\"IMG_0459.jpg\" src=\"http://pic.zaeke.com/img161171852034572.jpeg\" width=\"1400\" height=\"933\"><br></p><p>由于模块采用了便捷的磁吸方案，因此雷柏 V360 鼠标的拆卸组合方式便捷，在每一个可拆卸部件都预留了可轻松拆卸所需的小凹槽，确保了动手能力较差的使用人群也能轻松上手。为了提高鼠标内部的观赏性，其内部主板特意选用了轻薄的黑色 PCB 电路板，元器件布局十分规整。</p><p><img alt=\"IMG_0500.jpg\" src=\"http://pic.zaeke.com/img161171839543173.jpeg\" width=\"1400\" height=\"933\"><br></p><p>在手感方面，雷柏 V360 鼠标的尾部有特意做了一定的突起设计，好让掌心更能贴合鼠标，起到缓解手部疲劳的作用，而且鼠标机身左右两侧特意做了内凹设计，这样的设计除了为拇指、无名指、小指预留了足够的放置空间以外，也起到更牢固的鼠标握持体验。由于这款鼠标的机身采用的是左右对称式设计，因此它在一定程度也能迎合左撇子用户的上手需求。当然，侧键位置是固定的，无法随意更改。</p><p><img alt=\"IMG_0518.jpg\" src=\"http://pic.zaeke.com/img161171840259673.jpeg\" width=\"1400\" height=\"933\"><br></p><p>通过观察可以发现，其实雷柏 V360 鼠标机身底部的每一个脚垫都附有出厂贴膜，除了避免出厂测试环节中产生的使用痕迹以外，如果想要充分发挥出脚垫本身的顺滑质感，那么我建议用户在使用雷柏 V360 鼠标之前可提前撕掉脚贴上的贴膜。</p><p style=\"text-align: center;\"><b>既然定位电竞，那么性能自然需要兼顾</b></p><p>作为一款定位游戏的鼠标产品，雷柏 V360 鼠标的按键布局相对常规，其一共提供了 9 个机身按键，并且每个按键均提供了可编程特性。另外，雷柏 V360 鼠标搭载了手感清脆的 2000 万次微动开关。</p><p><img alt=\"IMG_0440.jpg\" src=\"http://pic.zaeke.com/img161171842749688.jpeg\" width=\"1400\" height=\"933\"><br></p><p>可是，我发现雷柏 V360 的左键和右键的有效按压面积相对有限，仅有前方二分之一面积可正常实现按键的按压，而后半段由于直接固定在鼠标内框架之中，因此该区域是无法有效按压鼠标左/右键，不过这称不上是设计缺点，可是上手是时需要适应。除此，鼠标滚轮后方的两个 DPI 调节键的键程略浅，按压手感不够清晰，但是优势之处就在于可降低按键误触概率。</p><p><img alt=\"IMG_0449.jpg\" src=\"http://pic.zaeke.com/img161171858488425.jpeg\" width=\"1400\" height=\"933\"><br></p><p>考虑到 RGB 灯效是电竞外设的标志性“装备”，雷柏 V360 鼠标自然也没有落下。通过照片不难看出，其机身发光区域主要集中在滚轮与鼠标尾部，该两个区域可以实现约 1680 万色 RGB 背光呈现。</p><p><img alt=\"鼠标截图动图.gif\" src=\"http://pic.zaeke.com/img161171748293364.gif\" width=\"960\" height=\"640\"><br></p><p>雷柏还为 V360 推出了专属电脑驱动程序， 其中鼠标功能被划分为“主要设置”、“灯光设置”、“宏编辑”三个功能页面，通过“主要设置”页面，可以针对鼠标的 <span style=\"font-size: 16px;\">DPI</span>、指针和滚轮速度、USB  报告率、每个实体按键的自定义进行自由设置。</p><p><img alt=\"GIF 2021-1-25 18-32-37.gif\" src=\"http://pic.zaeke.com/img161181653333096.gif\" width=\"729\" height=\"405\"><br></p><p>“灯光设置”页面，顾名思义就是控制鼠标的灯效呈现，默认模式下就是炫彩模式，用户也可以针对喜好自由调节滚动和灯带的灯光色彩，它还可实现光谱、呼吸、apm 呼吸、常亮几种灯光表现，当然也提供了关闭灯光的功能。</p><p>对于游戏爱好者而言，也许对“鼠标宏”已经不陌生，如果通俗一点理解，就是可通过程序对用户的鼠标操作进行录制，待用户有需求的时候可自由调动出事先预设好的动作，从而达到事半功倍的效果。不过并不是所有游戏都允许“鼠标宏”功能的应用，因此使用该功能时需要有所斟酌。</p><p><img alt=\"800.jpg\" src=\"http://pic.zaeke.com/img161171752425236.jpeg\" width=\"1000\" height=\"691\"><br></p><p><img alt=\"1200.jpg\" src=\"http://pic.zaeke.com/img161171753222959.jpeg\" width=\"1000\" height=\"691\"><br></p><p><img alt=\"1600.jpg\" src=\"http://pic.zaeke.com/img161171753735243.jpeg\" width=\"1000\" height=\"691\"><br></p><p>采用了 PMW3327 游戏传感器的雷柏 V360，可以带来 200 ~ 6200 DPI 表现，回报率为 125/250/500/1000Hz。雷柏 V360 鼠标以每 100 <span style=\"font-size: 16px;\">DPI</span> 为一个步进，共提供 7 个不同 <span style=\"font-size: 16px;\">DPI</span> 档位。使用 MouseTester 进行测试可以发现，在 800、1200、1600 三个 DPI 设置之下，其表现还是比较稳定的，离散点基本都在轨迹线之上，没有出现严重的丢帧情况。鼠标的准星精准度在游戏过程中是至关重要的，毕竟细微的误差都能左右胜局。</p><p><img alt=\"IMG_0501.jpg\" src=\"http://pic.zaeke.com/img161171756357659.jpeg\" width=\"1400\" height=\"933\"></p><p><img alt=\"IMG_0502.jpg\" src=\"http://pic.zaeke.com/img161172962525410.jpeg\" width=\"1400\" height=\"933\"><br></p><p>以第一人称射击类游戏《CS:GO》和战术竞技游戏《英雄联盟》为例，雷柏 V360 的按键手感非常适合玩家进行点射和游戏人物的指令下达，加上相对轻盈的机身和光滑的底部脚贴设计，杜绝了部分鼠标拖泥带水的感觉。另外，其侧面双按键给予了游戏足够丰富的自定义操作空间。</p><p style=\"text-align: center;\"><b>进一步降低游戏鼠标的入手门槛</b></p><p>毫无疑问，轻量化是雷柏 V360 鼠标的主旋律，而且凭借模块化设计，使其可自由组合出 8 种模块化组合。雷柏 V360 鼠标的模块化设计，除了兼顾了手感的随心组合以外，对于我这种热衷于玩拼装模型的用户而言，模块化设计为本来枯燥的生活提供了额外的小乐趣。</p><p><img alt=\"IMG_0514.jpg\" src=\"http://pic.zaeke.com/img161171880944112.jpeg\" width=\"1400\" height=\"933\"><br></p><p>除了最低轻至 82 克的轻量化设计 + 模块化造就了雷柏 V360 鼠标的产品优势以外，其 149 元的官方定价也较大程度地降低了消费者的入手门槛，更别提电商平台偶尔会推出的 129 元促销活动价，使得这款鼠标在性价比方面的表现尤为突出。</p><p><img alt=\"IMG_0503.jpg\" src=\"http://pic.zaeke.com/img161171881997137.jpeg\" width=\"1400\" height=\"933\"><br></p><p>综合以上所述，在百元级别游戏鼠标中，雷柏 V360 鼠标确实称得上是一款理想新选择，即便面对日常办公，还是游戏场景中，它都可以轻松胜任。</p>', '<p class=\"p_style\">鼠标作为最早诞生的电脑外设之一，在历经五十余年的发展之后，如今的鼠标产品已经衍生出多样化形态。相比较于前几年的“配重潮”，轻量化的鼠标减重设计反而成为了一种新趋势，这何尝不是一种“返璞归真”。</p><p style=\"text-align: center;\" class=\"p_style\"><b>轻量化是“主食”，而模块化宛如餐后“甜点”</b></p><p class=\"p_style\">蜂窝式设计可谓是近年大热的鼠标设计范畴之一，因为相比较于传统鼠标产品，蜂窝式打孔机身鼠标具备轻量化、利于散热、提高阻力等优势，而雷柏 V360 鼠标显然也紧贴了蜂窝式设计理念。</p><p class=\"jsmargin\"><img alt=\"IMG_0438.jpg\" src=\"http://pic.zaeke.com/img161171766824466.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">得益于采用了蜂窝镂空设计和有线连接的缘故，雷柏 V360 鼠标在蜂窝形态模式下的机身重量仅有 82 克，身为一位 MX Master&nbsp;用户，初次上手雷柏 V360 鼠标时，其轻巧机身确实给我带来了一丝欣喜，甚至需要短暂的时间重新适应这份久违的轻巧手感。</p><p class=\"jsmargin\"><img alt=\"IMG_0468.jpg\" src=\"http://pic.zaeke.com/img161171768137691.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_0466.jpg\" src=\"http://pic.zaeke.com/img161171772993757.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">不仅如此，为了迎合不同用户对于鼠标手感的需求，雷柏 V360 鼠标还提供了左侧群、右侧群、尾部顶盖 3 个区域的模块化拆卸组合功能，通过 6 个磁吸模组的 8 种模块化自由组合特性。</p><p class=\"jsmargin\"><img alt=\"IMG_0499.jpg\" src=\"http://pic.zaeke.com/img161171860574971.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">雷柏 V360 鼠标可实现 82 克、86 克、95 克的机身重量变化，其中实心顶盖采用了可拆卸式双配重块设计，用户可根据自己喜好调节配重块数量，该设计颇具心思。</p><p class=\"jsmargin\"><img alt=\"IMG_0456.jpg\" src=\"http://pic.zaeke.com/img161171852058353.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_0459.jpg\" src=\"http://pic.zaeke.com/img161171852034572.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">由于模块采用了便捷的磁吸方案，因此雷柏 V360 鼠标的拆卸组合方式便捷，在每一个可拆卸部件都预留了可轻松拆卸所需的小凹槽，确保了动手能力较差的使用人群也能轻松上手。为了提高鼠标内部的观赏性，其内部主板特意选用了轻薄的黑色 PCB 电路板，元器件布局十分规整。</p><p class=\"jsmargin\"><img alt=\"IMG_0500.jpg\" src=\"http://pic.zaeke.com/img161171839543173.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">在手感方面，雷柏 V360 鼠标的尾部有特意做了一定的突起设计，好让掌心更能贴合鼠标，起到缓解手部疲劳的作用，而且鼠标机身左右两侧特意做了内凹设计，这样的设计除了为拇指、无名指、小指预留了足够的放置空间以外，也起到更牢固的鼠标握持体验。由于这款鼠标的机身采用的是左右对称式设计，因此它在一定程度也能迎合左撇子用户的上手需求。当然，侧键位置是固定的，无法随意更改。</p><p class=\"jsmargin\"><img alt=\"IMG_0518.jpg\" src=\"http://pic.zaeke.com/img161171840259673.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">通过观察可以发现，其实雷柏 V360 鼠标机身底部的每一个脚垫都附有出厂贴膜，除了避免出厂测试环节中产生的使用痕迹以外，如果想要充分发挥出脚垫本身的顺滑质感，那么我建议用户在使用雷柏 V360 鼠标之前可提前撕掉脚贴上的贴膜。</p><p style=\"text-align: center;\" class=\"p_style\"><b>既然定位电竞，那么性能自然需要兼顾</b></p><p class=\"p_style\">作为一款定位游戏的鼠标产品，雷柏 V360 鼠标的按键布局相对常规，其一共提供了 9 个机身按键，并且每个按键均提供了可编程特性。另外，雷柏 V360 鼠标搭载了手感清脆的 2000 万次微动开关。</p><p class=\"jsmargin\"><img alt=\"IMG_0440.jpg\" src=\"http://pic.zaeke.com/img161171842749688.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">可是，我发现雷柏 V360 的左键和右键的有效按压面积相对有限，仅有前方二分之一面积可正常实现按键的按压，而后半段由于直接固定在鼠标内框架之中，因此该区域是无法有效按压鼠标左/右键，不过这称不上是设计缺点，可是上手是时需要适应。除此，鼠标滚轮后方的两个 DPI 调节键的键程略浅，按压手感不够清晰，但是优势之处就在于可降低按键误触概率。</p><p class=\"jsmargin\"><img alt=\"IMG_0449.jpg\" src=\"http://pic.zaeke.com/img161171858488425.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">考虑到 RGB 灯效是电竞外设的标志性“装备”，雷柏 V360 鼠标自然也没有落下。通过照片不难看出，其机身发光区域主要集中在滚轮与鼠标尾部，该两个区域可以实现约 1680 万色 RGB 背光呈现。</p><p class=\"jsmargin\"><img alt=\"鼠标截图动图.gif\" src=\"http://pic.zaeke.com/img161171748293364.gif\" width=\"960\" height=\"640\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">雷柏还为 V360 推出了专属电脑驱动程序， 其中鼠标功能被划分为“主要设置”、“灯光设置”、“宏编辑”三个功能页面，通过“主要设置”页面，可以针对鼠标的 <span style=\"font-size: 16px;\">DPI</span>、指针和滚轮速度、USB  报告率、每个实体按键的自定义进行自由设置。</p><p class=\"jsmargin\"><img alt=\"GIF 2021-1-25 18-32-37.gif\" src=\"http://pic.zaeke.com/img161181653333096.gif\" width=\"729\" height=\"405\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">“灯光设置”页面，顾名思义就是控制鼠标的灯效呈现，默认模式下就是炫彩模式，用户也可以针对喜好自由调节滚动和灯带的灯光色彩，它还可实现光谱、呼吸、apm 呼吸、常亮几种灯光表现，当然也提供了关闭灯光的功能。</p><p class=\"p_style\">对于游戏爱好者而言，也许对“鼠标宏”已经不陌生，如果通俗一点理解，就是可通过程序对用户的鼠标操作进行录制，待用户有需求的时候可自由调动出事先预设好的动作，从而达到事半功倍的效果。不过并不是所有游戏都允许“鼠标宏”功能的应用，因此使用该功能时需要有所斟酌。</p><p class=\"jsmargin\"><img alt=\"800.jpg\" src=\"http://pic.zaeke.com/img161171752425236.jpeg\" width=\"1000\" height=\"691\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"1200.jpg\" src=\"http://pic.zaeke.com/img161171753222959.jpeg\" width=\"1000\" height=\"691\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"1600.jpg\" src=\"http://pic.zaeke.com/img161171753735243.jpeg\" width=\"1000\" height=\"691\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">采用了 PMW3327 游戏传感器的雷柏 V360，可以带来 200 ~ 6200 DPI 表现，回报率为 125/250/500/1000Hz。雷柏 V360 鼠标以每 100 <span style=\"font-size: 16px;\">DPI</span> 为一个步进，共提供 7 个不同 <span style=\"font-size: 16px;\">DPI</span> 档位。使用 MouseTester 进行测试可以发现，在 800、1200、1600 三个 DPI 设置之下，其表现还是比较稳定的，离散点基本都在轨迹线之上，没有出现严重的丢帧情况。鼠标的准星精准度在游戏过程中是至关重要的，毕竟细微的误差都能左右胜局。</p><p class=\"jsmargin\"><img alt=\"IMG_0501.jpg\" src=\"http://pic.zaeke.com/img161171756357659.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_0502.jpg\" src=\"http://pic.zaeke.com/img161172962525410.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">以第一人称射击类游戏《CS:GO》和战术竞技游戏《英雄联盟》为例，雷柏 V360 的按键手感非常适合玩家进行点射和游戏人物的指令下达，加上相对轻盈的机身和光滑的底部脚贴设计，杜绝了部分鼠标拖泥带水的感觉。另外，其侧面双按键给予了游戏足够丰富的自定义操作空间。</p><p style=\"text-align: center;\" class=\"p_style\"><b>进一步降低游戏鼠标的入手门槛</b></p><p class=\"p_style\">毫无疑问，轻量化是雷柏 V360 鼠标的主旋律，而且凭借模块化设计，使其可自由组合出 8 种模块化组合。雷柏 V360 鼠标的模块化设计，除了兼顾了手感的随心组合以外，对于我这种热衷于玩拼装模型的用户而言，模块化设计为本来枯燥的生活提供了额外的小乐趣。</p><p class=\"jsmargin\"><img alt=\"IMG_0514.jpg\" src=\"http://pic.zaeke.com/img161171880944112.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">除了最低轻至 82 克的轻量化设计 + 模块化造就了雷柏 V360 鼠标的产品优势以外，其 149 元的官方定价也较大程度地降低了消费者的入手门槛，更别提电商平台偶尔会推出的 129 元促销活动价，使得这款鼠标在性价比方面的表现尤为突出。</p><p class=\"jsmargin\"><img alt=\"IMG_0503.jpg\" src=\"http://pic.zaeke.com/img161171881997137.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">综合以上所述，在百元级别游戏鼠标中，雷柏 V360 鼠标确实称得上是一款理想新选择，即便面对日常办公，还是游戏场景中，它都可以轻松胜任。</p>', 'http://pic.zaeke.com/img161251371578378.jpeg', 4055, 0, 0, NULL, '2021-01-26 19:40:46', '2021-03-26 09:56:10');
INSERT INTO `article` VALUES (16006, '大底？微云台？蔡司光学？vivo X60 Pro+什么都有', 'vivoX60Pro+是一台不少手机拍照爱好者都十分期待的新品，上一代的X50Pro+就因为拥有性能卓越且调校出色的后置相机模组被ZAEKE知客评为2020年最...', '[object Object]', 'victory3332', 31, 'http://pic.zaeke.com/user_1533281475_1.png', '<p>vivo X60 Pro+是一台不少手机拍照爱好者都十分期待的新品，上一代的X50 Pro+就因为拥有性能卓越且调校出色的后置相机模组被ZAEKE知客评为2020年最优秀的拍照手机之一。当时首发采用三星ISOCELL GN1传感器的它不论是白天夜晚均有极其优秀的表现，vivo对照片色彩的出色见解更是让X50 Pro+这台影像旗舰如虎添翼。</p><p style=\"text-align: center;\"><img src=\"http://pic.zaeke.com/img161190485812259.jpeg\" alt=\"\" width=\"466.3316582914573\" height=\"700\">  </p><p>到了2021年，vivo又在影像方面有了大动作，也就是和知名的光学厂商卡尔蔡司建立战略合作伙伴关系。vivo和蔡司双方建立合作关系之后开始携手研发智能手机相机，蔡司的技术、设计等也会被应用到vivo手机上。</p><p>vivo和蔡司合作的结晶就是全新的X60系列，这台X60 Pro+更加是vivo手机拍照和与蔡司合作成果的集大成者。X60 Pro+拥有vivo手机，乃至整个手机行业中最顶尖的相机硬件配置。在硕大的机身后置摄像头模组里面，vivo装下了一颗光学防抖GN1主摄，以及基于索尼IMX598打造的微云台超广角相机，作为第二主摄。</p><p style=\"text-align: center;\"><img src=\"http://pic.zaeke.com/img161190489740224.jpeg\" alt=\"\" width=\"466.3316582914573\" height=\"700\">  </p><p>蔡司著名的T*镀膜也被应用在X60 Pro+上，了解摄影器材的用户都知道蔡司的这款镀膜可以改善镜头模组的光学素质，防止或改善眩光等可能影响画面品质的问题。之前在X60/X60 Pro上首次出现的蔡司Biotar人像风格也没有在X60 Pro+上缺席，旋涡状的焦外虚化效果可以为用户带来更具有辨识性和梦幻效果的背景虚化照片。</p><p>除了这两颗存在感极强的主摄相机，X60 Pro+还搭载了独立的3200万像素2倍人像相机，以及800万像素5倍潜望式长焦相机。之前在X50 Pro+上搭载的独立色温传感器也被继承下来，这颗传感器曾经帮助X50 Pro+实现了非常出色的白平衡稳定度以及多相机模组之间的色彩统一性，让拍照体验更好。</p><p style=\"text-align: center;\"><img src=\"http://pic.zaeke.com/img161190490947851.jpeg\" alt=\"\" width=\"466.3316582914573\" height=\"700\">  </p><p>除了影像系统，X60 Pro+的其他配置也完全是旗舰级水准。6.56英寸FHD+ 120Hz刷新率OLED显示屏、高通骁龙888 5G移动平台、4200毫安时内置电池、55W快速充电等特性皆有提供。</p><p>同时这款影像旗舰还有专属的素皮材质机身，比玻璃机身更轻巧的同时还有良好的抗污能力和高档质感。素皮机身、曲面屏设计加上蔡司认证的标志，让vivo X60 Pro+成为了不折不扣的专业时尚影像利器，让追求极致手机拍照的人也可以尽情呼吸时尚的气息。</p><p><strong>更多图赏：</strong></p><p><img src=\"http://pic.zaeke.com/img161190495196104.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161190495394249.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161190494924832.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161190537512285.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161190537634678.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161190537695477.jpeg\" alt=\"\">  </p>', '<p class=\"p_style\">vivo X60 Pro+是一台不少手机拍照爱好者都十分期待的新品，上一代的X50 Pro+就因为拥有性能卓越且调校出色的后置相机模组被ZAEKE知客评为2020年最优秀的拍照手机之一。当时首发采用三星ISOCELL GN1传感器的它不论是白天夜晚均有极其优秀的表现，vivo对照片色彩的出色见解更是让X50 Pro+这台影像旗舰如虎添翼。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161190485812259.jpeg\" alt=\"\" width=\"466.3316582914573\" height=\"700\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">到了2021年，vivo又在影像方面有了大动作，也就是和知名的光学厂商卡尔蔡司建立战略合作伙伴关系。vivo和蔡司双方建立合作关系之后开始携手研发智能手机相机，蔡司的技术、设计等也会被应用到vivo手机上。</p><p class=\"p_style\">vivo和蔡司合作的结晶就是全新的X60系列，这台X60 Pro+更加是vivo手机拍照和与蔡司合作成果的集大成者。X60 Pro+拥有vivo手机，乃至整个手机行业中最顶尖的相机硬件配置。在硕大的机身后置摄像头模组里面，vivo装下了一颗光学防抖GN1主摄，以及基于索尼IMX598打造的微云台超广角相机，作为第二主摄。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161190489740224.jpeg\" alt=\"\" width=\"466.3316582914573\" height=\"700\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">蔡司著名的T*镀膜也被应用在X60 Pro+上，了解摄影器材的用户都知道蔡司的这款镀膜可以改善镜头模组的光学素质，防止或改善眩光等可能影响画面品质的问题。之前在X60/X60 Pro上首次出现的蔡司Biotar人像风格也没有在X60 Pro+上缺席，旋涡状的焦外虚化效果可以为用户带来更具有辨识性和梦幻效果的背景虚化照片。</p><p class=\"p_style\">除了这两颗存在感极强的主摄相机，X60 Pro+还搭载了独立的3200万像素2倍人像相机，以及800万像素5倍潜望式长焦相机。之前在X50 Pro+上搭载的独立色温传感器也被继承下来，这颗传感器曾经帮助X50 Pro+实现了非常出色的白平衡稳定度以及多相机模组之间的色彩统一性，让拍照体验更好。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161190490947851.jpeg\" alt=\"\" width=\"466.3316582914573\" height=\"700\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">除了影像系统，X60 Pro+的其他配置也完全是旗舰级水准。6.56英寸FHD+ 120Hz刷新率OLED显示屏、高通骁龙888 5G移动平台、4200毫安时内置电池、55W快速充电等特性皆有提供。</p><p class=\"p_style\">同时这款影像旗舰还有专属的素皮材质机身，比玻璃机身更轻巧的同时还有良好的抗污能力和高档质感。素皮机身、曲面屏设计加上蔡司认证的标志，让vivo X60 Pro+成为了不折不扣的专业时尚影像利器，让追求极致手机拍照的人也可以尽情呼吸时尚的气息。</p><p class=\"p_style\"><strong>更多图赏：</strong></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161190495196104.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161190495394249.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161190494924832.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161190537512285.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161190537634678.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161190537695477.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p>', 'http://pic.zaeke.com/img161190607627694.jpeg', 3327, 0, 0, NULL, '2021-01-28 23:32:19', '2021-03-27 07:05:57');
INSERT INTO `article` VALUES (16021, 'Galaxy Buds Pro：AirPods Pro，我是你的破壁人', '文首先来个真爱粉测验：还记得我们的GalaxyBudsLive评测文章标题是什么吗？彼时我们没能预料到的是，仅仅不到半年之后，三星GalaxyBuds系列真无线...', '[object Object]', '斗室听澜', 189, 'http://pic.zaeke.com/user_1535960095png', '<p>文首先来个真爱粉测验：还记得我们的Galaxy Buds Live评测文章标题是什么吗？<br></p><p><img alt=\"1.jpeg\" src=\"http://pic.zaeke.com/img161221524999995.jpeg\" width=\"1400\" height=\"560\"><br></p><p>彼时我们没能预料到的是，仅仅不到半年之后，三星Galaxy Buds系列真无线耳机的最新作不但名字里有“Pro”，产品力也真的Pro了起来。</p><p><img alt=\"DSC_5028.jpg\" src=\"http://pic.zaeke.com/img161221527275224.jpeg\" width=\"1920\" height=\"1280\"><br></p><p><b style=\"font-size: large;\">外在：稳中求进</b></p><p>不能不首先吐槽的是，我刚拿到Galaxy Buds Pro的时候几乎以为这是Galaxy Buds Live的什么新配色，毕竟二者的充电盒造型及尺寸几乎可以说是一模一样，连保护套都可以通用（Galaxy Buds和Galaxy Buds+的保护套通用性甚至也比它们俩差点）。</p><p><img alt=\"DSC_5031.jpg\" src=\"http://pic.zaeke.com/img161221530165168.jpeg\" width=\"1920\" height=\"1280\"><br></p><p>当然，除了省模具费用的嫌疑外，我还是愿意给Galaxy Buds Pro的充电盒以好评。用过Galaxy Buds Live的人应该都记得其充电盒是多么小巧，Galaxy Buds Pro同样继承了其体积小、盒盖阻尼感佳的优点，磨砂雾面质感也与金色Galaxy Buds Live一致。我这一副银色版本的充电盒颜色其实应该算浅浅的贝母蓝，充足光线下会反射出珠光。并且它在好看之余也比那些光面塑料货耐久得多，Galaxy Buds Pro的盒子曾被我不经意地从桌面扒拉到了瓷砖地上，也没留下肉眼可见的痕迹。</p><p><img alt=\"DSC_5029.jpg\" src=\"http://pic.zaeke.com/img161221538944295.jpeg\" width=\"1920\" height=\"1280\"><br></p><p>所以说如果你的使用习惯不算太糙，那么大可不去为它准备什么保护套，反正裸着的Galaxy Buds Pro也还蛮漂亮的。</p><p>不过耳机本体的外观就有些争议了，它不像Galaxy Buds Live的腰果造型那样具备较高的辨识度，远看就只是一块光秃秃的突镜面，紫色倒是不必担心，我这副银色耳机一旦脏了以后视觉上就很明显。而且这大块的触控面也使得Galaxy Buds Pro容易在佩戴或摘下的过程中发生误触，触控面较小的Galaxy Buds+要略友好一点。</p><p><img alt=\"DSC_5037.jpg\" src=\"http://pic.zaeke.com/img161221541397734.jpeg\" width=\"1920\" height=\"1280\"><br></p><p><img alt=\"DSC_5036.jpg\" src=\"http://pic.zaeke.com/img161221543631328.jpeg\" width=\"1920\" height=\"1280\"><br></p><p>虽说观感尚存争议，这一次Galaxy Buds Pro的腔体设计在我看来是截止目前所有三星真无线耳机产品中最成功的一个。它的锥形腔体拥有较小的截面积，对耳廓的压力会比Galaxy Buds+明显小一些，椭圆截面短导管配合椭圆截面硅胶套对耳洞的友好度也更高。<b>尽管同为入耳式形态的产品，Galaxy Buds Pro的佩戴舒适度仍显著优于Galaxy Buds+，甚至可以在侧躺状态下舒服地佩戴</b>（AirPods Pro做得到吗？），IPX7防水也令人用起来多几分安心。而对比“好听不舒服、舒服不好听”的Galaxy Buds Live，你也不需要再于音质、隔音和舒适度之间做单选题了。</p><p><img alt=\"DSC_5030.jpg\" src=\"http://pic.zaeke.com/img161221545919186.jpeg\" width=\"1920\" height=\"1280\"><br></p><p><b style=\"font-size: large;\">体验：不必再看苹果的尾灯</b></p><p>恭喜三星，你是安卓阵营中第一个做出综合体验可与AirPods Pro同台较量之真无线耳机产品的品牌。</p><p>首先在连接性能方面，自Galaxy Buds+开始搭载的博通BCM43015至今就没换过，所以Galaxy Buds Pro的连接性能表现与Galaxy Buds+以及Galaxy Buds Live并没有明显的区别：配对和连接十分迅速，明显比包括AirPods Pro在内的多数竞品更快；稳定性依然优秀，很少出现断续的情况；搭配S20Ultra时延迟低至可以轻松应对《和平精英》等射击游戏的需求，但仍然只对“游戏中心”内的应用生效。</p><p><img alt=\"DSC_5041.jpg\" src=\"http://pic.zaeke.com/img161221550414301.jpeg\" width=\"1920\" height=\"1280\"><br></p><p>需要细说的是两个新功能：无缝耳机连接和延迟调节。<b>无缝耳机连接适用于登陆了同一三星账号的三星设备，</b>从实际体验来说有两点优势：其一是当你将Galaxy Buds Pro与一台三星设备配对之后，它会出现在你的其它三星设备已配对列表中，无需手动配对；其二是在三星设备间切换要比跨品牌（比如S20Ultra和iPhone 12间）切换更加迅速顺畅。但要注意，Galaxy Buds Pro并不支持多设备连接，连接新设备后会与前一个设备断开；它也还不能像AirPods系列那样根据场景在设备间自主切换，所以在我看来与真正的“无缝”尚有距离。</p><p>至于One UI在Sound Assitant中新增的延迟调节功能，它适用于所有与其支持的三星设备连接的蓝牙耳机，并不局限于Galaxy Buds系列，但场景限制却比较严苛。实际用下来只对自带的音视频应用起作用，第三方应用哪怕是已经加入游戏中心也不好使。所以就目前而言，除非你用手机看本地视频，否则这个功能是可以忽略的——你问所以在线视频应用的延迟怎么解决？咱还是把它拖进“游戏中心”好了……</p><p><img alt=\"2.jpg\" src=\"http://pic.zaeke.com/img161221553060297.jpeg\" width=\"1440\" height=\"1957\"><br></p><p>续航方面，增加了主动降噪还优化了佩戴的Galaxy Buds Pro自然不可能复现Galaxy Buds+单体12小时以上那么夸张的成绩，官方说法其在开启和关闭主动降噪时的播放时间分别为5+13小时和8+20小时。实际使用下来开启降噪+60%音量播放一小时耗电在18-20%，与官称相符——毕竟三星在之前一贯喜欢“反向虚标”，不需要担心其实际续航表现有水分。快速充电和无线充电等前几款Galaxy Buds拥有的特性也没有缺席。Galaxy Buds Pro这次最受群众瞩目的应该就是降噪表现了，毕竟Galaxy Buds Live受限于开放式结构导致其综合降噪效果还不如普通的非主动降噪入耳式耳机（如Galaxy Buds+），包括我在内的星粉们都指望三星这一次能找回颜面来。至于实际表现……我给出的评价是“很不错”。</p><p><b>“不错”的是，Galaxy Buds Pro的主动降噪效果终于达到了当前主动降噪真无线耳机的上游水准，</b>良好的低频噪声削弱效果令其在通勤场景中具备足够高的可用性，办公室里对空调或风扇声的削弱效果也不错。需要注意一点，如果你只在办公室或者不算喧闹的街道上尝试Galaxy Buds Pro的主动降噪，很容易得出它降噪强度较弱的结论。但事实是它的降噪强度调节比多数竞品更加动态，地铁中能明显感受到Galaxy Buds Pro的降噪强度会拔到较高的水准上，对低频噪声的削弱效果会优于AirPods Pro；而在安静的办公室内，较低耳压且佩戴舒适的Galaxy Buds Pro能帮我舒舒服服地睡个午觉，那些或耳压较高或不能侧躺的产品（AirPods Pro两样都占）就做不到这一点。</p><p><img alt=\"DSC_5039.jpg\" src=\"http://pic.zaeke.com/img161221556215348.jpeg\" width=\"1920\" height=\"1280\"><br></p><p>不过同多数其它产品一样，Galaxy Buds Pro对中高频噪声的削弱能力要逊色于AirPods Pr的，但不得不说现阶段主动降噪耳机对中高频噪声的削弱效果都不会特别好，真正靠谱的手段还得是ER4那种高强度物理隔绝。排除BOSE QC Earbuds这个独孤求败的产品，再排除Enco X这种中高频噪声削弱拉胯太多的产品（Galaxy Buds Live你继续面壁吧），其它主流的主动降噪真无线耳机在降噪效果方面基本都可以划入同一梯队，彼此间有差距但体验拉不开档次。<b>而且主动降噪对音质也会产生负面影响，Galaxy Buds Pro能在保住消费级TWS音质之冠（这点我们后面细说）的前提下做到称得上较为优秀的降噪效果，我已经知足了。</b></p><p>除了降噪强度和耳压优化外，抗风噪性能对降噪体验也有显著影响，这一点上Galaxy Buds Pro优于我手中的OPPO W51、Enco X和小米Air 2 Pro。但跟公认抗风噪最强的AirPods Pro比起来，Galaxy Buds Pro在开启主动降噪或环境音时的风声还是要更加明显。</p><p><img alt=\"DSC_5048.jpg\" src=\"http://pic.zaeke.com/img161221559278147.jpeg\" width=\"1920\" height=\"1280\"><br></p><p><b>值得欣慰的是Galaxy Buds Pro的通话效果非常不错，受风声的干扰很小，</b>这点就很符合三星对麦克风抗风噪设计的描述，毕竟Galaxy Buds Live就已经搞定了通话风噪，作为后继产品的Galaxy Buds Pro自然没道理把牙膏再吸回去（仅支持25W充电的Galaxy S21Ultra：有被冒犯到）。而且优秀的通话效果也让我有理由猜测当前Galaxy Buds Pro的降噪/环境音下抗风噪效果不够完美的原因可能是软件优化欠佳造成的，毕竟它实现通话降噪的手段并非如Galaxy Buds Pro那样通过额外的骨振动传感器，而是纯粹依靠麦克风设计和软件算法，所以不完全排除其抗风噪效果在未来通过固件更新得到一定改善的可能性。</p><p>至于Bixby语音唤醒，安静环境下的唤醒成功率跟AirPods Pro的Hey Siri差别不大，但在不那么安静的户外环境中唤醒成功率和指令识别率都不那么可靠，这一点比起Galaxy Buds Live区别不大。不过我本人一直将之视为一个带有公开处刑色彩所以从不使用的功能，而对你有没有用还需要你自己来回答。</p><p>前面这些项目还不至于令人觉得Galaxy Buds Pro身怀了什么“绝技”，而接下来才是重头戏——此前还没有任何产品能够冲击AirPods Pro的项目：环境音。</p><p><img alt=\"DSC_5032.jpg\" src=\"http://pic.zaeke.com/img161221562987318.jpeg\" width=\"1920\" height=\"1280\"><br></p><p>论主动降噪、抗风噪以及通话质量，其实早已有产品能与AirPods Pro比肩甚至实现较大程度的超越，但唯有环境音品质这一项苹果面前还没有任何产品能构成威胁，直到Galaxy Buds Pro的出现。<b>毫不夸张地说，在环境音这一项目中，Galaxy Buds Pro与AirPods Pro的表现伯仲之间，二者的环境音品质都相当自然清晰真实，接近未佩戴耳机的体验。</b>唯一拖了三星后腿的就是我刚刚讲过的Galaxy Buds Pro抗风噪目前还不够强，有风环境下的表现要弱一些。</p><p>但Galaxy Buds Pro还有其它的优势可讲。首先它的环境音量有四档可调，你可以根据使用需要将环境音量调小或调大一些，选择更加灵活。我个人推荐中或者小音量，太高的环境音量容易使突发噪声惊吓到自己。</p><p><span style=\"font-size: 10.5pt;\">然后三星还为Galaxy Buds Pro增加了语音检测功能，这一功能同索尼在WH-1000XM4加入的智能免摘对话类似，耳机在检测到用户</span>说话时会自动降低音量并切换到环境音，10秒内没有说话则切换回主动降噪，这使得你在结账等场景中免去了摘下耳机或手动切换到环境音的麻烦。不过需要注意，它和WH-1000XM4一样会被咳嗽或清嗓的声音激活，如果三星能够像索尼那样提供灵敏度调节，这一功能的体验会更趋完美。</p><p><img alt=\"DSC_5040.jpg\" src=\"http://pic.zaeke.com/img161221567955102.jpeg\" width=\"1920\" height=\"1280\"><br></p><p><b>小结下Galaxy Buds Pro的使用体验：在发布十四个月后，AirPods Pro的最后一道“叹息之壁”即环境音功能终于被Galaxy Buds Pro攻破，三星甚至还整了更多的“花活儿”出来。</b>而其它方面二者的体验差距也拉近了不少，距离AirPods Pro推出已经过去了15个月，安卓阵营终于拿出了一款可与之正面对抗的产品——但愿传说中的第二代AirPods Pro不要很快推出吧（笑）。</p><p><b style=\"font-size: large;\">音质：唯有我，能战胜我</b></p><p>在Galaxy Buds+推出后的这一年时间里，我一直将其视为消费级真无线耳机的音质标杆。不考虑某些HiFi品牌所推出的售价离谱体验更离谱的奇葩产品，在“像是给人用的东西”这一范畴内，Galaxy Buds+几乎始终是调音与素质的综合最优解。我所评测过的上一款音质T0级别真无线耳机声阔降噪舱也只是素质比较Galaxy Buds+稍占优势，并未实现完全超越。</p><p>而Galaxy Buds Pro则在这两方面都有更好的表现。</p><p><img alt=\"DSC_5043.jpg\" src=\"http://pic.zaeke.com/img161221571472810.jpeg\" width=\"1920\" height=\"1280\"><br></p><p>推荐均衡器设置依然是“有活力”，最符合多数人的喜好。因为Galaxy Buds+不具备主动降噪功能，所以二者的音质比较主要参考关闭Galaxy Buds Pro降噪时的发挥。</p><p>比起Galaxy Buds+，Galaxy Buds Pro的音色细腻度和密度都有所提高，这令其拥有更好的鲜活度。与此同时Galaxy Buds Pro在声场广度和动态方面也更具优势，听起来要更加起伏大气一点。</p><p>音色方面Galaxy Buds Pro的低频更加丰满也更扎实，人声厚度有了显著增强。结合素质上的变化，Galaxy Buds Pro可以说在几乎任一类型的音乐中都具有比Galaxy Buds+更能讨人喜欢的表现，也更加耐听。而在降噪开启后，Galaxy Buds Pro的厚度会被削去一些，音色更加接近Galaxy Buds+一点——<b>也就是说最不济的情况下它仍然强于Galaxy Buds+。</b></p><p>当然了，我们没法真的指望Galaxy Buds Pro可以对Galaxy Buds+构成音质上的代际优势，毕竟后者的起点已经相当高，Galaxy Buds Pro能在优化了佩戴还塞进主动降噪之后音质不降反升已经大幅超出了我的预期——我的预期是它能在实现主动降噪的同时音质不缩水太多就好。</p><p><img alt=\"3.jpg\" src=\"http://pic.zaeke.com/img161221574866215.jpeg\" width=\"2868\" height=\"1417\"><br></p><p>至于360音频，三星为Galaxy Buds Pro加入了运动追踪传感器并引入杜比头部追踪与杜比全景声，实现类似AirPods Pro的空间音频功能。实测包括爱奇艺、腾讯视频、优酷视频和AcFun在内的主流视频应用以及新浪微博内的视频内容都支持360音频。不过对于头部追踪功能，我的态度同AirPods Max评测时一样，依然只是尝个新鲜，这种接近于将立体声开倒车回到单声道的功能对于视频的沉浸体验并无实质帮助，但可以期待它未来在增强现实场景发挥更大的作用——当然了，头部追踪无用不代表杜比全景声无用，开启360音频还是能够提升观影时的环绕感，如果能提供独立的头部追踪开关会更好。</p><p><b>总而言之，三星还将毫无悬念地继续稳坐消费级真无线耳机音质标杆的地位。</b>所以你们看，无论其他人怎么唱衰三星收购哈曼后的AKG，我都没有怀疑过其品牌实力。这事儿不需要争辩，已经推出的四款Galaxy Buds充电盒上那行“Sound by AKG”就足以粉碎一切质疑。</p><p><b style=\"font-size: large;\">给星粉最好的礼物</b></p><p><b>这一次Galaxy Buds Pro无疑是直面AirPods Pro而来的。</b>约22个月前，苹果发布了首款搭载H1芯片的耳机第二代AirPods，直到不久前推出的AirPods Max，H1仍然是保证苹果耳机体验的最强“心脏”，在它的驱动下苹果无线耳机一路所向披靡。</p><p>而除苹果外的整个行业在花了22个月时间后，我们才终于迎来了Galaxy Buds Pro这款综合体验能与苹果系真无线耳机打个有来有回的产品——论降噪抗风噪以及无缝体验，Galaxy Buds Pro与AirPods Pro尚有差距，但要说产品质感、佩戴舒适度、环境音品质和音质，Galaxy Buds Pro却毫不示弱，甚至占有相当明显的优势。如此水准的产品力摆在面前，1299元的定价显然没有留给我们丝毫的质疑空间。</p><p>作为市场地位和产品定位都非常接近的唯二品牌，三星终于也拿出了能与苹果正面抗衡的真无线耳机产品，最高兴的当然还是包括我在内的三星手机忠实用户们。而且不得不承认今年三星中国相当开窍，预定Galaxy Buds Pro送Galaxy Fit2手环、预定Galaxy S21/S21+送Galaxy Buds Pro半价券、预定Galaxy S21Ultra直接送Galaxy Buds Pro，价格和其它赠品服务也比过去厚道了不少。连原本已经因为S21Ultra缩水了充电功率而骂骂咧咧扬言绝对不买的我也还是预定了回来，甚至后悔自己没有参与先行者计划而错过了那枚“S币”。</p><p><img alt=\"DSC_5047.jpg\" src=\"http://pic.zaeke.com/img161221580467175.jpeg\" width=\"1920\" height=\"1280\"><br></p><p>相信被这副好看、好用又好听的耳机挽回心意的三星用户远不止我一个。至于非三星手机用户要不要选择Galaxy Buds Pro，我也简单理一下：缺少全家桶支持损失的基本是无缝连接这种“感知不强”的功能，唯一体验差距略明显的也就是延迟（至于音质落差完全是各个手机品牌的问题，搭配谁家的蓝牙耳机都依旧存在），与那些同样吃不到全家桶延迟优化的传统大牌产品相比，1299元的Galaxy Buds Pro性价比还是很突出。</p><p>最起码，仅仅是综合实力能与AirPods Pro掰腕子这件事儿，Galaxy Buds Pro就值得我们不吝惜喝彩乃至这一次就支持，对不对？</p>', '<p class=\"p_style\">文首先来个真爱粉测验：还记得我们的Galaxy Buds Live评测文章标题是什么吗？<br></p><p class=\"jsmargin\"><img alt=\"1.jpeg\" src=\"http://pic.zaeke.com/img161221524999995.jpeg\" width=\"1400\" height=\"560\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">彼时我们没能预料到的是，仅仅不到半年之后，三星Galaxy Buds系列真无线耳机的最新作不但名字里有“Pro”，产品力也真的Pro了起来。</p><p class=\"jsmargin\"><img alt=\"DSC_5028.jpg\" src=\"http://pic.zaeke.com/img161221527275224.jpeg\" width=\"1920\" height=\"1280\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\"><b style=\"font-size: large;\">外在：稳中求进</b></p><p class=\"p_style\">不能不首先吐槽的是，我刚拿到Galaxy Buds Pro的时候几乎以为这是Galaxy Buds Live的什么新配色，毕竟二者的充电盒造型及尺寸几乎可以说是一模一样，连保护套都可以通用（Galaxy Buds和Galaxy Buds+的保护套通用性甚至也比它们俩差点）。</p><p class=\"jsmargin\"><img alt=\"DSC_5031.jpg\" src=\"http://pic.zaeke.com/img161221530165168.jpeg\" width=\"1920\" height=\"1280\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">当然，除了省模具费用的嫌疑外，我还是愿意给Galaxy Buds Pro的充电盒以好评。用过Galaxy Buds Live的人应该都记得其充电盒是多么小巧，Galaxy Buds Pro同样继承了其体积小、盒盖阻尼感佳的优点，磨砂雾面质感也与金色Galaxy Buds Live一致。我这一副银色版本的充电盒颜色其实应该算浅浅的贝母蓝，充足光线下会反射出珠光。并且它在好看之余也比那些光面塑料货耐久得多，Galaxy Buds Pro的盒子曾被我不经意地从桌面扒拉到了瓷砖地上，也没留下肉眼可见的痕迹。</p><p class=\"jsmargin\"><img alt=\"DSC_5029.jpg\" src=\"http://pic.zaeke.com/img161221538944295.jpeg\" width=\"1920\" height=\"1280\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">所以说如果你的使用习惯不算太糙，那么大可不去为它准备什么保护套，反正裸着的Galaxy Buds Pro也还蛮漂亮的。</p><p class=\"p_style\">不过耳机本体的外观就有些争议了，它不像Galaxy Buds Live的腰果造型那样具备较高的辨识度，远看就只是一块光秃秃的突镜面，紫色倒是不必担心，我这副银色耳机一旦脏了以后视觉上就很明显。而且这大块的触控面也使得Galaxy Buds Pro容易在佩戴或摘下的过程中发生误触，触控面较小的Galaxy Buds+要略友好一点。</p><p class=\"jsmargin\"><img alt=\"DSC_5037.jpg\" src=\"http://pic.zaeke.com/img161221541397734.jpeg\" width=\"1920\" height=\"1280\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"DSC_5036.jpg\" src=\"http://pic.zaeke.com/img161221543631328.jpeg\" width=\"1920\" height=\"1280\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">虽说观感尚存争议，这一次Galaxy Buds Pro的腔体设计在我看来是截止目前所有三星真无线耳机产品中最成功的一个。它的锥形腔体拥有较小的截面积，对耳廓的压力会比Galaxy Buds+明显小一些，椭圆截面短导管配合椭圆截面硅胶套对耳洞的友好度也更高。<b>尽管同为入耳式形态的产品，Galaxy Buds Pro的佩戴舒适度仍显著优于Galaxy Buds+，甚至可以在侧躺状态下舒服地佩戴</b>（AirPods Pro做得到吗？），IPX7防水也令人用起来多几分安心。而对比“好听不舒服、舒服不好听”的Galaxy Buds Live，你也不需要再于音质、隔音和舒适度之间做单选题了。</p><p class=\"jsmargin\"><img alt=\"DSC_5030.jpg\" src=\"http://pic.zaeke.com/img161221545919186.jpeg\" width=\"1920\" height=\"1280\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\"><b style=\"font-size: large;\">体验：不必再看苹果的尾灯</b></p><p class=\"p_style\">恭喜三星，你是安卓阵营中第一个做出综合体验可与AirPods Pro同台较量之真无线耳机产品的品牌。</p><p class=\"p_style\">首先在连接性能方面，自Galaxy Buds+开始搭载的博通BCM43015至今就没换过，所以Galaxy Buds Pro的连接性能表现与Galaxy Buds+以及Galaxy Buds Live并没有明显的区别：配对和连接十分迅速，明显比包括AirPods Pro在内的多数竞品更快；稳定性依然优秀，很少出现断续的情况；搭配S20Ultra时延迟低至可以轻松应对《和平精英》等射击游戏的需求，但仍然只对“游戏中心”内的应用生效。</p><p class=\"jsmargin\"><img alt=\"DSC_5041.jpg\" src=\"http://pic.zaeke.com/img161221550414301.jpeg\" width=\"1920\" height=\"1280\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">需要细说的是两个新功能：无缝耳机连接和延迟调节。<b>无缝耳机连接适用于登陆了同一三星账号的三星设备，</b>从实际体验来说有两点优势：其一是当你将Galaxy Buds Pro与一台三星设备配对之后，它会出现在你的其它三星设备已配对列表中，无需手动配对；其二是在三星设备间切换要比跨品牌（比如S20Ultra和iPhone 12间）切换更加迅速顺畅。但要注意，Galaxy Buds Pro并不支持多设备连接，连接新设备后会与前一个设备断开；它也还不能像AirPods系列那样根据场景在设备间自主切换，所以在我看来与真正的“无缝”尚有距离。</p><p class=\"p_style\">至于One UI在Sound Assitant中新增的延迟调节功能，它适用于所有与其支持的三星设备连接的蓝牙耳机，并不局限于Galaxy Buds系列，但场景限制却比较严苛。实际用下来只对自带的音视频应用起作用，第三方应用哪怕是已经加入游戏中心也不好使。所以就目前而言，除非你用手机看本地视频，否则这个功能是可以忽略的——你问所以在线视频应用的延迟怎么解决？咱还是把它拖进“游戏中心”好了……</p><p class=\"jsmargin\"><img alt=\"2.jpg\" src=\"http://pic.zaeke.com/img161221553060297.jpeg\" width=\"1440\" height=\"1957\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">续航方面，增加了主动降噪还优化了佩戴的Galaxy Buds Pro自然不可能复现Galaxy Buds+单体12小时以上那么夸张的成绩，官方说法其在开启和关闭主动降噪时的播放时间分别为5+13小时和8+20小时。实际使用下来开启降噪+60%音量播放一小时耗电在18-20%，与官称相符——毕竟三星在之前一贯喜欢“反向虚标”，不需要担心其实际续航表现有水分。快速充电和无线充电等前几款Galaxy Buds拥有的特性也没有缺席。Galaxy Buds Pro这次最受群众瞩目的应该就是降噪表现了，毕竟Galaxy Buds Live受限于开放式结构导致其综合降噪效果还不如普通的非主动降噪入耳式耳机（如Galaxy Buds+），包括我在内的星粉们都指望三星这一次能找回颜面来。至于实际表现……我给出的评价是“很不错”。</p><p class=\"p_style\"><b>“不错”的是，Galaxy Buds Pro的主动降噪效果终于达到了当前主动降噪真无线耳机的上游水准，</b>良好的低频噪声削弱效果令其在通勤场景中具备足够高的可用性，办公室里对空调或风扇声的削弱效果也不错。需要注意一点，如果你只在办公室或者不算喧闹的街道上尝试Galaxy Buds Pro的主动降噪，很容易得出它降噪强度较弱的结论。但事实是它的降噪强度调节比多数竞品更加动态，地铁中能明显感受到Galaxy Buds Pro的降噪强度会拔到较高的水准上，对低频噪声的削弱效果会优于AirPods Pro；而在安静的办公室内，较低耳压且佩戴舒适的Galaxy Buds Pro能帮我舒舒服服地睡个午觉，那些或耳压较高或不能侧躺的产品（AirPods Pro两样都占）就做不到这一点。</p><p class=\"jsmargin\"><img alt=\"DSC_5039.jpg\" src=\"http://pic.zaeke.com/img161221556215348.jpeg\" width=\"1920\" height=\"1280\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">不过同多数其它产品一样，Galaxy Buds Pro对中高频噪声的削弱能力要逊色于AirPods Pr的，但不得不说现阶段主动降噪耳机对中高频噪声的削弱效果都不会特别好，真正靠谱的手段还得是ER4那种高强度物理隔绝。排除BOSE QC Earbuds这个独孤求败的产品，再排除Enco X这种中高频噪声削弱拉胯太多的产品（Galaxy Buds Live你继续面壁吧），其它主流的主动降噪真无线耳机在降噪效果方面基本都可以划入同一梯队，彼此间有差距但体验拉不开档次。<b>而且主动降噪对音质也会产生负面影响，Galaxy Buds Pro能在保住消费级TWS音质之冠（这点我们后面细说）的前提下做到称得上较为优秀的降噪效果，我已经知足了。</b></p><p class=\"p_style\">除了降噪强度和耳压优化外，抗风噪性能对降噪体验也有显著影响，这一点上Galaxy Buds Pro优于我手中的OPPO W51、Enco X和小米Air 2 Pro。但跟公认抗风噪最强的AirPods Pro比起来，Galaxy Buds Pro在开启主动降噪或环境音时的风声还是要更加明显。</p><p class=\"jsmargin\"><img alt=\"DSC_5048.jpg\" src=\"http://pic.zaeke.com/img161221559278147.jpeg\" width=\"1920\" height=\"1280\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\"><b>值得欣慰的是Galaxy Buds Pro的通话效果非常不错，受风声的干扰很小，</b>这点就很符合三星对麦克风抗风噪设计的描述，毕竟Galaxy Buds Live就已经搞定了通话风噪，作为后继产品的Galaxy Buds Pro自然没道理把牙膏再吸回去（仅支持25W充电的Galaxy S21Ultra：有被冒犯到）。而且优秀的通话效果也让我有理由猜测当前Galaxy Buds Pro的降噪/环境音下抗风噪效果不够完美的原因可能是软件优化欠佳造成的，毕竟它实现通话降噪的手段并非如Galaxy Buds Pro那样通过额外的骨振动传感器，而是纯粹依靠麦克风设计和软件算法，所以不完全排除其抗风噪效果在未来通过固件更新得到一定改善的可能性。</p><p class=\"p_style\">至于Bixby语音唤醒，安静环境下的唤醒成功率跟AirPods Pro的Hey Siri差别不大，但在不那么安静的户外环境中唤醒成功率和指令识别率都不那么可靠，这一点比起Galaxy Buds Live区别不大。不过我本人一直将之视为一个带有公开处刑色彩所以从不使用的功能，而对你有没有用还需要你自己来回答。</p><p class=\"p_style\">前面这些项目还不至于令人觉得Galaxy Buds Pro身怀了什么“绝技”，而接下来才是重头戏——此前还没有任何产品能够冲击AirPods Pro的项目：环境音。</p><p class=\"jsmargin\"><img alt=\"DSC_5032.jpg\" src=\"http://pic.zaeke.com/img161221562987318.jpeg\" width=\"1920\" height=\"1280\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">论主动降噪、抗风噪以及通话质量，其实早已有产品能与AirPods Pro比肩甚至实现较大程度的超越，但唯有环境音品质这一项苹果面前还没有任何产品能构成威胁，直到Galaxy Buds Pro的出现。<b>毫不夸张地说，在环境音这一项目中，Galaxy Buds Pro与AirPods Pro的表现伯仲之间，二者的环境音品质都相当自然清晰真实，接近未佩戴耳机的体验。</b>唯一拖了三星后腿的就是我刚刚讲过的Galaxy Buds Pro抗风噪目前还不够强，有风环境下的表现要弱一些。</p><p class=\"p_style\">但Galaxy Buds Pro还有其它的优势可讲。首先它的环境音量有四档可调，你可以根据使用需要将环境音量调小或调大一些，选择更加灵活。我个人推荐中或者小音量，太高的环境音量容易使突发噪声惊吓到自己。</p><p class=\"p_style\"><span style=\"font-size: 10.5pt;\">然后三星还为Galaxy Buds Pro增加了语音检测功能，这一功能同索尼在WH-1000XM4加入的智能免摘对话类似，耳机在检测到用户</span>说话时会自动降低音量并切换到环境音，10秒内没有说话则切换回主动降噪，这使得你在结账等场景中免去了摘下耳机或手动切换到环境音的麻烦。不过需要注意，它和WH-1000XM4一样会被咳嗽或清嗓的声音激活，如果三星能够像索尼那样提供灵敏度调节，这一功能的体验会更趋完美。</p><p class=\"jsmargin\"><img alt=\"DSC_5040.jpg\" src=\"http://pic.zaeke.com/img161221567955102.jpeg\" width=\"1920\" height=\"1280\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\"><b>小结下Galaxy Buds Pro的使用体验：在发布十四个月后，AirPods Pro的最后一道“叹息之壁”即环境音功能终于被Galaxy Buds Pro攻破，三星甚至还整了更多的“花活儿”出来。</b>而其它方面二者的体验差距也拉近了不少，距离AirPods Pro推出已经过去了15个月，安卓阵营终于拿出了一款可与之正面对抗的产品——但愿传说中的第二代AirPods Pro不要很快推出吧（笑）。</p><p class=\"p_style\"><b style=\"font-size: large;\">音质：唯有我，能战胜我</b></p><p class=\"p_style\">在Galaxy Buds+推出后的这一年时间里，我一直将其视为消费级真无线耳机的音质标杆。不考虑某些HiFi品牌所推出的售价离谱体验更离谱的奇葩产品，在“像是给人用的东西”这一范畴内，Galaxy Buds+几乎始终是调音与素质的综合最优解。我所评测过的上一款音质T0级别真无线耳机声阔降噪舱也只是素质比较Galaxy Buds+稍占优势，并未实现完全超越。</p><p class=\"p_style\">而Galaxy Buds Pro则在这两方面都有更好的表现。</p><p class=\"jsmargin\"><img alt=\"DSC_5043.jpg\" src=\"http://pic.zaeke.com/img161221571472810.jpeg\" width=\"1920\" height=\"1280\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">推荐均衡器设置依然是“有活力”，最符合多数人的喜好。因为Galaxy Buds+不具备主动降噪功能，所以二者的音质比较主要参考关闭Galaxy Buds Pro降噪时的发挥。</p><p class=\"p_style\">比起Galaxy Buds+，Galaxy Buds Pro的音色细腻度和密度都有所提高，这令其拥有更好的鲜活度。与此同时Galaxy Buds Pro在声场广度和动态方面也更具优势，听起来要更加起伏大气一点。</p><p class=\"p_style\">音色方面Galaxy Buds Pro的低频更加丰满也更扎实，人声厚度有了显著增强。结合素质上的变化，Galaxy Buds Pro可以说在几乎任一类型的音乐中都具有比Galaxy Buds+更能讨人喜欢的表现，也更加耐听。而在降噪开启后，Galaxy Buds Pro的厚度会被削去一些，音色更加接近Galaxy Buds+一点——<b>也就是说最不济的情况下它仍然强于Galaxy Buds+。</b></p><p class=\"p_style\">当然了，我们没法真的指望Galaxy Buds Pro可以对Galaxy Buds+构成音质上的代际优势，毕竟后者的起点已经相当高，Galaxy Buds Pro能在优化了佩戴还塞进主动降噪之后音质不降反升已经大幅超出了我的预期——我的预期是它能在实现主动降噪的同时音质不缩水太多就好。</p><p class=\"jsmargin\"><img alt=\"3.jpg\" src=\"http://pic.zaeke.com/img161221574866215.jpeg\" width=\"2868\" height=\"1417\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">至于360音频，三星为Galaxy Buds Pro加入了运动追踪传感器并引入杜比头部追踪与杜比全景声，实现类似AirPods Pro的空间音频功能。实测包括爱奇艺、腾讯视频、优酷视频和AcFun在内的主流视频应用以及新浪微博内的视频内容都支持360音频。不过对于头部追踪功能，我的态度同AirPods Max评测时一样，依然只是尝个新鲜，这种接近于将立体声开倒车回到单声道的功能对于视频的沉浸体验并无实质帮助，但可以期待它未来在增强现实场景发挥更大的作用——当然了，头部追踪无用不代表杜比全景声无用，开启360音频还是能够提升观影时的环绕感，如果能提供独立的头部追踪开关会更好。</p><p class=\"p_style\"><b>总而言之，三星还将毫无悬念地继续稳坐消费级真无线耳机音质标杆的地位。</b>所以你们看，无论其他人怎么唱衰三星收购哈曼后的AKG，我都没有怀疑过其品牌实力。这事儿不需要争辩，已经推出的四款Galaxy Buds充电盒上那行“Sound by AKG”就足以粉碎一切质疑。</p><p class=\"p_style\"><b style=\"font-size: large;\">给星粉最好的礼物</b></p><p class=\"p_style\"><b>这一次Galaxy Buds Pro无疑是直面AirPods Pro而来的。</b>约22个月前，苹果发布了首款搭载H1芯片的耳机第二代AirPods，直到不久前推出的AirPods Max，H1仍然是保证苹果耳机体验的最强“心脏”，在它的驱动下苹果无线耳机一路所向披靡。</p><p class=\"p_style\">而除苹果外的整个行业在花了22个月时间后，我们才终于迎来了Galaxy Buds Pro这款综合体验能与苹果系真无线耳机打个有来有回的产品——论降噪抗风噪以及无缝体验，Galaxy Buds Pro与AirPods Pro尚有差距，但要说产品质感、佩戴舒适度、环境音品质和音质，Galaxy Buds Pro却毫不示弱，甚至占有相当明显的优势。如此水准的产品力摆在面前，1299元的定价显然没有留给我们丝毫的质疑空间。</p><p class=\"p_style\">作为市场地位和产品定位都非常接近的唯二品牌，三星终于也拿出了能与苹果正面抗衡的真无线耳机产品，最高兴的当然还是包括我在内的三星手机忠实用户们。而且不得不承认今年三星中国相当开窍，预定Galaxy Buds Pro送Galaxy Fit2手环、预定Galaxy S21/S21+送Galaxy Buds Pro半价券、预定Galaxy S21Ultra直接送Galaxy Buds Pro，价格和其它赠品服务也比过去厚道了不少。连原本已经因为S21Ultra缩水了充电功率而骂骂咧咧扬言绝对不买的我也还是预定了回来，甚至后悔自己没有参与先行者计划而错过了那枚“S币”。</p><p class=\"jsmargin\"><img alt=\"DSC_5047.jpg\" src=\"http://pic.zaeke.com/img161221580467175.jpeg\" width=\"1920\" height=\"1280\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">相信被这副好看、好用又好听的耳机挽回心意的三星用户远不止我一个。至于非三星手机用户要不要选择Galaxy Buds Pro，我也简单理一下：缺少全家桶支持损失的基本是无缝连接这种“感知不强”的功能，唯一体验差距略明显的也就是延迟（至于音质落差完全是各个手机品牌的问题，搭配谁家的蓝牙耳机都依旧存在），与那些同样吃不到全家桶延迟优化的传统大牌产品相比，1299元的Galaxy Buds Pro性价比还是很突出。</p><p class=\"p_style\">最起码，仅仅是综合实力能与AirPods Pro掰腕子这件事儿，Galaxy Buds Pro就值得我们不吝惜喝彩乃至这一次就支持，对不对？</p>', 'http://pic.zaeke.com/img161355638391582.jpeg', 4368, 0, 0, NULL, '2021-02-01 13:44:21', '2021-03-27 07:33:25');
INSERT INTO `article` VALUES (16022, '静无止境的小鸟Libratone AIR+第2代降噪真无线耳机，处处彰显不同', '真无线耳机历经了几年的高速发展，其发展势头不仅没有丝毫减弱，反而呈现出持续性飞速发展。这不，刚迈入2021年，已经有不少具备主动降噪特性的真无线耳机陆续亮相，仿...', '[object Object]', '小啤', 170, 'http://pic.zaeke.com/user_1533281478_8.png', '<p>真无线耳机历经了几年的高速发展，其发展势头不仅没有丝毫减弱，反而呈现出持续性飞速发展。这不，刚迈入 2021 年，已经有不少具备主动降噪特性的真无线耳机陆续亮相，仿佛主动降噪功能已经成为了中、高端定位真无线耳机产品的一张无形入场券。</p><p style=\"text-align: center;\"><strong>这是一只赏心悦目的“小鸟”，并且独一无二</strong></p><p>也许不少人对 Libratone（小鸟音响）还比较陌生，尽管诞生于丹麦哥本哈根的 Libratone 成立于 2009 年，可是该品牌其实 2014 年才正式宣布进入国内市场。Libratone 作为率先推出兼容苹果 AirPlay 技术的蓝牙音箱品牌，凭借其在无线音箱产品方面丰富的研发经验，他们近年推出了多款极具品牌独特风格的无线耳机，包括旗下几款极具特色的真无线耳机。</p><p>时隔一年半，小鸟带来了Libratone AIR+ 的第 2 代降噪真无线耳机，这款拥有旗舰定位的真无线耳机产品，在外形设计方面可谓是费尽心思。注：为了提升文章的整体阅读性，接下来“小鸟 Libratone AIR+ 第 2 代降噪真无线耳机”将被简称为“Libratone AIR+”。</p><p><img src=\"http://pic.zaeke.com/img161218238050142.jpeg\" alt=\"IMG_0530.jpg\"></p><p>彰显其独特设计的源泉，就数这款耳机的充电仓设计。与大部分追求极简化风格的真无线耳机产品与众不同的地方在于，该耳机的充电仓正面被赋予了一个醒目的 Libratone 金属铭牌&nbsp;LOGO，并且采用了独特的斜开盖子设计，配合磨砂质感的机身可起到防滑、防刮、防指纹沾染的优势。当然，在实际体验中，由于其开盖方式比较特殊，导致在单手开启充电仓盖时，还是比较容易出现开盖打滑情况，需要一定的技巧才能实现单手的灵活开启。</p><p><img src=\"http://pic.zaeke.com/img161218239552055.jpeg\" alt=\"IMG_0547.jpg\"></p><p>打开充电仓的盖子后，可以发现 Libratone AIR+ 耳机主体直接呈现在眼前，充电仓与耳机主体之间通过巧妙的磁吸特性，使两者牢固地实现收纳。这种直立设计方案不仅视觉上给人一种新颖的观感，其在取/放的体验上也稍有不同，考虑到耳机主体大部分地方都没有被充电仓阻挡，因此它可以更轻松地从充电仓里被取出，摆放体验也是如此。有个值得分享的小细节，就是每当打开该耳机的充电仓时，其“L”“R” L/R 标识都会随之亮起，给人一种迎宾灯的仪式感，同时“L”“R”标识也肩负起展示单耳电量状态的作用，这些细节对于质感提升有一定帮助，也为用户在弱光环境下对取/放耳机主体给予一定的便利。</p><p><img src=\"http://pic.zaeke.com/img161218249183726.jpeg\" alt=\"IMG_0594.jpg\"></p><p>除此，Libratone&nbsp;AIR+ 的外在设计细致到什么程度？这款耳机的充电仓接口处特意加入了一圈充电状态指示灯，补电状态下灯光常亮，待电量完全补电完毕之后灯光随之熄灭。同时只需短按配对按钮状态指示灯，通过“L”“R”标识的闪烁次数，用户可快速查询充电仓的电量状态，例如闪烁3次表示“高电量”，闪烁2次表示“中电量”，闪烁1次“低电量”，当呈现红灯是代表耳机需要及时补电了。</p><p>不仅如此，该耳机的充电仓本身是支持无线充电特性的，实测 Qi 标准无线充电器可以为其正常补电。看上去上述设计都不是必须的，可是恰好是有了这些特性的加持，使这款耳机可彰显出一种与众不同的使用体验。<br></p><p style=\"text-align: center;\"><strong>静享音乐是基本功，超低延迟是惊喜</strong></p><p>该耳机的配对方式也相对常规，打开耳机盖子之后，确保耳机保持正常收纳状态，再长按机身背面的配对按钮，待充电仓内的 L/R 标识闪烁，即可在设备中的蓝牙列表中发现“Libratone&nbsp;AIR+”，然后选择配对即可。</p><p><img src=\"http://pic.zaeke.com/img161218235275635.jpeg\" alt=\"IMG_0524.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161218275514811.jpeg\" alt=\"IMG_0553.jpg\">  </p><p>可以发现，Libratone AIR+ 提供了大、中、小三种耳帽尺寸，耳帽的数量不算丰富，并不一定能覆盖所有用户对于耳帽的尺寸喜好或需求。为了呈现出最佳的降噪效果和声音体验，Libratone AIR+ 配合同名的 Libratone APP 中的“佩戴贴合度”测试，在较安静的测试环境下，该测试系统可以在几秒内得出用户选择的耳帽是否通过贴合度检验，协助用户正确选择合适尺寸的耳帽。</p><p><img src=\"http://pic.zaeke.com/img161218260612412.jpeg\" alt=\"IMG_0555.jpg\"></p><p>在降噪体验方面，这款耳机分别提供了透明模式、舒适模式、可调模式，其中透明模式更接近于半入耳式的听感体验，佩戴者可正常接收周围环境音，从根本削减耳朵压感；舒适模式则可通过对环境音的判断，自动采取不同的降噪强度，让降噪量自动适配您周围的环境，无需繁琐地手动调节降噪强度；可调模式，顾名思义就是通过个人喜好，自动调节需要的降噪强度。</p><p><img src=\"http://pic.zaeke.com/img161218301150628.jpeg\" alt=\"31323.jpg\"></p><p>实测发现，在室内环境，音乐音量设置为三分之一大小，然后开启至中等降噪强度之下，虽然能感知 ZAEKE知客工作室小伙伴之间在进行对谈，可是基本已经无法听清楚他们之间的对话内容，可大幅度降低音乐以外的环境音。不难发现，其实 Libratone AIR+ 比较擅长过滤一些相对持续的噪音，例如高铁行驶时的轮轨噪音、电脑主机的风扇噪音、马路上的汽车行驶声等。其实这款耳机的底噪把控得相对出色，即便在降噪模式下也能带来相对纯净的听感体验。因此，ibratone AIR+ 的主动降噪效果仍然是个可圈可点的加分项。</p><p><img src=\"http://pic.zaeke.com/img161218297855658.jpeg\" alt=\"IMG_0559.jpg\"></p><p>当然，Libratone AIR+ 在长时间的降噪模式下，还是能让我感受到一定的耳压感。除此之外，以我的主观佩戴体验而言，由于这款耳机的腔体的体积较大的缘故，在佩戴 30 分钟之后耳甲腔就会感受到比较明显的异物感，待耳机摘下来之后，甚至可以触摸到腔体所造成的佩戴压痕，即便替换了不同尺寸的耳帽之后，问题依旧。当然，耳机佩戴舒适性这个问题还是需要因人而异，毕竟每个人的耳朵轮廓存在个体差异。另外，具备佩戴检测功能的 Libratone AIR+，还有一个小细节功能特性，在开启摘下暂停功能的时候，摘掉一只耳机，另外一只耳机将会自动切换至透明模式，迅速平衡两耳的听感体验。</p><p>提供了多种音效可供用户自由选择，其中智能调音模式可针对部分用户无法通过耳塞尺寸大小及角度解决低频泄露的情况下，实现了对低频量感的智能调节，好让所有用户可以获得最佳的听感体验。提及芯片，Libratone AIR+ 率先采用高通旗舰 QCC5141 芯片，其拥有强大的四核处理能力，再配合高通 TrueWireless Mirroring （真无线镜像）技术，可以提升真无线耳机在单/双耳切换使用时的连接稳定性。因此，可以感受到 Libratone AIR+ 在连接速度和抗干扰能力方面尤为突出。</p><p><img src=\"http://pic.zaeke.com/img161218268167429.jpeg\" alt=\"IMG_0561.jpg\"></p><p>除了基本的连接稳定性，延迟表现也是真无线耳机中不可忽视的重要指标。使用 Libratone&nbsp;AIR+ 与一加 8 进行组合使用时，前者凭借蓝牙 5.2 和支持高通 aptX 音频编解码技术双特性加持，使得它在上手《和平精英》和《使命召唤手游》时，均可以带来较低的音频延迟表现，大约延迟在 200 毫秒左右，基本触发武器的时候，声音即可快速传递至耳朵，延迟表现属于众多真无线耳机里的中上水平。</p><p>在游戏的体验中，这款耳机对于多个方向传递的武器、脚步声、载具等音效有着较好的分离度，通过耳机大致能判断这些声音的传递方向，能感受到这款耳机对于低频还是追求一种相对清澈的质感，即便在游戏之中也不例外。既然能兼顾基本的游戏音频延迟体验，这款耳机面对流媒体视频程序时，自然就不在话下了，实测在腾讯视频、抖音、哔哩哔哩几款人气视频平台中，都能带来让人满意的低延迟音频表现，不会有显著的嘴型对不上的高延迟情况。</p><p><img src=\"http://pic.zaeke.com/img161218262984292.jpeg\" alt=\"5435.jpg\"></p><p>作为 Libratone 旗下最高端的真无线耳机产品，Libratone AIR+ 在音乐的表现力方面，其三频还是倾向于均衡的调教，可以感受到这款耳机对于音染的修饰较少，而且不俗的中频表现可以起到突出人声作用，倾向于追求声音的还原性。考虑到每个人对于三频表现的喜好存在差异，用户也可以通过 Libratone APP 自由选择符合个人喜好的音效。</p><p><img src=\"http://pic.zaeke.com/img161218272252817.jpeg\" alt=\"IMG_0544.jpg\"></p><p>特别在使用该耳机欣赏《Treaty》 的时候，我能透过这款耳机感受到歌手 Leonard Cohen 的低沉浑厚的嗓音魅力，在张远的《嘉宾》和陈奕迅的《全世界失眠》中，可以感受到这款耳机总是能把歌手的嗓音突显出来，仿佛在告诉大家别忘记了歌手才是歌曲的灵魂，乐器只是陪衬。</p><p><img src=\"http://pic.zaeke.com/img161218281524173.jpeg\" alt=\"IMG_0552.jpg\"></p><p>值得一提的是，Libratone&nbsp;AIR+ 并没有采用语音播报提示音，而是采用了相对柔和的音效，不会对我日常的音乐欣赏和娱乐需求造成太大干扰，这是一个能打动我的小细节。续航方面，实测该耳机在三分之一音量，且大部分情况开启降噪模式之下，据说可以提供单次 6 小时的续航能力，并且充电仓可额外提供 3 次充电能力。</p><p style=\"text-align: center;\"><strong>这只设计独特的“小鸟”，更有静享醇音体验</strong></p><p>产品的外观设计无疑是打动人心的敲门砖，Libratone&nbsp;AIR+ 凭借在外观设计上的独特性，使其可以在一众真无线耳机产品中脱颖而出，丰富了消费者对于真耳无线机在外观设计需求方面的选择空间。不仅如此，其实 Libratone&nbsp;AIR+ 还拥有不俗的机身做工，除了充电仓与耳机主体做到了严丝合缝以外，充电仓两处的灯效加持，一定程度上提高了耳机本身的调性。</p><p><img src=\"http://pic.zaeke.com/img161218279780378.jpeg\" alt=\"IMG_0596.jpg\"></p><p>同样，都 2021 年了，感觉今年不具备主动降噪特性的真无线耳机，都不好意思自诩旗舰级定位。具备了主动降噪特性的 Libratone&nbsp;AIR+，其实它在降噪部分给我带来最深刻的印象并非出色的降噪效果，反而是这款产品在降噪模式下做到了对底噪有着出色的把控能力，便于营造出相对纯净的音频表现。音质方面，相对平衡的调音风格，迎合了追求真实和自然听感的受众群体，属于耐听型产品。</p><p><img src=\"http://pic.zaeke.com/img161218269819877.jpeg\" alt=\"IMG_0541.jpg\"></p><p>与独特设计、做工相匹配的地方，还有小鸟 Libratone AIR+ 第 2 代的定价。官方定价 1598 元的小鸟 Libratone AIR+ 第 2 代降噪真无线耳机，确实匹配了一个足够“旗舰”的定价。 综合上述体验，不难发现这款产品无论是外在和内在都足以支撑起上千元的价格，只是目前 Libratone 的品牌知名度与一般音频大厂之间还存在较大的差距。当然，小鸟凭借自身差异化的设计风格，一定程度上契合了那些本就不愿意追逐主流的消费群体，这何尝不是一种享受的生活态度？</p>', '<p class=\"p_style\">真无线耳机历经了几年的高速发展，其发展势头不仅没有丝毫减弱，反而呈现出持续性飞速发展。这不，刚迈入 2021 年，已经有不少具备主动降噪特性的真无线耳机陆续亮相，仿佛主动降噪功能已经成为了中、高端定位真无线耳机产品的一张无形入场券。</p><p style=\"text-align: center;\" class=\"p_style\"><strong>这是一只赏心悦目的“小鸟”，并且独一无二</strong></p><p class=\"p_style\">也许不少人对 Libratone（小鸟音响）还比较陌生，尽管诞生于丹麦哥本哈根的 Libratone 成立于 2009 年，可是该品牌其实 2014 年才正式宣布进入国内市场。Libratone 作为率先推出兼容苹果 AirPlay 技术的蓝牙音箱品牌，凭借其在无线音箱产品方面丰富的研发经验，他们近年推出了多款极具品牌独特风格的无线耳机，包括旗下几款极具特色的真无线耳机。</p><p class=\"p_style\">时隔一年半，小鸟带来了Libratone AIR+ 的第 2 代降噪真无线耳机，这款拥有旗舰定位的真无线耳机产品，在外形设计方面可谓是费尽心思。注：为了提升文章的整体阅读性，接下来“小鸟 Libratone AIR+ 第 2 代降噪真无线耳机”将被简称为“Libratone AIR+”。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161218238050142.jpeg\" alt=\"IMG_0530.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">彰显其独特设计的源泉，就数这款耳机的充电仓设计。与大部分追求极简化风格的真无线耳机产品与众不同的地方在于，该耳机的充电仓正面被赋予了一个醒目的 Libratone 金属铭牌&nbsp;LOGO，并且采用了独特的斜开盖子设计，配合磨砂质感的机身可起到防滑、防刮、防指纹沾染的优势。当然，在实际体验中，由于其开盖方式比较特殊，导致在单手开启充电仓盖时，还是比较容易出现开盖打滑情况，需要一定的技巧才能实现单手的灵活开启。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161218239552055.jpeg\" alt=\"IMG_0547.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">打开充电仓的盖子后，可以发现 Libratone AIR+ 耳机主体直接呈现在眼前，充电仓与耳机主体之间通过巧妙的磁吸特性，使两者牢固地实现收纳。这种直立设计方案不仅视觉上给人一种新颖的观感，其在取/放的体验上也稍有不同，考虑到耳机主体大部分地方都没有被充电仓阻挡，因此它可以更轻松地从充电仓里被取出，摆放体验也是如此。有个值得分享的小细节，就是每当打开该耳机的充电仓时，其“L”“R” L/R 标识都会随之亮起，给人一种迎宾灯的仪式感，同时“L”“R”标识也肩负起展示单耳电量状态的作用，这些细节对于质感提升有一定帮助，也为用户在弱光环境下对取/放耳机主体给予一定的便利。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161218249183726.jpeg\" alt=\"IMG_0594.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">除此，Libratone&nbsp;AIR+ 的外在设计细致到什么程度？这款耳机的充电仓接口处特意加入了一圈充电状态指示灯，补电状态下灯光常亮，待电量完全补电完毕之后灯光随之熄灭。同时只需短按配对按钮状态指示灯，通过“L”“R”标识的闪烁次数，用户可快速查询充电仓的电量状态，例如闪烁3次表示“高电量”，闪烁2次表示“中电量”，闪烁1次“低电量”，当呈现红灯是代表耳机需要及时补电了。</p><p class=\"p_style\">不仅如此，该耳机的充电仓本身是支持无线充电特性的，实测 Qi 标准无线充电器可以为其正常补电。看上去上述设计都不是必须的，可是恰好是有了这些特性的加持，使这款耳机可彰显出一种与众不同的使用体验。<br></p><p style=\"text-align: center;\" class=\"p_style\"><strong>静享音乐是基本功，超低延迟是惊喜</strong></p><p class=\"p_style\">该耳机的配对方式也相对常规，打开耳机盖子之后，确保耳机保持正常收纳状态，再长按机身背面的配对按钮，待充电仓内的 L/R 标识闪烁，即可在设备中的蓝牙列表中发现“Libratone&nbsp;AIR+”，然后选择配对即可。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161218235275635.jpeg\" alt=\"IMG_0524.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161218275514811.jpeg\" alt=\"IMG_0553.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">可以发现，Libratone AIR+ 提供了大、中、小三种耳帽尺寸，耳帽的数量不算丰富，并不一定能覆盖所有用户对于耳帽的尺寸喜好或需求。为了呈现出最佳的降噪效果和声音体验，Libratone AIR+ 配合同名的 Libratone APP 中的“佩戴贴合度”测试，在较安静的测试环境下，该测试系统可以在几秒内得出用户选择的耳帽是否通过贴合度检验，协助用户正确选择合适尺寸的耳帽。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161218260612412.jpeg\" alt=\"IMG_0555.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">在降噪体验方面，这款耳机分别提供了透明模式、舒适模式、可调模式，其中透明模式更接近于半入耳式的听感体验，佩戴者可正常接收周围环境音，从根本削减耳朵压感；舒适模式则可通过对环境音的判断，自动采取不同的降噪强度，让降噪量自动适配您周围的环境，无需繁琐地手动调节降噪强度；可调模式，顾名思义就是通过个人喜好，自动调节需要的降噪强度。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161218301150628.jpeg\" alt=\"31323.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">实测发现，在室内环境，音乐音量设置为三分之一大小，然后开启至中等降噪强度之下，虽然能感知 ZAEKE知客工作室小伙伴之间在进行对谈，可是基本已经无法听清楚他们之间的对话内容，可大幅度降低音乐以外的环境音。不难发现，其实 Libratone AIR+ 比较擅长过滤一些相对持续的噪音，例如高铁行驶时的轮轨噪音、电脑主机的风扇噪音、马路上的汽车行驶声等。其实这款耳机的底噪把控得相对出色，即便在降噪模式下也能带来相对纯净的听感体验。因此，ibratone AIR+ 的主动降噪效果仍然是个可圈可点的加分项。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161218297855658.jpeg\" alt=\"IMG_0559.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">当然，Libratone AIR+ 在长时间的降噪模式下，还是能让我感受到一定的耳压感。除此之外，以我的主观佩戴体验而言，由于这款耳机的腔体的体积较大的缘故，在佩戴 30 分钟之后耳甲腔就会感受到比较明显的异物感，待耳机摘下来之后，甚至可以触摸到腔体所造成的佩戴压痕，即便替换了不同尺寸的耳帽之后，问题依旧。当然，耳机佩戴舒适性这个问题还是需要因人而异，毕竟每个人的耳朵轮廓存在个体差异。另外，具备佩戴检测功能的 Libratone AIR+，还有一个小细节功能特性，在开启摘下暂停功能的时候，摘掉一只耳机，另外一只耳机将会自动切换至透明模式，迅速平衡两耳的听感体验。</p><p class=\"p_style\">提供了多种音效可供用户自由选择，其中智能调音模式可针对部分用户无法通过耳塞尺寸大小及角度解决低频泄露的情况下，实现了对低频量感的智能调节，好让所有用户可以获得最佳的听感体验。提及芯片，Libratone AIR+ 率先采用高通旗舰 QCC5141 芯片，其拥有强大的四核处理能力，再配合高通 TrueWireless Mirroring （真无线镜像）技术，可以提升真无线耳机在单/双耳切换使用时的连接稳定性。因此，可以感受到 Libratone AIR+ 在连接速度和抗干扰能力方面尤为突出。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161218268167429.jpeg\" alt=\"IMG_0561.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">除了基本的连接稳定性，延迟表现也是真无线耳机中不可忽视的重要指标。使用 Libratone&nbsp;AIR+ 与一加 8 进行组合使用时，前者凭借蓝牙 5.2 和支持高通 aptX 音频编解码技术双特性加持，使得它在上手《和平精英》和《使命召唤手游》时，均可以带来较低的音频延迟表现，大约延迟在 200 毫秒左右，基本触发武器的时候，声音即可快速传递至耳朵，延迟表现属于众多真无线耳机里的中上水平。</p><p class=\"p_style\">在游戏的体验中，这款耳机对于多个方向传递的武器、脚步声、载具等音效有着较好的分离度，通过耳机大致能判断这些声音的传递方向，能感受到这款耳机对于低频还是追求一种相对清澈的质感，即便在游戏之中也不例外。既然能兼顾基本的游戏音频延迟体验，这款耳机面对流媒体视频程序时，自然就不在话下了，实测在腾讯视频、抖音、哔哩哔哩几款人气视频平台中，都能带来让人满意的低延迟音频表现，不会有显著的嘴型对不上的高延迟情况。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161218262984292.jpeg\" alt=\"5435.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">作为 Libratone 旗下最高端的真无线耳机产品，Libratone AIR+ 在音乐的表现力方面，其三频还是倾向于均衡的调教，可以感受到这款耳机对于音染的修饰较少，而且不俗的中频表现可以起到突出人声作用，倾向于追求声音的还原性。考虑到每个人对于三频表现的喜好存在差异，用户也可以通过 Libratone APP 自由选择符合个人喜好的音效。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161218272252817.jpeg\" alt=\"IMG_0544.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">特别在使用该耳机欣赏《Treaty》 的时候，我能透过这款耳机感受到歌手 Leonard Cohen 的低沉浑厚的嗓音魅力，在张远的《嘉宾》和陈奕迅的《全世界失眠》中，可以感受到这款耳机总是能把歌手的嗓音突显出来，仿佛在告诉大家别忘记了歌手才是歌曲的灵魂，乐器只是陪衬。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161218281524173.jpeg\" alt=\"IMG_0552.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">值得一提的是，Libratone&nbsp;AIR+ 并没有采用语音播报提示音，而是采用了相对柔和的音效，不会对我日常的音乐欣赏和娱乐需求造成太大干扰，这是一个能打动我的小细节。续航方面，实测该耳机在三分之一音量，且大部分情况开启降噪模式之下，据说可以提供单次 6 小时的续航能力，并且充电仓可额外提供 3 次充电能力。</p><p style=\"text-align: center;\" class=\"p_style\"><strong>这只设计独特的“小鸟”，更有静享醇音体验</strong></p><p class=\"p_style\">产品的外观设计无疑是打动人心的敲门砖，Libratone&nbsp;AIR+ 凭借在外观设计上的独特性，使其可以在一众真无线耳机产品中脱颖而出，丰富了消费者对于真耳无线机在外观设计需求方面的选择空间。不仅如此，其实 Libratone&nbsp;AIR+ 还拥有不俗的机身做工，除了充电仓与耳机主体做到了严丝合缝以外，充电仓两处的灯效加持，一定程度上提高了耳机本身的调性。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161218279780378.jpeg\" alt=\"IMG_0596.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">同样，都 2021 年了，感觉今年不具备主动降噪特性的真无线耳机，都不好意思自诩旗舰级定位。具备了主动降噪特性的 Libratone&nbsp;AIR+，其实它在降噪部分给我带来最深刻的印象并非出色的降噪效果，反而是这款产品在降噪模式下做到了对底噪有着出色的把控能力，便于营造出相对纯净的音频表现。音质方面，相对平衡的调音风格，迎合了追求真实和自然听感的受众群体，属于耐听型产品。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161218269819877.jpeg\" alt=\"IMG_0541.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">与独特设计、做工相匹配的地方，还有小鸟 Libratone AIR+ 第 2 代的定价。官方定价 1598 元的小鸟 Libratone AIR+ 第 2 代降噪真无线耳机，确实匹配了一个足够“旗舰”的定价。 综合上述体验，不难发现这款产品无论是外在和内在都足以支撑起上千元的价格，只是目前 Libratone 的品牌知名度与一般音频大厂之间还存在较大的差距。当然，小鸟凭借自身差异化的设计风格，一定程度上契合了那些本就不愿意追逐主流的消费群体，这何尝不是一种享受的生活态度？</p>', 'http://pic.zaeke.com/img161355640883481.jpeg', 2252, 0, 0, NULL, '2021-02-04 18:04:19', '2021-03-27 05:01:17');
INSERT INTO `article` VALUES (16063, 'vivo X60 Pro+拍照专项评测：大底、蔡司与微云台，这回无需抉择', '如果你和我一样是个很在乎手机拍照表现、不那么在乎其它体验的用户，感觉自己没必要多加几千块钱去买用不着的双扬声器、2K屏等功能，那么vivoX60Pro+或许是目...', '[object Object]', '烫头孙师傅', 38, 'http://pic.zaeke.com/user_1533281477_5.png', '<p>如果你和我一样是个很在乎手机拍照表现、不那么在乎其它体验的用户，感觉自己没必要多加几千块钱去买用不着的双扬声器、2K屏等功能，那么vivo X60 Pro+或许是目前的唯一的选择。</p><p>尽管价格只是5K档位，可从相机硬件与拍照表现上来看，vivo X60 Pro+在拍照表现上却足以和友商们万元级的产品一较高下如果你真的非常重视拍照而在别的硬件和体验上比较佛系的话，价格相对便宜的vivo X60 Pro+甚至可以说上一句性价比很高。</p><p><img src=\"http://pic.zaeke.com/img161242009099911.jpeg\" alt=\"591A7958.jpg\">  </p><p>vivo X60 Pro+的主摄采用三星将于感光元件配备F1.57的大光圈镜头，支持光学防抖。而超广角相机则采用索尼IMX598感光元件，并配备了微云台技术。而2X人像相机则搭载三星GD1感光元件；5X长焦相机则为800万像素，配备OV08A10感光元件。</p><p>而在拥有强大四摄模组的同时，vivo X60 Pro+也拉来了蔡司背书，该机四颗后置相机均配备蔡司当家的T*镀膜不说，实际抗眩光表现也有非常显著的进步，并且蔡司的影像专业性与品牌价值也能够进一步赋予vivo X60 Pro+在影像上的竞争力。</p><p><img src=\"http://pic.zaeke.com/img161242243439131.jpeg\" alt=\"591A7966.jpg\">  </p><p>这种很不水桶的定位或许看起来距离很多人追求的全能旗舰距离不小，不过这种用户定位清晰的产品也同样很有市场。只要拍照这块长板做得足够长，vivo X60 Pro+就会拥有讨人喜欢的本钱。</p><p style=\"text-align: center;\"><strong style=\"font-size: medium;\">熟悉的大底，全新的体验</strong></p><p>其实vivo X60 Pro+的主摄感光元件其实还是熟悉的三星GN1感光元件，这款感光元件曾经在vivo X50 Pro+与iQOO 5系列上展现过一流的画质表现。由于智能手机的感光元件不能无限制地大下去，所以尺寸大战在来到1/1.3英寸左右时逐渐停滞，因此这颗GN1的硬件性能依然能够保证旗舰机的主摄画质。</p><p><img src=\"http://pic.zaeke.com/img161242235115851.jpeg\" alt=\"IMG_20210121_165534.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242250182014.jpeg\" alt=\"IMG_20210127_161227.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242505631346.jpeg\" alt=\"IMG_20210127_152834.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242438655506.jpeg\" alt=\"IMG_20210121_171922.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242526796692.jpeg\" alt=\"IMG_20210127_173007.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161250797153570.jpeg\" alt=\"IMG_20210203_174749.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161250884068772.jpeg\" alt=\"IMG_20210203_171521.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161250884059576.jpeg\" alt=\"IMG_20210203_183104.jpg\">  </p><p>先说说低感表现，vivo X60 Pro+的主摄在低感的情况下能够实现非常优秀的画质表现，尽管有明显的涂抹，但不至于让画面细节过分丢失。从样张上能够很直接地感受到这颗大底带来的出色画面扎实度与细节，并且画面的噪点与伪色控制也非常突出。这点其实并不出奇，毕竟vivo首次使用GN1的时候就实现了非常成熟的调校，因此在基本的画面素质上肯定不会翻车。</p><p>而以色彩上来看，vivo的色彩延续性也很明显，vivo X60 Pro+也是一款风格承续自前作的机型，色彩还原并不会明显跑偏，大多数场景下的白平衡表现也相对稳定，色彩尽管相对鲜艳但也不会显得过于浓郁，整体观感优势非常明显。</p><p><img src=\"http://pic.zaeke.com/img161242379618781.jpeg\" alt=\"IMG_20210119_202507.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242490485699.jpeg\" alt=\"IMG_20210121_185421.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242243548642.jpeg\" alt=\"IMG_20210121_185552.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242399693914.jpeg\" alt=\"IMG_20210119_202252.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242562665422.jpeg\" alt=\"IMG_20210127_202523.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242574066682.jpeg\" alt=\"IMG_20210127_202529.jpg\">  </p><p>甚至从影调上来说，不同于一些品牌为了动态范围高而动态范围高的设定，vivo X60 Pro+依然有着较高的反差，画面立体感更强。当然vivo X60 Pro+现在的调校相较于代产品，也就是vivo X50 Pro+在暗部提亮上更加积极，所以立体感略有下降。尤其是在夜景表现上，该机能够同时满足影调的自然醒与色彩的观感。</p><p>当然反差大小是否好看并非由统一的衡量标准判断，这是一件非常萝卜青菜的事情，并且vivo X60 Pro+也不至于出现画面过平的情况，反差只是相对低而非绝对低。</p><p>这种画面风格调校的事情实际上很难讨论出对错，毕竟不论将画面的色彩以及影调调整成怎样的风格都是有人欢喜有人忧，没有办法做到让所有人满意的效果。至于究竟会选择怎样的风格，或者说在后续的产品中产生怎样的风格变化，在我看来显然还是市场调研中大部分用户的反馈说了算。</p><p><img src=\"http://pic.zaeke.com/img161250655743127.jpeg\" alt=\"IMG_20210203_175205.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161250655649950.jpeg\" alt=\"IMG_20210203_175306.jpg\"></p><p>不过在黄昏场景，尤其是同时出现蓝天与暖色人造光源的场景下，vivo X60 Pro+有时会出现白平衡失准的情况。具体表现为刚抬起手机是白平衡正常，而抬起并稳定之后则会出现白平衡过暖的现象。此时原本蓝色的天空也会明显发灰，画面观感会出现下降。</p><p>事实上这个问题早在iQOO 5上我们就已经发现了，而如果vivo希望自家机型在黄昏场景下能够拥有更好的观感，如何规避人造光源对黄昏场景下白平衡的影响，就是vivo接下来需要努力的事情了。</p><p>仍需强调的是，反差降低的风格变化与黄昏白平衡失准的缺点在其它三颗相机上也可能会出现。</p><p><img src=\"http://pic.zaeke.com/img161242953975653.jpeg\" alt=\"vivo X50 Pro+_副本.jpg\">  </p><p>而蔡司T镀膜则是vivo X60 Pro+的绝对亮点，其中主摄镜头的7片镜片均采用T镀膜，其中前5片镜片采用多层复合镀膜，后两边镜片则采用了纳米晶体镀膜。从我们的实际测试来看，采用T*镀膜的vivo X60 Pro+的抗眩光表现相较于前代有着非常显著的提升。</p><p>并且vivo X60 Pro+相机模组中的四颗相机均通过了T*认证，因此理论上整个模组的抗眩光表现都会有所提升。</p><p><img src=\"http://pic.zaeke.com/img161250791623103.jpeg\" alt=\"IMG_20210203_174749.jpg\">  </p><p>此外，vivo X60 Pro+拍摄包含天空的场景时会出现的色彩断层问题，理论上在前两年就已经存在。色彩断层这样的问题或许对于常规机型没那么大的问题，可对于一款影像旗舰而言就显得有些拖后腿了。vivo X60 Pro+的直出JPG色彩断层依然相对明显，并且在后期之后会出现明显的色块。</p><p>所幸这点vivo已经在设法解决，或许能在未来的新款机型上看到结果。</p><p><img src=\"http://pic.zaeke.com/img161250729435539.jpeg\" alt=\"IMG_20210127_153203.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161250730841040.jpeg\" alt=\"IMG_20210127_185943.jpg\">  </p><p>还有个很有趣的功能是时光慢门，尽管看起来这个功能是友商们早就拥有的功能，不过这回vivo X60 Pro+甚至能够在手持的情况下拍摄出慢门堆栈的效果，在实用性与便利性上远超那些需要三脚架的堆栈慢门模式好了非常多。</p><p>不过目前时光慢门模式在部分场景下可能会出现明显的天空色彩断层，这一点经过反馈之后vivo也正在修复当中。不过目前的时光慢门仅支持主摄，在使用场景上也受到了限制，我们只能寄希望于未来vivo能够针对多摄进行完整的调校。</p><p>毕竟相较于其他品牌来说，vivo能够支持全焦段的RAW格式输出以及全焦段的夜景模式，这点对于使用体验的提升非常明显。如果能进一步做到全焦段的时光慢门的话，显然又能够赋予用户更多的创作可能性。</p><p style=\"text-align: center;\"><strong style=\"font-size: medium;\">微云台与超广角的合体</strong></p><p>伴随着智能手机相机模组的堆料大战，单主摄显然无法满足，所以这回vivo为超广角相机也提供了主摄级的索尼IMX598感光元件，并且曾经好评如潮的微云台也出现在了这颗超广角相机上。</p><p><img src=\"http://pic.zaeke.com/img161242013293706.jpeg\" alt=\"591A7968.jpg\">  </p><p>从实际的配置上来说，这颗超广角相机搭载微云台之后能堵住不少人的嘴。</p><p>毕竟不管微云台能够在稳定性上带来多大的提升，但在不怎么需要稳定性的高照度场景之下，大底感光元件的画质往往一定更好。用一颗相对较小的主摄配合微云台的做法很多人没办法接受，如今真正的大底与支持微云台的超广角都集成在一起，很多人对小底微云台的忧虑显然可以减轻或消失。</p><p><img src=\"http://pic.zaeke.com/img161242380241484.jpeg\" alt=\"IMG_20210121_171445.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242609728483.jpeg\" alt=\"IMG_20210127_153712.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161250868099144.jpeg\" alt=\"IMG_20210203_171504.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242434920091.jpeg\" alt=\"IMG_20210121_173738.jpg\">  </p><p>这颗超广角相机的画质在目前的智能手机中处于顶级水准。尽管由于要把视角做得足够广，不可避免会出现边角画质发软的情况，不过与市面上大多数机型不同，vivo X60 Pro+的边角并不存在大颗粒的噪点与明显的伪色，这样的边角画质只能说软而不能说是崩。</p><p>换句话说，vivo X60 Pro+的超广角相机的画质表现已经非常出色了，并且也同时兼顾了视角广度。虽然超广角不是广就等于好，而是不同的焦距都有用处，不过在这个消费者更喜欢广视角的舆论环境之下，广视角与好画质的组合确实很得人心。</p><p><img src=\"http://pic.zaeke.com/img161242421683758.jpeg\" alt=\"IMG_20210119_182359.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161250868120926.jpeg\" alt=\"IMG_20210203_183155.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242440142341.jpeg\" alt=\"IMG_20210127_182638.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242390072146.jpeg\" alt=\"IMG_20210121_185850.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242432933598.jpeg\" alt=\"IMG_20210121_153759.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242421894325.jpeg\" alt=\"IMG_20210119_202803.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242616197135.jpeg\" alt=\"IMG_20210127_202406.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242384981691.jpeg\" alt=\"IMG_20210121_185458.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242652618234.jpeg\" alt=\"IMG_20210121_185512.jpg\">  </p><p>从画质上来说，vivo X60 Pro+的超广角相机的中心画质与常规的主摄差距不大，不过相对软的边角画质就显然不能和中心画质一概而论了。</p><p>其实vivo X60 Pro+并不是首款采用1/2英寸的感光元件超广角相机的机型，但确实同尺寸感光元件的超广角相机中夜景画质最好的。原因显然不难理解，毕竟通过微云台能够实现低感超曝光的策略，因此能够实现更好的低照度画质表现。</p><p><img src=\"http://pic.zaeke.com/img161250972616410.jpeg\" alt=\"11.jpg\">  </p><p>把微云台塞到超广角还有一个好处，微云台的稳定性本来就有助于拍摄视频。并且这次vivo X60 Pro+准备了VIS五轴视频防抖。这无五轴其中两轴为微云台自带的光学防抖，另外三轴则为电子防抖，通过这种混合式防抖来实现更好的视频稳定表现。</p><p>那么问题来了：我们在评测vivo X60 Pro的时候有说过，尽管其视频稳定性非常出色，不过代价则是在电子防抖对画面进行了较大的裁切。而超广角微云台拍摄则能够规避这个问题。首先是14mm的焦距带来的广视野在裁切之后也足够广，其次则是画面裁切之后恰好避免了超广角相机边角画质较软的部分，能够充分利用画质较好的画面中心部分。</p><p>怎么说呢？这种发扬优点并巧妙规避缺点的方式，让这颗超广角微云台相机在拍摄视频的时候能够在保证画质的同时提升视野的广度，如果你喜欢拍摄建筑或者风光类的视频题材，那么一个广且画质优秀的超广角相机确实能够提升对这类题材的拍摄体验。</p><p style=\"text-align: center;\"><strong style=\"font-size: medium;\">一年之后，我依然是顶级2X人像</strong></p><p>vivo X60 Pro+的2X人像相机搭载三星GD1感光元件，虽然这也是从vivo X30 Pro时代用到现在的一员老将，但这颗感光元件对于2X人像相机来讲依然是依然是非常优秀的硬件。</p><p>或许是等效50mm的焦距不容易把控，又或者是因为50mm焦距做好没有超广角或长焦更抓人眼球，目前的2X相机的感光元件尺寸最大不过是约等于或略小于索尼IMX363感光元件的水平，这也更让三星GD1感光元件在2X相机中显得竞争力极强了。</p><p><img src=\"http://pic.zaeke.com/img161242654455345.jpeg\" alt=\"IMG_20210121_143944.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242656185557.jpeg\" alt=\"IMG_20210121_161435.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161250888734972.jpeg\" alt=\"IMG_20210203_170124.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242661692773.jpeg\" alt=\"IMG_20210128_155014.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242666061984.jpeg\" alt=\"IMG_20210120_101951.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242696545090.jpeg\" alt=\"IMG_20210127_161932.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242697098826.jpeg\" alt=\"IMG_20210125_085730.jpg\">  </p><p>虽然在像素四合一之后只有800万像素，看起来有点像某些手机里的凑数相机，不过vivo X60 Pro+的2X人像相机最高可以输出高达3200万像素的图片。尽管受限于硬件本身其在暗光下的表现很难说非常达到主摄级水准，不过在光线较强的时候其输出的画面拥有非常充分扎实的画面细节，与此同时也保证了不错的色彩与观感。</p><p>更重要的是，这颗2X人像相机的高像素模式拥有充分的可用性，在输出3200万像素的图片时并不会出现明显的伪色，因此在光线条件下较强的场景下，其拍摄的高像素照片可用性是非常高的。</p><p><img src=\"http://pic.zaeke.com/img161242800284041.jpeg\" alt=\"IMG_20210130_171522.jpg\">  </p><p>常规人像模式</p><p><img src=\"http://pic.zaeke.com/img161242779083339.jpeg\" alt=\"IMG_20210202_160251.jpg\">  </p><p>蔡司Biotar风格</p><p>在这还需要特别提一下vivo这次带来的蔡司Biotar人像滤镜，虽然这个人像滤镜其实每颗相机都可以使用，但显然这颗“2X人像相机”的使用频率最高。</p><p>蔡司Biotar人像滤镜首先是一个色彩滤镜，本身带一定的色彩风格变化。接着则是非常有趣的模拟旋焦效果，与真正的蔡司Biotar镜头的焦外风格非常相似。这个效果到底好不好看显然是一件仁者见仁智者见智的事情，不过对于古典镜头爱好者来说这肯定是个好消息，毕竟常规的模拟虚化光斑这么多年也差不多该看腻了。</p><p>当然，拍人像始终是一件挺麻烦的事情，毕竟既有人像三要素，在对于场景和背景的要求也非常高。不过只要场景与背景合适，即便没有人像摄影经验也能够拍出背景极具特色的照片。</p><p><img src=\"http://pic.zaeke.com/img161242656937123.jpeg\" alt=\"IMG_20210121_185854.jpg\">  </p><p>可惜的是，受限于感光元件尺寸、没有防抖以及光圈大小，这颗2X人像相机在拍摄夜景时的画质很难说非常出彩，没有办法与主摄相比，只能说其在夜景拍摄中处在2X相机中的顶级水准。不过要知道这已经是市面上相对较强的2X人像相机了，如果对这颗2X人像相机的画质还不满意的话，那么市面上绝大多数的2X相机其实都不够看。</p><p style=\"text-align: center;\"><strong style=\"font-size: medium;\">长焦？还是多想想超广角吧</strong></p><p>虽然有很多人开始喜欢长焦，不过vivo X60 Pro+的确是更加着力于超广角相机。</p><p><img src=\"http://pic.zaeke.com/img161242021290818.jpeg\" alt=\"591A7984.jpg\"><br></p><p><img src=\"http://pic.zaeke.com/img161242334081484.jpeg\" alt=\"591A7980.jpg\"><br></p><p>很遗憾的是，尽管vivo X60 Pro+的相机模组尺寸已经非常巨大，但那颗5X长焦相机的规格还是退步了，从vivo X50 Pro+上的1300万像素的三星S5K3L6或S5K3M5（软件读取问题无法确定究竟是二者之中的哪款），退步为如今800万像素的OV08A10感光元件。</p><p>虽然说是有失必有得，在5X长焦相机画质退步的同时，vivo X60 Pro+获得了目前顶级的超广角相机，但对于长焦爱好者来说这肯定是个坏消息。虽然大家都期待四个焦段都能做到主摄级硬件，但这显然不现实。</p><p><img src=\"http://pic.zaeke.com/img161242029096984.jpeg\" alt=\"591A7995.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242024931070.jpeg\" alt=\"591A7990.jpg\">  </p><p>毕竟只要你不在乎厚度和售价，做个十多厘米厚的机身然后在里面塞个53.4x40.0mm的中画幅感光元件，再卖个10万块钱也不是不行。可作为智能手机又必须考虑机身尺寸与成本，无论是相机模组的占板面积还是厚度都不可能无限制的提升，从理智上我们是能够理解5X长焦退步的现象。</p><p>可理智能接受的同时，希望多摄硬件与表现全面提升的朴素想法肯定存在，我们只能寄希望于未来能够实现更好的解决方案了。</p><p><img src=\"http://pic.zaeke.com/img161242716967870.jpeg\" alt=\"IMG_20210127_145919.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242714137196.jpeg\" alt=\"IMG_20210121_143910.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242738558703.jpeg\" alt=\"IMG_20210128_155203.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161242729524496.jpeg\" alt=\"IMG_20210127_164455.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242724623126.jpeg\" alt=\"IMG_20210127_154150.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161250890832059.jpeg\" alt=\"IMG_20210203_174824.jpg\">  </p><p>回到这颗5X相机本身，从画质上来说其相较于前代确实有比较明显的下降，画面细节与扎实度差距也确实不小，这颗长焦相机确实是vivo X60 Pro+上显得相对不那么Pro+的部分。硬件本身的不足对于画质的影响在这款长焦相机上的表现非常明显。</p><p>尽管vivo X60 Pro+的5X长焦相机严格来说调校很不错，无论是伪色控制还是纯粹的色彩与观感本身，都属于非常成熟的水平。在光线充足的场景之下的确没啥太大影响，不过只要照度略差些那么画质的劣化就会对画面观感造成负面影响。</p><p><img src=\"http://pic.zaeke.com/img161242733618198.jpeg\" alt=\"IMG_20210127_181112.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161242734056825.jpeg\" alt=\"IMG_20210127_185241.jpg\">  </p><p>因此这可5X长焦相机的夜景画质就有些感人了，在拍摄夜景时画质较差并且细节丢失也相对明显。不过倒是有一件非常有趣的事情，毕竟市面上有一部分机型在拍摄夜景时并不会真正的调用5X长焦相机的硬件，即便他拥有5X长焦的硬件也同样会采用主摄裁切的方式来实现五倍的视角。</p><p>这与拥有这种夜景策略的机型相比时，拍摄夜景真的会调用五倍长焦镜头的vivo X60 Pro+倒是不显得那么吃亏。况且还有不错的月亮模式，虽然贴图归贴图，在朋友圈求个赞总归是没毛病的。</p><p><img src=\"http://pic.zaeke.com/img161233581754606.jpeg\" alt=\"591A7974.jpg\">  </p><p>所以这个5X长焦相机最重要的意义依然是丰富焦段。真正通过相应焦距光学镜头进行成像的5X长焦相机所带来的画质要比那些裁切方案的机型好很多。</p><p>毕竟即便是那些像素极高的大底感光元件成像，最终裁切之后出现的画面往往在画面扎实度以及细节表现上都很难令人感到满意，光学的5X成像在很多时候带来的扎实感是通常数码裁切所难以比拟的。</p><p style=\"text-align: center;\"><strong style=\"font-size: medium;\">影像旗舰虽好，但我想全都要</strong></p><p>vivo X60 Pro+是一款设定非常有趣的影像旗舰，它以有限的价格——或者说是以别家中杯大杯的价格就做到了顶级的影像表现，只在拍照这一点上它完全不会输给一些售价高出很多的机型。</p><p><img src=\"http://pic.zaeke.com/img161233576355077.jpeg\" alt=\"591A7961.jpg\">  </p><p>所以别看严格来说并不便宜，但这确实一款性价比非常高的智能手机，只不过它并不是全能旗舰，或者说vivo也并不希望打造一款水桶式的全能旗舰。但正是这样的思路与产品定位，就能让我们以相对便宜的价格买到拥有目前顶级拍照水准的机型。</p><p>从这点来看，我们似乎是赚了？</p><p><img src=\"http://pic.zaeke.com/img161242012119339.jpeg\" alt=\"591A7955.jpg\">  </p><p>不过考虑到打铁仍需自身硬，vivo X60 Pro+完全是通过实例让我们来感受到它不仅仅是拥有顶级的相机模组硬件，并且在软件上也有着不错的表现，尽管调校并不能说做到了完美，但这样的强势表现确实是很有吸引力的。</p><p>如果考虑到那句很出名的话，小孩子才做选择，成年人我全都要。vivo X60 Pro+加依然是一款在需要选择的机型。至于那款我全都要的顶级旗舰，是不是我们现在该开始期待下一代NEX了？</p>', '<p class=\"p_style\">如果你和我一样是个很在乎手机拍照表现、不那么在乎其它体验的用户，感觉自己没必要多加几千块钱去买用不着的双扬声器、2K屏等功能，那么vivo X60 Pro+或许是目前的唯一的选择。</p><p class=\"p_style\">尽管价格只是5K档位，可从相机硬件与拍照表现上来看，vivo X60 Pro+在拍照表现上却足以和友商们万元级的产品一较高下如果你真的非常重视拍照而在别的硬件和体验上比较佛系的话，价格相对便宜的vivo X60 Pro+甚至可以说上一句性价比很高。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242009099911.jpeg\" alt=\"591A7958.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">vivo X60 Pro+的主摄采用三星将于感光元件配备F1.57的大光圈镜头，支持光学防抖。而超广角相机则采用索尼IMX598感光元件，并配备了微云台技术。而2X人像相机则搭载三星GD1感光元件；5X长焦相机则为800万像素，配备OV08A10感光元件。</p><p class=\"p_style\">而在拥有强大四摄模组的同时，vivo X60 Pro+也拉来了蔡司背书，该机四颗后置相机均配备蔡司当家的T*镀膜不说，实际抗眩光表现也有非常显著的进步，并且蔡司的影像专业性与品牌价值也能够进一步赋予vivo X60 Pro+在影像上的竞争力。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242243439131.jpeg\" alt=\"591A7966.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">这种很不水桶的定位或许看起来距离很多人追求的全能旗舰距离不小，不过这种用户定位清晰的产品也同样很有市场。只要拍照这块长板做得足够长，vivo X60 Pro+就会拥有讨人喜欢的本钱。</p><p style=\"text-align: center;\" class=\"p_style\"><strong style=\"font-size: medium;\">熟悉的大底，全新的体验</strong></p><p class=\"p_style\">其实vivo X60 Pro+的主摄感光元件其实还是熟悉的三星GN1感光元件，这款感光元件曾经在vivo X50 Pro+与iQOO 5系列上展现过一流的画质表现。由于智能手机的感光元件不能无限制地大下去，所以尺寸大战在来到1/1.3英寸左右时逐渐停滞，因此这颗GN1的硬件性能依然能够保证旗舰机的主摄画质。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242235115851.jpeg\" alt=\"IMG_20210121_165534.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242250182014.jpeg\" alt=\"IMG_20210127_161227.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242505631346.jpeg\" alt=\"IMG_20210127_152834.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242438655506.jpeg\" alt=\"IMG_20210121_171922.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242526796692.jpeg\" alt=\"IMG_20210127_173007.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161250797153570.jpeg\" alt=\"IMG_20210203_174749.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161250884068772.jpeg\" alt=\"IMG_20210203_171521.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161250884059576.jpeg\" alt=\"IMG_20210203_183104.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">先说说低感表现，vivo X60 Pro+的主摄在低感的情况下能够实现非常优秀的画质表现，尽管有明显的涂抹，但不至于让画面细节过分丢失。从样张上能够很直接地感受到这颗大底带来的出色画面扎实度与细节，并且画面的噪点与伪色控制也非常突出。这点其实并不出奇，毕竟vivo首次使用GN1的时候就实现了非常成熟的调校，因此在基本的画面素质上肯定不会翻车。</p><p class=\"p_style\">而以色彩上来看，vivo的色彩延续性也很明显，vivo X60 Pro+也是一款风格承续自前作的机型，色彩还原并不会明显跑偏，大多数场景下的白平衡表现也相对稳定，色彩尽管相对鲜艳但也不会显得过于浓郁，整体观感优势非常明显。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242379618781.jpeg\" alt=\"IMG_20210119_202507.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242490485699.jpeg\" alt=\"IMG_20210121_185421.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242243548642.jpeg\" alt=\"IMG_20210121_185552.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242399693914.jpeg\" alt=\"IMG_20210119_202252.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242562665422.jpeg\" alt=\"IMG_20210127_202523.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242574066682.jpeg\" alt=\"IMG_20210127_202529.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">甚至从影调上来说，不同于一些品牌为了动态范围高而动态范围高的设定，vivo X60 Pro+依然有着较高的反差，画面立体感更强。当然vivo X60 Pro+现在的调校相较于代产品，也就是vivo X50 Pro+在暗部提亮上更加积极，所以立体感略有下降。尤其是在夜景表现上，该机能够同时满足影调的自然醒与色彩的观感。</p><p class=\"p_style\">当然反差大小是否好看并非由统一的衡量标准判断，这是一件非常萝卜青菜的事情，并且vivo X60 Pro+也不至于出现画面过平的情况，反差只是相对低而非绝对低。</p><p class=\"p_style\">这种画面风格调校的事情实际上很难讨论出对错，毕竟不论将画面的色彩以及影调调整成怎样的风格都是有人欢喜有人忧，没有办法做到让所有人满意的效果。至于究竟会选择怎样的风格，或者说在后续的产品中产生怎样的风格变化，在我看来显然还是市场调研中大部分用户的反馈说了算。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161250655743127.jpeg\" alt=\"IMG_20210203_175205.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161250655649950.jpeg\" alt=\"IMG_20210203_175306.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">不过在黄昏场景，尤其是同时出现蓝天与暖色人造光源的场景下，vivo X60 Pro+有时会出现白平衡失准的情况。具体表现为刚抬起手机是白平衡正常，而抬起并稳定之后则会出现白平衡过暖的现象。此时原本蓝色的天空也会明显发灰，画面观感会出现下降。</p><p class=\"p_style\">事实上这个问题早在iQOO 5上我们就已经发现了，而如果vivo希望自家机型在黄昏场景下能够拥有更好的观感，如何规避人造光源对黄昏场景下白平衡的影响，就是vivo接下来需要努力的事情了。</p><p class=\"p_style\">仍需强调的是，反差降低的风格变化与黄昏白平衡失准的缺点在其它三颗相机上也可能会出现。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242953975653.jpeg\" alt=\"vivo X50 Pro+_副本.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">而蔡司T镀膜则是vivo X60 Pro+的绝对亮点，其中主摄镜头的7片镜片均采用T镀膜，其中前5片镜片采用多层复合镀膜，后两边镜片则采用了纳米晶体镀膜。从我们的实际测试来看，采用T*镀膜的vivo X60 Pro+的抗眩光表现相较于前代有着非常显著的提升。</p><p class=\"p_style\">并且vivo X60 Pro+相机模组中的四颗相机均通过了T*认证，因此理论上整个模组的抗眩光表现都会有所提升。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161250791623103.jpeg\" alt=\"IMG_20210203_174749.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">此外，vivo X60 Pro+拍摄包含天空的场景时会出现的色彩断层问题，理论上在前两年就已经存在。色彩断层这样的问题或许对于常规机型没那么大的问题，可对于一款影像旗舰而言就显得有些拖后腿了。vivo X60 Pro+的直出JPG色彩断层依然相对明显，并且在后期之后会出现明显的色块。</p><p class=\"p_style\">所幸这点vivo已经在设法解决，或许能在未来的新款机型上看到结果。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161250729435539.jpeg\" alt=\"IMG_20210127_153203.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161250730841040.jpeg\" alt=\"IMG_20210127_185943.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">还有个很有趣的功能是时光慢门，尽管看起来这个功能是友商们早就拥有的功能，不过这回vivo X60 Pro+甚至能够在手持的情况下拍摄出慢门堆栈的效果，在实用性与便利性上远超那些需要三脚架的堆栈慢门模式好了非常多。</p><p class=\"p_style\">不过目前时光慢门模式在部分场景下可能会出现明显的天空色彩断层，这一点经过反馈之后vivo也正在修复当中。不过目前的时光慢门仅支持主摄，在使用场景上也受到了限制，我们只能寄希望于未来vivo能够针对多摄进行完整的调校。</p><p class=\"p_style\">毕竟相较于其他品牌来说，vivo能够支持全焦段的RAW格式输出以及全焦段的夜景模式，这点对于使用体验的提升非常明显。如果能进一步做到全焦段的时光慢门的话，显然又能够赋予用户更多的创作可能性。</p><p style=\"text-align: center;\" class=\"p_style\"><strong style=\"font-size: medium;\">微云台与超广角的合体</strong></p><p class=\"p_style\">伴随着智能手机相机模组的堆料大战，单主摄显然无法满足，所以这回vivo为超广角相机也提供了主摄级的索尼IMX598感光元件，并且曾经好评如潮的微云台也出现在了这颗超广角相机上。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242013293706.jpeg\" alt=\"591A7968.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">从实际的配置上来说，这颗超广角相机搭载微云台之后能堵住不少人的嘴。</p><p class=\"p_style\">毕竟不管微云台能够在稳定性上带来多大的提升，但在不怎么需要稳定性的高照度场景之下，大底感光元件的画质往往一定更好。用一颗相对较小的主摄配合微云台的做法很多人没办法接受，如今真正的大底与支持微云台的超广角都集成在一起，很多人对小底微云台的忧虑显然可以减轻或消失。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242380241484.jpeg\" alt=\"IMG_20210121_171445.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242609728483.jpeg\" alt=\"IMG_20210127_153712.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161250868099144.jpeg\" alt=\"IMG_20210203_171504.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242434920091.jpeg\" alt=\"IMG_20210121_173738.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">这颗超广角相机的画质在目前的智能手机中处于顶级水准。尽管由于要把视角做得足够广，不可避免会出现边角画质发软的情况，不过与市面上大多数机型不同，vivo X60 Pro+的边角并不存在大颗粒的噪点与明显的伪色，这样的边角画质只能说软而不能说是崩。</p><p class=\"p_style\">换句话说，vivo X60 Pro+的超广角相机的画质表现已经非常出色了，并且也同时兼顾了视角广度。虽然超广角不是广就等于好，而是不同的焦距都有用处，不过在这个消费者更喜欢广视角的舆论环境之下，广视角与好画质的组合确实很得人心。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242421683758.jpeg\" alt=\"IMG_20210119_182359.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161250868120926.jpeg\" alt=\"IMG_20210203_183155.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242440142341.jpeg\" alt=\"IMG_20210127_182638.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242390072146.jpeg\" alt=\"IMG_20210121_185850.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242432933598.jpeg\" alt=\"IMG_20210121_153759.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242421894325.jpeg\" alt=\"IMG_20210119_202803.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242616197135.jpeg\" alt=\"IMG_20210127_202406.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242384981691.jpeg\" alt=\"IMG_20210121_185458.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242652618234.jpeg\" alt=\"IMG_20210121_185512.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">从画质上来说，vivo X60 Pro+的超广角相机的中心画质与常规的主摄差距不大，不过相对软的边角画质就显然不能和中心画质一概而论了。</p><p class=\"p_style\">其实vivo X60 Pro+并不是首款采用1/2英寸的感光元件超广角相机的机型，但确实同尺寸感光元件的超广角相机中夜景画质最好的。原因显然不难理解，毕竟通过微云台能够实现低感超曝光的策略，因此能够实现更好的低照度画质表现。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161250972616410.jpeg\" alt=\"11.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">把微云台塞到超广角还有一个好处，微云台的稳定性本来就有助于拍摄视频。并且这次vivo X60 Pro+准备了VIS五轴视频防抖。这无五轴其中两轴为微云台自带的光学防抖，另外三轴则为电子防抖，通过这种混合式防抖来实现更好的视频稳定表现。</p><p class=\"p_style\">那么问题来了：我们在评测vivo X60 Pro的时候有说过，尽管其视频稳定性非常出色，不过代价则是在电子防抖对画面进行了较大的裁切。而超广角微云台拍摄则能够规避这个问题。首先是14mm的焦距带来的广视野在裁切之后也足够广，其次则是画面裁切之后恰好避免了超广角相机边角画质较软的部分，能够充分利用画质较好的画面中心部分。</p><p class=\"p_style\">怎么说呢？这种发扬优点并巧妙规避缺点的方式，让这颗超广角微云台相机在拍摄视频的时候能够在保证画质的同时提升视野的广度，如果你喜欢拍摄建筑或者风光类的视频题材，那么一个广且画质优秀的超广角相机确实能够提升对这类题材的拍摄体验。</p><p style=\"text-align: center;\" class=\"p_style\"><strong style=\"font-size: medium;\">一年之后，我依然是顶级2X人像</strong></p><p class=\"p_style\">vivo X60 Pro+的2X人像相机搭载三星GD1感光元件，虽然这也是从vivo X30 Pro时代用到现在的一员老将，但这颗感光元件对于2X人像相机来讲依然是依然是非常优秀的硬件。</p><p class=\"p_style\">或许是等效50mm的焦距不容易把控，又或者是因为50mm焦距做好没有超广角或长焦更抓人眼球，目前的2X相机的感光元件尺寸最大不过是约等于或略小于索尼IMX363感光元件的水平，这也更让三星GD1感光元件在2X相机中显得竞争力极强了。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242654455345.jpeg\" alt=\"IMG_20210121_143944.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242656185557.jpeg\" alt=\"IMG_20210121_161435.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161250888734972.jpeg\" alt=\"IMG_20210203_170124.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242661692773.jpeg\" alt=\"IMG_20210128_155014.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242666061984.jpeg\" alt=\"IMG_20210120_101951.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242696545090.jpeg\" alt=\"IMG_20210127_161932.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242697098826.jpeg\" alt=\"IMG_20210125_085730.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">虽然在像素四合一之后只有800万像素，看起来有点像某些手机里的凑数相机，不过vivo X60 Pro+的2X人像相机最高可以输出高达3200万像素的图片。尽管受限于硬件本身其在暗光下的表现很难说非常达到主摄级水准，不过在光线较强的时候其输出的画面拥有非常充分扎实的画面细节，与此同时也保证了不错的色彩与观感。</p><p class=\"p_style\">更重要的是，这颗2X人像相机的高像素模式拥有充分的可用性，在输出3200万像素的图片时并不会出现明显的伪色，因此在光线条件下较强的场景下，其拍摄的高像素照片可用性是非常高的。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242800284041.jpeg\" alt=\"IMG_20210130_171522.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">常规人像模式</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242779083339.jpeg\" alt=\"IMG_20210202_160251.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">蔡司Biotar风格</p><p class=\"p_style\">在这还需要特别提一下vivo这次带来的蔡司Biotar人像滤镜，虽然这个人像滤镜其实每颗相机都可以使用，但显然这颗“2X人像相机”的使用频率最高。</p><p class=\"p_style\">蔡司Biotar人像滤镜首先是一个色彩滤镜，本身带一定的色彩风格变化。接着则是非常有趣的模拟旋焦效果，与真正的蔡司Biotar镜头的焦外风格非常相似。这个效果到底好不好看显然是一件仁者见仁智者见智的事情，不过对于古典镜头爱好者来说这肯定是个好消息，毕竟常规的模拟虚化光斑这么多年也差不多该看腻了。</p><p class=\"p_style\">当然，拍人像始终是一件挺麻烦的事情，毕竟既有人像三要素，在对于场景和背景的要求也非常高。不过只要场景与背景合适，即便没有人像摄影经验也能够拍出背景极具特色的照片。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242656937123.jpeg\" alt=\"IMG_20210121_185854.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">可惜的是，受限于感光元件尺寸、没有防抖以及光圈大小，这颗2X人像相机在拍摄夜景时的画质很难说非常出彩，没有办法与主摄相比，只能说其在夜景拍摄中处在2X相机中的顶级水准。不过要知道这已经是市面上相对较强的2X人像相机了，如果对这颗2X人像相机的画质还不满意的话，那么市面上绝大多数的2X相机其实都不够看。</p><p style=\"text-align: center;\" class=\"p_style\"><strong style=\"font-size: medium;\">长焦？还是多想想超广角吧</strong></p><p class=\"p_style\">虽然有很多人开始喜欢长焦，不过vivo X60 Pro+的确是更加着力于超广角相机。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242021290818.jpeg\" alt=\"591A7984.jpg\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242334081484.jpeg\" alt=\"591A7980.jpg\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">很遗憾的是，尽管vivo X60 Pro+的相机模组尺寸已经非常巨大，但那颗5X长焦相机的规格还是退步了，从vivo X50 Pro+上的1300万像素的三星S5K3L6或S5K3M5（软件读取问题无法确定究竟是二者之中的哪款），退步为如今800万像素的OV08A10感光元件。</p><p class=\"p_style\">虽然说是有失必有得，在5X长焦相机画质退步的同时，vivo X60 Pro+获得了目前顶级的超广角相机，但对于长焦爱好者来说这肯定是个坏消息。虽然大家都期待四个焦段都能做到主摄级硬件，但这显然不现实。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242029096984.jpeg\" alt=\"591A7995.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242024931070.jpeg\" alt=\"591A7990.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">毕竟只要你不在乎厚度和售价，做个十多厘米厚的机身然后在里面塞个53.4x40.0mm的中画幅感光元件，再卖个10万块钱也不是不行。可作为智能手机又必须考虑机身尺寸与成本，无论是相机模组的占板面积还是厚度都不可能无限制的提升，从理智上我们是能够理解5X长焦退步的现象。</p><p class=\"p_style\">可理智能接受的同时，希望多摄硬件与表现全面提升的朴素想法肯定存在，我们只能寄希望于未来能够实现更好的解决方案了。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242716967870.jpeg\" alt=\"IMG_20210127_145919.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242714137196.jpeg\" alt=\"IMG_20210121_143910.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242738558703.jpeg\" alt=\"IMG_20210128_155203.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242729524496.jpeg\" alt=\"IMG_20210127_164455.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242724623126.jpeg\" alt=\"IMG_20210127_154150.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161250890832059.jpeg\" alt=\"IMG_20210203_174824.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">回到这颗5X相机本身，从画质上来说其相较于前代确实有比较明显的下降，画面细节与扎实度差距也确实不小，这颗长焦相机确实是vivo X60 Pro+上显得相对不那么Pro+的部分。硬件本身的不足对于画质的影响在这款长焦相机上的表现非常明显。</p><p class=\"p_style\">尽管vivo X60 Pro+的5X长焦相机严格来说调校很不错，无论是伪色控制还是纯粹的色彩与观感本身，都属于非常成熟的水平。在光线充足的场景之下的确没啥太大影响，不过只要照度略差些那么画质的劣化就会对画面观感造成负面影响。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242733618198.jpeg\" alt=\"IMG_20210127_181112.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242734056825.jpeg\" alt=\"IMG_20210127_185241.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">因此这可5X长焦相机的夜景画质就有些感人了，在拍摄夜景时画质较差并且细节丢失也相对明显。不过倒是有一件非常有趣的事情，毕竟市面上有一部分机型在拍摄夜景时并不会真正的调用5X长焦相机的硬件，即便他拥有5X长焦的硬件也同样会采用主摄裁切的方式来实现五倍的视角。</p><p class=\"p_style\">这与拥有这种夜景策略的机型相比时，拍摄夜景真的会调用五倍长焦镜头的vivo X60 Pro+倒是不显得那么吃亏。况且还有不错的月亮模式，虽然贴图归贴图，在朋友圈求个赞总归是没毛病的。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161233581754606.jpeg\" alt=\"591A7974.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">所以这个5X长焦相机最重要的意义依然是丰富焦段。真正通过相应焦距光学镜头进行成像的5X长焦相机所带来的画质要比那些裁切方案的机型好很多。</p><p class=\"p_style\">毕竟即便是那些像素极高的大底感光元件成像，最终裁切之后出现的画面往往在画面扎实度以及细节表现上都很难令人感到满意，光学的5X成像在很多时候带来的扎实感是通常数码裁切所难以比拟的。</p><p style=\"text-align: center;\" class=\"p_style\"><strong style=\"font-size: medium;\">影像旗舰虽好，但我想全都要</strong></p><p class=\"p_style\">vivo X60 Pro+是一款设定非常有趣的影像旗舰，它以有限的价格——或者说是以别家中杯大杯的价格就做到了顶级的影像表现，只在拍照这一点上它完全不会输给一些售价高出很多的机型。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161233576355077.jpeg\" alt=\"591A7961.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">所以别看严格来说并不便宜，但这确实一款性价比非常高的智能手机，只不过它并不是全能旗舰，或者说vivo也并不希望打造一款水桶式的全能旗舰。但正是这样的思路与产品定位，就能让我们以相对便宜的价格买到拥有目前顶级拍照水准的机型。</p><p class=\"p_style\">从这点来看，我们似乎是赚了？</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161242012119339.jpeg\" alt=\"591A7955.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">不过考虑到打铁仍需自身硬，vivo X60 Pro+完全是通过实例让我们来感受到它不仅仅是拥有顶级的相机模组硬件，并且在软件上也有着不错的表现，尽管调校并不能说做到了完美，但这样的强势表现确实是很有吸引力的。</p><p class=\"p_style\">如果考虑到那句很出名的话，小孩子才做选择，成年人我全都要。vivo X60 Pro+加依然是一款在需要选择的机型。至于那款我全都要的顶级旗舰，是不是我们现在该开始期待下一代NEX了？</p>', 'http://pic.zaeke.com/img161433409220149.jpeg', 15628, 0, 0, NULL, '2021-02-14 01:34:07', '2021-03-27 08:12:14');
INSERT INTO `article` VALUES (16095, 'LG 的卷轴屏手机应该还在做吧？', '针对韩联社的相关报道，指LG已经取消其在CES2021期间展示的LGRollable卷轴屏手机的开发项目。LG发言人对TheVerge回应，“Icanfirml...', '[object Object]', '冯伟文', 19, 'http://pic.zaeke.com/user_1610094930png', '<p>针对韩联社的相关报道，指 LG 已经取消其在 CES 2021 期间展示的 LG Rollable 卷轴屏手机的开发项目。LG 发言人对 <a href=\"https://www.theverge.com/2021/2/22/22296298/lg-rollable-denial-not-on-hold-or-cancelled\" target=\"_blank\">The Verge</a> 回应，“I can firmly deny that any such decision on future mobile products has been finalized”，也就是坚决否认相关计划的决定是已经敲定的。</p><p><img alt=\"3NXKq5k.jpeg\" src=\"http://pic.zaeke.com/img161405219910383.jpeg\" width=\"1200\" height=\"675\"><br></p><p>这......听起来就有点似是而非了。考虑到此前曾有传闻 LG 考虑推出智能手机市场，在坚决否认后却又承认确实在考虑出售、撤回或者是缩小旗下智能手机业务规模的操作，看来未来什么可能都会有。</p>', '<p class=\"p_style\">针对韩联社的相关报道，指 LG 已经取消其在 CES 2021 期间展示的 LG Rollable 卷轴屏手机的开发项目。LG 发言人对 <a href=\"https://www.theverge.com/2021/2/22/22296298/lg-rollable-denial-not-on-hold-or-cancelled\" target=\"_blank\">The Verge</a> 回应，“I can firmly deny that any such decision on future mobile products has been finalized”，也就是坚决否认相关计划的决定是已经敲定的。</p><p class=\"jsmargin\"><img alt=\"3NXKq5k.jpeg\" src=\"http://pic.zaeke.com/img161405219910383.jpeg\" width=\"1200\" height=\"675\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">这......听起来就有点似是而非了。考虑到此前曾有传闻 LG 考虑推出智能手机市场，在坚决否认后却又承认确实在考虑出售、撤回或者是缩小旗下智能手机业务规模的操作，看来未来什么可能都会有。</p>', '', 331, 0, 0, NULL, '2021-02-22 19:50:52', '2021-03-27 03:52:31');
INSERT INTO `article` VALUES (16099, '小块头的七彩虹U6，拥有不输“砖头”的大能量', '严格来说第一块“国砖”应是HIFIMAN的HM801，但相信很多人跟我一样，是从七彩虹C4开始认识“国砖”这种产品的。设计复古、质感粗拙、交互低效、体积硕大构成...', '[object Object]', '斗室听澜', 189, 'http://pic.zaeke.com/user_1535960095png', '<p>严格来说第一块“国砖”应是HIFIMAN的HM801，但相信很多人跟我一样，是从七彩虹C4开始认识“国砖”这种产品的。设计复古、质感粗拙、交互低效、体积硕大构成了后MP3时代第一批国产便携HiFi播放器的关键词，浓缩为两个字，便是“国砖”。<br></p><p>令人唏嘘的是，作为国砖最早期布道者的七彩虹，在C4之后沉寂了很久没有推出过有关注度的同类产品，直到旗舰型号U8，七彩虹才算是又一次真正回到了玩家的视线之中——然而作为七彩虹回归的首个型号，U8在使用体验和续航力等方面被诟病颇多。“康复训练”过后，七彩虹推出的这台U6才终于算是一款较为成熟的产品。</p><p><img alt=\"DSC_4749.jpg\" src=\"http://pic.zaeke.com/img161413471282687.jpeg\" width=\"1400\" height=\"933\"><br></p><p>这种“成熟”首先在机身设计上就有体现，U6放弃了U8那华而不实的滑动触摸音量调节方式，换回了更加务实的滚轮，并且这颗滚轮的阻尼感做得还不错，金色与机身的暗绿色也很搭。与此同时U6的电源按键形状也更加常规，左侧上下曲与播放/暂停三键垫高于机身很多，盲操起来要舒服不少——缺点是也因此比较容易被误碰到。</p><p><img alt=\"DSC_4752.jpg\" src=\"http://pic.zaeke.com/img161413472456732.jpeg\" width=\"1400\" height=\"933\"><br></p><p><img alt=\"DSC_4750.jpg\" src=\"http://pic.zaeke.com/img161413481133771.jpeg\" width=\"1400\" height=\"933\"><br></p><p>在同价位的便携DAP中，U6显然属于个头比较小的产品，比起隔壁Astell&amp;Kern主打小巧便携的SR25还要短一些，厚度和宽度则稍大一点，二者体积规格基本相当，单手操作很轻松。对于U6的外观设计，我只有两点意见：其一是TF插入卡槽后会稍突出于机身；其二是U6的平衡接口既非2.5mm亦非4.4mm，而是爹不疼娘不爱的3.5Pro……这导致市面上兼容其平衡接口的耳机与升级线很难找，作为补偿方案U6随机附送了一条转接2.5mm的小尾巴，然而转接终究只是个比较“将就”的方案，更别说4.4mm用户还是要自备转接线。</p><p><img alt=\"DSC_4753.jpg\" src=\"http://pic.zaeke.com/img161413485249870.jpeg\" width=\"1400\" height=\"933\"><br></p><p><img alt=\"DSC_4751.jpg\" src=\"http://pic.zaeke.com/img161413486698189.jpeg\" width=\"1400\" height=\"933\"><br></p><p>虽然在吸取了U8的经验教训之后U6的的外观设计很快恢复了正常，但软件体验这事儿显然不是一朝一夕就能搞定的。U6论流畅度倒是不错，但交互体验说实话并不怎么友好，其界面和逻辑都有很多反人类的问题：比如音量调节动画不能手动快速退出，又比如白色底的专辑封面侵入顶栏会导致电量及音量信息看不清，叒比如分类导航栏太窄导致经常误点到曲目，叕比如它不支持耳机拔出自动暂停……<b>总之就是相当恼人，</b>更别指望什么流媒体。另外U6的续航虽然比U8正常了不少但也远谈不上优秀，充电只支持到5V 2A，甚至不兼容PD充电器的USB-C输出，发热量也比较大，不戴保护套的话握持起来实在不好受。</p><p><img alt=\"DSC_4756.jpg\" src=\"http://pic.zaeke.com/img161413489413067.jpeg\" width=\"1400\" height=\"933\"><br></p><p>不过兴许能与其发热量挂上关系的是，<b>个头颇为迷你的U6声音中所蕴藏的能量感却相当充足。</b>单纯看纸面参数的话，U6的输出电平不过2.1Vrms平衡4.2Vrms，最大输出功率130mW，跟国砖们最看不起的Astell&amp;Kern前一代产品（SP1000、SE100、SR15）倒是很接近。然而“推好”这事儿对于绝大多数耳塞来说与前端的最大输出功率并没有太强的直接关联（除非弱到了Walkman那份儿上），玩家想要的“推好”其实无非密度扎实动态饱满，而推力欠奉的主要表现就是密度虚散动态疲软——聊到这里U6可就不困了啊，虽说2.1Vrms的输出电平看起来不免有些弱鸡，但人家同时也标榜了121dB的动态范围。</p><p><img alt=\"DSC_5137.jpg\" src=\"http://pic.zaeke.com/img161413493739947.jpeg\" width=\"1400\" height=\"933\"><br></p><p>事实上U6的声音也的确足够澎湃，我尝试搭配的几条耳塞中既包括光、RSV、Turii、破军、VxV、AK T9iE这种较易驱动的，也包括Dorado 2020和RS10这种相对难伺候的，单端口驱动出来的密度与动态表现相对U6所在价位而言都可以称得上出色。尤其像Turii和破军这种本身以动态见长的产品，搭配U6还能进一步突出其优势，喜欢澎湃动感路线又不想在预算或体积上让步太多的玩家大可以考虑下U6。</p><p>而且值得夸赞的一点是，U6的“动感”的确是依靠优秀的动态表现来达成的，而非把能量分布翘得比较高以做出一副比较冲而激烈的面貌。论能量分布，U6恰是一台较为均衡持重的机器，并没有刻意突出高低频或强化速度感来摆出大开大合的架势，也不会对耳机自身的风格造成明显的干涉。足够的动态储备加上不偏科的音色令U6对于各类曲目和不同风格的耳机都有较为良好的适应性。</p><p><img alt=\"DSC_5136.jpg\" src=\"http://pic.zaeke.com/img161413496438004.jpeg\" width=\"1400\" height=\"933\"><br></p><p>唯一能挑出来的毛病是，U6的单端音色存在比较容易察觉到的颗粒感。虽说体积相当纸面参数也接近的情况下，U6能够驱动出的动态明显优于SR25，但SR25的音色要比U6更加细腻流畅一点——这时候我们就有必要搬出平衡口来了。</p><p>U6的平衡口面貌与单端一致性还是比较高的，能量分布依然比较均衡，不会如有些产品那样平衡口比单端口冷薄快一大截。在此基础你能明显感知到平衡口驱动力提升所带来的密度优势，这会令U6呈现出的声音较单端更加扎实细腻顺畅，换言之补上了单端口的唯一短板。</p><p>然后就是平衡输出增加分离度的常规操作，声场会伸展得更开扬层次感也更清晰错落。动态方面倒是没听出明显的长进，不过U6的单端口动态已经足够好就是了。</p><p><img alt=\"DSC_5132.jpg\" src=\"http://pic.zaeke.com/img161413499476154.jpeg\" width=\"1400\" height=\"933\"><br></p><p>所以我更喜欢U6的平衡口，只可惜它是3.5Pro，这大大降低了使用便利性，在没有3.5Pro插头线材的情况下我只能搭配随U6附送的转接线使用。不过好在U6的单端口也蛮优秀，嫌麻烦的时候用单端也不至于觉得差很多。</p><p><b>搭配方面，U6的音色并不偏科所以对耳塞的音色包容度比较高，</b>我手头的耳塞搭配U6的音色面貌都比较忠实于耳塞原本的风格。即便是RS10这样难于驱动的型号，充裕的动态储备也令其完全可听。所以如果你将U6作为主力前端那么直接根据自己的音色喜好挑选对应的耳机就好，要是飞朵的天狼星二代或者阿缇米斯这种自带3.5Pro的产品就更好（虽说飞朵也是转接线但好歹材质与耳机线是一样的，避免了U6附赠转接线引入的影响）。</p><p><b>至于音乐类型，同样出于比较均衡的能量分布，U6也并不挑歌。</b>而且比起同样属于“MP3体型”的ZX505和SR25，U6凭借其动态优势还能更好地胜任摇滚或交响乐曲目。不同于当年经常与IE8成双出现的“古典神器”C4，如今的U6对于不同风格的耳机和不同类型的音乐均具备良好的包容性。</p><p><img alt=\"DSC_5133.jpg\" src=\"http://pic.zaeke.com/img161413503038241.jpeg\" width=\"1400\" height=\"933\"><br></p><p>总体而言，U6作为5000元价位的便携播放器，其外观设计、体积控制与音质表现都能令我满意，需要重点改进的是以下这两点：一是3.5Pro标准并没有成功地推广开，主流耳机与线材品牌鲜有适配，换成4.4mm或2.5mm与4.4mm同时提供势在必行；二是U6的界面设计还存在诸多反人类的地方，所幸流畅度和稳定性没问题，改起来的难度不会特别高。</p><p>这两个痛点真的“很痛”，但并不算很难解决。如果七彩虹的下一台便携HiFi播放器能搞定这两点硬伤，想必会具备不俗的竞争力。</p>', '<p class=\"p_style\">严格来说第一块“国砖”应是HIFIMAN的HM801，但相信很多人跟我一样，是从七彩虹C4开始认识“国砖”这种产品的。设计复古、质感粗拙、交互低效、体积硕大构成了后MP3时代第一批国产便携HiFi播放器的关键词，浓缩为两个字，便是“国砖”。<br></p><p class=\"p_style\">令人唏嘘的是，作为国砖最早期布道者的七彩虹，在C4之后沉寂了很久没有推出过有关注度的同类产品，直到旗舰型号U8，七彩虹才算是又一次真正回到了玩家的视线之中——然而作为七彩虹回归的首个型号，U8在使用体验和续航力等方面被诟病颇多。“康复训练”过后，七彩虹推出的这台U6才终于算是一款较为成熟的产品。</p><p class=\"jsmargin\"><img alt=\"DSC_4749.jpg\" src=\"http://pic.zaeke.com/img161413471282687.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">这种“成熟”首先在机身设计上就有体现，U6放弃了U8那华而不实的滑动触摸音量调节方式，换回了更加务实的滚轮，并且这颗滚轮的阻尼感做得还不错，金色与机身的暗绿色也很搭。与此同时U6的电源按键形状也更加常规，左侧上下曲与播放/暂停三键垫高于机身很多，盲操起来要舒服不少——缺点是也因此比较容易被误碰到。</p><p class=\"jsmargin\"><img alt=\"DSC_4752.jpg\" src=\"http://pic.zaeke.com/img161413472456732.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"DSC_4750.jpg\" src=\"http://pic.zaeke.com/img161413481133771.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">在同价位的便携DAP中，U6显然属于个头比较小的产品，比起隔壁Astell&amp;Kern主打小巧便携的SR25还要短一些，厚度和宽度则稍大一点，二者体积规格基本相当，单手操作很轻松。对于U6的外观设计，我只有两点意见：其一是TF插入卡槽后会稍突出于机身；其二是U6的平衡接口既非2.5mm亦非4.4mm，而是爹不疼娘不爱的3.5Pro……这导致市面上兼容其平衡接口的耳机与升级线很难找，作为补偿方案U6随机附送了一条转接2.5mm的小尾巴，然而转接终究只是个比较“将就”的方案，更别说4.4mm用户还是要自备转接线。</p><p class=\"jsmargin\"><img alt=\"DSC_4753.jpg\" src=\"http://pic.zaeke.com/img161413485249870.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"DSC_4751.jpg\" src=\"http://pic.zaeke.com/img161413486698189.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">虽然在吸取了U8的经验教训之后U6的的外观设计很快恢复了正常，但软件体验这事儿显然不是一朝一夕就能搞定的。U6论流畅度倒是不错，但交互体验说实话并不怎么友好，其界面和逻辑都有很多反人类的问题：比如音量调节动画不能手动快速退出，又比如白色底的专辑封面侵入顶栏会导致电量及音量信息看不清，叒比如分类导航栏太窄导致经常误点到曲目，叕比如它不支持耳机拔出自动暂停……<b>总之就是相当恼人，</b>更别指望什么流媒体。另外U6的续航虽然比U8正常了不少但也远谈不上优秀，充电只支持到5V 2A，甚至不兼容PD充电器的USB-C输出，发热量也比较大，不戴保护套的话握持起来实在不好受。</p><p class=\"jsmargin\"><img alt=\"DSC_4756.jpg\" src=\"http://pic.zaeke.com/img161413489413067.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">不过兴许能与其发热量挂上关系的是，<b>个头颇为迷你的U6声音中所蕴藏的能量感却相当充足。</b>单纯看纸面参数的话，U6的输出电平不过2.1Vrms平衡4.2Vrms，最大输出功率130mW，跟国砖们最看不起的Astell&amp;Kern前一代产品（SP1000、SE100、SR15）倒是很接近。然而“推好”这事儿对于绝大多数耳塞来说与前端的最大输出功率并没有太强的直接关联（除非弱到了Walkman那份儿上），玩家想要的“推好”其实无非密度扎实动态饱满，而推力欠奉的主要表现就是密度虚散动态疲软——聊到这里U6可就不困了啊，虽说2.1Vrms的输出电平看起来不免有些弱鸡，但人家同时也标榜了121dB的动态范围。</p><p class=\"jsmargin\"><img alt=\"DSC_5137.jpg\" src=\"http://pic.zaeke.com/img161413493739947.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">事实上U6的声音也的确足够澎湃，我尝试搭配的几条耳塞中既包括光、RSV、Turii、破军、VxV、AK T9iE这种较易驱动的，也包括Dorado 2020和RS10这种相对难伺候的，单端口驱动出来的密度与动态表现相对U6所在价位而言都可以称得上出色。尤其像Turii和破军这种本身以动态见长的产品，搭配U6还能进一步突出其优势，喜欢澎湃动感路线又不想在预算或体积上让步太多的玩家大可以考虑下U6。</p><p class=\"p_style\">而且值得夸赞的一点是，U6的“动感”的确是依靠优秀的动态表现来达成的，而非把能量分布翘得比较高以做出一副比较冲而激烈的面貌。论能量分布，U6恰是一台较为均衡持重的机器，并没有刻意突出高低频或强化速度感来摆出大开大合的架势，也不会对耳机自身的风格造成明显的干涉。足够的动态储备加上不偏科的音色令U6对于各类曲目和不同风格的耳机都有较为良好的适应性。</p><p class=\"jsmargin\"><img alt=\"DSC_5136.jpg\" src=\"http://pic.zaeke.com/img161413496438004.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">唯一能挑出来的毛病是，U6的单端音色存在比较容易察觉到的颗粒感。虽说体积相当纸面参数也接近的情况下，U6能够驱动出的动态明显优于SR25，但SR25的音色要比U6更加细腻流畅一点——这时候我们就有必要搬出平衡口来了。</p><p class=\"p_style\">U6的平衡口面貌与单端一致性还是比较高的，能量分布依然比较均衡，不会如有些产品那样平衡口比单端口冷薄快一大截。在此基础你能明显感知到平衡口驱动力提升所带来的密度优势，这会令U6呈现出的声音较单端更加扎实细腻顺畅，换言之补上了单端口的唯一短板。</p><p class=\"p_style\">然后就是平衡输出增加分离度的常规操作，声场会伸展得更开扬层次感也更清晰错落。动态方面倒是没听出明显的长进，不过U6的单端口动态已经足够好就是了。</p><p class=\"jsmargin\"><img alt=\"DSC_5132.jpg\" src=\"http://pic.zaeke.com/img161413499476154.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">所以我更喜欢U6的平衡口，只可惜它是3.5Pro，这大大降低了使用便利性，在没有3.5Pro插头线材的情况下我只能搭配随U6附送的转接线使用。不过好在U6的单端口也蛮优秀，嫌麻烦的时候用单端也不至于觉得差很多。</p><p class=\"p_style\"><b>搭配方面，U6的音色并不偏科所以对耳塞的音色包容度比较高，</b>我手头的耳塞搭配U6的音色面貌都比较忠实于耳塞原本的风格。即便是RS10这样难于驱动的型号，充裕的动态储备也令其完全可听。所以如果你将U6作为主力前端那么直接根据自己的音色喜好挑选对应的耳机就好，要是飞朵的天狼星二代或者阿缇米斯这种自带3.5Pro的产品就更好（虽说飞朵也是转接线但好歹材质与耳机线是一样的，避免了U6附赠转接线引入的影响）。</p><p class=\"p_style\"><b>至于音乐类型，同样出于比较均衡的能量分布，U6也并不挑歌。</b>而且比起同样属于“MP3体型”的ZX505和SR25，U6凭借其动态优势还能更好地胜任摇滚或交响乐曲目。不同于当年经常与IE8成双出现的“古典神器”C4，如今的U6对于不同风格的耳机和不同类型的音乐均具备良好的包容性。</p><p class=\"jsmargin\"><img alt=\"DSC_5133.jpg\" src=\"http://pic.zaeke.com/img161413503038241.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">总体而言，U6作为5000元价位的便携播放器，其外观设计、体积控制与音质表现都能令我满意，需要重点改进的是以下这两点：一是3.5Pro标准并没有成功地推广开，主流耳机与线材品牌鲜有适配，换成4.4mm或2.5mm与4.4mm同时提供势在必行；二是U6的界面设计还存在诸多反人类的地方，所幸流畅度和稳定性没问题，改起来的难度不会特别高。</p><p class=\"p_style\">这两个痛点真的“很痛”，但并不算很难解决。如果七彩虹的下一台便携HiFi播放器能搞定这两点硬伤，想必会具备不俗的竞争力。</p>', 'http://pic.zaeke.com/img161456906669923.jpeg', 3794, 0, 0, NULL, '2021-02-23 18:52:11', '2021-03-26 23:21:22');
INSERT INTO `article` VALUES (16103, '轻装上阵的雷柏 VM150S 游戏耳机，带来轻盈鏖战新体验', '战术竞技游戏是一项集操控与反应于一身的娱乐项目，特别对于近年大热的战术竞技型射击类沙盒游戏而言，玩家在游戏过程中除了需要眼观四处以外，其还能透过音效判断当前游戏...', '[object Object]', '小啤', 170, 'http://pic.zaeke.com/user_1533281478_8.png', '<p>战术竞技游戏是一项集操控与反应于一身的娱乐项目，特别对于近年大热的战术竞技型射击类沙盒游戏而言，玩家在游戏过程中除了需要眼观四处以外，其还能透过音效判断当前游戏的即时战况。当然，玩家手上的硬件设备性能决定了游戏的上手体验，甚至可以提高游戏胜率。</p><p style=\"text-align: center;\"><b>拒绝闷热与沉重，多声道游戏耳机也能轻盈化</b></p><p>提及多声道游戏耳机，大部分人先入为主地把它与头戴式耳机有所联想。确实，目前市面上的绝大部分多声道耳机都是以头戴式的形态示人，当中缘由并不复杂，因为头戴式耳机凭借足够大的机身尺寸，其机身可轻松放置大尺寸扬声器，并且通过产品本身的独特形态，使用户的耳朵与音源之间可以保持一定的距离，从而给予用户更饱满、空间感更深的听觉体验。</p><p><img alt=\"IMG_0637.jpg\" src=\"http://pic.zaeke.com/img161423670696240.jpeg\" width=\"1400\" height=\"933\"><br></p><p>任何事物都一定具有两面性，头戴式耳机在体验方面的劣势也是比较显著的，例如它普遍存在闷热、体积重、不便携等弊端。既然头戴式游戏耳机存在诸多软肋，那么音频厂商们自然也看在眼里，专注游戏耳机领域多年的雷柏，近日就推出了一款主打轻量化路线的虚拟 7.1 声道游戏耳机——雷柏 VM150S。</p><p><img alt=\"IMG_0651.jpg\" src=\"http://pic.zaeke.com/img161423687823149.jpeg\" width=\"1400\" height=\"933\"><br></p><p>与一般的头戴式游戏耳机不同，雷柏 VM150S 耳机主体采用了入耳式方案，较大程度地缓解了久戴之后产生的佩戴疲劳感和闷热情况，考虑到这是一款可提供虚拟 7.1 声道的游戏耳机产品，因此耳机主体内部空间相对有限的它，采用了外置 USB 声卡方案。除此，该外置 USB 声卡模块还提供了耳机音量大小调节、麦克风音量大小调节、麦克风开/关闭、Logo 炫光灯开/关四个按键功能，至于该区域的用料和做工嘛，确实还有较大提升空间。</p><p><img alt=\"IMG_0644.jpg\" src=\"http://pic.zaeke.com/img161423671669472.jpeg\" width=\"1400\" height=\"933\"><br></p><p> </p><p>雷柏 VM150S 游戏耳机提供了 S 和 M 两种尺寸的耳帽，为了提高佩戴稳固性，两款耳帽均自带耳撑设计。按照我的主观佩戴感受，得益于这款耳机的耳帽相对柔软，即便长时间佩戴，也不会有明显胀痛感。显然，相比较于头戴式耳机，雷柏 VM150S 游戏耳机轻量化设计，比一般的头戴式耳机的佩戴方式都要显得舒适，不用担心夏季被耳罩闷热问题所困。</p><p style=\"text-align: center;\"><b>体积虽小，可本事却一点都不小</b></p><p>作为一款游戏定位的有线耳机，雷柏 VM150S 游戏耳机需要依靠 USB-A 接口进行供电和连接，想要完全发挥出这款耳机的性能，只能通过 USB-A 接口进行搭配使用。可以说，它是一款针对 PC 连身而设的游戏耳机。</p><p><img alt=\"IMG_0662.jpg\" src=\"http://pic.zaeke.com/img161423678931535.jpeg\" width=\"1400\" height=\"933\"><br></p><p>对于无 USB-A 接口的设备而言，雷柏 VM150S 游戏耳机也提供了一条传统 1 分 2 音频 3.5mm 转换线，可以把它视为音频延长线，方便台式机这类设备连接需要，而连接手机和平板这类手持式移动电子设备时，可以直接用耳机本身的 3.5mm 音频口直连。</p><p><img alt=\"IMG_0656.jpg\" src=\"http://pic.zaeke.com/img161423683946166.jpeg\" width=\"1400\" height=\"933\"><br></p><p>不仅如此，即使脱离了外置 USB 声卡，这款耳机也额外提供了可调节音量调节按钮和歌曲的播放/暂停功能的线控，只不过音量大小调节按键基本没有阻尼感。<br></p><p><img alt=\"1613893682(1)_副本.gif\" src=\"http://pic.zaeke.com/img161423673571365.gif\" width=\"1000\" height=\"750\"><br></p><p>为了满足用户对于雷柏 VM150S 游戏耳机的音效自定义和功能切换需求，雷柏专门为此开发了对应的驱动程序。初次上手 Rapoo 7.1CH Gaming Headphone 驱动程序时，由于该程序没有提供上手引导教程，因此经过了超十分钟的摸索之后，才发现要双击左侧的“Speakers”和“Mic”图标才能打开相关的设置页面。</p><p>通过 Rapoo 7.1CH Gaming Headphone 驱动程序，用户可以针对不同类型的游戏进行音效的设置，例如 FPS、TPS、MOBA、RPG 等类型游戏。其中“喇叭驱动器”则提供了多达 7 种微调选项，用户可以自行调节耳机的音量大小、取样频率、均衡器、环境音效、升降 KEY 和原因消除等，官方默认提供了 Hi-Fi、电影、音乐、自定义四种模式供用户快速选择。</p><p>针对“麦克风”的功能，该耳机也提供了音量控制、取样频率、麦克风回音、魔幻音效、话筒增益等功能调节，其中取样频率分别提供了 44.1KHz 和 48KHz 两个选项，另外魔幻音效则提供了怪兽、卡通、男人、女人 4 种趣味性选项。不难发现，雷柏其实是希望尽可能丰富该耳机的可玩性。整体而言，Rapoo 7.1CH Gaming Headphone 驱动程序的功能还是比较全面的，唯独界面的美感似乎还有一定的提升空间。</p><p><img alt=\"IMG_0654.jpg\" src=\"http://pic.zaeke.com/img161423677586917.jpeg\" width=\"1400\" height=\"933\"><br></p><p>雷柏 VM150S 游戏耳机还提供了可拆卸式指向性麦克风，其中指向性麦克风可以尽可能降低环境噪音对于人声的覆盖，给予万家足够清晰的队友沟通体验。毕竟不是所有游戏都需要使用麦克风沟通，日常不需要使用时可以对它进行拆卸收纳，这样的设计既保证了收音效果，也确保了耳机的佩戴舒适性。</p><p><img alt=\"IMG_0659.jpg\" src=\"http://pic.zaeke.com/img161423690659446.jpeg\" width=\"1400\" height=\"933\"><br></p><p>在实际体验中，使用雷柏 VM150S 游戏耳机上手《CS：GO》 、《绝地求生》、《英雄联盟》三款游戏时，虽然《CS：GO》最高仅支持 5.1 声道，可是默认的立体声音效其实已经可协助用户从声源判断战况，例如游戏中的武器触发位置距离、脚步声，甚至近距离时候的手雷拉弦都能清晰地接收，可是这并不能完全发挥出雷柏 VM150S 游戏耳机的真正实力。</p><p><img alt=\"IMG_0660.jpg\" src=\"http://pic.zaeke.com/img161423692273814.jpeg\" width=\"1400\" height=\"933\"><br></p><p>除此，在《绝地求生》这款游戏中，在确认开启游戏中的 HRTF 功能之后，搭配雷柏 VM150S 游戏耳机上手这款游戏时，可以明显感受到耳机本身凭借不俗的声场表现力，可以营造出一种声临其境的听感，从而达到提高沉浸感的作用。</p><p><img alt=\"IMG_0658.jpg\" src=\"http://pic.zaeke.com/img161423689842611.jpeg\" width=\"1400\" height=\"933\"><br></p><p>虽然《英雄联盟》这类 MOBA 游戏对于音效的依赖程度不及上述两款射击类游戏，可是当队友发出信号，部分英雄技能也会有音效反馈，更别提传送和闪现都是有技能音效，像雷柏 VM150S 游戏耳机这类游戏耳机，一定程度给予用户游戏体验的提升感。尽管在听声辨位的呈现方面不及部分头戴式游戏耳机，可是雷柏 VM150S 游戏耳机以绝对的体积优势，使玩家轻松获得不俗的游戏听感。</p><p><img alt=\"IMG_0663.jpg\" src=\"http://pic.zaeke.com/img161423693944547.jpeg\" width=\"1400\" height=\"933\"><br></p><p>凭借机身的 10mm 高性能扬声器和 OFC 无氧铜线材，雷柏 VM150S 游戏耳机不仅在游戏中可以带来不俗的声场表现，面对日常的流媒体视频和音乐的欣赏时，这款耳机的实际表现也能有所顾及。</p><p style=\"text-align: center;\"><b>不必付出高昂投入，也能让玩家掌控战局</b></p><p>不可否认，雷柏 VM150S 游戏耳机与部分头戴式游戏耳机相比，其声场和空间感无疑会略逊一筹，可凭借更轻便的机身和更舒适的佩戴体验，它确实迎合了那些不喜欢佩戴头戴式耳机产品的消费群体需求。</p><p><img alt=\"IMG_0650.jpg\" src=\"http://pic.zaeke.com/img161423695533406.jpeg\" width=\"1400\" height=\"933\"><br></p><p>雷柏 VM150S 游戏耳机旨在提供一种身临其境的游戏体验，其最可贵的莫过于自身的高性价比。作为一款具备独立声卡的虚拟 7.1 声道游戏耳机产品，其官方定价仅需 179 元，再配合电商平台偶尔推出的打折优惠活动，使这款耳机在价格优势上尤为突出。在有限的预算之内，如果不过分追求极致的堆料，那么雷柏 VM150S 游戏耳机已经可以满足入门级游戏爱好者的听感需求了。</p>', '<p class=\"p_style\">战术竞技游戏是一项集操控与反应于一身的娱乐项目，特别对于近年大热的战术竞技型射击类沙盒游戏而言，玩家在游戏过程中除了需要眼观四处以外，其还能透过音效判断当前游戏的即时战况。当然，玩家手上的硬件设备性能决定了游戏的上手体验，甚至可以提高游戏胜率。</p><p style=\"text-align: center;\" class=\"p_style\"><b>拒绝闷热与沉重，多声道游戏耳机也能轻盈化</b></p><p class=\"p_style\">提及多声道游戏耳机，大部分人先入为主地把它与头戴式耳机有所联想。确实，目前市面上的绝大部分多声道耳机都是以头戴式的形态示人，当中缘由并不复杂，因为头戴式耳机凭借足够大的机身尺寸，其机身可轻松放置大尺寸扬声器，并且通过产品本身的独特形态，使用户的耳朵与音源之间可以保持一定的距离，从而给予用户更饱满、空间感更深的听觉体验。</p><p class=\"jsmargin\"><img alt=\"IMG_0637.jpg\" src=\"http://pic.zaeke.com/img161423670696240.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">任何事物都一定具有两面性，头戴式耳机在体验方面的劣势也是比较显著的，例如它普遍存在闷热、体积重、不便携等弊端。既然头戴式游戏耳机存在诸多软肋，那么音频厂商们自然也看在眼里，专注游戏耳机领域多年的雷柏，近日就推出了一款主打轻量化路线的虚拟 7.1 声道游戏耳机——雷柏 VM150S。</p><p class=\"jsmargin\"><img alt=\"IMG_0651.jpg\" src=\"http://pic.zaeke.com/img161423687823149.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">与一般的头戴式游戏耳机不同，雷柏 VM150S 耳机主体采用了入耳式方案，较大程度地缓解了久戴之后产生的佩戴疲劳感和闷热情况，考虑到这是一款可提供虚拟 7.1 声道的游戏耳机产品，因此耳机主体内部空间相对有限的它，采用了外置 USB 声卡方案。除此，该外置 USB 声卡模块还提供了耳机音量大小调节、麦克风音量大小调节、麦克风开/关闭、Logo 炫光灯开/关四个按键功能，至于该区域的用料和做工嘛，确实还有较大提升空间。</p><p class=\"jsmargin\"><img alt=\"IMG_0644.jpg\" src=\"http://pic.zaeke.com/img161423671669472.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\"> </p><p class=\"p_style\">雷柏 VM150S 游戏耳机提供了 S 和 M 两种尺寸的耳帽，为了提高佩戴稳固性，两款耳帽均自带耳撑设计。按照我的主观佩戴感受，得益于这款耳机的耳帽相对柔软，即便长时间佩戴，也不会有明显胀痛感。显然，相比较于头戴式耳机，雷柏 VM150S 游戏耳机轻量化设计，比一般的头戴式耳机的佩戴方式都要显得舒适，不用担心夏季被耳罩闷热问题所困。</p><p style=\"text-align: center;\" class=\"p_style\"><b>体积虽小，可本事却一点都不小</b></p><p class=\"p_style\">作为一款游戏定位的有线耳机，雷柏 VM150S 游戏耳机需要依靠 USB-A 接口进行供电和连接，想要完全发挥出这款耳机的性能，只能通过 USB-A 接口进行搭配使用。可以说，它是一款针对 PC 连身而设的游戏耳机。</p><p class=\"jsmargin\"><img alt=\"IMG_0662.jpg\" src=\"http://pic.zaeke.com/img161423678931535.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">对于无 USB-A 接口的设备而言，雷柏 VM150S 游戏耳机也提供了一条传统 1 分 2 音频 3.5mm 转换线，可以把它视为音频延长线，方便台式机这类设备连接需要，而连接手机和平板这类手持式移动电子设备时，可以直接用耳机本身的 3.5mm 音频口直连。</p><p class=\"jsmargin\"><img alt=\"IMG_0656.jpg\" src=\"http://pic.zaeke.com/img161423683946166.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">不仅如此，即使脱离了外置 USB 声卡，这款耳机也额外提供了可调节音量调节按钮和歌曲的播放/暂停功能的线控，只不过音量大小调节按键基本没有阻尼感。<br></p><p class=\"jsmargin\"><img alt=\"1613893682(1)_副本.gif\" src=\"http://pic.zaeke.com/img161423673571365.gif\" width=\"1000\" height=\"750\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">为了满足用户对于雷柏 VM150S 游戏耳机的音效自定义和功能切换需求，雷柏专门为此开发了对应的驱动程序。初次上手 Rapoo 7.1CH Gaming Headphone 驱动程序时，由于该程序没有提供上手引导教程，因此经过了超十分钟的摸索之后，才发现要双击左侧的“Speakers”和“Mic”图标才能打开相关的设置页面。</p><p class=\"p_style\">通过 Rapoo 7.1CH Gaming Headphone 驱动程序，用户可以针对不同类型的游戏进行音效的设置，例如 FPS、TPS、MOBA、RPG 等类型游戏。其中“喇叭驱动器”则提供了多达 7 种微调选项，用户可以自行调节耳机的音量大小、取样频率、均衡器、环境音效、升降 KEY 和原因消除等，官方默认提供了 Hi-Fi、电影、音乐、自定义四种模式供用户快速选择。</p><p class=\"p_style\">针对“麦克风”的功能，该耳机也提供了音量控制、取样频率、麦克风回音、魔幻音效、话筒增益等功能调节，其中取样频率分别提供了 44.1KHz 和 48KHz 两个选项，另外魔幻音效则提供了怪兽、卡通、男人、女人 4 种趣味性选项。不难发现，雷柏其实是希望尽可能丰富该耳机的可玩性。整体而言，Rapoo 7.1CH Gaming Headphone 驱动程序的功能还是比较全面的，唯独界面的美感似乎还有一定的提升空间。</p><p class=\"jsmargin\"><img alt=\"IMG_0654.jpg\" src=\"http://pic.zaeke.com/img161423677586917.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">雷柏 VM150S 游戏耳机还提供了可拆卸式指向性麦克风，其中指向性麦克风可以尽可能降低环境噪音对于人声的覆盖，给予万家足够清晰的队友沟通体验。毕竟不是所有游戏都需要使用麦克风沟通，日常不需要使用时可以对它进行拆卸收纳，这样的设计既保证了收音效果，也确保了耳机的佩戴舒适性。</p><p class=\"jsmargin\"><img alt=\"IMG_0659.jpg\" src=\"http://pic.zaeke.com/img161423690659446.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">在实际体验中，使用雷柏 VM150S 游戏耳机上手《CS：GO》 、《绝地求生》、《英雄联盟》三款游戏时，虽然《CS：GO》最高仅支持 5.1 声道，可是默认的立体声音效其实已经可协助用户从声源判断战况，例如游戏中的武器触发位置距离、脚步声，甚至近距离时候的手雷拉弦都能清晰地接收，可是这并不能完全发挥出雷柏 VM150S 游戏耳机的真正实力。</p><p class=\"jsmargin\"><img alt=\"IMG_0660.jpg\" src=\"http://pic.zaeke.com/img161423692273814.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">除此，在《绝地求生》这款游戏中，在确认开启游戏中的 HRTF 功能之后，搭配雷柏 VM150S 游戏耳机上手这款游戏时，可以明显感受到耳机本身凭借不俗的声场表现力，可以营造出一种声临其境的听感，从而达到提高沉浸感的作用。</p><p class=\"jsmargin\"><img alt=\"IMG_0658.jpg\" src=\"http://pic.zaeke.com/img161423689842611.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">虽然《英雄联盟》这类 MOBA 游戏对于音效的依赖程度不及上述两款射击类游戏，可是当队友发出信号，部分英雄技能也会有音效反馈，更别提传送和闪现都是有技能音效，像雷柏 VM150S 游戏耳机这类游戏耳机，一定程度给予用户游戏体验的提升感。尽管在听声辨位的呈现方面不及部分头戴式游戏耳机，可是雷柏 VM150S 游戏耳机以绝对的体积优势，使玩家轻松获得不俗的游戏听感。</p><p class=\"jsmargin\"><img alt=\"IMG_0663.jpg\" src=\"http://pic.zaeke.com/img161423693944547.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">凭借机身的 10mm 高性能扬声器和 OFC 无氧铜线材，雷柏 VM150S 游戏耳机不仅在游戏中可以带来不俗的声场表现，面对日常的流媒体视频和音乐的欣赏时，这款耳机的实际表现也能有所顾及。</p><p style=\"text-align: center;\" class=\"p_style\"><b>不必付出高昂投入，也能让玩家掌控战局</b></p><p class=\"p_style\">不可否认，雷柏 VM150S 游戏耳机与部分头戴式游戏耳机相比，其声场和空间感无疑会略逊一筹，可凭借更轻便的机身和更舒适的佩戴体验，它确实迎合了那些不喜欢佩戴头戴式耳机产品的消费群体需求。</p><p class=\"jsmargin\"><img alt=\"IMG_0650.jpg\" src=\"http://pic.zaeke.com/img161423695533406.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">雷柏 VM150S 游戏耳机旨在提供一种身临其境的游戏体验，其最可贵的莫过于自身的高性价比。作为一款具备独立声卡的虚拟 7.1 声道游戏耳机产品，其官方定价仅需 179 元，再配合电商平台偶尔推出的打折优惠活动，使这款耳机在价格优势上尤为突出。在有限的预算之内，如果不过分追求极致的堆料，那么雷柏 VM150S 游戏耳机已经可以满足入门级游戏爱好者的听感需求了。</p>', 'http://pic.zaeke.com/img161456905551091.jpeg', 5129, 0, 0, NULL, '2021-02-24 23:09:56', '2021-03-27 07:03:49');
INSERT INTO `article` VALUES (16105, '64Audio Nio评测：“未来”已来', '提到“Nio”，估计大多数人首先会想到的是某知名电动车品牌，及其代表的汽车行业“大趋势”——好巧不巧的是，64Audio这副被命名为“Nio”的耳塞，也可以说是...', '[object Object]', '斗室听澜', 189, 'http://pic.zaeke.com/user_1535960095png', '<p>提到“Nio”，估计大多数人首先会想到的是某知名电动车品牌，及其代表的汽车行业“大趋势”——好巧不巧的是，64Audio这副被命名为“Nio”的耳塞，也可以说是高端HiFi耳机产品“大趋势”的一个缩影。<br></p><p>由于这一次我们评测所使用的Nio样机来自总代理，且对方仅提供了裸机，没有给到原包装配件，故我们不对其包装配件做过多评价。更何况64Audio耳机的包装配件相对其价位而言向来是无功亦无过的水平，本也没有太多好说的。</p><p>虽然Nio是一副圈铁耳机但它并没有采用Tia系列的设计，其造型设计与U18t等纯动铁耳塞产品基本一致，都具有标志性的Apex模块。Apex的意义在于不同规格模块通过改变空气流量可以提供不同程度的隔音效果和低频量，类似IE8/80/80S的旋钮构造，但可以徒手更换，无须借助任何工具。</p><p><img alt=\"DSC_5167.jpg\" src=\"http://pic.zaeke.com/img161424162463797.jpeg\" width=\"1400\" height=\"933\"><br></p><p>造型还是很熟悉的内味儿，但Nio在我看来比64Audio过去的公模耳塞产品都要漂亮不少，说它是最好看的64Audio公模耳塞也不过分。一方面Nio的面板框架选择了醒目的亮银色，更重要的是它在面板当中镶嵌了蓝色的新西兰鲍鱼壳——不同于率先在耳塞产品中采用鲍鱼壳装饰的Solaris SE，Nio的鲍鱼壳面板杂色更少且并非直接裸露在外，其在鲍鱼壳之上覆有64Audio logo以及树脂，令面板质感光洁圆润，“抽奖”成分相比Solaris SE大幅减少。即便是同两倍价格的本家旗舰Tia Fourte Noir相比，Nio的外观质感也要更上档次一些，两副耳机同时摆在不了解64Audio品牌的用户面前的话，Nio更可能被看做旗舰产品。</p><p><img alt=\"DSC_5159.jpg\" src=\"http://pic.zaeke.com/img161424164416790.jpeg\" width=\"1400\" height=\"933\"><br></p><p><img alt=\"DSC_5164.jpg\" src=\"http://pic.zaeke.com/img161424166152711.jpeg\" width=\"1400\" height=\"933\"><br></p><p>稍显可惜的是Nio的原配线比较拖累产品的档次感。这根黑皮铜镀银线连个正经的分线器都没有，线身和耳挂的质感也略显粗糙，线径虽然比较细但并不柔软，整条线都有如铁丝耳挂般容易被弯折定型，至于什么平衡插头甚至可换插头更不要想。这条在64Audio官网也仅售39.99美元的线实在是衬不上Nio的身价——当然，Tia Fourte Noir的原线卖相也没有比它好很多就是了。</p><p><img alt=\"DSC_5168.jpg\" src=\"http://pic.zaeke.com/img161424175912938.jpeg\" width=\"1400\" height=\"933\"><br></p><p>也因为Nio的原线为3.5mm立体声插头，本文对Nio的音质评价基于SP2000和钛菊的3.5mm单端接口。耳塞套并非原装，而是Symbio W即“小橘套”——随样机一起来的耳塞套就只要这个，并且Symbio耳塞套同样由64Audio的国代销售，国代也组织过Nio用户送Symbio耳塞套的活动，姑且当做国代指定官配也没什么问题。</p><p>Apex模块选择mX，其它几种模块虽然能提供比mX更优越的隔音效果，但同时也会将其低频量增加到较为夸张的程度以至于严重削弱Nio的通透感，<b>所以我并不推荐你们为Nio搭配其它Apex模块，这与我在Illumination评测中建议你们摘除滤网基于同样的原因：对通透感的牺牲得不偿失。</b></p><p>搭配mX模块和Symbio W的Nio音色是比较中正平衡的，无论看频响还是讲听感都是足够 “HiFi”、追求高保真的类型，能量分布均匀平滑，没有哪一部分频段的能量明显突出或凹陷。不过比起接近绝对中立的RS10，Nio还是有些许风格倾向的：一方面Nio的人声不像RS10那样素，厚度稍高结象位置稍近，听感会更加抓耳，甜美细腻的女声表现是Nio的一大亮点；另一方面，比起我的私模RS10，Nio的低频还要更加克制与收敛，量感属于中性稍显不足的程度，弹性虽然不错但弹回来得略早，即下潜不是很能令人满意。</p><p><img alt=\"DSC_5171.jpg\" src=\"http://pic.zaeke.com/img161424177870405.jpeg\" width=\"1400\" height=\"933\"><br></p><p>把比较对象换做同价位另一款热门圈铁Solaris 2020，Nio的特点会更加凸显，毕竟Solaris 2020恰恰是以澎湃且高质量的低频见长的产品，相形之下Nio的低频力量感差距就比较悬殊了。但在中高频表现力方面Nio又能反过来扳回些优势，更加纤细清晰的线条感令它的女声和高频乐器质感都比Solaris 2020抢眼且细致，虽然不至于像另一副鲍鱼壳耳机Solaris SE那样令我印象深刻，但至少它的女声表现优于RS10，女声和高频器乐表现力则都要超过Solaris 2020。</p><p>所以Nio一定要靠献祭低频质感来换取中高频的质感优势么？</p><p>其实这个问题部分要归因到Symbio W上，它虽然具有海绵夹层但整体仍然是偏硬且码数偏大的，这就令其舒服地佩戴严密不那么容易，低频自然会有损失。<b>更换过几种耳塞套之后，我认为另一种硅胶夹海绵构造的耳塞套EarrBOND EBT是最适合Nio的搭配，</b>它的硅胶与海绵部分质地要比Symbio W柔软很多，低频能量感与下潜都有明显改善，并且不会影响通透度——不过EarrBOND EBT也有个问题，就是S码与M码的大小差距过于明显，可能会出现S码嫌小M码又嫌大的情况。</p><p><img alt=\"DSC_5178.jpg\" src=\"http://pic.zaeke.com/img161424180424798.jpeg\" width=\"1400\" height=\"933\"><br></p><p>总之我建议Nio用户们尝试下更换耳塞套来平衡它的低频质感，除了弥补低频短板外也能找回来一些动态。至于Apex模块我还要再强调一次，不建议选择mX以外的模块，其它模块对低频的补偿太过火了。</p><p>以搭配mX模块和EarrBOND EBT的表现为准的话，Nio的音色可以说是没有明显缺憾的，硬素质也处在10000-15000元价位中比较出彩的水平上。对比RS10，同样走“HiFi”路线的Nio论细腻度并不落下风，较低的驱动力需求还使得它更容易达成良好的动态与密度；相比动态和声场拔群的Solaris 2020，Nio会显得拘谨不少，但细腻感更胜一筹。</p><p>足够中正全面的音色和不错的硬素质保障令Nio在大多数类型的音乐中都具备不错的表现力，人声也好高低频乐器也罢，Nio的还原力都是能够令人满意的。唯一我觉得差点意思的是Nio的声场不算出彩，虽说远不至于给人促狭混乱的印象，可也没法跟开阔兼具强烈立体感的Solaris 2020和A8000相比，在万元档产品中居中游。所以如果你钟情交响乐等强调场面感的曲目，Nio能给出尚算不错的表现力但也不拔尖，其它类型音乐爱好者可以放心选择。</p><p><img alt=\"DSC_5176.jpg\" src=\"http://pic.zaeke.com/img161424181866259.jpeg\" width=\"1400\" height=\"933\"><br></p><p>至于前端搭配，音色并不奇难险怪驱动力需求也不高的Nio自然不挑食，SP2000和钛菊搭配下来都能令我满意。二选一的话我更建议SP2000，钛菊的驱动力优势在Nio身上体现不出多少，同时音色会比SP2000严肃一些，SP2000搭配Nio表现出的人声和高频会比钛菊更鲜活动听一点。如果你不想随身带着动辄三四百克的播放器，那么一条优质的小尾巴如PAW S1或蓝蜻蜓都可以将Nio驱动至较为完整的音色状态。</p><p>总评Nio，这是64Audio迄今推出过的所有耳塞产品中我个人最满意的一款。虽然Nio的硬素质在64Audio产品中占不上第一流，但追究起音色的自然度，Nio就是最好的64Audio耳机，没有之一。再加上相当不错的卖相，在我看来Nio是64Audio耳机中最有旗舰风范的产品，以市场对该品牌的惯性认知，即便定价再高一些也不妨碍玩家们买单。</p><p><img alt=\"DSC_5170.jpg\" src=\"http://pic.zaeke.com/img161424183078604.jpeg\" width=\"1400\" height=\"933\"><br></p><p>跳出品牌着眼整个市场，2020年起出现的成功产品大都也是类似Nio的路线，强调平衡稳重自然的声音风格，鲜有特别个性的产品。你可以把“Nio”这个名字看做64Audio的未来愿景，也可以当做高端HiFi耳机的“大趋势”，无论如何，今后的高端产品平均水准都相当可期。</p><p>所以，你的钱包准备好了吗？</p>', '<p class=\"p_style\">提到“Nio”，估计大多数人首先会想到的是某知名电动车品牌，及其代表的汽车行业“大趋势”——好巧不巧的是，64Audio这副被命名为“Nio”的耳塞，也可以说是高端HiFi耳机产品“大趋势”的一个缩影。<br></p><p class=\"p_style\">由于这一次我们评测所使用的Nio样机来自总代理，且对方仅提供了裸机，没有给到原包装配件，故我们不对其包装配件做过多评价。更何况64Audio耳机的包装配件相对其价位而言向来是无功亦无过的水平，本也没有太多好说的。</p><p class=\"p_style\">虽然Nio是一副圈铁耳机但它并没有采用Tia系列的设计，其造型设计与U18t等纯动铁耳塞产品基本一致，都具有标志性的Apex模块。Apex的意义在于不同规格模块通过改变空气流量可以提供不同程度的隔音效果和低频量，类似IE8/80/80S的旋钮构造，但可以徒手更换，无须借助任何工具。</p><p class=\"jsmargin\"><img alt=\"DSC_5167.jpg\" src=\"http://pic.zaeke.com/img161424162463797.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">造型还是很熟悉的内味儿，但Nio在我看来比64Audio过去的公模耳塞产品都要漂亮不少，说它是最好看的64Audio公模耳塞也不过分。一方面Nio的面板框架选择了醒目的亮银色，更重要的是它在面板当中镶嵌了蓝色的新西兰鲍鱼壳——不同于率先在耳塞产品中采用鲍鱼壳装饰的Solaris SE，Nio的鲍鱼壳面板杂色更少且并非直接裸露在外，其在鲍鱼壳之上覆有64Audio logo以及树脂，令面板质感光洁圆润，“抽奖”成分相比Solaris SE大幅减少。即便是同两倍价格的本家旗舰Tia Fourte Noir相比，Nio的外观质感也要更上档次一些，两副耳机同时摆在不了解64Audio品牌的用户面前的话，Nio更可能被看做旗舰产品。</p><p class=\"jsmargin\"><img alt=\"DSC_5159.jpg\" src=\"http://pic.zaeke.com/img161424164416790.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"DSC_5164.jpg\" src=\"http://pic.zaeke.com/img161424166152711.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">稍显可惜的是Nio的原配线比较拖累产品的档次感。这根黑皮铜镀银线连个正经的分线器都没有，线身和耳挂的质感也略显粗糙，线径虽然比较细但并不柔软，整条线都有如铁丝耳挂般容易被弯折定型，至于什么平衡插头甚至可换插头更不要想。这条在64Audio官网也仅售39.99美元的线实在是衬不上Nio的身价——当然，Tia Fourte Noir的原线卖相也没有比它好很多就是了。</p><p class=\"jsmargin\"><img alt=\"DSC_5168.jpg\" src=\"http://pic.zaeke.com/img161424175912938.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">也因为Nio的原线为3.5mm立体声插头，本文对Nio的音质评价基于SP2000和钛菊的3.5mm单端接口。耳塞套并非原装，而是Symbio W即“小橘套”——随样机一起来的耳塞套就只要这个，并且Symbio耳塞套同样由64Audio的国代销售，国代也组织过Nio用户送Symbio耳塞套的活动，姑且当做国代指定官配也没什么问题。</p><p class=\"p_style\">Apex模块选择mX，其它几种模块虽然能提供比mX更优越的隔音效果，但同时也会将其低频量增加到较为夸张的程度以至于严重削弱Nio的通透感，<b>所以我并不推荐你们为Nio搭配其它Apex模块，这与我在Illumination评测中建议你们摘除滤网基于同样的原因：对通透感的牺牲得不偿失。</b></p><p class=\"p_style\">搭配mX模块和Symbio W的Nio音色是比较中正平衡的，无论看频响还是讲听感都是足够 “HiFi”、追求高保真的类型，能量分布均匀平滑，没有哪一部分频段的能量明显突出或凹陷。不过比起接近绝对中立的RS10，Nio还是有些许风格倾向的：一方面Nio的人声不像RS10那样素，厚度稍高结象位置稍近，听感会更加抓耳，甜美细腻的女声表现是Nio的一大亮点；另一方面，比起我的私模RS10，Nio的低频还要更加克制与收敛，量感属于中性稍显不足的程度，弹性虽然不错但弹回来得略早，即下潜不是很能令人满意。</p><p class=\"jsmargin\"><img alt=\"DSC_5171.jpg\" src=\"http://pic.zaeke.com/img161424177870405.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">把比较对象换做同价位另一款热门圈铁Solaris 2020，Nio的特点会更加凸显，毕竟Solaris 2020恰恰是以澎湃且高质量的低频见长的产品，相形之下Nio的低频力量感差距就比较悬殊了。但在中高频表现力方面Nio又能反过来扳回些优势，更加纤细清晰的线条感令它的女声和高频乐器质感都比Solaris 2020抢眼且细致，虽然不至于像另一副鲍鱼壳耳机Solaris SE那样令我印象深刻，但至少它的女声表现优于RS10，女声和高频器乐表现力则都要超过Solaris 2020。</p><p class=\"p_style\">所以Nio一定要靠献祭低频质感来换取中高频的质感优势么？</p><p class=\"p_style\">其实这个问题部分要归因到Symbio W上，它虽然具有海绵夹层但整体仍然是偏硬且码数偏大的，这就令其舒服地佩戴严密不那么容易，低频自然会有损失。<b>更换过几种耳塞套之后，我认为另一种硅胶夹海绵构造的耳塞套EarrBOND EBT是最适合Nio的搭配，</b>它的硅胶与海绵部分质地要比Symbio W柔软很多，低频能量感与下潜都有明显改善，并且不会影响通透度——不过EarrBOND EBT也有个问题，就是S码与M码的大小差距过于明显，可能会出现S码嫌小M码又嫌大的情况。</p><p class=\"jsmargin\"><img alt=\"DSC_5178.jpg\" src=\"http://pic.zaeke.com/img161424180424798.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">总之我建议Nio用户们尝试下更换耳塞套来平衡它的低频质感，除了弥补低频短板外也能找回来一些动态。至于Apex模块我还要再强调一次，不建议选择mX以外的模块，其它模块对低频的补偿太过火了。</p><p class=\"p_style\">以搭配mX模块和EarrBOND EBT的表现为准的话，Nio的音色可以说是没有明显缺憾的，硬素质也处在10000-15000元价位中比较出彩的水平上。对比RS10，同样走“HiFi”路线的Nio论细腻度并不落下风，较低的驱动力需求还使得它更容易达成良好的动态与密度；相比动态和声场拔群的Solaris 2020，Nio会显得拘谨不少，但细腻感更胜一筹。</p><p class=\"p_style\">足够中正全面的音色和不错的硬素质保障令Nio在大多数类型的音乐中都具备不错的表现力，人声也好高低频乐器也罢，Nio的还原力都是能够令人满意的。唯一我觉得差点意思的是Nio的声场不算出彩，虽说远不至于给人促狭混乱的印象，可也没法跟开阔兼具强烈立体感的Solaris 2020和A8000相比，在万元档产品中居中游。所以如果你钟情交响乐等强调场面感的曲目，Nio能给出尚算不错的表现力但也不拔尖，其它类型音乐爱好者可以放心选择。</p><p class=\"jsmargin\"><img alt=\"DSC_5176.jpg\" src=\"http://pic.zaeke.com/img161424181866259.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">至于前端搭配，音色并不奇难险怪驱动力需求也不高的Nio自然不挑食，SP2000和钛菊搭配下来都能令我满意。二选一的话我更建议SP2000，钛菊的驱动力优势在Nio身上体现不出多少，同时音色会比SP2000严肃一些，SP2000搭配Nio表现出的人声和高频会比钛菊更鲜活动听一点。如果你不想随身带着动辄三四百克的播放器，那么一条优质的小尾巴如PAW S1或蓝蜻蜓都可以将Nio驱动至较为完整的音色状态。</p><p class=\"p_style\">总评Nio，这是64Audio迄今推出过的所有耳塞产品中我个人最满意的一款。虽然Nio的硬素质在64Audio产品中占不上第一流，但追究起音色的自然度，Nio就是最好的64Audio耳机，没有之一。再加上相当不错的卖相，在我看来Nio是64Audio耳机中最有旗舰风范的产品，以市场对该品牌的惯性认知，即便定价再高一些也不妨碍玩家们买单。</p><p class=\"jsmargin\"><img alt=\"DSC_5170.jpg\" src=\"http://pic.zaeke.com/img161424183078604.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">跳出品牌着眼整个市场，2020年起出现的成功产品大都也是类似Nio的路线，强调平衡稳重自然的声音风格，鲜有特别个性的产品。你可以把“Nio”这个名字看做64Audio的未来愿景，也可以当做高端HiFi耳机的“大趋势”，无论如何，今后的高端产品平均水准都相当可期。</p><p class=\"p_style\">所以，你的钱包准备好了吗？</p>', 'http://pic.zaeke.com/img161456903359743.jpeg', 1050, 0, 0, NULL, '2021-02-25 00:31:28', '2021-03-27 08:09:27');
INSERT INTO `article` VALUES (16108, 'Redmi K40 系列“三驾马车”齐驱，力求策马扬鞭再奋蹄', '刚享受完春节的悠闲时光，年后就迎来了新机的扎堆发布，手机厂商们仿佛都把目光瞅准了消费者手中的压岁钱。众所周知，小米赶在2021年来临前就迫不及待地发布了新一代旗...', '[object Object]', '小啤', 170, 'http://pic.zaeke.com/user_1533281478_8.png', '<p>刚享受完春节的悠闲时光，年后就迎来了新机的扎堆发布，手机厂商们仿佛都把目光瞅准了消费者手中的压岁钱。众所周知，小米赶在 2021 年来临前就迫不及待地发布了新一代旗舰机型小米 11， 紧随其后的 Redmi 也在 2 月 25 日带来了他们的 2021 年度旗舰系列机型 —— Redmi K40 系列。</p><p>Redmi K40 关键参数信息：</p><ul><li>6.67 英寸三星 E4 AMOLED 全高清屏幕、120Hz 刷新率</li><li>骁龙 870 移动平台</li><li>LPDDR5 5500Mbps + UFS 3.1 存储规格组合</li><li>4800 万像素主摄 + 800 万像素超广角 + 500 万像素长焦微距</li><li>2000 万像素前置摄像头</li><li>侧边指纹</li><li>4520mAh 电池容量</li><li>33W 有线充电</li><li>X 轴线性马达</li></ul><p>Redmi K40 Pro 关键参数信息：</p><ul><li>骁龙 888 移动平台</li><li>Wi-Fi 6 增强版</li><li>LPDDR5 6400Mbps + UFS 3.1 存储规格组合</li><li>6400 万像素主摄 + 800 万像素超广角 + 500 万像素长焦微距</li></ul><p>Redmi K40 Pro+ 关键参数信息：</p><ul><li>骁龙 888 移动平台</li><li>Wi-Fi 6 增强版</li><li>LPDDR5 6400Mbps + UFS 3.1 存储规格组合</li><li>1.08 亿像素主摄 + 800 万像素超广角 + 500 万像素长焦微距</li></ul><p>本次的 Redmi K40 系列共推出了三款机型，分别为 Redmi K40、Redmi K40 Pro 、Redmi K40 Pro+，三款机型虽然均有着不俗的综合性能表现，可是凭借不同的定价策略，使其产品线可满足更多价位段消费群体的购买需求。</p><p><img alt=\"未标题-5.jpg\" src=\"http://pic.zaeke.com/img161423887837787.jpeg\" width=\"1400\" height=\"788\"><br></p><p>首先，在核心元器件的搭配方面，Redmi K40 搭载了骁龙 870 移动平台，这是一款性能介于骁龙 865 和骁龙 888 之间的一款中高端级 SoC，虽然骁龙 870 与高通前代旗舰芯片骁龙 865 同出一源，可是骁龙 870 在同样为 7nm 工艺制程的基础之下，其主频从骁龙 865 的 2.84GHz 提升至 3.2GHz，峰值性能略胜一筹。</p><p><img alt=\"591A8111.jpg\" src=\"http://pic.zaeke.com/img161423893767592.jpeg\" width=\"1400\" height=\"788\"><br></p><p>与 Redmi K40 不同，Redmi K40 Pro 和 Redmi K40 Pro+ 则搭载了性能更强劲的最新一代高通旗舰 SoC —— 骁龙 888 移动平台。高通骁龙 888 移动平台的账面数据是非常“诱人”，首先 CPU 部分采用了 1 超 3 大 4 小的核心组合，1 颗基于 Cortex-X1 深度定制的 Kryo680 超级大核，主频高达 2.84GHz，3 颗基于 Cortex-A78 深度定制的 Kryo680 大核，主频 2.42GHz，以及 4 颗基于 Cortex-A55 的 Kryo680 小核；GPU 方面，其配备了全新的 Adreno660 图形处理器，据说该 GPU 相比较于上代骁龙 865 所采用的 Adreno650 GPU ，前者有着 35% 的性能提升，可是功耗却降低 20%。</p><p><img alt=\"591A8091.jpg\" src=\"http://pic.zaeke.com/img161423895988518.jpeg\" width=\"1400\" height=\"787\"><br></p><p>得益于骁龙 888 移动平台具备 5G 双卡双待、5G 双载波聚合等一系列先进特性，因此 Redmi K40 Pro 和 Redmi K40 Pro+ 通过集成的骁龙 X60 5G 调制解调器及射频系统，可同时聚合两个 100MHz 载波，实现 200MHz 超大通路，令 5G 网络下的速率翻倍。</p><p>Wi-Fi 规格方面，上述两款机型均支持 Wi-Fi 6 增强版，而 Redmi K40 为 Wi-Fi 6。三款机型的存储组合规格虽然均为 LPDDR 5 和 UFS 3.1 存储规格组合，可是 Redmi K40 的内存规格与其它两款机型有着 900Mbps 的性能差距，分别为 6400Mbps 和 5500Mbps。</p><p><img alt=\"IMG_0672.jpg\" src=\"http://pic.zaeke.com/img161423899025881.jpeg\" width=\"1400\" height=\"933\"><br></p><p>除了 SoC 以外，三款机型搭载的主摄也各有差异，Redmi K40 主摄传感器为索尼 IMX582，而 Redmi K40 Pro 和 Redmi K40 Pro+ 的主摄传感器分别为索尼 IMX686 和三星 HM2 传感器。显而易见，主摄的性能表现，成为了这三款机型在定位方面的分水岭，理论上售价越高的机型，主摄的综合表现越好。</p><p>拍照方面，三款机型均采用了后置三摄组合，另外两颗副摄像头分别为 800 万像素超广角和 500 万像素长焦微距，凭借去年 Redmi K30&nbsp;Pro 那颗长焦微距打下的不俗口碑，今年 Redmi K40 全系都果断配备了一颗等效焦距 50mm 的长焦微距摄像头，相比较于超近距离的拍摄特性，长焦微距摄像头在拍摄过程中可以减少与小昆虫的近距离接触，从而达到提高成片率的效果。</p><p><img alt=\"IMG_0700.jpg\" src=\"http://pic.zaeke.com/img161423905457148.jpeg\" width=\"1400\" height=\"933\"><br></p><p>相比较于上一代产品，这次 Redmi K40 系列也朝着轻量化方向迈进，三款机型的机身重量均为 196 克，机身厚度为 7.8mm，电池容量自然都是 4520mAh，并且辅以最高 33W 的有线充电。按照官方描述，三款机型在无电量状态下充至满电，需耗时 52 分钟。</p><p><img alt=\"IMG_0680.jpg\" src=\"http://pic.zaeke.com/img161423903681910.jpeg\" width=\"1400\" height=\"933\"><br></p><p>外观设计方面，Redmi K40 系列强调视觉上平衡有序，整体设计遵从对称美学。值得一提的是，该机型的前置主摄开孔直径非常小（2.76mm），一定程度上降低了开孔对于屏幕完整性的视觉观感。机身背面的后置三摄突起幅度明显，边缘容易堆积灰尘，而后盖则搭配了 AG 工艺玻璃。尽管并没有采用金属中框，虽然视觉上并没有给人一种廉价感，可触感上却存在着材质差异。</p><p><img alt=\"591A8097.jpg\" src=\"http://pic.zaeke.com/img161423906578025.jpeg\" width=\"1400\" height=\"787\"><br></p><p>Redmi K40 系列搭载了一块 6.67 英寸直屏，该屏幕的峰值亮度为 1300nit、对比度 5,000,000:1，通过自研屏幕色彩校准算法，每一片屏幕出厂前都经过精确校准，确保每款机型的屏幕可以达到 JNCD≈0.36、△E≈0.35 色准表现。配合 TrueTone 真彩显示技术加持，可以让手机本身根据不同环境光线对屏幕色温进行补偿，让显示色温更加符合环境色温。</p><p><img alt=\"IMG_0682.jpg\" src=\"http://pic.zaeke.com/img161423913773432.jpeg\" width=\"1400\" height=\"933\"><br></p><p>另外，这块屏幕具备 120Hz 刷新率与 360Hz 触控采样率，首发超分辨率触控，将屏幕的触控分区由原来的 2400 × 1080 提升 8 倍至 19200 × 8640，以达到进一步提高触控精准度目的。通过前后光线传感器特性，Redmi K40 系列可实现 360° 前后双光感，大大提高环境光的识别准确度，再配合 8192 级亮度调节，让自动亮度调节更加细腻平滑。</p><p><img alt=\"IMG_0666.jpg\" src=\"http://pic.zaeke.com/img161423992819337.jpeg\" width=\"1400\" height=\"933\"><br></p><p>音频方面，Redmi K40 系列具备杜比全景声立体双扬声器，通过除底部的超线性扬声器和听筒的二合一扬声器，以实现立体声效果。不仅如此，不同于常规的屏幕指纹机型，Redmi K40 系列采用了侧面指纹方案，解锁速度非常迅速，感觉比市面上绝大部分屏下解锁的解锁速度都要出色，而且解锁成功率较高。除此，Redmi K40 系列还兼顾了 X 轴线性马达、全功能 NFC 以及红外遥控，可谓是一应俱全了。</p><p><img alt=\"591A8089.jpg\" src=\"http://pic.zaeke.com/img161423909082213.jpeg\" width=\"1400\" height=\"788\"><br></p><p>Redmi K40 Pro 系列两款机型提供墨羽、晴雪、幻境三款配色，而 Redmi K40 则提供亮黑、晴雪、幻境三款配色。售价方面，Redmi K40 6GB + 128GB 售价为 1999 元、8GB + 128GB 售价为 2199 元、8GB + 256GB 售价 2499 元、12GB + 256GB 售价 2 699 元（特价限量期间为 2499 元）；Redmi K40 Pro 6GB + 128GB 售价为 2799 元、8GB + 128GB 售价为 2999 元、8GB + 256GB 售价为 3299 元；Redmi K40 Pro+ 128GB + 256GB 售价为 3699 元。</p>', '<p class=\"p_style\">刚享受完春节的悠闲时光，年后就迎来了新机的扎堆发布，手机厂商们仿佛都把目光瞅准了消费者手中的压岁钱。众所周知，小米赶在 2021 年来临前就迫不及待地发布了新一代旗舰机型小米 11， 紧随其后的 Redmi 也在 2 月 25 日带来了他们的 2021 年度旗舰系列机型 —— Redmi K40 系列。</p><p class=\"p_style\">Redmi K40 关键参数信息：</p><ul><li>6.67 英寸三星 E4 AMOLED 全高清屏幕、120Hz 刷新率</li><li>骁龙 870 移动平台</li><li>LPDDR5 5500Mbps + UFS 3.1 存储规格组合</li><li>4800 万像素主摄 + 800 万像素超广角 + 500 万像素长焦微距</li><li>2000 万像素前置摄像头</li><li>侧边指纹</li><li>4520mAh 电池容量</li><li>33W 有线充电</li><li>X 轴线性马达</li></ul><p class=\"p_style\">Redmi K40 Pro 关键参数信息：</p><ul><li>骁龙 888 移动平台</li><li>Wi-Fi 6 增强版</li><li>LPDDR5 6400Mbps + UFS 3.1 存储规格组合</li><li>6400 万像素主摄 + 800 万像素超广角 + 500 万像素长焦微距</li></ul><p class=\"p_style\">Redmi K40 Pro+ 关键参数信息：</p><ul><li>骁龙 888 移动平台</li><li>Wi-Fi 6 增强版</li><li>LPDDR5 6400Mbps + UFS 3.1 存储规格组合</li><li>1.08 亿像素主摄 + 800 万像素超广角 + 500 万像素长焦微距</li></ul><p class=\"p_style\">本次的 Redmi K40 系列共推出了三款机型，分别为 Redmi K40、Redmi K40 Pro 、Redmi K40 Pro+，三款机型虽然均有着不俗的综合性能表现，可是凭借不同的定价策略，使其产品线可满足更多价位段消费群体的购买需求。</p><p class=\"jsmargin\"><img alt=\"未标题-5.jpg\" src=\"http://pic.zaeke.com/img161423887837787.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">首先，在核心元器件的搭配方面，Redmi K40 搭载了骁龙 870 移动平台，这是一款性能介于骁龙 865 和骁龙 888 之间的一款中高端级 SoC，虽然骁龙 870 与高通前代旗舰芯片骁龙 865 同出一源，可是骁龙 870 在同样为 7nm 工艺制程的基础之下，其主频从骁龙 865 的 2.84GHz 提升至 3.2GHz，峰值性能略胜一筹。</p><p class=\"jsmargin\"><img alt=\"591A8111.jpg\" src=\"http://pic.zaeke.com/img161423893767592.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">与 Redmi K40 不同，Redmi K40 Pro 和 Redmi K40 Pro+ 则搭载了性能更强劲的最新一代高通旗舰 SoC —— 骁龙 888 移动平台。高通骁龙 888 移动平台的账面数据是非常“诱人”，首先 CPU 部分采用了 1 超 3 大 4 小的核心组合，1 颗基于 Cortex-X1 深度定制的 Kryo680 超级大核，主频高达 2.84GHz，3 颗基于 Cortex-A78 深度定制的 Kryo680 大核，主频 2.42GHz，以及 4 颗基于 Cortex-A55 的 Kryo680 小核；GPU 方面，其配备了全新的 Adreno660 图形处理器，据说该 GPU 相比较于上代骁龙 865 所采用的 Adreno650 GPU ，前者有着 35% 的性能提升，可是功耗却降低 20%。</p><p class=\"jsmargin\"><img alt=\"591A8091.jpg\" src=\"http://pic.zaeke.com/img161423895988518.jpeg\" width=\"1400\" height=\"787\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">得益于骁龙 888 移动平台具备 5G 双卡双待、5G 双载波聚合等一系列先进特性，因此 Redmi K40 Pro 和 Redmi K40 Pro+ 通过集成的骁龙 X60 5G 调制解调器及射频系统，可同时聚合两个 100MHz 载波，实现 200MHz 超大通路，令 5G 网络下的速率翻倍。</p><p class=\"p_style\">Wi-Fi 规格方面，上述两款机型均支持 Wi-Fi 6 增强版，而 Redmi K40 为 Wi-Fi 6。三款机型的存储组合规格虽然均为 LPDDR 5 和 UFS 3.1 存储规格组合，可是 Redmi K40 的内存规格与其它两款机型有着 900Mbps 的性能差距，分别为 6400Mbps 和 5500Mbps。</p><p class=\"jsmargin\"><img alt=\"IMG_0672.jpg\" src=\"http://pic.zaeke.com/img161423899025881.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">除了 SoC 以外，三款机型搭载的主摄也各有差异，Redmi K40 主摄传感器为索尼 IMX582，而 Redmi K40 Pro 和 Redmi K40 Pro+ 的主摄传感器分别为索尼 IMX686 和三星 HM2 传感器。显而易见，主摄的性能表现，成为了这三款机型在定位方面的分水岭，理论上售价越高的机型，主摄的综合表现越好。</p><p class=\"p_style\">拍照方面，三款机型均采用了后置三摄组合，另外两颗副摄像头分别为 800 万像素超广角和 500 万像素长焦微距，凭借去年 Redmi K30&nbsp;Pro 那颗长焦微距打下的不俗口碑，今年 Redmi K40 全系都果断配备了一颗等效焦距 50mm 的长焦微距摄像头，相比较于超近距离的拍摄特性，长焦微距摄像头在拍摄过程中可以减少与小昆虫的近距离接触，从而达到提高成片率的效果。</p><p class=\"jsmargin\"><img alt=\"IMG_0700.jpg\" src=\"http://pic.zaeke.com/img161423905457148.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">相比较于上一代产品，这次 Redmi K40 系列也朝着轻量化方向迈进，三款机型的机身重量均为 196 克，机身厚度为 7.8mm，电池容量自然都是 4520mAh，并且辅以最高 33W 的有线充电。按照官方描述，三款机型在无电量状态下充至满电，需耗时 52 分钟。</p><p class=\"jsmargin\"><img alt=\"IMG_0680.jpg\" src=\"http://pic.zaeke.com/img161423903681910.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">外观设计方面，Redmi K40 系列强调视觉上平衡有序，整体设计遵从对称美学。值得一提的是，该机型的前置主摄开孔直径非常小（2.76mm），一定程度上降低了开孔对于屏幕完整性的视觉观感。机身背面的后置三摄突起幅度明显，边缘容易堆积灰尘，而后盖则搭配了 AG 工艺玻璃。尽管并没有采用金属中框，虽然视觉上并没有给人一种廉价感，可触感上却存在着材质差异。</p><p class=\"jsmargin\"><img alt=\"591A8097.jpg\" src=\"http://pic.zaeke.com/img161423906578025.jpeg\" width=\"1400\" height=\"787\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">Redmi K40 系列搭载了一块 6.67 英寸直屏，该屏幕的峰值亮度为 1300nit、对比度 5,000,000:1，通过自研屏幕色彩校准算法，每一片屏幕出厂前都经过精确校准，确保每款机型的屏幕可以达到 JNCD≈0.36、△E≈0.35 色准表现。配合 TrueTone 真彩显示技术加持，可以让手机本身根据不同环境光线对屏幕色温进行补偿，让显示色温更加符合环境色温。</p><p class=\"jsmargin\"><img alt=\"IMG_0682.jpg\" src=\"http://pic.zaeke.com/img161423913773432.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">另外，这块屏幕具备 120Hz 刷新率与 360Hz 触控采样率，首发超分辨率触控，将屏幕的触控分区由原来的 2400 × 1080 提升 8 倍至 19200 × 8640，以达到进一步提高触控精准度目的。通过前后光线传感器特性，Redmi K40 系列可实现 360° 前后双光感，大大提高环境光的识别准确度，再配合 8192 级亮度调节，让自动亮度调节更加细腻平滑。</p><p class=\"jsmargin\"><img alt=\"IMG_0666.jpg\" src=\"http://pic.zaeke.com/img161423992819337.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">音频方面，Redmi K40 系列具备杜比全景声立体双扬声器，通过除底部的超线性扬声器和听筒的二合一扬声器，以实现立体声效果。不仅如此，不同于常规的屏幕指纹机型，Redmi K40 系列采用了侧面指纹方案，解锁速度非常迅速，感觉比市面上绝大部分屏下解锁的解锁速度都要出色，而且解锁成功率较高。除此，Redmi K40 系列还兼顾了 X 轴线性马达、全功能 NFC 以及红外遥控，可谓是一应俱全了。</p><p class=\"jsmargin\"><img alt=\"591A8089.jpg\" src=\"http://pic.zaeke.com/img161423909082213.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">Redmi K40 Pro 系列两款机型提供墨羽、晴雪、幻境三款配色，而 Redmi K40 则提供亮黑、晴雪、幻境三款配色。售价方面，Redmi K40 6GB + 128GB 售价为 1999 元、8GB + 128GB 售价为 2199 元、8GB + 256GB 售价 2499 元、12GB + 256GB 售价 2 699 元（特价限量期间为 2499 元）；Redmi K40 Pro 6GB + 128GB 售价为 2799 元、8GB + 128GB 售价为 2999 元、8GB + 256GB 售价为 3299 元；Redmi K40 Pro+ 128GB + 256GB 售价为 3699 元。</p>', 'http://pic.zaeke.com/img161430368848000.jpeg', 7428, 0, 0, NULL, '2021-02-25 17:41:58', '2021-03-27 07:03:32');
INSERT INTO `article` VALUES (16114, 'motorola edge s的目标，是将摩托罗拉带回大众视野之中', '我们终于完成了motorolaedges的评测，这是摩托罗拉在2021年的重点机型，也是摩托罗拉想要在国内重返辉煌的一个重要里程碑。因为其放下了曾经“只做高端”...', '[object Object]', 'Kururu', 33, 'http://pic.zaeke.com/user_1533281476_2.png', '<p>我们终于完成了motorola edge s的评测，这是摩托罗拉在2021年的重点机型，也是摩托罗拉想要在国内重返辉煌的一个重要里程碑。因为其放下了曾经“只做高端”的姿态将一切过往化为序章，并视图通过性价比这一新品牌手中最好用的“敲门砖”，重新让“Hello moto”的铃声出现在大街小巷之中。  </p><p><img src=\"http://pic.zaeke.com/img161415694914434.jpeg\" alt=\"591A8014.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161415697383334.jpeg\" alt=\"591A8017.jpg\">  </p><p>先来回顾一下motorola edge s的各项配置：  </p><p><b>&nbsp;· </b>高通骁龙870移动平台</p><p><b>&nbsp;· </b>6/8GB&nbsp;LPDDR5内存 + 128/256GB UFS 3.1存储</p><p><b>&nbsp;· </b>6.7英寸21:9比例2520×1080分辨率90Hz刷新率LCD双孔屏</p><p><b>&nbsp;· </b>后置6400万像素主摄 + 1600万像素广角 + 200万像素景深+ToF  </p><p><b>&nbsp;· </b>前置1600万像素主摄 + 800万像素广角</p><p><b>&nbsp;· </b>5000毫安时电池、20W有线充电功率、双扬声器、侧面指纹识别、NFC</p><p>在上述硬件之中，最引人注目的莫过于其全球首发的高通骁龙870移动平台。这是款基于骁龙865 Plus升级而来的产品，很多规格都与骁龙865和骁龙865 Plus一致。主要升级项为CPU超级大核的工作主频被提高至3.2GHz，以及新加入的4K 60Hz设备内屏显示输出能力。</p><p><img src=\"http://pic.zaeke.com/img161415743924800.jpeg\" alt=\"591A8117.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161415746088372.jpeg\" alt=\"591A8114.jpg\">  </p><p>就定位而言，骁龙870移动平台是高通以完善自身产品线为目的推出的产品。不仅不会撼动骁龙888的旗舰级定位，甚至还可以协助智能手机制造商们推出更多像同时具备“性价比”和“旗舰级性能”这两项特征的5G手机。</p><p>其中“性价比”的话，1999元的起售价可以诠释一切。而“旗舰级性能”的话，实测数据显示motorola edge s的骁龙870安兔兔成绩为64.9万分，鲁大师成绩为84.2万分，Geekbench单核成绩为4428分，多核成绩为11307分，与绝大多数骁龙865及骁龙865 Plus机型的成绩非常接近，远远领先于同价位多数机型所采用的天玑1000+、麒麟820和骁龙765G。</p><p><img src=\"http://pic.zaeke.com/img161416084089623.jpeg\" alt=\"未标题-1.jpg\">  </p><p>在运行主流游戏时，骁龙870移动平台的表现也复刻了跑分时的模样，无限接近于骁龙865及骁龙865 Plus机型的表现。由于motorola edge s+目前暂不支持《王者荣耀》超高帧率(90&nbsp;FPS)，所以我们的测试将在高帧率(60&nbsp;FPS)+超高分辨率+抗锯齿设置下进行。结果显示在这局游戏的平均帧率为60.2 FPS，小规模团帧率处于53至57 FPS之间，十人团帧率处于45至49 FPS之间。全场最低帧率就出现在十人团刚爆发各种技能特效集中展现的瞬间，那时的帧率为44.9 FPS。  </p><p>而一局超高清画质+超高帧数(40&nbsp;FPS)+水面反射设置下的《和平精英》海岛地图平均帧率为39.9 FPS，若将设置改成HDR高清+极限帧数(60 FPS)+抗锯齿的话，平均帧率则为53.9 FPS。当然这与人机局交战次数较多有关，毕竟高倍镜的每次射击和搭乘载具快速移动时，都会带来明显的帧数下滑。但整个游戏过程的体验仍然与预期相同，可以用“无需担心尽情享受”来形容。</p><p><img src=\"http://pic.zaeke.com/img161415707027935.jpeg\" alt=\"591A8143.jpg\">  </p><p>至于续航表现，内置5000毫安时电池的motorola edge s在关闭Wi-Fi并开启单卡5G+单卡4G的环境下10小时待机耗电量约为4%。开启Wi-Fi和90Hz刷新率，连接有线耳机且将亮度和音量调至50%的环境下，1小时短视频约耗电7%，1小时在线视频(开启弹幕)约耗电8%，一局10分钟的《王者荣耀》约耗电4%，一局28分钟的《和平精英》约耗电10%。</p><p>如果是类似于《天龙八部手游》这种需要长时间在线挂机但无需操作的游戏，那么我们在将屏幕亮度调至最低并静音运行1小时的耗电量约为8%。</p><p><img src=\"http://pic.zaeke.com/img161415705956308.jpeg\" alt=\"591A8141.jpg\">  </p><p>充电方面，motorola edge s那兼容PD协议的20W有线快充显然与其5000毫安时的电池容量有些不搭。我们在将电量耗尽之后，从0%开始充电3分钟之后才能开机，开机后手机显示电量为3%。充至第10分钟时电量为9%，第20分钟时为20%，第40分钟时为40%，第60分钟时为61%。</p><p>最终，它在第90分钟电量达到90%，并在第118分钟充满，也就是0%到100%充电时间为1小时58分钟。除电量达到90%之后的涓流充电时间之外，motorola edge s几乎全程都按每分钟约1%的速率在获取电量。  </p><p>虽然这个速度与同价位目前动辄33W、40W或65W快充的表现相距甚远，但相信在大容量电池和领先的性能优势面前，多数同学都会理解并主动选择将这点不搭连带215克的重量、9.69毫米的整机厚度、不太合眼缘的双孔屏和略显厚实的下边框一同选择性忽视的，对吧？</p><p><img src=\"http://pic.zaeke.com/img161416154521961.jpeg\" alt=\"591A8124.jpg\">  </p><p>硬件之后就是大家关心的软件，如果说motorola edge s最吸引人的是“1999元就能买到骁龙870”，那最令人担心的一定是这套在安卓11基础上增加功能并进行了一定本地化的MYUI操作界面。这也确实，在联想智能手机业务那么多年的历史长河上，唯一颇具好评的操作界面貌似只有当时ZUK品牌的ZUI，可那已经是2015至2016年期间的事情了。</p><p>好好好，我们知道摩托罗拉近几年在海外某些市场的销售表现不错也颇受欢迎。但单就motorola edge s面向的中国大陆市场而言，貌似没有哪家智能手机制造商可以在凭借一个基于原生安卓轻度定制的系统，做出满足<span style=\"font-size: 16px;\">摩托罗拉和</span>联想手机业务野心的成绩。<strike>这点可以看看目前支持一加用ColorOS取代氢OS的呼声有多大。</strike></p><p><img src=\"http://pic.zaeke.com/img161423812337825.jpeg\" alt=\"591A8118.jpg\">  </p><p>在我们看来，MYUI目前存在许多可能会令新用户或潜在用户感到困惑的问题，但导致这些问题的原因不是安卓11不稳定或硬件兼容性，而是本地化程度太低。  </p><p>就以大家最常用的截屏为例来说，目前国内几乎所有智能手机在“音量下+电源键”这套截屏组合键时基本都是按下后立即触发，但MYUI选择按照了原生安卓的设计，需要按住这两个键一秒钟才能截屏。并且系统预置的指导应用中对此没无说明，设置中也没有对于这套组合键的相关选项可供参考。  </p><p>虽然这导致我在从仓库中翻出那部本地化程度同样很低的索尼Xperia XZ3验证之前，直接就认为motorola edge s不支持“音量下+电源键”组合键这种截屏方式。不过幸运的是，我们凭借经验丰富的同事和数量尚可的评测机在审稿阶段避免了一次评测事故。但显然不是每位消费都恰好认识一位资深数码编辑或拥有可供验证的手机，所以MYUI的确得用心写好系统预置的“moto”指导应用，让大家知道它跟目前主流的其它系统有哪些不同，而不是像现在这样简单的“汉化”一下。</p><p><img src=\"http://pic.zaeke.com/img161423759920450.jpeg\" alt=\"591A8116.jpg\">  </p><p>另外，我们在研究截屏问题时还顺便发现了一个奇怪的问题，那就是搜索功能不支持模糊词搜索。以下图为例，当我们想切换刷新率但找不到在哪切换准备通过搜索找时，发现只搜索“刷新率”这三个字是得不到检索结果的。为什么呢？因为这个设置的名称是“显示刷新率”，所以我们必须输入<span style=\"font-size: 16px;\">设置</span>名称开头的“显示”或完整输入“显示刷新率”，系统才能成功检索。</p><p>然而，应该没有人能在要调整刷新率的时候会想到自己得搜“显示”这两个字吧。</p><p><img src=\"http://pic.zaeke.com/img161416139130447.jpeg\" alt=\"未标题-1.jpg\">  </p><p>语音助手方面，MYUI的“乐语音”可以说算是个还不错的功能。因为它拾音准确率不错，大多时候反馈都很快，无论是修改系统设置，打开第三方APP还是进行网页搜索的速度都很不错。甚至可以快捷的通过语音完成设置导航地点、找附近的停车场、打开听歌识曲、播放音乐/电影/电视剧、控制微信/QQ发红包、打开扫一扫、发消息和视频通话等操作。</p><p>并且在motorola edge s机身左侧拥有实体快捷键的前提下，“乐语音”可以直接通过与快捷键关联的“一键触达”功能唤醒。当然，这个功能不仅可以唤醒语音助手，还可以打开健康码和付款码这类小程序，以及相机和便签这类系统预置应用，外加微博和王者荣耀这类自行下载的应用。</p><p><img src=\"http://pic.zaeke.com/img161415725385198.jpeg\" alt=\"591A8151.jpg\">  </p><p>此外，MYUI还拥有包含手势操作、moto AI丽音算法、游戏模式、PPT/商务文件拍摄、自定义APP音量和多屏协同在内的诸多特征。  </p><p>其中较为实用或颇具趣味的是最后两个，自定义APP音量和多屏协同。前者就像电脑上的音量合成器一样，可以自定义每个APP的音量大小，让自己在游戏时获得音量合适的背景音乐。后者则与EMUI的多屏协同或One UI的Samsung DeX一样，属于可以通过显示器及外设增强手机多任务处理能力的功能。</p><p><img src=\"http://pic.zaeke.com/img161415752135300.jpeg\" alt=\"591A8140.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161416095080361.png\" alt=\"Screenshot_20210221-233353.png\">  </p><p>如果说EMUI、MIUI、ColorOS和OriginOS等所选择的是将更多功能整合进系统中的道路，那MYUI则更像是氢OS那般轻巧简约的道路，并且比氢OS还要更接近于原生的安卓系统，大概就是索尼Xperia手机系统的样子。</p><p>在整个评测产出的周期中，MYUI的表现都属于流畅且稳定，我们只遇到过一次自带的应用商店“假死”以及一次语音助手回馈慢(当然这个可能跟我司当时网络有关)的情况，至于应用崩溃、闪退或是系统不稳定卡死的情况则从来没有出现过。所以MYUI对于所有有意购入motorola edge s的人而言，需要关心的点就在于“好不好用”，而非“能不能用”。</p><p>再简单点说，就是“愿不愿意适应MYUI和现在手机UI的差异”和“对于现在手机UI有但MYUI没有的功能是否有依赖”这两个问题了。</p><p><img src=\"http://pic.zaeke.com/img161415733854927.jpeg\" alt=\"591A8145.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161415732127902.jpeg\" alt=\"591A8148.jpg\">  </p><p>按照惯例，影像其实不应该放这么后面。但或许由于motorola edge s在极低的预算帽之下将其中的大多数资源都投入到了性能相关之处，所以导致其拍照其实跟20W快充一样属于整机略显尴尬的部分，成片质量也只是同价位手机里的常规水平。那常规水平是什么样？自然就是光线充足可以一战，但会有涂抹感和边角画质下降的问题。而光线不充足时，只能满足基本的记录需求。  </p><p>如果你很喜欢拍蓝天白云的话，那么motorola edge s显然与你缘分未到，因为它拍出的照片蓝色饱和度明显偏低。就以我们这组样张为例，明明是大晴天顶着烈日出去拍的，但成片却怎么看都只能让人感觉那天应该是阴天。啥？你问我们蓝色饱和度为什么会偏低？我们也不知道。<strike>也许是产线颜料不够，所以工程师把蓝色都拿去喷在后盖上了吧。</strike>  </p><p><img src=\"http://pic.zaeke.com/img161416021051182.jpeg\" alt=\"IMG_20210223_144325907.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161416020183492.jpeg\" alt=\"IMG_20210223_144056692.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161416018594100.jpeg\" alt=\"IMG_20210223_143835143.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161416017813517.jpeg\" alt=\"IMG_20210223_142625776_SRRM.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161416012743787.jpeg\" alt=\"IMG_20210223_140528928_HDR.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161416014190817.jpeg\" alt=\"IMG_20210223_141128761.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161432943699229.jpeg\" alt=\"IMG_20210225_194303705.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161432945537850.jpeg\" alt=\"IMG_20210225_193204255.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161432949414509.jpeg\" alt=\"IMG_20210225_194229107.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161432950547220.jpeg\" alt=\"IMG_20210225_191654316.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161432952721198.jpeg\" alt=\"IMG_20210225_195455849.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161432954137496.jpeg\" alt=\"IMG_20210225_200033729_HDR.jpg\">  </p><p>虽然成像并不理想，但由于骁龙870移动平台能带来旗舰级性能的缘故，所以motorola edge s无论相机启动速度，对焦速度，取景器画面都非常流畅，相册里也不会出现“图片先模糊后清晰”的加载过程，可以说是同价位拍照流畅度最好的机型之一。此外，该机还支持240 FPS的慢动作拍摄以及6K 30FPS或4K60 FPS的视频拍摄，并配备了稀有的环形微距补光灯。  </p><p><img src=\"http://pic.zaeke.com/img161415757436137.jpeg\" alt=\"591A8131.jpg\">  </p><p>motorola edge s显然正披着摩托罗拉的皮，对外展示着联想手机擅长的“价格屠夫”姿态。毫无疑问的说，motorola edge s的硬件在其所属价格段中处于领先梯队之中，但软件却在所属价格段中处于队尾，外加相机的表现实属一般。尽管1999元的起售价开了个好头，但......咦，1999？嗨，一部1999元的手机要求那么高做什么，喜欢的话就“交个朋友”，不喜欢的话只需一句“下次一定”，那就完事了。</p><p>不得不说1999的确具有说服力，当这个数字以价格的形式在mototola edge s发布会上出来的一瞬间，整个B站直播间的画面就被“牛逼”和“666”之类的弹幕所覆盖。这也许就是所有数码爱好者乃至吃瓜群众们都喜闻乐见的东西，毕竟自从步入5G时代之后，两三千元的手机几乎已经成了“温饱线”一般的存在。</p><p>但话说回来，我们希望联想手机业务和摩托罗拉不要被发布会和上市初期的舆论赞扬声冲昏了头脑，毕竟2021年还能屹立于智能手机市场上的品牌没有任何一个是善茬。mototola edge s给联想手机业务或摩托罗拉的2021年开了个好头，他们的“回归”毫无疑问会给这个竞争火热的中国智能手机市场再添一桶燃料。而接下来需要做到的自然就是持之以恒，不要让自己像流星一样短暂发光，然后消失在人们的视野中。</p>', '<p class=\"p_style\">我们终于完成了motorola edge s的评测，这是摩托罗拉在2021年的重点机型，也是摩托罗拉想要在国内重返辉煌的一个重要里程碑。因为其放下了曾经“只做高端”的姿态将一切过往化为序章，并视图通过性价比这一新品牌手中最好用的“敲门砖”，重新让“Hello moto”的铃声出现在大街小巷之中。  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161415694914434.jpeg\" alt=\"591A8014.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161415697383334.jpeg\" alt=\"591A8017.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">先来回顾一下motorola edge s的各项配置：  </p><p class=\"p_style\"><b>&nbsp;· </b>高通骁龙870移动平台</p><p class=\"p_style\"><b>&nbsp;· </b>6/8GB&nbsp;LPDDR5内存 + 128/256GB UFS 3.1存储</p><p class=\"p_style\"><b>&nbsp;· </b>6.7英寸21:9比例2520×1080分辨率90Hz刷新率LCD双孔屏</p><p class=\"p_style\"><b>&nbsp;· </b>后置6400万像素主摄 + 1600万像素广角 + 200万像素景深+ToF  </p><p class=\"p_style\"><b>&nbsp;· </b>前置1600万像素主摄 + 800万像素广角</p><p class=\"p_style\"><b>&nbsp;· </b>5000毫安时电池、20W有线充电功率、双扬声器、侧面指纹识别、NFC</p><p class=\"p_style\">在上述硬件之中，最引人注目的莫过于其全球首发的高通骁龙870移动平台。这是款基于骁龙865 Plus升级而来的产品，很多规格都与骁龙865和骁龙865 Plus一致。主要升级项为CPU超级大核的工作主频被提高至3.2GHz，以及新加入的4K 60Hz设备内屏显示输出能力。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161415743924800.jpeg\" alt=\"591A8117.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161415746088372.jpeg\" alt=\"591A8114.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">就定位而言，骁龙870移动平台是高通以完善自身产品线为目的推出的产品。不仅不会撼动骁龙888的旗舰级定位，甚至还可以协助智能手机制造商们推出更多像同时具备“性价比”和“旗舰级性能”这两项特征的5G手机。</p><p class=\"p_style\">其中“性价比”的话，1999元的起售价可以诠释一切。而“旗舰级性能”的话，实测数据显示motorola edge s的骁龙870安兔兔成绩为64.9万分，鲁大师成绩为84.2万分，Geekbench单核成绩为4428分，多核成绩为11307分，与绝大多数骁龙865及骁龙865 Plus机型的成绩非常接近，远远领先于同价位多数机型所采用的天玑1000+、麒麟820和骁龙765G。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161416084089623.jpeg\" alt=\"未标题-1.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">在运行主流游戏时，骁龙870移动平台的表现也复刻了跑分时的模样，无限接近于骁龙865及骁龙865 Plus机型的表现。由于motorola edge s+目前暂不支持《王者荣耀》超高帧率(90&nbsp;FPS)，所以我们的测试将在高帧率(60&nbsp;FPS)+超高分辨率+抗锯齿设置下进行。结果显示在这局游戏的平均帧率为60.2 FPS，小规模团帧率处于53至57 FPS之间，十人团帧率处于45至49 FPS之间。全场最低帧率就出现在十人团刚爆发各种技能特效集中展现的瞬间，那时的帧率为44.9 FPS。  </p><p class=\"p_style\">而一局超高清画质+超高帧数(40&nbsp;FPS)+水面反射设置下的《和平精英》海岛地图平均帧率为39.9 FPS，若将设置改成HDR高清+极限帧数(60 FPS)+抗锯齿的话，平均帧率则为53.9 FPS。当然这与人机局交战次数较多有关，毕竟高倍镜的每次射击和搭乘载具快速移动时，都会带来明显的帧数下滑。但整个游戏过程的体验仍然与预期相同，可以用“无需担心尽情享受”来形容。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161415707027935.jpeg\" alt=\"591A8143.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">至于续航表现，内置5000毫安时电池的motorola edge s在关闭Wi-Fi并开启单卡5G+单卡4G的环境下10小时待机耗电量约为4%。开启Wi-Fi和90Hz刷新率，连接有线耳机且将亮度和音量调至50%的环境下，1小时短视频约耗电7%，1小时在线视频(开启弹幕)约耗电8%，一局10分钟的《王者荣耀》约耗电4%，一局28分钟的《和平精英》约耗电10%。</p><p class=\"p_style\">如果是类似于《天龙八部手游》这种需要长时间在线挂机但无需操作的游戏，那么我们在将屏幕亮度调至最低并静音运行1小时的耗电量约为8%。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161415705956308.jpeg\" alt=\"591A8141.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">充电方面，motorola edge s那兼容PD协议的20W有线快充显然与其5000毫安时的电池容量有些不搭。我们在将电量耗尽之后，从0%开始充电3分钟之后才能开机，开机后手机显示电量为3%。充至第10分钟时电量为9%，第20分钟时为20%，第40分钟时为40%，第60分钟时为61%。</p><p class=\"p_style\">最终，它在第90分钟电量达到90%，并在第118分钟充满，也就是0%到100%充电时间为1小时58分钟。除电量达到90%之后的涓流充电时间之外，motorola edge s几乎全程都按每分钟约1%的速率在获取电量。  </p><p class=\"p_style\">虽然这个速度与同价位目前动辄33W、40W或65W快充的表现相距甚远，但相信在大容量电池和领先的性能优势面前，多数同学都会理解并主动选择将这点不搭连带215克的重量、9.69毫米的整机厚度、不太合眼缘的双孔屏和略显厚实的下边框一同选择性忽视的，对吧？</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161416154521961.jpeg\" alt=\"591A8124.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">硬件之后就是大家关心的软件，如果说motorola edge s最吸引人的是“1999元就能买到骁龙870”，那最令人担心的一定是这套在安卓11基础上增加功能并进行了一定本地化的MYUI操作界面。这也确实，在联想智能手机业务那么多年的历史长河上，唯一颇具好评的操作界面貌似只有当时ZUK品牌的ZUI，可那已经是2015至2016年期间的事情了。</p><p class=\"p_style\">好好好，我们知道摩托罗拉近几年在海外某些市场的销售表现不错也颇受欢迎。但单就motorola edge s面向的中国大陆市场而言，貌似没有哪家智能手机制造商可以在凭借一个基于原生安卓轻度定制的系统，做出满足<span style=\"font-size: 16px;\">摩托罗拉和</span>联想手机业务野心的成绩。<strike>这点可以看看目前支持一加用ColorOS取代氢OS的呼声有多大。</strike></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161423812337825.jpeg\" alt=\"591A8118.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">在我们看来，MYUI目前存在许多可能会令新用户或潜在用户感到困惑的问题，但导致这些问题的原因不是安卓11不稳定或硬件兼容性，而是本地化程度太低。  </p><p class=\"p_style\">就以大家最常用的截屏为例来说，目前国内几乎所有智能手机在“音量下+电源键”这套截屏组合键时基本都是按下后立即触发，但MYUI选择按照了原生安卓的设计，需要按住这两个键一秒钟才能截屏。并且系统预置的指导应用中对此没无说明，设置中也没有对于这套组合键的相关选项可供参考。  </p><p class=\"p_style\">虽然这导致我在从仓库中翻出那部本地化程度同样很低的索尼Xperia XZ3验证之前，直接就认为motorola edge s不支持“音量下+电源键”组合键这种截屏方式。不过幸运的是，我们凭借经验丰富的同事和数量尚可的评测机在审稿阶段避免了一次评测事故。但显然不是每位消费都恰好认识一位资深数码编辑或拥有可供验证的手机，所以MYUI的确得用心写好系统预置的“moto”指导应用，让大家知道它跟目前主流的其它系统有哪些不同，而不是像现在这样简单的“汉化”一下。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161423759920450.jpeg\" alt=\"591A8116.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">另外，我们在研究截屏问题时还顺便发现了一个奇怪的问题，那就是搜索功能不支持模糊词搜索。以下图为例，当我们想切换刷新率但找不到在哪切换准备通过搜索找时，发现只搜索“刷新率”这三个字是得不到检索结果的。为什么呢？因为这个设置的名称是“显示刷新率”，所以我们必须输入<span style=\"font-size: 16px;\">设置</span>名称开头的“显示”或完整输入“显示刷新率”，系统才能成功检索。</p><p class=\"p_style\">然而，应该没有人能在要调整刷新率的时候会想到自己得搜“显示”这两个字吧。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161416139130447.jpeg\" alt=\"未标题-1.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">语音助手方面，MYUI的“乐语音”可以说算是个还不错的功能。因为它拾音准确率不错，大多时候反馈都很快，无论是修改系统设置，打开第三方APP还是进行网页搜索的速度都很不错。甚至可以快捷的通过语音完成设置导航地点、找附近的停车场、打开听歌识曲、播放音乐/电影/电视剧、控制微信/QQ发红包、打开扫一扫、发消息和视频通话等操作。</p><p class=\"p_style\">并且在motorola edge s机身左侧拥有实体快捷键的前提下，“乐语音”可以直接通过与快捷键关联的“一键触达”功能唤醒。当然，这个功能不仅可以唤醒语音助手，还可以打开健康码和付款码这类小程序，以及相机和便签这类系统预置应用，外加微博和王者荣耀这类自行下载的应用。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161415725385198.jpeg\" alt=\"591A8151.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">此外，MYUI还拥有包含手势操作、moto AI丽音算法、游戏模式、PPT/商务文件拍摄、自定义APP音量和多屏协同在内的诸多特征。  </p><p class=\"p_style\">其中较为实用或颇具趣味的是最后两个，自定义APP音量和多屏协同。前者就像电脑上的音量合成器一样，可以自定义每个APP的音量大小，让自己在游戏时获得音量合适的背景音乐。后者则与EMUI的多屏协同或One UI的Samsung DeX一样，属于可以通过显示器及外设增强手机多任务处理能力的功能。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161415752135300.jpeg\" alt=\"591A8140.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161416095080361.png\" alt=\"Screenshot_20210221-233353.png\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">如果说EMUI、MIUI、ColorOS和OriginOS等所选择的是将更多功能整合进系统中的道路，那MYUI则更像是氢OS那般轻巧简约的道路，并且比氢OS还要更接近于原生的安卓系统，大概就是索尼Xperia手机系统的样子。</p><p class=\"p_style\">在整个评测产出的周期中，MYUI的表现都属于流畅且稳定，我们只遇到过一次自带的应用商店“假死”以及一次语音助手回馈慢(当然这个可能跟我司当时网络有关)的情况，至于应用崩溃、闪退或是系统不稳定卡死的情况则从来没有出现过。所以MYUI对于所有有意购入motorola edge s的人而言，需要关心的点就在于“好不好用”，而非“能不能用”。</p><p class=\"p_style\">再简单点说，就是“愿不愿意适应MYUI和现在手机UI的差异”和“对于现在手机UI有但MYUI没有的功能是否有依赖”这两个问题了。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161415733854927.jpeg\" alt=\"591A8145.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161415732127902.jpeg\" alt=\"591A8148.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">按照惯例，影像其实不应该放这么后面。但或许由于motorola edge s在极低的预算帽之下将其中的大多数资源都投入到了性能相关之处，所以导致其拍照其实跟20W快充一样属于整机略显尴尬的部分，成片质量也只是同价位手机里的常规水平。那常规水平是什么样？自然就是光线充足可以一战，但会有涂抹感和边角画质下降的问题。而光线不充足时，只能满足基本的记录需求。  </p><p class=\"p_style\">如果你很喜欢拍蓝天白云的话，那么motorola edge s显然与你缘分未到，因为它拍出的照片蓝色饱和度明显偏低。就以我们这组样张为例，明明是大晴天顶着烈日出去拍的，但成片却怎么看都只能让人感觉那天应该是阴天。啥？你问我们蓝色饱和度为什么会偏低？我们也不知道。<strike>也许是产线颜料不够，所以工程师把蓝色都拿去喷在后盖上了吧。</strike>  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161416021051182.jpeg\" alt=\"IMG_20210223_144325907.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161416020183492.jpeg\" alt=\"IMG_20210223_144056692.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161416018594100.jpeg\" alt=\"IMG_20210223_143835143.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161416017813517.jpeg\" alt=\"IMG_20210223_142625776_SRRM.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161416012743787.jpeg\" alt=\"IMG_20210223_140528928_HDR.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161416014190817.jpeg\" alt=\"IMG_20210223_141128761.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161432943699229.jpeg\" alt=\"IMG_20210225_194303705.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161432945537850.jpeg\" alt=\"IMG_20210225_193204255.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161432949414509.jpeg\" alt=\"IMG_20210225_194229107.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161432950547220.jpeg\" alt=\"IMG_20210225_191654316.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161432952721198.jpeg\" alt=\"IMG_20210225_195455849.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161432954137496.jpeg\" alt=\"IMG_20210225_200033729_HDR.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">虽然成像并不理想，但由于骁龙870移动平台能带来旗舰级性能的缘故，所以motorola edge s无论相机启动速度，对焦速度，取景器画面都非常流畅，相册里也不会出现“图片先模糊后清晰”的加载过程，可以说是同价位拍照流畅度最好的机型之一。此外，该机还支持240 FPS的慢动作拍摄以及6K 30FPS或4K60 FPS的视频拍摄，并配备了稀有的环形微距补光灯。  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161415757436137.jpeg\" alt=\"591A8131.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">motorola edge s显然正披着摩托罗拉的皮，对外展示着联想手机擅长的“价格屠夫”姿态。毫无疑问的说，motorola edge s的硬件在其所属价格段中处于领先梯队之中，但软件却在所属价格段中处于队尾，外加相机的表现实属一般。尽管1999元的起售价开了个好头，但......咦，1999？嗨，一部1999元的手机要求那么高做什么，喜欢的话就“交个朋友”，不喜欢的话只需一句“下次一定”，那就完事了。</p><p class=\"p_style\">不得不说1999的确具有说服力，当这个数字以价格的形式在mototola edge s发布会上出来的一瞬间，整个B站直播间的画面就被“牛逼”和“666”之类的弹幕所覆盖。这也许就是所有数码爱好者乃至吃瓜群众们都喜闻乐见的东西，毕竟自从步入5G时代之后，两三千元的手机几乎已经成了“温饱线”一般的存在。</p><p class=\"p_style\">但话说回来，我们希望联想手机业务和摩托罗拉不要被发布会和上市初期的舆论赞扬声冲昏了头脑，毕竟2021年还能屹立于智能手机市场上的品牌没有任何一个是善茬。mototola edge s给联想手机业务或摩托罗拉的2021年开了个好头，他们的“回归”毫无疑问会给这个竞争火热的中国智能手机市场再添一桶燃料。而接下来需要做到的自然就是持之以恒，不要让自己像流星一样短暂发光，然后消失在人们的视野中。</p>', 'http://pic.zaeke.com/img161457284893390.jpeg', 7568, 0, 0, NULL, '2021-02-28 20:27:49', '2021-03-27 02:21:43');
INSERT INTO `article` VALUES (16118, 'Flyme9不仅是新系统，更是魅族的新战略', '这场发布会或许并不像是系统的全新展示会，但像是魅族在向我们骄傲地展示他们未来几年内的发展方向。之所以会这样认为，是因为魅族在这次发布会当中介绍的并不仅仅是Fly...', '[object Object]', '烫头孙师傅', 38, 'http://pic.zaeke.com/user_1533281477_5.png', '<p>这场发布会或许并不像是系统的全新展示会，但像是魅族在向我们骄傲地展示他们未来几年内的发展方向。</p><p>之所以会这样认为，是因为魅族在这次发布会当中介绍的并不仅仅是Flyme 9这个系统本身，而是无时无刻不在阐述自己的理念，并且这些理念这中有很多也是远远超过目前智能手机行业平均值的。</p><p><img alt=\"P1090706.jpg\" src=\"http://pic.zaeke.com/img161472890075133.jpeg\" width=\"1400\" height=\"936\"><br></p><p><img alt=\"P1090630.jpg\" src=\"http://pic.zaeke.com/img161472895738190.jpeg\" width=\"3956\" height=\"2643\"><br></p><p>首先是设计。这么多年来魅族在系统设计的审美上一直是业界标杆，不少友商在重构自己的系统时，往往都在审美上都或多或少参照了魅族的思路。尽管魅族本质上是一个小而美的企业，Flyme系统也遵循着小而美的思路来设计，但在美学上确实引领着业界潮流。</p><p>而Flyme 9则在前代的基础上，在本就简约的设计下又做了减法。</p><p><img alt=\"P1090687.jpg\" src=\"http://pic.zaeke.com/img161472901052223.jpeg\" width=\"1400\" height=\"936\"></p><p><img alt=\"P1090622 (1).jpg\" src=\"http://pic.zaeke.com/img161472901046553.jpeg\" width=\"1400\" height=\"936\"><br></p><p>例如屏幕指纹解锁的复杂图标，被魅族简化为既简约又饱含情怀的经典小圆圈；系统界面的装饰元素也得到了充分的精简，并且也在系统当中统一了设计语言，尤其是统一了桌面图标的形状，甚至第三方图标也能够实现完美的统一。并且魅族这次还加入了图标大小的五级调节，对于桌面布局的调整更加自由，而全新的桌面插件也能够进一步提升桌面排布的自由度与功能性。</p><p>除此之外，针对很多品牌当中老大难的横屏动效问题，Flyme 9也进行了全新的设计。Flyme 9也支持半屏交互功能，甚至重新设计了通知中心与用户中心，在保留完整功能性的同时，获得了更加简约干净的视觉效果，这也是魅族所谓的“轻心如意”的设计风格。</p><p><img alt=\"P1090636.jpg\" src=\"http://pic.zaeke.com/img161472912842784.jpeg\" width=\"1400\" height=\"936\"><br></p><p><img alt=\"P1090642.jpg\" src=\"http://pic.zaeke.com/img161472908887640.jpeg\" width=\"1400\" height=\"936\"></p><p>除了系统的交互界面之外，Flyme 9也在息屏显示功能上下了一番苦心。首先是增加了更加有趣且丰富显示效果，其中最值得表扬的是对经典小圆圈呼吸灯的复刻。Flyme 9也提供了有更丰富的Alive壁纸，甚至用户也能够自行创作壁纸，之从而获得更好的壁纸观感体验。</p><p>Flyme 9的动效也变得更加简单耐看，这其中不仅有更流畅的转屏动画和横屏打开动画的功劳，也有更加跟手的上下滑动动效。并且Flyme 9还增加了更加具有空间感的操作动效，窗口动效能够与壁纸联动，用户还可以主动打断动效。</p><p>而针对第三方应用并未遵守安卓设计规范设计图标的情况，Flyme 9还支持图标自分层算法，未图层数量不足的应用添加图层，从而实现流畅的动画效果。甚至为了满足魅友们的情怀，这回魅族还提供了不同版本旧版Flyme的经典主题。</p><p><img alt=\"P1090650.jpg\" src=\"http://pic.zaeke.com/img161472915060912.jpeg\" width=\"1400\" height=\"936\"><br></p><p>最早推出小窗模式的魅族如今在Flyme 9带来了更强大的小窗模式3.0。小窗模式3.0不仅优化了设计，并且支持主动呼出与应用调用等情况下打开。例如在分享图片至社交媒体时应用往往会进行跳转，而跳转时也常会出现横屏与竖屏间的切换，使用体验的割裂感很明显。而在这些情况下Flyme 9都能够通过小窗的形式打开分享的应用，从而获得更加流畅自然的效果。</p><p>显然魅族不单单满足于提供一个好用的小窗模式，甚至希望将小窗模式做得更加独立自主。例如悬浮小窗能便于你能够更方便的一心二用，小窗的尺寸也能够进行调节或贴边隐藏，甚至还能够进行独立的音量调节，多个小窗之间能够通过小窗全手势进行切换。并且在系统导航上，Flyme 9将全手势导航与mBack进行结合，也支持了更加优秀的下拉式操作。</p><p><img alt=\"P1090708.jpg\" src=\"http://pic.zaeke.com/img161472929555292.jpeg\" width=\"1400\" height=\"936\"><br></p><p><img alt=\"P1090695.jpg\" src=\"http://pic.zaeke.com/img161472930812352.jpeg\" width=\"1400\" height=\"936\"><br></p><p>在相机方面，Flyme 9支持全新的动态照片功能，能够以动态影像的方式来记录影像信息。系统自带相册中也增加的可视化进度条以及更丰富的视频编辑功能，能够在视频回放与视频后期处理上提供更加专业便捷的体验。</p><p>并且Flyme 9上的Aicy助手也支持了听歌识曲、连续对话、语音播报、通知语音朗读、播放控制等功能，并且还支持智能家居控制，在实际使用体验以及功能的丰富性上都迈上了一个全新的台阶。</p><p><img alt=\"P1090655.jpg\" src=\"http://pic.zaeke.com/img161472932875123.jpeg\" width=\"1400\" height=\"936\"><br></p><p>当然，上面这些都是主要基于传统的设计与功能方向的内容，更大程度上是解决好不好看以及好不好用。而魅族这次想要解决的显然不只是这两个问题，还有隐私安全保护这个老大难的问题等待着魅族去解决。</p><p><img alt=\"P1090654.jpg\" src=\"http://pic.zaeke.com/img161472935040295.jpeg\" width=\"1400\" height=\"936\"><br></p><p>为了能够更好的保护用户隐私，Flyme 9准备了四大功能，分别是守护权杖、全视鹰眼、隐形妙靴以及以私密斗篷功能。其中守护权杖功能给了用户更多的权限选择权，从而让用户能够有针对性地向应用授予权限，而AI拦截功能也能够主动拦截应用互相唤醒；而全视鹰眼功能则可以实现应用行为记录，并且支持敏感行为提醒，也能够通过使用习惯来提供应用管理建议。</p><p>隐形妙靴功能能够更严格的进行定位授权，也能够模糊定位使用无法读取相关隐私信息；私密斗篷功能则支持空信息授权剪切板隐私保护图片与文件的有限访问功能，以及文件删除保护功能。</p><p><img alt=\"P1090665.jpg\" src=\"http://pic.zaeke.com/img161472938490935.jpeg\" width=\"1400\" height=\"936\"><br></p><p>或许有人无法理解魅族在隐私安全保护上发力的原因，其实在隐私上下这么大的功夫，因为魅族江隐私安全保护作为了自己未来的战略性能力。毕竟面对愈发嚣张与放肆的应用以及越来越难保护的个人隐私，智能手机的隐私保护功能肯定是越强越好，Flyme 9强大的隐私安全保护功能获取也将成为许多用户在购机时倾向于选择魅族的砝码。</p><p>至于适配时间嘛，首发搭载Flyme 9的机型自然是魅族18系列，而魅族17系列则将在3月5日正式内测版、5月份正式推送稳定版系统，而魅族16全家桶机型的适配工作也在有序进行中。</p><p><img alt=\"P1090657.jpg\" src=\"http://pic.zaeke.com/img161472941027570.jpeg\" width=\"1400\" height=\"936\"></p><p><img alt=\"P1090663.jpg\" src=\"http://pic.zaeke.com/img161472941138762.jpeg\" width=\"1400\" height=\"936\"><br></p><p>可别以为上面这些就完了，这次还有一个One more thing——Flyme For Watch。Flyme For Watch采用圆角矩形的图标，搭配全新设计的启动器，还拥有更好的动效。其提示音也与智能手机有所区别，不容易混淆。Flyme For Watch的表盘可以通过小组件进行DIY，也得拥有审美与观感上更好的艺术表盘，手机相册图片也能够作为表盘图案，甚至还支持以动态图片作为表盘。</p><p>而在功能上，Flyme For Watch同时支持通知、同步查找手机、双向查找、音乐控制、遥控拍照等功能，并且由于其支持eSIM，因此能够实现独立的通信功能。而为了在有限的智能手表硬件上实现更好的性能，魅族也带来了One mind for watch，提升了手表的帧率以及续航表现。</p><p>与此同时，魅族 Lipro LED筒灯标准版与Lipro LED智能吸顶灯也将在五月份正式登场，相关的应用也在紧张的研发当中。</p><p><img alt=\"P1090620 (1).jpg\" src=\"http://pic.zaeke.com/img161472947265004.jpeg\" width=\"1400\" height=\"936\"><br></p><p>这是魅族第一次为自家的Flyme系统开发布会，不过这也并不是一场简简单单的手机系统发布会。事实上魅族在这场发布会当中传递了自己的很多全新的理念以及战略构想。未来的Flyme不在拘泥于传统的小而美，而是将在隐私保护上进行转型，打造属于自己的独门秘籍，而智能可穿戴设备与智能家居设备也讲成为魅族全新的努力方向。</p><p>考虑到魅族去年依靠产品力较强的魅族17系列获得盈利，甚至可以说是逆势增长，今年魅族也就更可以放开手脚去做自己想做的事情。Flyme是一个小而美的系统，只不过在精致与美观的同时，如今又多了一份属于魅族自己的小野心。</p>', '<p class=\"p_style\">这场发布会或许并不像是系统的全新展示会，但像是魅族在向我们骄傲地展示他们未来几年内的发展方向。</p><p class=\"p_style\">之所以会这样认为，是因为魅族在这次发布会当中介绍的并不仅仅是Flyme 9这个系统本身，而是无时无刻不在阐述自己的理念，并且这些理念这中有很多也是远远超过目前智能手机行业平均值的。</p><p class=\"jsmargin\"><img alt=\"P1090706.jpg\" src=\"http://pic.zaeke.com/img161472890075133.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"P1090630.jpg\" src=\"http://pic.zaeke.com/img161472895738190.jpeg\" width=\"3956\" height=\"2643\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">首先是设计。这么多年来魅族在系统设计的审美上一直是业界标杆，不少友商在重构自己的系统时，往往都在审美上都或多或少参照了魅族的思路。尽管魅族本质上是一个小而美的企业，Flyme系统也遵循着小而美的思路来设计，但在美学上确实引领着业界潮流。</p><p class=\"p_style\">而Flyme 9则在前代的基础上，在本就简约的设计下又做了减法。</p><p class=\"jsmargin\"><img alt=\"P1090687.jpg\" src=\"http://pic.zaeke.com/img161472901052223.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"P1090622 (1).jpg\" src=\"http://pic.zaeke.com/img161472901046553.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">例如屏幕指纹解锁的复杂图标，被魅族简化为既简约又饱含情怀的经典小圆圈；系统界面的装饰元素也得到了充分的精简，并且也在系统当中统一了设计语言，尤其是统一了桌面图标的形状，甚至第三方图标也能够实现完美的统一。并且魅族这次还加入了图标大小的五级调节，对于桌面布局的调整更加自由，而全新的桌面插件也能够进一步提升桌面排布的自由度与功能性。</p><p class=\"p_style\">除此之外，针对很多品牌当中老大难的横屏动效问题，Flyme 9也进行了全新的设计。Flyme 9也支持半屏交互功能，甚至重新设计了通知中心与用户中心，在保留完整功能性的同时，获得了更加简约干净的视觉效果，这也是魅族所谓的“轻心如意”的设计风格。</p><p class=\"jsmargin\"><img alt=\"P1090636.jpg\" src=\"http://pic.zaeke.com/img161472912842784.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"P1090642.jpg\" src=\"http://pic.zaeke.com/img161472908887640.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">除了系统的交互界面之外，Flyme 9也在息屏显示功能上下了一番苦心。首先是增加了更加有趣且丰富显示效果，其中最值得表扬的是对经典小圆圈呼吸灯的复刻。Flyme 9也提供了有更丰富的Alive壁纸，甚至用户也能够自行创作壁纸，之从而获得更好的壁纸观感体验。</p><p class=\"p_style\">Flyme 9的动效也变得更加简单耐看，这其中不仅有更流畅的转屏动画和横屏打开动画的功劳，也有更加跟手的上下滑动动效。并且Flyme 9还增加了更加具有空间感的操作动效，窗口动效能够与壁纸联动，用户还可以主动打断动效。</p><p class=\"p_style\">而针对第三方应用并未遵守安卓设计规范设计图标的情况，Flyme 9还支持图标自分层算法，未图层数量不足的应用添加图层，从而实现流畅的动画效果。甚至为了满足魅友们的情怀，这回魅族还提供了不同版本旧版Flyme的经典主题。</p><p class=\"jsmargin\"><img alt=\"P1090650.jpg\" src=\"http://pic.zaeke.com/img161472915060912.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">最早推出小窗模式的魅族如今在Flyme 9带来了更强大的小窗模式3.0。小窗模式3.0不仅优化了设计，并且支持主动呼出与应用调用等情况下打开。例如在分享图片至社交媒体时应用往往会进行跳转，而跳转时也常会出现横屏与竖屏间的切换，使用体验的割裂感很明显。而在这些情况下Flyme 9都能够通过小窗的形式打开分享的应用，从而获得更加流畅自然的效果。</p><p class=\"p_style\">显然魅族不单单满足于提供一个好用的小窗模式，甚至希望将小窗模式做得更加独立自主。例如悬浮小窗能便于你能够更方便的一心二用，小窗的尺寸也能够进行调节或贴边隐藏，甚至还能够进行独立的音量调节，多个小窗之间能够通过小窗全手势进行切换。并且在系统导航上，Flyme 9将全手势导航与mBack进行结合，也支持了更加优秀的下拉式操作。</p><p class=\"jsmargin\"><img alt=\"P1090708.jpg\" src=\"http://pic.zaeke.com/img161472929555292.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"P1090695.jpg\" src=\"http://pic.zaeke.com/img161472930812352.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">在相机方面，Flyme 9支持全新的动态照片功能，能够以动态影像的方式来记录影像信息。系统自带相册中也增加的可视化进度条以及更丰富的视频编辑功能，能够在视频回放与视频后期处理上提供更加专业便捷的体验。</p><p class=\"p_style\">并且Flyme 9上的Aicy助手也支持了听歌识曲、连续对话、语音播报、通知语音朗读、播放控制等功能，并且还支持智能家居控制，在实际使用体验以及功能的丰富性上都迈上了一个全新的台阶。</p><p class=\"jsmargin\"><img alt=\"P1090655.jpg\" src=\"http://pic.zaeke.com/img161472932875123.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">当然，上面这些都是主要基于传统的设计与功能方向的内容，更大程度上是解决好不好看以及好不好用。而魅族这次想要解决的显然不只是这两个问题，还有隐私安全保护这个老大难的问题等待着魅族去解决。</p><p class=\"jsmargin\"><img alt=\"P1090654.jpg\" src=\"http://pic.zaeke.com/img161472935040295.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">为了能够更好的保护用户隐私，Flyme 9准备了四大功能，分别是守护权杖、全视鹰眼、隐形妙靴以及以私密斗篷功能。其中守护权杖功能给了用户更多的权限选择权，从而让用户能够有针对性地向应用授予权限，而AI拦截功能也能够主动拦截应用互相唤醒；而全视鹰眼功能则可以实现应用行为记录，并且支持敏感行为提醒，也能够通过使用习惯来提供应用管理建议。</p><p class=\"p_style\">隐形妙靴功能能够更严格的进行定位授权，也能够模糊定位使用无法读取相关隐私信息；私密斗篷功能则支持空信息授权剪切板隐私保护图片与文件的有限访问功能，以及文件删除保护功能。</p><p class=\"jsmargin\"><img alt=\"P1090665.jpg\" src=\"http://pic.zaeke.com/img161472938490935.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">或许有人无法理解魅族在隐私安全保护上发力的原因，其实在隐私上下这么大的功夫，因为魅族江隐私安全保护作为了自己未来的战略性能力。毕竟面对愈发嚣张与放肆的应用以及越来越难保护的个人隐私，智能手机的隐私保护功能肯定是越强越好，Flyme 9强大的隐私安全保护功能获取也将成为许多用户在购机时倾向于选择魅族的砝码。</p><p class=\"p_style\">至于适配时间嘛，首发搭载Flyme 9的机型自然是魅族18系列，而魅族17系列则将在3月5日正式内测版、5月份正式推送稳定版系统，而魅族16全家桶机型的适配工作也在有序进行中。</p><p class=\"jsmargin\"><img alt=\"P1090657.jpg\" src=\"http://pic.zaeke.com/img161472941027570.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"P1090663.jpg\" src=\"http://pic.zaeke.com/img161472941138762.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">可别以为上面这些就完了，这次还有一个One more thing——Flyme For Watch。Flyme For Watch采用圆角矩形的图标，搭配全新设计的启动器，还拥有更好的动效。其提示音也与智能手机有所区别，不容易混淆。Flyme For Watch的表盘可以通过小组件进行DIY，也得拥有审美与观感上更好的艺术表盘，手机相册图片也能够作为表盘图案，甚至还支持以动态图片作为表盘。</p><p class=\"p_style\">而在功能上，Flyme For Watch同时支持通知、同步查找手机、双向查找、音乐控制、遥控拍照等功能，并且由于其支持eSIM，因此能够实现独立的通信功能。而为了在有限的智能手表硬件上实现更好的性能，魅族也带来了One mind for watch，提升了手表的帧率以及续航表现。</p><p class=\"p_style\">与此同时，魅族 Lipro LED筒灯标准版与Lipro LED智能吸顶灯也将在五月份正式登场，相关的应用也在紧张的研发当中。</p><p class=\"jsmargin\"><img alt=\"P1090620 (1).jpg\" src=\"http://pic.zaeke.com/img161472947265004.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">这是魅族第一次为自家的Flyme系统开发布会，不过这也并不是一场简简单单的手机系统发布会。事实上魅族在这场发布会当中传递了自己的很多全新的理念以及战略构想。未来的Flyme不在拘泥于传统的小而美，而是将在隐私保护上进行转型，打造属于自己的独门秘籍，而智能可穿戴设备与智能家居设备也讲成为魅族全新的努力方向。</p><p class=\"p_style\">考虑到魅族去年依靠产品力较强的魅族17系列获得盈利，甚至可以说是逆势增长，今年魅族也就更可以放开手脚去做自己想做的事情。Flyme是一个小而美的系统，只不过在精致与美观的同时，如今又多了一份属于魅族自己的小野心。</p>', 'http://pic.zaeke.com/img161472950541872.jpeg', 4677, 0, 0, NULL, '2021-03-02 15:58:39', '2021-03-27 07:05:47');
INSERT INTO `article` VALUES (16121, 'vivo S9将星光藏入眼眉，从此你一颦一簇皆是温柔', 'vivo那句“2000万像素柔光双摄，照亮你的美”等广告语时不时我们就得拿出来提一遍，原因很简单：尽管vivo的产品线在不断整合与升级，但始终拥有那么一款在自拍...', '[object Object]', 'victory3332', 31, 'http://pic.zaeke.com/user_1533281475_1.png', '<p>vivo那句“2000万像素柔光双摄，照亮你的美”等广告语时不时我们就得拿出来提一遍，原因很简单：尽管vivo的产品线在不断整合与升级，但始终拥有那么一款在自拍表现上非常优异的产品。</p><p>这个主打自拍产品系列曾经是X系列，不过如今提升自拍表现的重任就交到了S系列手上。顶级的前置相机配合熟悉的柔光灯，在加上更成熟的软件自拍算法，就让该系列的最新款vivo S9成为了一款时尚少男少女的自拍利器。</p><p><img src=\"http://pic.zaeke.com/img161476897037332.jpeg\" alt=\"\">  </p><p>时尚怎能被厚重拖累？因此vivo S9特地被打造得极致轻薄。事实上S9是vivo迄今为止最轻薄的5G手机，其厚度仅为7.35毫米，重量也只有173克。而包含印象拾光、子夜蓝与朝露白的三款配色也都颜值颇高，对于时尚机型来说这漂亮的外观设计就足够拿高分了。</p><p>此外，vivo S9是目前市面上极少数采用小刘海的机型，不过这个小刘海中容纳了高水准的前置双摄硬件，所以采用小刘海设计也能够理解。而机身侧面直边式的中框设计，也赶上了今年的复古潮流，棱角分明的同时，中框上也通过亮面金属倒角设计提升质感。</p><p><img src=\"http://pic.zaeke.com/img161476897940791.jpeg\" alt=\"DSC03976.jpg\">  </p><p>vivo S9采用6.44英寸、2400×1080分辨率的AMOLED屏幕，并且支持90Hz高刷新率和180Hz触控采样率。其屏幕指纹能够实现最快0.32秒的解锁速度，从而带来迅速快捷的解锁表现。</p><p>可别见到S9的主打特性和套路就觉得它是个花瓶，事实上vivo S9是市面上首款搭载联发科天玑1100处理器的5G手机。天玑1100是联发科现役的旗舰系列之一，其采用6nm制程打造，搭载4颗2.6GHz的Cortex-A78性能内核与4颗2GHz的Cortex-A55效率内核，搭配的GPU则是Mali-G77 MC9。</p><p>联发科天玑1100还是集成了5核心ISP，可将AI夜景拍摄速度提升20%，或是进行更准确的AI场景识别和AI对象分离。最后天玑1100还支持USF 3.1存储介质，可大幅度提升vivo S9打开应用、载入游戏等日常操作的速度。</p><p>除了上面这些，vivo S9还拥有超薄VC均热板帮助散热，在轻薄机身内则搭载4000mAh电池，支持33W闪充。</p><p><img src=\"http://pic.zaeke.com/img161476900159761.jpeg\" alt=\"\">  </p><p>vivo S9的后置相机采用了好评如潮的云阶设计，后置相机采用6400万像素主摄+800万像素超广角相机+200万像素黑白相机的后置三摄模组。其前置相机则为4400万像素主摄+800万像素超广角相机的前置双摄模组，其中4400万像素主前摄支持自动对焦。</p><p>为了“照亮你的美”，vivo S9引入了双重前置补光的方案。除了使用屏幕对自拍补光，vivo还特地在S9机身屏幕顶部和边框之间对称位置装有2颗隐藏式LED柔光灯，可以在自拍时开启。</p><p>而在最为重要的自拍功能上，vivo S9通过人脸3D建模的方式实现了5重超质感美颜算法，拥有全新的光影人像风格，以及潮趣自拍玩法。对于希望随手拍出好自拍的用户来说，这些新功能显然能够实现更好的自拍观感。</p><p><img src=\"http://pic.zaeke.com/img161476901191508.jpeg\" alt=\"\">  </p><p>vivo S9当然不是一款定位于线上市场的产品，它无论在设计功能还是体验上都更加针对线下用户。vivo S9或许不是这个价位性价比最高的机型，但对于有强烈自拍需求的用户来说这，或许是同价位之中总体自拍表现于观感最优秀的机型之一，甚至可能是唯一。</p><p>打造出一款能够“照亮你的美”的智能手机很简单，但将这个思路贯彻到底，让自拍机型形成有延续性的系列产品，就需要不小的坚持与努力了。</p><p><strong>更多图赏</strong></p><p><img src=\"http://pic.zaeke.com/img161476906329459.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161476906343448.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161476906219573.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161476907617647.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161476908578505.jpeg\" alt=\"\"> </p>', '<p class=\"p_style\">vivo那句“2000万像素柔光双摄，照亮你的美”等广告语时不时我们就得拿出来提一遍，原因很简单：尽管vivo的产品线在不断整合与升级，但始终拥有那么一款在自拍表现上非常优异的产品。</p><p class=\"p_style\">这个主打自拍产品系列曾经是X系列，不过如今提升自拍表现的重任就交到了S系列手上。顶级的前置相机配合熟悉的柔光灯，在加上更成熟的软件自拍算法，就让该系列的最新款vivo S9成为了一款时尚少男少女的自拍利器。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161476897037332.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">时尚怎能被厚重拖累？因此vivo S9特地被打造得极致轻薄。事实上S9是vivo迄今为止最轻薄的5G手机，其厚度仅为7.35毫米，重量也只有173克。而包含印象拾光、子夜蓝与朝露白的三款配色也都颜值颇高，对于时尚机型来说这漂亮的外观设计就足够拿高分了。</p><p class=\"p_style\">此外，vivo S9是目前市面上极少数采用小刘海的机型，不过这个小刘海中容纳了高水准的前置双摄硬件，所以采用小刘海设计也能够理解。而机身侧面直边式的中框设计，也赶上了今年的复古潮流，棱角分明的同时，中框上也通过亮面金属倒角设计提升质感。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161476897940791.jpeg\" alt=\"DSC03976.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">vivo S9采用6.44英寸、2400×1080分辨率的AMOLED屏幕，并且支持90Hz高刷新率和180Hz触控采样率。其屏幕指纹能够实现最快0.32秒的解锁速度，从而带来迅速快捷的解锁表现。</p><p class=\"p_style\">可别见到S9的主打特性和套路就觉得它是个花瓶，事实上vivo S9是市面上首款搭载联发科天玑1100处理器的5G手机。天玑1100是联发科现役的旗舰系列之一，其采用6nm制程打造，搭载4颗2.6GHz的Cortex-A78性能内核与4颗2GHz的Cortex-A55效率内核，搭配的GPU则是Mali-G77 MC9。</p><p class=\"p_style\">联发科天玑1100还是集成了5核心ISP，可将AI夜景拍摄速度提升20%，或是进行更准确的AI场景识别和AI对象分离。最后天玑1100还支持USF 3.1存储介质，可大幅度提升vivo S9打开应用、载入游戏等日常操作的速度。</p><p class=\"p_style\">除了上面这些，vivo S9还拥有超薄VC均热板帮助散热，在轻薄机身内则搭载4000mAh电池，支持33W闪充。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161476900159761.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">vivo S9的后置相机采用了好评如潮的云阶设计，后置相机采用6400万像素主摄+800万像素超广角相机+200万像素黑白相机的后置三摄模组。其前置相机则为4400万像素主摄+800万像素超广角相机的前置双摄模组，其中4400万像素主前摄支持自动对焦。</p><p class=\"p_style\">为了“照亮你的美”，vivo S9引入了双重前置补光的方案。除了使用屏幕对自拍补光，vivo还特地在S9机身屏幕顶部和边框之间对称位置装有2颗隐藏式LED柔光灯，可以在自拍时开启。</p><p class=\"p_style\">而在最为重要的自拍功能上，vivo S9通过人脸3D建模的方式实现了5重超质感美颜算法，拥有全新的光影人像风格，以及潮趣自拍玩法。对于希望随手拍出好自拍的用户来说，这些新功能显然能够实现更好的自拍观感。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161476901191508.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">vivo S9当然不是一款定位于线上市场的产品，它无论在设计功能还是体验上都更加针对线下用户。vivo S9或许不是这个价位性价比最高的机型，但对于有强烈自拍需求的用户来说这，或许是同价位之中总体自拍表现于观感最优秀的机型之一，甚至可能是唯一。</p><p class=\"p_style\">打造出一款能够“照亮你的美”的智能手机很简单，但将这个思路贯彻到底，让自拍机型形成有延续性的系列产品，就需要不小的坚持与努力了。</p><p class=\"p_style\"><strong>更多图赏</strong></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161476906329459.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161476906343448.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161476906219573.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161476907617647.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161476908578505.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"> </p>', 'http://pic.zaeke.com/img161476920112139.jpeg', 4961, 0, 0, NULL, '2021-03-03 03:00:34', '2021-03-27 07:03:29');
INSERT INTO `article` VALUES (16124, '魅族18 Pro既是高端精品，也是熟悉的青年良品', '​​没想到吧，去年魅族靠走精品路线的方式实现了盈利，那么今年维持这个路线显然也是魅族的最佳途径，魅族18系列也同样秉承着这个思路。无论是魅族18还是魅族18Pr...', '[object Object]', '烫头孙师傅', 38, 'http://pic.zaeke.com/user_1533281477_5.png', '<p>​​没想到吧，去年魅族靠走精品路线的方式实现了盈利，那么今年维持这个路线显然也是魅族的最佳途径，魅族18系列也同样秉承着这个思路。</p><p>无论是魅族18还是魅族18 Pro，均搭载高通骁龙888移动平台，能够提供顶级性能。与此同时，这两款机型在功能性上有一定的差异，作为超大杯的魅族18 Pro试图通过堆料打造出顶级旗舰，小而美依然的魅族18则是一款轻薄旗舰。</p><p style=\"text-align: center;\"><img alt=\"P1090736.jpg\" src=\"http://pic.zaeke.com/img161482559684196.jpeg\" width=\"1400\" height=\"936\"></p><p style=\"text-align: center;\"><img alt=\"P1090804.jpg\" src=\"http://pic.zaeke.com/img161482559042011.jpeg\" width=\"1400\" height=\"936\"><br></p><p>虽然尺寸不同，但这两款机型均搭载2K+分辨率屏幕，支持120Hz高刷新率与240Hz的高触控采样率，甚至这两块屏幕也都支持HDR10+。而高通骁龙888移动平台与LPDDR5+UFS3.1的搭配，也更加符合旗舰机的定位。</p><p>二者在影像上的主要差距在于：魅族18没有深感摄像头，超广角摄像头则是由魅族18 Pro的3200像素降为1600万像素，主摄则由5000万像素的三星GN1感光元件降为了6400万像素的索尼IMX682感光元件。</p><p>换句话说，这两款机型都是真正意义上的旗舰机，只不过若你更在意包含影像在内的丰富功能，那么选魅族18 Pro最好；如果更加在乎轻薄与质感，那么魅族18再合适不过。</p><p style=\"text-align: center;\"><img alt=\"P1090725.jpg\" src=\"http://pic.zaeke.com/img161482567528602.jpeg\" width=\"1400\" height=\"936\"></p><p style=\"text-align: center;\"><img alt=\"P1090734 (1).jpg\" src=\"http://pic.zaeke.com/img161482568548679.jpeg\" width=\"1400\" height=\"936\"><br></p><p>由于我们拿到的是魅族18 Pro，所以本文主要围绕魅族18 Pro来说。先从设计上来看，在我们那看看外观设计非常出色，不似去年的魅族17系列争议不小的“防爆盾”设计，魅族18系列矩阵式相机模组的设计非常漂亮。并且以我们拿到的白色版本为例，相机盖板玻璃也不是市面上常见的黑色，是非常漂亮的白色，整体观感非常和谐。</p><p><img alt=\"P1090838.jpg\" src=\"http://pic.zaeke.com/img161482571019145.jpeg\" width=\"1400\" height=\"936\"><br></p><p style=\"text-align: center;\"><img alt=\"P1090818.jpg\" src=\"http://pic.zaeke.com/img161482571262677.jpeg\" width=\"1400\" height=\"936\"><br></p><p><font color=\"#333333\">虽然魅族18这一代已经不再拥有白色前面板的版本，不过窄边框的设计搭配微曲面在观感上依然出彩。并且为了实现更好的使用体验，魅族这次也没有让屏幕曲面的曲度过大，从而在一定程度上减少了误触</font><font color=\"#333333\">可能。而白色后盖搭配银色中框的观感也非常讨好要求，从我个人的审美偏好出发，魅族18 Pro的设计我可以给个高分。</font><br></p><p>但这对于魅族来说或许只是常规操作。毕竟魅族在机身设计的美学表现上一直有极高的造诣，魅族设计的不少机型也被很多用户认为颜值较高且有独特的审美追求，魅族18系列在颜值上的高表现也完全在意料之中。<br></p><p style=\"text-align: center;\"><img alt=\"P1090738 (1).jpg\" src=\"http://pic.zaeke.com/img161482577294091.jpeg\" width=\"1400\" height=\"936\"></p><p style=\"text-align: center;\"><img alt=\"P1090744.jpg\" src=\"http://pic.zaeke.com/img161482576723451.jpeg\" width=\"1400\" height=\"936\"><br></p><p>屏幕可以说是这次魅族18 Pro的亮点，首先这块3200×1440分辨率的6.7英寸屏幕在精细度上非常优秀，而120Hz高刷新率、HDR10+、E4发光材料这些特性也足够有吸引力。这块屏幕从纸面参数来看其实就已经足够赏心悦目了，并且从实际观感的角度出发，其清晰度与色彩表现也非常优秀，对于这个定位的机型来说肯定没有问题。</p><p>而另一个很容易令人忽略的细节，是魅族为魅族18系列准备了一张手感不错的原厂屏幕贴膜。要知道贴膜一直是曲面屏手机的老大难问题，好贴的手感不好、手感好的不好贴，一块手感不涩、甚至是顺滑有余的出场贴膜确实能够博得蛮多好感。</p><p style=\"text-align: center;\"><img alt=\"P1090753.jpg\" src=\"http://pic.zaeke.com/img161482584196548.jpeg\" width=\"1400\" height=\"936\"></p><p style=\"text-align: center;\"><img alt=\"P1090772.jpg\" src=\"http://pic.zaeke.com/img161482585792934.jpeg\" width=\"1400\" height=\"936\"><br></p><p>还有一点值得一提，魅族18 Pro的指纹识别采用超声波指纹识别方案。如果放在前两年，用超声波指纹肯定会骂声一片，不过如今超声波指纹的技术也逐渐开始成熟，因此解锁速度和准确率也不再拉胯，具体速度表现可以参考三星Galaxy S21 Ultra。并且这个指纹识别模组的位置也被魅族安排在了稍微向上的位置，在使用时不需要以远离手机重心的方式来握持，相较于市面上部分指纹识别模块偏下的机型而言更加顺手。</p><p>性能上当然也就没什么多说的了。魅族18 Pro搭载高通骁龙888移动平台，配合最高12GB RAM+256GB ROM的搭配，并且也如前文所述是LPDDR5搭配UFS 3.1。旗舰级的性能配置当然只是基本功，不过由于目前刚刚拿到机器，我们也没有办法进行严谨的测试，如果有兴趣的话就请关注我们后面的详细评测文章吧。</p><p style=\"text-align: center;\"><img alt=\"P1090834.jpg\" src=\"http://pic.zaeke.com/img161482595940359.jpeg\" width=\"1400\" height=\"936\"></p><p style=\"text-align: center;\"><img alt=\"P1090842.jpg\" src=\"http://pic.zaeke.com/img161482596689947.jpeg\" width=\"1400\" height=\"936\"><br></p><p>影像系统则是魅族18 Pro升级非常大的一块，该机配备5000万像素主摄+3200万像素超广角摄像机+800万像素3X长焦相机+深感相机的后置相机模组。相较于上代，魅族18 Pro在影像系统上的进步非常明显，尤其是主摄在补足短板之后终于有和旗舰机们一较高下的资本。</p><p style=\"text-align: center;\"><img alt=\"P10225-165130.jpg\" src=\"http://pic.zaeke.com/img161482609637767.jpeg\" width=\"1050\" height=\"1400\"></p><p style=\"text-align: center;\"><img alt=\"P10225-165403.jpg\" src=\"http://pic.zaeke.com/img161482610278339.jpeg\" width=\"1400\" height=\"1050\"><br></p><p>相比魅族 17 在刚刚发布时，超广角摄像头的表现还需要好好打磨，魅族 18 Pro 现在的固件中的表现已经是要成熟得多。诸如边角的伪色和噪点的控制都相当不错，可以说现在的超广角摄像头的调校已经是令人满意的。</p><p><img alt=\"P10225-170603.jpg\" src=\"http://pic.zaeke.com/img161482619625878.jpeg\" width=\"1050\" height=\"1400\"><br></p><p><img alt=\"P10225-170116.jpg\" src=\"http://pic.zaeke.com/img161482618791422.jpeg\" width=\"1050\" height=\"1400\"><br></p><p style=\"text-align: center;\"><img alt=\"P10225-170056.jpg\" src=\"http://pic.zaeke.com/img161482616464878.jpeg\" width=\"1400\" height=\"1050\"><br></p><p style=\"text-align: center;\"><img alt=\"P10225-165441.jpg\" src=\"http://pic.zaeke.com/img161482617598283.jpeg\" width=\"1400\" height=\"1050\"><br></p><p>魅族18 Pro的主摄采用的三星GN1感光元件，魅族围绕着这颗感光元件在防抖与设计上下了许多功夫。从实际拍摄的表现，来看这颗主摄在画面细节上完全符合这颗大底感光元件所应拥有的素质，画面色彩方面也因魅族在色彩上的审美而相对出彩，这颗主摄在我们简单测试的夜景场景下的观感也非常优秀，色彩鲜艳并且反差也非常合理。</p><p style=\"text-align: center;\"><img alt=\"P10225-165008.jpg\" src=\"http://pic.zaeke.com/img161482625290596.jpeg\" width=\"1400\" height=\"1050\"></p><p style=\"text-align: center;\"><img alt=\"P10225-165311.jpg\" src=\"http://pic.zaeke.com/img161482624524771.jpeg\" width=\"1050\" height=\"1400\"><br></p><p>这回的3X长焦相机也非常优秀，我曾经就就表扬过魅族17 Pro的3X长焦相机的出彩表现，魅族18 Pro的3X长焦同样表现出彩。尽管像素不高，并且画质严格来说也并不能说非常优秀，但合理的焦段以及够用的画质，再配合相对较近的最近对焦距离，让这颗3X长焦相机不止能够拍摄人像，拍摄小物件是非常不错的，甚至不严谨的产品摄影也能够基本胜任。</p><p>距离我们拿到魅族18 Pro总共也没有几个小时，因此我们对其相机的评价也只是一个非常初步的观点，影像方面的深度评价就留到后面的详细评测内容再说了。</p><p style=\"text-align: center;\"><img alt=\"P1090722.jpg\" src=\"http://pic.zaeke.com/img161482635161500.jpeg\" width=\"4000\" height=\"2672\"><br></p><p>伴随着魅族18系列的发布，魅族还做出了一个新的决定。未来的魅族手机都将0广告、0营销推送以及0预装第三方应用。这固然建立在魅族18系列价格升高的前提下，可我们也得反过来考虑：很多价格较高的旗舰机行在保有相对丰富的利润空间的同时也依然在推送广告，对吧？</p><p>广告收入始终是一块肥肉，就算走高端路线也有品牌不愿意放弃这部分收入。尽管魅族现在走高端精品路线但让你放弃这比收入换来用户体验的策划其实也是值得表扬的。</p><p>也同样是为了环保，魅族18系列取消了随机附送摸充电头。不过与此同时魅族还给出非常有趣的方案，除了给充电头以套餐优惠价之外，还可以用家中的2个废旧充电头（哪怕是无法工作的报废品）交由魅族回收，魅族在进行环保处理的同时也将回赠一个全新的魅族充电头。</p><p style=\"text-align: center;\"><img alt=\"P1090850.jpg\" src=\"http://pic.zaeke.com/img161482648095202.jpeg\" width=\"1400\" height=\"936\"><br></p><p style=\"text-align: center;\"><img alt=\"P1090848.jpg\" src=\"http://pic.zaeke.com/img161482643219796.jpeg\" width=\"1400\" height=\"936\"><br></p><p>在魅族16系列那一代，魅族选择了高性价比的路线；而在魅族17系列那一代，魅族要选择了高端精品路线；而在魅族18系列上，魅族选择了高端精品这个概念贯彻到底。</p><p>或许有部分用户会觉得这个价格没什么竞争力，不过去年魅族17系列也被这么认为，然而魅族却实现了逆势增长的好成绩。在魅族18系列刚发布的时间节点，我们确实也不知道魅族18系列是否也能够确定比较好的市场成表现。不过魅族的转型态度却非常明显。无论是Flyme 9系统对于隐私的重视，还是魅族18系列放开手脚走高端精品路线都有所体现。</p><p>至于魅族18系列能不能重铸荣光，或者成为这个目标实现的重要过程，市场会给我们答案。​​​​</p>', '<p class=\"p_style\">​​没想到吧，去年魅族靠走精品路线的方式实现了盈利，那么今年维持这个路线显然也是魅族的最佳途径，魅族18系列也同样秉承着这个思路。</p><p class=\"p_style\">无论是魅族18还是魅族18 Pro，均搭载高通骁龙888移动平台，能够提供顶级性能。与此同时，这两款机型在功能性上有一定的差异，作为超大杯的魅族18 Pro试图通过堆料打造出顶级旗舰，小而美依然的魅族18则是一款轻薄旗舰。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"P1090736.jpg\" src=\"http://pic.zaeke.com/img161482559684196.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"P1090804.jpg\" src=\"http://pic.zaeke.com/img161482559042011.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">虽然尺寸不同，但这两款机型均搭载2K+分辨率屏幕，支持120Hz高刷新率与240Hz的高触控采样率，甚至这两块屏幕也都支持HDR10+。而高通骁龙888移动平台与LPDDR5+UFS3.1的搭配，也更加符合旗舰机的定位。</p><p class=\"p_style\">二者在影像上的主要差距在于：魅族18没有深感摄像头，超广角摄像头则是由魅族18 Pro的3200像素降为1600万像素，主摄则由5000万像素的三星GN1感光元件降为了6400万像素的索尼IMX682感光元件。</p><p class=\"p_style\">换句话说，这两款机型都是真正意义上的旗舰机，只不过若你更在意包含影像在内的丰富功能，那么选魅族18 Pro最好；如果更加在乎轻薄与质感，那么魅族18再合适不过。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"P1090725.jpg\" src=\"http://pic.zaeke.com/img161482567528602.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"P1090734 (1).jpg\" src=\"http://pic.zaeke.com/img161482568548679.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">由于我们拿到的是魅族18 Pro，所以本文主要围绕魅族18 Pro来说。先从设计上来看，在我们那看看外观设计非常出色，不似去年的魅族17系列争议不小的“防爆盾”设计，魅族18系列矩阵式相机模组的设计非常漂亮。并且以我们拿到的白色版本为例，相机盖板玻璃也不是市面上常见的黑色，是非常漂亮的白色，整体观感非常和谐。</p><p class=\"jsmargin\"><img alt=\"P1090838.jpg\" src=\"http://pic.zaeke.com/img161482571019145.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"><br></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"P1090818.jpg\" src=\"http://pic.zaeke.com/img161482571262677.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\"><font color=\"#333333\">虽然魅族18这一代已经不再拥有白色前面板的版本，不过窄边框的设计搭配微曲面在观感上依然出彩。并且为了实现更好的使用体验，魅族这次也没有让屏幕曲面的曲度过大，从而在一定程度上减少了误触</font><font color=\"#333333\">可能。而白色后盖搭配银色中框的观感也非常讨好要求，从我个人的审美偏好出发，魅族18 Pro的设计我可以给个高分。</font><br></p><p class=\"p_style\">但这对于魅族来说或许只是常规操作。毕竟魅族在机身设计的美学表现上一直有极高的造诣，魅族设计的不少机型也被很多用户认为颜值较高且有独特的审美追求，魅族18系列在颜值上的高表现也完全在意料之中。<br></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"P1090738 (1).jpg\" src=\"http://pic.zaeke.com/img161482577294091.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"P1090744.jpg\" src=\"http://pic.zaeke.com/img161482576723451.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">屏幕可以说是这次魅族18 Pro的亮点，首先这块3200×1440分辨率的6.7英寸屏幕在精细度上非常优秀，而120Hz高刷新率、HDR10+、E4发光材料这些特性也足够有吸引力。这块屏幕从纸面参数来看其实就已经足够赏心悦目了，并且从实际观感的角度出发，其清晰度与色彩表现也非常优秀，对于这个定位的机型来说肯定没有问题。</p><p class=\"p_style\">而另一个很容易令人忽略的细节，是魅族为魅族18系列准备了一张手感不错的原厂屏幕贴膜。要知道贴膜一直是曲面屏手机的老大难问题，好贴的手感不好、手感好的不好贴，一块手感不涩、甚至是顺滑有余的出场贴膜确实能够博得蛮多好感。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"P1090753.jpg\" src=\"http://pic.zaeke.com/img161482584196548.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"P1090772.jpg\" src=\"http://pic.zaeke.com/img161482585792934.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">还有一点值得一提，魅族18 Pro的指纹识别采用超声波指纹识别方案。如果放在前两年，用超声波指纹肯定会骂声一片，不过如今超声波指纹的技术也逐渐开始成熟，因此解锁速度和准确率也不再拉胯，具体速度表现可以参考三星Galaxy S21 Ultra。并且这个指纹识别模组的位置也被魅族安排在了稍微向上的位置，在使用时不需要以远离手机重心的方式来握持，相较于市面上部分指纹识别模块偏下的机型而言更加顺手。</p><p class=\"p_style\">性能上当然也就没什么多说的了。魅族18 Pro搭载高通骁龙888移动平台，配合最高12GB RAM+256GB ROM的搭配，并且也如前文所述是LPDDR5搭配UFS 3.1。旗舰级的性能配置当然只是基本功，不过由于目前刚刚拿到机器，我们也没有办法进行严谨的测试，如果有兴趣的话就请关注我们后面的详细评测文章吧。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"P1090834.jpg\" src=\"http://pic.zaeke.com/img161482595940359.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"P1090842.jpg\" src=\"http://pic.zaeke.com/img161482596689947.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">影像系统则是魅族18 Pro升级非常大的一块，该机配备5000万像素主摄+3200万像素超广角摄像机+800万像素3X长焦相机+深感相机的后置相机模组。相较于上代，魅族18 Pro在影像系统上的进步非常明显，尤其是主摄在补足短板之后终于有和旗舰机们一较高下的资本。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"P10225-165130.jpg\" src=\"http://pic.zaeke.com/img161482609637767.jpeg\" width=\"1050\" height=\"1400\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"P10225-165403.jpg\" src=\"http://pic.zaeke.com/img161482610278339.jpeg\" width=\"1400\" height=\"1050\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">相比魅族 17 在刚刚发布时，超广角摄像头的表现还需要好好打磨，魅族 18 Pro 现在的固件中的表现已经是要成熟得多。诸如边角的伪色和噪点的控制都相当不错，可以说现在的超广角摄像头的调校已经是令人满意的。</p><p class=\"jsmargin\"><img alt=\"P10225-170603.jpg\" src=\"http://pic.zaeke.com/img161482619625878.jpeg\" width=\"1050\" height=\"1400\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"P10225-170116.jpg\" src=\"http://pic.zaeke.com/img161482618791422.jpeg\" width=\"1050\" height=\"1400\" style=\"margin: 0px auto; height: auto;\"><br></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"P10225-170056.jpg\" src=\"http://pic.zaeke.com/img161482616464878.jpeg\" width=\"1400\" height=\"1050\" style=\"margin: 0px auto; height: auto;\"><br></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"P10225-165441.jpg\" src=\"http://pic.zaeke.com/img161482617598283.jpeg\" width=\"1400\" height=\"1050\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">魅族18 Pro的主摄采用的三星GN1感光元件，魅族围绕着这颗感光元件在防抖与设计上下了许多功夫。从实际拍摄的表现，来看这颗主摄在画面细节上完全符合这颗大底感光元件所应拥有的素质，画面色彩方面也因魅族在色彩上的审美而相对出彩，这颗主摄在我们简单测试的夜景场景下的观感也非常优秀，色彩鲜艳并且反差也非常合理。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"P10225-165008.jpg\" src=\"http://pic.zaeke.com/img161482625290596.jpeg\" width=\"1400\" height=\"1050\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"P10225-165311.jpg\" src=\"http://pic.zaeke.com/img161482624524771.jpeg\" width=\"1050\" height=\"1400\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">这回的3X长焦相机也非常优秀，我曾经就就表扬过魅族17 Pro的3X长焦相机的出彩表现，魅族18 Pro的3X长焦同样表现出彩。尽管像素不高，并且画质严格来说也并不能说非常优秀，但合理的焦段以及够用的画质，再配合相对较近的最近对焦距离，让这颗3X长焦相机不止能够拍摄人像，拍摄小物件是非常不错的，甚至不严谨的产品摄影也能够基本胜任。</p><p class=\"p_style\">距离我们拿到魅族18 Pro总共也没有几个小时，因此我们对其相机的评价也只是一个非常初步的观点，影像方面的深度评价就留到后面的详细评测内容再说了。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"P1090722.jpg\" src=\"http://pic.zaeke.com/img161482635161500.jpeg\" width=\"4000\" height=\"2672\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">伴随着魅族18系列的发布，魅族还做出了一个新的决定。未来的魅族手机都将0广告、0营销推送以及0预装第三方应用。这固然建立在魅族18系列价格升高的前提下，可我们也得反过来考虑：很多价格较高的旗舰机行在保有相对丰富的利润空间的同时也依然在推送广告，对吧？</p><p class=\"p_style\">广告收入始终是一块肥肉，就算走高端路线也有品牌不愿意放弃这部分收入。尽管魅族现在走高端精品路线但让你放弃这比收入换来用户体验的策划其实也是值得表扬的。</p><p class=\"p_style\">也同样是为了环保，魅族18系列取消了随机附送摸充电头。不过与此同时魅族还给出非常有趣的方案，除了给充电头以套餐优惠价之外，还可以用家中的2个废旧充电头（哪怕是无法工作的报废品）交由魅族回收，魅族在进行环保处理的同时也将回赠一个全新的魅族充电头。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"P1090850.jpg\" src=\"http://pic.zaeke.com/img161482648095202.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"><br></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"P1090848.jpg\" src=\"http://pic.zaeke.com/img161482643219796.jpeg\" width=\"1400\" height=\"936\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">在魅族16系列那一代，魅族选择了高性价比的路线；而在魅族17系列那一代，魅族要选择了高端精品路线；而在魅族18系列上，魅族选择了高端精品这个概念贯彻到底。</p><p class=\"p_style\">或许有部分用户会觉得这个价格没什么竞争力，不过去年魅族17系列也被这么认为，然而魅族却实现了逆势增长的好成绩。在魅族18系列刚发布的时间节点，我们确实也不知道魅族18系列是否也能够确定比较好的市场成表现。不过魅族的转型态度却非常明显。无论是Flyme 9系统对于隐私的重视，还是魅族18系列放开手脚走高端精品路线都有所体现。</p><p class=\"p_style\">至于魅族18系列能不能重铸荣光，或者成为这个目标实现的重要过程，市场会给我们答案。​​​​</p>', 'http://pic.zaeke.com/img161482537226863.jpeg', 10971, 0, 0, NULL, '2021-03-03 18:36:35', '2021-03-27 07:02:07');
INSERT INTO `article` VALUES (16126, 'realme GT图赏：性价比旗舰，这就做给你看', 'realmeGT是款蛮有意思的机型。在这个旗舰手机价格飞涨的时代，光是3K+价位、采用旗舰级平台的机型就足以被称作“性价比”。即便有那么少数2K+价位的旗舰性能...', '[object Object]', '烫头孙师傅', 38, 'http://pic.zaeke.com/user_1533281477_5.png', '<p>realme GT是款蛮有意思的机型。</p><p>在这个旗舰手机价格飞涨的时代，光是3K+价位、采用旗舰级平台的机型就足以被称作“性价比”。即便有那么少数2K+价位的旗舰性能机型，也往往出现在一款移动平台产品周期的末期，在乎性价比的用户往往只能选择等。</p><p><img alt=\"591A8232.jpg\" src=\"http://pic.zaeke.com/img161484007126584.jpeg\" width=\"2000\" height=\"1125\"><br></p><p>而realme GT却是当下少见的、在骁龙888平台产品周期的早期发布的、起售价不到3000元的性价比旗舰机型。对于追求极致性价比的用户群体而言，realme GT的确是他们心中最酷的性价比产品。</p><p>虽然论精致感比不过那些高端旗舰，不过realme GT在设计上也非常出则。例如银河战舰与深海飞艇配色采用3D玻璃材质打造机身后盖，并拥有大面积的“加速赛道纹”；而战神特别版-曙光则采用素皮+玻璃的黑黄撞色设计，拥有非常独特的机身手感。</p><p><img alt=\"591A8237.jpg\" src=\"http://pic.zaeke.com/img161484008416237.jpeg\" width=\"2000\" height=\"1125\"></p><p><img alt=\"591A8286.jpg\" src=\"http://pic.zaeke.com/img161484008414665.jpeg\" width=\"2000\" height=\"1125\"></p><p>虽然是性价比机型，但realme GT依然采用了120Hz Super AMOLED电竞屏，这块屏幕的分辨率为2400×1080，触控采样率则高达360Hz。此外realme GT还拥有前后双环境光传感器，支持新一代光学屏下指纹，并且包含《王者荣耀》、《和平精英》在内的游戏也将随后适配90Hz模式、</p><p>在性能上，realme GT搭载高通骁龙888移动平台，并配备LPDDR5的RAM与UFS 3.1 ROM。对于一款性价比旗舰来说，关乎性能的硬件当然也不会出现缩水，毕竟这个定位机器砍啥也不会砍性能。</p><p>为了让骁龙888平台能够持续输出性能，realme GT还准备了3D液冷钢化散热。以铜钢复合内部结构搭配多层立体式石墨散热，并优化毛细散热结构，从而提升散热效率。并且在软件层面上，该机也拥有GT模式，开启后能够全面释放系统性能并全面开放120Hz高刷。</p><p><img alt=\"591A8227.jpg\" src=\"http://pic.zaeke.com/img161484012959855.jpeg\" width=\"2000\" height=\"1125\"></p><p><img alt=\"591A8257.jpg\" src=\"http://pic.zaeke.com/img161484012970974.jpeg\" width=\"2000\" height=\"1125\"><br></p><p>而在相机部分，realme GT采用6400万像素主摄+800万像素超广角相机+200万像素微距相机的后置三摄模组，其中主摄为索尼IMX682感光元件，超广角相机则达到了119°视角，前置相机则为1600万像素。</p><p>并且realme GT还拥有新一代Pure RAW夜景算法也超级暗光视频2.0，能够在暗光场景下更好地拍摄出观感优异的静态/动态影像。而针对自拍需求，该机的前置相机也支持前置人像超级夜景、人像虚化+HDR自拍功能。</p><p><img alt=\"591A8210.jpg\" src=\"http://pic.zaeke.com/img161484014197991.jpeg\" width=\"2000\" height=\"1125\"><br></p><p>在特色功能上，realme GT还拥有多功能NFC、Tactile Engine线性马达与双扬声器，虽然性价比机型的定位并不能让该机面面俱到，但能为体验带来明显感知的功能也确实相对全面。</p><p>此外，realme GT内置4500mAh电池，根据官方说法能够持续王者荣耀游戏5.6小时，并且其采用的65W智慧闪充在同价位也算非常出彩，35分钟即可充满全部电量，无需为充电进行过多等待。</p><p><img alt=\"591A8295.jpg\" src=\"http://pic.zaeke.com/img161484017275197.jpeg\" width=\"2000\" height=\"1125\"><br></p><p>作为realme的全新旗舰系列，realme GT在保证旗舰级性能的同时，也实现了非常优秀的性价比。尽管正像前文所述那样，它并不能真正全方位看齐顶级旗舰，可也的确是一款极具竞争力的机型，也更是性价比用户的福音。</p><p>当然，realme GT并不是唯一一款售价3000内的骁龙888机型，市面上这场性价比旗舰之战似乎菜刚刚开始，只不过realme这回的确抢到了先机。</p><p><b>更多美图：</b></p><p><b><img alt=\"591A8207.jpg\" src=\"http://pic.zaeke.com/img161484020252681.jpeg\" width=\"2000\" height=\"1125\"><br></b></p><p><b style=\"font-size: 16px;\"><img alt=\"591A8226.jpg\" src=\"http://pic.zaeke.com/img161484020213123.jpeg\" width=\"2000\" height=\"1125\"></b></p><p><b style=\"font-size: 16px;\"><img alt=\"591A8282.jpg\" src=\"http://pic.zaeke.com/img161484021941308.jpeg\" width=\"2000\" height=\"1125\"></b></p><p><b style=\"font-size: 16px;\"><b style=\"font-size: 16px;\"><img alt=\"591A8269.jpg\" src=\"http://pic.zaeke.com/img161484020285525.jpeg\" width=\"2000\" height=\"1125\"></b><br></b></p><p><img alt=\"591A8297.jpg\" src=\"http://pic.zaeke.com/img161484022181624.jpeg\" width=\"2000\" height=\"1125\"></p>', '<p class=\"p_style\">realme GT是款蛮有意思的机型。</p><p class=\"p_style\">在这个旗舰手机价格飞涨的时代，光是3K+价位、采用旗舰级平台的机型就足以被称作“性价比”。即便有那么少数2K+价位的旗舰性能机型，也往往出现在一款移动平台产品周期的末期，在乎性价比的用户往往只能选择等。</p><p class=\"jsmargin\"><img alt=\"591A8232.jpg\" src=\"http://pic.zaeke.com/img161484007126584.jpeg\" width=\"2000\" height=\"1125\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">而realme GT却是当下少见的、在骁龙888平台产品周期的早期发布的、起售价不到3000元的性价比旗舰机型。对于追求极致性价比的用户群体而言，realme GT的确是他们心中最酷的性价比产品。</p><p class=\"p_style\">虽然论精致感比不过那些高端旗舰，不过realme GT在设计上也非常出则。例如银河战舰与深海飞艇配色采用3D玻璃材质打造机身后盖，并拥有大面积的“加速赛道纹”；而战神特别版-曙光则采用素皮+玻璃的黑黄撞色设计，拥有非常独特的机身手感。</p><p class=\"jsmargin\"><img alt=\"591A8237.jpg\" src=\"http://pic.zaeke.com/img161484008416237.jpeg\" width=\"2000\" height=\"1125\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"591A8286.jpg\" src=\"http://pic.zaeke.com/img161484008414665.jpeg\" width=\"2000\" height=\"1125\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">虽然是性价比机型，但realme GT依然采用了120Hz Super AMOLED电竞屏，这块屏幕的分辨率为2400×1080，触控采样率则高达360Hz。此外realme GT还拥有前后双环境光传感器，支持新一代光学屏下指纹，并且包含《王者荣耀》、《和平精英》在内的游戏也将随后适配90Hz模式、</p><p class=\"p_style\">在性能上，realme GT搭载高通骁龙888移动平台，并配备LPDDR5的RAM与UFS 3.1 ROM。对于一款性价比旗舰来说，关乎性能的硬件当然也不会出现缩水，毕竟这个定位机器砍啥也不会砍性能。</p><p class=\"p_style\">为了让骁龙888平台能够持续输出性能，realme GT还准备了3D液冷钢化散热。以铜钢复合内部结构搭配多层立体式石墨散热，并优化毛细散热结构，从而提升散热效率。并且在软件层面上，该机也拥有GT模式，开启后能够全面释放系统性能并全面开放120Hz高刷。</p><p class=\"jsmargin\"><img alt=\"591A8227.jpg\" src=\"http://pic.zaeke.com/img161484012959855.jpeg\" width=\"2000\" height=\"1125\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"591A8257.jpg\" src=\"http://pic.zaeke.com/img161484012970974.jpeg\" width=\"2000\" height=\"1125\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">而在相机部分，realme GT采用6400万像素主摄+800万像素超广角相机+200万像素微距相机的后置三摄模组，其中主摄为索尼IMX682感光元件，超广角相机则达到了119°视角，前置相机则为1600万像素。</p><p class=\"p_style\">并且realme GT还拥有新一代Pure RAW夜景算法也超级暗光视频2.0，能够在暗光场景下更好地拍摄出观感优异的静态/动态影像。而针对自拍需求，该机的前置相机也支持前置人像超级夜景、人像虚化+HDR自拍功能。</p><p class=\"jsmargin\"><img alt=\"591A8210.jpg\" src=\"http://pic.zaeke.com/img161484014197991.jpeg\" width=\"2000\" height=\"1125\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">在特色功能上，realme GT还拥有多功能NFC、Tactile Engine线性马达与双扬声器，虽然性价比机型的定位并不能让该机面面俱到，但能为体验带来明显感知的功能也确实相对全面。</p><p class=\"p_style\">此外，realme GT内置4500mAh电池，根据官方说法能够持续王者荣耀游戏5.6小时，并且其采用的65W智慧闪充在同价位也算非常出彩，35分钟即可充满全部电量，无需为充电进行过多等待。</p><p class=\"jsmargin\"><img alt=\"591A8295.jpg\" src=\"http://pic.zaeke.com/img161484017275197.jpeg\" width=\"2000\" height=\"1125\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">作为realme的全新旗舰系列，realme GT在保证旗舰级性能的同时，也实现了非常优秀的性价比。尽管正像前文所述那样，它并不能真正全方位看齐顶级旗舰，可也的确是一款极具竞争力的机型，也更是性价比用户的福音。</p><p class=\"p_style\">当然，realme GT并不是唯一一款售价3000内的骁龙888机型，市面上这场性价比旗舰之战似乎菜刚刚开始，只不过realme这回的确抢到了先机。</p><p class=\"p_style\"><b>更多美图：</b></p><p class=\"jsmargin\"><b><img alt=\"591A8207.jpg\" src=\"http://pic.zaeke.com/img161484020252681.jpeg\" width=\"2000\" height=\"1125\" style=\"margin: 0px auto; height: auto;\"><br></b></p><p class=\"jsmargin\"><b style=\"font-size: 16px;\"><img alt=\"591A8226.jpg\" src=\"http://pic.zaeke.com/img161484020213123.jpeg\" width=\"2000\" height=\"1125\" style=\"margin: 0px auto; height: auto;\"></b></p><p class=\"jsmargin\"><b style=\"font-size: 16px;\"><img alt=\"591A8282.jpg\" src=\"http://pic.zaeke.com/img161484021941308.jpeg\" width=\"2000\" height=\"1125\" style=\"margin: 0px auto; height: auto;\"></b></p><p class=\"jsmargin\"><b style=\"font-size: 16px;\"><b style=\"font-size: 16px;\"><img alt=\"591A8269.jpg\" src=\"http://pic.zaeke.com/img161484020285525.jpeg\" width=\"2000\" height=\"1125\" style=\"margin: 0px auto; height: auto;\"></b><br></b></p><p class=\"jsmargin\"><img alt=\"591A8297.jpg\" src=\"http://pic.zaeke.com/img161484022181624.jpeg\" width=\"2000\" height=\"1125\" style=\"margin: 0px auto; height: auto;\"></p>', 'http://pic.zaeke.com/img161527816298042.jpeg', 8101, 0, 0, NULL, '2021-03-03 22:46:00', '2021-03-27 07:03:18');
INSERT INTO `article` VALUES (16134, 'Redmi K40 系列评测：不缺懂得欣赏的人，只是缺货....', '对于坚持极致性价比的执念，Redmi称得上是坚决的行动派，而不是空想家。从正式宣布品牌独立那一刻起，Redmi犹如接过了小米手上的“接力棒”，在过去的两年时间里...', '[object Object]', '小啤', 170, 'http://pic.zaeke.com/user_1533281478_8.png', '<p>对于坚持极致性价比的执念，Redmi 称得上是坚决的行动派，而不是空想家。从正式宣布品牌独立那一刻起，Redmi 犹如接过了小米手上的“接力棒”，在过去的两年时间里，Redmi  给市场带来了不少高性价比的产品，包括定位旗舰的 K 系列。</p><p style=\"text-align: center;\"><strong>Redmi “狠”起来，连“自己人”都难以招架</strong></p><p>顾名思义，“性价比”是指商品的性能值与价格值比，Redmi K40 Pro 系列想要守住性价比之王的宝座，自然在硬件配置方面需要多下功夫。旗舰机型的赛道上领跑对手，那么一款旗舰级的 SoC 几乎是必不可少的，Redmi K40 Pro 与 Redmi K40 Pro+ 均搭载了高通最新的旗舰级骁龙 888 移动平台，而 Redmi K40 则搭载了骁龙 870 移动平台。</p><p><img src=\"http://pic.zaeke.com/img161492719075716.jpeg\" alt=\"591A8091.jpg\">  </p><p>除了 SoC 的差异以外，虽然 Redmi K40 Pro 系列和 Redmi K40 均为 LPDDR 5 和 UFS 3.1 存储规格组合，可是 Redmi K40 的内存规格与其它两款机型有着 900Mbps 的性能差距，分别为 6400Mbps 和 5500Mbps。也许更多人会好奇，Redmi K40 Pro 系列与 Redmi K40 之间的性能差距有多大，毕竟面对日常的系统流畅性，三款机型的表现都处于有过而无不及的水平。</p><p><img src=\"http://pic.zaeke.com/img161492731625843.gif\" alt=\"K40 PRO 原神跑分_副本.gif\">  </p><p>抛开账面数据和纯粹的跑分对比，在使用 Redmi K40 Pro 和  Redmi K40 两款机型上手一些具有代表性的大型游戏时，更能直观地感受到两款机型之间的性能差距，而且两款机型均为 8GB + 256GB 存储组合版本。以最近比较热门的《原神》为例，在同样画面调至极限画质、帧数设置为 60 帧，且室温约 22℃，无装载手机壳的情况下，经过 30 分钟以上的上手体验测试，Redmi K40 Pro 取得了 50.1 的平均游戏帧数；同样的游戏画面设置和测试环境下，Redmi K40 的《原神》平均帧数为 49.8 帧。</p><p>其实两款机型在《原神》游戏中的整体帧数差距很微弱，不过 Redmi K40 在开始前几分钟的帧数稳定性最佳，后续帧数波动越来越大，而 Redmi K40 Pro 的最稳定的区间反而是中间位置，开始和结束阶段的帧数波动比较显著。</p><p><img src=\"http://pic.zaeke.com/img161492732484241.gif\" alt=\"K40 PRO 和平精英_副本.gif\">  </p><p>除此， Redmi K40 Pro 在《和平精英》HDR 高清画质、极限帧数设置之下，经过 30 分钟的游戏测试，其帧数可以达到几乎满帧的平均帧数为 59.7 帧，基本感受不到游戏过程中的画面卡顿情况；同样的设置之下，Redmi K40  30 分钟的表现甚至略好一些，实际的游戏平均帧数为 59.9 帧。</p><p><img src=\"http://pic.zaeke.com/img161492734771935.jpeg\" alt=\"IMG_0684.jpg\">  </p><p>从上述两款游戏的实际体验可以发现，Redmi K40 Pro 和 Redmi K40 的游戏运行表现比较出色，达到旗舰级手机该有的水平，而 Redmi K40 作为一款起售价不足 2000 元的机型，它的综合性能表现实属让人惊喜，特别在《和平精英》测试环节中取得了最接近于满帧的游戏表现。</p><p><img src=\"http://pic.zaeke.com/img161492740270651.jpeg\" alt=\"591A8097.jpg\">  </p><p>除性能表现以外，在旗舰级机型普遍热衷于采用曲面屏的如今，Redmi K40 全系搭载的直面屏反而成为了一种返朴归真的卖点，先抛开成本顾虑，直面屏确实有着边缘误触率低、贴膜方便、无绿边等优势，可是不能否认曲面屏在使用全面屏手势的时候，其屏幕边缘手感更舒适，视觉效果上更出众一些。</p><p><img src=\"http://pic.zaeke.com/img161493076122679.jpeg\" alt=\"591A8060.jpg\">  </p><p>与此同时，由于 Redmi K40 系列屏幕均无 DC 调光功能，在较低屏幕亮度的情况下将自动进入 PWM 调光，这就意味着在弱光场景下用户长时间盯着屏幕时，用户的眼睛更容易产生疲劳感。暂不清楚 Redmi 后续是否可以通过软件升级的形式，可是按照小米手机系统软件部总监张国全回复相关网友的答复可以了解到，其主要原因在于 Redmi K40 的 DC 调光效果不够理想，不推出该功能是为了避免出现众口难调情况。</p><p><img src=\"http://pic.zaeke.com/img161492744731572.jpeg\" alt=\"IMG_0697.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161492744842563.jpeg\" alt=\"IMG_0700.jpg\">  </p><p>Redmi K40 系列最具争议的两个地方，莫过于其采用的电容式侧边指纹和塑料中框。让人比较疑惑的地方就在于，虽然 Redmi K40 搭载了 AMOLED 屏幕，可是该系列机型却没有选择主流的光学屏下指纹方案，取而代之的是部分 LCD 屏幕机型热衷于采用的电容式侧边指纹。</p><p>在实际的使用中，Redmi K40 系列搭载的电容式侧边指纹的解锁效率是要高于绝大部分采用光学屏下指纹解锁的机型，而且整体解锁成功率也较高。当然，对于像我这类习惯了使用屏幕指纹的用户而言，在使用 Redmi K40 的侧边指纹时，需要额外花费时间进行适应，而且考虑到该指纹按键并没有采用凹陷设计，导致手机在我口袋中遭遇了误触解锁，误拨打通讯录中的友人电话。另外，除了基础的侧边解锁解除方案以外，该机型也同样支持人脸解锁和蓝牙设备解锁等多种解锁方案。</p><p><img src=\"http://pic.zaeke.com/img161492750449285.jpeg\" alt=\"IMG_0701.jpg\">  </p><p>外观设计方面，Redmi K40 系列强调视觉上平衡有序，整体设计遵从对称美学。值得一提的是，该机型的前置主摄开孔直径非常小（2.76mm），一定程度上降低了开孔对于屏幕完整性的视觉观感。机身背面的后置三摄突起幅度明显，边缘容易堆积灰尘，而后盖则搭配了 AG 工艺玻璃。尽管并没有采用金属中框，虽然视觉上并没有给人一种廉价感，可触感上却存在着材质差异。</p><p><img src=\"http://pic.zaeke.com/img161492752953578.jpeg\" alt=\"IMG_0704.jpg\">  </p><p>在我看来，Redmi K40 系列最显著的优势其实是积极拥抱轻量化方向，即使 196 克的机身算不上非常轻巧，可是相比较于 Redmi K30 208 克的机身重量，两款机型之间有着 12 克的重量差，对于手机这种需要长时间手持的电子产品，机身越轻巧才能有效降低用户的手腕疲劳感。Redmi K40 系列轻巧的主要原因，也许是因为没有坚持采用金属中框。</p><p><img src=\"http://pic.zaeke.com/img161492762151449.jpeg\" alt=\"IMG_0694.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492763342367.jpeg\" alt=\"591A8071.jpg\">  </p><p>可贵的地方在于，即便 Redmi K40 系列机型变得更轻巧了，可是它仍搭载了一块 4520 毫安时电池，一定程度上给予手机相对满意的续航保障，只不过 Redmi K40 系列全系只配备了最高 33W 的有线快充，并且没有无线充电功能。虽然只配备了最高 33W 的有线快充，可是 Redmi K40 系列仅需 52 分钟即可把无电量状态充至 100% 电量，它的充电效率表现甚至略优于部分标称着更高功率的机型。只是，相比较于那些具备上百瓦的超级快充机型而言，Redmi K40 全系仅提供 33W 有线快充的搭配，似乎已经有点跟不上时代步伐了。</p><p><img src=\"http://pic.zaeke.com/img161492755033756.jpeg\" alt=\"IMG_0801.jpg\"></p><p>想要给手机一个周全的保护，那么手机保护壳自然是必不可少的。Redmi K40 系列机型虽然随机附带了透明硅胶保护壳，可是为了满足颜值控的爱美之心，Redmi 专门为 K40 系列量身推出了迷彩保护壳。</p><p><img src=\"http://pic.zaeke.com/img161492755184744.jpeg\" alt=\"IMG_0813.jpg\">  </p><p>迷彩保护壳提供了三个不同配色和图案设计，除了机身底部以外，其余部位均能有效包裹机身，整体保护性不错。手感方面，该手机壳摸起来有细腻的磨砂质感，可以起到一定的防滑效果，可毕竟是 PC 水贴材质，主要是兼顾轻薄和防滑性，并且由于按键位置做了镂空设计，因此外壳与机身电源按键之间会形成一种高度落差，一定程度降低了指纹误触的概率。迷彩保护壳的官方售价为 69 元/个，目前已开放销售。</p><p style=\"text-align: center;\"><strong>主摄表现，也许是 Redmi K40 系列机型的分水岭</strong></p><p>除了 SoC 以外，三款机型搭载的主摄也各有差异，Redmi K40 主摄传感器为索尼 IMX582，而 Redmi K40 Pro 和 Redmi K40 Pro+ 的主摄传感器分别为索尼 IMX686 和三星 HM2 传感器。显而易见，主摄的性能表现，成为了这三款机型在定位方面的分水岭，理论上售价越高的机型，主摄的综合表现越好。</p><p><img src=\"http://pic.zaeke.com/img161492758423945.jpeg\" alt=\"IMG_0690.jpg\">  </p><p>以 Redmi K40 Pro 为例，这款机型分别配备了 6400 万像素索尼 IMX686 传感器主摄 + 800 万像素 119° 超广角镜头 + 500 万像素长焦微距镜头三摄组合，其中主摄配备的索尼 IMX686 传感器在去年获得了不俗的口碑，包括去年的 Redmi K30 Pro 的主摄就是采用了这款传感器。</p><p><img src=\"http://pic.zaeke.com/img161492904593244.jpeg\" alt=\"IMG_20210228_213155.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161492904584243.jpeg\" alt=\"IMG_20210228_220032.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161492904585484.jpeg\" alt=\"IMG_20210301_153848.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161492905764492.jpeg\" alt=\"IMG_20210301_154038.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492913921100.jpeg\" alt=\"IMG_20210302_092930.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492907958879.jpeg\" alt=\"IMG_20210301_191105.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492926277724.jpeg\" alt=\"IMG_20210302_124654.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492938499555.jpeg\" alt=\"IMG_20210302_140503.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492934047601.jpeg\" alt=\"IMG_20210302_140748.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492944732676.jpeg\" alt=\"IMG_20210302_160414.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492975086236.jpeg\" alt=\"IMG_20210302_152129.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492945861425.jpeg\" alt=\"IMG_20210301_204233.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161493119890249.jpeg\" alt=\"IMG_20210301_203159.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492951926630.jpeg\" alt=\"IMG_20210301_204037.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492949631380.jpeg\" alt=\"IMG_20210301_204132.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492987721830.jpeg\" alt=\"IMG_20210301_203623.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161493124738248.jpeg\" alt=\"IMG_20210301_203538.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161493121979258.jpeg\" alt=\"IMG_20210301_211015.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492940635005.jpeg\" alt=\"IMG_20210301_185016.jpg\">  </p><p>从 Redmi K40 Pro 的主摄成像表现可以发现，得益于索尼 IMX686 自身的 1/1. 7 英寸底子，这款主摄的弱光场景下的表现比较出众，实测在昏暗的弱光环境之下，它也能拍摄出肉眼都无法观察的暗部细节，并且噪点控制在让我满意的范围之内，但也禁不住局部裁剪使用。</p><p>在光线充足的环境下，其画面表现就更不在话下了，凭借不俗的解析力，细节禁得住用户放大“数毛”。只是个人觉得 Redmi K40 Pro 的快门策略不够智能，有时候即便在光线充足的环境下，其快门时间仍然处于 1/100 秒，对于抓拍一些动态的小动物或者小孩子的时候，画面主体很容易出现模糊情况。</p><p><img src=\"http://pic.zaeke.com/img161493018971088.jpeg\" alt=\"IMG_20210302_192043.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161493004050232.jpeg\" alt=\"IMG_20210303_103624.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161493004041736.jpeg\" alt=\"IMG_20210303_103654.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161493007661353.jpeg\" alt=\"IMG_20210303_103526.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161493009837876.jpeg\" alt=\"IMG_20210302_093004.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161493014374378.jpeg\" alt=\"IMG_20210302_092958.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161493011850262.jpeg\" alt=\"IMG_20210302_195559.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161493014227595.jpeg\" alt=\"IMG_20210301_185657.jpg\">  </p><p>除了&nbsp;<span style=\"font-size: 16px;\">&nbsp;Redmi K40 Pro 的主摄以外，从上数这组的</span>&nbsp;Redmi K40 主摄样张可以看到，在光线充足的情况下两款机型的主摄成像差距并不大，经过仔细比对的情况之下，Redmi K40 搭载的索尼 IMX582 传感器在默认的 1200 万像素输出时，其解析力并不比 Redmi K40 Pro 的索尼 IMX686 逊色，甚至在部分样张的细节方面，还稍微占据一点小优势。只不过，索尼 IMX582 传感器在弱光环境下会稍微拉开一些差距表现，可是它的综合表现完全对得起要价。</p><p><img src=\"http://pic.zaeke.com/img161492982884148.jpeg\" alt=\"IMG_20210302_155639.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161493135590186.jpeg\" alt=\"IMG_20210302_155753.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492976481445.jpeg\" alt=\"IMG_20210302_155444.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492922665295.jpeg\" alt=\"IMG_20210302_091504.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161493142149583.jpeg\" alt=\"IMG_20210301_185024.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492959319773.jpeg\" alt=\"IMG_20210301_203048.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492962833460.jpeg\" alt=\"IMG_20210301_190150.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492968034627.jpeg\" alt=\"IMG_20210301_203205.jpg\">  </p><p>超广角镜头的表现就稍微逊色一些，在光线充足的情况下画面整体画质也能有所保证，可由于其只有 800 万像素，因此相对有限的有效像素，使它不适宜后期裁剪。不过 Redmi K40 系列搭载的这款超广角镜头的边缘畸变控制得比较出色，样张边缘的畸变情况改善显著。因为三颗摄像头均全局支持夜景模式，所以这颗超广角镜头在弱光环境下的成像效果有所提升，只是在光线非常昏暗的情况下，其成像效果还是无法让人满意，毕竟传感器尺寸就那么大。</p><p><img src=\"http://pic.zaeke.com/img161492901054401.jpeg\" alt=\"IMG_20210301_154321.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492897114910.jpeg\" alt=\"IMG_20210302_160124.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492893287468.jpeg\" alt=\"IMG_20210302_160344.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492891563166.jpeg\" alt=\"IMG_20210301_214800.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492890172984.jpeg\" alt=\"IMG_20210302_124720.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492883192837.jpeg\" alt=\"IMG_20210305_101504.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161492883267184.jpeg\" alt=\"IMG_20210305_101658.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161492883244087.jpeg\" alt=\"IMG_20210305_101744.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492881780858.jpeg\" alt=\"IMG_20210224_155145.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161492879071920.jpeg\" alt=\"IMG_20210303_104006.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161492879045774.jpeg\" alt=\"IMG_20210303_104112.jpg\">  </p><p>在 Redmi K30 机型上“出道”的长焦微距镜头，已经成为 Redmi K 系列标志性的优势功能。尝到甜头的 Redmi 为 K40 全系配备了这颗人气不俗的长焦微距相机，别小瞧了这颗只有 500 万像素的镜头，不夸张地说，平时自己看不到的一些物品细节，这颗微距镜头都可以清晰记录，它可以捕捉到人眼不易察觉到的微观世界。另外，我其实还挺喜欢这颗镜头的焦外虚化效果，光斑效果给人一种赏心悦目的感觉。</p><p style=\"text-align: center;\"><strong>喜迎升“杯”的 Redmi K40 系列，真正的加量还降价</strong></p><p>实际体验下来，实在很难想象 Redmi K40 系列是一款 1999 元起售的产品，因为它已经颠覆了我对于这个价位机型中可以带来的越级体验，骁龙 870 移动平台不俗的性能表现，作为新旗舰的骁龙 888 移动平台也没有带来太显著的性能优势，如果不过分追求弱光环境下的主摄拍摄能力，那么定价更低廉的 Redmi K40 无疑成为了最具竞争力的首推机型。</p><p><img src=\"http://pic.zaeke.com/img161492764968658.jpeg\" alt=\"IMG_0680.jpg\">  </p><p>考虑到 Redmi K40 系列的最低配和最顶配之间并没有拉开太大的体验差距，消费者在作出抉择的时候就变得更明确了，最显著的结果就是 Redmi K40 在某东的预定人数是 Redmi K40 Pro 的六倍以上，恰好证明了大众消费群体的眼睛是雪亮的，在可以少花钱的情况下，绝对不会多掏钱，这不就是大众消费群体最简单的快乐途径么。不过，Redmi K40 Pro 系列的起售价格也从上一代产品的 2999 元，降至 2799 元起售，这何尝不是一种意料之外的小惊喜。</p><p><img src=\"http://pic.zaeke.com/img161492766242385.jpeg\" alt=\"IMG_0676.jpg\">  </p><p>当然，定价策略决定了 Redmi K40 系列在某些方面需要作出取舍，例如采用侧边指纹、非金属中框、最高仅支持 33W 快充等，只不过 Redmi 在相对有限的成本限制之下，尽可能地把控“刀法”，确保用户在有限的预算下可以领略越级般体验。</p><p><img src=\"http://pic.zaeke.com/img161492836117573.jpeg\" alt=\"未标题-5.jpg\">  </p><p>在我看来，Redmi K40 系列既然站立在聚光灯之下，就难免会被消费者们“挑刺”。总而言之，懂得欣赏它的人，自然愿意为它买单，而有着更充沛预算的消费者，完全可以根据自己需求挑选规格更高的产品。如今，从 1999 元至 3699 元的价格区间 Redmi K40 系列都能有所顾及，而更高价位段则托付给兄弟品牌进行冲击，两个品牌之间的分工仍然是一如既往的清晰明了。</p><p>毋庸置疑，在有限的预算内，目前 Redmi K40 系列绝对是性能控的不二之选，做到了真正的加量还降价。其实，目前最纠结的并不是 Redmi K40 是否能带来旗舰级的使用体验，而是在一机难求的情况下，消费者如何在不加价的情况把它收入囊中。</p>', '<p class=\"p_style\">对于坚持极致性价比的执念，Redmi 称得上是坚决的行动派，而不是空想家。从正式宣布品牌独立那一刻起，Redmi 犹如接过了小米手上的“接力棒”，在过去的两年时间里，Redmi  给市场带来了不少高性价比的产品，包括定位旗舰的 K 系列。</p><p style=\"text-align: center;\" class=\"p_style\"><strong>Redmi “狠”起来，连“自己人”都难以招架</strong></p><p class=\"p_style\">顾名思义，“性价比”是指商品的性能值与价格值比，Redmi K40 Pro 系列想要守住性价比之王的宝座，自然在硬件配置方面需要多下功夫。旗舰机型的赛道上领跑对手，那么一款旗舰级的 SoC 几乎是必不可少的，Redmi K40 Pro 与 Redmi K40 Pro+ 均搭载了高通最新的旗舰级骁龙 888 移动平台，而 Redmi K40 则搭载了骁龙 870 移动平台。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492719075716.jpeg\" alt=\"591A8091.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">除了 SoC 的差异以外，虽然 Redmi K40 Pro 系列和 Redmi K40 均为 LPDDR 5 和 UFS 3.1 存储规格组合，可是 Redmi K40 的内存规格与其它两款机型有着 900Mbps 的性能差距，分别为 6400Mbps 和 5500Mbps。也许更多人会好奇，Redmi K40 Pro 系列与 Redmi K40 之间的性能差距有多大，毕竟面对日常的系统流畅性，三款机型的表现都处于有过而无不及的水平。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492731625843.gif\" alt=\"K40 PRO 原神跑分_副本.gif\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">抛开账面数据和纯粹的跑分对比，在使用 Redmi K40 Pro 和  Redmi K40 两款机型上手一些具有代表性的大型游戏时，更能直观地感受到两款机型之间的性能差距，而且两款机型均为 8GB + 256GB 存储组合版本。以最近比较热门的《原神》为例，在同样画面调至极限画质、帧数设置为 60 帧，且室温约 22℃，无装载手机壳的情况下，经过 30 分钟以上的上手体验测试，Redmi K40 Pro 取得了 50.1 的平均游戏帧数；同样的游戏画面设置和测试环境下，Redmi K40 的《原神》平均帧数为 49.8 帧。</p><p class=\"p_style\">其实两款机型在《原神》游戏中的整体帧数差距很微弱，不过 Redmi K40 在开始前几分钟的帧数稳定性最佳，后续帧数波动越来越大，而 Redmi K40 Pro 的最稳定的区间反而是中间位置，开始和结束阶段的帧数波动比较显著。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492732484241.gif\" alt=\"K40 PRO 和平精英_副本.gif\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">除此， Redmi K40 Pro 在《和平精英》HDR 高清画质、极限帧数设置之下，经过 30 分钟的游戏测试，其帧数可以达到几乎满帧的平均帧数为 59.7 帧，基本感受不到游戏过程中的画面卡顿情况；同样的设置之下，Redmi K40  30 分钟的表现甚至略好一些，实际的游戏平均帧数为 59.9 帧。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492734771935.jpeg\" alt=\"IMG_0684.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">从上述两款游戏的实际体验可以发现，Redmi K40 Pro 和 Redmi K40 的游戏运行表现比较出色，达到旗舰级手机该有的水平，而 Redmi K40 作为一款起售价不足 2000 元的机型，它的综合性能表现实属让人惊喜，特别在《和平精英》测试环节中取得了最接近于满帧的游戏表现。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492740270651.jpeg\" alt=\"591A8097.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">除性能表现以外，在旗舰级机型普遍热衷于采用曲面屏的如今，Redmi K40 全系搭载的直面屏反而成为了一种返朴归真的卖点，先抛开成本顾虑，直面屏确实有着边缘误触率低、贴膜方便、无绿边等优势，可是不能否认曲面屏在使用全面屏手势的时候，其屏幕边缘手感更舒适，视觉效果上更出众一些。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161493076122679.jpeg\" alt=\"591A8060.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">与此同时，由于 Redmi K40 系列屏幕均无 DC 调光功能，在较低屏幕亮度的情况下将自动进入 PWM 调光，这就意味着在弱光场景下用户长时间盯着屏幕时，用户的眼睛更容易产生疲劳感。暂不清楚 Redmi 后续是否可以通过软件升级的形式，可是按照小米手机系统软件部总监张国全回复相关网友的答复可以了解到，其主要原因在于 Redmi K40 的 DC 调光效果不够理想，不推出该功能是为了避免出现众口难调情况。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492744731572.jpeg\" alt=\"IMG_0697.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492744842563.jpeg\" alt=\"IMG_0700.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">Redmi K40 系列最具争议的两个地方，莫过于其采用的电容式侧边指纹和塑料中框。让人比较疑惑的地方就在于，虽然 Redmi K40 搭载了 AMOLED 屏幕，可是该系列机型却没有选择主流的光学屏下指纹方案，取而代之的是部分 LCD 屏幕机型热衷于采用的电容式侧边指纹。</p><p class=\"p_style\">在实际的使用中，Redmi K40 系列搭载的电容式侧边指纹的解锁效率是要高于绝大部分采用光学屏下指纹解锁的机型，而且整体解锁成功率也较高。当然，对于像我这类习惯了使用屏幕指纹的用户而言，在使用 Redmi K40 的侧边指纹时，需要额外花费时间进行适应，而且考虑到该指纹按键并没有采用凹陷设计，导致手机在我口袋中遭遇了误触解锁，误拨打通讯录中的友人电话。另外，除了基础的侧边解锁解除方案以外，该机型也同样支持人脸解锁和蓝牙设备解锁等多种解锁方案。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492750449285.jpeg\" alt=\"IMG_0701.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">外观设计方面，Redmi K40 系列强调视觉上平衡有序，整体设计遵从对称美学。值得一提的是，该机型的前置主摄开孔直径非常小（2.76mm），一定程度上降低了开孔对于屏幕完整性的视觉观感。机身背面的后置三摄突起幅度明显，边缘容易堆积灰尘，而后盖则搭配了 AG 工艺玻璃。尽管并没有采用金属中框，虽然视觉上并没有给人一种廉价感，可触感上却存在着材质差异。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492752953578.jpeg\" alt=\"IMG_0704.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">在我看来，Redmi K40 系列最显著的优势其实是积极拥抱轻量化方向，即使 196 克的机身算不上非常轻巧，可是相比较于 Redmi K30 208 克的机身重量，两款机型之间有着 12 克的重量差，对于手机这种需要长时间手持的电子产品，机身越轻巧才能有效降低用户的手腕疲劳感。Redmi K40 系列轻巧的主要原因，也许是因为没有坚持采用金属中框。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492762151449.jpeg\" alt=\"IMG_0694.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492763342367.jpeg\" alt=\"591A8071.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">可贵的地方在于，即便 Redmi K40 系列机型变得更轻巧了，可是它仍搭载了一块 4520 毫安时电池，一定程度上给予手机相对满意的续航保障，只不过 Redmi K40 系列全系只配备了最高 33W 的有线快充，并且没有无线充电功能。虽然只配备了最高 33W 的有线快充，可是 Redmi K40 系列仅需 52 分钟即可把无电量状态充至 100% 电量，它的充电效率表现甚至略优于部分标称着更高功率的机型。只是，相比较于那些具备上百瓦的超级快充机型而言，Redmi K40 全系仅提供 33W 有线快充的搭配，似乎已经有点跟不上时代步伐了。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492755033756.jpeg\" alt=\"IMG_0801.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\">想要给手机一个周全的保护，那么手机保护壳自然是必不可少的。Redmi K40 系列机型虽然随机附带了透明硅胶保护壳，可是为了满足颜值控的爱美之心，Redmi 专门为 K40 系列量身推出了迷彩保护壳。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492755184744.jpeg\" alt=\"IMG_0813.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">迷彩保护壳提供了三个不同配色和图案设计，除了机身底部以外，其余部位均能有效包裹机身，整体保护性不错。手感方面，该手机壳摸起来有细腻的磨砂质感，可以起到一定的防滑效果，可毕竟是 PC 水贴材质，主要是兼顾轻薄和防滑性，并且由于按键位置做了镂空设计，因此外壳与机身电源按键之间会形成一种高度落差，一定程度降低了指纹误触的概率。迷彩保护壳的官方售价为 69 元/个，目前已开放销售。</p><p style=\"text-align: center;\" class=\"p_style\"><strong>主摄表现，也许是 Redmi K40 系列机型的分水岭</strong></p><p class=\"p_style\">除了 SoC 以外，三款机型搭载的主摄也各有差异，Redmi K40 主摄传感器为索尼 IMX582，而 Redmi K40 Pro 和 Redmi K40 Pro+ 的主摄传感器分别为索尼 IMX686 和三星 HM2 传感器。显而易见，主摄的性能表现，成为了这三款机型在定位方面的分水岭，理论上售价越高的机型，主摄的综合表现越好。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492758423945.jpeg\" alt=\"IMG_0690.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">以 Redmi K40 Pro 为例，这款机型分别配备了 6400 万像素索尼 IMX686 传感器主摄 + 800 万像素 119° 超广角镜头 + 500 万像素长焦微距镜头三摄组合，其中主摄配备的索尼 IMX686 传感器在去年获得了不俗的口碑，包括去年的 Redmi K30 Pro 的主摄就是采用了这款传感器。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492904593244.jpeg\" alt=\"IMG_20210228_213155.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492904584243.jpeg\" alt=\"IMG_20210228_220032.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492904585484.jpeg\" alt=\"IMG_20210301_153848.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492905764492.jpeg\" alt=\"IMG_20210301_154038.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492913921100.jpeg\" alt=\"IMG_20210302_092930.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492907958879.jpeg\" alt=\"IMG_20210301_191105.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492926277724.jpeg\" alt=\"IMG_20210302_124654.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492938499555.jpeg\" alt=\"IMG_20210302_140503.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492934047601.jpeg\" alt=\"IMG_20210302_140748.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492944732676.jpeg\" alt=\"IMG_20210302_160414.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492975086236.jpeg\" alt=\"IMG_20210302_152129.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492945861425.jpeg\" alt=\"IMG_20210301_204233.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161493119890249.jpeg\" alt=\"IMG_20210301_203159.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492951926630.jpeg\" alt=\"IMG_20210301_204037.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492949631380.jpeg\" alt=\"IMG_20210301_204132.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492987721830.jpeg\" alt=\"IMG_20210301_203623.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161493124738248.jpeg\" alt=\"IMG_20210301_203538.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161493121979258.jpeg\" alt=\"IMG_20210301_211015.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492940635005.jpeg\" alt=\"IMG_20210301_185016.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">从 Redmi K40 Pro 的主摄成像表现可以发现，得益于索尼 IMX686 自身的 1/1. 7 英寸底子，这款主摄的弱光场景下的表现比较出众，实测在昏暗的弱光环境之下，它也能拍摄出肉眼都无法观察的暗部细节，并且噪点控制在让我满意的范围之内，但也禁不住局部裁剪使用。</p><p class=\"p_style\">在光线充足的环境下，其画面表现就更不在话下了，凭借不俗的解析力，细节禁得住用户放大“数毛”。只是个人觉得 Redmi K40 Pro 的快门策略不够智能，有时候即便在光线充足的环境下，其快门时间仍然处于 1/100 秒，对于抓拍一些动态的小动物或者小孩子的时候，画面主体很容易出现模糊情况。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161493018971088.jpeg\" alt=\"IMG_20210302_192043.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161493004050232.jpeg\" alt=\"IMG_20210303_103624.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161493004041736.jpeg\" alt=\"IMG_20210303_103654.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161493007661353.jpeg\" alt=\"IMG_20210303_103526.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161493009837876.jpeg\" alt=\"IMG_20210302_093004.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161493014374378.jpeg\" alt=\"IMG_20210302_092958.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161493011850262.jpeg\" alt=\"IMG_20210302_195559.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161493014227595.jpeg\" alt=\"IMG_20210301_185657.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">除了&nbsp;<span style=\"font-size: 16px;\">&nbsp;Redmi K40 Pro 的主摄以外，从上数这组的</span>&nbsp;Redmi K40 主摄样张可以看到，在光线充足的情况下两款机型的主摄成像差距并不大，经过仔细比对的情况之下，Redmi K40 搭载的索尼 IMX582 传感器在默认的 1200 万像素输出时，其解析力并不比 Redmi K40 Pro 的索尼 IMX686 逊色，甚至在部分样张的细节方面，还稍微占据一点小优势。只不过，索尼 IMX582 传感器在弱光环境下会稍微拉开一些差距表现，可是它的综合表现完全对得起要价。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492982884148.jpeg\" alt=\"IMG_20210302_155639.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161493135590186.jpeg\" alt=\"IMG_20210302_155753.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492976481445.jpeg\" alt=\"IMG_20210302_155444.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492922665295.jpeg\" alt=\"IMG_20210302_091504.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161493142149583.jpeg\" alt=\"IMG_20210301_185024.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492959319773.jpeg\" alt=\"IMG_20210301_203048.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492962833460.jpeg\" alt=\"IMG_20210301_190150.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492968034627.jpeg\" alt=\"IMG_20210301_203205.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">超广角镜头的表现就稍微逊色一些，在光线充足的情况下画面整体画质也能有所保证，可由于其只有 800 万像素，因此相对有限的有效像素，使它不适宜后期裁剪。不过 Redmi K40 系列搭载的这款超广角镜头的边缘畸变控制得比较出色，样张边缘的畸变情况改善显著。因为三颗摄像头均全局支持夜景模式，所以这颗超广角镜头在弱光环境下的成像效果有所提升，只是在光线非常昏暗的情况下，其成像效果还是无法让人满意，毕竟传感器尺寸就那么大。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492901054401.jpeg\" alt=\"IMG_20210301_154321.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492897114910.jpeg\" alt=\"IMG_20210302_160124.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492893287468.jpeg\" alt=\"IMG_20210302_160344.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492891563166.jpeg\" alt=\"IMG_20210301_214800.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492890172984.jpeg\" alt=\"IMG_20210302_124720.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492883192837.jpeg\" alt=\"IMG_20210305_101504.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492883267184.jpeg\" alt=\"IMG_20210305_101658.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492883244087.jpeg\" alt=\"IMG_20210305_101744.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492881780858.jpeg\" alt=\"IMG_20210224_155145.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492879071920.jpeg\" alt=\"IMG_20210303_104006.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492879045774.jpeg\" alt=\"IMG_20210303_104112.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">在 Redmi K30 机型上“出道”的长焦微距镜头，已经成为 Redmi K 系列标志性的优势功能。尝到甜头的 Redmi 为 K40 全系配备了这颗人气不俗的长焦微距相机，别小瞧了这颗只有 500 万像素的镜头，不夸张地说，平时自己看不到的一些物品细节，这颗微距镜头都可以清晰记录，它可以捕捉到人眼不易察觉到的微观世界。另外，我其实还挺喜欢这颗镜头的焦外虚化效果，光斑效果给人一种赏心悦目的感觉。</p><p style=\"text-align: center;\" class=\"p_style\"><strong>喜迎升“杯”的 Redmi K40 系列，真正的加量还降价</strong></p><p class=\"p_style\">实际体验下来，实在很难想象 Redmi K40 系列是一款 1999 元起售的产品，因为它已经颠覆了我对于这个价位机型中可以带来的越级体验，骁龙 870 移动平台不俗的性能表现，作为新旗舰的骁龙 888 移动平台也没有带来太显著的性能优势，如果不过分追求弱光环境下的主摄拍摄能力，那么定价更低廉的 Redmi K40 无疑成为了最具竞争力的首推机型。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492764968658.jpeg\" alt=\"IMG_0680.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">考虑到 Redmi K40 系列的最低配和最顶配之间并没有拉开太大的体验差距，消费者在作出抉择的时候就变得更明确了，最显著的结果就是 Redmi K40 在某东的预定人数是 Redmi K40 Pro 的六倍以上，恰好证明了大众消费群体的眼睛是雪亮的，在可以少花钱的情况下，绝对不会多掏钱，这不就是大众消费群体最简单的快乐途径么。不过，Redmi K40 Pro 系列的起售价格也从上一代产品的 2999 元，降至 2799 元起售，这何尝不是一种意料之外的小惊喜。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492766242385.jpeg\" alt=\"IMG_0676.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">当然，定价策略决定了 Redmi K40 系列在某些方面需要作出取舍，例如采用侧边指纹、非金属中框、最高仅支持 33W 快充等，只不过 Redmi 在相对有限的成本限制之下，尽可能地把控“刀法”，确保用户在有限的预算下可以领略越级般体验。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161492836117573.jpeg\" alt=\"未标题-5.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">在我看来，Redmi K40 系列既然站立在聚光灯之下，就难免会被消费者们“挑刺”。总而言之，懂得欣赏它的人，自然愿意为它买单，而有着更充沛预算的消费者，完全可以根据自己需求挑选规格更高的产品。如今，从 1999 元至 3699 元的价格区间 Redmi K40 系列都能有所顾及，而更高价位段则托付给兄弟品牌进行冲击，两个品牌之间的分工仍然是一如既往的清晰明了。</p><p class=\"p_style\">毋庸置疑，在有限的预算内，目前 Redmi K40 系列绝对是性能控的不二之选，做到了真正的加量还降价。其实，目前最纠结的并不是 Redmi K40 是否能带来旗舰级的使用体验，而是在一机难求的情况下，消费者如何在不加价的情况把它收入囊中。</p>', 'http://pic.zaeke.com/img161527818228639.jpeg', 13984, 0, 0, NULL, '2021-03-04 23:49:55', '2021-03-27 07:20:55');
INSERT INTO `article` VALUES (16135, '你没看错，这副耳机的名字就叫做“VxV”', '如果说把一对兔子耳朵作为品牌logo的FirAudio是当下最“俏皮”的高端耳机厂商，那么这副VxV（5×5）可以说他们最“皮”的一款产品——刚拿到它时我几乎怀...', '[object Object]', '斗室听澜', 189, 'http://pic.zaeke.com/user_1535960095png', '<p>如果说把一对兔子耳朵作为品牌logo的Fir Audio是当下最“俏皮”的高端耳机厂商，那么这副VxV（5×5）可以说他们最“皮”的一款产品——刚拿到它时我几乎怀疑品牌方是不是给我发错了货，6999块钱的耳机就装在这个巴掌大的白色瓦楞纸盒子里面，这真的不是什么周边配件的包装么？<br></p><p><img alt=\"DSC_5067.jpg\" src=\"http://pic.zaeke.com/img161504625334771.jpeg\" width=\"1400\" height=\"933\"><br></p><p>严格地说，我就算买一只百来块钱的转接头，包装规格都可能比这更好。但盒子上的标签清楚表明了VxV的本体就容纳其中，你只需要扯着线头撕开封签便能得到一只皮质收纳盒、五对耳塞套、一支清洁棒、若干贴纸以及耳机主体。三对单节套和一对记忆海绵套搭配VxV的音色我并不喜欢，反倒是那对极易沾灰、在海绵衬层中被挤压得变了形且无法恢复原形、工业气质与气味十足的双节套佩戴和声音都令我比较满意，后面的音质评价就基于它了。</p><p><img alt=\"DSC_5068.jpg\" src=\"http://pic.zaeke.com/img161504626428900.jpeg\" width=\"1400\" height=\"933\"><br></p><p><img alt=\"DSC_5069.jpg\" src=\"http://pic.zaeke.com/img161504627429619.jpeg\" width=\"1400\" height=\"933\"><br></p><p>VxV的本体造型与Fir Audio的M3/4/5公模一致，略短胖的身形与浑圆的尾端令其看起来很像地图app中的定位戳。老实说我不是很喜欢它的造型，但也没法否认这套姑且算得上久经考验的设计具备不错的佩戴体验。并且不同于M系列一整块光秃秃的印字面板，VxV的面板刻印了浅浮雕的兔耳和摆着“VxV”表情的兔头logo，令它看起来比那些更昂贵的同门产品活泼了许多。</p><p><img alt=\"DSC_5074.jpg\" src=\"http://pic.zaeke.com/img161504629413439.jpeg\" width=\"1400\" height=\"933\"><br></p><p>需要吐槽的是VxV的MMCX接口构造仍旧比较特殊，虽然能兼容一般的MMCX插针但母座会裸露一截在外面，着实不算美观。好在标配的这根铜镀银线品质尚可，柔软度也很好，2.5mm平衡插头方便转接其它接口——不过Fir Audio并没有随耳机送你转接头或转接线。尽管2.5mm平衡插头转接其它标准要比3.5mm单端或4.4mm平衡灵活很多，然而毋庸置疑，可换插头才是最方便且易维护的解决方案。</p><p><img alt=\"DSC_5079.jpg\" src=\"http://pic.zaeke.com/img161504630372785.jpeg\" width=\"1400\" height=\"933\"><br></p><p>作为一副售价6999元的耳机，VxV的外在规格可以说是粗糙简陋的。可说实话这并没有引起我的抵触，因为不同于GRADO那种典型的“纯粹美式敷衍”，VxV的粗线条风格显然是经过用心设计的，多看几眼甚至会觉得很“带感”——就像Minecraft玩家并不会觉得满是方块的游戏画面粗糙一样，VxV略有点微妙真空管朋克味儿的设计语言甚至会让我从兔头logo联想到Vault Boy上去。当然仅从实用性的角度看它也并非没有可取之处，比如非常紧凑的包装能节省运费和仓储空间（商家狂喜），一次性封签也能避免你买到七天无理由退货品（鸡老师狂怒）。</p><p><img alt=\"DSC_5010.jpg\" src=\"http://pic.zaeke.com/img161504632437402.jpeg\" width=\"1400\" height=\"933\"><br></p><p>所以VxV看起来的确很“皮”，但事实上还不仅仅局限于外在，它的声音风格也挺欢脱。我们都知道Fir Audio的创始人本是64Audio团队的核心人物，但不同于平衡稳重的Nio等64Audio现役主力产品，Fir Audio的公模耳塞声音风格大都是比较“美式”的，VxV也不例外。VxV是一副两端能量相对突出的耳塞，它拥有形体丰满、下潜优秀且密度扎实的低频，高频的亮度和线条感也足够抢眼。</p><p>但是不同于比较激烈、听感略冲的M5公模（这里强调公模的原因在于M5私模非但不冲还非常宽松鲜活，捎带手为高阶玩家们推荐一下M5私模），<b>VxV在风格上应属于“适度收敛的美式风情”，</b>总体而言VxV姑且还可以算是一只脚踩进了中正平衡的范畴，至少它没有明显干涩散碎发刺等影响耐听度的问题。而且即便我说了这是一副两端能量突出的耳机，可这并不代表VxV存在诸如IE800、榭兰图那样中频凹陷的情况，恰恰相反，VxV的人声距离很近结象也很饱满，只是受低频影响其男声的清晰度不算很出彩。</p><p><img alt=\"DSC_5142.jpg\" src=\"http://pic.zaeke.com/img161504635188998.jpeg\" width=\"1400\" height=\"933\"><br></p><p>这种面貌的音色如果能匹配上出色的动态表现那么对摇滚乐或金属乐都会有不错的表现力——事实也的确如此。<b>VxV是一副极其易于驱动的耳塞，</b>搭配SP2000我大概只需要55-65格音量就能得到自己习惯的声压，此时VxV展现出的动态和密度在6000-7000元价位上无疑是较为出色的。再加上它的调音本就颇为动感，VxV在演绎重视力量感表达的低频或高频乐器时都具有突出的优势。</p><p>VxV的这种特点令我联想到了另外两款产品：Campfire Audio Solaris（金色面板的旧太阳神）和达音科ZEN“禅”。旧太阳神和禅同样是这种两端能量略强但不过分的风格并且也都比较易于驱动，很容易达成优秀的动态和全频密度。</p><p><img alt=\"DSC_5144.jpg\" src=\"http://pic.zaeke.com/img161504637683957.jpeg\" width=\"1400\" height=\"933\"><br></p><p>坏消息是它们也拥有共同的短板：声场。这种能量取向还追求全频密度和大动态的耳机基本都要献祭声场表现，哪怕昂贵如Empire Ears Odin也难以免俗。它们的声场并不会给人以拥挤局促怪异的感觉，但规模是很难撑得特别开的，立体感也稍逊——其实对中频刻薄一点的话就能避免这个问题，可你喜欢听IE800吗？我反正是听不下去，鱼与熊掌难以得兼的时候我还是会选择正常一些的东西。</p><p><b>所以一定要追究VxV的硬伤，那就是它的声场的确算不上十足开阔立体，这限制了它对交响乐等曲目的发挥，</b>如果你对这方面要求较高，我更建议你加钱选择Softears Turii。除此之外，VxV对绝大多数的流行乐以及各个频段的器乐表现在我看来都是配得上6999元的价格的。不算太偏门的风格与良好的易驱动性也令你在选择前端时不需要劳心费神，预算不充裕的话一条不错的小尾巴也能推个七七八八，肯上旗舰或次旗舰级播放器当然也有不小的提升，也能略为弥补其短板。考虑到它就只有2.5mm插头，我比较建议你们选择KANN ALPHA或SE200等Astell&amp;Kern的中端型号。</p><p><img alt=\"DSC_5138.jpg\" src=\"http://pic.zaeke.com/img161504639439459.jpeg\" width=\"1400\" height=\"933\"><br></p><p>总而言之VxV真的是一款从头“皮”到脚的产品，它的包装规格在挑战你的接受度下限，它的音色也在健康范畴的边缘反复试探。不过好在它最终都算是没有越线，在市场可以接受的范围内最大限度地张扬了品牌个性。这样“皮中带稳”的产品路线恰恰需要谨慎拿捏和反复推敲，并非随性而为。</p><p>高大全、很“HiFi”的产品固然非常好，但正因为有VxV之流的存在市场才不会显得无聊，不是么？</p>', '<p class=\"p_style\">如果说把一对兔子耳朵作为品牌logo的Fir Audio是当下最“俏皮”的高端耳机厂商，那么这副VxV（5×5）可以说他们最“皮”的一款产品——刚拿到它时我几乎怀疑品牌方是不是给我发错了货，6999块钱的耳机就装在这个巴掌大的白色瓦楞纸盒子里面，这真的不是什么周边配件的包装么？<br></p><p class=\"jsmargin\"><img alt=\"DSC_5067.jpg\" src=\"http://pic.zaeke.com/img161504625334771.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">严格地说，我就算买一只百来块钱的转接头，包装规格都可能比这更好。但盒子上的标签清楚表明了VxV的本体就容纳其中，你只需要扯着线头撕开封签便能得到一只皮质收纳盒、五对耳塞套、一支清洁棒、若干贴纸以及耳机主体。三对单节套和一对记忆海绵套搭配VxV的音色我并不喜欢，反倒是那对极易沾灰、在海绵衬层中被挤压得变了形且无法恢复原形、工业气质与气味十足的双节套佩戴和声音都令我比较满意，后面的音质评价就基于它了。</p><p class=\"jsmargin\"><img alt=\"DSC_5068.jpg\" src=\"http://pic.zaeke.com/img161504626428900.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"DSC_5069.jpg\" src=\"http://pic.zaeke.com/img161504627429619.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">VxV的本体造型与Fir Audio的M3/4/5公模一致，略短胖的身形与浑圆的尾端令其看起来很像地图app中的定位戳。老实说我不是很喜欢它的造型，但也没法否认这套姑且算得上久经考验的设计具备不错的佩戴体验。并且不同于M系列一整块光秃秃的印字面板，VxV的面板刻印了浅浮雕的兔耳和摆着“VxV”表情的兔头logo，令它看起来比那些更昂贵的同门产品活泼了许多。</p><p class=\"jsmargin\"><img alt=\"DSC_5074.jpg\" src=\"http://pic.zaeke.com/img161504629413439.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">需要吐槽的是VxV的MMCX接口构造仍旧比较特殊，虽然能兼容一般的MMCX插针但母座会裸露一截在外面，着实不算美观。好在标配的这根铜镀银线品质尚可，柔软度也很好，2.5mm平衡插头方便转接其它接口——不过Fir Audio并没有随耳机送你转接头或转接线。尽管2.5mm平衡插头转接其它标准要比3.5mm单端或4.4mm平衡灵活很多，然而毋庸置疑，可换插头才是最方便且易维护的解决方案。</p><p class=\"jsmargin\"><img alt=\"DSC_5079.jpg\" src=\"http://pic.zaeke.com/img161504630372785.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">作为一副售价6999元的耳机，VxV的外在规格可以说是粗糙简陋的。可说实话这并没有引起我的抵触，因为不同于GRADO那种典型的“纯粹美式敷衍”，VxV的粗线条风格显然是经过用心设计的，多看几眼甚至会觉得很“带感”——就像Minecraft玩家并不会觉得满是方块的游戏画面粗糙一样，VxV略有点微妙真空管朋克味儿的设计语言甚至会让我从兔头logo联想到Vault Boy上去。当然仅从实用性的角度看它也并非没有可取之处，比如非常紧凑的包装能节省运费和仓储空间（商家狂喜），一次性封签也能避免你买到七天无理由退货品（鸡老师狂怒）。</p><p class=\"jsmargin\"><img alt=\"DSC_5010.jpg\" src=\"http://pic.zaeke.com/img161504632437402.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">所以VxV看起来的确很“皮”，但事实上还不仅仅局限于外在，它的声音风格也挺欢脱。我们都知道Fir Audio的创始人本是64Audio团队的核心人物，但不同于平衡稳重的Nio等64Audio现役主力产品，Fir Audio的公模耳塞声音风格大都是比较“美式”的，VxV也不例外。VxV是一副两端能量相对突出的耳塞，它拥有形体丰满、下潜优秀且密度扎实的低频，高频的亮度和线条感也足够抢眼。</p><p class=\"p_style\">但是不同于比较激烈、听感略冲的M5公模（这里强调公模的原因在于M5私模非但不冲还非常宽松鲜活，捎带手为高阶玩家们推荐一下M5私模），<b>VxV在风格上应属于“适度收敛的美式风情”，</b>总体而言VxV姑且还可以算是一只脚踩进了中正平衡的范畴，至少它没有明显干涩散碎发刺等影响耐听度的问题。而且即便我说了这是一副两端能量突出的耳机，可这并不代表VxV存在诸如IE800、榭兰图那样中频凹陷的情况，恰恰相反，VxV的人声距离很近结象也很饱满，只是受低频影响其男声的清晰度不算很出彩。</p><p class=\"jsmargin\"><img alt=\"DSC_5142.jpg\" src=\"http://pic.zaeke.com/img161504635188998.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">这种面貌的音色如果能匹配上出色的动态表现那么对摇滚乐或金属乐都会有不错的表现力——事实也的确如此。<b>VxV是一副极其易于驱动的耳塞，</b>搭配SP2000我大概只需要55-65格音量就能得到自己习惯的声压，此时VxV展现出的动态和密度在6000-7000元价位上无疑是较为出色的。再加上它的调音本就颇为动感，VxV在演绎重视力量感表达的低频或高频乐器时都具有突出的优势。</p><p class=\"p_style\">VxV的这种特点令我联想到了另外两款产品：Campfire Audio Solaris（金色面板的旧太阳神）和达音科ZEN“禅”。旧太阳神和禅同样是这种两端能量略强但不过分的风格并且也都比较易于驱动，很容易达成优秀的动态和全频密度。</p><p class=\"jsmargin\"><img alt=\"DSC_5144.jpg\" src=\"http://pic.zaeke.com/img161504637683957.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">坏消息是它们也拥有共同的短板：声场。这种能量取向还追求全频密度和大动态的耳机基本都要献祭声场表现，哪怕昂贵如Empire Ears Odin也难以免俗。它们的声场并不会给人以拥挤局促怪异的感觉，但规模是很难撑得特别开的，立体感也稍逊——其实对中频刻薄一点的话就能避免这个问题，可你喜欢听IE800吗？我反正是听不下去，鱼与熊掌难以得兼的时候我还是会选择正常一些的东西。</p><p class=\"p_style\"><b>所以一定要追究VxV的硬伤，那就是它的声场的确算不上十足开阔立体，这限制了它对交响乐等曲目的发挥，</b>如果你对这方面要求较高，我更建议你加钱选择Softears Turii。除此之外，VxV对绝大多数的流行乐以及各个频段的器乐表现在我看来都是配得上6999元的价格的。不算太偏门的风格与良好的易驱动性也令你在选择前端时不需要劳心费神，预算不充裕的话一条不错的小尾巴也能推个七七八八，肯上旗舰或次旗舰级播放器当然也有不小的提升，也能略为弥补其短板。考虑到它就只有2.5mm插头，我比较建议你们选择KANN ALPHA或SE200等Astell&amp;Kern的中端型号。</p><p class=\"jsmargin\"><img alt=\"DSC_5138.jpg\" src=\"http://pic.zaeke.com/img161504639439459.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">总而言之VxV真的是一款从头“皮”到脚的产品，它的包装规格在挑战你的接受度下限，它的音色也在健康范畴的边缘反复试探。不过好在它最终都算是没有越线，在市场可以接受的范围内最大限度地张扬了品牌个性。这样“皮中带稳”的产品路线恰恰需要谨慎拿捏和反复推敲，并非随性而为。</p><p class=\"p_style\">高大全、很“HiFi”的产品固然非常好，但正因为有VxV之流的存在市场才不会显得无聊，不是么？</p>', 'http://pic.zaeke.com/img161504622774366.jpeg', 947, 0, 0, NULL, '2021-03-06 08:01:05', '2021-03-26 10:38:33');
INSERT INTO `article` VALUES (16139, 'realme GT评测：新瓶老酒，不变的是极致性价比', '还记得当年旗舰配置真的只花1999元就能买到，可随着智能手机硬件成本的上涨与5G时代下的新一轮成本飙升，似乎没个四五千块钱都买不到能看的手机了？也不能这么说，即...', '[object Object]', '烫头孙师傅', 38, 'http://pic.zaeke.com/user_1533281477_5.png', '<p>还记得当年旗舰配置真的只花1999元就能买到，可随着智能手机硬件成本的上涨与5G时代下的新一轮成本飙升，似乎没个四五千块钱都买不到能看的手机了？  </p><p>也不能这么说，即便在旗舰移动平台上市初期价格实在压不下来，很多品牌也选择在其市场周期的末期打造一款高性价比机型。不过realme GT倒显得有些一反常态，毕竟这款机型在刚刚上市时就把起售价拉到了三千元以下。</p><p><img src=\"http://pic.zaeke.com/img161526051343980.jpeg\" alt=\"591A8232.jpg\">  </p><p>缘何如此？</p><p>答案当然是性价比。这过去这两年中，传统的线上品牌开始走高端路线与线下路线，而传统的线上品牌也开始通过子品牌蚕食线上性价比市场。realme GT显然是后者思路之下的产物，并且对于挑剔的线上用户来说，谁能把旗舰机卖得更便宜就会选择谁，因而这款真正意义上的性价比怪兽便出现了。</p><p style=\"text-align: center;\"><strong>便宜价格，一流性能</strong></p><p>2799元起售的价格就足以说明realme GT的性价比之高，这可是目前时间上售价最低的骁龙888机型之一，对于那些只求性能不求其它的性价比用户来说，光是这点就够说服他们掏钱了。</p><p><img src=\"http://pic.zaeke.com/img161526053767039.jpeg\" alt=\"591A8237.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161526050813715.jpeg\" alt=\"591A8226.jpg\">  </p><p>且别管这个“性价比怪兽”背后的操盘手到底是谁，先来看看硬件本身。realme GT采用的高通骁龙888移动屏+LPDDR5+3.1的强强联手，让我们又开始有了曾经那种便宜大碗的感觉。虽然在质感、做工、功能上比不过顶级旗舰，但起码使其在性能上足以和顶级旗舰掰掰手腕，总会让人觉得自己买赚了。</p><p>并且由于realme GT也算是市面上相对较早推出的骁龙888机型，市面上的选项还不那么丰富，再怎么说也有一定的先发优势。</p><p><img src=\"http://pic.zaeke.com/img161528669717203.jpeg\" alt=\"591A8250.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161528670254423.jpeg\" alt=\"591A8257.jpg\">  </p><p>虽说relame GT这个定位的机型在设计上通常没啥可说，不过妙就妙在该机推出了曙光战神特别版。这块特别版的后盖不仅采用了黑黄撞色，同时也兼具素皮与玻璃材质，并将二者合二为一。并且由于黄色素皮部分的面积相对较大，因此其手机也是那种防滑的类肤触感。  </p><p>对于一款性价比机型来说能做好设计确实难能可贵，毕竟这个外观这个和“性价比”没啥关系的事情上多下功夫的品牌确实不多。</p><p><img src=\"http://pic.zaeke.com/img161527755615084.jpeg\" alt=\"Screenshot_20210205_151455_副本.jpg\">  </p><p>所以我们还是先来看看realme GT的跑分成绩吧。</p><p>开启GT模式后，在GeekBench5下realme GT得到了939的单核分数和3300的多核分数；而在安兔兔上，在确保无后台且处理器为常温的情况下确可以跑出接近75万分的分数，即便是在使用后未清除后台且有一定发热的情况下测试也不低于70万分；而在3DMark的Wild Life Stress Test测试下，该机也获得了5696的最高循环分数。</p><p>这个跑分我觉得没啥毛病，对于一款旗舰性价比机型而言，realme GT在性能显然是没啥问题的，如果你属于希望用相对低的价格买到尽可能强的性能的用户，那么我觉得这款机型没啥问题。不过这也是realme的初衷嘛，性价比才是这类机型的核心竞争力。</p><p>作为性价比旗舰，realme GT显然没办法做到什么顶级拍照、高端质感之类的特性，只能让我们花尽可能少的钱买到骁龙888。那么既然没办法做到“软硬兼施”，起码“软”的这部分得做到位。</p><p><img src=\"http://pic.zaeke.com/img161526062340766.jpeg\" alt=\"591A8435.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161526062845745.jpeg\" alt=\"591A8436.jpg\">  </p><p>先说一个我最喜欢的功能，也就是微信的浮窗回复功能。事实上浮窗并不是什么新功能，目前市面上系统做得比较好的机型基本都有，形式和使用方式也大同小异，可realme GT在微信消息提示集成了“小窗回复”的选项，并且小窗本身也保留了独立的关闭按键。</p><p>或许有人会觉得“你们是不是实在没东西写了，连这都得专门拿出来说？”</p><p>其实真值得。要知道目前市面上不少小窗模式往往需要通过长按、下拉菜单或手势操作的形式开启，操作的直观度远不如消息提示中集成的、带文字说明按键直观高效。而小窗自带的关闭按钮也能够通过点按而非手势的方式关闭小窗，也明显更加高效。</p><p>尽管这套逻辑本质上是承袭自ColorOS 11，但论体验的高效易上手程度，仍然值得单独拎出来说说。要知道点按一定快过长按和手势，但清楚明晰的交互提示与对点按操作的合理保留，让如今的realme UI 2.0（包括ColorOS 11）在这类深度功能的易用性和使用效率上更高。哪怕我会手势操作也罢，多一个操作维度一样会带来便利性呀。</p><p><img src=\"http://pic.zaeke.com/img161526060684526.jpeg\" alt=\"591A8430.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161526063255138.jpeg\" alt=\"591A8439.jpg\">  </p><p>除了这些，realme GT还专门准备了一个GT模式。GT模式能够实现全场景的120Hz高刷新率与全场景的高性能模式。该模式实际上与一些游戏手机的高性能模式类似，只不过realme GT是少数在常规机型上搭载的“鸡血”模式。</p><p>先来说说帧率，GT模式的在帧率上的逻辑是必须要了解的，否则会出现“为什么我开了GT后却依然是60帧”的疑问。</p><p><img src=\"http://pic.zaeke.com/img161527755671402.jpeg\" alt=\"Screenshot_20210205_1514erter55_副本-2.jpg\">  </p><p>以realme GT的自带浏览器和微博为例，通过Perfdog测得：在未开启GT模式时，无论帧率设置在任何档位，在开启GT模式后设置中的模式都将强制调节为120Hz。而在开启GT模式之后，如在设置中调整为60Hz，则帧率最高位60；若开启GT模式后在设置中调节为智能刷新率，则软件恢复至GT模式开启前的最高帧率。</p><p>最后一点显然相对难以理解。举个例子：微博开开启GT模式前滑动时的帧率率已经是120fps，那么在开启GT模式后手动调节至智能刷新率时，依然保持开启前的最高帧率（120fps）。而自带浏览器在开启GT模式前的帧率为60，则即便在开启GT模式后已经变为120帧，若在开启GT模式的前提下调节智能刷新率，则会回复开启GT模式前的60fps帧数。</p><p>所以如果你希望GT模式能够始终保持120Hz，那么在开启GT模式后就不能够在设置中调节帧率设置，否则GT模式对于帧率的调节将形同虚设。而在关闭GT模式后，系统也将从强制开启120Hz刷新率的状态下自动切换回原本的刷新率模式。</p><p><img src=\"http://pic.zaeke.com/img161527755646603.jpeg\" alt=\"Screenshot_20210205_呃呃呃额为151455_副本-22_1.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161527755627589.jpeg\" alt=\"Screenshot_20210eweee205_151455_副本_1.jpg\">  </p><p>而在游戏方面，也必须要游戏本身支持输出120fps帧数才行，否则即便开启GT模式也依然将维持在游戏支持的最高帧数。</p><p>在开启GT模式之后，realme GT首先的表现是温控更加放得开了。即便在《和平精英》高清画质+极限帧率这种压力相对小的场景下，在一局游戏中未开启GT模式时的温度最高达到45℃，而开启GT模式后则为54℃。</p><p>为了了解这个问题，我们在通过CPU检测软件可看出，在开启GT模式之后，四颗能效核心的频率将会被强制锁定在不低于1305Mhz，并且超大核与大核的调度也会更加积极，这应该就是在GT模式开启之后即便未达到高负责也相对发热较高的原因。</p><p>总而言之，GT模式在开启之后的确能够带来更高的帧率与更积极的性能调度，只不过在帧率上也必须应用支持，而机身发热与功耗上也会显著提升。是否需要选择这个能够更充分压榨骁龙888性能的额模式，就纯看诸位选择了，实在不行也能加个散热背夹不是？</p><p><img src=\"http://pic.zaeke.com/img161526047570532.jpeg\" alt=\"591A8211.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161526064274634.jpeg\" alt=\"591A8442.jpg\">  </p><p>至于续航方面，我们用PCMark做了个场景未必那么典型的续航测试。在开启自动亮度与自适应刷新率、且并未打开GT模式的情况与夜间至早晨的测试环境下，该机在工作2.0电池使用寿命测试（运行至电量低于20%）中的成绩达到了15小时6分。</p><p>不过考虑到夜间关灯后屏幕亮度相对较低，这个测试未必具有典型意义，因此在这里仅作为诸位的参考，并不具备绝对严谨对比价值。</p><p>除了GT模式之外，这回realme GT也准备了一些非常有趣的功能。例如能够提升视频观感的01超感画质引擎，其中也包含了适配超清增强与适配色彩增强功能。并且该机采用的65W智慧闪充在目前也快充的第二梯队，官方表示只需35分钟即可充满全部电量。</p><p style=\"text-align: center;\"><strong>拍照？够用就好</strong>  </p><p>虽然是性价比旗舰，realme GT倒是也没过于克扣硬件，起码在拍照方面虽说其模组并不非常出彩，可考虑到价格与性价比之类的前提，这个模组倒也不糊显得太寒碜。</p><p><img src=\"http://pic.zaeke.com/img161526051078312.jpeg\" alt=\"591A8227.jpg\">  </p><p>从模组整体的实际画质表现上来看，我觉得realme依然处在“稳步上升”的状态。当然没必要把这句话理解成夸赞，毕竟realme在影像上的进步更像是佳能式的挤牙膏，每次都有明显提升却又提升不明显，而友商们在影像上却经常出现牙膏挤爆的短期快速进步。</p><p>我们也不能说realme不思进取，毕竟人家是真的进步了；可你要说realme进步很大吧又不至于，甚至因为友商们的进步神速显得有些不进则退。当然我能理解realme作为性价比品牌或许不需要把拍照放在第一位，但友商倒是有性价比机型虽硬件有限但观感极佳的例子，这点或许值得realme去学习。</p><p><img src=\"http://pic.zaeke.com/img161528083860441.jpeg\" alt=\"IMG20210301164213.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161528083850068.jpeg\" alt=\"IMG20210301164401.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161528087512882.jpeg\" alt=\"IMG20210304123018.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161528094511790.jpeg\" alt=\"IMG20210304161436.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161528120616724.jpeg\" alt=\"IMG20210304151934.jpg\">  </p><p style=\"text-align: center;\"><strong>主摄</strong></p><p><img src=\"http://pic.zaeke.com/img161528042758615.jpeg\" alt=\"IMG20210301162622.jpg\"></p><p><img src=\"http://pic.zaeke.com/img161528040583688.jpeg\" alt=\"IMG20210301162549.jpg\"><br></p><p><img src=\"http://pic.zaeke.com/img161528047614677.jpeg\" alt=\"IMG20210301163853.jpg\"><br><br></p><p><img src=\"http://pic.zaeke.com/img161528070841391.jpeg\" alt=\"IMG20210304152032.jpg\"></p><p style=\"text-align: center;\"><strong>超广角</strong></p><p>回到硬件本身上来，realme如今的算法涂抹感相对明显，这点对于realme GT来说也不意外。不过在细节涂抹感强的同时，realme GT依然保持着相对浓艳的风格，这点在好天气时未必都是优势，可在深圳这些个阴雨连绵的时间倒是能为灰暗的场景增色不少。</p><p>realme GT的相机在照度良好的室内场景下表现很不错，色彩浓郁抢眼，并且反差也相对合理，不会出现画面反差过小过平的情况。甚至在拍摄食物时，该机不错的白平衡表现与浓郁的色彩也会带来不错的效果。</p><p>只不过在面对光比相对明显的场景时，realme GT无论主摄还是超广角相机对暗部的处理都不算到位。尤其是边角处的暗部，会非常容易出现大颗粒的噪点与绿色的明显伪色，这点对于画面影像比较明显。并且市面上大多数同价位机型在类似场景下都能够较好地控制噪点与伪色，这点可以说说realme GT在拍照上最大的缺点了。</p><p><img src=\"http://pic.zaeke.com/img161526057257875.jpeg\" alt=\"591A8259.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161526060472853.jpeg\" alt=\"591A8303.jpg\">  </p><p>这并不是个老问题，事实上从realme首次尝试1/1.7英寸感光元件开始，暗部噪点与伪色的问题就是始终存在。当然之前的三星GW1感光元件随着调校不断在进步，可没想到的是采用索尼IMX682感光元件的realme在主摄上也会有这样的问题。这也正是前面所说的有进步但进步不大的问题，要知道一部分友商可是在首款同尺寸感光元件机型上，就拿出了非常成熟可靠的调校。</p><p>所以realme不是没进步，可惜进步实在不够明显。</p><p>所幸该机的相机在功能上也足够丰富，诸如证件照模式、高像素模式、多景视频与支持RAW格式输出的专业模式都没有缺席，我觉得没啥缺失的拍照功能倒是为realme GT还是挽回了不小的面子。</p><p style=\"text-align: center;\"><strong>存在就是正义</strong></p><p>作为一款常规类型的性价比旗舰，realme GT似乎在细节上稍微往游戏手机那边偏了些，可从整体的产品形态而言，这又是款彻头彻尾的常规机型——它只是知道你们都喜欢玩游戏，于是在这方面多下了些功夫罢了。</p><p><img src=\"http://pic.zaeke.com/img161526049832990.jpeg\" alt=\"591A8216.jpg\">  </p><p>作为性价比机型，“性能价格比”这个最基础的概念对于realme GT而言可以说至关重要，于是该机的确成为了市面上最便宜骁龙888机型之一。不过在这个基础上，realme GT在性能释放上也有足够的实力，完全能够向更高价的机型们在性能上发起挑战。</p><p>虽然这个定位的机型没法做到十全十美，例如拍照并不出彩，例如屏幕的全局最高亮度也不算凸出，可在这个价格下能够作为购机的重要选项之一，realme GT其实就已经成功了一大半。</p>', '<p class=\"p_style\">还记得当年旗舰配置真的只花1999元就能买到，可随着智能手机硬件成本的上涨与5G时代下的新一轮成本飙升，似乎没个四五千块钱都买不到能看的手机了？  </p><p class=\"p_style\">也不能这么说，即便在旗舰移动平台上市初期价格实在压不下来，很多品牌也选择在其市场周期的末期打造一款高性价比机型。不过realme GT倒显得有些一反常态，毕竟这款机型在刚刚上市时就把起售价拉到了三千元以下。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161526051343980.jpeg\" alt=\"591A8232.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">缘何如此？</p><p class=\"p_style\">答案当然是性价比。这过去这两年中，传统的线上品牌开始走高端路线与线下路线，而传统的线上品牌也开始通过子品牌蚕食线上性价比市场。realme GT显然是后者思路之下的产物，并且对于挑剔的线上用户来说，谁能把旗舰机卖得更便宜就会选择谁，因而这款真正意义上的性价比怪兽便出现了。</p><p style=\"text-align: center;\" class=\"p_style\"><strong>便宜价格，一流性能</strong></p><p class=\"p_style\">2799元起售的价格就足以说明realme GT的性价比之高，这可是目前时间上售价最低的骁龙888机型之一，对于那些只求性能不求其它的性价比用户来说，光是这点就够说服他们掏钱了。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161526053767039.jpeg\" alt=\"591A8237.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161526050813715.jpeg\" alt=\"591A8226.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">且别管这个“性价比怪兽”背后的操盘手到底是谁，先来看看硬件本身。realme GT采用的高通骁龙888移动屏+LPDDR5+3.1的强强联手，让我们又开始有了曾经那种便宜大碗的感觉。虽然在质感、做工、功能上比不过顶级旗舰，但起码使其在性能上足以和顶级旗舰掰掰手腕，总会让人觉得自己买赚了。</p><p class=\"p_style\">并且由于realme GT也算是市面上相对较早推出的骁龙888机型，市面上的选项还不那么丰富，再怎么说也有一定的先发优势。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161528669717203.jpeg\" alt=\"591A8250.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161528670254423.jpeg\" alt=\"591A8257.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">虽说relame GT这个定位的机型在设计上通常没啥可说，不过妙就妙在该机推出了曙光战神特别版。这块特别版的后盖不仅采用了黑黄撞色，同时也兼具素皮与玻璃材质，并将二者合二为一。并且由于黄色素皮部分的面积相对较大，因此其手机也是那种防滑的类肤触感。  </p><p class=\"p_style\">对于一款性价比机型来说能做好设计确实难能可贵，毕竟这个外观这个和“性价比”没啥关系的事情上多下功夫的品牌确实不多。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161527755615084.jpeg\" alt=\"Screenshot_20210205_151455_副本.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">所以我们还是先来看看realme GT的跑分成绩吧。</p><p class=\"p_style\">开启GT模式后，在GeekBench5下realme GT得到了939的单核分数和3300的多核分数；而在安兔兔上，在确保无后台且处理器为常温的情况下确可以跑出接近75万分的分数，即便是在使用后未清除后台且有一定发热的情况下测试也不低于70万分；而在3DMark的Wild Life Stress Test测试下，该机也获得了5696的最高循环分数。</p><p class=\"p_style\">这个跑分我觉得没啥毛病，对于一款旗舰性价比机型而言，realme GT在性能显然是没啥问题的，如果你属于希望用相对低的价格买到尽可能强的性能的用户，那么我觉得这款机型没啥问题。不过这也是realme的初衷嘛，性价比才是这类机型的核心竞争力。</p><p class=\"p_style\">作为性价比旗舰，realme GT显然没办法做到什么顶级拍照、高端质感之类的特性，只能让我们花尽可能少的钱买到骁龙888。那么既然没办法做到“软硬兼施”，起码“软”的这部分得做到位。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161526062340766.jpeg\" alt=\"591A8435.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161526062845745.jpeg\" alt=\"591A8436.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">先说一个我最喜欢的功能，也就是微信的浮窗回复功能。事实上浮窗并不是什么新功能，目前市面上系统做得比较好的机型基本都有，形式和使用方式也大同小异，可realme GT在微信消息提示集成了“小窗回复”的选项，并且小窗本身也保留了独立的关闭按键。</p><p class=\"p_style\">或许有人会觉得“你们是不是实在没东西写了，连这都得专门拿出来说？”</p><p class=\"p_style\">其实真值得。要知道目前市面上不少小窗模式往往需要通过长按、下拉菜单或手势操作的形式开启，操作的直观度远不如消息提示中集成的、带文字说明按键直观高效。而小窗自带的关闭按钮也能够通过点按而非手势的方式关闭小窗，也明显更加高效。</p><p class=\"p_style\">尽管这套逻辑本质上是承袭自ColorOS 11，但论体验的高效易上手程度，仍然值得单独拎出来说说。要知道点按一定快过长按和手势，但清楚明晰的交互提示与对点按操作的合理保留，让如今的realme UI 2.0（包括ColorOS 11）在这类深度功能的易用性和使用效率上更高。哪怕我会手势操作也罢，多一个操作维度一样会带来便利性呀。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161526060684526.jpeg\" alt=\"591A8430.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161526063255138.jpeg\" alt=\"591A8439.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">除了这些，realme GT还专门准备了一个GT模式。GT模式能够实现全场景的120Hz高刷新率与全场景的高性能模式。该模式实际上与一些游戏手机的高性能模式类似，只不过realme GT是少数在常规机型上搭载的“鸡血”模式。</p><p class=\"p_style\">先来说说帧率，GT模式的在帧率上的逻辑是必须要了解的，否则会出现“为什么我开了GT后却依然是60帧”的疑问。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161527755671402.jpeg\" alt=\"Screenshot_20210205_1514erter55_副本-2.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">以realme GT的自带浏览器和微博为例，通过Perfdog测得：在未开启GT模式时，无论帧率设置在任何档位，在开启GT模式后设置中的模式都将强制调节为120Hz。而在开启GT模式之后，如在设置中调整为60Hz，则帧率最高位60；若开启GT模式后在设置中调节为智能刷新率，则软件恢复至GT模式开启前的最高帧率。</p><p class=\"p_style\">最后一点显然相对难以理解。举个例子：微博开开启GT模式前滑动时的帧率率已经是120fps，那么在开启GT模式后手动调节至智能刷新率时，依然保持开启前的最高帧率（120fps）。而自带浏览器在开启GT模式前的帧率为60，则即便在开启GT模式后已经变为120帧，若在开启GT模式的前提下调节智能刷新率，则会回复开启GT模式前的60fps帧数。</p><p class=\"p_style\">所以如果你希望GT模式能够始终保持120Hz，那么在开启GT模式后就不能够在设置中调节帧率设置，否则GT模式对于帧率的调节将形同虚设。而在关闭GT模式后，系统也将从强制开启120Hz刷新率的状态下自动切换回原本的刷新率模式。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161527755646603.jpeg\" alt=\"Screenshot_20210205_呃呃呃额为151455_副本-22_1.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161527755627589.jpeg\" alt=\"Screenshot_20210eweee205_151455_副本_1.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">而在游戏方面，也必须要游戏本身支持输出120fps帧数才行，否则即便开启GT模式也依然将维持在游戏支持的最高帧数。</p><p class=\"p_style\">在开启GT模式之后，realme GT首先的表现是温控更加放得开了。即便在《和平精英》高清画质+极限帧率这种压力相对小的场景下，在一局游戏中未开启GT模式时的温度最高达到45℃，而开启GT模式后则为54℃。</p><p class=\"p_style\">为了了解这个问题，我们在通过CPU检测软件可看出，在开启GT模式之后，四颗能效核心的频率将会被强制锁定在不低于1305Mhz，并且超大核与大核的调度也会更加积极，这应该就是在GT模式开启之后即便未达到高负责也相对发热较高的原因。</p><p class=\"p_style\">总而言之，GT模式在开启之后的确能够带来更高的帧率与更积极的性能调度，只不过在帧率上也必须应用支持，而机身发热与功耗上也会显著提升。是否需要选择这个能够更充分压榨骁龙888性能的额模式，就纯看诸位选择了，实在不行也能加个散热背夹不是？</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161526047570532.jpeg\" alt=\"591A8211.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161526064274634.jpeg\" alt=\"591A8442.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">至于续航方面，我们用PCMark做了个场景未必那么典型的续航测试。在开启自动亮度与自适应刷新率、且并未打开GT模式的情况与夜间至早晨的测试环境下，该机在工作2.0电池使用寿命测试（运行至电量低于20%）中的成绩达到了15小时6分。</p><p class=\"p_style\">不过考虑到夜间关灯后屏幕亮度相对较低，这个测试未必具有典型意义，因此在这里仅作为诸位的参考，并不具备绝对严谨对比价值。</p><p class=\"p_style\">除了GT模式之外，这回realme GT也准备了一些非常有趣的功能。例如能够提升视频观感的01超感画质引擎，其中也包含了适配超清增强与适配色彩增强功能。并且该机采用的65W智慧闪充在目前也快充的第二梯队，官方表示只需35分钟即可充满全部电量。</p><p style=\"text-align: center;\" class=\"p_style\"><strong>拍照？够用就好</strong>  </p><p class=\"p_style\">虽然是性价比旗舰，realme GT倒是也没过于克扣硬件，起码在拍照方面虽说其模组并不非常出彩，可考虑到价格与性价比之类的前提，这个模组倒也不糊显得太寒碜。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161526051078312.jpeg\" alt=\"591A8227.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">从模组整体的实际画质表现上来看，我觉得realme依然处在“稳步上升”的状态。当然没必要把这句话理解成夸赞，毕竟realme在影像上的进步更像是佳能式的挤牙膏，每次都有明显提升却又提升不明显，而友商们在影像上却经常出现牙膏挤爆的短期快速进步。</p><p class=\"p_style\">我们也不能说realme不思进取，毕竟人家是真的进步了；可你要说realme进步很大吧又不至于，甚至因为友商们的进步神速显得有些不进则退。当然我能理解realme作为性价比品牌或许不需要把拍照放在第一位，但友商倒是有性价比机型虽硬件有限但观感极佳的例子，这点或许值得realme去学习。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161528083860441.jpeg\" alt=\"IMG20210301164213.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161528083850068.jpeg\" alt=\"IMG20210301164401.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161528087512882.jpeg\" alt=\"IMG20210304123018.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161528094511790.jpeg\" alt=\"IMG20210304161436.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161528120616724.jpeg\" alt=\"IMG20210304151934.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p style=\"text-align: center;\" class=\"p_style\"><strong>主摄</strong></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161528042758615.jpeg\" alt=\"IMG20210301162622.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161528040583688.jpeg\" alt=\"IMG20210301162549.jpg\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161528047614677.jpeg\" alt=\"IMG20210301163853.jpg\" style=\"margin: 0px auto; height: auto;\"><br><br></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161528070841391.jpeg\" alt=\"IMG20210304152032.jpg\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"p_style\"><strong>超广角</strong></p><p class=\"p_style\">回到硬件本身上来，realme如今的算法涂抹感相对明显，这点对于realme GT来说也不意外。不过在细节涂抹感强的同时，realme GT依然保持着相对浓艳的风格，这点在好天气时未必都是优势，可在深圳这些个阴雨连绵的时间倒是能为灰暗的场景增色不少。</p><p class=\"p_style\">realme GT的相机在照度良好的室内场景下表现很不错，色彩浓郁抢眼，并且反差也相对合理，不会出现画面反差过小过平的情况。甚至在拍摄食物时，该机不错的白平衡表现与浓郁的色彩也会带来不错的效果。</p><p class=\"p_style\">只不过在面对光比相对明显的场景时，realme GT无论主摄还是超广角相机对暗部的处理都不算到位。尤其是边角处的暗部，会非常容易出现大颗粒的噪点与绿色的明显伪色，这点对于画面影像比较明显。并且市面上大多数同价位机型在类似场景下都能够较好地控制噪点与伪色，这点可以说说realme GT在拍照上最大的缺点了。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161526057257875.jpeg\" alt=\"591A8259.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161526060472853.jpeg\" alt=\"591A8303.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">这并不是个老问题，事实上从realme首次尝试1/1.7英寸感光元件开始，暗部噪点与伪色的问题就是始终存在。当然之前的三星GW1感光元件随着调校不断在进步，可没想到的是采用索尼IMX682感光元件的realme在主摄上也会有这样的问题。这也正是前面所说的有进步但进步不大的问题，要知道一部分友商可是在首款同尺寸感光元件机型上，就拿出了非常成熟可靠的调校。</p><p class=\"p_style\">所以realme不是没进步，可惜进步实在不够明显。</p><p class=\"p_style\">所幸该机的相机在功能上也足够丰富，诸如证件照模式、高像素模式、多景视频与支持RAW格式输出的专业模式都没有缺席，我觉得没啥缺失的拍照功能倒是为realme GT还是挽回了不小的面子。</p><p style=\"text-align: center;\" class=\"p_style\"><strong>存在就是正义</strong></p><p class=\"p_style\">作为一款常规类型的性价比旗舰，realme GT似乎在细节上稍微往游戏手机那边偏了些，可从整体的产品形态而言，这又是款彻头彻尾的常规机型——它只是知道你们都喜欢玩游戏，于是在这方面多下了些功夫罢了。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161526049832990.jpeg\" alt=\"591A8216.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">作为性价比机型，“性能价格比”这个最基础的概念对于realme GT而言可以说至关重要，于是该机的确成为了市面上最便宜骁龙888机型之一。不过在这个基础上，realme GT在性能释放上也有足够的实力，完全能够向更高价的机型们在性能上发起挑战。</p><p class=\"p_style\">虽然这个定位的机型没法做到十全十美，例如拍照并不出彩，例如屏幕的全局最高亮度也不算凸出，可在这个价格下能够作为购机的重要选项之一，realme GT其实就已经成功了一大半。</p>', 'http://pic.zaeke.com/img161527821198819.jpeg', 7797, 0, 0, NULL, '2021-03-09 00:25:23', '2021-03-27 08:06:04');
INSERT INTO `article` VALUES (16140, 'Razer Viper 8K Hz 评测：摸不着的“光速”', '雷蛇毒蝰8KHz游戏鼠标的回报率达到8000Hz，这是一个什么概念？目前主流或者更应该说是绝大多数的游戏鼠标，无论是有线还是无线，1000Hz回报率已经是标配。...', '[object Object]', '冯伟文', 19, 'http://pic.zaeke.com/user_1610094930png', '<p style=\"text-align: center;\"><img alt=\"DSC03852.jpg\" src=\"http://pic.zaeke.com/img161528702195002.jpeg\" width=\"1400\" height=\"933\"><br></p><p>雷蛇毒蝰 8KHz 游戏鼠标的回报率达到 8000Hz，这是一个什么概念？目前主流或者更应该说是绝大多数的游戏鼠标，无论是有线还是无线，1000Hz 回报率已经是标配。理论延迟为 1ms，这足够满足日常使用以及游戏的需要。</p><p>所以 8000Hz 回报率就是翻了八倍，理论延迟更是进一步降低至 0.125ms，这可以说是超出了感知的范围。所以问题也就来了，这么高的回报率有什么问题么？如此低的延迟有什么用？这样的鼠标能够让我更容易吃鸡么？</p><p><br></p><p>在回答这些问题之前，倒是要说清楚一个大前提，那就是追求更低的延迟是所有厂商都在也都要努力的。因为低延迟是一整个系统工程，是一整个链路都需要打通的工作，在 Razer HyperPolling 技术这支视频里面，就有相当明确的说明。那就是鼠标点击再到画面显示，需要鼠标、处理器、显卡、显示器等几个环节，这里的每个环节都与延迟有关。</p><p>要是有一定年头的玩家肯定知道，许多年前的鼠标回报率达到 1000Hz 可是一个卖点，是游戏鼠标才会有的待遇。在往后的这几年甚至可以说是十几年，随着游戏玩家越来越多，各方面的要求也就更高，于是高性能传感器、微动、显示器等等，便一山还比一山高。特别容易看到的，那就是显示器的刷新率越来越高，从曾经的 120Hz 就够到现在的 240Hz 才够好。</p><p><img alt=\"62482243gy1go7y48e7caj20u00vkttv.jpg\" src=\"http://pic.zaeke.com/img161528706797150.jpeg\" width=\"1080\" height=\"1136\"><br></p><p>还有现在出场率是越来越高的光学微动，本意是为了解决双击和使用寿命的问题，可同时降低了点击延迟以及提高了响应速度。所以并不难理解，雷蛇的 Razer HyperPolling 技术并不是换用支持 8000Hz 回报率的 USB 控制器就行，还包含自家的光学微动在内。并且这一只游戏鼠标对于玩家的硬件配置的要求可不低，推荐使用：</p><p>处理器：Inte Core i5-8600K 或 AMD R5 3600；</p><p>显卡：NVIDIA GTX 1080 或 AMD RX5700；</p><p>显示器：144Hz 刷新率，关闭自适应同步（G-Sync 或 FreeSync）；&nbsp;</p><p>这说明了，正是有着更高刷新率的显示器、更快的光学微动以及更强的硬件性能这些条件之后，高回报率的游戏鼠标才有用武之地，也是补上链路中的一环。</p><p style=\"text-align: center;\"><img alt=\"微信图片_20210309002455.png\" src=\"http://pic.zaeke.com/img161528709142776.png\" width=\"854\" height=\"619\"><br></p><p>说到硬件配置，有一定年头的玩家应该回想起来当年鼠标的回报率也是有争议的，那就是越高回报率会占用更多的硬件资源，这在当时确实是需要取舍的。那么在经过这么多年后，硬件配置的性能提升，回报率也翻了八倍，这还需要取舍么？</p><p>实际测试以我的配置：AMD R7-5900X/NVIDIA RTX 2070 Super/120Hz。关闭前后台应用后，分别观察鼠标在静止、运动时的任务管理器占用比，毒蝰终极版（8000Hz）的变化大概是从静止时的 3~7%，波动到 10% 左右。</p><p>显然高回报率确实需要占用更多的系统资源，只不过这会儿看来影响并不是特别大。在考虑到目前吃处理器的游戏并不算多，这个比例在实际游戏时应该不打紧。总而言之，只要不是上古配置，这没啥好担心的。</p><p style=\"text-align: center;\"><img alt=\"62482243gy1go7y3f2kf9j212n0qetfx.jpg\" src=\"http://pic.zaeke.com/img161528707953197.jpeg\" width=\"1391\" height=\"950\"><br></p><p>与显示器有关的两个要求，也就是高刷新率与关闭自适应同步，这可以说是相辅相成的。雷蛇表示后者是假设玩家的配置足够高，那么实际游戏帧率就会比显示器的刷新率更高，那么用毒蝰 8K Hz 版的收益就要比锁帧的更好。同样道理，显示器的刷新率更高，收益也就更为明显。</p><p>这从理论上来说是成立的，其实就是显示器的每一帧与鼠标的回报率匹配的延迟会更低。然而受限于财力的缘故，我就没啥条件测试的了，毕竟刷新率到 240、360Hz 的显示器还是高贵了些......</p><p style=\"text-align: center;\"><img alt=\"微信图片_20210306230504.png\" src=\"http://pic.zaeke.com/img161528709811414.png\" width=\"1451\" height=\"1430\"><br></p><p>讲了这么多有的没的前提、条件啥的，还是回到雷蛇毒蝰 8K Hz 这只鼠标来。首先要确认的第一件事情，那就是 8000Hz 回报率是否真实存在。</p><p>这个说白了就是用各种软件进行测试，雷蛇自己也想到这件事情，所以发布鼠标的同时还发布了个本地的回报率测试软件。这个软件需要切换至英语输入法，点击 M 进行设置，N 键开始测试，然后进行快速运动，便会有实时回报率以及折线图。</p><p>实际测试毒蝰 8K Hz 在快速圆周运动时，回报率很轻松就可以达到 8000Hz 左右，（实际是 799X Hz 波动）。作为对比，我还测试了毒蝰终极版、罗技 G Pro X Superlight、海盗船&nbsp;Dark Core Pro SE 等，在同样情况下的回报率可达 1000Hz，（实际是 99X Hz 波动）</p><p style=\"text-align: center;\"><img alt=\"1615046819(1).png\" src=\"http://pic.zaeke.com/img161528711098240.png\" width=\"1071\" height=\"582\"><br></p><p>然后是用 MouseTest V1.5.3 做进一步测试，毒蝰 8K Hz 的回报率可以看到是相当稳定地在 0.5ms 以内波动。放大看基本是在 0.3~0.1ms 之间波动，这可以判作为 8000Hz 回报率的正常情况。</p><p>好了，既然毒蝰 8K Hz 的回报率确实是能够达到 8000Hz，并且表现相当稳定。那么就意味着其相比起常规鼠标的 1000Hz 回报率有着数倍的提升，那么毒蝰 8K Hz 使用起来是否也会有同样的倍数提升？或者问的更直接点，这样的高回报率真的有用么？</p><p style=\"text-align: center;\"><img alt=\"1615047161(1).png\" src=\"http://pic.zaeke.com/img161528713280074.png\" width=\"1654\" height=\"710\"></p><p style=\"text-align: center;\"><img alt=\"1615047192(1).png\" src=\"http://pic.zaeke.com/img161528713197965.png\" width=\"1632\" height=\"692\"><br></p><p><b>这也是我对毒蝰 8K Hz 的第一个评价，那就是超高回报率确实是有所帮助，并且是实实在在的性能提升</b>。</p><p>这要理解起来很简单，回报率就是鼠标在 1 秒以内报告了多少次自己的位置，也就是打了多少个点，将这些点连接起来就是鼠标的移动轨迹。更高的回报率就意味着更多的点，鼠标的移动轨迹就更真实、顺畅。所以同样是快速画圆，可以看到毒蝰 8K Hz 相比毒蝰终极版的点要更为密集，并且没有疏密程度不均的情况。</p><p>说的再简单直接点，那就是相比目前市面上的所有鼠标，毒蝰 8K Hz 能让你更接近“人鼠合一”的状态。</p><p style=\"text-align: center;\"><img alt=\"DSC04004.jpg\" src=\"http://pic.zaeke.com/img161528715517955.jpeg\" width=\"1400\" height=\"935\"><br></p><p>既然毒蝰 8K Hz 有着如此明显的性能提升，那么换了这只鼠标就可以变神枪手咯？答案当然......不是。<b>这也是我对毒蝰 8K Hz 的第二个评价，那就是超高回报率的感知不强</b>。</p><p>确实更高的回报率有更低的延迟，毒蝰 8K Hz 的理论最低延迟只有 0.125ms，即便是实际情况也能够控制在 0.3ms 以内。这相比起目前主流的 1000Hz 回报率的 1ms，可是有数倍的提升。然而这样的性能提升反应到实际使用或者说是实际手感里，变化程度并不大，甚至可以说是感知不明显。</p><p>这其实就是边际效应，毕竟 1000Hz 回报率也就是 1ms 延迟已经足够满足大多数人的使用需要，甚至是超出大多数人的认知。那么进一步到 2000Hz、4000Hz 甚至是 8000Hz，这样越来越低的延迟对于使用者来说反而是越来越难以感觉到。</p><p style=\"text-align: center;\"><img alt=\"DSC04012.jpg\" src=\"http://pic.zaeke.com/img161528716769837.jpeg\" width=\"935\" height=\"1400\"><br></p><p>可是难以感知并不能够极端地认为没用或者是噱头。以我个人使用感受来说，在游戏的过程中从毒蝰终极版换成毒蝰 8K Hz，会有「变顺滑了？」的感觉。并且在 PUBG 压枪、跟枪、开镜瞄准、快速切换目标时，都会有似乎更手的感觉。</p><p>要是玩守望先锋、APEX 这样更快节奏的 FPS 类型游戏，那么感觉应该会更明显，收益也更好。然而我必须得说这样的感觉并不算明显，若不是快速切换毒蝰 8K Hz 与终极版进行对比，以及特意去关注移动时的手感，那么这点感知可能并不会让人觉察到，甚至会认为是不是个错觉。</p><p>所以指望之前的枪法因为换用毒蝰 8K Hz 而有着明显提升，那么应该会铩羽而归。</p><p style=\"text-align: center;\"><img alt=\"DSC03859.jpg\" src=\"http://pic.zaeke.com/img161528724358970.jpeg\" width=\"1400\" height=\"933\"></p><p style=\"text-align: center;\"><img alt=\"DSC03862.jpg\" src=\"http://pic.zaeke.com/img161528724399497.jpeg\" width=\"1400\" height=\"933\"><br></p><p><b>然而现阶段的毒蝰 8K Hz 是相当值得选购的，因为雷蛇在提高回报率的同时，还给这只鼠标塞进去了更高的配置以及更好的按键手感。</b><br></p><p>相比起元祖版毒蝰，也就是 69g 重的有线版本，雷蛇给毒蝰 8K Hz 换用了 100% 的 PTFE 材质脚贴，传感器升级为 PAW3399，光学微动升级为第二代，重量略微增加至 71g。其实毒蝰 8K Hz 就是毒蝰终极版的有线高回报率版本，对于相当部分玩家/蛇迷来说是期待已久的了，<strike>那么毒蝰迷你版是不是也可以......</strike></p><p><strike><img alt=\"DSC04006.jpg\" src=\"http://pic.zaeke.com/img161528728136818.jpeg\" width=\"935\" height=\"1400\"><br></strike></p><p>更关键的是左右键的手感。首先是微动，毒蝰 8K Hz 所采用的雷蛇第二代光学微动并没有啥技术变化，依然是光触发原理和 7000 万次寿命。有所不同的是手感相比第一代要硬朗些，确认感更明确些，这也是相当部分玩家/蛇迷来说是期待已久的。</p><p>显然雷蛇还对按键结构做了些调整，毒蝰 8K Hz 的左右键几乎没有啥晃动，空键程更是不存在。这样的改进甚至不需要拆解验证，将鼠标摇一摇就知道了。拿到耳边摇晃听声音，毒蝰终极版（黑）的声音是哗啦哗啦的，毒蝰终极版（白/赛博朋克版）与毒蝰 8K Hz 的声响要小多咯。</p><p style=\"text-align: center;\"><img alt=\"DSC03852.jpg\" src=\"http://pic.zaeke.com/img161528730440358.jpeg\" width=\"1400\" height=\"933\"><br></p><p>可以说雷蛇毒蝰 8K Hz 是一只相当有趣的游戏鼠标。其最大的亮点也是最吸引人的，绝对是那高达 8000Hz 的超高回报率。这在账面相较主流游戏鼠标有着八倍的性能提升，堪称是碾压优势。</p><p>然而在实际使用时，并不能够给使用者带来同样倍数级的感知。更是对显示器、硬件配置等方面的高要求，还有适用游戏类型等方面的条件，其实这只鼠标的使用门槛并不低，或者说现阶段并不能够发挥出最好的效果。</p><p>然而毒蝰 8K Hz 并不是只有超高回报率，同步升级的脚垫材质、传感器性能、微动手感以及按键结构，这可以说是雷蛇当下的最高杰作，也是雷蛇最强的有线鼠标。以此基础，再来看 8000Hz 回报率所带来的那么些细微的手感提升，可就是锦上添花了。</p><p>PS.1.实际使用发现毒蝰 8K Hz 使用不同的 USB 接口时，可能会出现 8000Hz 回报率上不去的情况，建议搭配雷蛇的本地测试软件一同使用，以确保运行在高回报率；</p><p>PS.2.雷蛇表示高回报率可能会与部分游戏出现兼容性问题，并且将会与各大游戏厂商合作解决这可能出现的问题。但并没有描述具体情况，我仅使用了在 PUBG 中，并没有感觉到异常；​​​​</p>', '<p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"DSC03852.jpg\" src=\"http://pic.zaeke.com/img161528702195002.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">雷蛇毒蝰 8KHz 游戏鼠标的回报率达到 8000Hz，这是一个什么概念？目前主流或者更应该说是绝大多数的游戏鼠标，无论是有线还是无线，1000Hz 回报率已经是标配。理论延迟为 1ms，这足够满足日常使用以及游戏的需要。</p><p class=\"p_style\">所以 8000Hz 回报率就是翻了八倍，理论延迟更是进一步降低至 0.125ms，这可以说是超出了感知的范围。所以问题也就来了，这么高的回报率有什么问题么？如此低的延迟有什么用？这样的鼠标能够让我更容易吃鸡么？</p><p class=\"p_style\"><br></p><p class=\"p_style\">在回答这些问题之前，倒是要说清楚一个大前提，那就是追求更低的延迟是所有厂商都在也都要努力的。因为低延迟是一整个系统工程，是一整个链路都需要打通的工作，在 Razer HyperPolling 技术这支视频里面，就有相当明确的说明。那就是鼠标点击再到画面显示，需要鼠标、处理器、显卡、显示器等几个环节，这里的每个环节都与延迟有关。</p><p class=\"p_style\">要是有一定年头的玩家肯定知道，许多年前的鼠标回报率达到 1000Hz 可是一个卖点，是游戏鼠标才会有的待遇。在往后的这几年甚至可以说是十几年，随着游戏玩家越来越多，各方面的要求也就更高，于是高性能传感器、微动、显示器等等，便一山还比一山高。特别容易看到的，那就是显示器的刷新率越来越高，从曾经的 120Hz 就够到现在的 240Hz 才够好。</p><p class=\"jsmargin\"><img alt=\"62482243gy1go7y48e7caj20u00vkttv.jpg\" src=\"http://pic.zaeke.com/img161528706797150.jpeg\" width=\"1080\" height=\"1136\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">还有现在出场率是越来越高的光学微动，本意是为了解决双击和使用寿命的问题，可同时降低了点击延迟以及提高了响应速度。所以并不难理解，雷蛇的 Razer HyperPolling 技术并不是换用支持 8000Hz 回报率的 USB 控制器就行，还包含自家的光学微动在内。并且这一只游戏鼠标对于玩家的硬件配置的要求可不低，推荐使用：</p><p class=\"p_style\">处理器：Inte Core i5-8600K 或 AMD R5 3600；</p><p class=\"p_style\">显卡：NVIDIA GTX 1080 或 AMD RX5700；</p><p class=\"p_style\">显示器：144Hz 刷新率，关闭自适应同步（G-Sync 或 FreeSync）；&nbsp;</p><p class=\"p_style\">这说明了，正是有着更高刷新率的显示器、更快的光学微动以及更强的硬件性能这些条件之后，高回报率的游戏鼠标才有用武之地，也是补上链路中的一环。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"微信图片_20210309002455.png\" src=\"http://pic.zaeke.com/img161528709142776.png\" width=\"854\" height=\"619\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">说到硬件配置，有一定年头的玩家应该回想起来当年鼠标的回报率也是有争议的，那就是越高回报率会占用更多的硬件资源，这在当时确实是需要取舍的。那么在经过这么多年后，硬件配置的性能提升，回报率也翻了八倍，这还需要取舍么？</p><p class=\"p_style\">实际测试以我的配置：AMD R7-5900X/NVIDIA RTX 2070 Super/120Hz。关闭前后台应用后，分别观察鼠标在静止、运动时的任务管理器占用比，毒蝰终极版（8000Hz）的变化大概是从静止时的 3~7%，波动到 10% 左右。</p><p class=\"p_style\">显然高回报率确实需要占用更多的系统资源，只不过这会儿看来影响并不是特别大。在考虑到目前吃处理器的游戏并不算多，这个比例在实际游戏时应该不打紧。总而言之，只要不是上古配置，这没啥好担心的。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"62482243gy1go7y3f2kf9j212n0qetfx.jpg\" src=\"http://pic.zaeke.com/img161528707953197.jpeg\" width=\"1391\" height=\"950\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">与显示器有关的两个要求，也就是高刷新率与关闭自适应同步，这可以说是相辅相成的。雷蛇表示后者是假设玩家的配置足够高，那么实际游戏帧率就会比显示器的刷新率更高，那么用毒蝰 8K Hz 版的收益就要比锁帧的更好。同样道理，显示器的刷新率更高，收益也就更为明显。</p><p class=\"p_style\">这从理论上来说是成立的，其实就是显示器的每一帧与鼠标的回报率匹配的延迟会更低。然而受限于财力的缘故，我就没啥条件测试的了，毕竟刷新率到 240、360Hz 的显示器还是高贵了些......</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"微信图片_20210306230504.png\" src=\"http://pic.zaeke.com/img161528709811414.png\" width=\"1451\" height=\"1430\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">讲了这么多有的没的前提、条件啥的，还是回到雷蛇毒蝰 8K Hz 这只鼠标来。首先要确认的第一件事情，那就是 8000Hz 回报率是否真实存在。</p><p class=\"p_style\">这个说白了就是用各种软件进行测试，雷蛇自己也想到这件事情，所以发布鼠标的同时还发布了个本地的回报率测试软件。这个软件需要切换至英语输入法，点击 M 进行设置，N 键开始测试，然后进行快速运动，便会有实时回报率以及折线图。</p><p class=\"p_style\">实际测试毒蝰 8K Hz 在快速圆周运动时，回报率很轻松就可以达到 8000Hz 左右，（实际是 799X Hz 波动）。作为对比，我还测试了毒蝰终极版、罗技 G Pro X Superlight、海盗船&nbsp;Dark Core Pro SE 等，在同样情况下的回报率可达 1000Hz，（实际是 99X Hz 波动）</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"1615046819(1).png\" src=\"http://pic.zaeke.com/img161528711098240.png\" width=\"1071\" height=\"582\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">然后是用 MouseTest V1.5.3 做进一步测试，毒蝰 8K Hz 的回报率可以看到是相当稳定地在 0.5ms 以内波动。放大看基本是在 0.3~0.1ms 之间波动，这可以判作为 8000Hz 回报率的正常情况。</p><p class=\"p_style\">好了，既然毒蝰 8K Hz 的回报率确实是能够达到 8000Hz，并且表现相当稳定。那么就意味着其相比起常规鼠标的 1000Hz 回报率有着数倍的提升，那么毒蝰 8K Hz 使用起来是否也会有同样的倍数提升？或者问的更直接点，这样的高回报率真的有用么？</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"1615047161(1).png\" src=\"http://pic.zaeke.com/img161528713280074.png\" width=\"1654\" height=\"710\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"1615047192(1).png\" src=\"http://pic.zaeke.com/img161528713197965.png\" width=\"1632\" height=\"692\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\"><b>这也是我对毒蝰 8K Hz 的第一个评价，那就是超高回报率确实是有所帮助，并且是实实在在的性能提升</b>。</p><p class=\"p_style\">这要理解起来很简单，回报率就是鼠标在 1 秒以内报告了多少次自己的位置，也就是打了多少个点，将这些点连接起来就是鼠标的移动轨迹。更高的回报率就意味着更多的点，鼠标的移动轨迹就更真实、顺畅。所以同样是快速画圆，可以看到毒蝰 8K Hz 相比毒蝰终极版的点要更为密集，并且没有疏密程度不均的情况。</p><p class=\"p_style\">说的再简单直接点，那就是相比目前市面上的所有鼠标，毒蝰 8K Hz 能让你更接近“人鼠合一”的状态。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"DSC04004.jpg\" src=\"http://pic.zaeke.com/img161528715517955.jpeg\" width=\"1400\" height=\"935\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">既然毒蝰 8K Hz 有着如此明显的性能提升，那么换了这只鼠标就可以变神枪手咯？答案当然......不是。<b>这也是我对毒蝰 8K Hz 的第二个评价，那就是超高回报率的感知不强</b>。</p><p class=\"p_style\">确实更高的回报率有更低的延迟，毒蝰 8K Hz 的理论最低延迟只有 0.125ms，即便是实际情况也能够控制在 0.3ms 以内。这相比起目前主流的 1000Hz 回报率的 1ms，可是有数倍的提升。然而这样的性能提升反应到实际使用或者说是实际手感里，变化程度并不大，甚至可以说是感知不明显。</p><p class=\"p_style\">这其实就是边际效应，毕竟 1000Hz 回报率也就是 1ms 延迟已经足够满足大多数人的使用需要，甚至是超出大多数人的认知。那么进一步到 2000Hz、4000Hz 甚至是 8000Hz，这样越来越低的延迟对于使用者来说反而是越来越难以感觉到。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"DSC04012.jpg\" src=\"http://pic.zaeke.com/img161528716769837.jpeg\" width=\"935\" height=\"1400\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">可是难以感知并不能够极端地认为没用或者是噱头。以我个人使用感受来说，在游戏的过程中从毒蝰终极版换成毒蝰 8K Hz，会有「变顺滑了？」的感觉。并且在 PUBG 压枪、跟枪、开镜瞄准、快速切换目标时，都会有似乎更手的感觉。</p><p class=\"p_style\">要是玩守望先锋、APEX 这样更快节奏的 FPS 类型游戏，那么感觉应该会更明显，收益也更好。然而我必须得说这样的感觉并不算明显，若不是快速切换毒蝰 8K Hz 与终极版进行对比，以及特意去关注移动时的手感，那么这点感知可能并不会让人觉察到，甚至会认为是不是个错觉。</p><p class=\"p_style\">所以指望之前的枪法因为换用毒蝰 8K Hz 而有着明显提升，那么应该会铩羽而归。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"DSC03859.jpg\" src=\"http://pic.zaeke.com/img161528724358970.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"DSC03862.jpg\" src=\"http://pic.zaeke.com/img161528724399497.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\"><b>然而现阶段的毒蝰 8K Hz 是相当值得选购的，因为雷蛇在提高回报率的同时，还给这只鼠标塞进去了更高的配置以及更好的按键手感。</b><br></p><p class=\"p_style\">相比起元祖版毒蝰，也就是 69g 重的有线版本，雷蛇给毒蝰 8K Hz 换用了 100% 的 PTFE 材质脚贴，传感器升级为 PAW3399，光学微动升级为第二代，重量略微增加至 71g。其实毒蝰 8K Hz 就是毒蝰终极版的有线高回报率版本，对于相当部分玩家/蛇迷来说是期待已久的了，<strike>那么毒蝰迷你版是不是也可以......</strike></p><p class=\"jsmargin\"><strike><img alt=\"DSC04006.jpg\" src=\"http://pic.zaeke.com/img161528728136818.jpeg\" width=\"935\" height=\"1400\" style=\"margin: 0px auto; height: auto;\"><br></strike></p><p class=\"p_style\">更关键的是左右键的手感。首先是微动，毒蝰 8K Hz 所采用的雷蛇第二代光学微动并没有啥技术变化，依然是光触发原理和 7000 万次寿命。有所不同的是手感相比第一代要硬朗些，确认感更明确些，这也是相当部分玩家/蛇迷来说是期待已久的。</p><p class=\"p_style\">显然雷蛇还对按键结构做了些调整，毒蝰 8K Hz 的左右键几乎没有啥晃动，空键程更是不存在。这样的改进甚至不需要拆解验证，将鼠标摇一摇就知道了。拿到耳边摇晃听声音，毒蝰终极版（黑）的声音是哗啦哗啦的，毒蝰终极版（白/赛博朋克版）与毒蝰 8K Hz 的声响要小多咯。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"DSC03852.jpg\" src=\"http://pic.zaeke.com/img161528730440358.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">可以说雷蛇毒蝰 8K Hz 是一只相当有趣的游戏鼠标。其最大的亮点也是最吸引人的，绝对是那高达 8000Hz 的超高回报率。这在账面相较主流游戏鼠标有着八倍的性能提升，堪称是碾压优势。</p><p class=\"p_style\">然而在实际使用时，并不能够给使用者带来同样倍数级的感知。更是对显示器、硬件配置等方面的高要求，还有适用游戏类型等方面的条件，其实这只鼠标的使用门槛并不低，或者说现阶段并不能够发挥出最好的效果。</p><p class=\"p_style\">然而毒蝰 8K Hz 并不是只有超高回报率，同步升级的脚垫材质、传感器性能、微动手感以及按键结构，这可以说是雷蛇当下的最高杰作，也是雷蛇最强的有线鼠标。以此基础，再来看 8000Hz 回报率所带来的那么些细微的手感提升，可就是锦上添花了。</p><p class=\"p_style\">PS.1.实际使用发现毒蝰 8K Hz 使用不同的 USB 接口时，可能会出现 8000Hz 回报率上不去的情况，建议搭配雷蛇的本地测试软件一同使用，以确保运行在高回报率；</p><p class=\"p_style\">PS.2.雷蛇表示高回报率可能会与部分游戏出现兼容性问题，并且将会与各大游戏厂商合作解决这可能出现的问题。但并没有描述具体情况，我仅使用了在 PUBG 中，并没有感觉到异常；​​​​</p>', 'http://pic.zaeke.com/img161528742686531.jpeg', 2719, 0, 0, NULL, '2021-03-09 02:57:19', '2021-03-27 07:01:29');
INSERT INTO `article` VALUES (16143, '小米使用了融合卡，小米获得了小米10S', '截至2021年3月10日，小米一共推出了5款名字里带有“小米10”的智能手机产品，它们分别是小米10、小米10Pro、小米10至尊纪念版、小米10青春版，以及最...', '[object Object]', 'victory3332', 31, 'http://pic.zaeke.com/user_1533281475_1.png', '<p>截至2021年3月10日，小米一共推出了5款名字里带有“小米10”的智能手机产品，它们分别是小米10、小米10 Pro、小米10至尊纪念版、小米10青春版，以及最新加入战场的小米10S。如果抛开Redmi那边，这是很长一段时间以来小米手机数字系列中再次出现带“S”后缀的机器。</p><p>按照小米的描述，小米10S是一款基于小米10“升级”所得的产品。因此虽然它的外观看起来是小米10至尊纪念版类似的设计，可实际上的定位却并不是什么“至尊”型的东西。</p><p><img src=\"http://pic.zaeke.com/img161535751964858.jpeg\" alt=\"\">  </p><p>和小米10比起来，小米10S主要的升级方面如下：</p><ul><li>高通骁龙865 5G移动平台→高通骁龙870 5G移动平台；</li><li>双1216超线性对称式扬声器→双1216超线性对称式扬声器，最大振幅0.7mm，哈曼卡顿调音；</li><li>普通外观→“至尊”外观。（话说这真的算是升级？？？）</li></ul><p>小米10是2020年最成功的智能手机产品之一，首批搭载全新的移动平台、丰富全面的配置和一开始看起来很高端可随后被友商衬托得极有性价比的定价，都是小米10成功的原因。其具有标志性的对称式扬声器硬件配置更是拥有出色的表现，被不少玩家津津乐道。小米10S保留了这一在小米10至尊纪念版上被取消的特性，并引入哈曼卡顿的调校，让小米10S成为目前市面上外放效果最优秀的智能手机之一。</p><p><img src=\"http://pic.zaeke.com/img161535757881277.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161535757810452.jpeg\" alt=\"\">  </p><p>小米10S搭载骁龙870移动平台，内置有3000平方毫米大面积VC均热板散热设计，搭配石墨烯和大面积石墨立体散热设计。机身后置相机模组则是1/1.33英寸1.08亿像素光学防抖主摄，1300万像素超广角相机，200万像素微距相机和200万像素景深相机组成。</p><p>小米10S的屏幕尺寸和小米10一致，为6.67英寸，分辨率则是常规的2340×1080。这块屏幕同时也做了出厂校准，JNCD色彩精度可以达到约0.43。小米10上的前后环境光感也在这款手机上传承下来。</p><p><img src=\"http://pic.zaeke.com/img161535779777317.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161535779754816.jpeg\" alt=\"\">  </p><p>因此和去年的小米10一样，小米10S也显得很均衡。大容量内置电池、较高功率的有线充电和无线充电、一亿像素相机等，配置还算丰富。这款手机上唯一显得不“2021”的是那块屏幕，最高90Hz刷新率/180Hz触控采样率的显示屏和同等级竞争对手的120Hz刷新率/240Hz触控采样率的屏幕比起来并无明显优势。</p><p>小米10S的推出给本来最近就很热闹的国内智能手机市场又添了一把火，额外一台均衡的骁龙870机型给了持币待购的买家们更多选择，同时也可以从侧面缓解骁龙888供应不足造成的新品机型缺货之问题。依靠着这款新品以及火爆的K40系列，小米几乎是开足了马力抢占市场。</p><p><strong>更多图赏：</strong></p><p><img src=\"http://pic.zaeke.com/img161535782292599.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161535803891461.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161535785169664.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161535783145299.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161535787531344.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161535787990217.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161535788274822.jpeg\" alt=\"\">  </p>', '<p class=\"p_style\">截至2021年3月10日，小米一共推出了5款名字里带有“小米10”的智能手机产品，它们分别是小米10、小米10 Pro、小米10至尊纪念版、小米10青春版，以及最新加入战场的小米10S。如果抛开Redmi那边，这是很长一段时间以来小米手机数字系列中再次出现带“S”后缀的机器。</p><p class=\"p_style\">按照小米的描述，小米10S是一款基于小米10“升级”所得的产品。因此虽然它的外观看起来是小米10至尊纪念版类似的设计，可实际上的定位却并不是什么“至尊”型的东西。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161535751964858.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">和小米10比起来，小米10S主要的升级方面如下：</p><ul><li>高通骁龙865 5G移动平台→高通骁龙870 5G移动平台；</li><li>双1216超线性对称式扬声器→双1216超线性对称式扬声器，最大振幅0.7mm，哈曼卡顿调音；</li><li>普通外观→“至尊”外观。（话说这真的算是升级？？？）</li></ul><p class=\"p_style\">小米10是2020年最成功的智能手机产品之一，首批搭载全新的移动平台、丰富全面的配置和一开始看起来很高端可随后被友商衬托得极有性价比的定价，都是小米10成功的原因。其具有标志性的对称式扬声器硬件配置更是拥有出色的表现，被不少玩家津津乐道。小米10S保留了这一在小米10至尊纪念版上被取消的特性，并引入哈曼卡顿的调校，让小米10S成为目前市面上外放效果最优秀的智能手机之一。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161535757881277.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161535757810452.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">小米10S搭载骁龙870移动平台，内置有3000平方毫米大面积VC均热板散热设计，搭配石墨烯和大面积石墨立体散热设计。机身后置相机模组则是1/1.33英寸1.08亿像素光学防抖主摄，1300万像素超广角相机，200万像素微距相机和200万像素景深相机组成。</p><p class=\"p_style\">小米10S的屏幕尺寸和小米10一致，为6.67英寸，分辨率则是常规的2340×1080。这块屏幕同时也做了出厂校准，JNCD色彩精度可以达到约0.43。小米10上的前后环境光感也在这款手机上传承下来。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161535779777317.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161535779754816.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">因此和去年的小米10一样，小米10S也显得很均衡。大容量内置电池、较高功率的有线充电和无线充电、一亿像素相机等，配置还算丰富。这款手机上唯一显得不“2021”的是那块屏幕，最高90Hz刷新率/180Hz触控采样率的显示屏和同等级竞争对手的120Hz刷新率/240Hz触控采样率的屏幕比起来并无明显优势。</p><p class=\"p_style\">小米10S的推出给本来最近就很热闹的国内智能手机市场又添了一把火，额外一台均衡的骁龙870机型给了持币待购的买家们更多选择，同时也可以从侧面缓解骁龙888供应不足造成的新品机型缺货之问题。依靠着这款新品以及火爆的K40系列，小米几乎是开足了马力抢占市场。</p><p class=\"p_style\"><strong>更多图赏：</strong></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161535782292599.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161535803891461.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161535785169664.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161535783145299.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161535787531344.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161535787990217.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161535788274822.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p>', 'http://pic.zaeke.com/img161615130870380.jpeg', 14228, 0, 0, NULL, '2021-03-09 22:37:23', '2021-03-27 07:01:18');
INSERT INTO `article` VALUES (16152, 'Find X3里有OPPO的5分理想，5分浪漫，以及90分的实力', '“Find一直在追寻的是科技与艺术之间最完美的交合点。”“在顶级的产品上，给用户顶级的技术能力，不用限制它在照片还是在视频上使用，这是高端机应该具有的能力。”“...', '[object Object]', 'victory3332', 31, 'http://pic.zaeke.com/user_1533281475_1.png', '<p>“Find一直在追寻的是科技与艺术之间最完美的交合点。”</p><p>“在顶级的产品上，给用户顶级的技术能力，不用限制它在照片还是在视频上使用，这是高端机应该具有的能力。”</p><p>“轻薄不是一种选择，是一种能力储备。”</p><p><img alt=\"_1094650.jpg\" src=\"http://pic.zaeke.com/img161556678241774.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>不少国内的产品经理可能都会羡慕在OPPO工作的同行，尤其是Find系列的产品经理。因为OPPO Find的产品经理确实可以发挥自己的主观能动性，找来些行业内最好的东西去做一款拥有强大产品力的产品，然后在面对媒体专访时说这种有点“凡尔赛”的语句。</p><p><b>“为什么要做10bit？”</b></p><p>10bit这东西在OPPO Find X3系列发布会上的存在感极强，10bit屏幕、10bit相机、10bit社交网络内容上传……OPPO说了很多，仿佛Find X3系列会是一台能够替代专业设备的手机。</p><p>但不，OPPO的实际想法是，Find X3系列的10bit的重点是给用户提供额外的创作空间上限。在10bit之外，HDR视频、log视频、RAW+照片等等，Find X3都给了用户全新的自由度。但OPPO从没有说过Find X3可以或要去替代专业影像，Find X3最终也要被卖给绝大部分普通用户。</p><p><img alt=\"_1094757.jpg\" src=\"http://pic.zaeke.com/img161556674418367.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>当然，从另一个角度出发，OPPO也认为手机可以作为专业设备的重要补充，这也是他们决定在X3系列上加入专业的影像功能的理由之一。那些专业影像功能可能对硬件没有过多需求，可他们却给OPPO的软件工程师带来了更多压力。</p><p>对于普通用户来说，OPPO认为Find X3系列的10bit能力的价值在于“留存”。影像在OPPO看来是一种冻结或穿越时间的手段，普通人用手机记录下的除了是影像，也有按下快门那一刻的记忆、情感。将这些东西以更高的空间记录，或是应用类似于“记忆色”的调色风格将那一时刻的景象强化，都是帮助用户去更好记忆和留存的手段。至于10bit这个东西和名词，反倒不那么重要。</p><p>可10bit这东西还存在一个问题：OPPO准备怎么让一般的用户去理解10bit的价值？街头逮着一个路人就科普？</p><p>OPPO的答案是——不管。</p><p><img alt=\"_1094697.jpg\" src=\"http://pic.zaeke.com/img161556673350951.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>没错，OPPO给这个大问题对应了个非常非常佛系的回答。OPPO认为行业内任何具有突破和创新的的恭喜对于用户来说都有适应、接受和理解的过程。除了在X3系列急性上主打10bit摄录回放，OPPO还在Android操作系统底层支持10bit这件事情上希望进行推动和普及。至于其他自己之外的东西，OPPO所能够做的无非就是尽可能去做外部应用适配。</p><p>OPPO觉得，与其花大力气去教育用户“什么是10bit”，不如通过自己优秀的产品来展示“10bit能做什么”。当自己产品本身的能力在线，可以利用新技术和特性去做些有趣的东西时，用户自然就会对这个新东西感兴趣，主动去学习和接受。也许如今10bit的潜能还没有被完全发掘，但OPPO觉得一旦用户领略到新技术的好之后，这些新技术自然会在将来变成刚需。</p><p><b>“这是手机后盖的一小步，但是智能手机业的一大步。”</b></p><p>这个问题正好是ZAEKE知客在听了OPPO对Find系列机型设计阐述之后想到的。OPPO特别喜欢强调自己在以前的Find手机上做了什么大胆的外观设计创新，可Find X3这次的外观设计并没有之前Find那般的高原创度，OPPO反而是在后盖上花大力气。</p><p>当然，OPPO之所以这么肯花力气，是因为他们认为“一体化”这个东西真的很重要。</p><p><img alt=\"_1094678.jpg\" src=\"http://pic.zaeke.com/img161556671467454.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>一体化意味着什么？一体化意味着无接缝、无断层、无台阶，这也是Find X3系列后盖摸上去手感卓越的原因。一体化后盖在市面上也甚少有采用者（OPPO这边原话是“没有”，不过显然……），因此“一体化”的东西被大众赋予了稀有和高级的感觉，当然事实上一体化的后天也确实因为难做而成本高，它也确实是个“高级”的东西。</p><p>因为要兼顾机身的一体化、轻薄以及强大的影像模组，OPPO为Find X3系列的后盖付出了非常高昂的成本。OPPO反复强调Find X3的玻璃后盖曲面需要超过2000个点调试，一片后盖的加工时间高达40小时。因此从成本和最终呈现的效果，OPPO也认为“曲面后盖”是个实打实的创新。</p><p>至于具体的摄像头布局，OPPO认为Find X3作为顶着OPPO名号的顶级旗舰手机，不能够随意妥协。因此对于“高规格相机”和“大容量电池”这东西是“我全都要”，Find X3系列最终之所以被设计成这样就是为了能在不缩减电池容量的前提下，将体积巨大的后置双主摄模组塞进6.7英寸屏幕的手机里面。</p><p><b>“有人走过的路，再走一遍就不叫探索。”</b></p><p>在整个OPPO Find X3产品经理专访的速记里面，“探索”这个词出现了30次，是最有存在感的词汇。这是OPPO对Find X3系列机型，或是Find系列机型的一个定义——去尝试新的东西，哪怕要付出代价。比如去做10bit，比如去做“不可能的曲面”，比如去玩“显微镜”。</p><p>这些值得吗？</p><p>从经济角度来看，不一定有，但这并不代表不该去做。如果这个世界永远是“够用就好”，那么我们将会失去很多美好的东西。当年人们觉得手机能打电话发消息就好，手机拍照只是添头或奢侈品，可如今手机拍照已经强大到完全取代了普通消费级相机，我们却习以为常；当年人们觉得4G网络无非就是上下行速度更快点，可如今我们却完全离不开高速移动互联网和其提供的服务。</p><p><img alt=\"WeChat 圖片_20210313002443.jpg\" src=\"http://pic.zaeke.com/img161556655740879.jpeg\" width=\"2000\" height=\"1333\"><br></p><p>“不去试试，怎么知道这个东西有没有意义呢？”OPPO的产品经理大约说过这样的一番话。</p><p>OPPO有着追求更好产品这个简单又朴素的想法，然后用复杂的工艺和技术造出了Find X3系列。但是和单纯的输出理想主义比起来，Find X3系列那些创新都有这台手机本身坚实的产品力作为基础，10bit、精巧机身等等都是让Find X3系列锦上添花。在纯粹的理想主义者看来，Find X3这个理想结晶里终究混杂着“现实”。<br></p><p>这是问题么？当然不是。毕竟在手机这个残酷的行业里，光有理想是不够的，理想只有在实力的土壤上才能发芽。因此，Find X3系表面看起来尽是浪漫和理想，可在华丽的背后，有OPPO的努力和实力作为支撑。</p>', '<p class=\"p_style\">“Find一直在追寻的是科技与艺术之间最完美的交合点。”</p><p class=\"p_style\">“在顶级的产品上，给用户顶级的技术能力，不用限制它在照片还是在视频上使用，这是高端机应该具有的能力。”</p><p class=\"p_style\">“轻薄不是一种选择，是一种能力储备。”</p><p class=\"jsmargin\"><img alt=\"_1094650.jpg\" src=\"http://pic.zaeke.com/img161556678241774.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">不少国内的产品经理可能都会羡慕在OPPO工作的同行，尤其是Find系列的产品经理。因为OPPO Find的产品经理确实可以发挥自己的主观能动性，找来些行业内最好的东西去做一款拥有强大产品力的产品，然后在面对媒体专访时说这种有点“凡尔赛”的语句。</p><p class=\"p_style\"><b>“为什么要做10bit？”</b></p><p class=\"p_style\">10bit这东西在OPPO Find X3系列发布会上的存在感极强，10bit屏幕、10bit相机、10bit社交网络内容上传……OPPO说了很多，仿佛Find X3系列会是一台能够替代专业设备的手机。</p><p class=\"p_style\">但不，OPPO的实际想法是，Find X3系列的10bit的重点是给用户提供额外的创作空间上限。在10bit之外，HDR视频、log视频、RAW+照片等等，Find X3都给了用户全新的自由度。但OPPO从没有说过Find X3可以或要去替代专业影像，Find X3最终也要被卖给绝大部分普通用户。</p><p class=\"jsmargin\"><img alt=\"_1094757.jpg\" src=\"http://pic.zaeke.com/img161556674418367.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">当然，从另一个角度出发，OPPO也认为手机可以作为专业设备的重要补充，这也是他们决定在X3系列上加入专业的影像功能的理由之一。那些专业影像功能可能对硬件没有过多需求，可他们却给OPPO的软件工程师带来了更多压力。</p><p class=\"p_style\">对于普通用户来说，OPPO认为Find X3系列的10bit能力的价值在于“留存”。影像在OPPO看来是一种冻结或穿越时间的手段，普通人用手机记录下的除了是影像，也有按下快门那一刻的记忆、情感。将这些东西以更高的空间记录，或是应用类似于“记忆色”的调色风格将那一时刻的景象强化，都是帮助用户去更好记忆和留存的手段。至于10bit这个东西和名词，反倒不那么重要。</p><p class=\"p_style\">可10bit这东西还存在一个问题：OPPO准备怎么让一般的用户去理解10bit的价值？街头逮着一个路人就科普？</p><p class=\"p_style\">OPPO的答案是——不管。</p><p class=\"jsmargin\"><img alt=\"_1094697.jpg\" src=\"http://pic.zaeke.com/img161556673350951.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">没错，OPPO给这个大问题对应了个非常非常佛系的回答。OPPO认为行业内任何具有突破和创新的的恭喜对于用户来说都有适应、接受和理解的过程。除了在X3系列急性上主打10bit摄录回放，OPPO还在Android操作系统底层支持10bit这件事情上希望进行推动和普及。至于其他自己之外的东西，OPPO所能够做的无非就是尽可能去做外部应用适配。</p><p class=\"p_style\">OPPO觉得，与其花大力气去教育用户“什么是10bit”，不如通过自己优秀的产品来展示“10bit能做什么”。当自己产品本身的能力在线，可以利用新技术和特性去做些有趣的东西时，用户自然就会对这个新东西感兴趣，主动去学习和接受。也许如今10bit的潜能还没有被完全发掘，但OPPO觉得一旦用户领略到新技术的好之后，这些新技术自然会在将来变成刚需。</p><p class=\"p_style\"><b>“这是手机后盖的一小步，但是智能手机业的一大步。”</b></p><p class=\"p_style\">这个问题正好是ZAEKE知客在听了OPPO对Find系列机型设计阐述之后想到的。OPPO特别喜欢强调自己在以前的Find手机上做了什么大胆的外观设计创新，可Find X3这次的外观设计并没有之前Find那般的高原创度，OPPO反而是在后盖上花大力气。</p><p class=\"p_style\">当然，OPPO之所以这么肯花力气，是因为他们认为“一体化”这个东西真的很重要。</p><p class=\"jsmargin\"><img alt=\"_1094678.jpg\" src=\"http://pic.zaeke.com/img161556671467454.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">一体化意味着什么？一体化意味着无接缝、无断层、无台阶，这也是Find X3系列后盖摸上去手感卓越的原因。一体化后盖在市面上也甚少有采用者（OPPO这边原话是“没有”，不过显然……），因此“一体化”的东西被大众赋予了稀有和高级的感觉，当然事实上一体化的后天也确实因为难做而成本高，它也确实是个“高级”的东西。</p><p class=\"p_style\">因为要兼顾机身的一体化、轻薄以及强大的影像模组，OPPO为Find X3系列的后盖付出了非常高昂的成本。OPPO反复强调Find X3的玻璃后盖曲面需要超过2000个点调试，一片后盖的加工时间高达40小时。因此从成本和最终呈现的效果，OPPO也认为“曲面后盖”是个实打实的创新。</p><p class=\"p_style\">至于具体的摄像头布局，OPPO认为Find X3作为顶着OPPO名号的顶级旗舰手机，不能够随意妥协。因此对于“高规格相机”和“大容量电池”这东西是“我全都要”，Find X3系列最终之所以被设计成这样就是为了能在不缩减电池容量的前提下，将体积巨大的后置双主摄模组塞进6.7英寸屏幕的手机里面。</p><p class=\"p_style\"><b>“有人走过的路，再走一遍就不叫探索。”</b></p><p class=\"p_style\">在整个OPPO Find X3产品经理专访的速记里面，“探索”这个词出现了30次，是最有存在感的词汇。这是OPPO对Find X3系列机型，或是Find系列机型的一个定义——去尝试新的东西，哪怕要付出代价。比如去做10bit，比如去做“不可能的曲面”，比如去玩“显微镜”。</p><p class=\"p_style\">这些值得吗？</p><p class=\"p_style\">从经济角度来看，不一定有，但这并不代表不该去做。如果这个世界永远是“够用就好”，那么我们将会失去很多美好的东西。当年人们觉得手机能打电话发消息就好，手机拍照只是添头或奢侈品，可如今手机拍照已经强大到完全取代了普通消费级相机，我们却习以为常；当年人们觉得4G网络无非就是上下行速度更快点，可如今我们却完全离不开高速移动互联网和其提供的服务。</p><p class=\"jsmargin\"><img alt=\"WeChat 圖片_20210313002443.jpg\" src=\"http://pic.zaeke.com/img161556655740879.jpeg\" width=\"2000\" height=\"1333\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">“不去试试，怎么知道这个东西有没有意义呢？”OPPO的产品经理大约说过这样的一番话。</p><p class=\"p_style\">OPPO有着追求更好产品这个简单又朴素的想法，然后用复杂的工艺和技术造出了Find X3系列。但是和单纯的输出理想主义比起来，Find X3系列那些创新都有这台手机本身坚实的产品力作为基础，10bit、精巧机身等等都是让Find X3系列锦上添花。在纯粹的理想主义者看来，Find X3这个理想结晶里终究混杂着“现实”。<br></p><p class=\"p_style\">这是问题么？当然不是。毕竟在手机这个残酷的行业里，光有理想是不够的，理想只有在实力的土壤上才能发芽。因此，Find X3系表面看起来尽是浪漫和理想，可在华丽的背后，有OPPO的努力和实力作为支撑。</p>', 'http://pic.zaeke.com/img161556654567067.jpeg', 4495, 0, 0, NULL, '2021-03-12 08:33:43', '2021-03-27 07:03:22');
INSERT INTO `article` VALUES (16157, '“不可能的曲面”？OPPO Find X3 Pro就是要挑战“不可能”', 'OPPOFindX3Pro很有可能是2021年上半年期待值最高的旗舰智能手机之一，因为每个人都好奇在这台以“Find”为名的新品上OPPO会给我们的带来什么让人...', '[object Object]', 'victory3332', 31, 'http://pic.zaeke.com/user_1533281475_1.png', '<p>OPPO Find X3 Pro很有可能是2021年上半年期待值最高的旗舰智能手机之一，因为每个人都好奇在这台以“Find”为名的新品上OPPO会给我们的带来什么让人耳目一新的东西。因此当“不可能的曲面”出现在OPPO官方预热文案里面的时候，每个人都大约心里有了底。</p><p>这次，OPPO把“不可能的曲面”变成了“可能”。</p><p><img src=\"http://pic.zaeke.com/img161572562621027.jpeg\" alt=\"\">  </p><p>Find X3 Pro的外观是这款新机最大的特色，它的背面采用一整块玻璃制造，并且在摄像头之外的区域没有任何开孔和拼接。为了平衡纤薄机身和大体积双主摄后置相机模组，OPPO花了高额成本打造了一块在业界前所未有的曲面玻璃。</p><p>为了打造Find X3 Pro的后盖玻璃曲面，OPPO在整个后背上用了超过2000个控制点。一整块玻璃放入高精度3D模具之后被送入700℃的3D热锻成型，随后再使用CNC抛光开孔。每一台Find X3 Pro以及采用相同设计的Find X3后背玻璃生产时间长达40小时，更不要提因为复杂工艺导致后盖玻璃良率远低于普通的平面玻璃或3D玻璃。</p><p>Find X3 Pro提供有镜黑、凝白、雾蓝三个玻璃机身版本。其中白色和蓝色款为哑光玻璃搭配亮面之设计，黑色款则采用OC0工艺让玻璃呈现出类似于陶瓷的视觉效果和光泽。</p><p><img src=\"http://pic.zaeke.com/img161572563958545.jpeg\" alt=\"\">  </p><p>Find X3 Pro拥有一块6.7英寸3216×1440分辨率的AMOLED显示屏，搭载最新的LTPO混合背板技术，支持1Hz~120Hz刷新率自适应调节，相比上代120Hz显示屏功耗最多降低46%。这块屏幕还支持原生10bit色深、8192级自动亮度，且每一台Find X3系列出厂前都会进行屏幕色彩校准，屏幕色准JNCD约为0.4。凭借出色的性能和调校，Find X3系列的屏幕也获得了DisplayMate的A+评级。</p><p>在10bit屏幕之基础上，Find X3 Pro还祭出了“全链路10bit”的概念。Find X3 Pro的后置双主摄、长焦相机均支持10bit照片和10bit HDR视频摄录，由Find X3 Pro拍摄的10bit HDR内容除了无损存储和在屏幕上无损播放，也支持上传至微博和bilibili，并在Find X3上无损观看。</p><p><img src=\"http://pic.zaeke.com/img161572566632320.jpeg\" alt=\"\">  </p><p>在Find X3 Pro的后置4相机模组里，主摄和超广角均采用5000万像素索尼IMX766传感器，其中超广角相机还有额外的自由曲面镜片用于降低畸变。剩余2颗相机分别是2x长焦相机和首次出现在手机上的显微镜相机，显微镜相机支持最多60倍放大。</p><p>这套相机也支持RAW照片、log视频来扩大后期处理空间，OPPO还与著名导演姜文合作推出了两套姜文相机滤镜，让用户可以随手拍出与大导演影视作品风格相同的照片和视频。</p><p>OPPO Find X3 Pro其他部分主要功能和规格配置如下：</p><p>高通骁龙888 5G移动平台；4500毫安时内置电池；65W有线充电；30W无线充电；IP68防水。</p><p><strong>更多图赏：</strong></p><p><img src=\"http://pic.zaeke.com/img161572569486176.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161572569464921.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161572569439652.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161572570842978.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161572571413941.jpeg\" alt=\"\"></p><p><img src=\"http://pic.zaeke.com/img161572572398763.jpeg\" alt=\"\">  </p>', '<p class=\"p_style\">OPPO Find X3 Pro很有可能是2021年上半年期待值最高的旗舰智能手机之一，因为每个人都好奇在这台以“Find”为名的新品上OPPO会给我们的带来什么让人耳目一新的东西。因此当“不可能的曲面”出现在OPPO官方预热文案里面的时候，每个人都大约心里有了底。</p><p class=\"p_style\">这次，OPPO把“不可能的曲面”变成了“可能”。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161572562621027.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">Find X3 Pro的外观是这款新机最大的特色，它的背面采用一整块玻璃制造，并且在摄像头之外的区域没有任何开孔和拼接。为了平衡纤薄机身和大体积双主摄后置相机模组，OPPO花了高额成本打造了一块在业界前所未有的曲面玻璃。</p><p class=\"p_style\">为了打造Find X3 Pro的后盖玻璃曲面，OPPO在整个后背上用了超过2000个控制点。一整块玻璃放入高精度3D模具之后被送入700℃的3D热锻成型，随后再使用CNC抛光开孔。每一台Find X3 Pro以及采用相同设计的Find X3后背玻璃生产时间长达40小时，更不要提因为复杂工艺导致后盖玻璃良率远低于普通的平面玻璃或3D玻璃。</p><p class=\"p_style\">Find X3 Pro提供有镜黑、凝白、雾蓝三个玻璃机身版本。其中白色和蓝色款为哑光玻璃搭配亮面之设计，黑色款则采用OC0工艺让玻璃呈现出类似于陶瓷的视觉效果和光泽。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161572563958545.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">Find X3 Pro拥有一块6.7英寸3216×1440分辨率的AMOLED显示屏，搭载最新的LTPO混合背板技术，支持1Hz~120Hz刷新率自适应调节，相比上代120Hz显示屏功耗最多降低46%。这块屏幕还支持原生10bit色深、8192级自动亮度，且每一台Find X3系列出厂前都会进行屏幕色彩校准，屏幕色准JNCD约为0.4。凭借出色的性能和调校，Find X3系列的屏幕也获得了DisplayMate的A+评级。</p><p class=\"p_style\">在10bit屏幕之基础上，Find X3 Pro还祭出了“全链路10bit”的概念。Find X3 Pro的后置双主摄、长焦相机均支持10bit照片和10bit HDR视频摄录，由Find X3 Pro拍摄的10bit HDR内容除了无损存储和在屏幕上无损播放，也支持上传至微博和bilibili，并在Find X3上无损观看。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161572566632320.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">在Find X3 Pro的后置4相机模组里，主摄和超广角均采用5000万像素索尼IMX766传感器，其中超广角相机还有额外的自由曲面镜片用于降低畸变。剩余2颗相机分别是2x长焦相机和首次出现在手机上的显微镜相机，显微镜相机支持最多60倍放大。</p><p class=\"p_style\">这套相机也支持RAW照片、log视频来扩大后期处理空间，OPPO还与著名导演姜文合作推出了两套姜文相机滤镜，让用户可以随手拍出与大导演影视作品风格相同的照片和视频。</p><p class=\"p_style\">OPPO Find X3 Pro其他部分主要功能和规格配置如下：</p><p class=\"p_style\">高通骁龙888 5G移动平台；4500毫安时内置电池；65W有线充电；30W无线充电；IP68防水。</p><p class=\"p_style\"><strong>更多图赏：</strong></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161572569486176.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161572569464921.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161572569439652.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161572570842978.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161572571413941.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161572572398763.jpeg\" alt=\"\" style=\"margin: 0px auto; height: auto;\">  </p>', 'http://pic.zaeke.com/img161572595911470.jpeg', 9998, 0, 0, NULL, '2021-03-14 04:46:46', '2021-03-27 07:01:06');
INSERT INTO `article` VALUES (16173, '红魔6 Pro评测：风扇吹响战歌，没人能在我的BGM里打赢我', '在前些年厂家们扎堆推出游戏手机时，我们还在怀疑游戏手机这个看似复古的概念到底有没有受众。不过从这些年的情况来看，应该是有的——要真是年年亏早该不卖了。而在这条路...', '[object Object]', '烫头孙师傅', 38, 'http://pic.zaeke.com/user_1533281477_5.png', '<p>在前些年厂家们扎堆推出游戏手机时，我们还在怀疑游戏手机这个看似复古的概念到底有没有受众。不过从这些年的情况来看，应该是有的——要真是年年亏早该不卖了。</p><p><img src=\"http://pic.zaeke.com/img161595692625616.jpeg\" alt=\"591A8411.jpg\">  </p><p>而在这条路上，红魔走出了属于自己的路子。除了但凡是游戏手机都有的肩键、RGB和硬朗的外观之外，也有内置风扇这样的“骚操作”。虽然和常规旗舰相比游戏手机的市场的确相对小了些，不过只要讨得这少数人的开心也就拥有了生存的空间。</p><p>更何况在拥有旗舰级性能的同时，红魔6 Pro确实在游戏上能够带来优秀的功能与体验，并足以成为趁手的电竞工具。毕竟根据网络上的相当一部分言论来看，很多人买个手机真的就是为了打游戏，丝毫不在乎相机等等。对于他们来说买游戏手机就是把钱花在自己最需要的地方，也算是种理性消费观。</p><p style=\"text-align: center;\"><strong>由外到内：“我是游戏手机”</strong></p><p>打造一款游戏手机，得先从外观设计开始。</p><p>非常有趣的是，红魔6 Pro的后置相机模组倒是非常符合很多人对常规旗舰的幻想——沿中轴线布置。当然之所以能这么做，既是因为这款游戏手机不需要追求轻薄，也不需要顶级的影像表现。但这个设计带来的对称式机身确实具备充分的美感。</p><p><img src=\"http://pic.zaeke.com/img161595679863714.jpeg\" alt=\"591A8325.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161595683919396.jpeg\" alt=\"591A8336.jpg\">  </p><p>虽然说这个设计对于红魔来说是个熟悉的操作，不过能够在如今也保持着这样特立独行的设计也算是很有坚持了。毕竟也有一部分游戏手机采用了常规布局，又或是有些常规布局的手机支持许多游戏手机的特性。</p><p>且慢，这个对称设计真的只是符合很多人的对称主义审美么？</p><p><img src=\"http://pic.zaeke.com/img161595684417183.jpeg\" alt=\"591A8342.jpg\"><br></p><p>上手以后我觉得真不是。要知道如果背后有个凸起的摄像头，那么在使用时经常会出现机身翻转之后会出现手感上的不适应。红魔6 Pro上这个上下左右均对称的设计，让我无论怎样旋转机身都会拥有同样的手感，因此就算遇到腾讯系游戏祖传的重力感应乱跳问题，也不会出现翻转机身后手感不适应的问题。</p><p>所以别再觉得这个对称式设计是个“花架子”了，市面上的游戏手机常用的对称式设计其实对于游戏手感来说至关重要，这甚至也可以作为目前衡量游戏手机的重要标准了——毕竟手感对游戏来说真的很重要。</p><p><img alt=\"Screenshot_2021-03-12-16-16-40-283_副本.png\" src=\"http://pic.zaeke.com/img161605205912199.png\" width=\"1400\" height=\"1032\"><br></p><p>性能方面，红魔6 Pro搭载高通骁龙888移动平台，照例先看跑分。红魔6 Pro在GeeekBench测试中得到了1127分的单核分数与3609分的多核分数，而在3DMARK的Sling Shot Extreme测试中拿到了8806分，安兔兔测试也能够超过74万分。</p><p>从跑分上来看，红魔6 Pro显然很符合这块机型的定位，毕竟游戏手机要的就是顶级性能。可要知道高通骁龙888移动平台由于较为明显的发热与较大的功耗，然后很多人有些望而却步。</p><p>为了打消用户的顾虑，红魔6 Pro采用ICE6.0 7层多维散热系统，超轻纳米离心风扇、散热峡谷风道、超导铜箔、导热凝胶、石墨烯、VC散热片、航天级散热冰刃等组合而成的散热系统，尤其是20000/分的高速离心风扇，在理论上能够带来更好的散热表现。</p><p><img src=\"http://pic.zaeke.com/img161595686557518.jpeg\" alt=\"591A8354.jpg\"><br></p><p><img src=\"http://pic.zaeke.com/img161595688115443.jpeg\" alt=\"591A8358.jpg\"><br></p><p>所以还是直接进入实战环节吧。</p><p>虽然红魔6 Pro支持高达165Hz的刷新率，但官方目前唯一适配的《王牌战士》在严格意义上并不能算是款大多数同学都在玩的“国民手游”。所以我们还是按照惯例，用大家常玩的《王者荣耀》与《和平精英》这两款大家一眼就能看出孰强孰弱的“真 · 国民手游”来给大家跑测试。</p><p>在《王者荣耀》超高帧率(90 FPS)+超高分辨率+抗锯齿的设置下，两局的平均帧率都为90.3 FPS，团战帧率基本处于81至86 FPS之间，其中最低的66 FPS出现在团战爆发时阵亡“黑屏”的瞬间。而在《和平精英》超高帧率(40 FPS)+超高清分辨率+抗锯齿的设置下，全局平均帧率为39.9 FPS，最低帧率为6倍镜瞄向房区时的32 FPS。</p><p><img alt=\"1.png\" src=\"http://pic.zaeke.com/img161596813737031.png\" width=\"1162\" height=\"631\"><br></p><p><img alt=\"2.png\" src=\"http://pic.zaeke.com/img161596814339764.png\" width=\"1164\" height=\"632\"><br></p><p>需要注意的是，上述成绩均为未开启电竞游戏模式和未启动散热风扇的情况下测出。因为《王者荣耀》这款游戏及《和平精英》的超高帧率(40 FPS)设置对于红魔6 Pro来说可谓是轻轻松松，游戏运行过程中手机只会给人温热的感觉，并不需要启动徒增噪音和耗电的风扇。</p><p>但如果是将《和平精英》的设置上调至90帧率(90 FPS)+流畅分辨率+抗锯齿之后，红魔6 Pro就需要开启风扇进行降温了，否则体感温度就会快速上升并达到烫手的水平。随后我们在这个设置下进行了两局游戏测试，结果显示“电竞游戏模式+散热风扇是否启动”对于红魔6 Pro的游戏性能影响并不大，因为关闭的那局平均帧率为89.4 FPS，开启后的那局平均帧率仅提升0.2至89.6 FPS。如果抛开两局游戏之间因航线导致的环境差异，那完全可以用性能表现一致来形容。</p><p><img alt=\"微信截图_20210317153754.png\" src=\"http://pic.zaeke.com/img161596816762138.png\" width=\"1162\" height=\"631\"><br></p><p><img alt=\"微信截图_20210317154312.png\" src=\"http://pic.zaeke.com/img161596818178700.png\" width=\"1162\" height=\"631\"><br></p><p>甚至在《穿越火线：枪战王者》的部分模式——例如爆破模式中中，在开启极高画质+极高帧率（官方标称120 FPS）后甚至跑出了最高137 FPS的帧率成绩。这点也是令我们没有想到的，毕竟市面上能够在实际测试中跑出比官方标称最高帧率更高成绩的机型是非常少见的。只不过在生化追击模式下，该机在画面中玩家较多的场景下帧率稳定性相对一般，仅为116.7 FPS。</p><p><img src=\"http://pic.zaeke.com/img161596442274080.png\" alt=\"CF电竞关.png\"></p><p><img src=\"http://pic.zaeke.com/img161596442213634.png\" alt=\"生化电竞开.png\"></p><p><br></p><p>这说明电竞游戏模式实属“画龙点睛”？并不是，因为当前的手机已经完全可以识别游戏并自动调用相应的性能策略，所以电竞游戏模式的重点在于提供“无打扰的游戏体验”，而非Windows系统笔记本电脑里“高性能模式”的电源策略。</p><p>那“徒增功耗”的是散热风扇？也不是，虽然红魔6 Pro已经有了可以满足各个部件持续进行性能释放的散热效率，但那是对于各种芯片和元器件而言的足够，而不是对于人手的足够，毕竟我们玩游戏时双手握持的后盖也是整个散热系统的一部分。这时就凸显出了散热风扇的作用：可以在不到1分钟的时间内将后盖的体感温度从烫手变成温热。</p><p><img alt=\"Screenshot_2021-03-17-15-29-02-916_副本.png\" src=\"http://pic.zaeke.com/img161596958737785.png\" width=\"670\" height=\"260\"><br></p><p>根据我们的测试，红魔6 Pro在《穿越火线：枪战王者》开启极高帧率与极高画质后，在约24摄氏度的室内环境下，打开机内风扇后未开启电竞键时长时间游戏后的机身最高温度为41.7摄氏度，而在开启电竞键后的机身最高温度则为44.5摄氏度。</p><p>但由于风扇的确有明显且持续的噪音，所以我们推荐大家在未戴耳机或手机不烫手时尽可能关闭风扇，毕竟周期且长时间听这种噪音是有可能导致耳鸣的。</p><p><img alt=\"591A8344.jpg\" src=\"http://pic.zaeke.com/img161597841964030.jpeg\" width=\"1400\" height=\"788\"><br></p><p><img src=\"http://pic.zaeke.com/img161595687480394.jpeg\" alt=\"591A8355.jpg\"><br></p><p>在拥有强大的性能表现和散热效率之后，红魔6 Pro还将专注力放在了可以让玩家“合法开挂”的肩键之上。通过触控报点率达到400Hz的双IC独立触控肩键，我们可以在《王者荣耀》中实现“移动+普攻时按出闪现和恢复”或“复活甲复活过程中快速购买名刀”之类的操作。当然我们也必须提醒大家，肩键是一个必须要时间适应，否则会适得其反的功能。</p><p>此外，如果你有在玩类似于《天龙八部手游》这般需要长时间挂机的游戏，那么电竞游戏模式中所提供的“熄屏挂机”功能将非常适合你。这项可以让手机在游戏运行时关闭屏幕的功能不仅可以提升续航表现，而且还能减少那块AMOLED显示屏因长时间显示同一内容导致烧屏的概率。</p><p><img src=\"http://pic.zaeke.com/img161595692940454.jpeg\" alt=\"591A8414.jpg\">  </p><p>毫无疑问，红魔6 Pro在游戏体验上的每一个特征都是环环相扣：选择合适硬件带来顶尖性能；搭配散热效率领先的ICE 6.0系统和充电分离技术保证持续性；借助红魔Arc Reactor和腾讯Solar Core游戏引擎提升可靠性；通过诸如“和平精英决赛圈播报”、“红魔投屏”、“双独立IC触控肩键”和“熄屏挂机”等功能满足玩家的进阶需求。</p><p>在支持小窗挂起的游戏中游戏时，如关闭电竞键，那么系统将会返回桌面，同时正在运行的游戏则以小窗的形式挂起。这点对于中途需要进行其它操作的情况非常友好，毕竟不少机型挂在后台也可能会被判定为未联网，而很多机型的小窗模式还得自己开，红魔这个实体电竞键在小窗挂起这个功能的高效性上非常优秀。</p><p>一句话总结红魔6 Pro的游戏体验，那一定是目前运行主流游戏最流畅的手机之一。毕竟目前主流的游戏，大多都在腾讯游戏旗下呀。</p><p><img src=\"http://pic.zaeke.com/img161595737343145.jpeg\" alt=\"591A8317.jpg\">  </p><p><img src=\"http://pic.zaeke.com/img161595688115443.jpeg\" alt=\"591A8358.jpg\">  </p><p>不过作为一款全面屏机型，红魔6 Pro没能免俗，也同样支持全面屏手势操作。别的游戏我倒是不敢断言，但对于快节奏的射击类手游来说，指尖的滑动操作误触率非常高，很容易误操作返回桌面。即便做了两次滑动退出的设计也难以避免误操作，毕竟旋转视角与拉枪线时多次快速滑动操作的概率很高，并且也未必都在屏幕框内操作。</p><p>当然这个问题在开启电竞键后并不存在，所以如果你和我一样常玩快节奏射击游戏且未开启电竞键，那么选择传统的“三大金刚”导航键或许会更好些。</p><p><img src=\"http://pic.zaeke.com/img161595696082448.jpeg\" alt=\"591A8424.jpg\"><br></p><p>虽然严格来说我们并没有去特意测试触控采样率，但红魔6 Pro的跟手度表现在主观上就已经足够好了。由于工作需要我经常在不同的机型上进行游戏测试，因此时常会遇到换机之后不跟手的现象，而红魔6 Pro在跟手度上则几乎不需要“适应期”。</p><p>当然受限于智能手机的黑箱化现状，我们很难说跟手度好的原因就是触控采样率不同，不过从结果上来说，触控采样率较高的红魔6 Pro的确拥有更好的跟手度（只不过我们不敢100%肯定原因是否是采样率）。</p><p style=\"text-align: center;\"><strong>那些游戏体验之外的事情</strong></p><p>作为一款游戏手机，那些游戏之外的东西看起来确实不那么重要，可不管游戏味儿多重，游戏手机也始终需要承载“智能手机”的基础功能。</p><p><img alt=\"21-03-16-14-23-49.png\" src=\"http://pic.zaeke.com/img161596579033608.png\" width=\"1920\" height=\"1080\"><br></p><p>例如在充电方面，红魔6 Pro支持120W快充。经过我们的测试，红魔6 Pro能够在前5分钟以约100W的功率充电（不排除测试工具无法记录到120W的峰值），然后在电量充至40%时将充电功率降低至约75W，在电量65%时降为约56W，在电量80%时降至48W并短暂维持，然后再在一定的波动后稳定在约21W直至充满全部电量。该机的总充电时间约为22.5分钟。</p><p>这个充电成绩虽然并不是入市场上的NO.1，不过却依然是第一梯队。尤其是对于一款游戏手机来说，在常常需要充分释放性能的前提下4500mAh也很难说充足，足够快的充电能够尽可能让游戏体验更加持久。甚至哪怕你一不小心把手机玩到没电，插上充电头赶紧开机也大概率能赶得上重连。</p><p><img alt=\"Screenshot_2021-03-17-15-29-02-916_副本.png\" src=\"http://pic.zaeke.com/img161596632930343.png\" width=\"1400\" height=\"1032\"><br></p><p>首先聊聊系统，其实从个人的观点出发，我觉得红魔手机的系统要比一本同源的努比亚手机的系统要更有意思。尽管红魔6 Pro的系统易用程度或许并不是目前市面上的第一流水平，各类功能也或许并未做到顶级体验，但很多细节的打磨都非常有趣。</p><p>例如无需进入设置中好几层的子菜单，便可下拉菜单的控制中心中设置灯效开关与风扇开关 ，就连屏幕刷新率也可以直接在控制中心的子菜单进行切换，无需进入设置。这些功能的高效性要远超许多常规设计的机型，并且对于时间宝贵、暂停没命的手游需求来说，这些高效的开关设计非常令人满意。</p><p><img src=\"http://pic.zaeke.com/img161595694855604.jpeg\" alt=\"591A8421.jpg\"><br></p><p><img src=\"http://pic.zaeke.com/img161595694615974.jpeg\" alt=\"591A8420.jpg\"><br></p><p>系统的细节其实更加彰显了红魔6 Pro作为游戏手机的本色。毕竟若只是迎合游戏玩家的表面需求，拿出一款外观硬朗、内置肩键、有RGB灯光的机型其实一点都不难，可在与游戏息息相关的系统功能上下一番功夫，可以说虽是系统体验的一部分，也更是游戏体验的再度升华。</p><p>这几个或许在有些人看来有些“无聊”、“这也能吹”的开关，在我看来倒确实是用了心。</p><p><img src=\"http://pic.zaeke.com/img161595696650392.jpeg\" alt=\"591A8427.jpg\">  </p><p>相信绝大多数购买游戏手机的用户不会再相机上抱过高的期望值，所以我也能放心大胆地“泼冷水”了。尽管是6400万像素主摄+800万像素超广角+200万像素微距相机的后置三摄模组，但对于同价位甚至价位低很多的机型来说，红魔6 Pro的拍照表现也是相对平庸的。</p><p>所以，红魔6 Pro的相机更多是在解决有无问题。</p><p><img alt=\"IMG_20210317_153938.jpg\" src=\"http://pic.zaeke.com/img161597028160255.jpeg\" width=\"1400\" height=\"1050\"></p><p><img alt=\"IMG_20210317_154208.jpg\" src=\"http://pic.zaeke.com/img161597028255886.jpeg\" width=\"1400\" height=\"1050\"></p><p><img alt=\"IMG_20210317_154800.jpg\" src=\"http://pic.zaeke.com/img161597030349078.jpeg\" width=\"1400\" height=\"1050\"><br></p><p><img alt=\"IMG_20210316_175208.jpg\" src=\"http://pic.zaeke.com/img161597028232750.jpeg\" width=\"1400\" height=\"1050\"><br></p><p style=\"text-align: center;\"><b>主摄</b></p><p style=\"text-align: center;\"><b><img alt=\"IMG_20210317_154240.jpg\" src=\"http://pic.zaeke.com/img161597047655750.jpeg\" width=\"1050\" height=\"1400\"><br></b></p><p style=\"text-align: center;\"><b><b style=\"font-size: 16px;\"><img alt=\"IMG_20210317_154930.jpg\" src=\"http://pic.zaeke.com/img161597047657212.jpeg\" width=\"1050\" height=\"1400\"></b><br></b></p><p style=\"text-align: center;\"><b><b style=\"font-size: 16px;\">超广角</b></b></p><p style=\"text-align: center;\"></p><p>从实际表现来看，红魔6 Pro的相机色彩相对素淡，因此观感上不容易讨好眼球，并且画面细节也相对较少，涂抹感明显。而在光比较大的场景下，无论主摄还是超广角相机都会在暗部出现明显的噪点与伪色。而超广角相机在大光比场景下也容易出现高光信息丢失严重的问题，对观感也有较大的影响。</p><p>尽管承袭自努比亚的Neovision相机家族带来的丰富功能，能够稍稍为相机体验扳回一城，可……该机似乎只有在专业模式下才能切换至超广角相机，常规模式下没有（或者说找不到）切换至超广角相机的方法。但无论是（身经百战的数码编辑都）找不到开关还是真就只能在专业模式下调用超广角硬件，这个逻辑目前都是不够合格的。</p><p>有喜有忧，就是红魔6 Pro在功能上的情况，所幸其中的不足大概率是软的、能靠OTA更新解决的。</p><p style=\"text-align: center;\"><strong>吃准了游戏玩家</strong></p><p>其实红魔这个品牌的出现，或许就有常规旗舰咱斗不过别人，不如另辟蹊径的思维。不过正像文章开篇所说的那样，这个路子方向没错，而且红魔截止到目前为止也还“活”得好好的。</p><p><img src=\"http://pic.zaeke.com/img161595684156247.jpeg\" alt=\"591A8340.jpg\">  </p><p>这当然不难理解，毕竟红魔游戏手机就是吃准了游戏玩家的成果。这些玩家可以在很多功能——例如拍照上进行很大的让步，而选择更加稳定的性能输出、更加专业高效的游戏功能、更加炫酷多样的周边配件。所以红魔也能够将精力集中在这些玩家们重视的地方上，从而以“术业有专攻”的方式获得属于自己的核心竞争力。</p><p>所以红魔6 Pro尽管是一款拥有顶级性能的安卓旗舰手机，但它并不是所谓的“全能旗舰”。可如果你真的在乎游戏体验胜过其它，那么这又或许是目前市面上的最佳选择之一，甚至类似的可选项加起来总共也没多少个。</p><p>你想要的我都给你，这或许就是红魔6 Pro的处世哲学吧？</p>', '<p class=\"p_style\">在前些年厂家们扎堆推出游戏手机时，我们还在怀疑游戏手机这个看似复古的概念到底有没有受众。不过从这些年的情况来看，应该是有的——要真是年年亏早该不卖了。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161595692625616.jpeg\" alt=\"591A8411.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">而在这条路上，红魔走出了属于自己的路子。除了但凡是游戏手机都有的肩键、RGB和硬朗的外观之外，也有内置风扇这样的“骚操作”。虽然和常规旗舰相比游戏手机的市场的确相对小了些，不过只要讨得这少数人的开心也就拥有了生存的空间。</p><p class=\"p_style\">更何况在拥有旗舰级性能的同时，红魔6 Pro确实在游戏上能够带来优秀的功能与体验，并足以成为趁手的电竞工具。毕竟根据网络上的相当一部分言论来看，很多人买个手机真的就是为了打游戏，丝毫不在乎相机等等。对于他们来说买游戏手机就是把钱花在自己最需要的地方，也算是种理性消费观。</p><p style=\"text-align: center;\" class=\"p_style\"><strong>由外到内：“我是游戏手机”</strong></p><p class=\"p_style\">打造一款游戏手机，得先从外观设计开始。</p><p class=\"p_style\">非常有趣的是，红魔6 Pro的后置相机模组倒是非常符合很多人对常规旗舰的幻想——沿中轴线布置。当然之所以能这么做，既是因为这款游戏手机不需要追求轻薄，也不需要顶级的影像表现。但这个设计带来的对称式机身确实具备充分的美感。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161595679863714.jpeg\" alt=\"591A8325.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161595683919396.jpeg\" alt=\"591A8336.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">虽然说这个设计对于红魔来说是个熟悉的操作，不过能够在如今也保持着这样特立独行的设计也算是很有坚持了。毕竟也有一部分游戏手机采用了常规布局，又或是有些常规布局的手机支持许多游戏手机的特性。</p><p class=\"p_style\">且慢，这个对称设计真的只是符合很多人的对称主义审美么？</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161595684417183.jpeg\" alt=\"591A8342.jpg\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">上手以后我觉得真不是。要知道如果背后有个凸起的摄像头，那么在使用时经常会出现机身翻转之后会出现手感上的不适应。红魔6 Pro上这个上下左右均对称的设计，让我无论怎样旋转机身都会拥有同样的手感，因此就算遇到腾讯系游戏祖传的重力感应乱跳问题，也不会出现翻转机身后手感不适应的问题。</p><p class=\"p_style\">所以别再觉得这个对称式设计是个“花架子”了，市面上的游戏手机常用的对称式设计其实对于游戏手感来说至关重要，这甚至也可以作为目前衡量游戏手机的重要标准了——毕竟手感对游戏来说真的很重要。</p><p class=\"jsmargin\"><img alt=\"Screenshot_2021-03-12-16-16-40-283_副本.png\" src=\"http://pic.zaeke.com/img161605205912199.png\" width=\"1400\" height=\"1032\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">性能方面，红魔6 Pro搭载高通骁龙888移动平台，照例先看跑分。红魔6 Pro在GeeekBench测试中得到了1127分的单核分数与3609分的多核分数，而在3DMARK的Sling Shot Extreme测试中拿到了8806分，安兔兔测试也能够超过74万分。</p><p class=\"p_style\">从跑分上来看，红魔6 Pro显然很符合这块机型的定位，毕竟游戏手机要的就是顶级性能。可要知道高通骁龙888移动平台由于较为明显的发热与较大的功耗，然后很多人有些望而却步。</p><p class=\"p_style\">为了打消用户的顾虑，红魔6 Pro采用ICE6.0 7层多维散热系统，超轻纳米离心风扇、散热峡谷风道、超导铜箔、导热凝胶、石墨烯、VC散热片、航天级散热冰刃等组合而成的散热系统，尤其是20000/分的高速离心风扇，在理论上能够带来更好的散热表现。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161595686557518.jpeg\" alt=\"591A8354.jpg\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161595688115443.jpeg\" alt=\"591A8358.jpg\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">所以还是直接进入实战环节吧。</p><p class=\"p_style\">虽然红魔6 Pro支持高达165Hz的刷新率，但官方目前唯一适配的《王牌战士》在严格意义上并不能算是款大多数同学都在玩的“国民手游”。所以我们还是按照惯例，用大家常玩的《王者荣耀》与《和平精英》这两款大家一眼就能看出孰强孰弱的“真 · 国民手游”来给大家跑测试。</p><p class=\"p_style\">在《王者荣耀》超高帧率(90 FPS)+超高分辨率+抗锯齿的设置下，两局的平均帧率都为90.3 FPS，团战帧率基本处于81至86 FPS之间，其中最低的66 FPS出现在团战爆发时阵亡“黑屏”的瞬间。而在《和平精英》超高帧率(40 FPS)+超高清分辨率+抗锯齿的设置下，全局平均帧率为39.9 FPS，最低帧率为6倍镜瞄向房区时的32 FPS。</p><p class=\"jsmargin\"><img alt=\"1.png\" src=\"http://pic.zaeke.com/img161596813737031.png\" width=\"1162\" height=\"631\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"2.png\" src=\"http://pic.zaeke.com/img161596814339764.png\" width=\"1164\" height=\"632\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">需要注意的是，上述成绩均为未开启电竞游戏模式和未启动散热风扇的情况下测出。因为《王者荣耀》这款游戏及《和平精英》的超高帧率(40 FPS)设置对于红魔6 Pro来说可谓是轻轻松松，游戏运行过程中手机只会给人温热的感觉，并不需要启动徒增噪音和耗电的风扇。</p><p class=\"p_style\">但如果是将《和平精英》的设置上调至90帧率(90 FPS)+流畅分辨率+抗锯齿之后，红魔6 Pro就需要开启风扇进行降温了，否则体感温度就会快速上升并达到烫手的水平。随后我们在这个设置下进行了两局游戏测试，结果显示“电竞游戏模式+散热风扇是否启动”对于红魔6 Pro的游戏性能影响并不大，因为关闭的那局平均帧率为89.4 FPS，开启后的那局平均帧率仅提升0.2至89.6 FPS。如果抛开两局游戏之间因航线导致的环境差异，那完全可以用性能表现一致来形容。</p><p class=\"jsmargin\"><img alt=\"微信截图_20210317153754.png\" src=\"http://pic.zaeke.com/img161596816762138.png\" width=\"1162\" height=\"631\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"微信截图_20210317154312.png\" src=\"http://pic.zaeke.com/img161596818178700.png\" width=\"1162\" height=\"631\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">甚至在《穿越火线：枪战王者》的部分模式——例如爆破模式中中，在开启极高画质+极高帧率（官方标称120 FPS）后甚至跑出了最高137 FPS的帧率成绩。这点也是令我们没有想到的，毕竟市面上能够在实际测试中跑出比官方标称最高帧率更高成绩的机型是非常少见的。只不过在生化追击模式下，该机在画面中玩家较多的场景下帧率稳定性相对一般，仅为116.7 FPS。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161596442274080.png\" alt=\"CF电竞关.png\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161596442213634.png\" alt=\"生化电竞开.png\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"p_style\"><br></p><p class=\"p_style\">这说明电竞游戏模式实属“画龙点睛”？并不是，因为当前的手机已经完全可以识别游戏并自动调用相应的性能策略，所以电竞游戏模式的重点在于提供“无打扰的游戏体验”，而非Windows系统笔记本电脑里“高性能模式”的电源策略。</p><p class=\"p_style\">那“徒增功耗”的是散热风扇？也不是，虽然红魔6 Pro已经有了可以满足各个部件持续进行性能释放的散热效率，但那是对于各种芯片和元器件而言的足够，而不是对于人手的足够，毕竟我们玩游戏时双手握持的后盖也是整个散热系统的一部分。这时就凸显出了散热风扇的作用：可以在不到1分钟的时间内将后盖的体感温度从烫手变成温热。</p><p class=\"jsmargin\"><img alt=\"Screenshot_2021-03-17-15-29-02-916_副本.png\" src=\"http://pic.zaeke.com/img161596958737785.png\" width=\"670\" height=\"260\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">根据我们的测试，红魔6 Pro在《穿越火线：枪战王者》开启极高帧率与极高画质后，在约24摄氏度的室内环境下，打开机内风扇后未开启电竞键时长时间游戏后的机身最高温度为41.7摄氏度，而在开启电竞键后的机身最高温度则为44.5摄氏度。</p><p class=\"p_style\">但由于风扇的确有明显且持续的噪音，所以我们推荐大家在未戴耳机或手机不烫手时尽可能关闭风扇，毕竟周期且长时间听这种噪音是有可能导致耳鸣的。</p><p class=\"jsmargin\"><img alt=\"591A8344.jpg\" src=\"http://pic.zaeke.com/img161597841964030.jpeg\" width=\"1400\" height=\"788\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161595687480394.jpeg\" alt=\"591A8355.jpg\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">在拥有强大的性能表现和散热效率之后，红魔6 Pro还将专注力放在了可以让玩家“合法开挂”的肩键之上。通过触控报点率达到400Hz的双IC独立触控肩键，我们可以在《王者荣耀》中实现“移动+普攻时按出闪现和恢复”或“复活甲复活过程中快速购买名刀”之类的操作。当然我们也必须提醒大家，肩键是一个必须要时间适应，否则会适得其反的功能。</p><p class=\"p_style\">此外，如果你有在玩类似于《天龙八部手游》这般需要长时间挂机的游戏，那么电竞游戏模式中所提供的“熄屏挂机”功能将非常适合你。这项可以让手机在游戏运行时关闭屏幕的功能不仅可以提升续航表现，而且还能减少那块AMOLED显示屏因长时间显示同一内容导致烧屏的概率。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161595692940454.jpeg\" alt=\"591A8414.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">毫无疑问，红魔6 Pro在游戏体验上的每一个特征都是环环相扣：选择合适硬件带来顶尖性能；搭配散热效率领先的ICE 6.0系统和充电分离技术保证持续性；借助红魔Arc Reactor和腾讯Solar Core游戏引擎提升可靠性；通过诸如“和平精英决赛圈播报”、“红魔投屏”、“双独立IC触控肩键”和“熄屏挂机”等功能满足玩家的进阶需求。</p><p class=\"p_style\">在支持小窗挂起的游戏中游戏时，如关闭电竞键，那么系统将会返回桌面，同时正在运行的游戏则以小窗的形式挂起。这点对于中途需要进行其它操作的情况非常友好，毕竟不少机型挂在后台也可能会被判定为未联网，而很多机型的小窗模式还得自己开，红魔这个实体电竞键在小窗挂起这个功能的高效性上非常优秀。</p><p class=\"p_style\">一句话总结红魔6 Pro的游戏体验，那一定是目前运行主流游戏最流畅的手机之一。毕竟目前主流的游戏，大多都在腾讯游戏旗下呀。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161595737343145.jpeg\" alt=\"591A8317.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161595688115443.jpeg\" alt=\"591A8358.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">不过作为一款全面屏机型，红魔6 Pro没能免俗，也同样支持全面屏手势操作。别的游戏我倒是不敢断言，但对于快节奏的射击类手游来说，指尖的滑动操作误触率非常高，很容易误操作返回桌面。即便做了两次滑动退出的设计也难以避免误操作，毕竟旋转视角与拉枪线时多次快速滑动操作的概率很高，并且也未必都在屏幕框内操作。</p><p class=\"p_style\">当然这个问题在开启电竞键后并不存在，所以如果你和我一样常玩快节奏射击游戏且未开启电竞键，那么选择传统的“三大金刚”导航键或许会更好些。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161595696082448.jpeg\" alt=\"591A8424.jpg\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">虽然严格来说我们并没有去特意测试触控采样率，但红魔6 Pro的跟手度表现在主观上就已经足够好了。由于工作需要我经常在不同的机型上进行游戏测试，因此时常会遇到换机之后不跟手的现象，而红魔6 Pro在跟手度上则几乎不需要“适应期”。</p><p class=\"p_style\">当然受限于智能手机的黑箱化现状，我们很难说跟手度好的原因就是触控采样率不同，不过从结果上来说，触控采样率较高的红魔6 Pro的确拥有更好的跟手度（只不过我们不敢100%肯定原因是否是采样率）。</p><p style=\"text-align: center;\" class=\"p_style\"><strong>那些游戏体验之外的事情</strong></p><p class=\"p_style\">作为一款游戏手机，那些游戏之外的东西看起来确实不那么重要，可不管游戏味儿多重，游戏手机也始终需要承载“智能手机”的基础功能。</p><p class=\"jsmargin\"><img alt=\"21-03-16-14-23-49.png\" src=\"http://pic.zaeke.com/img161596579033608.png\" width=\"1920\" height=\"1080\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">例如在充电方面，红魔6 Pro支持120W快充。经过我们的测试，红魔6 Pro能够在前5分钟以约100W的功率充电（不排除测试工具无法记录到120W的峰值），然后在电量充至40%时将充电功率降低至约75W，在电量65%时降为约56W，在电量80%时降至48W并短暂维持，然后再在一定的波动后稳定在约21W直至充满全部电量。该机的总充电时间约为22.5分钟。</p><p class=\"p_style\">这个充电成绩虽然并不是入市场上的NO.1，不过却依然是第一梯队。尤其是对于一款游戏手机来说，在常常需要充分释放性能的前提下4500mAh也很难说充足，足够快的充电能够尽可能让游戏体验更加持久。甚至哪怕你一不小心把手机玩到没电，插上充电头赶紧开机也大概率能赶得上重连。</p><p class=\"jsmargin\"><img alt=\"Screenshot_2021-03-17-15-29-02-916_副本.png\" src=\"http://pic.zaeke.com/img161596632930343.png\" width=\"1400\" height=\"1032\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">首先聊聊系统，其实从个人的观点出发，我觉得红魔手机的系统要比一本同源的努比亚手机的系统要更有意思。尽管红魔6 Pro的系统易用程度或许并不是目前市面上的第一流水平，各类功能也或许并未做到顶级体验，但很多细节的打磨都非常有趣。</p><p class=\"p_style\">例如无需进入设置中好几层的子菜单，便可下拉菜单的控制中心中设置灯效开关与风扇开关 ，就连屏幕刷新率也可以直接在控制中心的子菜单进行切换，无需进入设置。这些功能的高效性要远超许多常规设计的机型，并且对于时间宝贵、暂停没命的手游需求来说，这些高效的开关设计非常令人满意。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161595694855604.jpeg\" alt=\"591A8421.jpg\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161595694615974.jpeg\" alt=\"591A8420.jpg\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">系统的细节其实更加彰显了红魔6 Pro作为游戏手机的本色。毕竟若只是迎合游戏玩家的表面需求，拿出一款外观硬朗、内置肩键、有RGB灯光的机型其实一点都不难，可在与游戏息息相关的系统功能上下一番功夫，可以说虽是系统体验的一部分，也更是游戏体验的再度升华。</p><p class=\"p_style\">这几个或许在有些人看来有些“无聊”、“这也能吹”的开关，在我看来倒确实是用了心。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161595696650392.jpeg\" alt=\"591A8427.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">相信绝大多数购买游戏手机的用户不会再相机上抱过高的期望值，所以我也能放心大胆地“泼冷水”了。尽管是6400万像素主摄+800万像素超广角+200万像素微距相机的后置三摄模组，但对于同价位甚至价位低很多的机型来说，红魔6 Pro的拍照表现也是相对平庸的。</p><p class=\"p_style\">所以，红魔6 Pro的相机更多是在解决有无问题。</p><p class=\"jsmargin\"><img alt=\"IMG_20210317_153938.jpg\" src=\"http://pic.zaeke.com/img161597028160255.jpeg\" width=\"1400\" height=\"1050\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210317_154208.jpg\" src=\"http://pic.zaeke.com/img161597028255886.jpeg\" width=\"1400\" height=\"1050\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"IMG_20210317_154800.jpg\" src=\"http://pic.zaeke.com/img161597030349078.jpeg\" width=\"1400\" height=\"1050\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"IMG_20210316_175208.jpg\" src=\"http://pic.zaeke.com/img161597028232750.jpeg\" width=\"1400\" height=\"1050\" style=\"margin: 0px auto; height: auto;\"><br></p><p style=\"text-align: center;\" class=\"p_style\"><b>主摄</b></p><p style=\"text-align: center;\" class=\"jsmargin\"><b><img alt=\"IMG_20210317_154240.jpg\" src=\"http://pic.zaeke.com/img161597047655750.jpeg\" width=\"1050\" height=\"1400\" style=\"margin: 0px auto; height: auto;\"><br></b></p><p style=\"text-align: center;\" class=\"jsmargin\"><b><b style=\"font-size: 16px;\"><img alt=\"IMG_20210317_154930.jpg\" src=\"http://pic.zaeke.com/img161597047657212.jpeg\" width=\"1050\" height=\"1400\" style=\"margin: 0px auto; height: auto;\"></b><br></b></p><p style=\"text-align: center;\" class=\"p_style\"><b><b style=\"font-size: 16px;\">超广角</b></b></p><p style=\"text-align: center;\" class=\"p_style\"></p><p class=\"p_style\">从实际表现来看，红魔6 Pro的相机色彩相对素淡，因此观感上不容易讨好眼球，并且画面细节也相对较少，涂抹感明显。而在光比较大的场景下，无论主摄还是超广角相机都会在暗部出现明显的噪点与伪色。而超广角相机在大光比场景下也容易出现高光信息丢失严重的问题，对观感也有较大的影响。</p><p class=\"p_style\">尽管承袭自努比亚的Neovision相机家族带来的丰富功能，能够稍稍为相机体验扳回一城，可……该机似乎只有在专业模式下才能切换至超广角相机，常规模式下没有（或者说找不到）切换至超广角相机的方法。但无论是（身经百战的数码编辑都）找不到开关还是真就只能在专业模式下调用超广角硬件，这个逻辑目前都是不够合格的。</p><p class=\"p_style\">有喜有忧，就是红魔6 Pro在功能上的情况，所幸其中的不足大概率是软的、能靠OTA更新解决的。</p><p style=\"text-align: center;\" class=\"p_style\"><strong>吃准了游戏玩家</strong></p><p class=\"p_style\">其实红魔这个品牌的出现，或许就有常规旗舰咱斗不过别人，不如另辟蹊径的思维。不过正像文章开篇所说的那样，这个路子方向没错，而且红魔截止到目前为止也还“活”得好好的。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161595684156247.jpeg\" alt=\"591A8340.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">这当然不难理解，毕竟红魔游戏手机就是吃准了游戏玩家的成果。这些玩家可以在很多功能——例如拍照上进行很大的让步，而选择更加稳定的性能输出、更加专业高效的游戏功能、更加炫酷多样的周边配件。所以红魔也能够将精力集中在这些玩家们重视的地方上，从而以“术业有专攻”的方式获得属于自己的核心竞争力。</p><p class=\"p_style\">所以红魔6 Pro尽管是一款拥有顶级性能的安卓旗舰手机，但它并不是所谓的“全能旗舰”。可如果你真的在乎游戏体验胜过其它，那么这又或许是目前市面上的最佳选择之一，甚至类似的可选项加起来总共也没多少个。</p><p class=\"p_style\">你想要的我都给你，这或许就是红魔6 Pro的处世哲学吧？</p>', 'http://pic.zaeke.com/img161665776741819.jpeg', 11355, 0, 0, NULL, '2021-03-16 23:34:10', '2021-03-27 08:02:55');
INSERT INTO `article` VALUES (16184, '2021年的游戏手机：风景这边独好', '今年第一季度是手游爱好者的盛宴，因为今年上半年智能手机厂商们纷纷采用了“早鸟”模式，导致整个3月发布的智能手机产品数量几乎赶上了去年整个上半年。在各种各样智能手...', '[object Object]', 'Kururu', 33, 'http://pic.zaeke.com/user_1533281476_2.png', '<p>今年第一季度是手游爱好者的盛宴，因为今年上半年智能手机厂商们纷纷采用了“早鸟”模式，导致整个3月发布的智能手机产品数量几乎赶上了去年整个上半年。在各种各样智能手机产品上市的当下有一个细分品类特别引人注目，那就是“游戏手机”。<br></p><p>“游戏手机”大约是目前智能手机市场中增长最快的细分品类，这一方面是因为手游本身蓬勃发展，另一方面也是因为大量用户移动娱乐需求增长。而这些游戏手机，或至少是以游戏操控和功能作为主打特性的手机们也很识相，在这些产品上玩出了各种花活。</p><p><b>iQOO 7</b></p><p>iQOO 7是最早在公开场合亮相的高通骁龙888移动平台手机，因为其作为王者荣耀职业联赛KPL的指定用机，在去年KPL秋季赛决赛场上就曾经被当时夺得冠军的战队手持亮相。作为新一代的KPL比赛用机，iQOO 7相比上一代iQOO 5又进行了大量升级，以满足极致游戏体验的需求。</p><p><img alt=\"图片1.png\" src=\"http://pic.zaeke.com/img161614250841488.png\" width=\"1268\" height=\"713\"><br></p><p>iQOO 7上最显著也最重要的升级自然是全新的骁龙888移动平台。骁龙888移动平台采用5nm工艺制造，并首次在手机多丛集CPU中引入ARM Cortex-X1超级核心，拥有最高2.84GHz的运行频率。在新工艺新架构的帮助下，骁龙888的CPU性能比上一代骁龙新芯片提高25%；集成于骁龙888内部的Adreno 660 GPU则有35%性能提升和20%能效提升。</p><p><img alt=\"图片2.png\" src=\"http://pic.zaeke.com/img161614252117251.png\" width=\"1038\" height=\"692\"><br></p><p>iQOO 7的游戏体验全程突出一个“双”字，首先其拥有立体声双喇叭，支持环绕音效；机身内部搭载双线性马达，可以模拟立体复杂震感；在屏幕下方则有双压力感应区，玩家手指无需离开屏幕即可进行额外操作，可以双指模拟四指操作。最后当然还有高达120W的快速充电功能，最快只需要15分钟即可将iQOO 7内置电池充至满电。</p><p>在前面的基础上，iQOO 7还是首个支持腾讯游戏GVoice算法的手机。这一算法是高通、vivo、腾讯游戏三方专门针对实时互动游戏需求，专门为竞技玩家打造的高品质语音功能。和传统语音服务比起来，游戏语音复杂性更高。面对那些挑战，为玩家提供更好的游戏语音体验是GVoice算法的核心追求。</p><p><img alt=\"图片3.png\" src=\"http://pic.zaeke.com/img161614252885018.png\" width=\"1269\" height=\"714\"><br></p><p>游戏语音和传统语音的区别主要在2方面，其一是游戏语音的噪音源更加复杂。因为在进行传统语音的时候用户比较倾向于选择安静的环境，可进行游戏语音的时候玩家可能处于比较嘈杂的环境中，加上游戏本身的音效和音乐，让游戏语音需要面对的噪音源更加复杂。</p><p>第二，传统的语音算法大多运行于CPU上，因此需要消耗CPU算力和内存资源。这显然不利于游戏场景，因为复杂的游戏场景本身就要占用大量计算资源。</p><p>因此，GVoice的核心特性是利用高通骁龙平台的AI引擎里的Hexagon DSP运行算法。GVoice算法本身经过对游戏语音的深度优化，比传统语音算法更能压制游戏场景中的杂音，降噪效果更好。</p><p>更重要的是，将语音算法移动至高效的Hexagon DSP上运行后，整机的耗电量可减少20mA/s，比传统语音算法减少30% CPU占用。将宝贵的处理器性能更多分配到游戏上能够大幅度降低游戏卡顿的几率，同时更加清晰的语音也利于竞技游戏中玩家之间的交流，打造沉浸式手游体验。</p><p><b>红魔游戏手机6系列</b></p><p>红魔游戏手机6系列是2021年第一款官方认可的“游戏手机”，作为目前市面上唯一一个坚持采用内置主动散热风扇的手机产品，红魔6系列在游戏这方面显得非常硬核。</p><p>红魔6系列机型是目前市面上唯一一款搭载165Hz超高刷新率OLED显示屏的手机。这块超高刷新率显示屏支持HDR和10bit色深，采用新型CPHY-DSI屏幕接口，性能比传统屏幕接口更好。机身侧面拥有两颗拥有独立触控IC的触控游戏肩键，且支持按键按下和抬起的操作分离，让玩家能够模拟最多6手指触控操作屏幕。</p><p><img alt=\"图片4.png\" src=\"http://pic.zaeke.com/img161614254119366.png\" width=\"1269\" height=\"714\"><br></p><p>红魔游戏手机6系列另一个特点是超高屏幕触控采样率，其支持最高单点500Hz/多点360Hz触控采样率。骁龙888搭载的第三代Elite Gaming本次也特别加入了高通游戏快速触摸响应功能，游戏场景里触控延迟可被进一步降低最多20%，让游戏操作做到“一触即发”。</p><p><img alt=\"图片5.png\" src=\"http://pic.zaeke.com/img161614255158422.png\" width=\"692\" height=\"310\"><br></p><p>同时，红魔6系列还是搭载高通全新一代Quick Charge 5技术的游戏手机。Quick Charge 5技术支持超过100W峰值充电功率，充电速度相比上一代方案最多提高4倍。和上一代解决方案相比，Quick Charge 5充电效率最多提高70%，因此可大幅度降低充电时的发热。红魔6系列甚至还支持专门开发的充电分离特性，因此在边充电边游戏的时候机身发了更低，电池寿命也比传统方案下更长。</p><p>为了获得最佳的网络效能，新一代的骁龙888移动平台机型们大多支持Wi-Fi 6增强版无线网络连接，红魔6系列机型也不例外。在Wi-Fi 6增强版、网络智能调度算法和腾讯手游加速器联合加持下，红魔6系列在Wi-Fi和5G网络下平均延迟比没有这些新特性的机型低20毫秒。得益于Wi-Fi 6+5G的全面高性能网络通讯技术，红魔6系列可以为用户提供无死角的低延迟在线游戏体验。</p><p><b>ROG游戏手机5系列</b></p><p>如果说游戏玩家群体里面某一个品牌代表着“信仰”，那么这个品牌肯定包括ROG。全新的ROG游戏手机5系列包含3款产品，分别是ROG游戏手机5、ROG游戏手机5 Pro和ROG游戏手机5幻影。三款新品均搭载最新的高通骁龙888移动平台，额外的游戏触控按键、6000毫安时电池、65W邮箱快充和侧面+底部双USB Type-C接口。</p><p><img alt=\"图片6.png\" src=\"http://pic.zaeke.com/img161614257042653.png\" width=\"1269\" height=\"847\"><br></p><p>ROG游戏手机5系列全系列搭载来自三星的OLED显示屏，采用最新的E4发光材料，屏幕响应时间低至1毫秒，刷新率最高可达144Hz。ROG游戏手机5 Pro和ROG游戏手机5幻影这两款手机还搭载有可自定义的背部OLED显示屏、额外的背部触摸感应区，个性化程度和游戏操控双双拉满。</p><p>全新的骁龙888为包括ROG游戏手机5系列在内的产品们带来了第三代Elite Gaming体系。此全新世代标准中高通首次将可变速率着色功能带入移动设备，该特性允许游戏开发者将CPU和GPU资源灵活调配到游戏画面中更重要的部分，可带来最多30%游戏性能提升。在前代Elite Gaming中引入的超平滑游戏帧率功能也获得升级，如今骁龙888可以在最多144fps的游戏中应用游戏平滑功能，兼顾高帧率和高稳定性。</p><p><img alt=\"图片7.png\" src=\"http://pic.zaeke.com/img161614258436403.png\" width=\"1117\" height=\"621\"><br></p><p>新一代Elite Gaming体系自然也延续了之前最高HDR品质、端游级游戏特性、超流畅游戏体验、沉浸式游戏音频等功能，这些完善的生态功能圈加上骁龙888芯片强大的性能，新一代的顶级游戏手机会在游戏体验方面更进一步。</p><p>当然，iQOO 7、红魔游戏手机6系列、ROG游戏手机5系列不会是2021年游戏手机的全部阵容，还有即将发布的新一代黑鲨游戏手机4系列。目前关于黑鲨游戏手机4系列可以确定的是其也会加入高通骁龙888这个阵营，核心的游戏特色则是全系标配双机械升降实体游戏按钮。</p><p>随着智能手机行业的发展，“游戏手机”这个概念已经完全被市场接受，虽然和主流市场比起来依旧显得非常小众，可这并没有妨碍“游戏手机”这个小圈子里的产品推陈出新蓬勃发展。甚至在一部分人群，比如学生群体中，售价亲民又提供极致游戏体验的游戏手机们更是大受欢迎。国内电子竞技的正规化、大众化，加上大量作品推出移动版，更是让游戏手机这个小市场在整个智能手机市场中显得格外耀眼。</p><p>也许今年，我们会看见更多人为了游戏而买一台游戏手机。</p>', '<p class=\"p_style\">今年第一季度是手游爱好者的盛宴，因为今年上半年智能手机厂商们纷纷采用了“早鸟”模式，导致整个3月发布的智能手机产品数量几乎赶上了去年整个上半年。在各种各样智能手机产品上市的当下有一个细分品类特别引人注目，那就是“游戏手机”。<br></p><p class=\"p_style\">“游戏手机”大约是目前智能手机市场中增长最快的细分品类，这一方面是因为手游本身蓬勃发展，另一方面也是因为大量用户移动娱乐需求增长。而这些游戏手机，或至少是以游戏操控和功能作为主打特性的手机们也很识相，在这些产品上玩出了各种花活。</p><p class=\"p_style\"><b>iQOO 7</b></p><p class=\"p_style\">iQOO 7是最早在公开场合亮相的高通骁龙888移动平台手机，因为其作为王者荣耀职业联赛KPL的指定用机，在去年KPL秋季赛决赛场上就曾经被当时夺得冠军的战队手持亮相。作为新一代的KPL比赛用机，iQOO 7相比上一代iQOO 5又进行了大量升级，以满足极致游戏体验的需求。</p><p class=\"jsmargin\"><img alt=\"图片1.png\" src=\"http://pic.zaeke.com/img161614250841488.png\" width=\"1268\" height=\"713\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">iQOO 7上最显著也最重要的升级自然是全新的骁龙888移动平台。骁龙888移动平台采用5nm工艺制造，并首次在手机多丛集CPU中引入ARM Cortex-X1超级核心，拥有最高2.84GHz的运行频率。在新工艺新架构的帮助下，骁龙888的CPU性能比上一代骁龙新芯片提高25%；集成于骁龙888内部的Adreno 660 GPU则有35%性能提升和20%能效提升。</p><p class=\"jsmargin\"><img alt=\"图片2.png\" src=\"http://pic.zaeke.com/img161614252117251.png\" width=\"1038\" height=\"692\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">iQOO 7的游戏体验全程突出一个“双”字，首先其拥有立体声双喇叭，支持环绕音效；机身内部搭载双线性马达，可以模拟立体复杂震感；在屏幕下方则有双压力感应区，玩家手指无需离开屏幕即可进行额外操作，可以双指模拟四指操作。最后当然还有高达120W的快速充电功能，最快只需要15分钟即可将iQOO 7内置电池充至满电。</p><p class=\"p_style\">在前面的基础上，iQOO 7还是首个支持腾讯游戏GVoice算法的手机。这一算法是高通、vivo、腾讯游戏三方专门针对实时互动游戏需求，专门为竞技玩家打造的高品质语音功能。和传统语音服务比起来，游戏语音复杂性更高。面对那些挑战，为玩家提供更好的游戏语音体验是GVoice算法的核心追求。</p><p class=\"jsmargin\"><img alt=\"图片3.png\" src=\"http://pic.zaeke.com/img161614252885018.png\" width=\"1269\" height=\"714\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">游戏语音和传统语音的区别主要在2方面，其一是游戏语音的噪音源更加复杂。因为在进行传统语音的时候用户比较倾向于选择安静的环境，可进行游戏语音的时候玩家可能处于比较嘈杂的环境中，加上游戏本身的音效和音乐，让游戏语音需要面对的噪音源更加复杂。</p><p class=\"p_style\">第二，传统的语音算法大多运行于CPU上，因此需要消耗CPU算力和内存资源。这显然不利于游戏场景，因为复杂的游戏场景本身就要占用大量计算资源。</p><p class=\"p_style\">因此，GVoice的核心特性是利用高通骁龙平台的AI引擎里的Hexagon DSP运行算法。GVoice算法本身经过对游戏语音的深度优化，比传统语音算法更能压制游戏场景中的杂音，降噪效果更好。</p><p class=\"p_style\">更重要的是，将语音算法移动至高效的Hexagon DSP上运行后，整机的耗电量可减少20mA/s，比传统语音算法减少30% CPU占用。将宝贵的处理器性能更多分配到游戏上能够大幅度降低游戏卡顿的几率，同时更加清晰的语音也利于竞技游戏中玩家之间的交流，打造沉浸式手游体验。</p><p class=\"p_style\"><b>红魔游戏手机6系列</b></p><p class=\"p_style\">红魔游戏手机6系列是2021年第一款官方认可的“游戏手机”，作为目前市面上唯一一个坚持采用内置主动散热风扇的手机产品，红魔6系列在游戏这方面显得非常硬核。</p><p class=\"p_style\">红魔6系列机型是目前市面上唯一一款搭载165Hz超高刷新率OLED显示屏的手机。这块超高刷新率显示屏支持HDR和10bit色深，采用新型CPHY-DSI屏幕接口，性能比传统屏幕接口更好。机身侧面拥有两颗拥有独立触控IC的触控游戏肩键，且支持按键按下和抬起的操作分离，让玩家能够模拟最多6手指触控操作屏幕。</p><p class=\"jsmargin\"><img alt=\"图片4.png\" src=\"http://pic.zaeke.com/img161614254119366.png\" width=\"1269\" height=\"714\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">红魔游戏手机6系列另一个特点是超高屏幕触控采样率，其支持最高单点500Hz/多点360Hz触控采样率。骁龙888搭载的第三代Elite Gaming本次也特别加入了高通游戏快速触摸响应功能，游戏场景里触控延迟可被进一步降低最多20%，让游戏操作做到“一触即发”。</p><p class=\"jsmargin\"><img alt=\"图片5.png\" src=\"http://pic.zaeke.com/img161614255158422.png\" width=\"692\" height=\"310\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">同时，红魔6系列还是搭载高通全新一代Quick Charge 5技术的游戏手机。Quick Charge 5技术支持超过100W峰值充电功率，充电速度相比上一代方案最多提高4倍。和上一代解决方案相比，Quick Charge 5充电效率最多提高70%，因此可大幅度降低充电时的发热。红魔6系列甚至还支持专门开发的充电分离特性，因此在边充电边游戏的时候机身发了更低，电池寿命也比传统方案下更长。</p><p class=\"p_style\">为了获得最佳的网络效能，新一代的骁龙888移动平台机型们大多支持Wi-Fi 6增强版无线网络连接，红魔6系列机型也不例外。在Wi-Fi 6增强版、网络智能调度算法和腾讯手游加速器联合加持下，红魔6系列在Wi-Fi和5G网络下平均延迟比没有这些新特性的机型低20毫秒。得益于Wi-Fi 6+5G的全面高性能网络通讯技术，红魔6系列可以为用户提供无死角的低延迟在线游戏体验。</p><p class=\"p_style\"><b>ROG游戏手机5系列</b></p><p class=\"p_style\">如果说游戏玩家群体里面某一个品牌代表着“信仰”，那么这个品牌肯定包括ROG。全新的ROG游戏手机5系列包含3款产品，分别是ROG游戏手机5、ROG游戏手机5 Pro和ROG游戏手机5幻影。三款新品均搭载最新的高通骁龙888移动平台，额外的游戏触控按键、6000毫安时电池、65W邮箱快充和侧面+底部双USB Type-C接口。</p><p class=\"jsmargin\"><img alt=\"图片6.png\" src=\"http://pic.zaeke.com/img161614257042653.png\" width=\"1269\" height=\"847\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">ROG游戏手机5系列全系列搭载来自三星的OLED显示屏，采用最新的E4发光材料，屏幕响应时间低至1毫秒，刷新率最高可达144Hz。ROG游戏手机5 Pro和ROG游戏手机5幻影这两款手机还搭载有可自定义的背部OLED显示屏、额外的背部触摸感应区，个性化程度和游戏操控双双拉满。</p><p class=\"p_style\">全新的骁龙888为包括ROG游戏手机5系列在内的产品们带来了第三代Elite Gaming体系。此全新世代标准中高通首次将可变速率着色功能带入移动设备，该特性允许游戏开发者将CPU和GPU资源灵活调配到游戏画面中更重要的部分，可带来最多30%游戏性能提升。在前代Elite Gaming中引入的超平滑游戏帧率功能也获得升级，如今骁龙888可以在最多144fps的游戏中应用游戏平滑功能，兼顾高帧率和高稳定性。</p><p class=\"jsmargin\"><img alt=\"图片7.png\" src=\"http://pic.zaeke.com/img161614258436403.png\" width=\"1117\" height=\"621\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">新一代Elite Gaming体系自然也延续了之前最高HDR品质、端游级游戏特性、超流畅游戏体验、沉浸式游戏音频等功能，这些完善的生态功能圈加上骁龙888芯片强大的性能，新一代的顶级游戏手机会在游戏体验方面更进一步。</p><p class=\"p_style\">当然，iQOO 7、红魔游戏手机6系列、ROG游戏手机5系列不会是2021年游戏手机的全部阵容，还有即将发布的新一代黑鲨游戏手机4系列。目前关于黑鲨游戏手机4系列可以确定的是其也会加入高通骁龙888这个阵营，核心的游戏特色则是全系标配双机械升降实体游戏按钮。</p><p class=\"p_style\">随着智能手机行业的发展，“游戏手机”这个概念已经完全被市场接受，虽然和主流市场比起来依旧显得非常小众，可这并没有妨碍“游戏手机”这个小圈子里的产品推陈出新蓬勃发展。甚至在一部分人群，比如学生群体中，售价亲民又提供极致游戏体验的游戏手机们更是大受欢迎。国内电子竞技的正规化、大众化，加上大量作品推出移动版，更是让游戏手机这个小市场在整个智能手机市场中显得格外耀眼。</p><p class=\"p_style\">也许今年，我们会看见更多人为了游戏而买一台游戏手机。</p>', 'http://pic.zaeke.com/img161665778882128.jpeg', 21395, 0, 0, NULL, '2021-03-19 00:31:07', '2021-03-27 07:45:52');
INSERT INTO `article` VALUES (16189, 'OPPO Find X3 Pro 评测：无惧争议，深陷争议，直面争议', '“Find不应该惧怕争议”，可能正是有这一句话作为纲领或者是挡箭牌，所以OPPO的产品经理们才能够打造出Find系列，并且有勇气推出市场。Find系列的每一款产...', '[object Object]', 'victory3332', 31, 'http://pic.zaeke.com/user_1533281475_1.png', '<p>“<b>Find 不应该惧怕争议</b>”，可能正是有这一句话作为纲领或者是挡箭牌，所以 OPPO 的产品经理们才能够打造出 Find 系列，并且有勇气推出市场。</p><p>Find 系列的每一款产品确实都有所争议，比如当时全球最薄的 Finder 被人质疑可靠性；比如搭载 VOOC 闪充的 Find 7 被人质疑通用性；比如采用升降结构的 Find X 被人质疑耐久性。哪怕是看起来堆料到极致的 Find X2 Pro，都有人怀疑其产品力能否支撑起高昂的上市售价。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img161631358895905.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>Find X3 以及 Find X3 Pro 依旧如此，OPPO 的产品经理们还是那么的有任性以及有勇气，在这款产品里面做了很多做起来很吃力，可是却会被不少人说“可以但没必要”的东西。</p><p><b>这个后盖真的很吃力，但......</b></p><p>超过 2000 个后盖控制点，超过 700 个镜组控制点，1 整块玻璃，用 40 个小时制造。OPPO Find X3 系列要是放到某个友商手里，那么整场发布会或者是产品 Slogan 大概就要变成“一块玻璃的艺术之旅”。</p><p>OPPO Find X3 系列从意外曝光的第一秒开始直到发布会正式开幕，再到现在，这个玻璃后盖一直有着相当的存在感。OPPO 官方那边呢，是由衷地对自己处理玻璃后盖的工艺以及最终呈现的结果感到自豪，而吃瓜网友嘛……</p><p>“这就是iPhone 13吗？”</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img161631361544729.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>很遗憾，这是个无法回避且必须要正面回应的问题。OPPO Find X3 系列的后置摄像头的开孔以及排列实在是太像 iPhone 12 Pro 系列了。确实可以三颗摄像头本身的排布方式上，OPPO 做的是由上至下右左右，苹果则是由上至下左右左，可改变不了 Find X3 系列的后置摄像头与 iPhone 形似的事实。</p><p>除了摄像头之外，还有相当多的言论火力集中在了 OPPO 这次的 3D 曲面后盖设计上。OPPO 做出这种设计的本意，是在机身内塞进巨大摄像头模组之后去平衡摄像头部分凸出和机身其他部分的厚度。在这一方面 OPPO 显然是走了最难的那条路，实际上只要整体外观协调并且拍照素质足够好，消费者对于摄像头的凸起是可以接受并且理解的。</p><p>在 OPPO Find X3 系列之前，ZAEKE 知客认为这一方面处理得最好的是 vivo 的“云阶”设计，其中 vivo X60 Pro 这款机器的后置相机模组和纤薄机身之间的处理我愿称之为年度最佳。这款机型的后置摄像头设计就是那种典型的讨巧设计，vivo 的“云阶”设计非常直接、成本相对不高，但同时又优雅大方且富有质感。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img161631363645562.jpeg\" width=\"2000\" height=\"1127\"><br></p><p><b>然而 OPPO 说不，他们要走最难的那条路</b>。</p><p>OPPO 不但要用一整块玻璃，还要用连续曲面这种连建模师听了都感觉头疼的东西。因此前面所说的超过 2000 个控制点等等东西就成了必要，毕竟我觉得就算是 Find X3 系列的设计师自己用都不一定能完美地手绘出 Find X3 系列的后盖曲线。至于成本核算、生产、品控等等的同事看了设计稿大概率是要在心里骂娘，然后礼貌而不失风度地在办公 OA 里面说一句“我们尽力”。</p><p>所以最终结果呢？确实牛逼。从视觉效果这个角度出发，我们拿到的这台白色款（官方叫凝白）的 Find X3 Pro 的真机甚至让人感到有点惊艳，而这种惊艳在官方贴出来的渲染图里面完全看不出。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img161631365986547.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>为啥呢，因为无论是在曝光还是正式发布后的官方效果图里，OPPO 为了强调自己在后盖工艺上花的心思而特别用光影去强调了 Find X3 系列的后盖的弧面。甚至官方还把采用亮面工艺的黑色版本放在 C 位，这种强调使得这个弧面以及本身摄像头模组的凸出看起来很高。</p><p>可实际上 Find X3 系列，尤其是采用 AG 玻璃的白色款，很难在任何光源下一眼看出后置摄像头明显凸出。OPPO 这个拥有复杂曲面的高成本后盖，在搭配 AG 工艺的时候，起到了很好的“遮瑕”效果。可能是为了让这个“遮瑕”更彻底，OPPO 不像其他一些 Android 厂商一样在后置相机附近标注各种参数，<strike>这看起来更像 iPhone 了呀</strike>。</p><p>同样的视觉效果也可以类比到同样是 AG 玻璃的“雾蓝”或者是擦的很干净的“镜黑”，可说实话让我选我还是会选白色。</p><p>所以从看到 OPPO Find X3 系列曝光图到见到真机再到拿在手里把玩，我个人对这台手机的好感度一直在上升。看见曝光图的时候心里的想法是“OPPO 又喝假酒了”，见着白色款真机的时候心里的想法是“其实还可以啦”，然后真正拿着用的时候感觉就成了“哎呀真 nice”。</p><p>甚至从一个多年给数码产品拍图的人角度出发，相比起其他厂商的后置摄像头有各种落差能藏灰，OPPO 这曲面设计甚至帮我省了不少擦灰尘的功夫。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img161631374943773.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>当然“像 iPhone”这件事情终究还是过不去的坎，当然不排除 OPPO 选用这种堆叠是因为内部结构空间堆叠所限巴拉巴拉。所以要是 Find X3 系列这几个摄像头能像上一代，也就是 Find X2 Pro 那样一字排开就好了，不过“Find 不应该惧怕争议”嘛。</p><p><b>人心都没长在正中间，这世界上会有真正“双主摄”？</b></p><p>智能手机圈里有一个词叫“双主摄”，这个词或这个词后面的概念不排除是被 OPPO 带起来的。Find X2 Pro其实就可以算是一款“双主摄”机型，在整个智能手机市场里面，三星也算是在玩“双主摄”。</p><p>从严格意义上来说，手机上其实不存在绝对的“双主摄”，甚至人的两颗眼睛里面也有主次之分。因此 ZAEKE 知客更倾向于兼“双主摄”理解为“搭载多于 1 颗拥有‘主摄级别’性能的相机”，在 OPPO Find X3 Pro 以及 Find X3 这两款机型上，即是超广角和广角相机。首先最明面的硬件参数，主摄像头与超广角摄像头均采用索尼 IMX766 传感器，其中主摄像头支持光学防抖，非常“常规”。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img161631377165393.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>然后那个超广角相机就有趣了，首先 OPPO 宣称 Find X3 系列的超广角拥有业界最大的传感器（1/1.56 英寸），最高的像素数量（5000 万像素）。然后很少见的，OPPO Find 系列的超广角还用上了 7P 镜头——甚至比真·主摄还多一片。</p><p>Find X3 系列的超广角还加入了自由曲面镜片。自由曲面镜片因为表面的曲面做成曲率渐变的形状，而非像普通镜片的曲率保持一定。所以和普通的球面镜片比起来，自由曲率镜片的优点在于能从硬件层面降低画面的畸变，缺点嘛，自然就是成本更高喽。</p><p>因为时间和篇幅的关系，我们会在后续对 Find X3 Pro 的相机系统进行全面和深度的评测，在这儿就还是以我个人的主观体验为主啦。</p><p style=\"text-align: center;\"><img alt=\"ZOOM.gif\" src=\"http://pic.zaeke.com/img161631435996034.gif\" width=\"450\" height=\"201\"><br></p><p><b>不过比起拍照具体品质，OPPO Find X3 Pro 给我的第一印象反而是绝佳拍照体验</b>。除了装满了各种功能的专业模式和 10bit 照片模式，OPPO 这次将多摄像头之间的切换的顺滑度做到了 Android 业界数一数二的水平。从超广角连续放大至长焦相机的过程非常顺滑，在放大过程中也没有明显的白平衡跳跃。</p><p>不过在这还是来个“免责条款”——以上我们说的“绝佳体验”仅针对骁龙 888 移动平台的 Find X3 Pro。同样评价不一定完全适用骁龙 870 移动平台的 Find X3，毕竟骁龙 888 是骁龙家族里面唯一一颗拥有 3-ISP 的移动平台，因此图像处理性能会更好。</p><p><img alt=\"IMG20210101014951.jpg\" src=\"http://pic.zaeke.com/img161631573163277.jpeg\" width=\"2000\" height=\"1500\"></p><p><img alt=\"\" src=\"http://pic.zaeke.com/img161631555579429.jpeg\" width=\"2000\" height=\"1500\"></p><p style=\"text-align: center;\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631558790488.jpeg\" width=\"2000\" height=\"1500\"><br></p><p style=\"text-align: center;\"><img alt=\"IMG20210304122843.jpg\" src=\"http://pic.zaeke.com/img161631559245908.jpeg\" width=\"2000\" height=\"1500\"></p><p style=\"text-align: center;\"><img alt=\"IMG20210316185259.jpg\" src=\"http://pic.zaeke.com/img161631566314586.jpeg\" width=\"2000\" height=\"1500\"><br></p><p style=\"text-align: center;\"><img alt=\"IMG20210318221411.jpg\" src=\"http://pic.zaeke.com/img161631579638356.jpeg\" width=\"2000\" height=\"1500\"><br></p><p style=\"text-align: center;\"><img alt=\"IMG20210316185106.jpg\" src=\"http://pic.zaeke.com/img161631580940199.jpeg\" width=\"2000\" height=\"1500\"><br></p><p style=\"text-align: center;\"><img alt=\"IMG20210316164936.jpg\" src=\"http://pic.zaeke.com/img161631582427761.jpeg\" width=\"562.6515763945029\" height=\"750\"><br></p><p style=\"text-align: center;\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631555652592.jpeg\" width=\"562.6515763945029\" height=\"750\"><br></p><p><b>OPPO Find X3 Pro 完全具有竞争 ZAEKE 知客 2021 年最佳拍照手机的能力</b>，因为其在 Find X2 Pro 优良画面观感之基础上大幅度强化了主摄及超广角的画面细节、锐度，因此 Find X3 Pro 的“双主摄”可以说是观感画质两开花。另外据详细评测过 Find X2 Pro 的孙师傅反映，Find X3 Pro 在更新超广角相机硬件之后也消除了此前超广角成片偏绿的毛病。</p><p>简单的试拍之后，我愿称 Find X3 Pro 为最强。</p><p>可横向对比上一代，Find X3 Pro 反而在长焦这边开了个倒车。Find X2 Pro 装载了一颗潜望式的 5x长焦相机，而 Find X3 Pro 则是一颗等效 2x 的普通长焦相机。</p><p>那啥，我个人对于潜望式长焦其实没有什么追求。毕竟潜望式长焦这东西除了在发布会上用超高倍率数字变焦吊打友商，或是在 DxOMark 上刷分之外，真实的用户甚少能从中获益。这也是 OPPO 的口径，那就是潜望式长焦在消费者里的使用率较低，因此在这一代取消了。</p><p>可是讲道理，Find X3 Pro 这颗长焦相机毕竟等效只有 2x 且不支持光学防抖，因此在室内拍摄静物或是拍夜景的时候局限性就比较大。</p><p style=\"text-align: center;\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631586849673.jpeg\" width=\"562.6515763945029\" height=\"750\"></p><p style=\"text-align: center;\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631586828570.jpeg\" width=\"562.6515763945029\" height=\"750\"></p><p style=\"text-align: center;\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631595151222.jpeg\" width=\"2000\" height=\"1500\"><br></p><p><img alt=\"\" src=\"http://pic.zaeke.com/img161631592826188.jpeg\" width=\"2000\" height=\"1500\"></p><p style=\"text-align: center;\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631589574148.jpeg\" width=\"2000\" height=\"1500\"><br></p><p style=\"text-align: center;\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631590223576.jpeg\" width=\"2000\" height=\"1500\"><br></p><p>不过好消息是 Find X3 Pro 这颗等效 2x 长焦画质也还 OK，虽然其像素数量只有 800 万，可依旧经得起放大观看细节。长焦相机整体成像品质非常稳定，最近对焦距离也足够日常的静物拍摄。非要鸡蛋里挑点骨头，那就是和主摄以及超广角相机比起来，这颗长焦相机的成像白平衡稍微有轻微的偏冷色。</p><p>可 Find X3 Pro 的长焦相机终究只有 2x 且没有光学防抖，因此其在长焦端的实用性以及多场景适应性还是相对偏弱些。在夜间拍摄的时候，也能明显感觉到这颗 2x 相机在感光能力上的短板。在中等至稍微不良光线环境中，Find X3 Pro 会比其他机型（比如搭载有光学防抖长焦的一加 8 Pro）更早地切换到主摄数字变焦。</p><p>Find X3 Pro 上另一个小特色是“显微镜头”。这颗镜头只有 300 万像素并且还是定焦，不过其拥有高达 30 倍的放大倍率，搭配数字变焦可以实现等效 60 倍放大。这是类似相机第一次出现在手机上，不知道这和姜文老师有没有啥直接关系。</p><p>等等，按照贵 ZAEKE 知客标准，这样的“显微镜头”难道不应该是“凑数”吗？</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img161631446016796.jpeg\" width=\"2000\" height=\"1127\"><br></p><p><img alt=\"\" src=\"http://pic.zaeke.com/img161631447445215.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>嗯……算是，不过 OPPO 这个“数”凑得比较聪明讨巧，至少对于许多数码博主来说这个镜头不算凑数。</p><p>因为这个镜头是定焦的关系，所以使用这个镜头拍摄的时候需要将整个手机贴在被摄物体上，然后通过倾斜手机的方式进行手动“对焦”。为了解决手机紧贴被摄物的补光问题，OPPO 还专门在这个显微镜头周围加了一圈 LED 补光灯。因此这个显微镜头确实是能拍出些东西来的，虽然画质一般般可胜在好玩，平时拿来拍拍皮肤、纸币、花瓣等等的细微纹理很有趣。</p><p style=\"text-align: center;\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631604034312.jpeg\" width=\"750\" height=\"750\"></p><p style=\"text-align: center;\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631604041209.jpeg\" width=\"750\" height=\"750\"></p><p style=\"text-align: center;\"><img alt=\"IMG20210101025528.jpg\" src=\"http://pic.zaeke.com/img161631604054666.jpeg\" width=\"750\" height=\"750\"><br></p><p>当然，对于很多数码博主来说，这个镜头最大的价值在于可以直接拍出来绝大部分屏幕的次像素排列。在手动调整对焦和亮度之后效果甚至还不错，应该可以免去自带显微镜的烦恼。将很多我们日常生活中司空见惯的东西“超级放大”之后，甚至有点梦幻的感觉。</p><p>这不比那些单纯的凑数微距强得多啦？至于这个显微镜和潜望式长焦哪个使用率会更高以及如何取舍，那就是见仁见智的事情，反正“Find 不应该惧怕争议”嘛。</p><p><b>并不完美的 10bit</b></p><p>OPPO Find X3 系列的发布会上，有一句话他们总是挂在嘴边，那就是“全链路10bit”。</p><p>说白了，就是 Find X3 Pro 的相机支持保存 10bit 的图片，屏幕支持原生 10bit 色深，系统支持 10bit 回放。然后 OPPO 进一步和微博、哔哩哔哩这两个平台合作，让 Find X3 Pro 拍摄的 10bit HDR 内容上传至平台，并且在 Find X3 Pro 上这块支持原生 10bit 的屏幕上无损回放。</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img161631449639115.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>听起来很好对吧？可实际上并没有那么美好，毕竟这世界上还有个叫 Windows 10 的东西。</p><p>因为 Find X3 Pro 如果想要保存 10bit 照片，那么就必须要使用 HEIF 格式照片。而 Windows 10 自带的“图库”应用居然无法识别 HEIF 照片，哪怕是安装了微软应用商店里提供的 HEIF 扩展插件也无法打开。进一步阅读 HEIF 插件的描述发现，要想在 Windows 10 电脑看 HEIF 照片，那么除了 HEIF 插件本身还要安装微软的 HEVC 插件，而后者……是收费的。</p><p>虽然只要 7 块钱，不过……微软你真棒！</p><p><img alt=\"\" src=\"http://pic.zaeke.com/img161631450816059.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>那如果是分享给别人呢？ZAEKE 知客这试了下红黄大眼夹、绿白小而美以及红白黑企鹅这三款相对来说比较常用的软件。</p><p>这三款软件都可以正常读取图库里以 HEIF 格式存储的照片，其中大眼夹和小而美都可以顺利完成 HEIF 照片的分享。不过需要指出的是在小而美里将 HEIF 照片分享给好友或 PC 端，好友或 PC 端再保存该照片时，照片就已经是被自动转换为 jpg 格式。</p><p>至于红围脖企鹅的状况就比较惨烈，Find X3 Pro 的 HEIF 照片如果使用手机 PC 互传功能传输至 PC，那么就会被企鹅消化成有双重后缀名的文件。可这个双重后缀名根本无法打开，看来是企鹅消化不良。同样的情况会不管是在企鹅软件本身的分享渠道，或是 Find X3 Pro 的图库分享渠道都会出现。</p><p style=\"text-align: center;\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631454725676.jpeg\" width=\"313.3622768933912\" height=\"700\"><br></p><p>所以为了尽可能地消灭兼容性问题，Find X3 Pro 在自带图库的设置里面有一个 HEIF 自动转格式的开关。在这个开关未开启的时候使用蓝牙发送 HEIF 照片，Find X3 Pro 甚至还会超贴心地弹出个窗口提示。可不知道为什么这个选项依旧对企鹅软件不起作用，哪怕是打开这个选项，被分享到企鹅的照片依旧是 HEIF 格式。</p><p>总而言之，这个 10bit 用起来还是有用的，只不过对于大多数人来说可能就停留在有这个功能。</p><p><b>权衡，权衡，权衡</b></p><p>不管 OPPO 自己在上市前预热宣传片里怎么说，Find X3 Pro 其实是一款有很多“妥协”的手机，而这些“妥协”的目的自然是降低售价。这台手机比上一代砍掉了潜望式长焦，将 X 轴线性马达换成了 Z轴，然后不带 Pro 的版本用的是骁龙 870 移动平台而非骁龙 888 移动平台。</p><p>为什么？你还好意思问？去年的 Find X2 Pro 很极致，但<strike>降价前</strike>你们买了吗？</p><p><img alt=\"_1094677.jpg\" src=\"http://pic.zaeke.com/img161631462263586.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>所以商业是很现实的，梦想、情怀和追求都要向现实低头。OPPO 的 Find X2 Pro 用当时几乎最猛的料做出了最好的国产智能手机。可发布时的高昂定价显然吓退了不少人，加之一些硬件不足（比如没有无线充电和续航一般）和时运不济，最终成了个叫好不叫座的悲情角色。</p><p>Find X3 系列很聪明地保留 2K/120Hz 屏幕以及 65W Super VOOC 超级闪充这两个很核心的优势。然后采用更大的电池以及终于加入 30W AirVOOC 无线充电，将上一代最明显的短板给补好了。按照我的个人使用习惯来说，充一次电可以正常在 5G 网络下用一天，要是你在桌面上放个无线充电底座，那 Find X3 Pro 几乎可以做到“永不断电”。</p><p><img alt=\"_1094697.jpg\" src=\"http://pic.zaeke.com/img161631463543074.jpeg\" width=\"2000\" height=\"1127\"><br></p><p>补齐短板之外， Find X3 系列还进一步扩大了屏幕的领先优势，甚至可以说它拥有目前最好的手机屏幕之一。采用了 LTPO 技术和超宽幅的可变刷新率，可以根据使用情况在 1~120Hz 之间切换，保证使用体验的同时降低功耗。在此基础上 Find X3 Pro 的屏幕的触控响应速度和触控精确度也极好。因此在日常打字、滑动列表等操作过程中手感极佳，甚至可以媲美某些号称有超高触控采样率和超高屏幕刷新率的游戏手机。</p><p>总之，Find X3 Pro 用起来真的太爽了，<b>除了那个 Z 轴马达还需要 OPPO 好好努力调校之外</b>。</p><p>所以觉得 ZAEKE 知客是推荐你们购买 OPPO Find X3 Pro 么？别那么肤浅。最好的选择当然是 Find X3，除非你并不在意那千把块钱，或者是愿意用这千把块钱换取 IP68 防水防尘以及骁龙 888 移动平台的 ISP 和对将来 5G 网络新技术的支持。</p><p><img alt=\"DSC04087.jpg\" src=\"http://pic.zaeke.com/img161633708647056.jpeg\" width=\"1400\" height=\"933\"><br></p><p>抛开这一点，Find X3 简直香爆了。价格低一截，可无论是那块很带劲的后盖曲面玻璃、超强的屏幕、有线无线都快充等，都是与“Pro”同款。然后因为骁龙 870 移动平台在现阶段，功耗、发热和性能平衡得更好的关系，能在提供几乎相同日常使用体验的前提下有效延长手机续航。至于骁龙 888 移动平台和骁龙 870 移动平台因为 ISP 可能导致成像表现这件事情嘛……我们觉得大部分非专业人士应该感觉不出来二者的区别。</p><p>所以，你看懂 OPPO 的“最优解”了吗？</p>', '<p class=\"p_style\">“<b>Find 不应该惧怕争议</b>”，可能正是有这一句话作为纲领或者是挡箭牌，所以 OPPO 的产品经理们才能够打造出 Find 系列，并且有勇气推出市场。</p><p class=\"p_style\">Find 系列的每一款产品确实都有所争议，比如当时全球最薄的 Finder 被人质疑可靠性；比如搭载 VOOC 闪充的 Find 7 被人质疑通用性；比如采用升降结构的 Find X 被人质疑耐久性。哪怕是看起来堆料到极致的 Find X2 Pro，都有人怀疑其产品力能否支撑起高昂的上市售价。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631358895905.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">Find X3 以及 Find X3 Pro 依旧如此，OPPO 的产品经理们还是那么的有任性以及有勇气，在这款产品里面做了很多做起来很吃力，可是却会被不少人说“可以但没必要”的东西。</p><p class=\"p_style\"><b>这个后盖真的很吃力，但......</b></p><p class=\"p_style\">超过 2000 个后盖控制点，超过 700 个镜组控制点，1 整块玻璃，用 40 个小时制造。OPPO Find X3 系列要是放到某个友商手里，那么整场发布会或者是产品 Slogan 大概就要变成“一块玻璃的艺术之旅”。</p><p class=\"p_style\">OPPO Find X3 系列从意外曝光的第一秒开始直到发布会正式开幕，再到现在，这个玻璃后盖一直有着相当的存在感。OPPO 官方那边呢，是由衷地对自己处理玻璃后盖的工艺以及最终呈现的结果感到自豪，而吃瓜网友嘛……</p><p class=\"p_style\">“这就是iPhone 13吗？”</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631361544729.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">很遗憾，这是个无法回避且必须要正面回应的问题。OPPO Find X3 系列的后置摄像头的开孔以及排列实在是太像 iPhone 12 Pro 系列了。确实可以三颗摄像头本身的排布方式上，OPPO 做的是由上至下右左右，苹果则是由上至下左右左，可改变不了 Find X3 系列的后置摄像头与 iPhone 形似的事实。</p><p class=\"p_style\">除了摄像头之外，还有相当多的言论火力集中在了 OPPO 这次的 3D 曲面后盖设计上。OPPO 做出这种设计的本意，是在机身内塞进巨大摄像头模组之后去平衡摄像头部分凸出和机身其他部分的厚度。在这一方面 OPPO 显然是走了最难的那条路，实际上只要整体外观协调并且拍照素质足够好，消费者对于摄像头的凸起是可以接受并且理解的。</p><p class=\"p_style\">在 OPPO Find X3 系列之前，ZAEKE 知客认为这一方面处理得最好的是 vivo 的“云阶”设计，其中 vivo X60 Pro 这款机器的后置相机模组和纤薄机身之间的处理我愿称之为年度最佳。这款机型的后置摄像头设计就是那种典型的讨巧设计，vivo 的“云阶”设计非常直接、成本相对不高，但同时又优雅大方且富有质感。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631363645562.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\"><b>然而 OPPO 说不，他们要走最难的那条路</b>。</p><p class=\"p_style\">OPPO 不但要用一整块玻璃，还要用连续曲面这种连建模师听了都感觉头疼的东西。因此前面所说的超过 2000 个控制点等等东西就成了必要，毕竟我觉得就算是 Find X3 系列的设计师自己用都不一定能完美地手绘出 Find X3 系列的后盖曲线。至于成本核算、生产、品控等等的同事看了设计稿大概率是要在心里骂娘，然后礼貌而不失风度地在办公 OA 里面说一句“我们尽力”。</p><p class=\"p_style\">所以最终结果呢？确实牛逼。从视觉效果这个角度出发，我们拿到的这台白色款（官方叫凝白）的 Find X3 Pro 的真机甚至让人感到有点惊艳，而这种惊艳在官方贴出来的渲染图里面完全看不出。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631365986547.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">为啥呢，因为无论是在曝光还是正式发布后的官方效果图里，OPPO 为了强调自己在后盖工艺上花的心思而特别用光影去强调了 Find X3 系列的后盖的弧面。甚至官方还把采用亮面工艺的黑色版本放在 C 位，这种强调使得这个弧面以及本身摄像头模组的凸出看起来很高。</p><p class=\"p_style\">可实际上 Find X3 系列，尤其是采用 AG 玻璃的白色款，很难在任何光源下一眼看出后置摄像头明显凸出。OPPO 这个拥有复杂曲面的高成本后盖，在搭配 AG 工艺的时候，起到了很好的“遮瑕”效果。可能是为了让这个“遮瑕”更彻底，OPPO 不像其他一些 Android 厂商一样在后置相机附近标注各种参数，<strike>这看起来更像 iPhone 了呀</strike>。</p><p class=\"p_style\">同样的视觉效果也可以类比到同样是 AG 玻璃的“雾蓝”或者是擦的很干净的“镜黑”，可说实话让我选我还是会选白色。</p><p class=\"p_style\">所以从看到 OPPO Find X3 系列曝光图到见到真机再到拿在手里把玩，我个人对这台手机的好感度一直在上升。看见曝光图的时候心里的想法是“OPPO 又喝假酒了”，见着白色款真机的时候心里的想法是“其实还可以啦”，然后真正拿着用的时候感觉就成了“哎呀真 nice”。</p><p class=\"p_style\">甚至从一个多年给数码产品拍图的人角度出发，相比起其他厂商的后置摄像头有各种落差能藏灰，OPPO 这曲面设计甚至帮我省了不少擦灰尘的功夫。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631374943773.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">当然“像 iPhone”这件事情终究还是过不去的坎，当然不排除 OPPO 选用这种堆叠是因为内部结构空间堆叠所限巴拉巴拉。所以要是 Find X3 系列这几个摄像头能像上一代，也就是 Find X2 Pro 那样一字排开就好了，不过“Find 不应该惧怕争议”嘛。</p><p class=\"p_style\"><b>人心都没长在正中间，这世界上会有真正“双主摄”？</b></p><p class=\"p_style\">智能手机圈里有一个词叫“双主摄”，这个词或这个词后面的概念不排除是被 OPPO 带起来的。Find X2 Pro其实就可以算是一款“双主摄”机型，在整个智能手机市场里面，三星也算是在玩“双主摄”。</p><p class=\"p_style\">从严格意义上来说，手机上其实不存在绝对的“双主摄”，甚至人的两颗眼睛里面也有主次之分。因此 ZAEKE 知客更倾向于兼“双主摄”理解为“搭载多于 1 颗拥有‘主摄级别’性能的相机”，在 OPPO Find X3 Pro 以及 Find X3 这两款机型上，即是超广角和广角相机。首先最明面的硬件参数，主摄像头与超广角摄像头均采用索尼 IMX766 传感器，其中主摄像头支持光学防抖，非常“常规”。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631377165393.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">然后那个超广角相机就有趣了，首先 OPPO 宣称 Find X3 系列的超广角拥有业界最大的传感器（1/1.56 英寸），最高的像素数量（5000 万像素）。然后很少见的，OPPO Find 系列的超广角还用上了 7P 镜头——甚至比真·主摄还多一片。</p><p class=\"p_style\">Find X3 系列的超广角还加入了自由曲面镜片。自由曲面镜片因为表面的曲面做成曲率渐变的形状，而非像普通镜片的曲率保持一定。所以和普通的球面镜片比起来，自由曲率镜片的优点在于能从硬件层面降低画面的畸变，缺点嘛，自然就是成本更高喽。</p><p class=\"p_style\">因为时间和篇幅的关系，我们会在后续对 Find X3 Pro 的相机系统进行全面和深度的评测，在这儿就还是以我个人的主观体验为主啦。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"ZOOM.gif\" src=\"http://pic.zaeke.com/img161631435996034.gif\" width=\"450\" height=\"201\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\"><b>不过比起拍照具体品质，OPPO Find X3 Pro 给我的第一印象反而是绝佳拍照体验</b>。除了装满了各种功能的专业模式和 10bit 照片模式，OPPO 这次将多摄像头之间的切换的顺滑度做到了 Android 业界数一数二的水平。从超广角连续放大至长焦相机的过程非常顺滑，在放大过程中也没有明显的白平衡跳跃。</p><p class=\"p_style\">不过在这还是来个“免责条款”——以上我们说的“绝佳体验”仅针对骁龙 888 移动平台的 Find X3 Pro。同样评价不一定完全适用骁龙 870 移动平台的 Find X3，毕竟骁龙 888 是骁龙家族里面唯一一颗拥有 3-ISP 的移动平台，因此图像处理性能会更好。</p><p class=\"jsmargin\"><img alt=\"IMG20210101014951.jpg\" src=\"http://pic.zaeke.com/img161631573163277.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"></p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631555579429.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631558790488.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"><br></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"IMG20210304122843.jpg\" src=\"http://pic.zaeke.com/img161631559245908.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"IMG20210316185259.jpg\" src=\"http://pic.zaeke.com/img161631566314586.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"><br></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"IMG20210318221411.jpg\" src=\"http://pic.zaeke.com/img161631579638356.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"><br></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"IMG20210316185106.jpg\" src=\"http://pic.zaeke.com/img161631580940199.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"><br></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"IMG20210316164936.jpg\" src=\"http://pic.zaeke.com/img161631582427761.jpeg\" width=\"562.6515763945029\" height=\"750\" style=\"margin: 0px auto; height: auto;\"><br></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631555652592.jpeg\" width=\"562.6515763945029\" height=\"750\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\"><b>OPPO Find X3 Pro 完全具有竞争 ZAEKE 知客 2021 年最佳拍照手机的能力</b>，因为其在 Find X2 Pro 优良画面观感之基础上大幅度强化了主摄及超广角的画面细节、锐度，因此 Find X3 Pro 的“双主摄”可以说是观感画质两开花。另外据详细评测过 Find X2 Pro 的孙师傅反映，Find X3 Pro 在更新超广角相机硬件之后也消除了此前超广角成片偏绿的毛病。</p><p class=\"p_style\">简单的试拍之后，我愿称 Find X3 Pro 为最强。</p><p class=\"p_style\">可横向对比上一代，Find X3 Pro 反而在长焦这边开了个倒车。Find X2 Pro 装载了一颗潜望式的 5x长焦相机，而 Find X3 Pro 则是一颗等效 2x 的普通长焦相机。</p><p class=\"p_style\">那啥，我个人对于潜望式长焦其实没有什么追求。毕竟潜望式长焦这东西除了在发布会上用超高倍率数字变焦吊打友商，或是在 DxOMark 上刷分之外，真实的用户甚少能从中获益。这也是 OPPO 的口径，那就是潜望式长焦在消费者里的使用率较低，因此在这一代取消了。</p><p class=\"p_style\">可是讲道理，Find X3 Pro 这颗长焦相机毕竟等效只有 2x 且不支持光学防抖，因此在室内拍摄静物或是拍夜景的时候局限性就比较大。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631586849673.jpeg\" width=\"562.6515763945029\" height=\"750\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631586828570.jpeg\" width=\"562.6515763945029\" height=\"750\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631595151222.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631592826188.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631589574148.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"><br></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631590223576.jpeg\" width=\"2000\" height=\"1500\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">不过好消息是 Find X3 Pro 这颗等效 2x 长焦画质也还 OK，虽然其像素数量只有 800 万，可依旧经得起放大观看细节。长焦相机整体成像品质非常稳定，最近对焦距离也足够日常的静物拍摄。非要鸡蛋里挑点骨头，那就是和主摄以及超广角相机比起来，这颗长焦相机的成像白平衡稍微有轻微的偏冷色。</p><p class=\"p_style\">可 Find X3 Pro 的长焦相机终究只有 2x 且没有光学防抖，因此其在长焦端的实用性以及多场景适应性还是相对偏弱些。在夜间拍摄的时候，也能明显感觉到这颗 2x 相机在感光能力上的短板。在中等至稍微不良光线环境中，Find X3 Pro 会比其他机型（比如搭载有光学防抖长焦的一加 8 Pro）更早地切换到主摄数字变焦。</p><p class=\"p_style\">Find X3 Pro 上另一个小特色是“显微镜头”。这颗镜头只有 300 万像素并且还是定焦，不过其拥有高达 30 倍的放大倍率，搭配数字变焦可以实现等效 60 倍放大。这是类似相机第一次出现在手机上，不知道这和姜文老师有没有啥直接关系。</p><p class=\"p_style\">等等，按照贵 ZAEKE 知客标准，这样的“显微镜头”难道不应该是“凑数”吗？</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631446016796.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631447445215.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">嗯……算是，不过 OPPO 这个“数”凑得比较聪明讨巧，至少对于许多数码博主来说这个镜头不算凑数。</p><p class=\"p_style\">因为这个镜头是定焦的关系，所以使用这个镜头拍摄的时候需要将整个手机贴在被摄物体上，然后通过倾斜手机的方式进行手动“对焦”。为了解决手机紧贴被摄物的补光问题，OPPO 还专门在这个显微镜头周围加了一圈 LED 补光灯。因此这个显微镜头确实是能拍出些东西来的，虽然画质一般般可胜在好玩，平时拿来拍拍皮肤、纸币、花瓣等等的细微纹理很有趣。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631604034312.jpeg\" width=\"750\" height=\"750\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631604041209.jpeg\" width=\"750\" height=\"750\" style=\"margin: 0px auto; height: auto;\"></p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"IMG20210101025528.jpg\" src=\"http://pic.zaeke.com/img161631604054666.jpeg\" width=\"750\" height=\"750\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">当然，对于很多数码博主来说，这个镜头最大的价值在于可以直接拍出来绝大部分屏幕的次像素排列。在手动调整对焦和亮度之后效果甚至还不错，应该可以免去自带显微镜的烦恼。将很多我们日常生活中司空见惯的东西“超级放大”之后，甚至有点梦幻的感觉。</p><p class=\"p_style\">这不比那些单纯的凑数微距强得多啦？至于这个显微镜和潜望式长焦哪个使用率会更高以及如何取舍，那就是见仁见智的事情，反正“Find 不应该惧怕争议”嘛。</p><p class=\"p_style\"><b>并不完美的 10bit</b></p><p class=\"p_style\">OPPO Find X3 系列的发布会上，有一句话他们总是挂在嘴边，那就是“全链路10bit”。</p><p class=\"p_style\">说白了，就是 Find X3 Pro 的相机支持保存 10bit 的图片，屏幕支持原生 10bit 色深，系统支持 10bit 回放。然后 OPPO 进一步和微博、哔哩哔哩这两个平台合作，让 Find X3 Pro 拍摄的 10bit HDR 内容上传至平台，并且在 Find X3 Pro 上这块支持原生 10bit 的屏幕上无损回放。</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631449639115.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">听起来很好对吧？可实际上并没有那么美好，毕竟这世界上还有个叫 Windows 10 的东西。</p><p class=\"p_style\">因为 Find X3 Pro 如果想要保存 10bit 照片，那么就必须要使用 HEIF 格式照片。而 Windows 10 自带的“图库”应用居然无法识别 HEIF 照片，哪怕是安装了微软应用商店里提供的 HEIF 扩展插件也无法打开。进一步阅读 HEIF 插件的描述发现，要想在 Windows 10 电脑看 HEIF 照片，那么除了 HEIF 插件本身还要安装微软的 HEVC 插件，而后者……是收费的。</p><p class=\"p_style\">虽然只要 7 块钱，不过……微软你真棒！</p><p class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631450816059.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">那如果是分享给别人呢？ZAEKE 知客这试了下红黄大眼夹、绿白小而美以及红白黑企鹅这三款相对来说比较常用的软件。</p><p class=\"p_style\">这三款软件都可以正常读取图库里以 HEIF 格式存储的照片，其中大眼夹和小而美都可以顺利完成 HEIF 照片的分享。不过需要指出的是在小而美里将 HEIF 照片分享给好友或 PC 端，好友或 PC 端再保存该照片时，照片就已经是被自动转换为 jpg 格式。</p><p class=\"p_style\">至于红围脖企鹅的状况就比较惨烈，Find X3 Pro 的 HEIF 照片如果使用手机 PC 互传功能传输至 PC，那么就会被企鹅消化成有双重后缀名的文件。可这个双重后缀名根本无法打开，看来是企鹅消化不良。同样的情况会不管是在企鹅软件本身的分享渠道，或是 Find X3 Pro 的图库分享渠道都会出现。</p><p style=\"text-align: center;\" class=\"jsmargin\"><img alt=\"\" src=\"http://pic.zaeke.com/img161631454725676.jpeg\" width=\"313.3622768933912\" height=\"700\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">所以为了尽可能地消灭兼容性问题，Find X3 Pro 在自带图库的设置里面有一个 HEIF 自动转格式的开关。在这个开关未开启的时候使用蓝牙发送 HEIF 照片，Find X3 Pro 甚至还会超贴心地弹出个窗口提示。可不知道为什么这个选项依旧对企鹅软件不起作用，哪怕是打开这个选项，被分享到企鹅的照片依旧是 HEIF 格式。</p><p class=\"p_style\">总而言之，这个 10bit 用起来还是有用的，只不过对于大多数人来说可能就停留在有这个功能。</p><p class=\"p_style\"><b>权衡，权衡，权衡</b></p><p class=\"p_style\">不管 OPPO 自己在上市前预热宣传片里怎么说，Find X3 Pro 其实是一款有很多“妥协”的手机，而这些“妥协”的目的自然是降低售价。这台手机比上一代砍掉了潜望式长焦，将 X 轴线性马达换成了 Z轴，然后不带 Pro 的版本用的是骁龙 870 移动平台而非骁龙 888 移动平台。</p><p class=\"p_style\">为什么？你还好意思问？去年的 Find X2 Pro 很极致，但<strike>降价前</strike>你们买了吗？</p><p class=\"jsmargin\"><img alt=\"_1094677.jpg\" src=\"http://pic.zaeke.com/img161631462263586.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">所以商业是很现实的，梦想、情怀和追求都要向现实低头。OPPO 的 Find X2 Pro 用当时几乎最猛的料做出了最好的国产智能手机。可发布时的高昂定价显然吓退了不少人，加之一些硬件不足（比如没有无线充电和续航一般）和时运不济，最终成了个叫好不叫座的悲情角色。</p><p class=\"p_style\">Find X3 系列很聪明地保留 2K/120Hz 屏幕以及 65W Super VOOC 超级闪充这两个很核心的优势。然后采用更大的电池以及终于加入 30W AirVOOC 无线充电，将上一代最明显的短板给补好了。按照我的个人使用习惯来说，充一次电可以正常在 5G 网络下用一天，要是你在桌面上放个无线充电底座，那 Find X3 Pro 几乎可以做到“永不断电”。</p><p class=\"jsmargin\"><img alt=\"_1094697.jpg\" src=\"http://pic.zaeke.com/img161631463543074.jpeg\" width=\"2000\" height=\"1127\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">补齐短板之外， Find X3 系列还进一步扩大了屏幕的领先优势，甚至可以说它拥有目前最好的手机屏幕之一。采用了 LTPO 技术和超宽幅的可变刷新率，可以根据使用情况在 1~120Hz 之间切换，保证使用体验的同时降低功耗。在此基础上 Find X3 Pro 的屏幕的触控响应速度和触控精确度也极好。因此在日常打字、滑动列表等操作过程中手感极佳，甚至可以媲美某些号称有超高触控采样率和超高屏幕刷新率的游戏手机。</p><p class=\"p_style\">总之，Find X3 Pro 用起来真的太爽了，<b>除了那个 Z 轴马达还需要 OPPO 好好努力调校之外</b>。</p><p class=\"p_style\">所以觉得 ZAEKE 知客是推荐你们购买 OPPO Find X3 Pro 么？别那么肤浅。最好的选择当然是 Find X3，除非你并不在意那千把块钱，或者是愿意用这千把块钱换取 IP68 防水防尘以及骁龙 888 移动平台的 ISP 和对将来 5G 网络新技术的支持。</p><p class=\"jsmargin\"><img alt=\"DSC04087.jpg\" src=\"http://pic.zaeke.com/img161633708647056.jpeg\" width=\"1400\" height=\"933\" style=\"margin: 0px auto; height: auto;\"><br></p><p class=\"p_style\">抛开这一点，Find X3 简直香爆了。价格低一截，可无论是那块很带劲的后盖曲面玻璃、超强的屏幕、有线无线都快充等，都是与“Pro”同款。然后因为骁龙 870 移动平台在现阶段，功耗、发热和性能平衡得更好的关系，能在提供几乎相同日常使用体验的前提下有效延长手机续航。至于骁龙 888 移动平台和骁龙 870 移动平台因为 ISP 可能导致成像表现这件事情嘛……我们觉得大部分非专业人士应该感觉不出来二者的区别。</p><p class=\"p_style\">所以，你看懂 OPPO 的“最优解”了吗？</p>', 'http://pic.zaeke.com/img161633664412650.jpeg', 14383, 0, 0, NULL, '2021-03-20 23:59:34', '2021-03-27 07:00:36');
INSERT INTO `article` VALUES (16191, 'vivo S9 图赏：想要照亮别人，首先要惊艳自己', '今年的3月手机市场格外热闹，因为有多款不同定位的手机新品如雨后春笋般涌现，对于有换新机需求的消费者而言，百花齐放的手机市场虽然充满了活力，可是也会让不少人陷入了...', '[object Object]', '小啤', 170, 'http://pic.zaeke.com/user_1533281478_8.png', '<p>今年的 3 月手机市场格外热闹，因为有多款不同定位的手机新品如雨后春笋般涌现，对于有换新机需求的消费者而言，百花齐放的手机市场虽然充满了活力，可是也会让不少人陷入了“选择困难症”。筛选手机的方法有很多，而根据个人需求进行筛选无疑是最合理的，如同不少人热衷于使用手机自拍记录生活，那么定位自拍手机的机型自然成为了上述用户的心仪之选。</p><p>市面上各种细分市场的机型看似很多，可是像 vivo 这样孜孜不倦地迭代升级自拍机型的手机厂商，似乎并不常见。</p><p><img src=\"http://pic.zaeke.com/img161639829673369.jpeg\" alt=\"Capture01908.jpg\">  </p><p>vivo&nbsp;S 系列作为主打极致自拍的时尚机型，距离其面世虽然仅两年左右，可是该系列却拥有不俗的传播度，背后除了 vivo 不惜一掷千金地进行轰炸式的广告投放以外，还要得益于那句琅琅上口的 Slogan —— “照亮你的美”。除此，vivo&nbsp;S 系列还拥有多位不同流量明星的加持，使该系列产品在年轻消费群体中被广泛熟知。</p><p><img src=\"http://pic.zaeke.com/img161639830574330.jpeg\" alt=\"Capture01887.jpg\">  </p><p>为了在视觉上给予消费者一定的新鲜感，vivo S9 引用了全新的直角中框设计，其中子夜蓝配色的机身厚度降至 7.35mm，朝露白、印象拾光为 7.43mm，机身厚度方面均比上一代机型有所降低，而且由于采用直角中框设计的缘故，这款机型的机身宽度也有所收窄，迎合了小手女性用户的单手握持体验。</p><p>机身背面的后置摄像头模块则采用了特有的双色云阶设计，降低了后置摄像头模块的突兀感，从而带来更自然的过渡效果，并且辅以 AG 工艺的玻璃后盖，每款颜色在不同角度都能呈现独有的渐变色，使这款产品的背面提供了足够的外观独特性。</p><p><img src=\"http://pic.zaeke.com/img161639831611351.jpeg\" alt=\"Capture01776.jpg\">  </p><p>另外，vivo S9 的机身重量也控制在 173 克，凭借相对轻巧的机身，使它可满足一般用户的长时间握持上手，缓解了用户长时间握持手机时的手部疲劳感。虽然机身相对轻巧，可是这款机型仍然搭载了 4000 毫安时电池，辅以 33W 有线充电，该电源组合在一定程度上能满足中、轻强度手机用户的一天续航需求。这次 vivo&nbsp;S9 不仅在外观设计上进行了改动，其还引用了新的 Slogan —— “照亮我的美”。</p><p><img src=\"http://pic.zaeke.com/img161639833269155.jpeg\" alt=\"Capture01847.jpg\">  </p><p>事实上，在其它硬件性能方面 vivo&nbsp;S9 也进行了多项升级，其中最直观的一项硬件提升，莫过于搭载的 6.44 英寸屏幕，虽然屏幕尺寸与上一代机型保持不变，可是屏幕刷新率却从 60Hz 升级为 90Hz 高刷新率，并且具备 180Hz 触控采样率，通过上述两项新特性，使得 vivo&nbsp;S9 的手机系统交互和部分游戏中可以获得更佳自然丝滑的视觉呈现，而且触摸响应速度的提升也利于营造“行云流水”般的触控体验。</p><p><img src=\"http://pic.zaeke.com/img161639835749485.jpeg\" alt=\"Capture01937.jpg\">  </p><p>除屏幕这种肉眼可见的硬件升级以外， vivo&nbsp;S9 的 SoC 的性能也迎来了一次大幅度升级，其搭载了定位高端定位的 SoC —— 天玑 1100 芯片。天玑 1100 采用了 6nm EUV 工艺打造，核心方面为 4 × 2.6GHz A78 + 4 × 2.0GHz A55 的八核架构，GPU 为 Mali-G77 MC9，整体性能水平不俗，因为在过去的评测测试中，已经能感受到这款 SoC 的实际性能水平，它可满足大部分用户对于大型游戏的上手需求。</p><p><img src=\"http://pic.zaeke.com/img161639834350973.jpeg\" alt=\"Capture02006.jpg\">  </p><p>作为一款以自拍为卖点的机型，vivo S9 的前置摄像头阵容自然与普通手机相比显得格外耀眼。vivo S9 搭载的前置 4400 万三星 GH1 传感器摄像头的感光能力较 S7 提升了 12%，配合 AF 自动对焦和 f/2.0 超大光圈特性，使得前置主摄的成像效果再上一台阶。</p><p>独特的“双极夜柔闪光灯”在不破坏外观设计完整性的情况下，实现了自拍影像的物理双灯光补光效果。另外，该机的前置还配备一颗 800 万像素 105° 超广角人像镜头，可满足多人合照时的自拍需求，真正做到了多人合照时不求人。</p><p><img src=\"http://pic.zaeke.com/img161639839525495.jpeg\" alt=\"Capture01691.jpg\">  </p><p>相比较于前置摄像头，这款机型的后置摄像头采用了 6400 万像素超高清主摄 + 800 万像素超广角镜头 + 200 万像素微距镜头后置三摄组合，通过三颗摄像头的相互配合，它可以满足大部分用户对于各种复杂场景的拍摄需求，其中主摄具备黑光夜视功能，在极暗的情况下也能照出足够明亮的画面。</p><p><img src=\"http://pic.zaeke.com/img161639839528122.jpeg\" alt=\"Capture02046.jpg\">  </p><p>vivo&nbsp;S9 最高可选 12GB +256GB 机身存储组合，即便预算有限的用户，也不必过于担心内存不足的问题，通过 Origin OS 独特的“内存融合技术”，vivo&nbsp;S9 可实现调用 3GB 存储空间到内存中进行合理分配，彻底避免了内存足襟见肘的尴尬情况。</p><p><img src=\"http://pic.zaeke.com/img161639837672477.jpeg\" alt=\"Capture01753.jpg\">  </p><p>售价方面，vivo&nbsp;S9 8GB + 128GB 存储组合售价 2999 元，12GB +256GB 机身存储组合 3299 元。该机一共提供了子夜蓝、朝露白、印象拾光三款机身配色，并没有区分高配独占配色，标配和高配版均有三色可选。</p>', '<p class=\"p_style\">今年的 3 月手机市场格外热闹，因为有多款不同定位的手机新品如雨后春笋般涌现，对于有换新机需求的消费者而言，百花齐放的手机市场虽然充满了活力，可是也会让不少人陷入了“选择困难症”。筛选手机的方法有很多，而根据个人需求进行筛选无疑是最合理的，如同不少人热衷于使用手机自拍记录生活，那么定位自拍手机的机型自然成为了上述用户的心仪之选。</p><p class=\"p_style\">市面上各种细分市场的机型看似很多，可是像 vivo 这样孜孜不倦地迭代升级自拍机型的手机厂商，似乎并不常见。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161639829673369.jpeg\" alt=\"Capture01908.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">vivo&nbsp;S 系列作为主打极致自拍的时尚机型，距离其面世虽然仅两年左右，可是该系列却拥有不俗的传播度，背后除了 vivo 不惜一掷千金地进行轰炸式的广告投放以外，还要得益于那句琅琅上口的 Slogan —— “照亮你的美”。除此，vivo&nbsp;S 系列还拥有多位不同流量明星的加持，使该系列产品在年轻消费群体中被广泛熟知。</p><p class=\"jsmargin\"><img src=\"http://pic.zaeke.com/img161639830574330.jpeg\" alt=\"Capture01887.jpg\" style=\"margin: 0px auto; height: auto;\">  </p><p class=\"p_style\">为了在视觉上给予消费者一定的新鲜感，vivo S9 引用了全新的直角中框设计，其中子夜蓝配色的机身厚度降至 7.35mm，朝露白、印象拾光为 7.43mm，机身厚度方面均比上一代机型有所降低，而且由于采用直角中框设计的缘